INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(1, 'Nhiều chuyến bay đến miền Trung phải hủy do mưa bão', 'Các hãng hàng không trong nước đã thông báo dừng khai thác ít nhất 18 chuyến bay từ Hà Nội và TP HCM đi Vinh, Đồng Hới. ', '<p class="Normal"> 	Do ảnh hưởng của bão Talas - dự kiến đổ bộ vào Thanh Hóa, Nghệ An, Hà Tĩnh vào đêm nay và rạng sáng mai, Vietnam Airlines thông báo dừng khai thác 4 chuyến từ Hà Nội và TP HCM đi Vinh, gồm VN1714, VN1715, VN1268, VN1269 vào chiều tối 16/7. Hãng sẽ bay bù các chuyến trên vào ngày 17/7.</p><p class="Normal"> 	Hãng Vietjet Air cũng hủy 8 chuyến bay chặng TP HCM đi Thanh Hóa, Vinh, Đồng Hới và ngược lại. Nhiều chuyến bay khác cũng bị chậm dây chuyền trong ngày do ảnh hưởng bão.</p><p class="Normal"> 	Hãng Jetstar Pacific thông báo hủy 4 chuyến trên đường bay giữa Vinh/Đồng Hới và TP HCM là BL524, BL525, BL350, BL351. Tương tự, hãng Vasco hủy 2 chuyến trên đường bay giữa Hà Nội và Đồng Hới là 0V8590, 0V8591.</p><p class="Normal"> 	Hành khách bị hủy chuyến sẽ được chuyển đổi miễn phí sang chuyến bay cùng hành trình trong ngày hôm sau nếu còn chỗ hoặc ngày tiếp theo.</p><p class="Normal"> 	Các hãng hàng không khuyến cáo hành khách đến và đi từ các sân bay Nội Bài, Cát Bi, Thọ Xuân, Vinh, Đồng Hới, Phú Bài, Đà Nẵng trong thời gian này cần cập nhật tình hình thời tiết, thông tin của hãng nhằm chủ động lịch trình đi lại.</p><p class="Normal"> 	Talas là cơn bão thứ hai xuất hiện ở biển Đông trong năm nay. <span>Theo Trung tâm dự báo khí tượng thủy văn Trung ương, chiều nay tâm bão cách bờ biển Thanh Hóa -Hà Tĩnh khoảng 290 km về phía đông đông nam, sức gió mạnh nhất đạt cấp 9-10 (75-100 km/giờ). Dự kiến bão đổ bộ vào các tỉnh Thanh Hóa, Nghệ An, Hà Tĩnh khoảng đêm nay và sáng mai, cường độ cấp 9-10.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Đoàn Loan</strong></p>', 1, NULL, NULL),
(2, 'Địa phương gấp rút ứng phó với bão cấp 9', 'Thanh Hóa, Nghệ An, Hà Tĩnh nơi bão có thể đổ bộ sẽ hoàn tất việc cấm biển, đưa tàu thuyền vào nơi an toàn trước 17h hôm nay.', '<p class="Normal"> 	<span>Theo Trung tâm dự báo khí tượng thủy văn Trung ương, khoảng 4h sáng mai bão sẽ đổ bộ các tỉnh Thanh Hóa, Nghệ An, Hà Tĩnh, sức gió tối đa khoảng 90 km/h, tương đương cấp 8-9. Các tỉnh lân cận sẽ bị ảnh hưởng. </span></p><p class="Normal"> 	Tại <strong>Thanh Hóa</strong>, <span>ông Nguyễn Đức Quyền, Phó chủ tịch UBND tỉnh cho biết, đến 9h hôm nay toàn tỉnh vẫn còn hơn 1.700 tàu thuyền với trên 8.000 lao động hoạt động trên biển, nhưng đều giữ liên lạc với đất liền và đang tìm nơi tránh trú.</span></p><p class="Normal"> 	Ông Quyền lo ngại trước đó Thanh Hóa có mưa liên tục với lượng mưa phổ biến 100-200 mm. Bão vào sẽ gây mưa lớn, đe dọa an toàn hồ đập. Toàn tỉnh có tới hơn 610 hồ đập lớn nhỏ, trong đó 121 hồ không đảm bảo an toàn. Đối phó với bão, tỉnh đã chỉ đạo 18 hồ không tích nước, 103 hồ hạn chế tích.</p><p class="Normal"> 	<span>Thanh Hóa đã có công điện chỉ đạo sẵn sàng sơ tán dân khi có lệnh ở vùng nguy cơ sạt lở, vùng cửa sông, ven biển; chủ động giải tỏa ách tắc lòng sông và các trục tiêu để bảo đảm tốt việc thoát lũ. Các huyện thị phải vận hành ngay trạm bơm tiêu, cống tiêu khi mưa lớn để bảo vệ diện tích lúa và hoa màu</span><span>. </span></p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941211067919840.gif" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngư dân Nghệ An đưa tàu vào bờ. Ảnh: <em>Văn Hải.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>12h ngày 16/7, UBND tỉnh <strong>Nghệ An</strong> có công điện khẩn cấm tàu thuyền, gồm cả tàu du lịch và vận tải ra khơi. Các huyện, thị xã ven biển Quỳnh Lưu, thị xã Hoàng Mai, Nghi Lộc, Diễn Châu và Cửa Lò phải thông báo cho tàu thuyền trên biển biết diễn biến của bão, kêu gọi về nơi trú ẩn an toàn</span><span> trước 17h hôm nay. </span></p><p class="Normal"> 	Toàn tỉnh có gần 4.000 phương tiện với hơn 18.000 lao động đánh bắt. Thống kê tới nay có hơn 3.390 tàu thuyền đã neo đậu tại bến. Hiện còn 500 tàu thuyền hoạt động trên vùng biển Nghệ An và ngoài tỉnh. Tuy nhiên, các chủ phương tiện này đã nhận được thông báo về vị trí, hướng đi của bão.</p><p class="Normal"> 	Cùng với "cấm biển", Nghệ An cũng đã, đang triển khai các phương án đảm bảo an toàn cho nông nghiệp, công trình thủy lợi, giao thông trọng điểm khi có bão lớn, lũ ống lũ quét. Trước đó từ đầu tháng <span>7,</span><span> Công ty cổ phần Công viên cây xanh thành phố Vinh đã huy động công nhân và máy móc chặt tỉa cây trên 44 tuyến đường. </span></p><div style="text-align: center;"> 	<div id="video-157030" data-component="true" data-component-type="video" data-component-value="157030" data-component-typevideo="2"></div></div><p class="Normal" style="text-align:center;"> 	<em>Ngư dân Nghệ An đối phó với bão Talas.</em></p><p class="Normal"> 	<span>Tại </span><strong>Hà Tĩnh</strong><span>, từ sáng đến trưa 16/7 có mưa lớn. Các huyện ven biển Lộc Hà, Cẩm Xuyên, Nghi Xuân mưa lớn kèm gió thổi mạnh. Hàng trăm tàu thuyền đã được đưa vào bờ tránh bão ở cảng cá </span><span>Cửa Sót (Lộc Hà), Cửa Hội (Nghi Xuân). Nhà chức trách đang kêu gọi tàu thuyền đánh bắt xa bờ cập cảng.</span></p><p class="Normal"> 	<span>Bí thư Tỉnh ủy Lê Đình Sơn yêu cầu trong chiều nay, tất cả hệ thống cống của hồ đập phải được vận hành, kiểm soát tình huống, xây dựng kịch bản mưa 300 mm để có phương án xử lý lũ quét ở các vùng Hương Sơn, Vũ Quang. C</span><span>ác huyện, thành phố, thị xã tổ chức sơ tán dân ở tàu thuyền, lồng bè, chòi canh, khu nuôi trồng thủy sản, vùng thấp trũng ven biển đến nơi an toàn trước 17h ngày 16/7. </span></p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194121537465125.gif" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các địa phương hoàn tất việc ứng phó với bão trước 17 ngày 16/7. Ảnh: <em>Văn Hải.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<strong><span>Hoàn thành công việc chống bão trước 17h</span></strong></p><p class="Normal"> 	<span>Sáng 16/7, tại phiên họp trực tuyến với các tỉnh, thành, </span><span>Thứ trưởng Nông nghiệp và Phát triển nông thôn Hoàng Văn Thắng đề nghị các địa phương cấm biển</span><span>, sơ tán dân vùng nguy cơ mất an toàn, hoàn tất mọi công việc trước 17 hôm nay.</span></p><p class="Normal"> 	<span>Ông Thắng lo ngại 3 tỉnh Thanh Hóa, Nghệ An, Hà Tĩnh có hơn 1.350 hồ lớn nhỏ, trong đó nhiều hồ có nguy cơ thiếu an toàn. Trường hợp mưa lớn, đập Hòa Bình dự kiến xả 3 cửa, khi đó nước sông Hồng sẽ lên cao, gây nguy cơ cho nhiều điểm đê xung yếu. "Quá trình xả lũ cần thông tin sớm cho chính quyền vùng hạ du để thông báo kịp thời tới nhân dân", ông Thắng nói. </span></p><p class="Normal"> 	<span>Đại tá Lê Thanh Sơn, Phó tham mưu trưởng Bộ đội biên phòng cho biết, hiện có khoảng 2.000 tổ bộ đội biên phòng xuống các địa phương cùng dân ứng phó với bão. Đến 7h hôm nay, toàn bộ tàu thuyền ngoài biển đã nhận được thông tin về bão. Trước 10h, Bộ đội biên phòng cùng địa phương sẽ sắp xếp tàu thuyền vào nơi tránh trú, không nhất thiết tàu tỉnh nào phải quay về tỉnh đó.</span></p><p class="Normal"> 	<span>Ông Trương Đức Nghĩa, Chánh văn phòng Ủy ban quốc gia tìm kiếm cứu nạn cho rằng không nên chủ quan vì đây là cơn bão đầu tiên vào Việt Nam dù không mạnh nhưng diễn biến khó lường. Thời điểm này đang có nhiều công trình dang dở, nhất là thủy điện thủy lợi và lượng khách du lịch nhiều nên các địa phương cần chủ động theo dõi, đảm bảo thông tin để không gây thiệt hại.</span></p><p class="Normal"> 	<span>Các thành viên Ban chỉ đạo cũng cảnh báo tình trạng nguy cơ sạt lở đất ở các tỉnh vùng núi, khi nhiều qua tại đây đã có mưa kéo dài. Khu vực đồng bằng cần đề phòng ngập úng.</span></p>', 1, '1500194121479006386.gif', NULL),
(3, 'Đóng cửa trụ sở trong giờ làm việc, cán bộ phường đi liên hoan', 'Phường Vĩnh Niệm (Lê Chân, Hải Phòng) đóng cửa trụ sở để cán bộ tổ chức liên hoan khiến người dân đến làm việc phải đi về.', '<table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194122178879736.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Lãnh đạo phường Vĩnh Niệm đóng cửa nhiệm sở trong giờ hành chính để tổ chức cho nhân viên đi chơi. Ảnh: <em>HH</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Thường trực Thành ủy Hải Phòng đang yêu cầu Thường trực Quận uỷ Lê Chân làm rõ thông tin cán bộ phường Vĩnh Niệm đóng cửa nhiệm sở trong giờ làm việc để đi ra biển Đồ Sơn liên hoan.</p><p class="Normal"> 	Ngày mai, lãnh đạo quận Lê Chân họp với UBND phường Vĩnh Niệm để nghe giải trình cụ thể, sau đó sẽ có phương án xử lý trách nhiệm người liên quan.</p><p class="Normal"> 	Sáng 14/7, HĐND phường Vĩnh Niệm tổ chức họp. Chiều cùng ngày, lãnh đạo phường thống nhất cho đóng cửa trụ sở, thuê 2 ôtô chở cán bộ, nhân viên của phường cùng tổ trưởng dân phố đi liên hoan, đến nửa đêm về.</p><p class="Normal"> 	Nhiệm sở bỏ trống trong giờ hành chính khiến một số người dân phản ánh đến liên hệ phải đi về.</p><p class="Normal"> 	<span class="_5yl5"><span>Chủ tịch phường Trần Quốc Khánh cho biết, đầu giờ chiều thấy vãn công việc, không thấy người dân đến làm thủ tục, lại là ngày cuối tuần nên lãnh đạo phường thống nhất "nghỉ sớm".</span></span></p><p class="Normal"> 	<span class="_5yl5"><span>Ông Khánh bố trí cán bộ trực một ở bộ phận một cửa, yêu cầu đi ra Đồ Sơn sau. "Tuy nhiên, chủ quan khi không thấy có người đến, những cán bộ trực đã đi lúc 15h20", ông nói.</span></span></p><p class="author" style="text-align:right;"> 	<strong>Giang Chinh</strong></p>', 1, '15001941221105933622.jpg', NULL),
(4, 'Bão tăng cấp, có thể đổ bộ Thanh Hóa - Hà Tĩnh vào sáng mai', 'Dự kiến khoảng 4h ngày 17/7, bão Talas sẽ vào Thanh Hóa - Hà Tĩnh với cường độ cấp 9, gây mưa to ở miền Bắc và Bắc Trung Bộ.', '<p class="Normal"> 	<span>Trung tâm dự báo khí tượng thủy văn Trung ương cho biết, 7h hôm nay bão trên </span><span>vùng biển phía nam đảo Hải Nam (Trung Quốc), cách bờ biển </span><span>Thanh Hóa - Hà Tĩnh khoảng 410 km về phía đông đông nam. Sức gió mạnh nhất 90 km/h (tương đương cấp 9), tăng một cấp so với hôm qua.</span></p><p class="Normal"> 	<span>Với hướng di chuyển tây tây bắc, vận tốc 20 km/h, đến 19h</span><span> bão sẽ </span><span>cách bờ biển Thanh Hóa - Hà Tĩnh khoảng 120 km về phía đông, sức gió không đổi.</span></p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941231647184859.gif" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dự báo đường đi của bão Talas. Ảnh: <em>NCHMF</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Ông Hoàng Đức Cường, Giám đốc Trung tâm dự báo khí tượng thủy văn Trung ương nhận định, v</span><span>ùng ảnh hưởng của bão từ Hải Phòng đến Quảng Ninh, nhưng trọng điểm là Thanh Hóa - Hà Tĩnh. </span><span>"Khoảng</span><span> đêm nay và rạng sáng mai, bão sẽ áp sát bờ biển Thanh Hóa - Hà Tĩnh, dự kiến 4h sáng 17/7 đi vào bờ với cường độ cấp 8-9. Tuy nhiên, bão có thể đến sớm lúc 1h và muộn nhất là 7h". </span></p><p class="Normal"> 	<span>Các trung tâm khí tượng thế giới đều nhận định, trong hôm nay bão di chuyển hướng tây tây bắc và hướng Bắc Trung Bộ, khi cập bờ đạt cấp 8-9.</span></p><p class="Normal"> 	<span>Hoàn lưu bão sẽ gây mưa to ở miền Bắc và Bắc Trung Bộ vào</span><span> ngày 16-18/7. Trong đó Thanh Hóa, Nghệ An có tổng lượng lên mưa đến </span><span>250-350 mm; đồng bằng Bắc Bộ và Hà Tĩnh 150-250 mm; các tỉnh Hòa Bình, Sơn La, Phú Thọ, Yên Bái, Điện Biên 100-200 mm. Khu vực khác của Bắc Bộ và Quảng Bình - Thừa Thiên Huế là 50-150 mm.</span></p><p class="Normal"> 	<span>Sau khi bão suy yếu, mưa vẫn duy trì ở vùng núi phía Bắc, nhất là Hòa Bình, nam Sơn La và dọc trung du đồng bằng với lượng mưa 100-150 mm.  </span></p><p class="Normal"> 	<span>Cơ quan khí tượng cảnh báo tình trạng ngập úng ở</span><span> khu đô thị, thuộc các tỉnh thành như: Hà Nội, Hải Phòng, Thái Bình, Nam Định, Ninh Bình, Hải Dương, Hưng Yên, Thanh Hóa, Vinh (Nghệ An), Hà Tĩnh. Vùng núi khả năng cao xảy ra lũ quét, sạt lở đất, nhất là tại </span><span>Lai Châu, Điện Biên, Sơn La, Hòa Bình, Lào Cai, Yên Bái, Hà Giang, Lạng Sơn, Quảng Ninh.</span></p><p class="Normal"> 	<span>Theo Ban chỉ đạo Trung ương về phòng chống thiên tai, đến 6h ngày 16/7 các địa phương ven biển từ Quảng Ninh đến Khánh Hòa đã hướng dẫn cho hơn 65.000 phương tiện với hơn 260.000 người và gần 3.000 lồng bè, lều chòi biết hướng di chuyển của bão để chủ động phòng chống.</span><span> <span>Cấm biển từ 12h trưa 16/7, t</span>ất cả công việc phải hoàn tất trước 17h hôm nay, nhất là ba tỉnh Thanh Hóa, Nghệ An, Hà Tĩnh.</span></p><table class="tbl_insert" style="width:100%;" align="center" border="1" cellpadding="1" cellspacing="0"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal"> 					<span>Hình thành từ vùng áp thấp ngày 13/7 và mạnh lên thành áp thấp nhiệt đới, sau đó là bão vào ngày 15/7, Ta</span><span>las là cơn bão thứ hai xuất hiện ở biển Đông trong năm nay. Cơn bão đầu tiên hình thành ngày 11/6, sau đó đi vào Trung Quốc.</span></p> 				<p class="Normal"> 					Theo cơ quan khí tượng, mùa mưa bão 2017 có khoảng 13-15 cơn bão và áp thấp nhiệt đới hoạt động trên biển Đông. Trong đó 3-4 cơn đổ bộ, ảnh hưởng trực tiếp đến đất liền Việt Nam, tập trung ở Trung Bộ.</p> 			</td> 		</tr></tbody></table>', 1, '150019412336728679.gif', NULL),
(5, 'Người thân chào đón đoàn học sinh thi Olympic Hóa về nước', 'Đạt thành tích cao nhất từ trước đến nay, đoàn học sinh Olympic Hoá học quốc tế trở về trong sự tự hào của thầy cô và gia đình.', '<p class="Normal"> 	18h ngày 15/7, sảnh A1 cảng hàng không quốc tế Nội Bài đông nghịt người cầm cờ, hoa, quà lưu niệm đứng đợi đoàn học sinh Việt Nam dự thi Olympic Hóa học từ Thái Lan về nước. Dù thấm mệt sau chuyến bay chiều, cả 4 em đều vui mừng trước sự chào đón nồng nhiệt của thầy cô, gia đình và bạn bè.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156947" data-component="true" data-component-type="video" data-component-value="156947" data-component-typevideo="2"></div></div> 	<p class="Normal"> 		<em>Thí sinh và người nhà chia sẻ cảm xúc về thành tích đã đạt được. </em></p> </div><p class="Normal"> 	Bất ngờ với sự chuẩn bị ăn mừng của mọi người, đặc biệt là sự góp mặt của nhiều học sinh lớp dưới, Phạm Đức Anh, trường THPT chuyên Khoa học tự nhiên - Đại học Quốc gia Hà Nội, không giấu nổi niềm sung sướng. "Là học sinh lớp 11 duy nhất của đoàn và giành huy chương vàng, em cảm thấy vui và tự hào vì đã có đóng góp cho Tổ quốc", Đức Anh nói.</p><p class="Normal"> 	Nam sinh chia sẻ cách đây 9 năm, anh trai ruột là Phạm Anh Tuấn, tham gia cuộc thi Olympic Hóa học và giành huy chương đồng. "Ước mơ duy nhất của em trước khi lên đường sang Thái Lan là có thể thay anh đổi màu huy chương. Kết quả em đã thực hiện được, không phụ sự kỳ vọng của anh trai và gia đình", chàng trai vàng chia sẻ.</p><p class="Normal"> 	Nguyễn Bằng Thanh Lâm, học sinh lớp 12 trường THPT chuyên Hà Nội - Amsterdam, kể lại giây phút xướng tên học sinh đạt huy chương. "Em thực sự hồi hộp khi ban tổ chức chuẩn bị đọc tên thí sinh cuối cùng giành được huy chương bạc rồi vỡ òa khi đó không phải tên mình. Lúc này em mới chắc chắn huy chương vàng thuộc về mình", Lâm nói. Em xếp thứ 20/297 thí sinh với số điểm 89,67/100.</p><p class="Normal"> 	<span>Không có chiến lược học tập cụ thể, cũng không xác định học để giành huy chương, Thanh Lâm bất ngờ với kết quả giành được. "Những cố gắng của con trong suốt thời gian qua đã nhận được thành quả xứng đáng", chị Trần Yến, mẹ của Thanh Lâm chia sẻ</span><span>. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194126855912139.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại diện Bộ Giáo dục và Đào tạo tặng hoa chúc mừng 4 thí sinh đạt thành tích cao trong kỳ thi Olympic Hóa học quốc tế 2017. Ảnh: <em>Thanh Tâm</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Khác với Đức Anh và Thanh Lâm, Đinh Quang Hiếu, học sinh lớp 12 trường THPT chuyên Khoa học tự nhiên - Đại học Quốc gia Hà Nội, không quá bất ngờ với kết quả của chính mình. Em đặt mục tiêu bảo vệ thành công huy chương vàng đã đạt được từ năm ngoái. "Vì giành huy chương vàng một lần nên em cảm thấy rất thoải mái trước và trong khi làm bài thi", Hiếu chia sẻ.</p><p class="Normal"> 	Chủ nhân của chiếc huy chương bạc là Hoàng Nghĩa Tuyến, học sinh lớp 12 trường THPT chuyên Phan Bội Châu (Nghệ An), trở về trong vòng tay của bố. "Dù không đạt thành tích như các bạn nhưng đó là nỗ lực của con trai tôi và tôi ghi nhận điều đó", phụ huynh đến từ Nghệ An chia sẻ.</p><p class="Normal"> 	Đoàn Việt Nam dự thi Olympic Hóa học quốc tế năm nay gồm 4 học sinh, trong đó có 3 em giành huy chương vàng là Đinh Quang Hiếu (92,13/100 điểm, xếp thứ 9), Nguyễn Bằng Thanh Lâm (89,76/100 điểm, xếp thứ 20), Phạm Đức Anh (89,46/100 điểm, xếp thứ 21) và một em đạt huy chương bạc là Hoàng Nghĩa Tuyến (85,91/100 điểm). Đây là kết quả cao nhất của đội tuyển Việt Nam tham dự Olympic Hóa học quốc tế từ trước đến nay.</p><p class="Normal"> 	Olympic Hoá học quốc tế năm 2017 có sự tham gia của 76 quốc gia và vùng lãnh thổ với 297 thí sinh. Theo bảng xếp hạng không chính thức, đội tuyển Mỹ xếp thứ nhất với 4 huy chương vàng; Việt Nam và Trung Quốc xếp thứ hai với 3 huy chương vàng, một huy chương bạc.</p><p class="Normal"> 	<span>Năm 2016, Việt Nam giành hai huy chương vàng và một bạc Olympic Hóa quốc tế. Trong đó em </span><a href="http://vnexpress.net/tin-tuc/giao-duc/chang-trai-vang-hoa-hoc-tung-khoc-khi-khong-lam-xong-bai-3449138.html">Đinh Quang Hiếu</a><span> (THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội) là một trong hai thí sinh giành giải vàng</span><span>.</span></p>', 1, '15001941261563293082.jpg', NULL),
(6, 'Chuyên gia: ''Bán đảo Sơn Trà sẽ đẻ ra tiền bền vững''', 'Cuộc tranh luận giữa 180 nhà khoa học, chính sách đã đi đến thống nhất về một bản kiến nghị không xây mới cơ sở lưu trú tại Sơn Trà.', '<p class="Normal"> 	<span>Ngày 15/7, tại Đà Nẵng, Viện sinh thái học miền Nam và Hiệp hội du lịch Đà Nẵng đã tổ chức hội thảo "Bảo tồn và phát triển bền vững hệ sinh thái tự nhiên Bán đảo Sơn Trà", với sự tham gia của </span><span>180 nhà khoa học, nhà hoạch định chính sách, đại diện chính quyền Đà Nẵng. </span></p><table class="tplCaption" style="padding:2px;border-collapse:collapse;border-spacing:0px;line-height:normal;color:rgb(0,0,0);" align="center" border="0" cellpadding="3" cellspacing="0"><tbody style="margin:0px;padding:0px;"><tr style="margin:0px auto;padding:2px;line-height:normal;border:none;"><td style="margin: 0px auto; outline: none; padding: 2px; line-height: normal;"> 				<div style="text-align: center"><img src="/pictures/news/15001941271624024306.jpg" /></div></td> 		</tr><tr style="margin:0px auto;padding:2px;line-height:normal;border:none;"><td style="margin: 0px auto; outline: none; padding: 2px; line-height: normal;"> 				<p class="Image" style="margin-top:5px;margin-bottom:5px;padding:0px;line-height:23px;"> 					Bản đảo Sơn Trà nhìn từ trên cao. Ảnh: <em style="margin:0px;padding:0px;">Nguyễn Đông.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Đây là hội thảo khoa học đầu tiên, từ sau khi Chính phủ phê duyệt quy hoạch khu du lịch quốc gia Sơn Trà cuối năm 2016, gây nhiều tranh cãi thời gian qua.</p><p class="Normal"> 	<span style="margin:0px;padding:0px;">Phó chủ tịch UBND TP Đà Nẵng, ông Hồ Kỳ Minh cho biết c</span><span>hính quyền thành phố đang rà soát tổng thể bán đảo Sơn Trà trên tinh thần cầu thị và cởi mở để hoàn thiện 3 loại quy hoạch: quy hoạch  chung, quy hoạch du lịch và quy hoạch bảo tồn rừng đặc dung. </span><span>"Tôi tin tưởng qua hội thảo, các đại biểu, các nhà khoa học giúp đưa ra những đề xuất, giải pháp hợp lý để phát triển bền vững Sơn Trà", ông Minh nói.</span></p><p class="Normal"> 	<strong>Được bảo tồn thì Sơn Trà sẽ đẻ ra tiền</strong></p><p class="Normal"> 	Ông Trương Trọng Nghĩa, đại biểu Quốc hội TP HCM, cho rằng bán đảo Sơn Trà là<span> di sản thiên nhiên quý báu mà khi mất đi không bao giờ tái tạo lại được. Sơn Trà cũng không phải chỉ để ngắm, </span><span>mà còn phục vụ mục đích kinh tế. Nhưng phục vụ kinh tế thì phải bảo tồn cho được di sản. Đó mới là phát triển bền vững.</span></p><p class="Normal"> 	<span>Theo ông, b</span><span>án đảo Sơn Trà trước đây là rừng cấm vì có yếu tố quân sự và cũng vì bảo tồn. Nhưng trong quá trình khai thác, diện tích rừng giảm từ hơn 4.000 ha xuống còn 2.500 ha, diện tích còn lại dành phục vụ du lịch. Ông Nghĩa nói khi xây dựng biệt thự ở Sơn Trà thì đại gia trong và ngoài nước sẽ đến mua để ở, và diện tích rừng còn lại cũng đứng trước nguy cơ bị khai thác, xâm hại.</span></p><p class="Normal"> 	<span>"Họ sẽ tiếp tục hủy hoại 2.500 hecta rừng còn lại trong một ngày nào đó. </span><span>Tôi ví dụ như vụ sân golf vừa qua ở sân bay Tân Sơn Nhất. </span><span>Có những thứ cộng sinh được nhưng có những thứ không được. Bây giờ đ</span><span>òi hỏi Sơn Trà phải đẻ ra được tiền để nuôi mình là không hợp lý", ông Nghĩa nói.</span></p><p class="Normal"> 	Ông Nghĩa cho biết, các quốc gia hiện đại trên thế giới gần các đô thị đều có một khu rừng. Người dân làm việc mệt mỏi, stress thì sẽ tìm đến để có không khí sạch nạp lại năng lượng. Điều đó giúp tái tạo sức lao động, làm cho chất lượng sống cao hơn và từ đó quay lại đẻ ra tiền. </p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941271173342361.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại biểu Trương Trọng Nghĩa phát biểu tại hội thảo. Ảnh: <em>Nguyễn Đông.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>"</span><span>Tôi rất muốn đấu tranh để thuyết phục các nhà quản lý hiểu rằng, có nhiều cách khai thác và có những giá trị tinh thần rất quý. Nếu chúng ta bảo tồn Sơn Trà thì không sợ không đẻ ra được tiền mà lại đẻ ra được tiền rất bền vữ</span><span>ng. Tôi tin rằng rất nhiều người dân sẽ đóng góp để nuôi khu bảo tồn Sơn Trà", ông Nghĩa</span><span> nhấn mạnh. </span></p><p class="Normal"> 	<span>Luật sư Nghĩa vừa dứt lời, phía dưới nhiều tiếng vỗ tay vang lên. </span></p><p class="Normal"> 	Chủ tịch Hiệp hội Du lịch Đà Nẵng, ông Huỳnh Tấn Vinh, đưa ý tưởng khai thác tour "tắm rừng" ở Sơn Trà. Đây là xu hướng đang khá thịnh hành ở Nhật Bản và một số nước châu Âu, khi con người được đắm chìm trong thiên nhiên sau những ngày làm việc mệt mỏi. Ở nước Nhật, du khách phải trả 80 USD cho mỗi giờ "tắm rừng".</p><p class="Normal"> 	<span>"Du khách phải trả tiền để nhìn thấy vẻ đẹp tự nhiên Sơn Trà, không phải bê tông, đồi trọc do khai thác ngắn hạn", ông Vinh nói và đề nghị không xây thêm các hạng mục bê tông trên Sơn Trà, khi du khách đến tham quan phải tuân thủ các</span><span> quy định nghiêm ngặt về quy chế ứng xử với thiên nhiên, h</span><span>ạn chế tối đa việc sử dụng phương tiện cơ giới gây tiếng ồn và ô nhiễm.</span></p><p class="Normal"> 	<span>"Bảo tồn là vấn đề của cộng đồng, như thế mới bền vững chứ không nên giao khoán tài sản quý báu như Sơn Trà, sẽ rất rủi ro. Nếu không cẩn trọng là s</span><span>ẽ phải xin lỗi thế hệ mai sau", ông Vinh nói.</span></p><p class="Normal"> 	<b>Bảo tồn và phát triển kinh tế ''không chung đường''</b></p><p class="Normal"> 	Đánh giá cao những giá trị đa dạng sinh học, lá phổi xanh giúp tái tạo không khí, "túi nước ngọt", nhiều nhà khoa học cho rằng vấn đề bảo tồn và phát triển kinh tế ở Sơn Trà thời điểm này "không thể đi chung đường".</p><p class="Normal"> 	<span>Dẫn ý kiến của Chủ tịch UBND TP Đà Nẵng </span><a href="http://vnexpress.net/tin-tuc/thoi-su/chu-tich-da-nang-tien-khong-tai-tao-duoc-gia-tri-cua-son-tra-3610339.html">tiền không tái tạo được giá trị của Sơn Trà</a>, ông<span> Phạm Viết Thuận, Viện trưởng Viện Kinh tế Tài nguyên và Môi trường, nói: "C</span><span>hủ trương của chúng ta là phát triển theo bảo tồn vì vậy lựa chọn cho Sơn Trà chỉ có một, đó là bảo tồn. Nếu có phát triển du lịch thì không có lưu trú".</span></p><p class="Normal"> 	Đồng quan điểm, GS.TS Nguyễn Hoàng Trí, Chủ tịch kiêm Tổng thư ký Ủy ban Quốc gia chương trình Con người và sinh quyển Việt Nam, cho rằng nếu chúng ta phát triển kinh tế đồng nghĩa với việc đánh đổi một phần thiên nhiên, còn nếu chọn bảo tồn thiên nhiên thì phải loại bỏ kinh tế.</p><p class="Normal"> 	"Chúng ta chọn chân dài đầu đất hay chân ngắn thông minh?", ông Trí ví von và cho rằng người<span> quyết định cuối cùng là UBND TP Đà Nẵng, người dân thành phố chứ không phải ai khác. </span></p><p class="Normal"> 	Theo ông Trí, câu chuyện ở Sơn Trà là "hành động địa phương nhưng tác động toàn cầu". "Chúng ta phải đặt ra văn hóa tôn trọng tự nhiên. Khu vực rừng từ 200m trở xuống đến mặt biển là nơi voọc kiếm ăn, uống nước mà lại quy hoạch làm các công trình bê tông là không ổn", ông nói.</p><p class="Normal"> 	Giáo sư Trí cũng nhận định, việc bảo tồn nguyên vẹn sinh quyển là không thể, vì đã xuất hiện con người thì chắc chắn sẽ tác động đến môi trường, đó là logic và chúng ta phải chấp nhận,<span> nói rõ để thống nhất về lựa chọn hướng đi cho Sơn Trà, thay vì cứ bàn vừa bảo tồn vừa phát triển kinh tế. </span></p><p class="Normal"> 	<span>Giám đốc Trung tâm nghiên cứu rừng và nước, ông </span><span>Nguyễn Chí Thành, khẳng định sẽ kiên quyết bảo tồn Sơn Trà. Trả lời cho câu hỏi bảo tồn như thế nào, ông Thành cho rằng trước hết phải xác định mục tiêu rõ ràng, khi những quyết định đều do Thủ tướng ban hành trước đây Sơn Trà là rừng cấm, sau đó lại là rừng đặc dụng.</span></p><p class="Normal"> 	<span>"Chúng ta muốn bảo tồn thì phải trên cơ sở của pháp luật. Khi thành rừng đặc dụng, mọi quy chế đều phải thực hiện trên cơ sở rừng đặc dụng", ông Thành nói và đề xuất thành lập ban quản lý rừng đặc dụng bảo vệ Sơn Trà, không nên để quá nhiều đơn vị cùng tham gia quản lý, từ kiểm lâm đến chính quyền phường như hiện nay.</span></p><table class="tplCaption" style="padding:2px;border-collapse:collapse;border-spacing:0px;line-height:normal;color:rgb(0,0,0);" align="center" border="0" cellpadding="3" cellspacing="0"><tbody style="margin:0px;padding:0px;"><tr style="margin:0px auto;padding:2px;line-height:normal;border:none;"><td style="margin: 0px auto; outline: none; padding: 2px; line-height: normal;"> 				<div style="text-align: center"><img src="/pictures/news/150019412784999164.jpg" /></div></td> 		</tr><tr style="margin:0px auto;padding:2px;line-height:normal;border:none;"><td style="margin: 0px auto; outline: none; padding: 2px; line-height: normal;"> 				<p class="Image" style="margin-top:5px;margin-bottom:5px;line-height:23px;padding:0px;"> 					Một khu nghỉ dưỡng cao cấp xây dựng trên Sơn Trà. Ảnh:<u style="margin:0px;padding:0px;"><em style="margin:0px;padding:0px;"> </em></u><em style="margin:0px;padding:0px;">Nguyễn Đông.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Phát biểu cuối cùng hội thảo, ông Trương Trọng Nghĩa cho rằng quy hoạch tổng thể do Chính phủ phê duyệt mới đây đưa <span>Sơn Trà thành khu du lịch quốc gia với hơn 4.000 ha trong đó có rừng đặc dụng là không đúng. </span></p><p class="Normal"> 	<span>"</span><span>Phó Thủ tướng Vũ Đức Đam đã khẳng định nếu cần bỏ Sơn Trà ra khỏi quy hoạch du lịch quốc gia thì Chính phủ cũng đồng ý, vậy nên chúng ta nên tập trung vào ý kiến này. Và khi Sơn Trà không còn là khu du lịch quốc gia thì chúng ta sẽ phát triển du lịch Sơn Trà với tư cách là khu bảo tồn thiên nhiên", ông Nghĩa nhấn mạnh.</span></p><p class="Normal"> 	<span class="_5yl5"><span>Các đại biểu đã thống nhất gửi kiến nghị sau hội thảo đến Chính phủ, Quốc hội và UBND TP Đà Nẵng. Trong đó ý kiến cốt lõi là đề xuất điều chỉnh bản quy hoạch khu du lịch quốc gia Sơn Trà, giữ nguyên hiện trạng theo tinh thần “cái gì đã xây thì giữ nguyên, cái gì chưa xây thì hủy bỏ”.</span></span></p><table class="tbl_insert" style="width:100%;" align="center" border="1" cellpadding="1" cellspacing="0"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal" style="margin-top:0px;padding:0px;line-height:18px;background:none;color:rgb(51,51,51);"> 					Bán đảo Sơn Trà cách trung tâm TP Đà Nẵng khoảng 10 km về phía đông bắc, rộng 4.400 ha, là tổng hòa của hệ sinh thái rừng tự nhiên gắn liền biển duy nhất ở Việt Nam. </p> 				<p class="Normal" style="margin-top:0px;padding:0px;line-height:18px;background:none;color:rgb(51,51,51);"> 					<span style="margin:0px;padding:0px;">Từ trước năm 2013, Đà Nẵng cấp phép cho 18 dự án với số phòng ước tính 5.000. Tháng 11/2016, Chính phủ phê duyệt Quy hoạch tổng thể phát triển Khu du lịch quốc gia Sơn Trà với số phòng giảm chỉ còn 1.600.</span></p> 				<p class="Normal" style="margin-top:0px;padding:0px;line-height:18px;background:none;color:rgb(51,51,51);"> 					<span style="margin:0px;padding:0px;">Tuy nhiên, sau khi công bố quy hoạch, Hiệp hội du lịch Đà Nẵng gửi kiến nghị đến Thủ tướng, bày tỏ lo ngại Sơn Trà sẽ bị bê tông hóa bởi các dự án du lịch; kiến nghị xem xét lại bản quy hoạch theo hướng giữ nguyên hiện trạng (với khoảng 300 phòng).</span></p> 				<p class="Normal" style="margin-top:0px;padding:0px;line-height:18px;background:none;color:rgb(51,51,51);"> 					Cuối tháng 5, Phó thủ tướng Vũ Đức Đam đã chủ trì họp với Bộ Văn hoá cùng UBND TP Đà Nẵng, chỉ đạo chưa triển khai quy hoạch Sơn Trà; chấp thuận cho Bộ Văn hoá và Đà Nẵng 3 tháng để trả lời kiến nghị của Hiệp hội du lịch.</p> 				<p class="Normal" style="margin-top:0px;padding:0px;line-height:18px;background:none;color:rgb(51,51,51);"> 					Cuối tháng 6, chủ tịch UBND TP Đà Nẵng Huỳnh Đức Thơ chủ trì buổi họp báo tại địa phương, khẳng định <span style="margin:0px;padding:0px;"><a style="margin:0px;padding:0px;color:rgb(0,79,139);">sẽ giảm các dự án ở Sơn Trà</a>.</span></p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong style="text-align:right;margin:0px;padding:0px;">Nguyễn Đông</strong></p>', 1, '15001941271509751384.jpg', NULL),
(7, 'Dốc cầu ''tử thần'' ở Sài Gòn', 'Hàng loạt tai nạn chết người đã xảy ra ở dốc cầu Phú Mỹ, đoạn từ quận 7 qua quận 2, khiến người dân e ngại khi qua đây.', '<p class="Normal"> 	Dòng xe container, xe tải, ôtô con nối đuôi nhau chạy qua cầu Phú Mỹ (thuộc đường Vành đai 2), chiều 15/7. Dù cầu có gắn biển hạn chế tốc độ (40 km/h) nhưng nhiều xe vẫn phóng ào ào để lên dốc cầu khá cao.</p><p class="Normal"> 	Ở hướng ngược lại, chiếc container biển số TP HCM đang đổ dốc (hướng quận 7 đi quận 2) đột ngột hãm phanh, bẻ lái sang làn khác để tránh tông vào ôtô 4 chỗ màu đỏ phía trước gặp dòng xe ùn ứ. Tiếng rít phanh vang lên khiến nhiều người đi xe máy ở làn bên cạnh loạng choạng.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194128324341204.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hiện trường vụ tai nạn làm 2 người đi xe máy tử vong hồi giữa tháng 6. Ảnh: <em>Sơn Hòa.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Tài xế Trần Anh Tài (quê Đồng Tháp) thường chở hàng qua cầu Phú Mỹ cho biết, mật độ giao thông qua đây luôn cao, nhiều nhất là xe container do là tuyến đường chính ra vào cảng Cát Lái từ các tỉnh miền Tây. Nhiều tài xế hay chạy quá tốc độ cho phép và không giữ khoảng cách an toàn giữa các xe nên tai nạn xảy ra liên tục.</p><p class="Normal"> 	"Cầu này dốc quá cao mà toàn xe chở hàng nặng chạy. Nếu không làm chủ được tay lái rất dễ gây tai nạn liên hoàn, hoặc lật xe gây nguy hiểm cho các xe nhỏ lưu thông làn bên cạnh", anh nói.</p><p class="Normal"> 	Nam tài xế bảo còn nhớ như in vụ tai nạn xảy ra <span style="color:rgb(0,0,0);"><a href="http://vnexpress.net/tin-tuc/thoi-su/giao-thong/oto-container-tong-nhieu-xe-may-khi-do-cau-phu-my-2-nguoi-chet-3599390.html">sáng 14/6</a>. </span>Tr<span style="color:rgb(0,0,0);">ong cơn mưa nhỏ, xe container khi vừa đổ hết dốc cầu (phường Thạnh Mỹ Lợi, quận 2)<span style="color:rgb(0,0,0);"> </span>bất ngờ lao qua phải tông 2 xe máy chạy cùng chiều, ép nhiều xe khác văng vào lề. </span>Hai nạn nhân bị xe container cuốn vào gầm, tử vong. Hiện trường hỗn độn, nhiều người đi qua không khỏi rùng mình.</p><p class="Normal"> 	Thường lái xe ra cao tốc TP HCM - Long Thành - Dầu Giây, anh Hải nhà ở quận 7 cho biết mỗi lần chạy qua cầu Phú Mỹ lại hồi hộp vì tận mắt chứng kiến vụ xe Camry màu đen bẹp dúm trong tai nạn liên hoàn cuối năm ngoái. Tài xế được mọi người đưa ra ngoài chuyển đi cấp cứu nhưng đã tử vong.</p><p class="Normal"> 	"Cảnh tượng kinh hoàng hôm đó khiến tôi rụng rời. Chiếc xe bị nạn đúng chỗ tôi vừa đi qua ít phút", anh Hải kể.</p><p class="Normal"> 	Theo anh Hải, cầu dốc cao, xe container rất đông nên ôtô thường rơi vào thế bị kẹt giữa các container. Chỉ cần tài xế mất tập trung lúc đổ dốc là tai nạn xảy ra ngay", anh Hải nói và bày tỏ mong muốn cơ quan chức năng sớm có biện pháp đảm bảo an toàn giao thông ở khu vực cầu Phú Mỹ.</p><p class="Normal"> 	Trong khi đó, chị Ngọc Thảo (ngụ quận 2) mỗi ngày đều đi làm bằng xe máy qua cầu cũng bị ám ảnh vì tận mắt thấy tại nạn thương tâm xảy ra trên cầu. "Mỗi ngày đi làm đều là mỗi lần lo. Dù có dải phân cách nhưng xe container mà lật thì cái dải bêtông đó chẳng có tác dụng gì. Đi song song bên cạnh như vậy thì taxi, xe bán tải còn sợ huống hồ gì xe máy, xe đạp như tụi tui", chị nói.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941281074977376.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ôtô Camry bị đâm bẹp dúm trong vụ tai nạn vào cuối năm 2016. Ảnh: <em>Sơn Hòa.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span class="_5yl5"><span>Theo thống kê, trong năm 2016 có hơn chục vụ tai nạn nghiêm trọng xảy ra ở khu vực này, riêng 6 tháng đầu năm nay có ít nhất 4 vụ. </span><strong><span style="color:#696969;"><span><span class="_5yl5"><span>Phú Mỹ được đánh giá là cây cầu xảy ra nhiều vụ tai nạn nhất trên địa bàn TP HCM thời gian qua</span></span></span></span></strong><span><span class="_5yl5"><span>.</span></span></span></span></p><p class="Normal"> 	Cơ quan chức năng sau đó đã cho lắp nhiều biển báo hạn chế tốc độ đối với các phương tiện. Xe tải nặng, xe đầu kéo tốc độ cho phép là 40 km/h thay vì 60 km/h như trước, ôtô con và xe máy lưu thông với tốc độ 60 km/h.</p><p class="Normal"> 	Nhìn nhận dốc cầu Phú Mỹ hướng từ quận 7 sang quận 2 là một "điểm đen" về tai nạn giao thông, <span style="color:#000000;">Phó giám đốc Sở Giao thông Vận tải TP HCM Trần Quang Lâm </span>cho rằng, d<span class="_5yl5"><span>ù cầu có độ tĩnh không và dộ dốc lớn nhất thành phố (4,5%) nhưng nằm trong tiêu chuẩn cho phép. Nhiều cây cầu khác ở miền Tây như Cần Thơ, Mỹ Thuận cũng có độ dốc tương đương. </span></span></p><p class="Normal"> 	"Nguyên nhân chính là do tài xế khi qua cầu đã không giữ khoảng cách an toàn, không chạy đúng tốc độ cho phép. Sau vụ tai nạn giữa xe container và xe máy làm 2 người chết hôm 14/6, nhiều đơn vị đã đến khu vực khảo sát, xác minh nguyên nhân trước khi khắc phục", ông Lâm nói.</p><p class="Normal"> 	Ngoài việc gắn các biển giới hạn tốc độ và cảnh báo "khu vực có bắn tốc độ" dọc hai bên cầu, Sở GTVT đã trình UBND TP HCM thông qua dự án mở rộng mặt đường phía dưới chân cầu - đoạn từ chân cầu đến trạm thu phí dài khoảng 1,5 km. Giải pháp này nhằm giảm độ cong của dốc cầu, đồng thời cũng thực hiện phân làn giữa ôtô và xe máy để hạn chế tai nạn.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194128623707609.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dốc cầu Phú Mỹ (hướng từ quận 7 qua quận 2) - nơi thường xuyên xảy ra tai nạn nghiêm trọng. Ảnh: <em>Hà Giang</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trong khi đó, chủ đầu tư cầu Phú Mỹ cho biết đã trình Sở GTVT TP HCM về dự án đầu tư khoảng 14 tỷ đồng lắp đặt hệ thống camera giám sát giao thông và tốc độ tại cầu Phú Mỹ... Qua đó, ngoài giám sát giao thông, các camera sẽ ghi nhận tốc độ và trích xuất cho CSGT phạt "nguội" các phương tiện vi phạm.</p><p class="Normal"> 	Dự án này cũng được Sở GTVT trình UBND TP HCM chờ thông qua như dự án mở rộng đường dưới chân cầu vì có liên quan đến hợp đồng BOT ký kết giữa TP HCM với chủ đầu tư.</p><p class="Normal"> 	Khởi công tháng 2/2005, cầu Phú Mỹ nối quận 2 và 7 được đầu tư 2.077 tỷ đồng. Tại thời điểm khánh thành vào lễ Quốc khánh năm 2009, đây là cầu dây văng có quy mô lớn nhất nước với chiều dài hơn 2 km, tĩnh không thông thuyền cao 45 m và được xem là cây cầu biểu tượng của TP HCM.</p><p class="Normal" style="text-align:right;"> 	<strong>Hữu Nguyên - </strong><strong>Hà Giang </strong></p>', 1, '15001941281180322334.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(8, 'Cựu binh hơn 30 năm bám trụ chiến trường tìm hài cốt đồng đội', 'Mang theo balô và chiếc xẻng, ông Lâm ở Huế trở lại chiến trường xưa tìm kiếm hài cốt đồng đội hy sinh trong kháng chiến chống Mỹ.', '<p class="Normal"> 	Ngày đầu tháng 7, trong ngôi nhà khang trang mới xây dựng ở tổ 10, phường Thủy Phương (thị xã Hương Thủy, Thừa Thiên Huế), ông Võ Văn Lâm lại hồi tưởng về những ngày đi tìm mộ liệt sĩ khi có khách hỏi thăm.</p><p class="Normal"> 	<span>Năm 1962, ông Lâm thoát ly, tham gia vào lực lượng vũ trang Hương Thủy, chiến đấu chống Mỹ tại Bình Trị Thiên. Trong trận đánh Mậu Thân 1968, ông đã chôn cất một đồng đội hy sinh ở bụi tre xã Thủy Vân. Sau này, ông cùng gia đình tìm được hài cốt liệt sĩ, đưa về an táng tại nghĩa trang quê nhà</span><span>.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194129902537587.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Võ Văn Lâm cùng thị đội Hương Thủy trên đường tìm hài cốt liệt sĩ ở khe 57. Ảnh: <em>Thị đội Hương Thủy.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Chứng kiến cảnh người nhà rưng rưng xúc động khi tìm thấy hài cốt liệt sĩ, ông Lâm lại nghĩ về đồng đội cùng chiến đấu với mình đang nằm đâu đó ở vùng rừng núi chiến khu Dương Hòa (xã Dương Hòa, thị xã Hương Thủy). Về hưu năm 1981, với balô cùng chiếc xẻng, người Đại đội trưởng lực lượng vũ trang Hương Thủy năm xưa trở lại chiến trường để tìm hài cốt liệt sĩ.</p><p class="Normal"> 	Chiến trường xưa đã thay đổi, những vị trí đánh dấu địa điểm chôn cất liệt sĩ không còn nữa, thay thế là cánh đồng, vườn tược, công trình xây dựng. Ông Lâm cố gắng kết nối các đồng đội còn sống, đơn vị bộ đội, xác minh địa điểm chôn cất và đến nay tìm được hơn 50 bộ hài cốt liệt sĩ đưa về nghĩa trang an táng.</p><p class="Normal"> 	Năm 1994, ông ở lại hẳn chiến khu xưa, vừa khai khẩn đất đai làm kinh tế, vừa tìm kiếm hài cốt đồng đội.</p><p class="Normal"> 	Trong hàng trăm chuyến đi, ông Lâm nhớ mãi chuyến tìm đồng đội tên Kim hy sinh năm 1962 ở vùng rừng núi huyện Nam Đông, được ông chôn cất dưới gốc cây rừng. Năm 1983, người nhà anh Kim đến nhờ ông tìm kiếm hài cốt. Mang theo xẻng, balô, ông quay trở lại chiến trường xưa.</p><p class="Normal"> 	<span>Cứ nghĩ trực tiếp chôn cất thì sẽ tìm ra sớm, nhưng thời gian đã làm thay đổi địa hình, ông rất khó xác định vị trí nấm mồ. Hơn một tháng đào bới nhiều địa điểm trong rừng, ông mới tìm thấy hài cốt đồng đội dưới gốc cây khô để đưa về nghĩa trang thị trấn Khe Tre (huyện Nam Đông) an táng</span><span>.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941291806472097.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Lâm vẫn day dứt khi chưa tìm ra trạm xá Nam nơi có nhiều đồng đội chôn cất. Ảnh: <em>Võ Thạnh.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cựu binh 74 tuổi với mái tóc gần bạc trắng bảo vẫn day dứt khi chưa tìm được trạm xá Nam, nơi ông dưỡng thương năm 1964. Là nơi điều trị cho các chiến sĩ thuộc lực lượng vũ trang 3 huyện phía Nam là Hương Thủy, Phú Lộc, Phú Vang, quanh trạm xá Nam có rất nhiều liệt sĩ được chôn cất.</p><p class="Normal"> 	"Ngày ấy, người lính nào hy sinh cũng được cuốn lại bằng tấm nylon, sau đó chôn tại gốc cây để sau này có thể nhận biết. Nếu sức khỏe cho phép, tôi muốn tìm ra trạm xá Nam. Hy vọng những chuyến đi sau tôi sẽ tìm được", ông Lâm chia sẻ.</p><p class="Normal"> 	Thượng tá Nguyễn Quang Cư, Chỉ huy trưởng Thị đội Hương Thủy cho biết, nhiều năm qua đơn vị đã phối hợp ông Lâm đi khảo sát các vùng đóng quân trước đây tại chiến khu Dương Hòa. Nhờ có ông, thị đội đã quy tập nhiều mộ liệt sĩ đưa về nghĩa trang thị xã an táng trong sự vui mừng của gia đình thân nhân.</p><p class="Normal"> 	<span>"Gần đây nhất dịp 30/4 vừa qua, bác Lâm cùng đơn vị tìm và quy tập được 2 hài cốt liệt sĩ ở khe 57 xã Dương Hòa. Mặc dù tuổi cao, nhưng khi chúng tôi đề nghị, bác lại mang dép rọ cùng anh em lên đường", thượng tá Cư kể</span><span style="color:rgb(34,34,34);">.</span></p><p class="Normal" style="margin:0cm 0cm 7.5pt;text-align:right;"> 	<strong><span style="color:rgb(34,34,34);">Võ Thạnh</span></strong></p>', 1, '1500194129917925925.jpg', NULL),
(9, 'Sẽ cắt điện, nước các chung cư vi phạm quy định phòng cháy', 'Ông Hoàng Trung Hải ủng hộ việc cơ quan chức năng cưỡng chế đưa thiết bị chữa cháy vào các toà nhà có vi phạm, buộc chủ đầu tư chi trả.', '<p class="Normal"> 	Tại buổi làm việc của Thường trực thành uỷ Hà Nội với Sở cảnh sát Phòng cháy chữa cháy (PCCC) sáng 15/7, Phó chủ tịch UBND TP Nguyễn Văn Sửu nhắc lại quan điểm của thành phố về việc kiên quyết xử lý các công trình vi phạm quy định PCCC, trong đó có biện pháp cắt điện, nước.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194131231751401.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phó chủ tịch UBND TP Hà Nội Nguyễn Văn Sửu phát biểu tại buổi làm việc với Sở cảnh sát PCCC sáng 15/7. Ảnh: <em>Võ Hải.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Theo ông Sửu, thành phố sẽ có thông báo kết luận với 79 chung cư vi phạm PCCC mà Sở Cảnh sát PCCC công bố cuối tháng 5. Trong đó, thành phố sẽ xử lý điểm một đến ba công trình. </p><p class="Normal"> 	<span>Quy trình cưỡng chế các công trình vi phạm đang được Sở cảnh sát PCCC xây dựng, dự kiến hoàn thành trong tháng 7.</span></p><p class="Normal"> 	“Những công trình đang xây dựng chưa đưa vào sử dụng, UBND TP sẽ ra văn bản yêu cầu các đơn vị liên quan không cấp điện, nước. Đơn vị nào cố tình cấp nước, điện thì người đứng đầu sẽ bị xử lý hình sự”, ông Sửu nói.</p><p class="Normal"> 	Cũng theo Phó chủ tịch thành phố, tới đây lãnh đạo quận, huyện, phường, xã cũng phải chịu trách nhiệm nếu để xảy ra cháy nổ trên địa bàn, <span>“không thể để tình trạng  công trình mấy chục tầng vi phạm PCCC lại nhảy lên bờ không có trách nhiệm gì cả”.</span></p><p class="Normal"> 	Lãnh đạo chính quyền Hà Nội cũng khuyến cáo người dân khi mua chung cư, cần tìm hiểu rõ tính pháp lý của khu nhà, trong đó có việc đảm bảo an toàn PCCC.</p><p class="Normal"> 	Phó chủ tịch thường trực HĐND TP Hà Nội Nguyễn Ngọc Tuấn thông tin, trong 79 chung cư vi phạm PCCC, thành phố báo cáo đã xử lý 7 trường hợp. Với 72 công trình còn lại, 71 công trình chưa có dân vào ở, một công trình đang xây dựng.</p><p class="Normal"> 	Ông Tuấn cho rằng, một trong những biện pháp răn đe với chủ đầu tư có công trình vi phạm là không giao dự án mới. Nhưng thực tế, nhiều chủ đầu tư của 79 công trình vi phạm nêu trên vẫn được giao dự án mới. Do đó, ông đề nghị Sở Kế hoạch Đầu tư kiểm tra lại việc này, p<span>hải xây dựng cả phương án di dời dân và yêu cầu chủ đầu tư khắc phục xong mới cho dân vào ở lại.</span></p><p class="Normal"> 	“Cần phải có thông điệp mạnh mẽ. Nếu xử lý không nghiêm lại có dự án khác vi phạm”, ông Tuấn nói.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194132731708571.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bí thư Thành uỷ Hà Nội Hoàng Trung Hải (thứ 2 từ phải qua) tới thăm lực lượng cảnh sát PCCC. Ảnh:<em> Võ Hải.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Theo Bí thư Thành uỷ Hà Nội Hoàng Trung Hải, chủ đầu tư không thực hiện các quy định về PCCC thì phải cưỡng chế để đảm bảo an toàn cho nhân dân và các tổ chức.</p><p class="Normal"> 	Ông Hải đề nghị, nếu phát hiện chung cư vi phạm thì cơ quan chức năng phải đưa trang thiết bị vào để đảm bảo an toàn cho dân, chi phí sẽ do người vi phạm chi trả. Nếu chỉ xử phạt hành chính, mức phạt không đủ tính răn đe. Hơn thế, sau khi xử lý hành chính, nếu xảy ra cháy nổ, trách nhiệm vẫn là phía chính quyền.</p><p class="Normal"> 	“Một chung cư, kể cả đảm bảo các phương tiện PCCC thì khi bị cháy nổ vẫn tiềm ản nguy cơ thiệt hại. Nếu không được trang bị đủ, thiệt hại có thể lớn hơn nhiều”, ông Hải nói.</p><p class="Normal"> 	Lãnh đạo thành ủy yêu cầu các đơn vị liên quan phân công rõ trách nhiệm xây dựng quy trình cưỡng chế, thời gian hoàn thành, để xử lý nghiêm các chủ đầu tư vi phạm.</p><p class="Normal" style="text-align:center;"> 	<a title="Danh sách 79 chung cư vi phạm quy định PCCC" href="http://vnexpress.net/tin-tuc/thoi-su/se-cat-dien-nuoc-cac-chung-cu-vi-pham-quy-dinh-phong-chay-3613910-p2.html"><strong>Danh sách 79 chung cư vi phạm quy định PCCC</strong></a></p><p class="Normal" style="text-align:right;"> 	<strong>Võ Hải</strong></p>', 1, '15001941311092990074.jpg', NULL),
(10, 'Quảng Nam xác minh thông tin người Trung Quốc gom mua đất ven biển', 'Trước việc giá đất tăng nhanh và thông tin người Việt Nam đứng tên mua đất cho người Trung Quốc, UBND tỉnh Quảng Nam chỉ đạo xác minh.', '<p class="Normal"> 	Ngày 15/7, UBND tỉnh Quảng Nam cho biết đang kiểm tra thông tin ở xã ven biển Tam Thanh, TP Tam Kỳ có tình trạng người Việt Nam đứng tên mua đất bán cho người Trung Quốc.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194133294735612.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hơn 110 bức tranh thuyền thúng được sắp đặt dưới tán cây dừa, bãi biển xã Tam Thanh. Ảnh: <em>Hà Nguyễn.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>UBND tỉnh Quảng Nam yêu cầu Sở Tài nguyên và Môi trường, TP Tam Kỳ rà soát các trường hợp có dấu hiệu phức tạp. “Đề nghị các ban ngành theo dõi nắm bắt tình hình đề có biện pháp chấn chỉnh; đồng thời tổ chức tuyên truyền để nhân dân địa phương hiểu, tránh bị kẻ xấu lợi dụng”, văn bản nêu.</span></p><p class="Normal"> 	Trong diễn biến khác, ông Văn Anh Tuấn, Chủ tịch TP Tam Kỳ khẳng định chưa phát hiện trường hợp nào bán đất cho người nước ngoài ở xã Tam Thanh. “Thành phố kiểm tra, giám sát việc này rất kỹ”, ông Tuấn nói.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194133909821669.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Làng bích họa Tam Thanh thu hút hàng ngàn lượt khác tham quan. Ảnh: <em>Đắc Thành</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Một lãnh đạo xã Tam Thanh thông tin, giá đất tại phương địa bàn xã lên nhanh. Trước đây một mét vuông vài trăm ngàn đồng nhưng nay lên đến 4 triệu đồng.</span></p><p class="Normal"> 	Xã Tam Thanh được biết đến là làng bích họa với hơn 100 bức tranh trên tường nhà do các hoạ sĩ Hàn Quốc thực hiện. Mới đây, nhiều họa sĩ cũng hoàn thành hơn 110 bức tranh vẽ trên thuyền thúng. Số tranh này được xác lập kỷ lục “Bộ sưu tập tranh vẽ trên thuyền thúng đầu tiên và nhiều nhất Việt Nam”.</p><p class="Normal"> 	Điểm nhấn này giúp Tam Thanh thu hút hàng nghìn lượt khách tham quan, tạo sinh kế cho người dân.</p><p class="Normal" style="text-align:right;"> 	<strong>Đắc Thành</strong></p>', 1, '15001941331065696951.jpg', NULL),
(11, 'Xe khách giường nằm cháy dữ dội ở Nghệ An', 'Vừa trả xong khách, chiếc xe giường nằm đang đỗ bên vỉa hè bốc cháy dữ dội đến trơ khung.', '<div style="text-align:center"> 	 	<div> 		<div id="video-156925" data-component="true" data-component-type="video" data-component-value="156925" data-component-typevideo="2"></div></div> 	 	<div style="text-align: left;"> 		<span style="color:rgb(0,0,0);text-align:left;">Chiều 15/7, xe khách giường nằm mang biển kiểm soát TP HCM dừng tại thị trấn huyện Con Cuông (Nghệ An) thì bất lửa ở phần đầu xe bốc lên. Một số người hô hoán tìm cách dập lửa song bất thành. Lửa nhanh chóng bao trùm toàn xe, cột khói bốc cao cả chục mét.</span></div> </div><p class="Normal"> 	Thời điểm xảy ra cháy, may mắn nhà xe vừa trả xong khách vào quán ăn cơm. </p><p class="Normal"> 	Cảnh sát phòng cháy chữa cháy Nghệ An điều xe chữa cháy tiếp cận hiện trường. <span>Vài chục phút sau, ngọn lửa được khống chế, song chiếc xe đã bị thiêu rụi, trơ khung sắt.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194134877586270.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chiếc xe trơ khung sau khi được khống chế ngọn lửa. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Xe khách gặp nạn của nhà xe Lam Hồng chạy tuyến huyện Con Cuông - TP HCM. Nhà chức trách Nghệ An đang khám nghiệm, làm rõ nguyên nhân.</span></p>', 1, '15001941341417880236.jpg', NULL),
(12, 'Bão cấp 8 hướng Thanh Hóa - Hà Tĩnh', 'Dự kiến ngày 17/7, cơn bão thứ hai trong năm sẽ đi vào đất liền các tỉnh Thanh Hóa - Hà Tĩnh với sức gió mạnh nhất 75 km/h. ', '<p class="Normal"> 	Trung tâm dự báo khí tượng thủy văn Trung ương cho biết, chiều nay áp thấp nhiệt đới đã mạnh lên thành bão, cơn bão số 2 trong năm ở biển Đông, tên quốc tế là Talas. </p><p class="Normal"> 	L<span>úc 13h, bão trên khu vực quần đảo Hoàng Sa, sức gió mạnh nhất 75 km/h (cấp 8). Với vận tốc 15 km/h, theo hướng tây bắc, đến chiều 16/7 bão cách bờ biển Nam Định - Nghệ An khoảng 320 km, sức gió không đổi.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194135734152316.gif" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dự báo đường đi của bão số 2. Ảnh: <em>NCHMF.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Ngày 17/7, bão trên bờ biển các</span><span> tỉnh Nam Định - Hà Tĩnh, cường độ gió giữ nguyên 75 km/h, giật cấp 9-10. </span><span>Tiếp đó, nó đi sâu vào đất liền các tỉnh Thanh Hoá - Hà Tĩnh và suy yếu thành vùng áp thấp. </span><span>Vùng nguy hiểm cho tàu thuyền là vĩ tuyến</span><span> 15,5 đến 20 độ; kinh tuyến 113 độ.</span></p><p class="Normal"> 	<span>Hoàn lưu bão gây mưa cho Bắc và Bắc Trung Bộ từ ngày 16 đến 18/7 với lượng mưa 100-300 mm. Hà Nội dù không nằm trên đường đi của bão nhưng vẫn có mưa lớn, khả năng ngập úng cao.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941351677087540.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dự báo đường đi bão của Đài khí tượng Hong Kong. Ảnh: <em>hko.gov.hk.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trên biển, trong đêm nay và ngày mai, <span>khu vực giữa và nam biển Đông (bao gồm cả vùng biển quần đảo Trường Sa), vùng biển từ Ninh Thuận đến Cà Mau tiếp tục có gió mạnh cấp 6, giật cấp 7-8; sóng biển cao 2-3 m. Vùng biển từ Cà Mau đến Kiên Giang và vịnh Thái Lan mưa rào và giông mạnh. </span></p><p class="Normal"> 	<strong><span>Cấm biển từ chiều 16/7</span></strong></p><p class="Normal"> 	<span>Chiều 15/7, Ban chỉ đạo Trung ương về phòng chống thiên tai có công điện yêu cầu các tỉnh ven biển từ Hà Tĩnh đến Quảng Ninh trước 17h ngày 16/7 phải hoàn thành việc kêu gọi tàu thuyền về nơi trú ẩn, cấm biển kể cả đối với tàu vận tải và du lịch. Địa phương phải sơ tán dân ở lồng bè, chòi canh thủy sản lên bờ; chỉ đạo chằng chống nhà cửa, đảm bảo an toàn hầm lò, bến cảng, khu du lịch. </span></p><p class="Normal"> 	<span>Các tỉnh </span><span>Bắc Bộ và Bắc Trung Bộ được yêu cầu tiêu thoát nước vùng đô thị và ruộng đồng, đề phòng úng ngập; kiểm tra đê điều, hồ đập, có phương án đề phòng sự cố. Các ngầm tràn, đường ngập nước, đò ngang, đò dọc cần có lực lượng kiểm soát, đảm bảo an toàn khi mưa lũ.</span></p><p class="Normal"> 	<span>Talas là cơn bão thứ hai xuất hiện ở biển Đông trong năm nay. Cơn bão đầu tiên hình thành ngày 11/6, sau đó đi vào Trung Quốc.</span></p><p class="Normal"> 	<span>Theo cơ quan khí tượng, mùa mưa bão 2017 có khoảng 13-15 cơn bão và áp thấp nhiệt đới hoạt động trên biển Đông. Trong đó 3-4 cơn đổ bộ, ảnh hưởng trực tiếp đến đất liền Việt Nam, tập trung ở Trung Bộ</span><span style="color:rgb(51,51,51);">.</span></p>', 1, '15001941351812459889.gif', NULL),
(13, 'Ôtô tông gần chục xe, lao vào cửa hàng ở Sài Gòn', 'Xe bán tải ôm cua ở quận 10 lao qua đường tông 3 ôtô, 5 xe máy khiến 3 người bị thương nhập viện.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div> 			<div id="video-156899" data-component="true" data-component-type="video" data-component-value="156899" data-component-typevideo="2"></div></div> 	</div> 	<p class="Normal" style="text-align:left;"> 		<span>Trưa 15/7, ông Lâm Văn Giang (41 tuổi) chạy ôtô bán tải trên đường 3 Tháng 2, hướng quận 10 đi quận 11. Đến giao lộ Nguyễn Kim (phường 14, quận 10), tài xế Giang cho xe rẽ trái để quay đầu.</span></p> </div><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941361802072156.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ô tô lao vào cửa hàng sau khi tông nhiều ôtô và xe máy. Ảnh: <em>Duy Trần.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Đang ôm cua, xe bất ngờ húc vào dải phân cách bằng sắt rồi lao qua đường, tông liên tiếp vào xe tải, 2 ôtô và 5 xe máy. Chiếc bán tải chỉ dừng lại khi đâm thẳng vào cửa hàng bên đường.</p><p class="Normal"> 	Tai nạn khiến chủ cửa hàng và hai mẹ con đi xe máy bị thương, được người dân chuyển vào bệnh viện. Những người còn lại bị xây xát. Các ôtô và xe máy đều hư hỏng nặng.</p><p class="Normal"> 	"Tôi đang chạy xe thì chiếc ôtô lao qua đường với tốc độ nhanh, tông tôi và nhiều xe khác. Chân tôi bị kẹt dưới xe máy, nhiều người xúm lại gỡ mới ra được", chị Nguyễn Thị Thanh Thúy, nạn nhân vụ tai nạn kể.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019413667069018.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các xe máy bị ôtô cán qua, hư hỏng. Ảnh: <em>Duy Trần.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Do ảnh hưởng của vụ tai nạn khiến giao thông qua khu vực gặp nhiều khó khăn. Đến 12h30, hiện trường được giải quyết xong. Nguyên nhân đang được cảnh sát điều tra. </p><p class="Normal" style="text-align:right;"> 	<strong>Duy Trần</strong></p>', 1, '1500194136143982627.jpg', NULL),
(14, 'Người cuối cùng của đội Việt Nam tuyên truyền giải phóng quân qua đời', 'Ông Tô Đình Cắm - một trong 34 chiến sĩ đầu tiên của Quân đội nhân dân Việt Nam qua đời tại Lâm Đồng, hưởng thọ 95 tuổi.', '<p class="Normal"> 	Sáng 15/7, ông Bùi Văn Hùng - Chủ tịch UBND huyện Đạ Tẻh, Lâm Đồng - cho biết ông Tô Đình Cắm (95 tuổi), người duy nhất còn lại trong 34 đội viên đầu tiên của Đội Việt Nam tuyên truyền giải phóng quân, đã qua đời đêm qua tại nhà riêng ở thị trấn Đạ Tẻh.</p><p class="Normal"> 	Ngành chức năng huyện Đạ Tẻl, Tỉnh đội Lâm Đồng cùng người nhà đang tổ chức lễ tang cho cụ.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941381980951892.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Cắm là một trong 34 chiến sĩ đầu tiên của Quân đội nhân dân Việt Nam. Ảnh: <em>Hoài Thanh.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ông Cắm, bí danh Tiến Lực, là người dân tộc Tày, sinh ra ở bản Um, xã Tam Kim của huyện Nguyên Bình, Cao Bằng. Năm 1941, ông tham gia cách mạng đánh đuổi thực dân Pháp.</p><p class="Normal"> 	Ngày 22/12/1944, ông vinh dự là một trong số 34 người tuyên thệ đứng vào hàng ngũ đội viên Đội Việt Nam tuyên truyền giải phóng quân - tiền thân của Quân đội nhân dân Việt Nam - dưới sự chỉ huy của Đại tướng Võ Nguyên Giáp.</p><p class="Normal"> 	Năm 1946, ông theo đoàn quân "Nam tiến", đóng quân ở Rạch Giá (Kiên Giang). Trong một trận càn, ông bị thương nặng nên đơn vị đưa ra Đà Nẵng điều trị rồi giải ngũ.</p><p class="Normal"> 	Lúc quân Pháp nhảy dù, thả bom xuống Bắc Kạn năm 1947, ông lại xung phong tái ngũ, giữ chức Trung đội trưởng Trung đội pháo binh. Sau chiến thắng Điện Biên Phủ, ông giải ngũ về với đời thường và tích cực tham gia công tác địa phương.</p><p class="Normal"> 	Đầu thập niên 1990, ông đưa gia đình vào thị trấn Đạ Tẻl làm kinh tế mới dù khi ấy tuổi đã ngoài 70 và sống ở đây cho đến nay.</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Đội Việt Nam tuyên truyền giải phóng quân được thành lập tại Cao Bằng với 34 chiến sĩ, chia làm 3 tiểu đội do Đại tướng Võ Nguyên Giáp trực tiếp chỉ huy. Trong số 34 cán bộ, chiến sĩ thì có 29 người dân tộc thiểu số miền núi phía Bắc. Đây là đơn vị chủ lực đầu tiên của lực lượng vũ trang cách mạng và là tiền thân của Quân đội nhân dân Việt Nam.</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Hoài Thanh</strong></p>', 1, '15001941381249916249.jpg', NULL),
(15, 'Taxi bị vò nát vì va chạm tàu hỏa ở Hà Nội', 'Taxi bị tàu hỏa kéo lê 20 mét rồi văng vào dải chắn ven đường, tài xế nhập viện trong tình trạng nguy kịch.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194139749617461.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hiện trường vụ tai nạn. Ảnh: <em>Trần Hoàng.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Khoảng 9h30 ngày 15/7, tại Km12+500 đường Ngọc Hồi (Hà Nội), chiếc taxi rẽ vào Viện điều tra quy hoạch rừng thì đúng lúc tàu hỏa lao tới, đâm trúng. Ôtô bị kéo lê 20 mét rồi văng vào dải chắn ven đường. </p><p class="Normal"> 	<span>Tài xế Đỗ Doãn Thương (27 tuổi, trú Hà Nội) được đưa đi cấp cứu tại Bệnh viện Nông nghiệp trong tình trạng nguy kịch. </span></p><p class="Normal"> 	Một nhân chứng cho biết, đoạn đường ngang dân sinh này không có gác chắn, khi tàu hỏa gần đến tài xế cố vượt nên xảy ra tai nạn. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941401396761327.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sau khi bị kéo lê 20 mét, taxi hư hỏng nặng. Ảnh: <em>Trần Hoàng.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Tại hiện trường, taxi bẹp rúm phần đầu. Hai cột thông tin trên đường sắt cũng bị hư hại. Đoàn tàu chở khách phải dừng để khắc phục sự cố.</span></p><p class="Normal"> 	Ban quản lý ga Văn Điển đã đưa taxi ra khỏi khu vực đường sắt để đảm bảo an toàn.</p><p class="Normal" style="text-align:right;"> 	<strong>Phạm Dự</strong></p>', 1, '1500194139103797697.jpg', NULL),
(16, 'Thổ Nhĩ Kỳ có thể chi 2,5 tỷ USD mua tên lửa S-400 Nga', 'Quan chức Thổ Nhĩ Kỳ cho biết nước này lựa chọn tổ hợp S-400 của Nga dù chúng không tương thích với hệ thống của NATO.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-157034" data-component="true" data-component-type="video" data-component-value="157034" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Tổ hợp S-400 khai hỏa.</p> </div><p class="Normal"> 	Một quan chức cấp cao Thổ Nhĩ Kỳ cho biết nước này đã đặt mua hệ thống tên lửa phòng không tầm xa S-400 Triumf trị giá 2,5 tỷ USD của Nga<span>, </span><em>Bloomberg </em><span>hôm 13/7 đưa tin</span><span>. </span></p><p class="Normal"> 	<span>Quan chức giấu tên này nói rằng hai tổ hợp S-400 đầu tiên sẽ được Nga bàn giao vào năm 2018, còn hai tổ hợp tiếp theo sẽ được sản xuất trên lãnh thổ Thổ Nhĩ Kỳ. </span><span>Tuy nhiên, đại diện tập đoàn xuất khẩu vũ khí Nga Rosoboronexport chưa đưa ra bình luận nào về tuyên bố này</span><span>.</span></p><p class="Normal"> 	Thổ Nhĩ Kỳ từng định mua hệ thống tên lửa phòng không HQ-9 từ Trung Quốc, nhưng vấp phải sự phản đối gay gắt từ khối NATO và buộc phải từ bỏ ý định. Là thành viên của khối quân sự này kể từ năm 1952, Thổ Nhĩ Kỳ được coi là tiền đồn quan trọng của phương Tây do nằm gần biên giới Nga.</p><p class="Normal"> 	Tuy nhiên, điều này đang dần thay đổi vì căng thẳng gần đây giữa Thổ Nhĩ Kỳ và phương Tây. Mỹ liên tục ủng hộ và viện trợ quân sự cho dân quân người Kurd ở Syria, vốn bị Thổ Nhĩ Kỳ coi là lực lượng khủng bố. Đức mới đây rút các đơn vị phòng không khỏi sân bay Incirlik của Thổ Nhĩ Kỳ, sau khi nước này không cho phép quan chức Đức đến thăm binh sĩ đồn trú tại đây.</p><p class="Normal"> 	"Thổ Nhĩ Kỳ rõ ràng đã quá thất vọng với Mỹ và các nước châu Âu", chuyên gia Konstantin Makienko thuộc Trung tâm Phân tích chiến lược và công nghệ Nga (CAST) nhận định. Tuy nhiên, ông Makienko cho rằng chưa thể chắc chắn điều gì về hợp đồng S-400 cho đến khi quá trình thanh toán và chuyển giao bắt đầu.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194142111885197.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một xe phóng đạn trong tổ hợp tên lửa S-400. Ảnh: <em>Kommersant</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	S-400 không tương thích với tiêu chuẩn vũ khí chung của NATO, khiến Thổ Nhĩ Kỳ không thể tích hợp nó vào hệ thống phòng không của các nước đồng minh. Tuy nhiên, hợp đồng với Nga lại cho phép Thổ Nhĩ Kỳ sở hữu công nghệ chế tạo tên lửa, điều mà nước này đang rất cần.</p><p class="Normal"> 	Ông Makienko cho rằng Ankara sẽ không được Nga chuyển giao toàn bộ công nghệ S-400, Moscow vẫn nắm giữ những kỹ thuật quan trọng nhất để đảm bảo bí mật. Thổ Nhĩ Kỳ cũng sẽ phải tự xây dựng cả một nền công nghiệp quốc phòng mới chỉ để sao chép được S-400.</p><p class="Normal"> 	Quan chức Thổ Nhĩ Kỳ cho biết hệ thống S-400 mua từ Nga sẽ không có hệ thống nhận biết địch - ta, giúp nó đối phó bất kỳ mối đe dọa nào, dù là từ Nga hay NATO.</p><p class="Normal" style="text-align:right;"> 	<strong>Lã Linh</strong></p>', 1, '1500194142959709836.jpg', NULL),
(17, 'Triều Tiên dọa trả đũa Mỹ nếu bị áp thêm lệnh trừng phạt', 'Bình Nhưỡng lên tiếng cảnh báo Washington khi áp lực trừng phạt của Hội đồng Bảo an Liên Hợp Quốc đang gia tăng.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941432133052198.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tên lửa đạn đạo Hwasong-14 của Triều Tiên có thể phóng đến lãnh thổ Mỹ. Ảnh minh hoạ: <em>Yonhap.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>"Sẽ là sai lầm chết người nếu nghĩ rằng một cường quốc hạt nhân </span><span>sở hữu tên lửa đạn đạo liên lục địa </span><span>như Triều Tiên dung thứ cho các biện pháp trừng phạt khinh suất mang tính hăm doạ của Mỹ và các lực lượng thù địch khác", p</span><span>hát ngôn viên Bộ Ngoại giao Triều Tiên </span><span>ngày 15/7</span><span> tuyên bố trên tờ </span><span><em>Rodong Sinmun.</em> </span></p><p class="Normal"> 	<span>Người phát ngôn này cho rằng Mỹ là nước nỗ lực đưa </span><span>một nghị quyết trừng phạt Triều Tiên khắc nghiệt chưa từng có </span><span>ra </span><span>Hội đồng Bảo an Liên Hợp Quốc, để đáp trả việc Bình Nhưỡng phóng thành công tên lửa đạn đạo liên lục địa </span><span>Hwasong-14 hôm 4/7. Ông kêu gọi Mỹ "thể hiện sự thận trọng, tính đến vị thế chiến lược của Triều Tiên đã ở mức độ cao chưa từng thấy".</span></p><p class="Normal"> 	<span>Người này còn cảnh báo Triều Tiên sẽ có các biện pháp "ăn miếng trả miếng" nếu <span>Hội đồng Bảo an </span>thông qua nghị quyết mới, cho phép áp dụng các biện pháp trừng phạt nặng nề hơn với Bình Nhưỡng. </span></p><p class="Normal"> 	Bà <span>Nikki Haley, </span><span>Đại sứ Mỹ tại </span><span>Liên Hợp Quốc </span><span>tuần trước cho biết Washington sẽ thảo nghị quyết của <span>Hội đồng Bảo an </span>về các lệnh trừng phạt mới với Triều Tiên. Bà cho rằng các nước cần cắt giảm các nguồn cung tài chính quan trọng với Bình Nhưỡng, tăng các hạn chế trên không và trên biển, áp lệnh trừng phạt với các quan chức cấp cao liên quan đến chương trình hạt nhân và tên lửa của nước này. Anh và Pháp đã thể hiện sự ủng hộ với các lệnh trừng phạt mới.</span></p><p class="Normal"> 	Trong khi đó, Nga cảnh báo động thái này, cho rằng lệnh trừng phạt không giúp giải quyết vấn đề, mà còn dẫn tới sự bế tắc trong nỗ lực giải quyết khủng hoảng Triều Tiên thông qua các biện pháp chính trị.</p><p class="Normal" style="text-align:right;"> 	<strong>Khánh Lynh</strong></p>', 1, '1500194143873301985.jpg', NULL),
(18, 'Ngoại trưởng Australia sốc khi Trump khen hình thể vợ Macron', 'Bà Julie Bishop thể hiện sự phản đối với nhận xét của Tổng thống Mỹ về vóc dáng của Đệ nhất phu nhân Pháp.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941442053624319.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngoại trưởng Australia Julie Bishop. Ảnh: <em>ABC News.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Tôi băn khoăn là bà ấy có thể đưa ra lời bình phẩm tương tự với ông Trump không? Nếu là tôi, tôi sẽ bị sốc", <span>Ngoại trưởng Australia Julie Bishop hôm nay trả lời </span><em><span>ABC </span></em><span>về lời bình luận mà Tổng thống Mỹ Donald Trump dành cho Đệ nhất phu nhân Pháp Brigitte Macron.</span></p><p class="Normal"> 	<span>Trong cuộc gặp vợ chồng </span><span>Tổng thống Pháp </span><span>Emmanuel Macron </span><span>tại Điện Invalides, Paris, hôm 13/7, ô</span><span>ng Trump nói với bà </span><span>Brigitte </span><span>rằng bà "có </span><span>vóc dáng đẹp".</span></p><p class="Normal"> 	<span>Ngoại trưởng Australia nhận xét thêm về cách sử dụng mạng xã hội của Tổng thống Mỹ, nói rằng bà sẽ "không viết bất cứ bình luận nào trên tài khoản Twitter của ông ấy".</span></p><p class="Normal"> 	<span>Ông Trump từng nhiều lần bị chỉ trích vì các phát ngôn công kích cá nhân với những người nổi tiếng và phản cảm về phụ nữ. Ngay trước cuộc bầu cử năm 2016, ông phải công khai xin lỗi vì có bình luận thiếu tế nhị về phụ nữ. Hồi tháng 6, ông viết trên Twitter rằng cô Mika Brzezinski, dẫn chương trình của đài MSNBC, là kẻ "điên khùng", "kém thông minh" và "tâm thần".</span></p><p class="Normal"> 	Tổng thống Mỹ vừa có chuyến thăm Pháp kéo dài hai ngày nhằm tăng cường hợp tác song phương. Ông Trump và người đồng cấp Macron trước đó khẳng định họ <span>muốn gạt sang một bên những bất đồng về thương mại và biến đổi khí hậu để tìm kiếm tiếng nói chung.</span></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-157026" data-component="true" data-component-type="video" data-component-value="157026" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Tổng thống Mỹ khen Đệ nhất phu nhân Pháp có hình thể đẹp. Video:<em> Reuters. </em></p> 	<p class="Normal" style="text-align:right;"> 		<strong style="text-align:right;"><span> Khánh Lynh</span></strong></p> </div>', 1, '1500194144196884361.jpg', NULL),
(19, 'Vắng McCain, thượng viện Mỹ hoãn bỏ phiếu dự luật thay thế Obamacare', 'Cuộc bỏ phiếu thông qua dự luật y tế mới bị thượng viện Mỹ hoãn lại để chờ Thượng nghị sĩ John McCain hồi phục sau tiểu phẫu.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941451271861738.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thượng nghị sĩ John McCain sẽ tĩnh dưỡng ở nhà đến tuần sau. Ảnh: <em>Geo.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Ông Mitch McConnell, lãnh đạo phe Cộng hoà ở thượng viện Mỹ, ngày 15/7 thông báo cuộc bỏ phiếu thông qua dự luật thay thế Obamacare sẽ bị hoãn lại vì vấn đề sức khỏe của Thượng nghị sĩ John McCain, <em>Reuters </em>đưa tin.</span></p><p class="Normal"> 	<span>"Trong khi John đang hồi phục, thượng viện sẽ tiếp tục công việc của mình về các vấn đề về luật và bổ nhiệm các chức danh mới nhưng sẽ hoãn việc xem xét Dự luật Chăm sóc Tốt hơn (BCA)", ông </span><span>McConnell nói trong tuyên bố.</span></p><p class="Normal"> 	<span>Ông nhắc đến việc Thượng nghị sĩ John McCain phải trải qua phẫu thuật loại bỏ cục máu đông khoảng 5 cm trên mắt bên trái hôm 14/7. Văn phòng của ông McCain cho biết sức khoẻ ông ổn và ông sẽ ở lại quê nhà Arizona trong tuần tới trong quá trình chờ bình phục.</span></p><p class="Normal"> 	Sự vắng mặt của ông McCain khiến các thành viên đảng Cộng hòa ở thượng viện lo ngại họ có thể không hội đủ 50 phiếu thuận cần thiết để thông qua BCA. </p><p class="Normal"> 	Ông McCain, ứng viên tổng thống năm 2008, đã bày tỏ lo ngại về dự luật mới nhưng chưa nói sẽ bỏ phiếu thế nào. </p><p class="Normal"> 	<span>Đạo luật Chăm sóc Sức khỏe Hợp túi tiền (ACA), hay còn gọi là Obamacare, là chương trình chăm sóc y tế do cựu tổng thống Barack Obama đề xuất và được thông qua năm 2010.  </span><span>Các nghị sĩ đảng Cộng hòa trong hạ viện Mỹ hồi tháng 5 đã thông qua dự luật thay thế Obamacare và chuyển lên thượng viện.</span></p><p class="Normal"> 	<span>Đây là lần thứ hai thượng viện Mỹ hoãn bỏ phiếu</span><span> thông qua BCA, lần đầu là ngày 27/6 khi các lãnh đạo đảng Cộng hòa ở thượng viện lo ngại họ chưa hội đủ số phiếu cần thiết để dự luật được phê chuẩn.</span></p><p class="Normal"> 	"Có một số người có quan điểm cứng rắn hơn bạn tôi McCain và tôi biết ông ấy sẽ trở lại sớm", ô<span>ng </span><span>McConnell nói.</span></p><p class="Normal"> 	<span>Hiện có hai thượng nghị sĩ Cộng hoà đã tuyên bố phản đối dự luật mới. </span><span>Tổng thống Mỹ Donald Trump cuối tháng trước tỏ ra mất kiên nhẫn, hối thúc các thượng nghị sĩ Cộng hoà xoá bỏ Obamacare rồi "thay thế sau".</span></p><p class="Normal"> 	<span>Việc loại bỏ Obamacare là một ưu tiên của ông Trump từ khi ông chạy đua vào Nhà Trắng năm ngoái.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Khánh Lynh</strong></p>', 1, '1500194145309909754.jpg', NULL),
(20, 'Tổng thống Thổ Nhĩ Kỳ dọa chặt đầu ''những kẻ phản bội''', 'Ông Tayyip Erdogan lên tiếng cảnh báo những người chống chính phủ trong sự kiện kỷ niệm một năm cuộc đảo chính quân sự.', '<div style="text-align:center"> 	<div style="text-align: left;"> 		<table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 						<div style="text-align: center"><img src="/pictures/news/15001941461227835680.jpg" /></div></td> 				</tr><tr><td> 						<p class="Image"> 							Ông Erdogan phát biểu trước đám đông ở Istanbul. Ảnh: <em>Reuters</em>.</p> 					</td> 				</tr></tbody></table></div> 	<div style="text-align: left;"> 		"Trước hết chúng ta sẽ chặt đầu những kẻ phản bội này", <em>AFP</em> ngày 15/7 dẫn lời Tổng thống Thổ Nhĩ Kỳ Tayyip Erdogan nói trước hàng nghìn người ủng hộ ở Istanbul, nhắc đến những người tham gia cuộc đảo chính quân sự bất thành năm ngoái.</div> 	<div> 		<p style="text-align:left;"> 			Tổng thống Thổ Nhĩ Kỳ thề sẽ ký bất cứ dự luật nào của quốc hội nhằm khôi phục án tử hình ở nước này, khẳng định quốc gia "được vận hành bằng pháp quyền".<br><br> 			Hàng nghìn người ủng hộ ông Erdogan đã tập trung biểu tình nhân kỷ niệm một năm cuộc đảo chính. Những con đường gần cây cầu Bosphorus, nơi người dân chặn binh lính và xe tăng đảo chính năm ngoái, nêm kín người. Họ mang theo cờ và cả hình ảnh những người đã thiệt mạng trong cuộc chính biến, theo <em>Reuters.</em></p> 		<div> 			<div id="video-156985" data-component="true" data-component-type="video" data-component-value="156985" data-component-typevideo="2"></div></div> 		<p class="Normal" style="text-align:left;"> 			<span style="text-align:left;">Ông Erdogan ca ngợi những dân thường không có vũ trang đã góp phần đập tan âm mưu lật đổ chính quyền.</span></p> 	</div> 	<p class="Normal" style="text-align:left;"> 		"Chúng đã không khoan nhượng khi chĩa súng vào người dân của tôi. Người dân của tôi có gì? Họ chỉ có cờ, như họ đang làm hôm nay, và có điều quan trọng hơn nhiều: Họ có niềm tin", ông Erdogan nói.</p> 	<p class="Normal" style="text-align:left;"> 		Ismet Dogan, một thanh niên 20 tuổi, đã bị trúng đạn vào cả hai chân trong cơn hỗn loạn năm ngoái, cho biết cậu và các bạn mình đã xuống đường đáp lại lời kêu gọi của Tổng thống thách thức những kẻ đảo chính.</p> 	<p class="Normal" style="text-align:left;"> 		"Chúng tôi nói với nhau rằng chúng ta có một đất nước, nếu phải chết, hãy chết anh dũng. Mọi người cùng tôi đến đây năm ngoái đều sẵn sàng hy sinh. Không ai sợ điều đó", Dogan nói.</p> 	<p class="Normal" style="text-align:left;"> 		<span>Khoảng 250 người đã thiệt mạng khi quân đội Thổ Nhĩ Kỳ tiến hành cuộc đảo chính nhằm lật đổ chính quyền của ông Erdogan tháng 7/2016. Sau khi cuộc đảo chính bị đập tan, khoảng 150.000 viên chức và sĩ quan quân đội Thổ Nhĩ Kỳ đã bị sa thải, hơn 50.000 người bị giam giữ do nghi liên quan đến đảo chính.</span></p> </div><p class="Normal"> 	<span style="text-align:center;">Chính phủ Thổ Nhĩ Kỳ hôm qua sa thải thêm 7.000 cảnh sát, công chức và nhà nghiên cứu với cáo buộc chống lại chính quyền. Ankara đang nỗ lực khôi phục án tử hình ở nước này nhằm trừng phạt những người cầm đầu đảo chính.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Khánh Lynh</strong></p>', 1, '1500194146529848280.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(21, 'Nỗ lực ''quyến rũ'' Trump của các lãnh đạo thế giới', 'Một số lãnh đạo thế giới thực hiện chính sách "tấn công quyến rũ", bỏ ra nhiều nỗ lực để lấy lòng Trump.', '<div style="text-align: center;"> 	<div id="video-156850" data-component="true" data-component-type="video" data-component-value="156850" data-component-typevideo="2"></div></div><p class="Image" style="text-align:center;"> 	Cái bắt tay 29 giây giữa Trump và Macron. Video: <em>CNN.</em></p><p class="Normal"> 	<span>Tổng thống Pháp Emmanuel Macron và Tổng thống Mỹ Donald Trump - một nhà lãnh đạo trẻ, toàn cầu hoá của Pháp và một tổng thống 71 tuổi có quan điểm "nước Mỹ trước tiên" đã thể hiện tình bạn vào ngày quốc khánh Pháp trên Champs-Élysées. </span></p><p class="Normal"> 	Macron rõ ràng đã có những tính toán khi thể hiện tình hữu nghị nồng nhiệt với Trump, mặc dù nhà lãnh đạo Mỹ không được yên mến tại châu Âu và khiến chính phủ Pháp phiền lòng vì rút khỏi Hiệp định Paris về biến đổi khí hậu. Lãnh đạo Pháp muốn đảm bảo Mỹ không hoàn toàn tách biệt với phần còn lại của phương Tây, theo cây bút Stephen Collinsion của <em>CNN</em>.</p><p class="Normal"> 	Một số lãnh đạo thế giới coi "tấn công quyến rũ" là cách tốt nhất để tiếp cận Trump. Không phải chỉ trích hay thuyết giảng Trump, mà là tâng bốc và biểu lộ lòng kính trọng.</p><div style="text-align: center;"> 	<div id="video-156607" data-component="true" data-component-type="video" data-component-value="156607" data-component-typevideo="2"></div></div><p class="Image" style="text-align:center;"> 	Bữa tối trên tháp Eiffel của vợ chồng Trump - Macron. Video: <em>AP.</em></p><p class="Normal"> 	<span>Vì vậy, Pháp đã chiêu đãi Trump ẩm thực thượng hạng trong một nhà hàng được mệnh danh là "đầy ắp những giấc mơ và ma thuật" tại tháp Eiffel. Họ đưa ông đi thăm quan lăng mộ Napoléon và mời Trump đến dự cuộc duyệt binh nhân ngày Quốc khánh Pháp.</span></p><p class="Normal"> 	Khi kết thúc chuyến đi, Trump cho thấy ông rất trân trọng sự hiếu khách. Trump đăng lên Twitter bức ảnh ông và Macron đứng cạnh nhau trò chuyện khi đang xem duyệt binh.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194147188836729.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trump đăng ảnh chụp với Macron lên mạng xã hội. Ảnh: <em>Twitter.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trump và Macron hoàn toàn có thể đã phát triển một tình bạn thật sự chứ không chỉ là màn thể hiện hào nhoáng bên ngoài. Tuy nhiên, trong các chuyến đi nước ngoài gần đây của Trump, các nước đối tác cũng có ​​những dấu hiệu tích cực lấy lòng tương tự.</p><p class="Normal"> 	Tại Arab Saudi, Tổng thống Mỹ thấy hình ảnh của mình được treo trên tường khách sạn, ông được đón tiếp long trọng, được mời tham gia điệu nhảy với kiếm truyền thống. Ông về nước với một chiếc dây chuyền vàng là huy chương danh dự cao quý nhất của vương quốc.</p><p class="Normal"> 	"Ngôn từ không diễn tả được hết sự hiếu khách tuyệt vời mà các bạn dành cho chúng tôi", Trump nói với Vua Salman.</p><div style="text-align: center;"> 	<div id="video-144438" data-component="true" data-component-type="video" data-component-value="144438" data-component-typevideo="2"></div></div><p class="Image" style="text-align:center;"> 	Trump tham gia điệu nhảy truyền thống với kiếm ở Arab Saudi. Video: <em>Reuters</em>.</p><p class="Normal"> 	<span>Tại Ba Lan , một đám đông thân thiện đã cổ vũ nhiệt tình cho bài phát biểu về chính sách đối ngoại của Trump - sự tương phản với suy nghĩ cho rằng châu Âu căm ghét tân tổng thống Mỹ.</span></p><p class="Normal"> 	"Đó là một quốc gia hùng vĩ, một nơi ngoạn mục có những phong cảnh đẹp nhất", Trump nói về Ba Lan.</p><p class="Normal"> 	Nhà lãnh đạo thế giới đầu tiên chú ý tới việc lấy lòng Tổng thống Mỹ có thể là Thủ tướng Nhật Shinzo Abe. Ngay cả trước khi Trump nhậm chức, ông Abe đã đến thăm tháp Trump và xây dựng mối quan hệ tốt đẹp.</p><p class="Normal"> 	Rõ ràng, hầu hết quốc gia đều cố gắng tiếp đón trọng thị khi chiếc Air Force One hạ cánh tại nước họ, bất kể tổng thống là ai. Nhưng Macron, Tổng thống Ba Lan Andrzej Duda và hoàng gia Arab Saudi đã nỗ lực rất nhiều để lấy lòng Trump. Không khó để hiểu lý do họ làm vậy.</p><p class="Normal"> 	Người Arab Saudi từng bực dọc trong những năm qua vì mối quan hệ không tốt đẹp với chính quyền Obama và họ không hài lòng về thỏa thuận hạt nhân Iran (quốc tế nới lỏng trừng phạt với Iran để đổi lấy việc Iran kiềm chế chương trình hạt nhân). Giờ họ có một người bạn mới trong Phòng Bầu dục.</p><p class="Normal"> 	Chính phủ cánh hữu của Tổng thống Ba Lan Duda chia sẻ một số quan điểm dân túy của chính quyền Trump và nghi ngờ về tình trạng quan liêu ở Liên minh châu Âu. Họ cũng cố gắng khiến Trump hiểu sự cần thiết phải đề phòng Nga.</p><p class="Normal"> 	Tuy nhiên, một số chuyên gia lo ngại rằng Trump quá dễ bị tác động bởi các nước khác.</p><p class="Normal"> 	"Arab Saudi làm lóa mắt những nhà đàm phán thiếu kinh nghiệm của Trump bằng sự tiếp đón long trọng, thỏa thuận mua vũ khí và khoản tài trợ cho quỹ vì phụ nữ của Ngân hàng Thế giới mà Ivanka Trump đang thúc đẩy", Fareed Zakaria của <em>CNN</em> viết.</p><p class="Normal"> 	Tóm lại, Arab Saudi đã gây ảnh hưởng được với Trump. Mỹ hiện ủng hộ chính sách Trung Đông của Arab Saudi.</p><p class="Normal"> 	Sự nhiệt tình của ông Abe cũng có vẻ đem về hiệu quả. Phái đoàn Nhật Bản đã rất ngạc nhiên khi trong cuộc gặp với Tổng thống Mỹ hồi tháng hai tại Washington, ông Trump không nhắc đến việc yêu cầu Tokyo trả thêm tiền cho quân đội Mỹ đóng tại Nhật Bản - điều ông từng gợi ý trong chiến dịch tranh cử.</p><p class="Normal"> 	Macron có thể đang chơi một trò chơi dài hơi, nâng cao vị thế của mình và của đất nước bằng cách xây dựng tình hữu nghị với Tổng thống Mỹ.</p><p class="Normal"> 	"Điều chưa rõ là sau tất cả sự tâng bốc này, Mỹ sẽ nhận được thứ gì", Collinsion viết.</p><p class="Normal" style="text-align:right;"> 	<strong>Phương Vũ</strong></p>', 1, '150019414726915677.jpg', NULL),
(22, 'Kịch bản đối đầu giữa hai tàu ngầm tấn công mạnh nhất thế giới', 'Tàu ngầm lớp Yasen Nga có uy lực tương đương lớp Virginia Mỹ, nhưng về lâu dài sẽ thất thế trước đối thủ về khả năng nâng cấp.', '<div style="text-align: center;"> 	<div id="video-153172" data-component="true" data-component-type="video" data-component-value="153172" data-component-typevideo="2"></div></div><p class="Image" style="text-align:center;"> 	Tàu ngầm Kazan thuộc Đề án 855M của Nga.</p><p class="Normal"> 	Sau Chiến tranh Lạnh, hải quân Mỹ thống trị lòng đại dương với nòng cốt là các tàu  ngầm lớp Virginia. Tuy nhiên, vị thế này đang bị thách thức bởi sự xuất hiện của tàu ngầm tấn công hạt nhân Đề án 855 (lớp Yasen) của Nga. Trận chiến giữa hai loại tàu này sẽ là cuộc chiến đại diện cho đỉnh cao của công nghệ tàu ngầm trong 20 năm qua, theo <em>National Interest</em>.</p><p class="subtitle"> 	Tàu ngầm Đề án 855 ''Yasen''</p><p class="Normal"> 	Giữa thập niên 1980, Cục thiết kế trung ương Malakhit, một trong ba cục thiết kế tàu ngầm chủ lực của Liên Xô, hoàn thiện thiết kế tàu ngầm tấn công lớp Yasen. Chiếc đầu tiên mang tên Severodvinsk được đóng tại nhà máy Sevmash năm 1993, dự kiến hạ thủy vào năm 1995 và biên chế sau đó ba năm.</p><p class="Normal"> 	Tuy nhiên, khó khăn kinh tế ở Nga thời hậu Liên Xô khiến dự án này bị đình trệ và dừng hoàn toàn vào năm 1996. Tới năm 2003, Bộ Quốc phòng Nga cấp vốn cho nhà máy Sevmash để tiếp tục hoàn thiện chiếc Severodvinsk. Tàu hạ thủy năm 2010 và được biên chế vào năm 2013.</p><p class="Normal"> 	Tàu ngầm lớp Yasen dài 118 m, lượng giãn nước 13.800 tấn khi lặn. Thủy thủ đoàn gồm 90 người, ít hơn nhiều so với tàu ngầm cùng loại của Mỹ, cho thấy mức độ tự động hóa rất cao của lớp tàu này. Hình dáng bên ngoài của tàu khá giống với Đề án 971 (lớp Shchuka-B), nhưng phần phía sau tháp chỉ huy được làm dài hơn để chứa các ống phóng tên lửa thẳng đứng.</p><p class="Normal"> 	Tàu Severodvinsk sử dụng lò phản ứng hạt nhân OK-650KPM công suất 200 MW, cho phép tàu đạt tốc độ tối đa 65 km/h khi lặn hoặc bơi hành trình yên tĩnh trong lòng biển ở vận tốc 37 km/h.</p><p class="Normal"> 	Severodvinsk được trang bị tổ hợp định vị thủy âm (sonar) MGK-600 "Irtysh-Amfora", bao gồm một sonar hình cầu ở mũi, nhiều cụm sonar hai bên sườn và một sonar kéo phía đuôi. Ngoài ra, tàu còn có một radar dò tìm mặt biển MRK-50 Albatross và hệ thống đối kháng - yểm trợ điện tử Rim Hat.</p><p class="Normal"> 	Vũ khí trên tàu gồm 4 ống phóng ngư lôi cỡ 533 mm và 4 ống phóng cỡ 650 mm. Chúng có thể khai hỏa nhiều loại ngư lôi, cũng như các biến thể diệt hạm, tấn công mặt đất và chống ngầm của tên lửa Kalibr. Ngoài ra, lớp Yasen còn trang bị 8 ống phóng thẳng đứng sau tháp chỉ huy, có thể lắp 32 tên lửa P-800 Oniks hoặc 40 tên lửa Kalibr với tầm bắn tới 2.500 km.</p><p class="subtitle"> 	Tàu ngầm lớp Virginia</p><p class="Normal"> 	Lớp Virginia là phiên bản nối tiếp và tiết kiệm chi phí của lớp Seawolf, loại tàu rất uy lực nhưng cực kỳ đắt đỏ. Sau khi ra mắt, lớp Virginia dần trở thành xương sống của lực lượng tàu ngầm Mỹ.</p><p class="Normal"> 	Lớp Virginia chỉ ngắn hơn Yasen khoảng 3 m, nhưng có lượng giãn nước lớn gấp đôi. Tàu có thủy thủ đoàn 113 người, sử dụng một lò phản ứng hạt nhân GE SG9 và trang bị động cơ phản lực dòng nước thay vì chân vịt. Tàu đạt vận tốc 65 km/h khi lặn và hành trình êm trong đại dương ở vận tốc 46 km/h. Chỉ có tàu ngầm hạt nhân chiến lược lớp Los Angeles mới có thể đọ được về thông số với Virginia.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-153495" data-component="true" data-component-type="video" data-component-value="153495" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Tàu ngầm lớp Virginia rời cảng đi tuần tra.</p> </div><p class="Normal"> 	Tương tự tàu Nga, lớp Virginia cũng sử dụng một sonar BQQ-10 hình cầu ở mũi tàu. Bắt đầu từ biến thể Block III, nó được thay thế bằng sonar mũi khẩu độ lớn (LABS) hình chữ U, cùng nhiều cụm sonar khẩu độ rộng khối lượng nhẹ (LWWAA) ở hai bên hông. LWWAA đặc biệt hiệu quả trong việc phát hiện các tàu ngầm diesel điện có độ ồn thấp. Phía đuôi tàu được lắp sonar kéo thụ động, trong khi sonar mảng pha cao tần được lắp trên tháp chỉ huy và dưới bụng, giúp lớp Virginia phát hiện và tránh thủy lôi.</p><p class="Normal"> 	Tàu ngầm Virginia chỉ có 4 ống phóng cỡ 533 mm dùng cho ngư lôi Mk.48 cải tiến (ADCAP) và tên lửa diệt hạm UGM-84. Cụm 12 ống phóng thẳng đứng ở giữa tàu chứa tên lửa hành trình Tomahawk, chúng dự kiến được thay bằng hai bệ phóng hình trụ với lượng tên lửa tương đương ở biến thể Block III, trong khi biến thể Block V sẽ được mang tới 40 tên lửa Tomahawk.</p><p class="subtitle"> 	Kịch bản đối đầu</p><p class="Normal"> 	Chuyên gia quân sự Kyle Mizokami đánh giá trong tình huống một đối một giữa tàu ngầm lớp Virginia Block III và Severodvinsk, rất khó để xác định thắng thua. Đây là hai tàu ngầm đại diện cho đỉnh cao công nghệ quân sự giữa hai cường quốc.</p><p class="Normal"> 	Vũ khí trên hai tàu có uy lực tương đương. Tàu Serevodvinsk được trang bị biến thể chống ngầm của tên lửa Kalibr, giúp nó tung đòn tấn công nhanh vào đối phương từ khoảng cách 50 km.</p><p class="Normal"> 	Tàu ngầm Severodvinsk có thể chạy chậm hơn nhưng lặn sâu hơn nhiều so với lớp Virginia. Trong khi đó, lớp Virginia có lợi thế ở khả năng phát hiện đối phương nhờ hệ thống LABS. Tàu ngầm Mỹ chạy êm và có sonar hiện đại hơn đối thủ. Đây được xem là sự kết hợp vô địch trong chiến tranh tàu ngầm, giúp nó có lợi thế di chuyển và phát hiện mà tàu Serevodvinsk không có.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194148258667078.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tàu ngầm lớp Virginia có lợi thế độ ồn thấp. Ảnh: <em>National Interest</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trong tương lai gần, tàu Virginia có thể được nâng cấp sonar qua gói phần mềm mới, trong khi tàu Severodvinsk không có tính năng này. Tuy nhiên, người Nga có thể đáp trả bằng Đề án 855M "Yasen cải tiến" với cảm biến và hệ thống điện tử vượt trội chiếc Severodvinsk. Tàu ngầm Kazan được Nga hạ thủy hồi đầu năm nay là chiếc đầu tiên thuộc Đề án 855M.</p><p class="Normal"> 	Về lâu dài, cuộc cạnh tranh giữa hai tàu ngầm này sẽ dẫn đến việc sử dụng các tàu lặn không người lái và nhiều công nghệ mới. Trong bối cảnh Mỹ quyết tâm phát triển năng lực tác chiến tàu ngầm từ sau Chiến tranh Lạnh, phiên bản mới nhất của lớp Virginia sẽ nhỉnh hơn chiếc đầu tiên của lớp Yasen, nhưng không thể bảo đảm lợi thế trước các tàu Yasen cải tiến, chuyên gia Mizokami nhận định.</p><p class="Normal" style="text-align:right;"> 	<strong>Duy Sơn</strong></p>', 1, '1500194148173342106.jpg', NULL),
(23, '4 siêu vũ khí trên chiến trường tương lai của Nga', 'Quân đội Nga đang phát triển hàng loạt vũ khí thế hệ mới, dựa trên các thành tựu khoa học trong thế kỷ 21.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156936" data-component="true" data-component-type="video" data-component-value="156936" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Hệ thống gây nhiễu có thể bao trùm cả châu Âu của Nga.</p> </div><p class="Normal"> 	Để chuẩn bị cho các cuộc chiến tranh trong tương lai, Nga đang phát triển các siêu vũ khí dựa trên những thành tựu khoa học mới, theo <em>RBTH</em>.</p><p class="subtitle"> 	Vũ khí vô tuyến điện tử</p><p class="Normal"> 	Vũ khí vô tuyến điện tử sử dụng chùm tia năng lượng cao để làm tê liệt các trang thiết bị điện tử của đối phương như radar, hệ thống kiểm soát máy bay không người lái (UAV), đạn dẫn đường chính xác. Các nhà khoa học Nga cho biết vũ khí này đã được họ thử nghiệm thành công trên thực tế.</p><p class="Normal"> 	Nhiều hệ thống tác chiến điện tử dự kiến được tích hợp lên tiêm kích thế hệ 6 của Nga, nhưng chúng chủ yếu sẽ xuất hiện trong lực lượng bộ binh để diệt UAV. Việc làm mù hệ thống điện tử và radar sẽ giúp Nga giành lợi thế không nhỏ trên chiến trường, đồng thời gây hoang mang cho đối phương.</p><p class="subtitle"> 	Pháo điện từ</p><p class="Normal"> 	Đây được đánh giá là loại vũ khí uy lực trong các cuộc chiến tương lai. Hệ thống nam châm điện sẽ tạo ra từ trường khổng lồ, đẩy quả đạn bay đi với tốc độ 10.800 km/h. Ở vận tốc này, một miếng nhựa nặng 15 g có thể xuyên thủng những tấm kim loại dày vài cm. Nếu sử dụng đầu đạn kim loại, không có mục tiêu nào thực sự an toàn trước pháo điện từ, kể cả những xe tăng hiện đại nhất.</p><p class="Normal"> 	Pháo điện từ vẫn đang trong quá trình phát triển, với mục tiêu chính là chế tạo máy phát điện công suất cao với kích thước nhỏ gọn, do loại vũ khí này cần nguồn năng lượng rất lớn để vận hành hiệu quả.</p><p class="subtitle"> 	Vũ khí laser</p><p class="Normal"> 	Công nghệ laser quân sự hiện nay chủ yếu được dùng để định vị, chỉ thị mục tiêu và dẫn bắn cho vũ khí thông minh. Tuy nhiên, Nga có thể sẽ phát triển các hệ thống vũ khí năng lượng định hướng, sử dụng tia laser để trực tiếp phá hủy khí tài đối phương. Chúng có thể được gắn trên phương tiện chiến đấu, máy bay và tàu chiến.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941491239039057.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một chiếc A-60 được Nga dùng cho thử nghiệm tổ hợp 1LK222. Ảnh: <em>Airliners.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Nguyên mẫu vũ khí laser từng được Moscow thử nghiệm trên một máy bay Beriev A-60. Mục đích của nó là làm mù, gây hư hỏng các hệ thống trinh sát quang điện tử và dẫn bắn của đối phương. Chưa có nước nào phát triển được tổ hợp vũ khí laser trên không có khả năng bắn rơi phi cơ, vì nó cần tới nguồn năng lượng quá lớn, không thể lắp đặt trên bất kỳ loại máy bay nào hiện nay.</p><p class="Normal"> 	Vào năm 2009, Nga bắt đầu tái nghiên cứu vũ khí laser cho máy bay A-60, nhằm hoàn thiện thiết kế tổ hợp 1LK222 Sokol Eshelon.</p><p class="subtitle"> 	Vũ khí âm thanh</p><p class="Normal"> 	Loại vũ khí này tạo ra luồng âm thanh lớn để khiến đối phương hoảng loạn, thậm chí hủy hoại cơ quan nội tạng dẫn đến tử vong. Cả Nga và Mỹ đều đang cố gắng chế tạo các loại đạn âm thanh, nhưng chỉ sử dụng được ở khoảng cách ngắn do cường độ sóng âm giảm nhanh theo quãng đường di chuyển.</p><p class="Normal"> 	Ngoài ra, các nhà khoa học vẫn phải đau đầu với việc tạo chùm sóng âm có định hướng để nhắm vào mục tiêu. Nguyên mẫu đạn âm thanh hiện nay vẫn chưa hoàn thiện, do có khả năng gây hại cho chính những người bắn ra. Học thuyết quân đội Nga năm 2010 đòi hỏi chế tạo vũ khí âm thanh cho các cuộc chiến tương lai, cho thấy đây sẽ là một hướng đi của quân đội Nga trong thế kỷ 21.</p><p class="Normal" style="text-align:right;"> 	<strong>Duy Sơn</strong></p>', 1, '1500194149920545301.jpg', NULL),
(24, 'Nữ hành khách Trung Quốc tung cước vào tiếp viên vì phục vụ chậm', 'Nữ hành khách bị cảnh sát bắt giữ vì đấm đá nữ tiếp viên sau khi cho rằng cô này dọn khay đồ ăn chậm trễ.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941501830366970.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một phi cơ của Cathay Pacific. Ảnh: <em>SCMP</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trên chuyến bay <span>mang số hiệu </span><span>CX754 </span><span>từ Bangkok tới Hong Kong của hãng </span><span>Cathay Pacific hôm 14/7, một nữ hàng khách mang quốc tịch Trung Quốc đã nóng giận mắng chửi và đá liên tiếp vào người một nữ tiếp viên hàng không vì cho rằng người này dọn khay đồ ăn quá chậm, <em>South China Morning Post </em>đưa tin.</span></p><p class="Normal"> 	<span>Nữ hành khách họ Liu, 34 tuổi, đã đá hai phát vào chân và đánh vào vai của tiếp viên họ Wen, theo cảnh sát Hong Kong.</span></p><p class="Normal"> 	Bà Liu đã bị cảnh sát bắt giữ ngay sau khi máy bay hạ cánh xuống sân bay Quốc tế Hong Kong, đại diện hãng Cathay Pacific cho biết. Tuy nhiên, hiện chưa rõ nữ hành khách này có phải đối mặt với cáo buộc nào hay không.</p><p class="Normal"> 	"Nữ tiếp viên của chúng tôi không sao và cô ấy đã được xuất viện", đại diện hãng hàng không nói.</p><p class="Normal"> 	Tháng trước, trên một <span>chuyến bay của hãng China Southern từ Thượng Hải đến Quảng Châu, một hành khách người Trung Quốc đã ném </span><span>9 đồng xu vào động cơ máy bay để cầu may, khiến chuyến bay bị hoãn hơn 5 tiếng</span><span>.</span></p><p class="Normal" style="text-align:right;"> 	<strong><span>An Hồng</span></strong></p>', 1, '1500194150553402690.jpg', NULL),
(25, 'Macron nói Trump có thể đổi ý về hiệp định biến đổi khí hậu', 'Tổng thống Pháp hy vọng ông Donald Trump sẽ thay đổi quyết định về việc Mỹ rút khỏi hiệp định chống biến đổi khí hậu toàn cầu. ', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941511430988960.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tổng thống Pháp Emmanuel Macron chuyện trò với Tổng thống Mỹ Donald Trump tại Điện Invalides ở Paris, Pháp hôm 13/7/2017. Ảnh: <em>Reuters</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Ông Trump nói với tôi rằng ông ấy sẽ cố gắng tìm ra giải pháp trong những tháng tới. Chúng tôi đã bàn bạc chi tiết về những thứ có thể khiến ông ấy quay trở lại hiệp định Paris", <span><em>Reuter </em>hôm nay dẫn lời Tổng thống Pháp Emmanuel Macron phát biểu trên tuần báo Le Journal du Dimanche.</span></p><p class="Normal"> 	<span>Tuyên bố trên được Tổng thống Pháp đưa ra sau khi người đồng cấp Mỹ Donald Trump vừa kết thúc chuyến thăm 2 ngày tới Pháp. Sau cuộc</span><span> hội đàm giữa hai nhà lãnh đạo tại Điện Elysees hôm 14/7, ông </span><span>Trump cũng tỏ ý để ngỏ khả năng thay đổi quan điểm về hiệp định Paris về biến đổi khí hậu.</span></p><p class="Normal"> 	Hiệp định về chống biến đổi khí hậu này đã được gần 200 quốc gia thông qua năm 2015, đặt mục tiêu giới hạn mức tăng nhiệt độ của Trái Đất bằng hoặc dưới 2 độ C vào năm 2100 thông qua các biện pháp giảm thải khí CO2 và các loại khí thải khác từ việc đốt nhiên liệu hóa thạch.</p><p class="Normal"> 	<span>Chính quyền Mỹ dưới thời ông Barack Obama đã phê chuẩn hiệp định này, tuy nhiên ông Trump quyết định rút Mỹ khỏi Hiệp định Paris hồi đầu năm nay. Ông cho rằng Hiệp định Paris quá mềm mỏng với những quốc gia xả thải nhiều như Trung Quốc và Ấn Độ, gây bất lợi cho các ngành công nghiệp của Mỹ và tuyên bố ông muốn đàm phán một hiệp định có lợi hơn.</span></p><p class="Normal" style="text-align:right;"> 	<strong>An Hồng</strong></p>', 1, '1500194151509435645.jpg', NULL),
(26, 'Trump chỉ định cố vấn đặc biệt xử lý cáo buộc liên quan tới Nga', 'Tổng thống Mỹ bổ nhiệm cựu công tố viên liên bang làm cố vấn đặc biệt giúp ông vượt qua cuộc điều tra về cáo buộc liên quan đến Nga. ', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941521243587961.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ty Cobb được bổ nhiệm làm cố vấn đặc biệt cho Tổng thống Trump. Ảnh: <em>ALM.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ty Cobb, luật sư tại công ty luật đa quốc gia Hogan Lovells có trụ sở ở Washington D.C., sẽ trở thành "một thành viên chủ chốt trong đội ngũ quan chức Nhà Trắng" với chức vụ là cố vấn đặc biệt của ông Donald Trump, <em>AFP </em>dẫn thông báo của chính quyền Trump hôm 15/7.</p><p class="Normal"> 	Ông Cobb sẽ chịu trách nhiệm về những phát ngôn của Nhà Trắng liên quan đến cuộc điều tra về cáo buộc Nga can thiệp vào cuộc bầu cử Mỹ năm 2016. Cobb từng là công tố viên liên bang trước khi làm việc cho Hogan Lovells.</p><p class="Normal"> 	Việc bổ nhiệm ông Cobb được công bố sau tuần nhiều sóng gió ở Nhà Trắng, khi con trai cả của Tổng thống Trump bị phát hiện đã gặp gỡ một nữ luật sư người Nga để biết những thông tin gây bất lợi cho Hillary Clinton, <span>đối thủ của cha mình trong cuộc bầu cử tổng thống năm ngoái</span><span>. </span></p><p class="Normal"> 	Cobb sẽ làm việc với <span>Marc Kasowitz, người đ</span><span>ược mệnh danh là luật sư cứng rắn nhất phố Wall, người từng đại diện cho ông Trump trong nhiều vụ pháp lý, từ thủ tục ly dị cho đến các giao dịch bất động sản, hay gần nhất là cáo buộc gian lận tại trường đại học Trump.</span></p><p class="Normal"> 	<span>"Siêu luật sư" Kasowitz được chỉ định </span><span>đại diện cho Tổng thống Mỹ xử lý các vấn đề pháp lý liên quan đến cuộc điều tra về mối liên hệ giữa đội ngũ tranh cử của ông với chính phủ Nga hồi tháng 5, sau khi </span><span>Bộ Tư Pháp bổ nhiệm cựu giám đốc FBI </span><span>Robert Mueller</span><span> làm công tố viên đặc biệt phụ trách cuộc điều tra về cáo buộc này</span><span>.</span></p><p class="Normal" style="text-align:right;"> 	<strong><span>An Hồng</span></strong></p>', 1, '15001941521495343212.jpg', NULL),
(27, 'Kẻ có thể kế nhiệm thủ lĩnh tối cao IS', 'Al-Tunisi được coi là ứng viên hàng đầu cho vị trí lãnh đạo phiến quân IS sau khi thủ lĩnh Abu Barkr al-Baghdadi bị tiêu diệt.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941531639325839.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Al-Tunisi, kẻ có thể trở thành tân thủ lĩnh của IS. Ảnh: <em>Newageislam.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Jalaluddin al-Tunisi, kẻ cầm đầu phiến quân Nhà nước Hồi giáo (IS) tự xưng ở </span><span>Libya, là người có khả năng cao nhất trở thành tân thủ lĩnh tổ chức này sau khi </span><span>Abu Bakr al-Baghdadi bị tiêu diệt, kênh truyền hình </span><span><em>Al Arabiya</em> của Arab Saudi ngày 15/7 đưa tin.</span></p><p class="Normal"> 	<span>Nga trước đó tuyên bố </span><span>al-Baghdadi đã bị tiêu diệt trong một cuộc không kích. IS hồi </span><span>đầu tuần cũng xác nhận cái</span><span> chết của thủ lĩnh này. Trong bối cảnh đang bị vây đánh quyết liệt ở cả Iraq lẫn Syria, IS rất cần một thủ lĩnh mới để đảm bảo cho sự tồn tại của mình.</span></p><p class="Normal"> 	<span>Al-Tunisi được coi là ứng viên tiềm năng nhất cho vị trí này, bởi nhiều lãnh đạo cấp cao của IS đã bị tiêu diệt trong thời gian qua. Có tên thật là </span><span>Mohamed Ben Salem al-Ayouni, </span><span>al-Tunisi sinh năm 1982 ở </span><span>Masaken, </span><span>Sousse, </span><span>tỉnh duyên hải của </span><span>Tunisia.</span></p><p class="Normal"> 	<span>Tên này di cư đến Pháp từ thập niên 1990 và được nhập tịch, nhưng sau đó trở về </span><span>Tunisia để tham gia phong trào nổi dậy. Năm 2011, </span><span>al-Tunisi đến Syria và tham gia cuộc nội chiến ở đây. Năm 2014, y tuyên bố gia nhập IS sau cái chết của chỉ huy "Tiểu đoàn </span><span>Ghoraba".</span></p><p class="Normal"> 	<span>Al-Tunisi là kẻ rất thân cận với </span><span>al-Baghdadi, kẻ năm ngoái giao cho y </span><span>phụ trách IS ở Libya. Tên này tin rằng mình có thể giành chiến thắng trong các trận chiến và duy trì quan hệ tốt với các nhóm quá khích khác ở Bắc Phi.</span></p><p class="Normal"> 	<span>IS đã quyết định thành lập một "đế chế mới" sau cái chết của </span><span>al-Baghdadi, đồng thời </span><span>nỗ lực mở rộng hoạt động ở Bắc Phi sau thất bại liên tiếp ở cả Iraq và Syria. </span></p><p class="Normal" style="text-align:right;"> 	<strong>Khánh Lynh</strong></p>', 1, '1500194153960592853.jpg', NULL),
(28, 'Chiến dịch của Trump chi tiền cho luật sư bảo vệ con trai Tổng thống', 'Luật sư của con trai Trump được trả một khoản tiền lớn chỉ một tuần trước khi thông tin cuộc gặp với luật sư Nga được công bố.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941541027661871.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Con trai Trump (phải) đang bị xem xét về cuộc gặp với luật sư Nga năm ngoái. Ảnh: <em>Reuters.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Văn phòng luật của <span>Alan Futerfas</span><span> đã được chiến dịch tái tranh cử của Tổng thống Mỹ Donald Trump trả 50.000 USD vào ngày 27/6, một tuần trước khi tin tức về cuộc gặp giữa con trai Tổng thống và luật sư Nga bị phát tán, <em>Reuters </em></span><span>hôm nay </span><span>đưa tin. </span><span>Alan Futerfas hiện là </span><span>hãng luật đại diện cho </span><span>Donald Trump Jr., con trai của ông Trump.</span></p><p class="Normal"> 	Khoản tiền được mô tả là "phí tư vấn pháp luật" này được phát hiện trong một hồ sơ của Uỷ ban bầu cử liên bang Mỹ <span>ngày 15/7, nhưng </span><span>chưa rõ </span><span>Futerfas </span><span>khi đó </span><span>đang đại diện cho ai. </span><span>Cả phát ngôn viên của chiến dịch tái tranh cử của ông Trump và </span><span>Futerfas đều không bình luận về việc này.</span></p><p class="Normal"> 	<span>Futerfas được công bố là luật sư của </span><span>Trump Jr. </span><span>ngày 10/7</span><span>, một ngày sau khi tờ </span><span>New York Times đăng tin con trai Trump gặp gỡ </span><span>luật sư Nga Natalia Veselnitskaya trong chiến dịch tranh cử tổng thống Mỹ năm ngoái. </span><span>Futerfas không cho biết ông đã được thuê khi nào. </span></p><p class="Normal"> 	<span>Chiến dịch tái tranh cử của ông Trump còn trả hơn 538.000 USD cho </span><span>Jones Day, công ty luật đại diện cho chiến dịch năm 2016. </span><span>Trump Corporation, công ty thuộc sở hữu của Tổng thống, cũng nhận được khoản phí tư vấn pháp luật gần 90.000 USD vào ngày 30/6.</span></p><p class="Normal"> 	<span>Trump Jr. thừa nhận đã gặp gỡ nữ luật sư Nga ở New York trong khi ông Trump đang tiến hành chiến dịch chạy đua vào Nhà Trắng. Con trai Trump được báo là bà </span><span>Veselnitskaya </span><span>có thể cung cấp thông tin bất lợi cho đối thủ của bố là bà </span><span>Hillary Clinton. Thông tin về cuộc gặp này có thể khiến ông Trump gặp thêm nhiều rắc rối về chính trị và pháp lý.</span></p><p class="Normal"> 	Mỹ vẫn đang xúc tiến việc điều tra cáo buộc Nga can thiệp bầu cử năm ngoái, sau khi cộng đồng tình báo tin rằng Moscow đã tấn công mạng nhằm giành lợi thế cho ông Trump. Các cơ quan điều tra cũng đang xem xét mối liên hệ giữa chiến dịch của ông Trump với các quan chức Nga.</p><p class="Normal"> 	Nhà Trắng ngày 15/7 tuyên bố <span>Ty Cobb, một cựu luật sư quân đội, sẽ là người chịu trách nhiệm đưa ra phản hồi của Nhà Trắng về các cuộc điều tra liên quan đến Nga. </span></p><p class="Normal"> 	Chiến dịch tái tranh cử của ông Trump trong quý II năm nay đã huy động được 8 triệu USD và tiêu hết 4,4 triệu USD.</p><p class="Normal" style="text-align:right;"> 	<strong>Khánh Lynh</strong></p>', 1, '1500194154898176469.jpg', NULL),
(29, 'Hạm đội tàu sân bay không mang nổi tiêm kích của Nhật', 'Nhật Bản sở hữu hạm đội tàu sân bay đầy uy lực nhưng không thể vận hành tiêm kích mà chỉ hỗ trợ trực thăng.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156503" data-component="true" data-component-type="video" data-component-value="156503" data-component-typevideo="2"></div></div> 	<p class="Image"> 		JS Izumo là tàu sân bay trực thăng lớn nhất của Nhật Bản.</p> </div><p class="Normal"> 	Lực lượng phòng vệ biển Nhật Bản (JMSDF) đang vận hành lực lượng "khu trục hạm trực thăng" có kích thước đồ sộ và đường băng dài, nhưng lại không thể vận hành chiến đấu cơ, theo <em>National Interest</em>.</p><p class="Normal"> 	Chuyên gia quân sự Kyle Mizokami cho biết sau Thế chiến II, Lực lượng phòng vệ Nhật Bản (JSDF) được thành lập với cơ cấu đầy đủ quân binh chủng, nhưng chỉ nhằm mục đích phòng thủ quốc gia. JMSDF là lực lượng hải quân có quy mô và uy lực lớn, nhưng không được phép sở hữu vũ khí có khả năng tiến công.</p><p class="Normal"> 	Trong nhiều thập kỷ qua, nhiệm vụ bảo vệ các tuyến hàng hải của Nhật Bản thôi thúc JMSDF chế tạo tàu sân bay cũng như <span>khôi phục lực lượng không quân hải quân</span><span>. </span></p><p class="Normal"> 	Cuối thập niên 1960, JMSDF bắt đầu đóng hai khu trục hạm mang trực thăng lớp Haruna, với khu vực nhà chứa và sàn đáp chiếm đến nửa chiều dài tàu. Chúng đóng vai trò căn cứ nổi di động để trực thăng tiến hành tác chiến chống ngầm. Nhiệm vụ này tiếp tục được áp dụng cho hai tàu khu trục lớp Shirane.</p><p class="Normal"> 	Lớp Haruna và Shirane không phải tàu sân bay, nhưng chúng cho thấy quá trình thử nghiệm triển khai không quân hải quân của JMSDF. Hai lớp tàu này trở thành nền tảng để Nhật chế tạo tàu đổ bộ lớp Osumi với lượng giãn nước đầy tải 14.000 tấn, cho phép JMSDF chuyên chở bộ binh đến các đảo xa bờ. Lớp Osumi cũng có sàn đáp kéo dài toàn bộ thân tàu tương tự tàu sân bay thông thường, dù không có nhà chứa máy bay.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156500" data-component="true" data-component-type="video" data-component-value="156500" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 JS Shirane là bản thử nghiệm cho giải pháp tàu sân bay trực thăng.</p> </div><p class="Normal"> 	Năm 2009, Nhật Bản đạt bước tiến quan trọng trong quá trình xây dựng không quân hải quân với việc hạ thủy khu trục hạm trực thăng JS Hyuga và JS Ise. Mỗi tàu dài 206 m, có giãn nước toàn tải 19.500 tấn, mang được 4 trực thăng chống ngầm và có khả năng hỗ trợ tới 11 chiếc. Lớp Hyuga có kích thước lớn hơn cả tàu sân bay HMS Invincible của Anh.</p><p class="Normal"> 	4 năm sau, Nhật Bản hạ thủy JS Izumo, chiếm hạm lớn nhất của nước này từ sau Thế chiến II. Lớp Izumo được Tokyo xếp vào nhóm tàu khu trục chở trực thăng, có chiều dài 248 m, giãn nước đầy tải 27.000 tấn, thường mang theo 9 trực thăng và có thể tăng lên 14 chiếc. JS Izumo và tàu JS Kaga cùng lớp đều đóng vai trò nòng cốt trong biên đội tàu khu trục hộ tống, có nhiệm vụ tiêu diệt tàu ngầm đối phương.</p><p class="Normal"> 	Hạm đội 4 khu trục hạm trực thăng của Nhật đều có sàn đáp kéo dài hết thân tàu, được trang bị thang nâng máy bay, thượng tầng dạng tháp và nhà chứa máy bay cỡ lớn trong thân. Vẻ ngoài của chúng không khác gì các tàu sân bay thực thụ.</p><p class="Normal"> 	Tuy nhiên, lớp Hyuga và Izumo lại không thể vận hành chiến đấu cơ. Trên lý thuyết, chúng có thể tiếp nhận siêu tiêm kích F-35B nhờ khả năng cất hạ cánh thẳng đứng hoặc trên đường băng ngắn (V/STOL). Nhưng các tàu của Nhật không có cầu nhảy để hỗ trợ máy bay cất cánh, việc tích hợp tính năng này đòi hỏi phải đưa tàu vào nhà máy trong hơn một năm. Bên cạnh đó, chỉ có một trong hai thang nâng đủ lớn để vận chuyển những phi cơ như F-35B và trực thăng lai MV-22 Osprey.</p><p class="Normal"> 	Kho chứa đạn và nhiên liệu hàng không trên lớp Hyuga và Izumo cũng cần mở rộng đáng kể để có thể hỗ trợ một lượng nhỏ tiêm kích F-35B. Nhưng điều này sẽ khiến không gian nhà chứa bị thu hẹp đáng kể, giảm khả năng mang theo máy bay trên tàu. Việc hoán cải chúng thành tàu sân bay cũng sẽ rất khó khăn, tốn thời gian và đắt đỏ.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156504" data-component="true" data-component-type="video" data-component-value="156504" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Tàu khu trục chở trực thăng JS Hyuga (DDH-181).</p> </div><p class="Normal"> 	Tuy nhiên, chuyên gia Mizokami cho rằng kích thước của lớp Izumo quá lớn so với một tàu khu trục trực thăng chuyên biệt. Nhiều khả năng Nhật Bản đang tiếp thu kinh nghiệm để chuẩn bị đóng tàu sân bay mang theo chiến đấu cơ thực sự, nhằm bổ sung cho lưới phòng không bao trùm quần đảo Ryukyu và Senkaku, nhiệm vụ đang do căn cứ không quân trên đảo Okinawa đảm nhận.</p><p class="Normal"> 	Tokyo hiện chưa có khả năng sở hữu các tàu sân bay thực sự. Tổng nợ công của Nhật Bản cao gấp đôi quy mô toàn bộ nền kinh tế. Ngân sách quốc phòng tăng chậm và chỉ đạt mức 1% GDP, cho thấy Tokyo chưa tập trung vào xây dựng lực lượng quân đội quá hùng mạnh.</p><p class="Normal"> 	Nhật Bản có đủ trình độ kỹ thuật để đóng tàu sân bay, cũng như khả năng mua tiêm kích hạm tốt nhất thế giới từ Mỹ, nhưng họ lại không có động cơ chiến lược để xây dựng hạm đội tàu sân bay, trừ khi quan hệ với Trung Quốc xấu đi nhanh chóng tới mức có nguy cơ nổ ra chiến tranh. Tuy nhiên, khả năng này rất khó xảy ra trong thời điểm hiện tại và tương lai gần, chuyên gia Mizokami nhận định.</p><p class="Normal" style="text-align:right;"> 	<strong>Duy Sơn</strong></p>', 1, NULL, NULL),
(30, 'Dân Singapore đòi điều tra Thủ tướng vì bê bối gia đình', 'Hàng trăm người dân Singapore hôm nay biểu tình kêu gọi điều tra độc lập với Thủ tướng Lý Hiển Long về bê bối liên quan đến gia đình.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194156731644713.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Gilbert Goh phát biểu với người ủng hộ. Ảnh: <em>Reuters.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cuộc biểu tình diễn ra tại khu vực tự do ngôn luận ở Singapore. Các nhà tổ chức ước tính số người tham gia là 600 nhưng phóng viên <em>AFP</em> xác nhận con số này chỉ khoảng 300.</p><p class="Normal"> 	Thủ lĩnh biểu tình Gilbert Goh nói ông và người ủng hộ kêu gọi một chính phủ minh bạch hơn và điều tra những cáo buộc gần đây với Thủ tướng Lý Hiển Long.</p><p class="Normal"> 	Đây là cuộc biểu tình đầu tiên từ khi tranh chấp trong gia đình Thủ tướng Lý Hiển Long được công khai ngày 14/6. Hai chị em Lý Vĩ Linh và Lý Hiển Dương đăng thông báo cáo buộc anh trai lạm quyền.</p><p class="Normal"> 	Hai người cho rằng từ khi người cha Lý Quang Diệu, thủ tướng Singapore đầu tiên, qua đời năm 2015, nước này có những thay đổi không thể hiện những giá trị mà ông Lý đã đại diện. Họ cảm thấy mất niềm tin vào anh trai với tư cách lãnh đạo.</p><p class="Normal"> 	Họ nhắc lại nguyện ước của cha về việc phá huỷ căn nhà gia đình tại số 38 đường Oxley sau khi ông qua đời, nhưng Thủ tướng Lý Hiển Long cùng vợ đã phản đối. Hai chị em cho rằng việc duy trì căn nhà sẽ cho phép anh trai "và gia đình ông kế thừa một tượng đài hữu hình về quyền lực của Lý Quang Diệu".</p><p class="Normal"> 	Thủ tướng Lý Hiển Long phủ nhận cáo buộc nhưng nói sẽ không kiện hai người em.</p><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, '1500194156741758441.jpg', NULL),
(31, 'Phi cơ quân sự Trung Quốc bay diễn tập gần Nhật Bản, Đài Loan', 'Không quân Trung Quốc thông báo các phi cơ quân sự nước này đã bay diễn tập tầm xa "nhiều lần" trong tuần gần Nhật Bản và Đài Loan.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941571491375902.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phi cơ H-6K của Trung Quốc bay qua kênh Bashi hồi tháng 9/2016. Ảnh: <em>Xinhua.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Không quân Trung Quốc tuần qua đã thực hiện nhiều cuộc diễn tập tầm xa trên biển, điều oanh tạc cơ H-6K cùng nhiều máy bay khác bay qua kênh Bashi và eo Miyako, kiểm tra khả năng thực chiến trên biển", <em>Reuters</em> dẫn thông báo do không quân Trung Quốc đăng trên mạng xã hội hôm nay cho biết.</p><p class="Normal"> 	Eo Miyako nằm giữa hai đảo miền nam Nhật Bản còn kênh Bashi nằm giữa đảo Đài Loan và Philippines.</p><p class="Normal"> 	Theo không quân Trung Quốc, hoạt động trên nằm trong kế hoạch diễn tập năm nay, phù hợp với luật pháp và thông lệ quốc tế, không nhằm vào bất cứ quốc gia cụ thể nào và sẽ tiếp tục diễn ra.</p><p class="Normal"> 	Bộ Quốc phòng Trung Quốc hôm qua thông báo Nhật Bản hãy "tập quen dần", sau khi Bắc Kinh cho 6 phi cơ quân sự bay qua eo Miyako.</p><p class="Normal"> 	Hải quân và không quân Trung Quốc những tháng gần đây tiến hành một chuỗi tập trận tại Tây Thái Bình Dương, rèn luyện năng lực hoạt động xa bờ. Trung Quốc còn đang thực hiện chương trình hiện đại hóa quân đội đầy tham vọng, trong đó có đóng tàu sân bay, phát triển chiến đấu cơ tàng hình.</p><table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941572128406040.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Vị trí eo Miyako. Đồ họa:<em> Reuters.</em></p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, '15001941571131459568.jpg', NULL),
(32, 'Nhân viên bảo trì ở Mỹ bị kẹt trong ATM, luồn giấy qua khe cầu cứu', 'Một nhân viên bảo trì bị mắc kẹt trong ATM ở bang Texas, Mỹ, buộc người này phải luồn giấy qua khe nhận tiền để cầu cứu.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941591603261099.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mẩu giấy xin giúp đỡ. Ảnh:<em> KzTV.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cảnh sát thành phố Corpus Christi, bang Texas, chiều ngày 12/7 nhận được cuộc gọi trình báo có người mắc kẹt trong máy rút tiền, theo<em> Kztv.</em></p><p class="Normal"> 	<span>"Làm ơn giúp tôi, tôi bị kẹt trong này, không mang điện thoại. Xin hãy gọi cho sếp tôi theo số ...", trích nội dung mẩu giấy được luồn qua khe nhận tiền.</span></p><p class="Normal"> 	<span>"Khi chúng tôi đến nơi, có tiếng nói nhỏ phát ra từ trong máy rút tiền. Ban đầu chúng tôi tưởng đó là trò đùa", sĩ quan Richard Olden cho biết.</span></p><div style="text-align: center;"> 	<div style="text-align: center;"> 		<div id="video-156934" data-component="true" data-component-type="video" data-component-value="156934" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> 	<p class="Image" style="text-align:center;"> 		<span style="color:rgb(0,0,0);">Sĩ quan Richard Olden kể lại vụ việc. Video: <em>KzTV.</em></span></p> </div><p class="Normal"> 	Cảnh sát phải phá khóa căn phòng đặt máy ATM để đưa nhân viên bảo trì ra ngoài sau hơn hai giờ mắc kẹt. Anh này cho biết nhiều khách tới rút tiền nghĩ đó là trò đùa và cảm thấy may mắn vì cuối cùng cũng có một người tin là thật.</p><p class="Normal"> 	"Mọi người đều ổn nhưng đây là lần đầu tiên tôi gặp chuyện này trong đời, có người thực sự đã kẹt trong máy ATM. Đúng là buồn cười", ông Olden nói. </p><p class="Normal" style="text-align:right;"> 	<strong>Hồng Hạnh</strong></p>', 1, '15001941582091169404.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(33, 'Thổ Nhĩ Kỳ sa thải hơn 7.000 người nghi liên quan đảo chính', 'Thổ Nhĩ Kỳ sa thải hơn 7.000 cảnh sát, binh sĩ và quan chức các bộ nghi liên quan đến cuộc đảo chính quân sự bất thành năm ngoái.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941602076737954.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một binh sĩ Thổ Nhĩ Kỳ tham gia đảo chính được đưa đến tòa án ở thành phố Mugla ngày 14/7. Ảnh: <em>Reuters.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	7.563 người, bao gồm cảnh sát, binh sĩ, quan chức các bộ, đã bị sa thải trong đợt thanh trừng mới nhất theo sắc lệnh mới ban hành ngày 14/7, hãng tin <em>Anadolu</em> cho biết. Nhà chức trách Thổ Nhĩ Kỳ còn tước quân hàm đối với 342 quân nhân đã nghỉ hưu.</p><p class="Normal"> 	Đợt sa thải diễn ra chỉ một ngày trước khi Thổ Nhĩ Kỳ đánh dấu một năm xảy ra đảo chính quân sự nhằm lật đổ Tổng thống Tayyip Erdogan.</p><p class="Normal"> 	Cuộc đảo chính quân sự ở Thổ Nhĩ Kỳ diễn ra đêm 15/7/2016, do một nhóm binh sĩ thực hiện, nhưng không thành công. Tổng thống Erdogan tố giáo sĩ Fethullah Gulen cùng người ủng hộ ông đứng sau đảo chính và tuyên bố sẽ nhổ tận gốc "virus" của Gulen trong các cơ quan chính phủ. Hơn 50.000 người đã bị bắt, khoảng 100.000 người bị sa thải hoặc đình chỉ công việc. Gulen, hiện ở Mỹ, phủ nhận có liên quan.</p><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, '1500194159854224377.jpg', NULL),
(34, 'Nỗi lo của mẹ Singapore có con sắp vào lớp 1', 'Lo lắng con tự ti, một người mẹ Singapore cho con học trước toán, tiếng Trung và võ để chuẩn bị cho cậu bé lên lớp 1.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156924" data-component="true" data-component-type="video" data-component-value="156924" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:right;"> 		<strong> Hồng Hạnh </strong>(theo <em>CNA</em>)</p> </div>', 1, NULL, NULL),
(35, 'Iraq duyệt binh ở Baghdad mừng đánh bại IS', 'Quân đội Iraq hôm nay tổ chức duyệt binh tại Baghdad để mừng chiến thắng mang tính lịch sử trước IS ở Mosul.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194162760365049.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Quân đội Iraq duyệt binh ở thủ đô Baghdad ngày 15/7. Ảnh: <em>Reuters.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cuộc duyệt binh diễn ra sáng nay tại quảng trường trong Vùng Xanh, nơi có nhiều văn phòng chính phủ và đại sứ quán nước ngoài, ở Baghdad, <em>Xinhua</em> dẫn thông tin từ văn phòng Thủ tướng Iraq Haider al-Abadi cho biết.</p><p class="Normal"> 	Cuộc duyệt binh có sự tham dự của Thủ tướng al-Abadi, tổng tư lệnh các lực lượng vũ trang Iraq, các quan chức đến từ Bộ Quốc phòng và Bộ Nội vụ.</p><p class="Normal"> 	Hầu hết các hãng truyền thông địa phương và quốc tế không được mời đến sự kiện vì lý do an ninh, một nguồn tin giấu tên nói.</p><p class="Normal"> 	Sự kiện được tổ chức để mừng chiến thắng của quân đội Iraq trước phiến quân Nhà nước Hồi giáo (IS) ở Mosul. Thủ tướng al-Abadi ngày 10/7 chính thức tuyên bố giải phóng Mosul khỏi IS.</p><p class="Normal"> 	Mosul rơi vào tay IS năm 2014 và, cùng với Raqqa, Syria, bị nhóm phiến quân biến thành thành trì. Quân đội Iraq và các tay súng liên minh bắt đầu chiến dịch giải phóng Mosul từ tháng 10/2016. Họ giành lại đông Mosul hồi tháng 1 và tấn công sang tây Mosul vào ngày 19/2.</p><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, '15001941621800145460.jpg', NULL),
(36, 'Trộm dùng máy xúc đào cả cây ATM ở Anh', 'Băng trộm ở Anh dùng máy xúc đa năng phá tường chứa cây rút tiền và chở đi bằng xe tải.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156926" data-component="true" data-component-type="video" data-component-value="156926" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> </div><p class="Normal"> 	Một vụ trộm xảy ra rạng sáng 10/7 ở thị trấn Ashby-de-la-Zouch, hạt Leicestershire, miền trung nước Anh. Băng tội phạm dùng máy xúc phá tường của tòa nhà đặt máy ATM, theo <em>9News</em>.</p><p class="Normal"> 	Chiếc máy xúc đa năng di chuyển nhiều lần, cố nhấc ATM lên. Một tên trộm mặc áo đen đứng phía sau xe tải cạnh máy xúc, cầm dây thừng chờ cột ATM lên xe bán tải màu bạc. Băng tội phạm tẩu thoát cùng một ôtô nhỏ sẫm màu.</p><p class="Normal"> 	<span>Cảnh sát Leicestershire hôm 14/7 công bố video, kêu gọi người dân cung cấp thông tin giúp nhận diện nghi phạm.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Hồng Hạnh</strong></p>', 1, NULL, NULL),
(37, 'Cháy chung cư 36 tầng ở Mỹ, ba người chết', 'Lửa xuất hiện tại một tòa chung cư 36 tầng ở Hawaii, Mỹ, làm ít nhất ba người thiệt mạng và 5 người bị thương.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156918" data-component="true" data-component-type="video" data-component-value="156918" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> </div><p class="Normal"> 	Vụ việc xảy ra ở chung cư Marco Polo 36 tầng, Honolulu, Hawaii, ngày 14/7. Lực lượng cứu hỏa kiểm soát ngọn lửa vào 18h30, 4 giờ sau khi đám cháy xuất hiện,<em> Reuters </em>dẫn lời thị trưởng Honolulu Kirk Caldwell nói.</p><p class="Normal"> 	Ngọn lửa bắt đầu từ tầng 26 và lan nhanh chóng lên hai tầng phía trên. Hiện chưa rõ nguyên nhân gây cháy. Vụ hỏa hoạn làm ba người chết và ít nhất 5 người bị thương.</p><p class="Normal"> 	Hình ảnh trên mạng xã hội cho thấy ngọn lửa bao trùm vài tầng và mảnh vỡ rơi xuống từ các cửa sổ. Manuel Neves, trưởng sở cứu hỏa thành phố, nói tòa nhà không có hệ thống phun nước.</p><p class="Normal"> 	Tờ <em>Honolulu Star-Advertiser</em> đưa tin Marco Polo được xây vào năm 1971, trước khi thành phố yêu cầu phải có hệ thống phun nước. Theo giới chức thành phố, nếu tòa nhà có hệ thống phun nước, ngọn lửa sẽ không thể lan ra khỏi nơi nó bắt đầu.</p><p class="Normal"> 	Vụ việc xảy ra một tháng sau vụ cháy chung cư Grenfell 24 tầng ở Anh, làm ít nhất 80 người chết. Tòa nhà Grenfell cũng không có hệ thống phun nước chữa cháy.</p><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, NULL, NULL),
(38, 'Qatar nói khủng hoảng vùng Vịnh không thể xử lý trong một ngày', 'Ngoại trưởng Qatar nói cuộc đối đầu ngoại giao giữa nước này và 4 quốc gia Arab "căng thẳng" và không thể giải quyết trong một ngày.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194165924523550.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngoại trưởng Qatar và Thổ Nhĩ Kỳ họp báo chung ở Ankara ngày 14/7. Ảnh:<em> Press TV.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cuộc khủng hoảng ngoại giao giữa Qatar và nhóm nước Arab, do Arab Saudi dẫn đầu, "không thể giải quyết trong một ngày", <em>Press TV</em> dẫn lời Ngoại trưởng Qatar Mohammed bin Abdulrahman al-Thani cho biết trong cuộc họp báo chung với người đồng cấp Thổ Nhĩ Kỳ ở Ankara ngày 14/7.</p><p class="Normal"> 	Bình luận trên được đưa ra trong bối cảnh Ngoại trưởng Mỹ Rex Tillerson vừa kết thúc chuyến thăm vùng Vịnh nhưng không đạt được kết quả đột phá. Ông Tillerson đã hội đàm với thái tử Arab Saudi Mohammad bin Salman al-Saud và Quốc vương Qatar Tamim bin Hamad al-Thani để tìm cách xóa bỏ rạn nứt.</p><p class="Normal"> 	4 nước Arab, gồm Arab Saudi, Bahrain, Ai Cập và Các tiểu vương quốc Arab Thống nhất (UAE), cắt quan hệ ngoại giao với Qatar ngày 5/6, cáo buộc nước này ủng hộ chủ nghĩa khủng bố và Iran nhưng Doha bác bỏ.</p><p class="Normal"> 	Khi đến thăm Doha, Ngoại trưởng Tillerson cùng người đồng cấp Qatar ký thỏa thuận chống tài trợ chủ nghĩa khủng bố nhằm giúp xoa dịu khủng hoảng. Tuy nhiên, 4 nước Arab cho rằng vẫn "chưa đủ". Ngoại trưởng UAE cho biết họ sẽ không sớm kết thúc cuộc khủng hoảng ngoại giao với Qatar.</p><p class="Normal" style="text-align:right;"> 	<strong>Như Tâm</strong></p>', 1, '1500194164239164061.jpg', NULL),
(39, 'Lực lượng phóng lựu Nga diễn tập quy mô lớn', 'Lực lượng phóng lựu Quân khu miền Đông Nga tổ chức diễn tập quân sự quy mô trong điều kiện thời tiết khắc nghiệt.', '<table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941661466999741.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Binh sĩ Nga trong một cuộc tập trận ở Viễn Đông. Ảnh: <em>Sputnik.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Hơn 2.000 lính phóng lựu thuộc quân khu miền Đông (EMD) Nga tham gia diễn tập quy mô tại khu vực Viễn Đông, bao gồm Cộng hòa Buryatia, tỉnh Khabarovsk, các vùng Primorsky, Zabaikalsky và Sakhalin, <em>Sputnik </em>ngày 13/7 đưa tin.</p><p class="Normal"> 	"Trong thời gian diễn tập, lực lượng phóng lựu học cách trinh sát, xác định và ngắm bắn nhiều mục tiêu trong điều kiện thời tiết bất lợi, bao gồm có gió mạnh", tuyên bố của quân khu miền Đông cho biết.</p><p class="Normal"> 	<span>Lính phóng lựu Nga thường được trang bị các loại vũ khí phóng lựu cỡ nòng lớn để diệt xe cơ giới và bộ binh địch. Trong cuộc diễn tập, họ còn thực hành chiến thuật di chuyển áp sát mục tiêu, phát hiện mục tiêu ngụy trang cũng như kỹ thuật bắn hạ phương tiện cơ giới và lính bắn tỉa của địch.</span></p><p class="Normal"> 	<span>Ngoài ra, lực lượng tên lửa thuộc EMD đã khai hỏa hệ thống tên lửa đạn đạo chiến thuật Iskander trong tình huống giả định có sự tấn công gây rối của lực lượng khủng bố.</span></p><p class="Normal"> 	<span>Cuộc diễn tập diễn ra trong bối cảnh 25.000 binh sĩ NATO từ 20 nước đồng minh do Mỹ dẫn đầu cũng đang tiến hành các cuộc tập trận lớn mang tên Saber Guardian 17 ở các nước Đông Âu như Bulgaria, Hungary và Romania.</span></p><table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194166951439228.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Khu vực Viễn Đông của Nga. Đồ họa: <em>RBTH.</em></p> 			</td> 		</tr></tbody></table><p style="text-align:right;"> 	<strong>Nguyễn Hoàng</strong></p>', 1, '15001941661290213329.jpg', NULL),
(40, 'Hố tử thần liên tục nuốt chửng nhà ở Mỹ', 'Hố tử thần ở bang Florida, Mỹ, liên tục mở rộng phá hủy hai ngôi nhà, buộc 10 hộ dân phải sơ tán.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156902" data-component="true" data-component-type="video" data-component-value="156902" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> </div><p class="Normal"> 	Hai ngôi nhà bị hố tử thần nuốt chửng hôm 14/7 ở Land O'' Lakes, khu dân cư cách thành phố Tampa, bang Florida, khoảng 32 km về phía bắc, theo <em>USA Today</em>.</p><p class="Normal"> 	Nhà chức trách cho biết hố đang không ngừng mở rộng. Khoảng 7h20 hôm 14/7, chính quyền nhận được tin báo về một cái hố to cỡ bể bơi nhỏ. Đến 15h30, miệng hố đã phát triển rộng tới 75 m và không có dấu hiệu sẽ dừng lại, trong hố đầy nước và bùn đất.</p><p class="Normal"> 	<span>Ngôi nhà đầu tiên, không có người bên trong, bị nuốt chửng vào buổi sáng. Tiếp theo, một ngôi nhà bị sụp khoảng 80%, ba người phụ nữ sống bên trong đã kịp thoát ra ngoài, theo <em>CNN.</em></span></p><p class="Normal"> 	<span>Chính quyền đã sơ tán người sinh sống trong 10 ngôi nhà quanh khu vực miệng hố. Ngoại ô Tampa và nhiều nơi ở Florida là những nơi có tầng đất nhiều đá vôi xốp, có thể sụp bất ngờ tạo thành hố tử thần.</span></p><p class="Normal"> 	Hồi tháng 2/2013, một hố tử thần xuất hiện ban đêm, cách Tampa khoảng 24 km về phía đông, đã nuốt chửng một ngôi nhà khiến một người chết. Hai năm sau, một cái hố lại mở ra đúng vị trí này. Theo một công ty xây dựng, cách duy nhất để đảm bảo hố không xuất hiện lại là khoan lỗ rồi bơm bê tông xuống lòng đất. </p><p class="Normal" style="text-align:right;"> 	<strong>Hồng Hạnh</strong></p>', 1, NULL, NULL),
(41, 'Vertu vẫn hút nhà giàu Việt bất chấp vỡ nợ', 'Mười hai năm bán ở Việt Nam, điện thoại Vertu liên tục được khách hàng giàu có săn đón kể cả khi hãng tuyên bố phải đóng cửa nhà máy.', '<p class="Normal"> 	Trao đổi với <em>VnExpress</em>, ông Nguyễn Quang Minh – Phó tổng giám đốc Công ty Thương mại FPT (FPT Trading), đơn vị phân phối chính thức điện thoại Vertu tại Việt Nam cho biết, đến lúc Vertu tuyên bố phải đóng cửa nhà máy thì tình hình kinh doanh dòng điện thoại siêu sang này tại Việt Nam vẫn phát triển tốt. Khách hàng vẫn đang liên tục đặt mua.</p><p class="Normal"> 	Tuy nhiên, trước diễn biến mới, nhà phân phối cho biết đã liên hệ với hãng tại Anh để nắm tình hình và triển khai kế hoạch tiếp theo.</p><p class="Normal"> 	“Chúng tôi đã liên hệ với công ty mẹ của Vertu và hiện đang chờ công ty này trả lời về việc tổ chức lại hoạt động trong thời gian tới. Dự kiến trong tuần sau sẽ phải có phản hồi để chúng tôi tính toán lại việc kinh doanh. Hiện công ty đang tạm hoãn giao điện thoại mới trong vài ba ngày cho khách đã đặt hàng để chờ tin, vì nó ảnh hưởng đến chế độ bảo hành”, ông Minh cho biết.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941701640341847.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cửa hàng Vertu tại khách sạn Metropole, Hà Nội.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cũng theo ông Minh, trước mắt hệ thống cửa hàng Vertu chính hãng tại TP HCM và Hà Nội vẫn hoạt động bình thường. Tuy nhiên, về dài hạn, tình hình sẽ phụ thuộc vào diễn biến mới của nhà sản xuất.</p><p class="Normal"> 	“Công ty Vertu đang tiến hành đàm phán các vấn đề nợ lương nhân viên và chuyển giao cho chủ mới. Kết quả cuối cùng vẫn chưa có nên chúng tôi còn đang phải chờ”, đại diện FPT Trading cho biết thêm.</p><p class="Normal"> 	Trước đó, báo chí quốc tế cho hay, ông chủ Vertu là Murat Hakan Uzan vừa thất bại trong việc cứu Vertu khỏi phá sản khi đề nghị trả các chủ nợ 1,9 triệu bảng trên tổng số nợ 128 triệu bảng. Do đó, các nhà máy của Vertu tại Anh sẽ phải dừng hoạt động, khiến cho 200 nhân viên mất việc. Tuy nhiên, ông Uzan vẫn sẽ giữ lại thương hiệu Vertu, công nghệ và giấy phép thiết kế. Theo một nguồn tin thân cận, ông chủ Vertu đã có kế hoạch khôi phục lại thương hiệu này.</p><p class="Normal"> 	Vertu được Nokia thành lập vào năm 1998. Đến đầu năm 2006, Vertu chính hãng bắt đầu được phân phối tại Việt Nam bởi Công ty TNHH Phân phối Sản phẩm Viễn thông FPT, nay là FPT Trading, thông qua 3 cửa hàng sang trọng tại TP HCM và Hà Nội. Bản thân FPT cũng đã 2 lần chứng kiến thương hiệu Vertu được đổi chủ và qua những lần này, tiêu thụ Vertu ở Việt Nam vẫn tăng.</p><p class="Normal"> 	Tháng 6/2012, Nokia bán lại Vertu cho quỹ EQT Partners AB (Thụy Điển). Đại diện nhà phân phối FPT lúc đó cho biết việc sang nhượng nhằm để tập trung nguồn lực cho nhà sản xuất và không ảnh hưởng đến hoạt động kinh doanh tại Việt Nam.</p><p class="Normal"> 	Đến năm 2015, Vertu được EQT Partners AB sang tay cho quỹ đầu tư Godin Holdings (Hong Kong). Diễn biến khi ấy cũng không ảnh hưởng gì đến hoạt động của FPT. Thậm chí, Phó chủ tịch của Vertu khi ấy còn gửi thư thông báo và cam kết giữ nguyên mọi hỗ trợ với Vertu Việt Nam, đồng thời mong muốn sự hợp tác của FPT và Vertu ngày càng phát triển.</p><p class="Normal"> 	Sở dĩ Vertu chăm sóc chu đáo thị trường Việt Nam bởi sau bao sóng gió, kể cả đến lúc thua lỗ phải đóng cửa nhà máy, Việt Nam vẫn hiện là thị trường phát triển rất tích cực của hãng. Ông Nguyễn Quang Minh từ chối tiết lộ doanh số trong nửa đầu năm nhưng khẳng định tình hình kinh doanh vẫn rất tốt. “Việt Nam luôn nằm trong top thị trường tiêu thụ tốt của hãng. Nửa đầu năm nay cũng vậy. Hiện nay chúng tôi vẫn còn các khách đang đặt hàng”, vị này tiết lộ.</p><p class="Normal"> 	Tại Việt Nam, dòng Signature S theo hướng cổ điển và Aster - smartphone đều bán rất chạy. Signature S được các doanh nhân, chủ doanh nghiệp ưa chuộng. Trong khi đó, Aster dành cho khách hàng trẻ hơn, có thu nhập tốt.</p><p class="Normal"> 	Tính đến cuối năm 2016, doanh thu của dòng điện thoại này vẫn duy trì tăng trưởng đều đặn trong hơn một thập niên được phân phối chính thức, bất chấp cơn bão điện thoại thông minh. Cùng với Hong Kong, Singapore, Việt Nam được đánh giá là một trong các thị trường quan trọng nhất của Vertu tại châu Á - Thái Bình Dương.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941701210106306.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ba mẫu điện thoại từng được Vertu sản xuất dành riêng cho thị trường Việt Nam với giá khởi điểm là 345 triệu đồng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trong năm ngoái, Vertu còn sản xuất ba phiên bản Bespoke của dòng Signature S dành riêng cho các khách hàng Việt. Trong đó, rẻ nhất là phiên bản Ceramic Black với giá cơ bản là 345 triệu đồng. Đắt nhất là phiên bản Red Gold Clous De Paris với giá khởi điểm từ 990 triệu đồng. Giá chưa bao gồm các tùy chọn về màu da, vật liệu trong 3 triệu tùy chọn mà hãng cho phép các "khách quý" người Việt cân nhắc để cá nhân hóa chiếc điện thoại xa xỉ của mình.</p><p class="Normal"> 	Hay như mẫu Vertu Aster Chevron giá 5.000 USD thậm chí còn cháy hàng khi vừa mới bán ra tại Việt Nam vào năm 2016.</p><p class="Normal"> 	“Định hướng của Vertu là dòng sản phẩm cao cấp, không chỉ phục vụ các CEO mà còn là người dùng trẻ. Chẳng bao giờ có chuyện, một thanh niên lái xe Porsche, mang túi Hermes, đeo đồng hồ Audemars Piguet, lại chỉ dùng một chiếc điện thoại bình thường. Một chiếc điện thoại Vertu với các tiêu chuẩn vượt trội về thủ công, chất lượng và dịch vụ mới là sự lựa chọn ý tưởng cho anh ấy”, ông Nicholas Holt – Tổng giám đốc Vertu khu vực châu Á – Thái Bình Dương nhận xét khi nói về triển vọng của Vertu tại Việt Nam vào cuối năm ngoái.</p><p class="Normal" style="text-align:right;"> 	<strong>Viễn Thông</strong></p>', 1, '15001941701479050059.jpg', NULL),
(42, 'Những nông sản Việt ''đắt như vàng'' khi xuất sang Nhật', 'Thanh long vào Nhật có giá 200.000 đồng một kg, xoài 100.000 đồng một trái, tía tô 700 đồng mỗi lá nếu qua được quy trình kiểm tra nghiêm ngặt. ', '<p class="Normal"> 	Từ năm 2010, khi Hiệp định Đối tác kinh tế Việt - Nhật (VJEPA) được triển khai đồng bộ, sẽ có trên 800 dòng sản phẩm nông sản và thủy sản Việt Nam vào Nhật với thuế suất 0%. Tuy nhiên, theo nhiều doanh nghiệp, Nhật cũng là thị trường rất khó tính với những quy trình kiểm tra nghiêm ngặt nên hiện mặt hàng nông sản Việt xuất khẩu tới Nhật chưa nhiều.  </p><p class="Normal"> 	<span>Theo Tổng cục Hải quan, đến hết tháng 5/2017, Việt Nam thu về hơn 43 triệu USD từ xuất khẩu rau quả vào thị trường Nhật Bản. </span><span>Một số mặt hàng nông sản Việt đang được bán với mức giá rất cao như thanh long, xoài, vải thiều... </span></p><p class="Normal"> 	<span style="color:#696969;"><strong>Thanh long 200.000 đồng một kg</strong></span></p><p class="Normal"> 	Thanh long vỏ đỏ ruột trắng là trái cây tươi đầu tiên của Việt Nam được xuất khẩu vào Nhật, sau 4 năm đàm phán. Trong đó có 3 năm triển khai dự án nghiên cứu xử lý thanh long bằng hơi nước nóng và hơn một năm chờ Nhật gỡ bỏ lệnh cấm. Gần đây, bên cạnh thanh long ruột trắng, loại ruột đỏ của Việt Nam cũng được xuất khẩu vào thị trường này. </p><p class="Normal"> 	Hiện giá bán lẻ mặt hàng này tại Nhật vào khoảng 180.000-200.000 đồng mỗi kg. Trong khi ở thị trường trong nước, có thời điểm giá thanh long giảm xuống chỉ còn 2.000 đồng mỗi kg. </p><p class="Normal"> 	<span style="color:rgb(105,105,105);"><strong>Xoài 100.000 đồng một trái</strong></span></p><p class="Normal"> 	Lô xoài Cát Chu đầu tiên được xuất khẩu vào Nhật là cuối năm 2015. Các cơ quan chức năng của Việt Nam đã phải mất 5 năm để chuẩn bị hồ sơ, xây dựng quy trình xử lý dịch hại mới được phía Nhật chấp thuận.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194171731897256.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Xoài Cát Chu được bày bán tại Nhật. Ảnh:<em> Bộ Công Thương</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trước khi vào Nhật, trái xoài bắt buộc phải xử lý bằng hơi nước nóng, tương tự trái thanh long. Hiện một số nhà máy hơi nước nóng tại Việt Nam đã được phía Nhật Bản kiểm tra cấp mã số. Mặt hàng này có giá bán lẻ tại các siêu thị Nhật khoảng 8-10 USD mỗi kg (khoảng 200.000-230.000 đồng). Với giá như vậy, một quả nhỏ có giá khoảng hơn 70.000 đồng và quả lớn khoảng 100.000 đồng, chưa tính thuế. </p><p class="Normal"> 	<span style="color:#696969;"><strong>Vải thiều hơn 400.000 đồng 12 trái</strong></span></p><p class="Normal"> 	Một số doanh nghiệp tại Việt đã đàm phán và xuất khẩu thành công lô vải thiều sang Nhật Bản từ năm 2014 và tiếp tục duy trì đến nay.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941711763508997.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					12 quả vải thiều tại Nhật được bán giá 400.000 đồng. Ảnh:<em> Báo Nông nghiệp Việt Nam</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Có thời điểm, vải thiều Lục Ngạn được bán trong siêu thị tại Nhật Bản với giá khoảng 1.980 yên (12 quả), tương đương khoảng 400.000 đồng. Nếu cộng thêm thuế thì 12 quả vải này có giá khoảng 430.000 đồng. </p><p class="Normal"> 	<span style="color:#696969;"><strong>Lá tía tô mùi tây 700 đồng một lá</strong></span></p><p class="Normal"> 	Loại tía tô màu xanh giống của Nhật được trồng tại Việt Nam khi xuất khẩu và bán cho các nhà hàng Nhật có giá lên tới 500-700 đồng mỗi lá. Một doanh nghiệp chuyên xuất khẩu mặt hàng này cho biết, để đủ điều kiện xuất khẩu, công ty đã khảo sát địa điểm, nguồn nước, chất đất, xây nhà kính và áp dụng quy trình gieo trồng, sản xuất nghiêm ngặt theo kỹ thuật, công nghệ và chuyên gia Nhật Bản. Bên cạnh yêu cầu khắt khe về kỹ thuật khi trồng, các lá tía tô còn phải được thu hoạch đúng ngày tuổi và có kích cỡ đều tăm tắp, không được rách, nát. Với những lá để quá lứa phải hái bỏ.</p><p class="Normal"> 	Hiện nay, mỗi ngày công ty này xuất khẩu sang Nhật vài chục nghìn lá, đồng thời phải đặt riêng một chỗ với hãng hàng không để đảm bảo ngày nào cũng có sản phẩm xuất sang Nhật Bản.</p><p class="Normal" style="text-align:right;"> 	<strong>Minh Châu</strong></p>', 1, '1500194171252111712.jpg', NULL),
(43, 'Loạn rao bán đất nền quanh sân bay Long Thành', 'Quy hoạch 21.000 hecta vùng phụ cận sân bay Long Thành chưa được phê duyệt, song việc mua bán đất đã sớm trở nên bát nháo.', '<p class="Normal"> 	Theo Quốc lộ 51 từ TP HCM về Bà Rịa - Vũng Tàu, vừa qua địa phận huyện Long Thành (Đồng Nai), người "săn đất nền" không khó để nhận thấy hàng trăm tờ rơi được treo, dán chi chít trên các cột điện, gốc cao su... bên đường. Tuy nhiên, điểm sôi động nhất phải kể đến 5 km dọc Tỉnh lộ 769 từ thị trấn Long Thành vào các xã Lộc An, Bình Sơn.</p><p class="Normal"> 	Một số xã được xem như vùng phụ cận của sân bay khác như Long An, An Phước, Long Phước cũng náo nhiệt không kém với bảng hiệu của nhiều công ty bất động sản mọc lên như nấm.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941751241332901.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một công ty mua đất của người dân rồi tự làm dự án khu dân cư, chưa hoàn thiện cơ sở hạ tầng nhưng vẫn rao bán với giá 500 triệu/100 m2, nền có giá cao nhất gần 1 tỷ đồng. Ảnh: <em>Phước Tuấn.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trước trụ sở một công ty bất động sản có trụ sở TP HCM đặt chi nhánh ở xã Lộc An, huyện Long Thành, nhiều nhân viên niềm nở khi có khách ghé vào hỏi đất. "Anh mua đất ạ, gặp em là anh may lắm đấy, em còn mấy lô đẹp và giá cả mềm nhất khu vực này", cậu nhân viên này nói rồi dẫn khách đi vào khu vực đất.</p><p class="Normal"> 	Tờ rơi rao với 100 m2 đất chỉ có giá 200 triệu đồng với cơ sở hạ tầng đầy đủ, ra dáng một khu dân cư khá hoàn chỉnh. Tuy nhiên, dẫn khách đến khu đất nằm khá xa đường Tỉnh lộ 769, lô nền ở đây chỉ là một bãi đất trống đang được san ủi nham nhở, lầy lội, dấu hiệu của khu dân cư là một con đường nội bộ rộng 4m lót tạm bằng đá dăm.</p><p class="Normal"> 	Lúc này, giá bán bất ngờ được nhân viên hét lên gấp đôi so với niêm yết trên tờ rơi, thập chí có lô hơn 150%. "Đất này công ty em mua lại của người dân rồi lập dự án khu dân cư, anh chị cứ yên tâm, pháp lý đầy đủ, 3 đến 4 tháng sẽ ra sổ hồng, xây dựng thoải mái. Nhìn vậy thôi chứ mấy bữa nữa cơ sở hạ tầng ngon lành, mấy chốc giá tăng vùn vụt", tay "cò" chỉ vào bãi đất rồi trấn an.</p><p class="Normal"> 	Trong khi đó, một dự án nằm ngay trên Tỉnh lộ 769, đối diện Khu dân cư Lộc An (Khu dân cư phục vụ dân tái định cư cho dự án sân bay Long Thành) có vẻ khá sôi động. Nhân viên của dự án tràn ra đường treo pano, áp phích tờ rơi trước cổng dự án để chào mời người đi đường. Khác với một số dự án nhỏ nằm cách xa trung tâm, dự án trên được đội giá rất cao với 100 m2 có giá gần 1 tỷ đồng, trong đó nhiều lô đẹp mặt tiền tỉnh lộ có giá hơn 2 tỷ.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941751047014309.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một dự án đất nền tại xã Lộc An tha hồ "chém gió" về quy hoạch 21.000 hecta vùng phụ cận quanh sân bay để bán đất nền. Ảnh: <em>Phước Tuấn.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Đưa bản đồ được cho là quy hoạch 21.000 hecta, nam nhân viên rỉ tai cho rằng dự án này nằm ngay đường vào cổng số 1 của sân bay trong tương lai nên nếu đầu tư sẽ sinh lời rất cao: "Hiện Đồng Nai tạm ngưng tách thửa nhưng bên em là dự án lớn, chỉ một tháng sau sẽ ra sổ hồng cho anh, xây dựng tự do lên đến 5 tầng".</p><p class="Normal"> 	Ngoài những dự án phân lô bán nền của các công ty bất động sản, thì đất nông nghiệp, đất vườn được cho đã tăng giá phi mã so với năm trước, chừng 30%. Nhiều nhà đầu tư bắt đầu lùng sục để gom đất đầu cơ, trong khi đó người dân cũng không mặn mà bán vì cho rằng, giá vẫn chưa lên đỉnh điểm nên chờ.</p><p class="Normal"> 	<span>Nằm sâu trong con hẻm cách Tỉnh lộ 769 chừng 2 km thuộc xã Bình Sơn, một chủ đầu tư ở TP Biên Hòa mua lại gần một hecta đất vườn rồi làm đường bê tông, rao bán đất với giá 160 triệu đồng/100 m2. "Đây là đất nông nghiệp nếu muốn tách thửa có sổ đỏ thì phải 500 m2, còn không thì sổ chung thôi, xây dựng nhà ở thoải mái", một phụ nữ quảng cáo. </span></p><p class="Normal"> 	<span>Theo một "cò" đất lâu năm ở đây tiết lộ, giá đất ở Long Thành đầu năm đến nay khá trầm lắng vì nhà đầu tư hồi hộp chờ các quyết định của Trung ương cũng như quy hoạch chi tiết vùng phụ cận. Tuy nhiên, khi có thông tin cho Đồng Nai tách tiểu dự án bồi thường, giải phóng mặt bằng dự án sân bay Long Thành thì giá đất bắt đầu sốt trở lại.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941771430338246.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một lô đất được chủ đầu tư rao bán với giá 160 triệu đồng/100 m2 tại xã Bình Sơn với lời đảm bảo xây nhà thoải mái mặc dù đây là đất nông nghiệp. Ảnh: <em>Phước Tuấn.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Trao đổi với <em>VnExpress, </em>ông Hoàng Văn Dung - Giám đốc Văn phòng Đăng ký nhà đất tỉnh Đồng Nai khẳng định tỉnh đang tạm ngưng tách thửa để rà soát lại các quy định, phù hợp với thực tế, nên việc một số người hứa hẹn tách thửa được là không đúng. Trong khi đó, quy hoạch ch</span><span>i tiết 21.000 hecta</span><span> </span><span>vùng phụ cận</span><span> quanh sân bay cũng chưa được phê duyệt.</span></p><p class="Normal"> 	<span>"Việc xuất hiện đầu cơ gom đất, phân lô bán nền, quảng cáo không đúng sự thật về quy hoạch đã gây nên tình trạng bát nháo ở khu vực này, người mua không tìm hiểu kỹ sẽ bị ảnh hưởng, tiền mất tật mang", ông Dung khuyến cáo.</span></p><p class="Normal"> 	<span>Theo ông Dung, thời gian qua nhiều ban ngành liên quan cũng có đợt thanh kiểm tra và thừa nhận có tình trạng mua bán đất bát nháo ở khu vực này. Đặc biệt, có thực trạng một số đơn vị mới đang xin đầu tư, chỉ trong giai đoạn quy hoạch đã liên kết sàn tiếp thị lấy bảng vẽ tung lên mời chào, đặt cọc tiền trước.</span></p><p class="Normal"> 	<span>"Ngoài việc kiến nghị chính quyền địa phương vào cuộc, chúng tôi khuyến cáo người dân thận trọng trong việc mua bán đất, đặc biệt là các dự án không đủ pháp lý. Đồng thời cần quản lý chặt chẽ, thường xuyên thanh kiểm tra để có biện pháp chấn chỉnh kịp thời", ông Dung nói.</span></p><p class="Normal"> 	<span>Dự án sân bay Long Thành được Thủ tướng phê duyệt quy hoạch 5.000 hecta trên địa giới hành chính 6 xã thuộc huyện Long Thành. Mục tiêu của dự án là đưa sân bay này trở thành cảng hàng không quốc tế cấp F4, có công suất 100 triệu hành khách một năm và 5 triệu tấn hàng hóa mỗi năm với tổng mức đầu tư 18,7 tỷ USD chia thành 3 giai đoạn. </span></p><p class="Normal"> 	<span>Tháng trước, Quốc hội đã đồng ý thông qua nghị quyết </span><span>tách nội dung bồi thường, hỗ trợ, tái định cư sân bay này thành dự án thành phần do UBND tỉnh Đồng Nai làm chủ đầu tư. </span><span>Hiện tổng kinh phí giải phóng mặt bằng và tái định cư dự kiến là 23.000 tỷ đồng.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Phước Tuấn</strong></p>', 1, '15001941742082478682.jpg', NULL),
(44, 'Thu tiền tỷ mỗi năm nhờ nuôi cá bống bớp', 'Nhiều hộ dân ở thị trấn Rạng Đông (Nam Định) thu lãi cả tỷ đồng một năm nhờ nghề nuôi cá bống bớp đặc sản.', '<p class="Normal"> 	Những năm gần đây, nhiều hộ nông dân ở thị trấn Rạng Đông (huyện Nghĩa Hưng, Nam Định) đã có thu nhập kinh tế ổn định từ mô hình nuôi cá bống bớp. Nhận thấy nghề nuôi cá đặc sản này mang lại hiệu quả kinh tế hơn nuôi cua và tôm, nhiều hộ dân tiếp tục đầu tư mở rộng trang trại.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941782007607162.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mỗi kg cá bống bớp thương phẩm hiện có giá gần 300.000 đồng. Ảnh: <em>Việt Linh</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Xuất hiện ở Nghĩa Hưng được khoảng 20 năm, nghề nuôi cá bống bớp ban đầu chỉ tự phát, cá được các hộ dân tự đánh bắt cá tự nhiên đem về nuôi thử. Qua nhiều năm tìm tòi phương pháp, người dân ở đây đã biết cách nhân giống cá, mở rộng quy mô chăn nuôi.</span></p><p class="Normal"> 	“Cá bống bớp vốn sống ở vùng nước mặn ven biển, dần dần được người dân thuần hoá có thể thích nghi ở môi trường nước lợ”, anh Nguyễn Văn Sơn (ở khu 6, thị trấn Rạng Đông) cho hay.</p><p class="Normal"> 	Với đặc tính khoẻ mạnh, ít mắc bệnh, khả năng thích nghi cao hơn nhiều loài cá khác nên sản lượng nuôi khá ổn định. Khi vận chuyển đi tiêu thụ, cá không cần phải ướp đá lạnh nên tiết kiệm chi phí và được các thương lái rất ưa chuộng. Mỗi ngày, các hộ dân ở Rạng Đông tiêu thụ hàng tấn cá thương phẩm.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194178922180438.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mô hình nuôi cá bống bớp ở Nghĩa Hưng được đánh giá cao về hiệu quả kinh tế. Ảnh:<em> Việt Linh.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Thị trường tiêu thụ cá bống bớp khá ổn định. Cá được xuất bán nhiều tỉnh thành như Hà Nội, Hải Phòng, Quảng Ninh... và cả nước ngoài. Giá cá bống bớp hiện khá cao, dao động khoảng 280.000-300.000 một kg (8-12 con).</span></p><p class="Normal"> 	Chị Nguyệt ở khu 6, thị trấn Rạng Đông cho hay, cá bống bớp từ khi ươm giống đến khi xuất bán khoảng một năm. Mỗi ha ao nuôi trong vòng một năm có thể thu về lợi nhuận từ 250 đến 300 triệu đồng. “Chưa có khi nào mà nuôi cá ra không bán được cả. Nghề nuôi cá đặc sản này đã giúp nhiều hộ dân làm giàu ngay trên mảnh đất quê nhà”, chị Nguyệt nói thêm.</p><div style="text-align: center;"> 	<div id="video-156929" data-component="true" data-component-type="video" data-component-value="156929" data-component-typevideo="2"></div></div><p class="Normal"> 	<span>Ông Nguyễn Duy Phong, Chủ tịch hội nông dân thị trấn Rạng Đông cho biết, cả huyện Nghĩa Hưng hiện có khoảng 400 hộ nuôi cá bống bớp, giải quyết việc làm cho hàng nghìn người dân. Hàng năm, tiền lãi mỗi hộ thu về lên đến cả tỷ đồng.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Việt Linh - Lê Hoàng</strong></p>', 1, '1500194178337437214.jpg', NULL),
(45, 'Từ nghiện ngập đến quản lý sự nghiệp triệu đô', 'Bất chấp quá khứ nghiện ma túy và tù tội, Keith Guyton làm lại cuộc đời một cách ngoạn mục.', '<p class="Normal"> 	Keith Guyton sinh ra ở Houston (Mỹ). Ông vốn là một đứa trẻ ngoan, hòa đồng và tích cực chơi thể thao. Đến tuổi thiếu niên, Guyton đi chơi nhiều hơn và bắt đầu được rủ rê sử dụng cần sa. Càng về sau, ông càng nghiện và sử dụng các loại ma túy mạnh hơn.</p><p class="Normal"> 	“Khi bạn sử dụng ma túy, bạn bắt đầu nghiện nó và muốn dùng nhiều hơn. Bạn không còn kiên nhẫn để làm việc và bắt đầu phạm tội”, Guyton kể lại. Thế là, ông bắt đầu buôn bán ma túy. Không thể qua mắt cảnh sát, ông bị bắt giữ và may mắn chỉ bị kết án một năm tù.</p><p class="Normal"> 	Mới ra tù, ban đầu Guyton vẫn "ngựa quen đường cũ" và đi bán ma túy. Nhưng nhanh chóng sau đó, ông thấy cần phải từ bỏ công việc này. Trong một đêm không còn chấp nhận được bản thân, Guyton đã ném số ma túy trị giá 50.000 USD xuống bồn cầu. Ông gọi cho tay buôn cấp trên, báo rằng số ma túy đã bị làm hỏng và xin rút khỏi đường dây.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156845" data-component="true" data-component-type="video" data-component-value="156845" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:left;"> 		<span>Dù bị dọa giết nhiều lần nhưng Guyton quyết định bỏ nghề buôn ma túy và kiên nhẫn làm nhiều công việc khác nhau trong suốt 8 năm để trả số nợ. Xong nợ, ông chuyển nơi sinh sống và đi xin việc ở hãng sửa ôtô Christian Brothers Auto. Guyton thành thật kể về quá khứ tù tội của mình. Chủ hãng sửa xe nghe xong chỉ đơn giản trả lời “cứ vào làm việc đi.”</span></p> </div><p class="Normal"> 	Vài năm sau, Guyton gặp Jonathan Carr, con trai của chủ hãng Mark Carr. Jonathan Carr quyết định chuyển ông đến một trong những chi nhánh lớn nhất hệ thống. Tại đây, ông đã góp phần làm nên doanh thu triệu đô cho hãng.</p><p class="Normal"> 	Nhận thấy tinh thần làm việc, sự cầu tiến của Guyton, Jonathan Carr định hướng cho ông làm quản lý hoặc sở hữu một chi nhánh của hãng. Vì thế, Guyton đã được cử đi học về nhượng quyền thương hiệu và hiện đã nhận nhượng quyền miễn phí để sở hữu một chi nhánh của Christian Brothers Auto.</p><p class="Normal"> 	“Tôi muốn nói với các bạn trẻ rằng việc quá khứ bạn từng làm gì hay bạn không có cha đi nữa cũng không phải vấn đề. Nếu bạn thay đổi cách thức và chính mình, bạn sẽ có hy vọng”, Guyton chia sẻ.</p><p class="Normal" style="text-align:right;"> 	<strong>Phiên An</strong> (<em>theo CNBC</em>)</p>', 1, NULL, NULL),
(46, 'Hãng thẻ tặng nhà hàng 10.000 USD nếu từ chối tiền mặt', 'Visa sẽ hỗ trợ các cửa hàng nhỏ để nâng cấp công nghệ, nhằm chấp nhận thanh toán từ điện thoại, đồng hồ thông minh hay các thiết bị khác.', '<p class="Normal"> 	<span>Kế hoạch mới của hãng thẻ tín dụng Mỹ có tên "Thử thách không tiền mặt của Visa". Họ sẽ thuyết phục các nhà hàng, quán café và xe thức ăn lưu động ngừng nhận thanh toán bằng tiền mặt. Việc này sẽ buộc khách hàng phải dùng thẻ tín dụng hoặc các dạng thanh toán điện tử.</span></p><p class="Normal"> 	"Visa sẽ cung cấp tới 500.000 USD cho 50 chủ cửa hàng thực phẩm nhỏ cam kết tham gia thử thách 100% không tiền mặt của chúng tôi", thông cáo của Visa cho biết.</p><p class="Normal"> 	<span>Nếu chọn tham gia, cửa hàng sẽ nhận được 10.000 USD hỗ trợ từ Visa để nâng cấp công nghệ. Có thể là lắp đặt nền tảng chấp nhận thanh toán từ điện thoại, đồng hồ thông minh hay các thiết bị khác.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194180887785385.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Visa muốn khuyến khích các nhà hàng nhận thanh toán không tiền mặt. Ảnh: <em>SS</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Các hãng thẻ tín dụng tính phí xử lý thẻ và các chi phí khác với những công ty chấp nhận thanh toán thẻ. Tuy nhiên, khoản tiền đó có thể làm giảm lợi nhuận của các công ty nhỏ, và thậm chí quyết định sự sống còn của các cửa hàng lưu động.</p><p class="Normal"> 	<span>Tuy nhiên, Visa cho biết họ đã chứng minh được cắt giảm tiền mặt có thể giúp các công ty nhỏ. Họ đã thực hiện một nghiên cứu với các nhà hàng trên khắp Mỹ. Theo đó, khi </span><span>ngừng nhận tiền mặt, </span><span>các cơ sở này đã thu về hàng tỷ USD mỗi năm.</span></p><p class="Normal"> 	Chỉ riêng tại New York City, Visa cho biết "các công ty đã có thêm 6,8 tỷ USD doanh thu và tiết kiệm hơn 186 giờ lao động" mỗi năm. Tuy vậy, báo cáo này vẫn chưa được công bố.</p><p class="Normal"> 	Khi được hỏi liệu có mở rộng chương trình không tiền mặt sang các mảng khác ngoài nhà hàng, Giám đốc giải pháp doanh nghiệp của Visa - Jack Forestell chỉ cho biết chương trình hiện tại là "một phần của sáng kiến lớn". Hãng đang đánh cược mọi người "sẽ tự do hơn khi không phải mang theo tiền mặt".</p><p class="Normal" style="text-align:right;"> 	<strong>Hà Thu</strong> <em>(theo CNN)</em></p>', 1, '15001941801235613683.jpg', NULL),
(47, 'Doanh thu phí bảo hiểm đạt hơn 47.000 tỷ đồng', 'Số doanh thu 6 tháng đầu năm tăng 20,77% so với cùng kỳ 2016 và năng lực tài chính của các công ty bảo hiểm tiếp tục nâng cao. ', '<p class="Normal"> 	<span>Tại hội nghị sơ kết 6 tháng đầu năm và triển khai công tác 6 tháng cuối năm 2017, Cục Quản lý, Giám sát Bảo hiểm cho biết, trong nửa đầu năm nay, thị trường bảo hiểm duy trì tốc độ tăng trưởng cao và ổn định, năng lực tài chính của các công ty tiếp tục được nâng cao.</span></p><p> 	Tổng doanh thu phí bảo hiểm toàn thị trường ước đạt 47.169 tỷ đồng (tăng 20,77%) so với cùng kỳ năm 2016, trong đó doanh thu các doanh nghiệp bảo hiểm phi nhân thọ ước đạt 19.340 tỷ đồng, doanh nghiệp bảo hiểm nhân thọ ước đạt 27.829 tỷ đồng.</p><p> 	Hiện hành lang pháp lý cho hoạt động kinh doanh bảo hiểm đang ngày càng được hoàn thiện. Tuy nhiên, để thị trường tiếp tục phát triển mạnh hơn nữa, các chuyên gia cho rằng doanh nghiệp bảo hiểm phải tiếp tục nỗ lực nâng cao năng lực quản trị rủi ro, đa dạng hóa sản phẩm, nâng cao chất lượng đại lý và chất lượng dịch vụ, đặc biệt là công tác giải quyết quyền lợi bảo hiểm… </p><p style="text-align:right;"> 	<strong>Thanh Lê</strong></p>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(48, 'Sáng mai tư vấn ''Doanh nghiệp nhỏ hưởng lợi gì từ luật Hỗ trợ doanh nghiệp nhỏ và vừa''', '10h30 ngày 17/7, Thứ trưởng Bộ Kế hoạch &amp; Đầu tư và Phó tổng giám đốc VPBank sẽ trả lời thắc mắc của độc giả xung quanh luật mới.', '<p class="Normal" style="text-align:center;"> 	<strong>*Độc giả gửi câu hỏi <a href="http://vnexpress.net/phong-van-truc-tuyen/tu-van-doanh-nghiep-loi-gi-tu-luat-ho-tro-doanh-nghiep-nho-va-vua-3613272.html" target="_blank">tại đây</a></strong></p><p class="Normal"> 	Theo thống kê của Bộ Kế hoạch & Đầu tư, doanh nghiệp nhỏ và vừa chiếm hơn 97% tổng số doanh nghiệp cả nước, nhưng lâu nay khu vực này chưa nhận được sự hỗ trợ đúng mực để phát triển tối ưu.</p><p class="Normal"> 	Những điều mà các doanh nghiệp cần là những quy định hỗ trợ cụ thể về: mặt bằng sản xuất, tham gia kế hoạch mua sắm, cung ứng dịch vụ công…nhưng nhiều chính sách mới chỉ dừng lại ở những quy định mang tính khuyến khích và chung chung, chưa có những quy định hỗ trợ rõ ràng. Đây là lý do chỉ có 30% doanh nghiệp nhỏ và vừa được tiếp cận các chương trình hỗ trợ, số còn lại hầu như không biết và không tiếp cận được chính sách này.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941811967258396.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thứ trưởng Bộ Kế hoạch & Đầu tư, ông Đặng Huy Đông.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Thêm nữa, sự phân tán quản lý ở nhiều bộ, ngành trong thực thi các chính sách liên quan khiến thủ tục tiếp cận hỗ trợ mang nặng tính xin - cho, doanh nghiệp nhiều khi không có đủ lực để theo đến cùng.</p><p class="Normal"> 	Theo quy định tại Luật hỗ trợ doanh nghiệp nhỏ và vừa sẽ có hiệu lực từ 1/1/2018, ngoài hỗ trợ chung Luật còn quy định một số hỗ trợ như doanh nghiệp nhỏ, siêu nhỏ sẽ được hưởng hỗ trợ thuế cao hơn… Tuy nhiên, mức hỗ trợ cụ thể bao nhiêu sẽ được trình Quốc hội thời gian tới.</p><p class="Normal"> 	Luật này cũng khuyến khích thành lập các tổ chức tư vấn độc lập để xếp hạng tín nhiệm doanh nghiệp nhỏ và vừa, hình thành các cơ sở ươm tạo, cơ sở kỹ thuật, khu làm việc chung, chuỗi phân phối sản phẩm…</p><p class="Normal"> 	Về hỗ trợ tín dụng, Luật không đưa ra quy định cứng, bắt buộc các ngân hàng, tổ chức tín dụng phải có ưu đãi cho doanh nghiệp nhỏ và vừa vay vốn. Thay vào đó, Luật khuyến khích các ngân hàng, tổ chức tín dụng cố gắng đạt tối thiểu 30% dư nợ tín dụng trong hệ thống dành cho doanh nghiệp.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941812019244953.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Nguyễn Thành Long - Phó tổng giám đốc VPBank.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Nhưng đối tượng doanh nghiệp nhỏ và vừa cụ thể nào sẽ nhận được hỗ trợ, mức hỗ trợ cụ thể về chính sách ra sao, ngân hàng có chính sách ưu đãi vay gì với doanh nghiệp nhỏ và vừa… sẽ lần lượt là những vấn đề được giải đáp tại buổi phỏng vấn trực tuyến "Doanh nghiệp được hưởng lợi gì từ luật Hỗ trợ doanh nghiệp nhỏ và vừa?” sẽ được VnExpress tổ chức vào 9h sáng ngày 17/7.</p><p class="Normal"> 	Buổi phỏng vấn trực tuyến sẽ có sự tham gia của các khách mời: Thứ trưởng Bộ Kế hoạch & Đầu tư Đặng Huy Đông, người có nhiều tâm huyết, đồng hành cùng sự phát triển của doanh nghiệp nhỏ và vừa.</p><p class="Normal"> 	Ông Nguyễn Thành Long - Phó tổng giám đốc VPBank, ngân hàng luôn giữ vai trò tiên phong trong đồng hành, hỗ trợ sự phát triển của các doanh nghiệp nhỏ và vừa những năm gần đây.</p><p class="Normal"> 	Tại buổi phỏng vấn trực tuyến, các doanh nghiệp nhỏ và vừa, hộ kinh doanh có thể đặt câu hỏi với các vị khách mời về các nội dung liên quan đến chính sách hỗ trợ thuế, tiếp cận vốn vay ngân hàng.</p><p class="Normal" style="text-align:right;"> 	<strong>Hoài Thu</strong></p>', 1, '15001941811663938879.jpg', NULL),
(49, 'Có một tỷ đồng nên mua căn hộ, nhà hay xe hơi?', 'Ba phương án trên đâu là hiệu quả nhất? Mong sự góp ý của mọi người. (Thu Hương, TP HCM). ', '<table align="right" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:40%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Độc giả hãy gửi câu hỏi cần tư vấn hoặc chia sẻ các bài viết liên quan đến kinh nghiệm đầu tư, làm giàu về kinhdoanh@vnexpress.net</td> 		</tr></tbody></table><p class="Normal"> 	<span style="color:rgb(34,34,34);">Chúng tôi sống ở TP HCM và đang thuê nhà khoảng 5 triệu đồng một tháng để ở. </span><span>Hiện tại, chúng tôi đang phân vân giữa ba phương án. Thứ nhất là dùng số tiền trên mua một căn hộ tầm trung, khoảng một tỷ đồng (đã bao gồm trang trí nội thất) để ở, nhưng lại lăn tăn là liệu sau này có nhanh xuống giá và bán lại có khó khăn không? </span></p><p class="Normal"> 	Thứ hai là tìm một lô đất tầm một tỷ đồng rồi đi vay mượn thêm tiền để xây căn nhà một trệt một lầu. Cái khó là chúng tôi rất sợ nợ nần và lại chưa tìm thấy địa điểm nào phù hợp. Còn phương án thứ ba là sẽ dùng số tiền này để mua một chiếc xe hơi để cuối tuần (thứ Bảy và chủ Nhật, hoặc ngoài giờ hành chính), chồng chạy xe thêm để tăng thu nhập.</p><p> 	Chúng tôi hiện đang băn khoăn giữa ba phương án này và chưa thể đưa ra quyết định. Rất mong độc giả cho lời khuyên để tôi có thể lựa chọn được phương án sử dụng số tiền trên hợp lý nhất.</p>', 1, NULL, NULL),
(50, 'Mở rộng đối tượng hành nghề đại lý thuế', 'Chỉ cần tốt nghiệp cao đẳng trở lên, thuộc các ngành kinh tế, thuế, tài chính, kế toán, luật… là đủ điều kiện dự thi làm đại lý thuế.', '<p> 	Thông tư số 51 của Bộ Tài chính có hiệu lực từ 15/7, có nhiều sửa đổi theo hướng mở hơn trong tổ chức thi và điều kiện được cấp chứng chỉ hành nghề dịch vụ làm thủ tục về thuế.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941821948180789.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại lý thuế phát triển sẽ hỗ trợ tốt cho doanh nghiệp thực hiện kê khai nộp thuế. Ảnh: <em>Lệ Chi.</em></p> 			</td> 		</tr></tbody></table><p> 	<span style="color:rgb(0,0,0);">Theo đó, Thông tư quy định rõ hơn và mở rộng hơn đối tượng đủ điều kiện dự thi chứng chỉ hành nghề dịch vụ về thuế, đó là cá nhân có bằng tốt nghiệp từ cao đẳng trở lên thuộc các ngành kinh tế, thuế, tài chính, ngân hàng, kế toán, kiểm toán, luật hoặc các chuyên ngành khác và có tổng số đơn vị học trình (hoặc tiết học) các môn học về tài chính, kế toán, kiểm toán, phân tích hoạt động tài chính, thuế từ 7% trên tổng số học trình (hoặc tiết học) cả khóa học trở lên kể từ khi tốt nghiệp là đủ điều kiện dự thi.</span></p><p> 	Về tổ chức thi và cấp chứng chỉ hành nghề, điểm mới của Thông tư số 51 là đã tăng kỳ thi cấp chứng chỉ hành nghề dịch vụ về thuế từ 1 lần một năm lên ít nhất 2 lần một năm. Đơn vị tổ chức thi, cấp chứng chỉ là Tổng cục Thuế.</p><p> 	Thông tư số 51cũng  hướng dẫn, công chức, viên chức công tác trong ngành Thuế từ 3 năm trở lên (trước đây là 5 năm), nếu có nhu cầu thi chứng chỉ về thuế thì được miễn môn thi pháp luật về thuế.</p><p class="Normal"> 	Ngoài ra, Thông tư bổ sung đối tượng được miễn 2 môn thi là pháp luật về thuế, kế toán đối với trường hợp người có chứng nhận đăng ký hành nghề kế toán do cơ quan có thẩm quyền cấp theo quy định của Bộ Tài chính; công chức, viên chức thuế có ngạch công chức, viên chức từ chuyên viên thuế, kiểm tra viên cao đẳng thuế, giảng viên trở lên và có thời gian công tác liên tục trong ngành thuế từ 5 năm trở lên (trước đây là 10 năm trở lên) thì được miễn thi 2 môn trên.</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Đại lý thuế là đơn vị chịu trách nhiệm trong việc kê khai tình hình nộp thuế và các vấn đề liên quan của doanh nghiệp trước Cơ quan thuế. Chính vì vậy để có thể hoạt động thì Đại Lý thuế phải được cấp phép hành nghề, phải có hai nhân viên có chứng chỉ hành nghề làm thủ tục về thuế do Bộ Tài Chính cấp. Ngoài ra một yêu cầu khách quan khác là các nhân viên đại lý thuế phải có kinh nghiệm và chuyên môn cao thì mới đáp ứng nhu cầu của doanh nghiệp và đảm bảo việc kê khai chính xác trước cơ quan thuế.</td> 		</tr></tbody></table><p style="text-align:right;"> 	<strong>Thanh Lê</strong></p>', 1, '15001941821007831133.jpg', NULL),
(51, 'Siêu dự án 6 tỷ USD ven sông Sài Gòn của Vạn Thịnh Phát chưa được phê duyệt', 'Dự án chưa được chấp thuận chủ trương đầu tư nên Bộ Tài chính đề nghị doanh nghiệp tính toán lại tiến độ cho phù hợp.', '<p class="Normal"> 	Bộ Tài chính vừa cho ý kiến về chủ trương thực hiện dự án đầu tư xây dựng hạ tầng kỹ thuật khu công viên Mũi Đèn Đỏ và khu nhà ở đô thị quy mô 117ha tại phường Phú Thuận, quận 7, TP HCM. </p><p class="Normal"> 	Đây là phản hồi của cơ quan này sau khi TP HCM có văn bản xin ý kiến các bộ, ngành về việc chấp thuận đầu tư dự án với quy mô theo tính toán của nhà đầu tư là Công ty cổ phần Tập đoàn Sài Gòn Peninsula, lên tới 9.232 tỷ đồng. </p><p class="Normal"> 	Theo Bộ Tài chính, với quy mô vốn đầu tư như đề xuất, việc quyết định chủ trương đầu tư dự án thuộc thẩm quyền của Thủ tướng. Bộ Kế hoạch & Đầu tư sẽ là cơ quan thẩm định dự án trình Thủ tướng xem xét, quyết định.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194183463608584.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mô hình dự án Mũi Đèn Đỏ tại TP HCM.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Tuy nhiên, Bộ Tài chính cũng đưa ra một vài ý kiến liên quan đến tài chính và tiến độ thực hiện dự án. Cụ thể, </span><span>Bộ Tài chính cho rằng tiến độ dự án đang chậm so với kế hoạch, nhà đầu tư dự kiến khâu chấp thuận chủ trương đầu tư dự án hoàn thành trong quý IV/2016, giải phóng mặt bằng phần diện tích còn lại thực hiện quý I/2017, triển khai hệ thống hạ tầng kỹ thuật dự án vào quý II/2017, hạ tầng xã hội từ quý IV/2017 đến quý I/2018. Nhưng đến nay chủ trương đầu tư dự án chưa được phê duyệt, nên Bộ Tài chính đề nghị Sài Gòn Peninsula rà soát, tính toán lại tiến độ dự án cho phù hợp.</span></p><p class="Normal"> 	Về năng lực nhà đầu tư, Bộ Tài chính cho rằng theo quy định của pháp luật, nhà đầu tư phải có vốn chủ sở hữu tối thiểu 20% tổng mức đầu tư đối với dự án có quy mô sử dụng đất dưới 20ha, và không thấp hơn 15% tổng mức đầu tư với dự án quy mô trên 20ha. Như vậy, vốn sở hữu tối thiểu phải có để Sài Gòn Peninsula tham gia dự án khoảng gần 1.400 tỷ đồng.</p><p class="Normal"> 	Theo nội dung thuyết minh phương án tài chính dự án, nhà đầu tư dự kiến góp vốn chủ sở hữu thực hiện dự án khoảng 6.000 tỷ đồng đủ điều kiện về năng lực tài chính thực hiện dự án.  Tuy nhiên, Bộ Tài chính cho rằng, nhà đầu tư cần bổ sung phương án cam kết góp vốn chủ sở hữu theo tiến độ thực hiện, làm cơ sở để cơ quan quản lý nhà nước giám sát việc thực hiện góp vốn.</p><p class="Normal"> 	Về cơ cấu nguồn vốn và phương án huy động vốn thực hiện dự án, theo hồ sơ dự án Sài Gòn Peninsula sẽ huy động từ 3 nguồn: vốn chủ sở hữu từ nhà đầu tư 6.000 tỷ đồng (chiếm 65% tổng vốn đầu tư), vốn vay dự kiến 3.000 tỷ đồng (chiếm 32,5%) và vốn ứng trước của khách hàng 232 tỷ (chiếm 2,5%). Đến nay, nhà đầu tư đã ký cam kết tài trợ tín dụng với ngân hàng và nhà băng này cũng đã xác nhận cung cấp tín dụng cho nhà đầu tư thực hiện dự án, nhưng cơ quan quản lý cho rằng hiện chưa rõ số tiền cho vay cụ thể. Do vậy, nhà đầu tư cần tiếp tục cụ thể hóa bằng hợp đồng tín dụng, xác định rõ tiến độ và khả năng huy động, lãi suất, thời hạn vay vốn.</p><p class="Normal"> 	<span>Dự án Saigon Peninsula có quy mô 118 ha đã được TP HCM chấp thuận cho Công ty cổ phần Tập đoàn Sài Gòn Peninsula làm chủ đầu tư và đã được phê duyệt quy hoạch tỷ lệ 1/500 với các hạng mục công trình phức hợp, gồm công viên đa chức năng, bến cảng du thuyền quốc tế, văn phòng, khu biệt thự, căn hộ, khách sạn, các khu chức năng khác cùng nhiều hạng mục công trình hạ tầng kỹ thuật. </span><span>Tập đoàn Sài Gòn Peninsula là </span><span>liên danh giữa Công ty cổ phần Tập đoàn Vạn Thịnh Phát và 2 nhà đầu tư nước ngoài là Pavilion Group và Genting Group đến từ Malaysia. </span></p><p class="Normal" style="text-align:right;"> 	<strong style="text-align:right;">Minh Châu</strong></p>', 1, '15001941831962930710.jpg', NULL),
(52, 'Tiền đồng bị định giá yếu vì một chiếc hamburger', 'So sánh một chiếc Big Mac của McDonald''s tại Việt Nam với Mỹ, The Economist cho rằng tiền đồng đang bị định giá thấp hơn USD 50,2%.', '<p class="Normal"> 	<span>Tạp chí <em>The Economist</em> vừa công chỉ số Big Mac năm nay. Theo đó, một chiếc Big Mac tại Việt Nam có giá 60.000 đồng, tương đương khoảng 2,64 USD. Trong khi đó, giá này tại Mỹ là 5,3 USD. Dựa trên thuyết ngang giá sức mua (PPP), các chuyên gia cho rằng tiền đồng đang bị định giá thấp hơn 50,2% so với đôla Mỹ. Con số này ngày càng lớn so với các lần tính toán trước của Economist.</span></p><p class="Normal"> 	Tỷ giá chính thức được tạp chí này sử dụng tại thời điểm tính là 22.733 đồng đổi một USD. Nhưng theo PPP với sản phẩm là Big Mac, con số này chỉ là 11.321 đồng.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941851892185016.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Giá Big Mac được sử dụng để ước lượng giá nhiều tiền tệ trên thế giới. Ảnh: <em>Reuters</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Công bố lần đầu năm 1986, dựa trên thuyết ngang giá sức mua (PPP), chỉ số này tính giá Big Mac - loại hamburger phổ biến nhất của McDonald’s tại các quốc gia trên thế giới, để chỉ ra đồng tiền nước đó đang bị định giá cao hay thấp. Nguyên tắc (theo PPP) là trong dài hạn, tỷ giá nên biến động theo hướng khiến giá hai mặt hàng hoặc dịch vụ giống nhau tại 2 nước phải bằng nhau.</p><p class="Normal"> 	Ví dụ, một chiếc bánh Big Mac tại Mỹ hiện có giá 5,3 USD, còn tại Trung Quốc là 2,92 USD theo tỷ giá chính thức. Như vậy, NDT đang bị định giá thấp hơn 45%.</p><p class="Normal"> 	Tuy vậy, các chuyên gia của <em>The Economist </em>cho biết chỉ số này không được tạo ra để làm thước đo chính xác xem giá trị một đồng tiền sai lệch thế nào. Nó chỉ giúp các lý thuyết kinh tế dễ hiểu hơn mà thôi. Dù vậy, Big Mac Index đã trở thành chuẩn mực toàn cầu, được đưa vào một số cuốn sách kinh tế và còn là chủ đề của 20 nghiên cứu hàn lâm.</p><p class="Normal"> 	McDonald''s mở cửa hàng tại Việt Nam năm 2014. Đây là lần đầu tiên sau hai thập kỷ, tập đoàn đồ ăn nhanh hàng đầu thế giới gia nhập một thị trường Đông Nam Á. Sau đó vài ngày, tạp chí The Economist đã bổ sung tiền đồng vào Big Mac Index. Hiện chỉ số này theo dõi giá Big Mac tại 48 nền kinh tế trên thế giới.</p><p class="Normal" style="text-align:right;"> 	<strong>Hà Thu</strong> <em>(theo Economist)</em></p>', 1, '1500194184654921518.jpg', NULL),
(53, '''Chúa đảo'' Tuần Châu dừng dự án thay thế chợ hoá chất Kim Biên', 'Sở Công Thương TP HCM đang rốt ráo tìm nhà đầu tư mới sau khi tập đoàn do ông Đào Hồng Tuyển làm chủ tịch ngừng triển khai dự án.', '<p class="Normal"> 	Sở Công Thương TP HCM cho biết, Tập đoàn Tuần Châu đã đề xuất dừng triển khai dự án xây dựng Trung tâm Kinh doanh hương liệu, hoá chất trên khu đất gần 20 ha tại quận 8 và huyện Bình Chánh (thay thế chợ Kim Biên, quận 5)<span>. Nguyên nhân là do phát sinh một số vướng mắc pháp lý sau khảo sát, đồng thời vị trí này cũng không phù hợp với quy hoạch của thành phố. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941861415286567.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					<span>Trung tâm Kinh doanh hương hiệu, hoá chất sẽ thay thế chợ Kim Biên. </span></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Đây từng là một trong 3 dự án trọng điểm được Tập đoàn Tuần Châu đề xuất xây dựng vào đầu năm nay, cùng với dự án Trung tâm hải cảng - hải sản huyện Cần Giờ và Đại lộ ven sông Sài Gòn. </span></p><p class="Normal"> 	Trước đó, lãnh đạo UBND TP HCM cũng đề nghị các đơn vị liên quan tạo điều kiện cho tập đoàn này hoàn tất thủ tục pháp lý nhằm sớm triển khai và bố trí cho các cơ sở kinh doanh hương liệu vào hoạt động tại trung tâm trong tháng 12/2017. </p><p class="Normal"> 	Sau khi Tập đoàn Tuần Châu rút lui, Sở Công Thương đang khẩn trương tổ chức đấu thầu lựa chọn nhà đầu tư và bồi thường, giải phóng mặt bằng khu đất hơn 11 ha gần Khu công nghiệp Phong Phú (quận 8) được phê duyệt trước đó. Đường đến khu đất này không qua nội ô thành phố nên hạn chế tác động xấu của sự cố hóa chất trong quá trình vận chuyển.</p><p class="Normal"> 	Theo phương án mới nhất, việc lựa chọn chủ đầu tư mới sẽ hoàn tất vào cuối năm nay. Trung tâm được đầu tư theo phương thức kêu gọi nguồn vốn xã hội hóa. Chủ đầu tư là đơn vị trực tiếp xây dựng, tổ chức quản lý và vận hành trung tâm. </p><p class="Normal"> 	Ngoài ra, Sở Công Thương cũng đã phối hợp với các quận, huyện nhằm rà soát danh sách, hiện trạng các cơ sở kinh doanh hóa chất và hương liệu trên địa bàn. Trước mắt, đơn vị này chuẩn bị phương án di dời các cơ sở tại một số quận như quận 5, 10, 11…</p><p class="Normal"> 	Chợ hóa chất Kim Biên được người Sài Gòn gọi là chợ “tử thần” do tình trạng bán hoá chất nguy hiểm tràn lan suốt 50 năm qua. Chợ<span> có 16 quầy, sạp (hộ cá thể) kinh doanh hương liệu, bột màu, bơ sữa, phụ gia thực phẩm... Xung quanh có 17 cơ sở mua bán những mặt hàng này và cả hóa chất công nghiệp.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Phương Đông</strong></p>', 1, '1500194185116270396.jpg', NULL),
(54, 'Thịt heo tăng giá lên 35.000 đồng một kg', 'Giá heo hơi đã tăng hơn 10.000 đồng mỗi kg từ mức "đáy" cách đây 3 tháng, nhưng người nuôi vẫn chưa có lãi.', '<p class="Normal"> 	Ông Nam, chủ trang trại nuôi heo tại Hoài Đức (Hà Nội) cho biết một tuần trở lại đây giá heo hơi được thương lái mua gom tận chuồng đã "tăng dần đều", hiện ở mức 34.000 đồng một kg.</p><p class="Normal"> 	Mức này đã cao hơn 12.000 - 14.000 đồng một kg so với thời điểm thịt heo rớt giá thảm cách đây 3 tháng. "Giá đang nhích dần từng ngày, hôm đầu tuần là 28.000 đồng một kg, thì nay đã tăng lên 34.000 đồng", ông Nam chia sẻ.</p><p class="Normal"> 	Tại một số tỉnh, thành phía Bắc như Bắc Giang, Bắc Ninh, Hải Dương, Hưng Yên... giá mỗi kg heo hơi được thương lái trả 34.000 - 35.000 đồng. Còn g<span>iá thu mua thịt heo tại Đồng Nai, nguồn cung cấp heo chính cho TP HCM và các tỉnh lân cận, bình quân 31.000  đồng một kg. Dự báo giá sẽ còn tiếp tục tăng trong những ngày tới nếu thị trường tiêu thụ tốt.</span></p><p class="Normal"> 	Dù giá tăng, nhưng các hộ chăn nuôi heo cho biết mới đủ hoà vốn. "Giá phải tăng thêm 3.000 - 5.000 đồng, lên 40.000 đồng một kg thì người nuôi mới có lãi", ông Nam giãi bày.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941861040793947.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Giá heo hơi xuất chuồng tại nhiều địa phương được thương lái thu mua giá cao nhất 35.000 đồng một kg, tăng 12.000 - 14.000 đồng mỗi kg so với đợt thấp điểm cách đây 3 tháng. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Lý giải giá hơi heo tăng nhanh tuần qua, các mối buôn cho hay chủ yếu do thương lái Trung Quốc tăng mua gom.</p><p class="Normal"> 	<span>Trước thực tế này, ông Đỗ Thắng Hải - Thứ trưởng Công Thương xem đây là "tín hiệu tốt cho người chăn nuôi cả nước sau hơn 3 tháng chật vật đối mặt với đợt rớt giá sâu".</span></p><p class="Normal"> 	Tuy nhiên, ở chiều ngược lại diễn biến tăng giá diễn ra nhanh trong vòng vài ngày cũng được nhà chức trách lưu ý cần xem xét thận trọng, là "tín hiệu có tốt nhưng bền vững hay không". Bởi, hiện Trung Quốc nhập thịt heo từ Việt Nam chủ yếu qua đường tiểu ngạch, chứ không phải chính ngạch. Nếu chỉ trông chờ vào thị trường này sẽ rủi ro.</p><p class="Normal"> 	"Cần xem xét một cách thận trọng tín hiệu đó có bền vững hay không. Nếu tăng đàn nhanh chóng có thể dẫn tới nguồn cung dư thừa, tiếp tục quay lại thực tế giải cứu heo như hiện tại", Thứ trưởng Công Thương nói.</p><p class="Normal"> 	Cảnh báo tương tự cũng được lãnh đạo Cục Chăn nuôi (Bộ Nông nghiệp & Phát triẻn nông thôn) đưa ra. Theo ông Nguyễn Xuân Dương - Phó cục trưởng Chăn nuôi, heo tăng giá trở lại là kết quả của nỗ lực "giải cứu" thịt heo từ các Bộ, ngành, người dân. "Chúng ta đừng vội mừng, không vội tăng đàn, tăng quy mô chăn nuôi, phải hết sức bình tĩnh", ông Dương đánh giá.</p><p class="Normal"> 	Vị lãnh đạo cơ quan quản lý ngành chăn nuôi cũng phủ nhận thông tin, do thương lái Trung Quốc quay trở lại mua gom khiến giá lợn hơi tăng trở lại.</p><p class="Normal"> 	“Thời gian qua, chúng ta kiểm soát tốt tình hình, giảm cung, tăng cầu. Nhu cầu tiêu thụ thịt lợn tăng mạnh, người dân cả nước chung tay hỗ trợ chăn nuôi, nhiều điểm bán thịt lợn rẻ nhằm kích cầu ở các địa phương được tổ chức...", ông nói thêm.</p><p class="Normal"> 	Với nhu cầu thị trường, cơ cấu lại đàn nuôi... nhà chức trách dự báo giá heo hơi nửa cuối năm 2017 sẽ "nhích dần lên", dao động 35.000 - 40.000 đồng một kg. Việc thành lập Cục Chế biến & Phát triển thị trường nông sản mới đây cũng được kỳ vọng sẽ tạo ra đầu mối xây dựng chuỗi liên kết trong chăn nuôi, phát triển thị trường đầu ra cho lĩnh vực sản xuất này.<br><br> 	Đại diện Bộ Nông nghiệp & Phát triển nông thôn cũng cho biết, cơ quan này đang tích cực đàm phán với phía Trung Quốc để đưa thịt heo Việt Nam sang thị trường này theo đường chính ngạch, đồng thời đa dạng hoá thị trường xuất khẩu để giảm áp lực do cung trong nước vẫn đang quá lớn, mà đầu ra hạn hẹp.</p><p class="Normal" style="text-align:right;"> 	<strong>Anh Minh</strong></p>', 1, '15001941862131349077.jpg', NULL),
(55, 'Hơn 210.000 tỷ đồng doanh thu viễn thông trong nửa đầu năm', 'Con số doanh thu trong lĩnh vực viễn thông 6 tháng đầu năm cao hơn gấp đôi so với cùng kỳ 2016. ', '<p class="Normal"> 	Bộ Thông tin & Truyền thông cho biết, 6 tháng đầu năm, doanh thu trong lĩnh vực viễn thông phát sinh ước đạt 213.355 tỷ đồng (không tính phần kinh doanh của Viettel ở nước ngoài), gấp đôi so với mức 93.500 tỷ đồng của cùng kỳ 2016. Con số này được tính toán dựa trên tổng doanh thu của cả doanh nghiệp kinh doanh mạng di động và cố định. </p><p class="Normal"> 	<span>Trong đó, doanh thu của 3 nhà mạng di động lớn trên thị trường viễn thông đều tăng trưởng 2 con số. Cụ thể, Tập đoàn Bưu chính Viễn thông Việt Nam (VNPT) đạt</span><span> 68.000 tỷ đồng, lợi nhuận </span><span>gần 2.400 tỷ, tăng 15,5% so với cùng kỳ.</span></p><p class="Normal"> 	<span>Tập đoàn Viễn thông Quân đội (Viettel) đạt doanh thu 117.714 tỷ đồng, lợi nhuận trước thuế 21.470 tỷ. Tính đến cuối tháng 6, tính chung tất cả các thị trường trong và ngoài nước, Viettel có gần 100 triệu thuê bao. Trong đó thuê bao trong nước đạt hơn 61 triệu, thuê bao nước ngoài hơn 30 triệu.</span></p><p class="Normal"> 	Tổng công ty viễn thông di động (MobiFone) đạt doanh thu 21.300 tỷ đồng, tăng 18,7% so với cùng kỳ, lợi nhuận 2.600 tỷ. </p><p class="Normal"> 	<span>Bộ Thông tin & Truyền thông cũng cho biết, tính đến hết tháng 6, số thuê bao 2G khoảng 68,8 triệu, 3G khoảng 54,2 triệu. Điện thoại cố định tiếp tục giảm chậm, tổng số thuê bao điện thoại hơn 130 triệu, trong đó có 7,3 triệu thuê bao cố định. </span></p>', 1, NULL, NULL),
(56, 'Uber chật vật tìm cách tồn tại ở Nga', 'Sau 3 năm hiện diện tại Nga, ứng dụng đi chung xe Uber đã phải bắt tay với một công ty địa phương để tiếp tục hoạt động.', '<p class="Normal"> 	<span>Uber vừa thông báo sẽ sáp nhập mảng đi chung xe với Yandex - hãng taxi của đại gia công nghệ Nga. Hai doanh nghiệp này sẽ kết hợp thành một liên doanh mới có giá trị khoảng 3,4 tỷ USD, cung cấp dịch vụ đi chung xe tại Nga và một số nước Đông Âu.</span></p><p class="Normal"> 	<span>Theo thoả thuận, Uber sẽ đầu tư 225 triệu USD. Trong khi, Yandex sẽ góp vốn 100 triệu USD và nắm phần lớn cổ phần. Đồng thời, CEO Yandex- Tigran Khudaverdyan sẽ nắm quyền điều hành công ty mới này. Thương vụ giữa hai công ty dự kiến sẽ hoàn tất vào cuối năm.</span></p><p class="Normal"> 	<span>"Chúng tôi sẽ tiếp tục xây dựng một ứng dụng đi chung xe, giúp người dùng có thêm lựa chọn thay thế việc sở hữu xe riêng hay đi phương tiện công cộng", ông Khudaverdyan cho biết.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941881450865170.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Xe taxi của Yandex rất phổ biến tại Nga. Ảnh: <em>AP</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Đây là động thái nhượng bộ hiếm có của Uber - vốn nổi tiếng hiếu chiến khi xâm nhập tất cả các thị trường trên toàn cầu. Đồng thời, nó cũng sẽ kết thúc một cuộc chiến tốn kém giữa hai công ty, kể từ khi Uber đến Nga ba năm trước.</span></p><p class="Normal"> 	<span>Thương vụ này còn phản ánh sự cạnh tranh khốc liệt mà Uber đang phải đối mặt khi bành trướng tại nước ngoài. Theo các cổ đông của Yandex, số xe dùng ứng dụng của công ty này hiện gấp đôi lượng xe Uber tại Nga.</span></p><p class="Normal"> 	Năm ngoái, Uber mới nhận ra mình bị Didi Chuxing vượt mặt ở Trung Quốc, và đã phải chi hàng tỷ USD cho các tài xế để giành được chỗ đứng tại thị trường này. Sau cuộc chiến kéo dài nhiều năm, Uber đã phải bán công ty tại Trung Quốc cho Didi Chuxing và chỉ giữ lại 17,5% cổ phần.</p><p class="Normal"> 	Từ bỏ những cuộc chiến đắt giá với các đối thủ cạnh tranh sẽ giúp Uber giảm mối lo ngại về chi phí của các nhà đầu tư. Uber đã lỗ gần 1 tỷ USD trong quý IV năm 2016 và 708 triệu USD trong ba tháng đầu năm nay.  </p><p class="Normal"> 	Yandex là một doanh nghiệp có "vai vế" tại Nga. Họ được coi là "Google của Nga" vì sở hữu và điều hành một cơ sở dữ liệu bản đồ quan trọng.</p><p class="Normal"> 	Ngoài ra, đây cũng là một thương hiệu internet có uy tín tại Đông Âu. Nhờ đó, Yandex có thể cung cấp dịch vụ tới các khách hàng tiềm năng thông qua các phương tiện trực tuyến. Đây là điều mà Uber không làm được.</p><p class="Normal" style="text-align:right;"> 	<span><strong>Anh Tú</strong> <em>(theo NY Times)</em> </span></p>', 1, '1500194188558016249.jpg', NULL),
(57, 'Gỡ nút thắt về vốn cho doanh nghiệp nhỏ', 'Các ngân hàng cho biết sẵn sàng đẩy mạnh cho vay tín chấp với doanh nghiệp nhỏ và tin rằng vẫn kiểm soát được rủi ro. ', '<p class="Normal"> 	<span>Tại </span><span style="color:rgb(34,34,34);">Ngày hội Kết nối Doanh nghiệp diễn ra ngày 15/7 tại TP HCM,</span><span> ông Đậu Anh Tuấn - Trưởng ban Pháp chế Phòng Thương mại và Công nghiệp Việt Nam - VCCI nhìn nhận, hiện nay doanh nghiệp nhỏ và vừa ở Việt Nam chiếm trên 90% nhưng họ còn gặp nhiều khó khăn trong hoạt động. Chẳng hạn như phát triển khách hàng, thị trường, đất đai, thủ tục hành chính, trong đó đặc biệt là vốn.</span></p><p class="Normal"> 	Trước thực trạng này, ông Tuấn cho biết, Chính phủ đã có những<span> chính sách tạo điều kiện phát triển cho loại hình doanh nghiệp nhỏ và vừa. Theo đó, đây không chỉ là đối tượng được bảo hộ và ưu tiên về tiếp cận vốn tín dụng, mà còn được hỗ trợ pháp lý, mở rộng thị trường, phát triển nguồn lực… </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941882037066308.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngân hàng sẽ đẩy mạnh cho vay tín chấp với doanh nghiệp nhỏ. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Tuy nhiên, đó chỉ mới là chính sách, còn thực tế, các doanh nghiệp nhỏ và vừa cho biết hiện họ vẫn đang chật vật tiếp cận vốn tín dụng. Chủ một doanh nghiệp nhỏ tại Bình Phước than thở, các doanh nghiệp nhỏ như ông hiện nay rất khó vay vốn ngân hàng. "</span><span>Nhiều khi chúng tôi đến ngân hàng ngồi đợi mấy tiếng đồng hồ nhưng lại không được phía nhà băng ra tiếp nhận", ông nói.</span></p><p> 	Một doanh nghiệp nhỏ làm nông nghiệp công nghệ cao cũng cho rằng việc tiếp cận vốn từ ngân hàng khá nhiêu khê về thủ tục. Đó là chưa kể tài sản thế chấp công nghệ cao của các doanh nghiệp không được định giá đúng với giá trị,...</p><p> 	Thừa nhận thực trạng này, ô<span style="color:rgb(0,0,0);">ng Đào Gia Hưng, Phó giám đốc khối doanh nghiệp nhỏ và vừa - SME của Ngân hàng Việt Nam Thịnh Vượng - VPBank cho rằng, nguyên nhân phần lớn là do doanh nghiệp nhỏ không có tài sản thế chấp, hạn chế</span><span style="color:rgb(0,0,0);"> về số năm thành lập, rắc rối trong vấn đề </span><span style="color:rgb(0,0,0);">sổ sách, giấy tờ vì không được hạch toán rõ ràng, chi tiết... Do đó, khi tiếp cận các ngân hàng, </span><span style="color:rgb(0,0,0);">thời gian phê duyệt với doanh nghiệp nhỏ sẽ bị kéo dài và chưa nhiều nhà băng sẵn sàng cho vay…</span></p><p> 	<span style="color:rgb(0,0,0);">Qua đó, ông Hưng chỉ ra cách mà ngân hàng ông sẽ hỗ trợ doanh nghiệp nhỏ tiếp cận vốn thông qua hai kênh chính là thẻ tín dụng doanh nghiệp và sản phẩm vay tín chấp. "Với những kênh này, chúng tôi không quá khắc khe về thời gian hoạt động của doanh nghiệp (chấp nhận doanh nghiệp h</span>oạt động dưới 1 năm với điều kiện đi lên từ hộ kinh doanh); phê duyệt cho vay dựa vào tình hình thực tế kinh doanh…</p><p class="Normal"> 	Trước những quan ngại về tính rủi ro khi đẩy mạnh cho vay đối tượng doanh nghiệp nhỏ, ông Hưng cho rằng, ngân hàng luôn đặt việc quản trị kiểm soát rủi ro lên hàng đầu thông qua việc xây dựng cơ sở hạ tầng như đội ngũ thu hồi nợ sau cho vay, tư vấn khách hàng để doanh nghiệp lớn lên.</p><p class="Normal"> 	"Trong những năm qua, khi triển khai các kênh này, tỷ lệ rủi ro đều trong phạm vi kiểm soát. Mỗi tháng, số lượng doanh nghiệp SME tăng hơn 1.000 khách hàng", ông cho biết.</p><p class="Normal"> 	Ông Nguyễn Hải An, Giám đốc Trung tâm Ươm tạo Doanh nghiệp Nông nghiệp Công nghệ cao TP HCM cũng đề xuất thêm một loạt giải pháp để hỗ trợ doanh nghiệp nhỏ phát triển. Theo đó, ông cho rằng trước hết là các doanh nghiệp cần nâng cao trình độ nguồn nhân lực, khả năng nắm bắt thị trường, văn hóa kinh doanh, hoạt động tiếp thị, chương trình hợp tác…Trong đó, ông An đặc biệt nhấn mạnh đến các giải pháp hỗ trợ nguồn vốn để doanh nghiệp mở rộng sản xuất kinh doanh.</p><p class="Normal"> 	Trong khi đó, ông Nguyễn Khánh Trình - CEO của Clever Ads (đối tác cao cấp của Google tại Việt Nam) chỉ ra tầm quan trọng của việc xây dựng thương hiệu với các doanh nghiệp nhỏ. Bằng kinh nghiệm thực tế, ông tư vấn cho các doanh nghiệp một số các giải pháp phù hợp với doanh nghiệp vi mô qua việc phát triển thương hiệu trên hai công cụ tiếp thị trực tuyến lớn nhất hiện nay là Google và Facebook…</p><p class="Normal" style="text-align:right;"> 	<strong>Lệ Chi</strong></p>', 1, '15001941881992233175.jpg', NULL),
(58, 'Thương mại điện tử châu Âu có thể đạt 602 tỷ euro', 'Dự kiến tổng doanh số thương mại điện tử ở châu Âu sẽ tăng trưởng 14% so với năm ngoái, đạt 602 tỷ euro.', '<p class="Normal"> 	Đây là kết quả từ Báo cáo Thương mại điện tử châu Âu năm 2017 do tổ chức Ecommerce Europe, EuroCommerce và Ecommerce Foundation công bố. Vào năm ngoái, kinh doanh trực tuyến châu Âu đạt 530 tỷ euro (trong khi dự đoán chỉ 509,9 tỷ euro), tăng 15% so với năm trước.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941891054855833.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Báo cáo thường niên cũng cho thấy tỷ lệ các công ty có từ 10 nhân viên trở lên sở hữu website nhiều hơn đáng kể. Nếu năm 2010 con số này là 20% thì đến năm ngoái đã có 77% công ty xây dựng website. Tuy nhiên, chỉ có 18% các công ty này bán sản phẩm hoặc dịch vụ thông qua trang web của họ.</p><p class="Normal"> 	Số lượng người tiêu dùng mua hàng trực tuyến rất khả quan. 87% người dùng ở Anh đặt mua sản phẩm qua Internet, trong khi ở Đan Mạch và Đức tương ứng mức 84% và 82%. Các quốc gia như Romania, Macedonia và Bulgaria có tỷ lệ người mua sắm trực tuyến thấp nhất.</p><p class="Normal"> 	Khu vực Trung và Đông Âu chứng kiến sự phát triển mạnh mẽ của thương mại điện tử. Ở Rumani, doanh số bán hàng trực tuyến tăng 38%, trong khi thị trường thương mại điện tử ở Slovakia và Estonia tăng 35%.</p><p class="Normal"> 	Giao dịch qua biên giới cũng phổ biến hơn với 33% người mua sắm trực tuyến ở châu Âu mua hàng ở nước ngoài, trong đó Luxembourg, Nga và Thụy Sĩ đứng đầu danh sách này với tỷ lệ đều trên 60%.</p><p class="Normal"> 	Năm ngoái, hai phần ba số thanh thiếu niên từ 16-24 tuổi mua hàng trên mạng thường xuyên, trong khi chỉ một phần ba những người từ 55-74 tuổi làm việc này. Châu Âu vẫn đối mặt với nhiều thách thức như các khiếu nại của người dùng về thời gian giao hàng, lỗi kỹ thuật và hàng hóa bị hư hỏng.</p><p class="Normal" style="text-align:right;"> 	<strong>Minh Trí</strong></p>', 1, '1500194189943723369.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(59, 'Đế chế kinh doanh hơn 25 tỷ USD của Hermès', 'Thương hiệu thời trang xa xỉ đã giúp nhiều thành viên gia đình Hermès góp mặt trong danh sách tỷ phú toàn cầu.', '<p class="Normal"> 	Bắt đầu từ một cửa hàng sản xuất yên cương, sau hơn 180 năm, Hermès trở thành công ty tăng trưởng nhanh nhất trong ngành công nghiệp xa xỉ với doanh số hàng tỷ USD. <span><em>Forbes</em> ước tính, ít nhất 5 thành viên trong gia tộc này có tên trong danh sách tỷ phú toàn cầu. Hiện tổng tài sản của gia đình Dumas ước tính hơn 25 tỷ USD.</span></p><p class="Normal"> 	<strong style="color:rgb(34,34,34);">Từ nhà sản xuất yên ngựa</strong></p><p> 	Đế chế Hermès bắt đầu từ Thierry Hermès, người con thứ 6 của một chủ quán trọ. Có năng khiếu về nghề da, ông mở một cửa hàng làm yên ngựa vào năm 1837. Khách hàng của ông rất giàu có với giới thượng lưu của Paris, hoàng gia châu Âu như Hoàng đế Napoleon III và hoàng hậu Eugenie.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941901675620625.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các thế hệ trong gia đình Hermès.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Khi con trai của Thierry là Émile-Charles lên quản lý kinh doanh, thương hiệu đã mở rộng thêm mặt hàng. Ông nhanh chóng đưa tên Hermès nổi tiếng, trở thành một trong những nhà cung cấp hàng xa xỉ uy tín trên thế giới. </span><span style="color:rgb(34,34,34);">Các sản phẩm gần như khâu tay, không thể vội vã. Điều này khiến không ít lần, lễ đăng quang của hoàng gia bị trì hoãn chỉ đề chờ hãng đưa tới đủ đồ cho các đoàn xe ngựa.</span></p><p class="Normal"> 	<strong><span>Xây "ngai vàng" trong lĩnh vực thời trang</span></strong></p><p class="Normal"> 	<span>Khoảng năm 1910, cháu nội của Thierry, </span><span style="color:rgb(34,34,34);">Emile Maurice</span><span> thừa kế cơ ngơi, bắt đầu bán thêm quần áo. </span><span>Họ không lựa chọn các loại rẻ tiền mà khởi đầu với jacket chơi golf làm bằng da đặt riêng cho Hoàng tử xứ Wales.</span></p><p class="Normal"> 	<span style="color:rgb(34,34,34);">Thế hệ thứ ba của gia đình này mang tính cách phiêu lưu, sáng tạo của mình vào từng thiết kế. Ô</span><span style="color:rgb(34,34,34);">ng là người đầu tiên ứng dụng chiếc khóa kéo cho áo khoác da. Chiếc khóa kéo đã tạo nên cuộc cách mạng trong ngành quần áo, giúp xưởng của Hermès trở thành địa chỉ để nhiều công ty khác đến học hỏi, kể cả Coco Chanel.</span></p><p> 	Émile-Charles cũng bổ sung vào danh mục nhiều sản phẩm kinh điển, như: khăn lụa (năm 1937), vòng Collier de Chien (những năm 1940).</p><p> 	Sau khi Émile-Charles mất vào năm 1951, các con rể ông trở thành lãnh đạo thế hệ thứ tư. Robert Dumas quản lý hãng, cùng Jean-René Guerrand.</p><p> 	Robert Dumas ghi dấu ấn trong những thiết kế mới. Vốn có chất nghệ sĩ và hướng nội hơn bố vợ mình, Dumas khai phá thêm các sản phẩm thắt lưng và túi xách. </p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194190670881182.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các sản phẩm của hãng luôn được giới thượng lưu trên thế giới săn đón.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>9 trong số 10 chiếc khăn bán chạy nhất của hãng được tạo ra dưới sự giám sát của Robert Dumas, trong số đó có chiếc Brides de Gala năm 1957 và Astrologie năm 1963.</span></p><p class="Normal"> 	<span>Chiếc túi được ưa chuộng nhất trong lịch sử - nguyên bản mang tên Haut a </span><span>Courroie, và sau này là Kelly và Birkin, vẫn là sản phẩm được khao khát nhất trên thế giới.</span></p><p class="Normal"> 	<strong style="color:rgb(34,34,34);">Thương hiệu xa xỉ toàn cầu</strong></p><p class="Normal"> 	Thế hệ thứ 5 của "đế chế" này là Jean-Louis Dumas, con trai Robert Dumas. Ông là CEO và Giám đốc nghệ thuật của hãng. <span>Jean-Louis Dumas có cái nhìn sắc sảo vượt ra ngoài khuôn khổ. Ông muốn đưa thương hiệu mang tính toàn cầu.</span></p><p class="Normal"> 	Ông nhận ra việc bán lẻ đã thay đổi. Nếu muốn hãng có thể sống sót mà không thỏa hiệp, họ cần định vị lại sản phẩm của mình để phù hợp hơn với nhiều lĩnh vực của cuộc sống.</p><p class="Normal"> 	<span>Jean-Louis Dumas</span> mở rộng kinh doanh bằng cách đầu tư hoặc mua toàn bộ những công ty có cùng triết lý.<span> </span><span>Từ năm 1982 đến 1989, doanh số hãng tăng từ 82 triệu USD tới 446 triệu USD. Năm 2006, con số này là 1,9 tỷ USD.</span></p><p class="Normal"> 	Sau khi Dumas nghỉ hưu, hãng chọn được bộ ba thế hệ thứ 6, gồm: Axel Dumas (cháu trai <span>Jean-Louis Dumas</span><span>) - CEO; hai đồng Giám đốc nghệ thuật là Pierre-Alexis Dumas và Pascale Mussard - con trai và cháu gái của Jean-Louis Dumas.</span></p><p class="Normal"> 	Cả Pierre-Alexis Dumas và Pascale Mussard lớn lên cùng với những ký ức về Hermès. Họ biết rõ từng góc, từng người trong vương quốc thời trang bí ẩn này. Pierre-Alexis Dumas phụ trách mảng phụ kiện về lụa, đồ vải, quần áo may sẵn, còn Mussard chuyên đồ da, trang sức, phụ kiện không bằng vải. <span>Họ tìm tiếng nói chung về mặt thẩm mỹ.</span></p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019419076689406.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Hầu hết các sản phẩm của hãng đều khâu tay với mức hoàn chỉnh đến độ tuyệt đối.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Mục tiêu của họ là công ty phát triển "chạm tới ánh sáng nhưng không quá chói". </span><span>Chuyên gia phân tích thị trường hàng cao cấp Ortelli cho biết kể cả khi họ đang đẩy mạnh sản xuất và phân phối toàn thế giới nhưng họ vẫn sẽ không chạy theo những cách tiếp cận quá lỗ liễu.</span></p><p class="Normal"> 	<span>Năm 2013, Hermès lập kỷ lục lợi nhuận 1,69 tỷ USD với doanh thu 5 tỷ USD. Hãng trở thành công ty tăng trưởng nhanh nhất ngành công nghiệp xa xỉ 6 năm qua, nhờ một loạt chiến dịch xây dựng thương hiệu và tiếp thị khéo léo.</span></p><p class="Normal"> 	"Chúng tôi phải luôn giữ vững những giá trị của mình. Nhưng chúng tôi phải thường xuyên tự làm mới, và chính điều đó là nền tảng cốt lõi của Hermès", Dumas nói.</p><p class="Normal"> 	Đại diện hãng tại Việt Nam cho biết, thương hiệu này không sử dụng các ngôi sao nổi tiếng trong quảng cáo, không cấp phép cho việc sử dụng thương hiệu bừa bãi, không bị lôi cuốn bởi các xu hướng ngắn hạn. Những gì mọi người làm là tạo ra sản phẩm cần thiết, từ nguyên liệu đẹp nhất, lối thiết kế thông minh, vượt qua thời trang và có thể tồn tại qua nhiều thế hệ.</p><p class="Normal"> 	<span>Jean Paul Gaultier - nhà thiết kế tài năng, sau khi từ chối lời mời của nhiều công ty khác, đã đồng ý làm việc cho hãng từ 2003 đến 2010, vì "Đó là một công ty cho phép sự tự do sáng tạo không có giới hạn".</span></p><p class="Normal" style="text-align:right;"> 	<strong>Mai Thương</strong></p>', 1, '1500194190141694372.png', NULL),
(60, 'Giá vàng miếng tăng theo thế giới', 'Giá thế giới lên đỉnh 2 tuần kéo giá vàng trong nước tăng thêm vài chục nghìn đồng mỗi lượng sáng nay.', '<p class="Normal"> 	Đến 9h, giá vàng SJC tại Tập đoàn DOJI được mua vào ở 36,26 triệu đồng, bán ra với 36,34 triệu. Giá này tăng 60.000 đồng ở chiều mua và giảm 10.000 đồng ở chiều bán.</p><p class="Normal"> 	Tại Công ty Vàng bạc Đá quý Phú Nhuận (PNJ), giá vàng hiện niêm yết quanh 36,2 – 36,39 triệu đồng. Giá tăng 50.000 đồng ở chiều mua và 60.000 đồng ở chiều bán.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941911717895492.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Giá vàng miếng tuần này điều chỉnh khoảng 100.000 đồng mỗi lượng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Giá thế giới hôm qua có lúc lên đỉnh 2 tuần, chốt phiên tại 1.227 USD, tương đương 33,68 triệu đồng một lượng (chưa thuế, phí, gia công). Nguyên nhân là chỉ số giá tiêu dùng Mỹ gần như không đổi trong tháng 6, làm tăng khả năng Fed nâng lãi suất từ từ.</p><p class="Normal"> 	Đánh giá về các phiên giao dịch tuần qua, DOJI cho biết giá vàng miếng trong nước điều chỉnh tăng giảm theo diễn biến chung của thị trường vàng thế giới và đã có sự cải thiện cả về số lượng và chất lượng. Tính trung bình, mỗi lượng vàng miếng điều chỉnh khoảng 100.000 đồng.</p><p class="Normal"> 	Trên thị trường ngoại hối, tỷ giá trung tâm do Ngân hàng Nhà nước công bố hiện là 22.445 đồng một USD. Giá mua bán đôla Mỹ tại Vietcombank hiện quanh 22.700 – 22.770 đồng.</p><p class="Normal" style="text-align:right;"> 	<strong>Hà Thu</strong></p>', 1, '1500194191769469878.jpg', NULL),
(61, 'Giá vàng thế giới tăng vọt phiên cuối tuần', 'Giá hôm qua có lúc lên 1.232 USD một ounce, cao nhất hơn 2 tuần, sau báo cáo lạm phát Mỹ.', '<p class="Normal"> 	Chốt tuần, mỗi ounce vàng thế giới hiện có giá 1.227 USD, tương đương 33,68 triệu đồng một lượng (chưa thuế, phí, gia công). Giá vàng miếng trong nước đóng cửa quanh 36,12 – 36,36 triệu đồng.</p><p class="Normal"> 	Chỉ số giá tiêu dùng Mỹ gần như không đổi trong tháng 6. Lãi suất trái phiếu hôm qua cũng đi xuống và Dollar Index xuống thấp nhất từ tháng 9/2016 vì tin lạm phát yếu. Số liệu này đang củng cố khả năng Cục Dự trữ liên bang Mỹ (Fed) sẽ nâng lãi suất thêm lần nữa trong năm nay.</p><p class="Normal"> 	Trong phiên, có lúc vàng lên 1.232 USD một ounce, cao nhất hơn 2 tuần. Tổng cộng cả tuần, giá đã tăng 1,3%, mạnh nhất từ giữa tháng 5.</p><p class="Normal"> 	Lạm phát không nhích lên có thể khiến Fed tiếp tục nâng lãi suất, nhưng sẽ rất chậm. Nhiều chuyên gia dự báo Mỹ sẽ có đợt tăng lãi nữa trong năm nay.</p><p class="Normal"> 	David Meger – Giám đốc Giao dịch kim loại tại High Ridge Futures cho rằng các phát biểu của Chủ tịch Fed – Janet Yellen trong phiên điều trần trước Quốc hội tuần này “nới lỏng hơn dự kiến”. Các số liệu hôm qua “đã củng cố quan điểm này”, từ đó kéo vàng đi lên.</p><p class="Normal"> 	Đồng đôla yếu cũng hỗ trợ giá vàng. Ole Hansen – chiến lược gia hàng hóa tại Saxo Bank nhận định trên góc độ kỹ thuật, giá đã phá xuống dưới đáy tháng 5. Tuy nhiên, vàng đang hồi phục. “Ngưỡng quan trọng là 1.230 USD. Để tăng được, giá cần vượt mốc đó lần nữa”, ông dự báo.</p><p class="Normal"> 	Trên thị trường vật chất, dự trữ tại SPDR Gold Trust – quỹ tín thác vàng lớn nhất thế giới đã giảm 0,43% hôm thứ Năm, xuống 828,8 tấn.</p><p class="Normal" style="text-align:right;"> 	<strong>Hà Thu</strong></p>', 1, NULL, NULL),
(62, 'Đấu giá nghĩa trang lớn nhất Sài Gòn làm khu phức hợp cao cấp', '40% nghĩa trang Bình Hưng Hòa sẽ được đấu giá để hoàn phí di dời ứng trước và thực hiện các giai đoạn tiếp theo.', '<p class="Normal"> 	Trao đổi với <em>VnExpress</em>, Phó chủ tịch UBND quận Bình Tân, Nguyễn Minh Nhựt cho biết những ngày qua xuất hiện thông tin cho rằng toàn bộ diện tích đất 44ha của nghĩa trang Bình Hưng Hòa sẽ làm chung cư, trung tâm thương mại cao cấp. Tuy nhiên, đây là thông tin chưa chính xác. Trên thực tế, chỉ có 20ha đất nghĩa trang này được quy hoạch thành khu phức hợp dân cư, thương mại, trong đó khoảng 18ha dự kiến sẽ đấu giá.</p><p class="Normal"> 	Lãnh đạo quận Bình Tân cho hay, dự án di dời nghĩa trang Bình Hưng Hòa có kinh phí đầu tư lớn, lên đến gần 2.500 tỷ đồng, quy mô di dời thuộc diện rộng nên TP HCM phải chia dự án này thành nhiều giai đoạn.</p><p class="Normal"> 	Theo kế hoạch ban đầu toàn bộ nghĩa trang sẽ là công viên cây xanh và tiện ích phục vụ cho 300.000 dân xung quanh. Tuy nhiên trong quá trình triển khai chi phí giải tỏa, di dời các ngôi mộ lên đến hàng nghìn tỷ đồng mà ngân sách của TP HCM không cân đối được.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941921106907092.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					40% đất nghĩa trang Bình Hưng Hòa sẽ được đấu giá để làm khu phức hợp cao cấp. Ảnh: <em>Quỳnh Trần</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Để giải quyết bài toán kinh tế, UBND TP HCM đã cho phép điều chỉnh quy hoạch sử dụng đất thuộc nghĩa trang Bình Hưng Hòa. Theo đó, dành ra 24 ha (chiếm 54% tổng diện tích nghĩa trang) làm công viên cây xanh, còn 12 ha dùng làm trung tâm thương mại, 8 ha làm khu phức hợp.</p><p class="Normal"> 	Việc bổ sung phần đất phức hợp chung cư cao tầng, thương mại, dịch vụ khoảng 18ha sẽ được dùng để đấu giá quyền sử dụng đất. Với hình thức đấu giá này, có thể kỳ vọng làm tăng khả năng hoàn trả vốn ngân sách thành phố đã ứng trước và cung ứng đủ kinh phí để thực hiện dự án di dời toàn bộ nghĩa trang Bình Hưng Hòa ở các giai đoạn kế tiếp. Hiện nay, dự án này vẫn chưa đến giai đoạn đấu giá và cũng chưa xác định nhà đầu tư tham gia. </p><p class="Normal"> 	Ông Nhựt giải thích, trước đây, quận từng nỗ lực kêu gọi đầu tư theo hình thức doanh nghiệp tự giải tỏa mộ. Tuy nhiên hầu hết các đơn vị khi xuống địa bàn đều không dám tiếp nhận vì ngại đụng mồ mả. Vì vậy UBND quận phải xin ứng trước tiền ngân sách để giải tỏa một phần nghĩa trang Bình Hưng Hòa. Khi giải tỏa xong sẽ lấy đất bán và trả lại cho thành phố. "Theo dự toán của đơn vị thẩm định giá, nếu đấu giá 18ha đất có chức năng nhà ở, thương mại và phức hợp này sẽ mang về nguồn thu 2.496 tỷ đồng, tương đương gần 15 triệu đồng một m2”.</p><p class="Normal"> 	Trước Bình Hưng Hòa, TP HCM từng giải tỏa các khu đất để làm công viên Lê Văn Tám, Lê Thị Riêng nâng cao chất lượng sống của người dân. Tuy nhiên, ông Nhựt thừa nhận, việc di dời nghĩa trang Bình Hưng Hòa khó khăn hơn so với những nghĩa trang đã thực hiện ở giai đoạn trước.</p><p class="Normal"> 	Thống kê từ ban bồi thường giải phóng mặt bằng quận, đơn giá bồi thường chi phí đào, bốc, di chuyển, xây dựng lại cho giai đoạn đầu tiên của dự án di dời nghĩa trang Bình Hưng Hòa dao động 2-8 triệu đồng mỗi mộ tùy mức độ kiên cố hay thô sơ. Chi phí hỗ trợ cải táng là 3 triệu đồng một mộ. Giai đoạn 1 (12ha) đã bốc được gần 12.500 ngôi mộ và còn 4.051 ngôi chưa thực hiện xong. Dự kiến kể từ  31/10/2017 sẽ bốc mộ vắng chủ. Tổng kinh phí bồi thường, hỗ trợ đối với dự án giai đoạn 1 theo dự toán được duyệt gần 785 tỷ đồng.</p><p class="Normal"> 	Từ năm 2013, dự án di dời Nghĩa trang Bình Hưng Hòa, quận Bình Tân nằm trong danh sách được Chính phủ phê duyệt quy hoạch tổng thể phát triển kinh tế  xã hội TP HCM đến năm 2020, tầm nhìn đến 2025. Đây là công trình kinh tế xã hội mang tính đòn bẩy, thành phố thuận về chủ trương ngưng chôn cất và di dời để xây dựng các công trình phúc lợi xã hội và nâng cấp hạ tầng kỹ thuật đô thị.<br><br> 	Đây là dự án cải tạo môi trường sống của khoảng hơn 300.000 dân tại khu vực tiếp giáp nghĩa trang (gồm phường Bình Hưng Hòa, Bình Hưng Hòa A quận Bình Tân và phường Tân Quý, Sơn Kỳ, Phú Thọ Hòa quận Tân Phú). Mục đích góp phần thực hiện chinh trang, cải tạo bộ mặt đô thị, giải quyết tình trạng phức tạp về an ninh trật tự, đẩy lùi phạm pháp hình sự, tệ nạn xã hội tại khu vực này. Tổng số mộ và kim tỉnh là 69.220 mộ. Tổng số hộ dân bị ảnh hưởng là 58 hộ. Diện tích đất di dời là 44 ha.</p><table class="tbl_insert" style="width:100%;" cellspacing="0" cellpadding="1" align="center" border="1"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal"> 					Nghĩa trang Bình Hưng Hòa nằm ở phía Tây, cách trung tâm Thành phố khoảng 12km. Giai đoạn 1 của dự án cải tạo thực hiện 12,9ha, tổng vốn đầu tư 784,56 tỷ đồng, thời gian thực hiện năm 2011-2017. Giai đoạn 2 dự kiến triển khai năm 2017-2019, quy mô 11,54ha, kinh phí 749,6 tỷ đồng. Giai đoạn 3 sẽ hoàn thành từ 2018 đến 2020, trên diện tích đất 20,158ha, nguồn kinh phí ước tính 948 tỷ đồng.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Vũ Lê </strong></p>', 1, '15001941921507027653.jpg', NULL),
(63, 'Đại gia Trung Quốc tăng mua lại nhiều dự án bất động sản', 'Quý II, các tỉnh phía Nam bất ngờ sôi động với nhiều thương vụ mua lại dự án bất động sản của nhà đầu tư Trung Quốc.', '<p class="Normal"> 	Kể từ cột mốc tháng 4/2017 đến nay, các tỉnh thành thuộc vùng TP HCM gồm Sài Gòn, Đồng Nai và Long An ghi nhận sự xuất hiện khá dày của những thương vụ chuyển nhượng hoặc đánh tiếng mua dự án liên quan đến nhà đầu tư Trung Quốc.</p><p class="Normal"> 	Giữa tháng 4, quỹ đầu tư VinaLand Limited và VinaCapital Vietnam Opportunity Fund Limited - VOF (thuộc VinaCapital) công bố đã thoái toàn bộ vốn nắm giữ tại dự án Đại Phước Lotus (tại Đồng Nai). Khoản tiền thu về của hai quỹ này lần lượt là 48,8 triệu USD (tương đương khoảng 1.100 tỷ đồng) và 16,5 triệu USD (370 tỷ đồng). Đơn vị nhận chuyển nhượng dự án là một công ty thuộc Tập đoàn China Fortune Land Development (CFLD).</p><p class="Normal"> 	Đây là tập đoàn chuyên phát triển những thành phố công nghiệp tại Trung Quốc. Tính đến tháng 6/2016, CFLD có trên 1.100 đối tác chiến lược trong lĩnh vực thành phố công nghiệp, với khoản đầu tư vốn khoảng 4,1 tỷ USD. <span>Doanh nghiệp đã hiện diện trên hơn 50 vùng miền trên thế giới trong đó có Indonesia, Ấn Độ, Việt Nam, Mỹ...</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194193281724443.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phối cảnh dự án Đại Phước (Đồng Nai) được CFLD mua cổ phần từ 2 quỹ của VinaCapital.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Hôm 22/4, tại Đại hội cổ đông thường niên 2017 Công ty cổ phần đầu tư Nam Long (mã CK: NLG), Chủ tịch HĐQT Nguyễn Xuân Quang cho biết dự án Waterpoint (Long An) quy mô 350 ha là lợi thế lớn về quỹ đất của doanh nghiệp, đang được một nhà đầu tư trong nước và Trung Quốc ngỏ ý mua đứt.</p><p class="Normal"> 	Ông Quang tiết lộ, dự án này đã có quy hoạch 1/2000, đền bù 99%, nộp tiền sử dụng đất khoảng 95% từ năm 2009. Trong nhiều lần NLG tiến hành gặp gỡ nhà đầu tư các năm trước, dự án khủng này từng được ước tính có vốn đầu tư lên đến 2 tỷ USD và doanh nghiệp có kế hoạch chia nhỏ để phát triển dần. Hiện nay, dù có nhà đầu tư trong nước và Trung Quốc ngấp nghé muốn mua dự án, nhưng ông Quang cho hay công ty không vội vàng mà sẽ cân nhắc các đề nghị.</p><p class="Normal"> 	Tháng 5 vừa qua, P.H Group (Đài Loan) cũng đã tiến hành mua Khu công nghiệp Bàu Bàng từ Becamex tại Bến Cát (Bình Dương). Không dừng lại ở vùng TP HCM, đến tháng 6, nhà đầu tư Đài Loan này tiếp tục hoàn tất thương vụ mua dự án khách sạn Future Otis tại Nha Trang.</p><p class="Normal"> 	Cũng trong tháng 5/2017, Hong Kong Land đã thâu tóm 64% cổ phần dự án nhà ở nằm trong Khu đô thị mới Thủ Thiêm của CII.</p><p class="Normal"> 	Sau đó một tháng, Alpha King Real Estate Development JSC, doanh nghiệp thành lập tại Việt Nam nhưng có giám đốc là người gốc Hoa, tạm trú tại khách sạn của Tập đoàn Vạn Thịnh Phát, vừa được một đơn vị tư vấn môi giới bất động sản tại TP HCM hé lộ mua dự án phức hợp Saigon One Tower. Đây là cao ốc đã đình trệ nhiều năm tại khu trung tâm TP HCM, chủ đầu tư là Saigon M&C.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194193195037127.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dự án phức hợp Saigon One Tower được cho là đã được một nhà đầu tư gốc Hoa mua. Ảnh: <em>Quỳnh Trần</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Các giao dịch của những nhà đầu tư Trung Quốc hoặc gốc Hoa có đặc điểm chung là thường kín tiếng về giá trị thương vụ, thậm chí nhiều trường hợp chỉ được công bố thông tin hạn chế do các bên ràng buộc bằng cam kết bảo mật.  </p><p class="Normal"> 	Trao đổi với <em>VnExpress</em>, ông Stephen Wyatt, Tổng giám đốc Jones Lang LaSalle Việt Nam (JLL) cho biết, cuộc đổ bộ của dòng vốn Trung Quốc,<em> </em>gồm cả Đài Loan, Hong Kong vào thị trường Việt Nam thời gian qua xuất phát từ nhiều lý do.</p><p class="Normal"> 	Thứ nhất, kinh tế vĩ mô Việt Nam ổn định, thị trường bất động sản Việt Nam đang là kênh đầu tư tương đối hấp dẫn đối với giới đầu tư quốc tế. Thứ hai, các nhà đầu tư Trung Quốc, Hong Kong, Đài Loan đang có xu hướng tìm kiếm cơ hội mới bên ngoài lãnh thổ của họ nên đã tạo nên làn sóng dịch chuyển dòng vốn vào thị trường Việt Nam trong thời gian qua. Đây là hình thức đa dạng hóa danh mục và điểm đến đầu tư của hầu hết tất cả các dòng vốn săn tìm cơ hội toàn cầu.</p><p class="Normal"> 	Thứ ba, làn sóng đầu tư này diễn ra theo xu hướng tự nhiên do vị trí địa lý gần nhau. Mối quan hệ giao thương kinh tế giữa Việt Nam với Trung Quốc, Đài Loan, Hong Kong vốn đã phát triển khá tốt từ trước đó.</p><p class="Normal"> 	Ông Stephen Wyatt đánh giá, hiện nay dòng vốn từ các nhà đầu tư gốc Hoa vào thị trường Việt Nam vẫn còn khiêm tốn so với đối thủ cùng khu vực châu Á như Nhật Bản, Hàn Quốc, Singapore. Xét ngắn hạn, sức ảnh hưởng của các nhà đầu tư đến từ Đại Lục tại thị trường bất động sản Việt Nam vẫn chưa rõ nét.</p><p class="Normal"> 	Trong thời gian tới, tăng trưởng của dòng vốn Trung Quốc kỳ vọng sẽ còn nhiều biến chuyển. Điều này sẽ tác động tích cực, đặc biệt làm đa dạng hóa các loại hình đầu tư bất động sản và tạo không khí sôi động cho thị trường địa ốc Việt Nam.</p><p class="Normal"> 	Theo nghiên cứu của JLL, nhà đầu tư Trung Quốc, Đài Loan, Hong Kong quan tâm khá nhiều loại tài sản: nhà ở thấp tầng lẫn cao tầng, thương mại, khu công nghiệp và cả bất động sản nghỉ dưỡng. Họ chuộng hình thức thâu tóm khá đa dạng: mua một phần hoặc 100% cổ phần các dự án bất động sản.</p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Lê</strong></p>', 1, '15001941931578019006.jpg', NULL),
(64, 'Bộ Công Thương: Uber, Grab liên tục khuyến mại không có nghĩa vi phạm cạnh tranh', 'Nhấn mạnh việc Uber, Grab phải tuân thủ theo pháp luật Việt Nam, nhưng đại diện Bộ Công Thương không khẳng định 2 đơn vị này vi phạm cạnh tranh. ', '<p class="Normal"> 	Tại cuộc họp báo thường kỳ của Bộ Công Thương tổ chức ngày 14/7, trước câu hỏi liên quan tới chuyện quản lý hoạt động của loại hình taxi Uber, Grab, Thứ trưởng Công Thương Đỗ Thắng Hải nhìn nhận, mô hình kinh doanh mới này vừa qua đã mang lại lợi ích tốt cho người tiêu dùng cả về giá cả, chất lượng. </p><p class="Normal"> 	Trước việc Uber, Grab liên tục tung ra các chương trình khuyến mại, giảm giá để cạnh tranh với taxi truyền thống, ông Hải cho hay, cơ quan này sẽ phối hợp với Bộ Giao thông vận tải để theo dõi thêm.</p><p class="Normal"> 	"Doanh nghiệp liên tục khuyến mại không có nghĩa là vi phạm. Khi thấy doanh nghiệp khuyến mại nhiều mà kết luận ngay là họ vi phạm thì chưa chính xác. Chúng tôi sẽ phối hợp với cơ quan quản lý ngành giao thông giám sát loại hình vận tải này, đảm bảo quyền lợi người tiêu dùng", người phát ngôn Bộ Công Thương nói, nhưng cũng nhấn mạnh, "tất nhiên khi đã hoạt động tại Việt Nam thì Uber, Grab sẽ phải tuân thủ pháp luật Việt Nam".</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941941945663322.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bộ Công Thương khẳng định sẽ phối hợp với Bộ Giao thông vận tải giám sát chặt hoạt động của Uber, Grab. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trước đó, cuối tháng 5/2017 Hiệp hội Taxi Hà Nội và Hiệp hội Taxi Việt Nam đã có công văn gửi Cục Xúc tiến thương mại, khiếu nại việc Uber và Grab vi phạm Luật Cạnh tranh khi liên tục đưa ra chương trình giảm giá, khuyến mại. Các Hiệp hội này cũng đưa ra hàng loạt ví dụ cho thấy Grab vi phạm quy định tổng thời gian giảm giá trong một năm không vượt quá 90 ngày, vi phạm quy định thời gian tối đa cho một chương trình giảm giá là 45 ngày, cũng như vi phạm quy định tổng giá trị hàng hoá dịch vụ dùng để khuyến mại không vượt quá 50% tổng giá trị hàng dùng để khuyến mại...</p><p class="Normal"> 	Theo ông Đỗ Quốc Bình, Chủ tịch Hiệp hội Taxi Việt Nam, việc 2 đơn vị này bỏ cả nghìn tỷ đồng để khuyến mại, giảm giá nhằm thôn tính thị phần, "ép chết" doanh nghiệp taxi truyền thống.</p><p class="Normal"> 	Theo một tính toán của đơn vị này, thị phần số cuốc xe taxi của các hãng truyền thống đã giảm tới 60%, một lượng lớn khách hàng chuyển từ đi taxi sang xe ôm Uber hay Grab.</p><p class="Normal"> 	Trong một diễn biến liên quan tới việc giảm thị phần sau sự xuất hiện của mô hình kinh doanh taxi mới, hãng taxi Vinasun mới đây cho biết, chỉ riêng trong quý I năm nay, đã có 4.239 người lao động nghỉ việc và 300 đầu xe phải nằm bãi, mà nguyên nhân chính được cho là chịu ảnh hưởng từ cạnh tranh "thiếu lành mạnh" của Uber và Grab.</p><p class="Normal" style="text-align:center;"> 	<em>* Taxi Uber, Grab khác gì nhau?</em></p><div style="text-align: center;"> 	<div id="video-117919" data-component="true" data-component-type="video" data-component-value="117919" data-component-typevideo="2"></div></div><p class="Normal" style="text-align:right;"> 	<strong>Anh Minh</strong></p>', 1, '1500194193318320744.jpg', NULL),
(65, 'Lương IT Việt Nam có thể đạt hơn 64 triệu mỗi tháng', 'Trong nửa đầu năm 2017, mức lương cao nhất mà các công ty có thể trả cho nhân sự IT là 2.840 USD mỗi tháng, tương đương 64,5 triệu đồng.', '<p class="Normal"> 	Báo cáo về mức lương, phúc lợi và xu hướng ngành IT quý I và II năm 2017 do chuyên trang tuyển dụng IT TopDev vừa công bố cho biết, các công ty IT đang sẵn sàng trả mức lương từ 2.300 USD đến 2.840 USD mỗi tháng (tương đương 52,2 đến 64,5 triệu đồng) cho nhân sự cấp giám đốc và cao hơn. Tuy nhiên, vị trí này hiện chỉ chiếm 1% nhu cầu của thị trường tuyển dụng và hiện cũng là vị trí đang khó tuyển hàng đầu.</p><p class="Normal"> 	Trong khi đó, cần tuyển nhiều nhất là các lao động có trên 2 năm kinh nghiệm, chiếm 75% nhu cầu thị trường. Vị trí này đang được chào mời với mức lương dao động từ 750 USD đến 812 USD mỗi tháng, tương đương từ 17 đến 18,5 triệu đồng. Đa phần các vị trí này dành cho kỹ sư phần mềm hoặc Tech Architect.</p><p class="Normal"> 	Sinh viên công nghệ thông tin mới ra trường hiện cũng có triển vọng thu nhập tốt, với mức lương dao động từ 350 USD đến 520 USD, tức xấp xỉ 8 đến 12 triệu đồng mỗi tháng. TopDev đánh giá, chỉ sau một năm làm việc thì các lập trình viên mới ra trường này sẽ được tăng lương đáng kể.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194194190082049.png" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Cũng theo báo cáo, do mất cân bằng cung - cầu trong thị trường lập trình viên có chất lượng nên cuộc chiến thu hút, giữ chân nhân tài giữa các công ty ngày càng gay gắt.  Java, SQL, CSS, và Javascript là những kỹ năng được nhà tuyển dụng săn đón và sẵn sàng trả lương cao nhất.</p><p class="Normal"> 	Một số các công ty công nghệ đang sẵn lòng trả mức lương cao hơn so với thị trường và cam kết tăng tới 20% lương cho các ứng viên có 2 năm kinh nghiệm, đi kèm với đó là các phúc lợi ưu đãi khác. Thậm chí, với các lập trình viên có nhiều năm kinh nghiệm trong những lĩnh vực Cloud Computing, Big Data hay AI có thể tìm được việc với mức lương dao động từ 1.000 USD đến 1.500 USD mỗi tháng, tùy theo năng lực và vị trí đảm nhận.</p><p class="Normal"> 	Tuy nhiên, hiện có đến 42% lập trình viên được hỏi cho biết muốn nhảy việc. Lý do phổ biến nhất là không hài lòng với mức lương hiện tại, chiếm 35%. Tuy nhiên, báo cáo đánh giá, yếu tố ảnh hưởng nhiều nhất tới quyết định đi hay ở của lập trình viên là phúc lợi của công ty và những chính sách đào tạo chuyên môn. Theo khảo sát, các nhân viên cảm thấy được tôn trọng và muốn gắn bó lâu dài hơn khi công ty có các chính sách phúc lợi phù hợp, đặc biệt là đào tạo.</p><p class="Normal"> 	Hiện Việt Nam vẫn đang được xem là "thiên đường" của outsourcing nhưng những năm gần đây đã có sự chuyển mình. Thị trường công nghệ Việt Nam ghi nhận những bước thay đổi quan trọng, chủ yếu là từ mô hình outsourcing sang cung cấp sản phẩm và các dịch vụ đa ngành nhiều hơn.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194194806010807.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Chỉ trong vòng 4 năm trở lại đây, số lượng việc làm ngành IT gia tăng mạnh mẽ, nhu cầu tuyển dụng IT tăng dần đều qua các năm. Đặc biệt, năm 2016 tăng 45% so với cùng kỳ năm 2012. Dự báo đến năm 2018, thị trường tuyển dụng IT cần tới 350.000 lập trình viên, tức tăng gấp 20 lần so với năm 2016. Trong khi đó, hiện tại thị trường chỉ mới đáp ứng được khoảng 200.000 lập trình viên, tức đang thiếu khoảng 150.000 lập trình viên. Tuy nhiên, nhiều chuyên gia nhận định đây chỉ là sự thiếu hụt ngắn hạn.</p><p class="Normal" style="text-align:right;"> 	<strong>Viễn Thông</strong></p>', 1, '150019419414811876.png', NULL),
(66, 'Chưa có hình thức kỷ luật Thứ trưởng Hồ Thị Kim Thoa', 'Lãnh đạo Bộ Công Thương cho biết cơ quan này chưa đưa ra đề xuất hình thức kỷ luật sai phạm của Thứ trưởng Hồ Thị Kim Thoa. ', '<p class="Normal"> 	Tại cuộc họp báo thường kỳ của Bộ Công Thương sáng 14/7, trả lời câu hỏi của <em>VnExpress </em>về hình thức xử lý kỷ luật đối với sai phạm của Thứ trưởng Công Thương Hồ Thị Kim Thoa, ông Đỗ Thắng Hải - người phát ngôn của Bộ này cho biết, chiều qua (13/7) Uỷ ban Kiểm tra trung ương đã có cuộc họp với Ban cán sự Đảng của Bộ để thông báo kết quả cuộc họp thứ 15 của Uỷ ban, liên quan tới sai phạm của Thứ trưởng Công Thương Hồ Thị Kim Thoa.</p><p class="Normal"> 	Theo đó, Thứ trưởng Thoa đã có một số vi phạm trong thời gian giữ các chức vụ Tổng giám đốc, rồi Chủ tịch Hội đồng quản trị Công ty cổ phần Bóng đèn Điện Quang từ tháng 1/2004 đến tháng 5/2010. </p><p class="Normal"> 	"Hiện Ban cán sự Đảng Bộ Công Thương, Đảng uỷ Bộ và Chi bộ Đảng nơi Thứ trưởng Thoa sinh hoạt đang họp, xem xét cụ thể sai phạm để đề xuất hình thức kỷ luật phù hợp, đúng theo kết luận chỉ đạo của Uỷ ban Kiểm tra Trung ương", ông Hải nhấn mạnh.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941955529111.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bộ Công Thương vẫn chưa đưa ra hình thức kỷ luật đề xuất đối với sai phạm của Thứ trưởng Hồ Thị Kim Thoa.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ngày 3/7, Uỷ ban Kiểm tra Trung ương thông báo nội dung kiểm tra khi có dấu hiệu vi phạm đối với bà Hồ Thị Kim Thoa.</p><p class="Normal"> 	Theo thông báo nội dung kiểm tra, bà Thoa đã có <span>những vi phạm, khuyết điểm trong thời gian giữ chức Chủ tịch Hội đồng quản trị Công ty cổ phần bóng đèn Điện Quang. Cụ thể, bà Thoa được cho đã vi phạm trình tự, thủ tục trong việc thực hiện cổ phần hóa doanh nghiệp, không báo cáo Bộ Công nghiệp (nay là Bộ Công Thương) xử lý và xử lý không đúng số tiền lãi vay được ngân hàng cho miễn 6,7 tỷ đồng.</span></p><p class="Normal"> 	Bà cũng đã không báo cáo các cơ quan có thẩm quyền xử lý và xử lý không đúng kh<span>oản thu </span><span>30 tỷ đồng từ việc chuyển nhượng quyền khai thác, sử dụng khu đất 12 Tôn Đản (TP HCM); đã mua cổ phần vượt mức quy định; chuyển nhượng cổ phần không đúng quy định của điều lệ công ty.</span></p><p class="Normal"> 	"Những vi phạm, khuyết điểm của bà Hồ Thị Kim Thoa là nghiêm trọng, đến mức phải xem xét thi hành kỷ luật", cơ quan kiểm tra nêu.</p><p class="Normal"> 	Báo cáo tài chính năm 2016 của Công ty cổ phần Bóng đèn Điện Quang cho thấy, các thành viên gia đình Thứ trưởng Hồ Thị Kim Thoa sở hữu gần 35% vốn điều lệ tại Công ty cổ phần Bóng đèn Điện Quang (Mã CK: DQC), tương đương khoảng 574,6 tỷ đồng theo thị giá hiện tại. Trong đó, riêng bà Thoa nắm gần 1,7 triệu cổ phiếu, tương đương gần 81 tỷ đồng.</p><p class="Normal" style="text-align:right;"> 	<strong>Anh Minh</strong></p>', 1, '15001941951962992665.jpg', NULL),
(67, 'Ý tưởng 40 triệu USD từ cuộc phỏng vấn rửa bát thuê', 'Từ ý tưởng xuất hiện trong cuộc phỏng vấn, Lina Hu đã xây dựng được công ty riêng của mình với doanh thu hàng chục triệu USD mỗi năm.', '<p class="Normal"> 	Lina Hu, 52 tuổi, là chủ tịch và giám đốc điều hành của Clipper Corp, công ty chuyên cung cấp đồng phục và hàng hóa cho các công ty trong ngành bán lẻ, khách sạn, giao hàng và dịch vụ ăn uống.</p><p class="Normal"> 	Vào đầu thập kỷ 90, thất bại khi xin vào làm công việc chăm sóc khách hàng của nhà mạng AT&T tại Boston, Hu quyết định chuyển tới Los Angeles để gần bạn bè. Cô hy vọng sẽ được nhận vào rửa bát tại một nhà hàng tại đây. "Khi cuộc sống đẩy bạn vào tình thế khó khăn, bạn sẽ ngạc nhiên trước những thứ mà bạn có thể làm để sống sót", cô nói. Cuộc phỏng vấn xin việc cũng là lúc Hu bật ra được ý tưởng kinh doanh cho chính bản thân mình.</p><p class="Normal"> 	"Tôi đã nhìn thấy cơ hội khi họ phỏng vấn tôi. Họ cho tôi xem những chiếc đĩa, thứ mà tôi sẽ rửa. Tôi cũng nhìn thấy nhiều thứ khác. Tôi nghĩ Trung Quốc có nhiều nhà máy và họ có thể làm những thứ này rẻ hơn. Tôi có thể cung cấp chúng cho những nhà hàng, nơi tôi đang xin việc", Hu nói.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194195470430535.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Lina Hu, chủ tịch và giám đốc điều hành của Clipper Corp - công ty được ra đời từ ý tưởng khi cô phỏng vấn công việc rửa bát tại một nhà hàng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Nhiều người cho rằng cô sẽ thất bại. "Thứ nhất, bạn không nói được tiếng Anh. Thứ hai, bạn không có sản phẩm, bạn chỉ có ý tưởng. Và cuối cùng bạn là người châu Á, họ sẽ không coi trọng bạn", Hu nhớ lại.</span></p><p class="Normal"> 	"Với bản thân tôi, tôi không bỏ cuộc. Tôi gọi điện đến các cửa hàng khác nhau và nói rằng có thể cung cấp cho họ những sản phẩm cùng chất lượng nhưng chi phí thấp hơn, hoặc bạn có thể chi ra bằng đó tiền nhưng sẽ nhận được những sản phẩm tốt hơn. Hầu hết không ai phản hồi lại cuộc gọi từ tôi", Hu nói.</p><p class="Normal"> 	Sau nhiều nỗ lực, cuối cùng, Burger King đã đồng ý gặp và Lina Hu đã có được hợp đồng đầu tiên. Cô trở về Trung Quốc để tìm nhà máy có thể đáp ứng được công việc đó. "Họ muốn tôi trả trước tiền cho họ, nhưng tôi nói rằng tôi không có tiền. Tôi là một người tiếp thị hàng miễn phí cho họ và công ty có thể trả tiền cho tôi vì việc đó. Họ làm ra sản phẩm và tôi sẽ tìm người mua", Hu chia sẻ.</p><p class="Normal"> 	Khách hàng sau đó của <span>Clipper Corp là nhà hàng Darden, chủ sở hữu của 8 nhà hàng ăn uống, bao gồm Olive Garden và LongHorn Steakhouse. Sau đó, Hu nhận được hợp đồng với Yum Brand, đơn vị điều hành KFC, Taco Bell và Pizza Hut.</span></p><p class="Normal"> 	<span>Đến nay Clipper Corp đã thuê 80 nhân sự tại Carson và Atlanta. Các nhà máy Trung Quốc làm việc cho Clipper cũng thuê hơn 500 nhân công. </span><span>"Chúng tôi cung cấp sản phẩm cho các nhà hàng, bao gồm giỏ chiên, dao steak, khay chứa đồ, chảo, bất cứ thứ gì bạn không thể ăn được. Chúng tôi cũng cung cấp các dụng cụ và đồ dùng bằng thép không gỉ cao cấp cho bán lẻ. Chúng tôi là nhà cung cấp dịch vụ từ thiết kế, sản xuất cho tới phân phối", Lina Hu chia sẻ.</span></p><p class="Normal"> 	Clipper Corp, được thành lập vào năm 1994 và dự báo doanh thu năm nay sẽ đạt khoảng 40 triệu USD. Khách hàng bao gồm Burger King Corp, Target Corp, Home Depot và FedEx Corp.</p><p class="Normal"> 	<span>Hu đặt tên cho công ty của mình lấy cảm hứng từ những chiếc thuyền buồm của thế kỷ 19. Những chiếc thuyền lớn với số cánh buồm nhiều hơn bất kỳ chiếc thuyền nào trước đó, cho phép chúng có thể đi nhanh hơn. "Điều đó đại diện cho phong cách của tôi. Không có vấn đề gì có thể cản trở, chúng tôi sẽ luôn tiến về phía trước, và đem đến những gì chúng tôi đã hứa", Hu nói.</span></p><p class="Normal"> 	<span>Cha Hu là kỹ sư còn mẹ cô là công nhân nhà máy, họ khuyến khích cô có những mục tiêu cao cả, nhưng "ở Trung Quốc, mọi người không có nhiều lựa chọn. Đó không phải là điều bạn muốn và có sẵn". </span></p><p class="Normal"> 	<span>Cô lấy bằng cử nhân về thống kê và thạc sỹ kinh doanh tại Đại học Chiết Giang, phía nam Thượng Hải. "Thực sự không có sự hiện diện của phụ nữ trong những mô hình tại Trung Quốc, nhưng tôi luôn có ước mơ bắt đầu công ty riêng của mình", Hu nói.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Minh Sơn</strong> (<em>Theo LA Times</em>)</p>', 1, '15001941951269619391.jpg', NULL),
(68, 'Gần 10.000 USD một chùm nho Nhật Bản', 'Một chùm nho Ruby Roman của Nhật vừa xác lập kỷ lục mới khi được bán đấu giá thành công hơn 9.700 USD, tương đương 221 triệu đồng.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156570" data-component="true" data-component-type="video" data-component-value="156570" data-component-typevideo="2"></div></div> </div><p class="Normal" style="text-align:right;"> 	<strong>Phiên An</strong> <em>(theo Straits Times, Ruptly</em>)</p>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(69, 'Nông dân Trung Quốc ''vỡ mộng'' đổi đời ở Hong Kong', 'Nhiều nông dân Trung Quốc đến Hong Kong với hy vọng đổi đời nhưng hầu hết đều phải lao động vất vả đến cuối đời với thu nhập ít ỏi.', '<p class="Normal"> 	"Một đội quân" người già đẩy xe đi nhặt bìa, thùng carton khắp các con phố là hình ảnh quen thuộc tại Hong Kong. Thi thoảng, họ bắt đầu công việc từ lúc bình minh nhưng đa phần vẫn làm việc suốt đêm. Mỗi ngày, họ chỉ kiếm được khoảng 2,6 USD.</p><p class="Normal"> 	<span>"Những bà già carton" là tên gọi của họ tại thành phố giàu có này. Một tổ chức phi chính phủ ước tính Hong Kong có khoảng 5.000 "bà già carton". Con số này nhiều hơn 40% các siêu xe Ferrari, Lamborghini và Rolls Royce thường xuyên xuất hiện trên đường phố đông đúc tại Trung tâm tài chính của châu Á.</span></p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941961385889787.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Có khoảng 5.000 "bà già carton" tại Hong Kong.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Sau 20 năm, Hong Kong đã tích luỹ được khối tài sản khổng lồ từ khi trở về với Trung Quốc, nhưng đây cũng là một trong những nơi có khoảng cách giàu nghèo cao hàng đầu thế giới. Năm 2017, bất bình đẳng thu nhập tại Hong Kong cao nhất châu Á, thậm chí vượt cả Mỹ và Anh.</p><p class="Normal"> 	<span>"Tôi làm công việc này để có tiền mua thức ăn", bà Fok Mei-sung 67 tuổi chia sẻ. Người nông dân này rời tỉnh Quảng Đông đến Hong Kong 20 năm trước.</span></p><p class="Normal"> 	Đây là thời điểm các nhà máy ở Hong Kong được chuyển về những vùng quê Trung Quốc. Trong khi, một làn sóng xây dựng bùng nổ đã biến hàng nghìn mẫu đất nông nghiệp tại Hong Kong thành các toà nhà phục vụ tầng lớp trung lưu ngày càng gia tăng. Và bà Fok là điển hình cho sự phân hoá giàu nghèo ngày càng rõ rệt tại Đặc khu hành chính này.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941961885717103.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bà Fok tới Hong Kong từ 20 năm trước.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Cuối tháng 6, một cuộc biểu tình nổ ra sau khi một "bà già carton" 75 tuổi bị bắt và phải nộp phạt 5.000 đôla Hong Kong vì bán một tấm bìa carton với giá 1 đôla không có giấy phép. Sau đó, giới chức Hong Kong đã bỏ khoản tiền phạt sau khi tham khảo ý kiến công tố viên và xem xét hoàn cảnh của bà cụ.</span></p><p class="Normal"> 	"Những người già này đã lao động suốt cả đời, góp phần xây dựng thành phố tươi đẹp. Nhưng những gì các bạn thấy hiện tại là một nhóm công nhân bị lãng quên. Khi tuổi cao sức yếu, họ chỉ có thể đi nhặt giấy bìa về bán với giá thấp để duy trì cuộc sống", Terry Lum - Giáo sư tại Đại học Hong Kong cho biết.</p><p class="Normal"> 	<span>Nhiều người lao động trong nhóm này, đặc biệt là phụ nữ ít được học hành di cư từ Trung Quốc tới Hong Kong vào cuối những năm 1990 với hy vọng có thể cải thiện cuộc sống của gia đình. Họ bắt đầu làm những công việc lương thấp mà họ tìm được.</span></p><p class="Normal"> 	Trung tâm Chính sách công Hong Kong coi những lao động này là thành phần kinh tế dễ bị tổn thương nhất và cần được hỗ trợ. Trong giai đoạn 1995 - 2002, 44% công nhân vệ sinh ở Hong Kong đến từ Trung Quốc, trong đó 78% là phụ nữ.</p><p class="Normal"> 	Bà Fok cũng từng là một công nhân vệ sinh với mức lương thấp, không đủ để mua nhà. Gần đây, bà thường xuyên nghĩ về những người hàng xóm cũ ở Trung Quốc.</p><p class="Normal"> 	Bà cho biết họ nhận được tiền đền bù đất nông nghiệp lên tới hàng trăm nghìn NDT. Bà tiếc nuối vì đã chuyển hộ khẩu rời đi nên không thể nhận được số tiền này, trong khi, giá bất động sản ở Hong Kong đã tăng 400% kể từ năm 2003.</p><p class="Normal"> 	"Họ hiện rất giàu có, thậm chí không thể tiêu hết tiền. Ở độ tuổi của tôi, họ được du lịch, uống trà, chơi mạt chược...", bà chia sẻ.</p><p class="Normal"> 	Theo Giáo sư Lum, lượng nhà ở xã hội và trợ cấp của chính quyền không đủ đáp ứng cho những người lao động nghèo về hưu. Thời gian đợi nhà ở xã hội trung bình là 5 năm dù chính quyền đã cam kết xây dựng nhiều hơn.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941961881579557.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bà Fok đã phải sống trong căn nhà thuê chật chội trước khi được hỗ trợ nhà ở xã hội.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Suốt nhiều năm qua, bà Fok đã phải dành hai phần ba trên 3.000 đôla Hong Kong thu nhập mỗi tháng để thuê một căn hộ nhỏ. Sau hai năm nghỉ hưu, bà đã tiêu hết hết 50.000 đôla - số tiền dành dụm hơn 20 năm lao động. Tuy nhiên, người phụ nữ này đã may mắn nhận được nhà ở xã hội vào tháng 9 năm ngoái sau 5 năm chờ đợi.</span></p><p class="Normal"> 	Bà cho biết: "Tôi cảm thấy rất hạnh phúc nhưng tôi biết nhiều người già không được may mắn như tôi. Chúng tôi nhận được khoản trợ cấp 2.490 đôla mỗi tháng nhưng vẫn phải vất vả để kiếm sống. Khoản tiền này chỉ đủ mua rau mỗi ngày".</p><p class="Normal"> 	Hơn 14 năm ngồi xổm và cúi cong người đã khiến bà Fok bị bệnh ở lưng. Bà ly dị chồng và không nhận tiền từ hai người con trai <span>vì họ đều làm những công việc lặt vặt, thu nhập còn không đủ lo cho gia đình. "Tôi hy vọng cuộc sống của tôi ở đây sẽ giúp cho con và cháu tôi có một tương lai tốt đẹp hơn", bà Fok tâm sự.</span></p><p class="Normal" style="text-align:center;"> 	<em>* Nhà hòm tại Hong Kong</em></p><div style="text-align: center;"> 	<div id="video-116675" data-component="true" data-component-type="video" data-component-value="116675" data-component-typevideo="2"></div></div><p class="Normal" style="text-align:right;"> 	<strong>Anh Tú</strong> <em>(theo Bloomberg)</em></p>', 1, '15001941961897714128.jpg', NULL),
(70, 'Bán hàng trên Facebook sẽ bị xử lý nếu không phản hồi cơ quan thuế', 'Nếu người kinh doanh qua mạng không phản hồi thông báo về việc kê khai, Cục Thuế Hà Nội sẽ tính đến áp dụng các biện pháp mạnh hơn. ', '<p class="Normal"> 	Trao đổi với báo chí ngày 14/7, lãnh đạo Cục Thuế TP Hà Nội cho biết<span> đã có thông báo yêu cầu người kinh doanh nhận được thông báo qua tin nhắn SMS cần thực hiện phản hồi thông tin.</span></p><p class="Normal"> 	Trường hợp người kinh doanh không phản hồi, không đăng ký, kê khai thông tin, cơ quan thuế sẽ phối hợp với các cơ quan chức năng để áp dụng biện pháp ngăn chặn những hành vi vi phạm theo quy định. </p><p class="Normal"> 	Cục Thuế TP Hà Nội cũng giải thích rõ, số thuế giá trị gia tăng (VAT), thuế thu nhập cá nhân của người bán hàng qua Facebook phải nộp được tính theo tỷ lệ thuế nhân với doanh thu. Ví dụ như đối với hoạt động phân phối, cung cấp hàng hóa: tỷ lệ thuế VAT là 1%, thuế thu nhập cá nhân là 0,5%. <span>Trường hợp doanh thu từ 100 triệu đồng mỗi năm trở xuống cá nhân phải kê khai thuế nhưng không phải nộp thuế... Trong năm, nếu quy mô kinh doanh người nộp thuế có thay đổi thì thực hiện kê khai bổ sung điều chỉnh.</span></p><p class="Normal"> 	<span>Trước đó, từ cuối tháng 6, Cục Thuế TP Hà Nội đã gửi tin nhắn trên hệ thống SMS và hướng dẫn truy cập trang thông tin của cơ quan thuế tới 13.422 chủ tài khoản Facebook có các hoạt động quảng cáo và bán hàng qua mạng trên địa bàn. Trong số các tài khoản nêu trên, có 1.950 cá nhân đã đăng ký và được cấp mã số dùng để kê khai, nộp thuế. Ngành thuế cũng cho biết đã thu thập được danh tính, số điện thoại của các chủ tài khoản.</span></p>', 1, NULL, NULL),
(71, 'Người nước ngoài sắp được gửi tiết kiệm tại Việt Nam', 'Theo dự thảo thông tư của Ngân hàng Nhà nước, người nước ngoài có thể gửi tiền tiết kiệm bằng VNĐ, ngoại tệ có kỳ hạn ở các ngân hàng.', '<p class="Normal"> 	<span>Ngân hàng Nhà nước đang lấy ý kiến về dự thảo thông tư hướng dẫn giao dịch tiền gửi có kỳ hạn tại tổ chức tín dụng, chi nhánh ngân hàng nước ngoài, theo hướng người không cư trú là pháp nhân nước ngoài, cá nhân có hiện diện tại Việt Nam được gửi tiền có kỳ hạn bằng VNĐ, ngoại tệ.</span></p><p class="Normal"> 	Trong đó, các pháp nhân nước ngoài có hiện diện tại Việt Nam dưới các hình thức chi nhánh, văn phòng đại diện, văn phòng điều hành, văn phòng hoạt động, cơ quan đại diện ngoại giao, cơ quan đại diện lãnh sự tại Việt Nam gửi tiền gửi có kỳ hạn để phục vụ hoạt động của các hình thức hiện diện tại Việt Nam của người không cư trú đó.</p><p class="Normal"> 	Còn người không cư trú là cá nhân có hiện diện tại Việt Nam gồm người nước ngoài cư trú tại Việt Nam với thời hạn dưới 12 tháng; người nước ngoài học tập, chữa bệnh, du lịch hoặc làm việc cho văn phòng đại diện, văn phòng điều hành, văn phòng hoạt động, cơ quan đại diện ngoại giao, cơ quan đại diện lãnh sự của pháp nhân nước ngoài tại Việt Nam không kể thời hạn.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194198296249704.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Người nước ngoài sắp được gửi tiết kiệm tại Việt Nam.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span style="color:rgb(0,0,0);">Theo Ngân hàng Nhà nước, quy định cho phép người nước ngoài gửi tiết kiệm bằng VNĐ, ngoại tệ tại các ngân hàng thương mại nhằm đảm bảo hạn chế các dòng vốn nóng, đầu cơ trên thị trường tiền tệ, cũng như đảm bảo quyền lợi hợp pháp cho các đối tượng là người không cư trú có hiện diện tại Việt Nam.</span></p><p class="Normal"> 	Để gửi tiết kiệm có kỳ hạn hưởng lãi suất, khách hàng là người nước ngoài không cư trú chỉ được sử dụng VNĐ, ngoại tệ trên tài khoản thanh toán VNĐ, tài khoản thanh toán ngoại tệ của mình. Quy định này nhằm đảm bảo phù hợp với chủ trương phát triển thanh toán không dùng tiền mặt và mục tiêu quản lý ngoại hối, tạo thuận lợi cho công tác quản lý, giám sát của các cơ quan quản lý nhà nước.</p><p class="Normal"> 	Hiện nay, theo các quy định tại Pháp lệnh ngoại hối không có quy định cho phép người cư trú là cá nhân nước ngoài, người không cư trú được gửi tiền gửi tiết kiệm bằng đồng VNĐ cũng như ngoại tệ tại tổ chức tín dụng.</p><p class="Normal"> 	Các chuyên gia cho rằng, nếu Thông tư mới này được ban hành sẽ là bước tiến lớn. Bởi trong một thời đại mà việc dịch chuyển dân cư theo xu hướng "thế giới phẳng" và "công dân toàn cầu" thì việc làm ăn sinh sống giữa các quốc gia cần phải được gỡ bỏ giới hạn về rào cản.</p><p class="Normal"> 	Cùng với ý nghĩa đó thì việc gửi tiết kiệm bằng đồng tiền của quốc gia đó từ chính thu nhập hợp pháp cần được thừa nhận và khuyến khích. Mặt khác, điều này sẽ giúp tránh sự lãng phí lớn về nguồn vốn, vì nếu không được gửi tiết kiệm tại Việt Nam, những nguồn thu nhập hợp pháp này sẽ phải chuyển ra nước ngoài để tái đầu tư (thông qua gửi tiết kiệm hoặc tiếp tục đầu tư). </p><p style="text-align:right;"> 	<strong>Hoài Thu</strong></p>', 1, '1500194197279200086.jpg', NULL),
(72, 'Hiệp định TPP sẽ không bị sửa đổi nhiều', 'Sau cuộc họp tại Nhật Bản, 11 nước Hiệp định Đối tác Xuyên Thái Bình Dương (TPP) đã thống nhất TPP sẽ được tiếp tục với ít sự điều chỉnh.', '<p class="Normal"> 	Các nhà đàm phán 11 nước TPP đã có cuộc gặp 2 ngày qua tại Kanagawa (Nhật Bản). Kết quả quan trọng nhất họ đạt được là đồng ý thúc đẩy một hiệp định mới với rất ít điều chỉnh. Mục tiêu của các nước là thiết lập một khuôn khổ cho TPP không Mỹ trước các cuộc họp tháng 11 này tại Việt Nam.</p><p class="Normal"> 	Trưởng đoàn đàm phán Nhật Bản - Kazuyoshi Umemoto cho biết những người tham gia đã “đồng thuận về hướng đi sắp tới” để hiện thực hóa thỏa thuận. 11 nước “sẽ tiếp tục làm việc và không hạ tiêu chuẩn của TPP xuống”.</p><p class="Normal"> 	Nếu có nhiều thay đổi, TPP sẽ không chỉ gây xung đột lợi ích lớn giữa các nước, mà còn khiến Mỹ khó quay lại. Trong trường hợp Mỹ đồng ý trở lại, hiệp định sẽ được thực hiện như ban đầu.</p><table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194198123949613.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trưởng đoàn đàm phán Nhật Bản - Kazuyoshi Umemoto. Ảnh: <em>Nikkei</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Các cuộc thảo luận được dẫn dắt bởi Nhật Bản, Australia và New Zealand. 3 nước này kỳ vọng TPP có hiệu lực càng sớm càng tốt. Mexico lần này được đánh giá tích cực hơn so với lần đàm phán tháng 5 tại Việt Nam.</p><p class="Normal"> 	Tuy nhiên, việc đàm phán vẫn chưa đủ sâu để tạo ra các điều chỉnh về những vấn đề quan trọng như thuế nhập khẩu. Các thành viên cũng chưa đưa ra điều khoản để thực hiện hiệp định điều chỉnh. Trên lý thuyết, TPP chỉ có thể có hiệu lực khi được ít nhất 6 thành viên phê chuẩn. Các nước này phải đóng góp ít nhất 85% GDP toàn khối.</p><p class="Normal"> 	Tình hình trong nước cũng có thể ảnh hưởng đến độ nhiệt huyết của các thành viên. Malaysia không cử trưởng đoàn đàm phán sang tham dự. Còn Canada cũng đã thay đổi chính phủ từ sau thời điểm ký TPP.</p><p class="Normal"> 	Cuộc họp sắp tới sẽ được tổ chức vào cuối tháng 8 hoặc đầu tháng 9 ở Australia. Câu hỏi lớn nhất hiện tại là liệu các thành viên có thể duy trì được đà đàm phán và dẹp bỏ các rào cản để đạt thỏa thuận mới hay không.</p><p class="Normal" style="text-align:right;"> 	<strong>Hà Thu</strong> <em>(theo Nikkei)</em></p>', 1, '15001941981184961080.jpg', NULL),
(73, 'TP HCM ''bêu'' tên 209 doanh nghiệp nợ tiền thuế', 'Cục Thuế TP HCM vừa công bố danh sách 209 doanh nghiệp nợ thuế tính đến cuối tháng 5 với số tiền trên 2.100 tỷ đồng.', '<table class="tplCaption" cellspacing="0" cellpadding="3" align="center" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001941992128684449.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hàng trăm doanh nghiệp TP HCM nợ thuế hơn 2.100 tỷ đồng. Ảnh: <em>PV</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Trong danh sách này, đứng đầu về nợ thuế là Công ty vận tải biển và bất động sản Việt Hải, nợ hơn 159 tỷ đồng. Kế tiếp là Công ty xây dựng công trình và đầu tư địa ốc Hồng Quang với hơn 100 tỷ đồng.</span></p><p class="Normal"> 	<span>Các công ty tiếp theo như Công ty Cát Long Hải,</span><span> </span><span>Công ty TNHH dịch vụ xây dựng Trang Thiên Phát, </span><span style="color:rgb(34,34,34);">Công ty xây lắp công nghiệp</span><span>,...</span><span> nợ lần lượt 52,6 tỷ đồng, 44,4 tỷ đồng và 43,9 tỷ đồng...</span></p><p class="Normal"> 	Cục Thuế Thành phố cho biết, dù cơ quan này đã áp dụng những biện pháp cưỡng chế như đề nghị thu hồi giấy đăng ký kinh doanh, thông báo hóa đơn không còn giá trị sử dụng, cưỡng chế tài khoản ngân hàng, thông báo bỏ địa chỉ kinh doanh... nhưng vẫn chưa thu được nợ thuế.</p><p class="Normal"> 	Đại diện ngành thuế cho biết sau danh sách này, những đơn vị khác không có ý thức trong việc nộp thuế cũng sẽ bị "bêu tên".</p><p class="Normal"> 	Trước đó vào cuối tháng 4, Cục Thuế TP HCM đã công khai 182 doanh nghiệp nợ thuế và thực hiện biện pháp ngăn chặn là tạm dừng xuất cảnh với những đối tượng chây ì nợ, đồng thời tăng cường thực hiện các biện pháp đôn đốc thu nợ và cưỡng chế nợ thuế.</p><p class="Normal"> 	<em>Danh sách 10 công ty nợ thuế nhiều nhất.</em></p><table class="tbl_insert" style="width:100%;" cellspacing="0" cellpadding="1" align="center" border="1"><tbody><tr><td> 				<strong>STT</strong></td> 			<td> 				<strong>Tên công ty</strong></td> 			<td> 				<p> 					<strong>Số tiền nợ </strong></p> 				<p> 					<em>(tỷ đồng)</em></p> 			</td> 		</tr><tr><td> 				1</td> 			<td> 				Công ty vận tải biển và BĐS Việt Hải</td> 			<td> 				159,4</td> 		</tr><tr><td> 				2</td> 			<td> 				Công ty xây dựng công trình và đầu tư địa ốc Hồng Quang</td> 			<td> 				100 </td> 		</tr><tr><td> 				3</td> 			<td> 				Công ty Cát Long Hải</td> 			<td> 				52,6</td> 		</tr><tr><td> 				4</td> 			<td> 				Công ty TNHH dịch vụ xây dựng Trang Thiên Phát</td> 			<td> 				44,4</td> 		</tr><tr><td> 				5</td> 			<td> 				Công ty xây lắp công nghiệp</td> 			<td> 				43,9</td> 		</tr><tr><td> 				6</td> 			<td> 				Công ty thép Thăng Long</td> 			<td> 				43,8</td> 		</tr><tr><td> 				7</td> 			<td> 				Công ty đầu tư và xây dựng Minh Sơn</td> 			<td> 				37,5</td> 		</tr><tr><td> 				8</td> 			<td> 				Công ty đầu tư thương mại dịch vụ Tân Phong</td> 			<td> 				37,5</td> 		</tr><tr><td> 				9</td> 			<td> 				Công ty TNHH kinh doanh nhà Tân Hồng Uy</td> 			<td> 				37</td> 		</tr><tr><td> 				10</td> 			<td> 				Công ty xây dựng đầu tư và phát triển Linh Phong</td> 			<td> 				36,6</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Hoài Thu</strong></p>', 1, '1500194199185832364.jpg', NULL),
(74, 'Thịt cá sấu ''cháy hàng'' ở Thái Lan', 'Trong lúc giá da cá sấu giảm sâu thì nhu cầu tiêu thụ các sản phẩm thịt, máu, mật loài bò sát này tại Thái Lan lại tăng mạnh.', '<p class="Normal"> 	Thịt cá sấu đang được tiêu thụ mạnh tại Thái Lan nhờ niềm tin vào lợi ích sức khỏe và sự tò mò của khách du lịch đến đây. Hiện tại, giá thịt được bán ở mức 300 baht (8,84 USD) mỗi kg.</p><p class="Normal"> 	Cùng với đó, do nhu cầu nguyên liệu để sản xuất các thực phẩm chức năng bồi bổ sức khỏe nên giá mật và máu cá sấu cũng đang khá cao. Cụ thể, máu cá sấu được mua với giá 500 baht (14,73 USD) một kg. Riêng mật cá có giá đến 40.000 baht (1.178 USD) mỗi ký.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156529" data-component="true" data-component-type="video" data-component-value="156529" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:left;"> 		<span>Dù đang có giá nhưng nguồn cung thịt cá sấu hiện khá thiếu. Nguyên nhân là do giá da cá sấu đang giảm sâu và các trang trại không thể mổ cá chỉ vì bán lấy thịt.</span></p> </div><p class="Normal" style="text-align:right;"> 	<strong>Phiên An</strong> (<em>theo Reuters</em>)</p>', 1, NULL, NULL),
(75, 'Vietcombank được bình chọn ''ngân hàng tốt nhất Việt Nam''', 'Ngân hàng được đánh giá cao về phát triển bền vững, kết quả hoạt động kinh doanh, quản lý rủi ro, công nghệ thông tin, chất lượng điều hành. ', '<p class="Normal"> 	Vào ngày 13/7, tại Island Shangri La - Hong Kong, Tạp chí <em>Euromoney </em>đã tổ chức lễ trao giải thưởng Awards for Excellence với sự tham dự của lãnh đạo cấp cao các định chế tài chính của gần 100 quốc gia trên thế giới. Vietcombank là ngân hàng duy nhất của Việt Nam tham dự lễ trao giải và nhận giải thưởng "Ngân hàng tốt nhất Việt Nam năm 2017". Đây là năm thứ ba liên tiếp ngân hàng nhận giải thưởng này.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194200327526736.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại diện Vietcombank nhận giải thưởng "Ngân hàng tốt nhất Việt Nam năm 2017" do Tạp chí Euromoney trao tặng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Giải thưởng "Best Bank in Vietnam" trao cho một ngân hàng dẫn đầu dựa trên các yếu tố khả năng phát triển bền vững, kết quả hoạt động kinh doanh, quản lý rủi ro, công nghệ thông tin, chất lượng điều hành và những đóng góp cho thị trường tài chính ngân hàng nội địa. Giải thưởng bình chọn dựa trên các tiêu chí tổng doanh thu, tổng tài sản, lợi nhuận trước thuế, thu nhập thuần…</p><p class="Normal"> 	Việc xét thưởng do Euromoney tiến hành bao gồm cả yếu tố định tính và định lượng nhằm đảm bảo tính công bằng, hướng đến việc tạo điều kiện cho các ngân hàng thể hiện được các thế mạnh vượt trội của mình.</p><p class="Normal"> 	Thời gian qua Vietcombank nhận nhiều giải thưởng khác như vào top 500 ngân hàng hàng đầu thế giới, top 500 ngân hàng mạnh nhất châu Á do Tạp chí <em>The Banker </em>đánh giá, "Thương hiệu ngân hàng giá trị nhất Việt Nam năm 2017" theo bình chọn của Tạp chí <em>Forbes...  </em></p><p class="Normal"> 	Giải thưởng Awards for Excellence do Tạp chí <em>Euromoney </em>bình chọn bao gồm trên 20 giải thưởng quốc tế và các giải thưởng Best Bank trao cho các ngân hàng dẫn đầu tại gần 100 quốc gia. Mục đích giải thưởng nhằm vinh danh các tổ chức và cá nhân thể hiện tinh thần lãnh đạo, động lực phát triển vượt trội tại các thị trường mà họ tham gia hoạt động.</p><p class="Normal"> 	Theo <em>Euromoney</em>, số lượng hồ sơ tham gia bình chọn năm nay lên đến con số 1.500 và ban tổ chức đã bình chọn 20 giải thưởng toàn cầu, hơn 50 giải thưởng khu vực và gần 100 giải ngân hàng tốt nhất tại các quốc gia.</p><p class="Normal"> 	Tạp chí <em>Euromoney </em>(trực thuộc Euromoney Institutional Investor PLC) là tạp chí kinh tế có uy tín trong lĩnh vực tài chính và thị trường vốn quốc tế, cung cấp thông tin nổi bật về thị trường trái phiếu, vốn và ngoại hối toàn cầu. Các ấn phẩm của <em>Euromoney </em>xuất bản và phát hành trên toàn thế giới với số lượng trên 25.000 bản mỗi tháng.</p><p class="Normal" style="text-align:right;"> 	<strong>Minh Trí</strong></p>', 1, '15001942001178805447.jpg', NULL),
(76, 'Vietcombank Ba Đình tài trợ tín dụng 250 tỷ đồng cho TUDI', 'Gói tài trợ sẽ giúp TUDI triển khai dự án tổ hợp văn phòng, dịch vụ thương mại cao tầng Friend’s Tower tại Hà Nội.', '<p class="Normal"> 	Ngân hàng TMCP Ngoại thương Việt Nam chi nhánh Ba Đình (Vietcombank Ba Đình) vừa ký kết thỏa thuận hợp tác toàn diện và hợp đồng tín dụng trị giá 250 tỷ đồng với Công ty CP Đầu tư Phát triển Đô thị và Thương mại (TUDI).</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942011849686629.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Theo đó Vietcombank Ba Đình sẽ tài trợ dự án tổ hợp văn phòng, dịch vụ thương mại cao tầng tại số 11 Láng Hạ, quận Ba Đình, Hà Nội (Friend’s Tower).</p><p class="Normal"> 	Friend’s Tower khởi công xây dựng vào ngày 17/7 trên diện tích đất 1.166m2, cao 20 tầng với 7 tầng khối đế, 13 tầng phần thân và 3 tầng hầm. Riêng tầng hầm B2 lắp đặt hệ thống đỗ xe hoàn toàn tự động 4 lớp hiện đại (tương đương với 3 tầng hầm). Dự án dự kiến sẽ hoàn thành vào quý II/2019.</p><p class="Normal"> 	TUDI đã triển khai nhiều dự án khác như tòa nhà hỗn hợp văn phòng - chung cư Silver Wings tại 137 Nguyễn Văn Cừ, Long Biên, Hà Nội, tòa nhà văn phòng 135 Lạc Long Quân, khu trung tâm thương mại văn phòng và nhà ở cao cấp Hapulico (Hapulico Complex), khu hỗn hợp nhà ở kết hợp thương mại, dịch vụ tại số 6 Lê Văn Thiêm (Thanh Xuân Complex)…</p><p class="Normal"> 	Trong thời gian vừa qua, Vietcombank Ba Đình và TUDI hợp tác thành công trong nhiều dự án. Lễ ký kết thỏa thuận hợp tác toàn diện giữa hai bên đánh dấu một bước phát triển trong quan hệ đối tác trên cở sở uy tín, chất lượng dịch vụ, đảm bảo hai bên cùng có lợi.</p><p class="Normal" style="text-align:right;"> 	<strong>Minh Trí</strong></p>', 1, '15001942011656821427.jpg', NULL),
(77, 'Niềng răng trong suốt với Invisalign', 'Giải pháp này phù hợp với những trường hợp răng mọc lệch hoặc khấp khểnh.', '<p> 	Nụ cười mang vẻ đẹp riêng, giúp bạn gây thiện cảm với người đối diện và tự tin hơn trong giao tiếp. Tuy nhiên, không phải ai cũng sở hữu hàm răng đều đẹp. Nếu nụ cười đó khiến bạn băn khoăn vì những chiếc răng cứ mọc theo kiểu “đường ai nấy đi" thì bạn nên khắc phục khiếm khuyết ấy. Trong đó, niềng răng là giải pháp phù hợp với những trường hợp răng mọc lệch hoặc khấp khểnh.</p><p> 	Với mong muốn nâng tầm nụ cười Việt, bác sĩ tại Elite Dental luôn lắng nghe băn khoăn của khách hàng về vấn đề làm sao để thoải mái ăn uống mà không lo lắng việc vệ sinh răng miệng; làm thế nào để chỉnh răng mà vẫn đảm bảo tính thẩm mỹ trong suốt liệu trình...Từ những buổi tư vấn trực tiếp cho bệnh nhân, Elite quyết định áp dụng giải pháp Invisalign - công nghệ niềng răng linh hoạt bằng khay chỉnh trong suốt, vừa thoải mái, vừa đem lại thẩm mỹ cao.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194201278807657.png" /></div></td> 		</tr></tbody></table><p class="subtitle"> 	Những ưu điểm của giải pháp Invisalign</p><p> 	<em>Tính thẩm mỹ</em>: khay niềng trong suốt khó nhận biết dù ở khoảng cách gần. Khay niềng có thể tháo gỡ linh hoạt khi ăn uống và vệ sinh răng miệng, người mang khay có thể tự tin giao tiếp và sinh hoạt.</p><p> 	<em>Không ảnh hưởng sức khỏe</em>: sản phẩm được nghiên cứu bởi Align Tech (Mỹ), chất liệu nhựa không gây ảnh hưởng sức khỏe và được FDA kiểm định. Bác sĩ điều trị được huấn luyện nghiệp vụ liên tục và chứng nhận bởi Invisalign mới có thể thao tác trên thực tế. Bên cạnh đó, bác sĩ sẽ cho bạn thấy kế hoạch điều trị có thể dự đoán trước bằng phần mềm ClinCheck và các ca đã điều trị tương tự nhằm giúp bạn hình dung và hiểu rõ kết quả sau khi thực hiện.</p><p> 	<em>Giải pháp chỉnh răng hiện đại</em>: Invisalign giải quyết được các ca - từ đơn giản đến phức tạp, giảm thiểu thời gian trên ghế điều trị và thăm khám. Đặc biệt, mỗi cá nhân sở hữu một chuỗi khay niềng riêng biệt.</p><p class="subtitle"> 	Cảm nhận của phái đẹp khi niềng răng</p><p> 	Chị Linh Trang - từng niềng răng tại Elite Dental chia sẻ, chị mong muốn sở hữu nụ cười đẹp để tự tin trong mọi tình huống và thành công trong cuộc sống. Chị cho rằng món đầu tư “hời” nhất là đầu tư vào nụ cười. "Tôi băn khoăn cấu trúc răng không trật tự của mình nhưng lại e dè trước câc biện pháp niềng răng truyền thống vì bất tiện, không thẩm mỹ, gây cản trở giao tiếp lẫn sinh hoạt", chị nói thêm.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942021420098473.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bệnh nhân được kiểm tra kỹ trước khi quyết định niềng.</p> 			</td> 		</tr></tbody></table><p> 	Sau khi được tư vấn bởi bác sĩ và biết đến công nghệ Invisalign từ Elite Dental, chịthấy an tâm và quyết định trải nghiệm niềng răng bằng khay trong suốt. Chị tâm sự: "Tôi đã có được nụ cười đẹp rạng rỡ nhờ giải pháp này. Suốt quá trình điều trị không đau mà rất thoải mái. Tôi hy vọng bạn có thể dẹp bỏ mọi lo lắng để tìm lại nụ cười trọn vẹn tại Elite Dental với ''trợ thủ trong suốt'' Invisalign".</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<p> 					<div style="text-align: center"><img src="/pictures/news/15001942021911243795.jpg" /></div></p> 			</td> 		</tr><tr><td> 				<p class="Image"> 					Nụ cười rạng rỡ giúp phái đẹp tự tin trong cuộc sống.</p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	Hội thảo về giải pháp niềng răng trong suốt</p><p> 	Để giải đáp thắc mắc của nhiều bạn trẻ về giải pháp niềng răng trong suốt với Invisalign, Nha khoa Elite tổ chức hội thảo "Invisalign - hành trình chỉnh nha tinh tế và hạnh phúc" vào ngày 16/7 tới đây. Sự kiện diễn ra tại không gian mới, nội thất sang trọng.</p><p> 	Tại đây, các chuyên gia về Invisalign sẽ giải đáp thắc mắc, cung cấp thông tin về quy trình, hướng dẫn sử dụng, sự phối hợp của bác sĩ và khách hàng trong quá trình điều trị... từ đó bạn có thêm cái nhìn tổng thể về Invisalign trước khi quyết định điều trị.</p><p> 	Bạn được thực hiện "Đánh giá nụ cười" bởi đội ngũ bác sĩ chỉnh nha chuyên nghiệp tại nha khoa Elite, được chụp phim x-quang, chụp hình đánh giá sơ khởi và kiểm trarăng và khớp cắn có phù hợp để điều trị với Invisalign hay không.</p><p> 	Tham dự sự kiện, bạn còn được tư vấn kỹ với bác sĩ chỉnh nha được đào tạo chuyên sâu, có chứng nhận thực hành Invisalign từ năm 2012. Với kho dữ kiện hình ảnh hàng trăm ca tương tự được thực hiện trước đó, bạn có thể hình dung kết quả khi điều trị với Invisalign.</p><p> 	Ngoài ra, chị em còn được trải nghiệm với khay Invisalign và tìm hiểu về phần mềm ClinCheck, máy scan răng kỹ thuật số; được bốc thăm may mắn giảm phí điều trị lên đến 10 triệu đồng cùng nhiều phần quà từ Elite.</p><p> 	Đại diện Nha khoa Elite cho biết: "Bằng kinh nghiệm thực hành 10 năm trong lĩnh vực chỉnh hình răng mặt, chúng tôi tin Invisalign có thể đem lại cho bạn trải nghiệm mới để tự tin với nụ cười nổi bật. Elite Dental hy vọng niềm vui của bạn sẽ đến từ những điều giản dị nhất mỗi ngày và Invisalign sẵn sàng đồng hành để bạn có được nụ cười rạng rỡ để niềm vui trọn vẹn hơn..</p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="title_news"> 			<p class="Normal"> 				Để tìm hiểu kỹ hơn về Invisalign, được trò chuyện trực tiếp và tư vấn cùng bác sĩ tại Elite Dental, bạn có thể tham gia hội thảo “Invisalign - hành trình chỉnh nha tinh tế và hạnh phúc” do Nha khoa Elite tổ chức. Đăng ký <a href="http://goo.gl/nmBH4H">tại đây</a>.</p> 			<p class="Normal"> 				Địa điểm: 51A Tú Xương, phường 7, quận 3, TP HCM<br><span>Thời gian: 8h - 12h chủ nhật ngày 16/7</span></p> 		</div> 	</div> </div><p style="text-align:right;"> 	(Nguồn: <em>Elite Dental</em>)</p>', 1, '1500194201404216142.png', NULL),
(78, 'Vietcombank ra mắt dịch vụ thanh toán thẻ qua điện thoại di động', 'Các chủ thẻ Vietcombank chỉ cần tải ứng dụng Moca có thể thanh toán hóa đơn mọi lúc mọi nơi ngay trên thiết bị di động.', '<p class="Normal"> 	Từ 17/7, dịch vụ thanh toán thẻ qua di động Vietcombank - Moca với nhiều tính năng mới dành cho các chủ thẻ tín dụng sẽ ra mắt thị trường.</p><p class="Normal"> 	<span>Đây là sản phẩm liên kết giữa Ngân hàng TMCP Ngoại Thương Việt Nam (Vietcombank) với Công ty Cổ phần Công nghệ và Dịch vụ Moca.</span></p><p class="Normal"> 	Với dịch vụ này, khách hàng là chủ thẻ Vietcombank chỉ cần tải ứng dụng Moca miễn phí từ App Store hoặc Google Play để thanh toán các hóa đơn mọi lúc mọi nơi ngay trên thiết bị di động mà không cần sử dụng thẻ vật lý.</p><p class="Normal"> 	Nhờ áp dụng công nghệ tokenization và mã QR, thông tin thẻ được hoàn toàn bảo mật. Việc thanh toán của khách hàng trở nên đơn giản khi chỉ cần chụp mã QR của người bán hàng và nhập số tiền cần thanh toán là khách hàng có thể hoàn tất giao dịch của mình chỉ trong vài giây.</p><p class="Normal"> 	Theo Vietcombank, trên thế giới, lượng khách hàng dần chuyển từ các kênh giao dịch truyền thống sang giao dịch trên điện thoại di động có xu hướng tăng.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194202850633832.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tải app Moca để sử dụng dịch vụ <a href="https://moca.vn/download">tại đây</a></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Tại Việt Nam, với lượng người dùng smartphone chiếm tới hơn 52% tổng số người dùng điện thoại di động giúp các phương thức thanh toán trên di động phát triển mạnh thời gian qua.</p><p class="Normal"> 	Dịch vụ thanh toán thẻ qua di động Vietcombank - Moca ra mắt sẽ đem đến cho khách hàng thêm một lựa chọn về kênh thanh toán hiện đại, thông minh, đơn giản và an toàn.</p><p class="Normal"> 	Là ngân hàng hàng đầu trong cung cấp sản phẩm dịch vụ thẻ, Vietcombank hiện có hơn 81.000 điểm chấp nhận thanh toán thẻ trên toàn quốc, có quan hệ ngân hàng đại lý với 1.726 ngân hàng tại 158 quốc gia và vùng lãnh thổ trên toàn thế giới.</p><p class="Normal"> 	<span>Công ty Cổ phần và Dịch vụ Moca</span><span> thành lập</span> từ năm 2013 bởi đội ngũ chuyên gia về công nghệ tài chính ngân hàng với nhiều năm kinh nghiệm từ Google, Microsoft và các ngân hàng hàng đầu Việt Nam.</p><p class="Normal" style="text-align:right;"> 	<strong>Thanh Thư</strong></p>', 1, '15001942021385714750.jpg', NULL),
(79, 'Eurowindow tặng 2017 phần quà kỷ niệm 15 năm thành lập', 'Khách hàng có cơ hội nhận 2017 giải thưởng, quà tặng với giải đặc biệt là căn hộ chung cư cao cấp Eurowindow River Park trị giá 1,5 tỷ đồng.', '<p> 	Nhằm tri ân khách hàng trong dịp kỷ niệm 15 năm thành lập, từ đầu năm đến nay, Eurowindo tổ chức nhiều chương trình khuyến mãi, chăm sóc khách hàng.</p><p> 	Trong chương trình kéo dài từ nay đến 10/8, khách hàng có cơ hội nhận 2017 giải thưởng và quà tặng giá trị bao gồm: một căn hộ cao cấp Eurowindow River Park trị giá 1,5 tỷ đồng; một xe máy Vespa Primavera 70,9 triệu đồng; 15 tivi Sony 43 inh full HD trị giá 15 triệu đồng cùng 2.000 mũ bảo hiểm. Ưu đãi dành cho khách hàng nhà riêng trên cả nước.</p><p> 	<div style="text-align: center"><img src="/pictures/news/15001942031580751877.jpg" /></div></p><p> 	Theo đại diện Eurowindow, thông thường, vào 6 tháng cuối năm, nhu cầu xây dựng và sửa chữa cải tạo nhà cửa của người dân tăng cao. Ngoài việc kích cầu người dân sử dụng vật liệu xây dựng hoàn thiện và sản phẩm cửa, chương trình khuyến mãi còn tạo cơ hội để Eurowindow tri ân khách hàng trong suốt 15 năm qua.</p><p> 	15 năm có mặt trên thị trường, Eurowindow chuyên cung cấp giải pháp tổng thể về cửa sổ. Với sản phẩm đầu tiên là cửa nhựa (uPVC), doanh nghiệp hợp tác với nhiều công ty hàng đầu thế giới sản xuất thêm những dòng cửa nhôm và vách nhôm kính lớn, cửa gỗ, cửa nhôm gỗ, cửa cuốn, các sản phẩm kính…, đáp ứng nhu cầu ngày càng cao của khách hàng. Đó chính là những yếu tố quan trọng góp phần vào sự thành công của Eurowindow trong suốt thời gian qua.</p><p> 	Các dòng cửa uPVC, cửa nhôm, hệ vách nhôm kính lớn và cửa nhôm gỗ được xem là giải pháp đồng bộ cho hệ thống cửa bao quanh ngôi nhà, bao gồm cửa sổ, cửa đi chính. Ngoài ra, Eurowindow còn cung cấp đa dạng các sản phẩm kính an toàn, kính cường lực, kính hoa văn... vừa tăng mức độ an toàn cho ngôi nhà vừa tiết kiệm đáng kể điện năng phù hợp với các công trình tòa nhà cao ốc văn phòng, trung tâm thương mại… Sản phẩm cửa gỗ Eurowindow bao gồm cửa đi chính, cửa thông phòng phù hợp với nội thất bên trong ngôi nhà.</p><p> 	Công ty hướng tới mục tiêu trở thành nhà cung cấp giải pháp tổng thể về cửa hàng đầu Việt Nam, từng bước vươn tầm quốc tế. Trong thời gian tới, Eurowindow tiếp tục giữ vững vị thế dẫn đầu thị trường trong nước, chú trọng đến chất lượng, đa dạng hóa sản phẩm và định hướng mở rộng thị phần, chuyển giao công nghệ sang các nước phát triển trong khu vực như Myanmar, Lào, Campuchia…</p><p> 	Ngoài ra, Eurowindow tăng cường hợp tác với các đối tác hàng đầu thế giới nhằm ứng dụng vật liệu, công nghệ mới trong sản xuất nhằm nâng cao năng xuất, chất lượng, nỗ lực tạo ra các sản phẩm mang thương hiệu Eurowindow thân thiện với môi trường, hiệu quả cho người sử dụng.</p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="title_news"> 			Chi tiết các thông tin về sản phẩm và chương trình khuyến mãi truy cập vào website <a href="http://www.eurowindow.biz" target="_blank">www.eurowindow.biz</a>. Hotline tại miền Bắc: 0909888000; miền Trung: 0906 000 111; miền Nam: 0903 11 8888 hoặc đến các showroom của Eurowindow trên toàn quốc.</div> 	</div> </div><p style="text-align:right;"> 	(Nguồn: <em>Eurowindow</em>)</p>', 1, '150019420345484590.jpg', NULL),
(80, 'Sunshine Group ứng dụng công nghệ trong quản lý dự án', 'Tập đoàn triển khai ứng dụng Sunshine và thẻ cư dân thông minh giúp quản lý dễ dàng tiến độ xây dựng và mọi tiện ích trong các dự án.', '<p> 	Theo đó, thay vì phải đến tận công trình để mục sở thị dự án, giờ đây, người mua nhà có thể trải nghiệm không gian sống và quản lý toàn bộ thông tin về ngôi nhà tương lai qua ứng dụng trên điện thoại thông minh.</p><p> 	Đại diện Sunshine Group cho hay, việc ứng dụng công nghệ sẽ giúp tăng tính minh bạch cho dự án. Người mua nhà có thể theo dõi tất cả thông tin về căn hộ từ khi quyết định xuống tiền (cập nhật tiến độ, theo dõi dòng tiền, tham khảo thông tin về dự án,…) đến việc quản lý tất cả dịch vụ sinh hoạt hàng ngày sau khi nhận bàn giao.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942041991147912.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thông tin chi tiết xem thêm tại: <a href="http://sunshinegroup.vn/">sunshinegroup.vn</a>, <a href="http://center.sunshinegroup.vn/">center.sunshinegroup.vn</a>, <a href="http://city.sunshinegroup.vn/">/city.sunshinegroup.vn</a>. Hotline: 0898.688.886, 1800 6559.</p> 			</td> 		</tr></tbody></table><p> 	Với ứng dụng Sunshine, khách mua nhà của Sunshine Group được khởi tạo một mã định danh (ID) cùng mật khẩu (Password), cung cấp tự động vào email hoặc tin nhắn điện thoại. Sau đó, người mua có thể đăng nhập vào ứng dụng để quản lý thông tin cá nhân cũng như các thủ tục mua bán, theo dõi việc thực hiện nghĩa vụ tài chính, triển khai dự án, các thông số như hướng, diện tích, tiến độ hoàn thiện, hình ảnh 3D, hình ảnh thực tế… của căn hộ đã mua.</p><p> 	Sau khi nhận bàn giao căn hộ, trở thành cư dân của Sunshine, người mua có thể khai thác các tính năng dịch vụ mà chủ đầu tư cung cấp như truyền hình, Internet, sửa chữa nhà, vận chuyển đồ, đưa đón học sinh, đăng ký nhập học, đi chợ online, dọn dẹp, ẩm thực tại gia, các thông báo đóng tiền điện, nước…</p><p> 	Trong khi đó, thẻ cư dân thông minh  của Sunshine giống như một chiếc chứng minh thư nhân dân điện tử, lưu giữ tất cả các thông tin cần thiết của cư dân trong mọi sinh hoạt như ra vào, vé gửi xe, vé tháng cho các dịch vụ giải trí, tích luỹ điểm tiêu dùng…</p><p> 	Với chiếc thẻ này, cư dân Sunshine có thể sử dụng các dịch vụ tiện ích tại các dự án khác do Tập đoàn Sunshine đầu tư. Như vậy, khách hàng sẽ được tận hưởng các dịch vụ tiện ích miễn phí như bể bơi, thư viện sách cộng đồng, khu vui chơi trẻ em… ở bất kỳ dự án nào của Sunshine Group.</p><p> 	Mùa hè này, Tập đoàn Sunshine Group áp dụng ứng dụng và thẻ cư dân thông minh tại Sunshine City và Sunshine Center. Với nền tảng công nghệ thông tin vững mạnh, tính minh bạch cao, S-Service sẽ là chứng thư bảo lãnh cho chất lượng cho 2 dự án này.</p><p> 	Chủ đầu tư còn cam kết thuê lại căn hộ tại Sunshine Center với giá 1.000 USD một tháng. Với Sunshine City, cam kết sinh lời 24% giá trị căn hộ trong 3 năm đầu.</p><p style="text-align:right;"> 	<strong>Huệ Chi</strong></p>', 1, '15001942031168954576.png', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(81, 'Nam A Bank huy động vốn tăng 12% sau 6 tháng', 'Tổng tài sản của ngân hàng đạt 48.531 tỷ đồng, tăng 13,4% so đầu năm; huy động vốn thị trường 1 là 38.145 tỷ, tăng 12% sau 2 quý.', '<p class="Normal"> 	Ngân hàng TMCP Nam Á (Nam A Bank) vừa sơ kết hoạt động kinh doanh 6 tháng đầu năm.</p><p class="Normal"> 	Theo báo cáo hoạt động kinh doanh, tính đến 30/6/2017, tổng tài sản của Nam A Bank đạt 48.531 tỷ đồng, tăng 13,4% so đầu năm; huy động vốn thị trường 1 (ngân hàng - với các cá nhân, tổ chức, doanh nghiệp) là 38.145 tỷ, tăng 12%. Cho vay thị trường 1 đạt 28.155 tỷ, tăng 4.116 tỷ so với đầu năm.</p><p class="Normal"> 	Hệ khách hàng và số lượng thẻ tín dụng tăng nhẹ lần lượt 21,2% và 25% so với mục tiêu đề ra. Doanh số thanh toán quốc tế trong 6 tháng là 234 triệu USD, tương đương 78% kế hoạch năm.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942051359036625.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bà Lương Thị Cẩm Tú - Tổng giám đốc Nam A Bank.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Thời gian qua, Nam A Bank hoàn thành 100% kế hoạch đồng bộ hóa hình ảnh thương hiệu trên toàn hệ thống. Nhiều sản phẩm, dịch vụ mới được ngân hàng giới thiệu, như: triển khai tiện ích xác thực giao dịch thẻ trực tuyến 3D Secure, golive giao diện Mobile Banking phiên bản 2.0, gói combo với nhiều tiện ích, ra mắt các chương trình ưu đãi, khuyến mãi dành cho khách hàng…</p><p class="Normal"> 	Bà Lương Thị Cẩm Tú - Tổng giám đốc Nam A Bank cho biết, ngân hàng sẽ đẩy nhanh tiến độ hoàn thiện và ứng dụng nền tảng ngân hàng số (Digital Bank) trong các sản phẩm dịch vụ, mang đến cho khách hàng những trải nghiệm mới nhất, phù hợp với xu thế hiện đại.</p><p class="Normal"> 	6 tháng cuối năm, Nam A Bank tiếp tục tăng cường năng lực phục vụ khách hàng, tinh gọn và linh hoạt hóa các quy trình. Mới đây, ngân hàng hợp tác chiến lược với tập đoàn bảo hiểm FWD nhằm cung cấp các sản phẩm bảo hiểm đa đạng và toàn diện cho khách hàng.</p><p class="Normal"> 	Vừa qua, Nam A Bank được Ngân hàng Nhà nước chấp thuận bổ sung các nghiệp vụ kinh doanh, như: cấp tín dụng dưới hình thức bao thanh toán trong nước; tư vấn tài chính doanh nghiệp; tham gia đấu thầu, mua, bán tín phiếu kho bạc…</p><p class="Normal"> 	Ngân hàng có kế hoạch tuyển dụng 500 nhân sự cho các vị trí từ chuyên viên đến cấp quản lý trên toàn hệ thống nhằm đáp ứng nhu cầu mở rộng quy mô hoạt động.</p><p class="Normal" style="text-align:right;"> 	<strong>Thanh Thư</strong></p>', 1, '15001942041595563753.png', NULL),
(82, 'Valencia Garden thừa hưởng tiện ích của khu Đông Hà Nội', 'Ngoài tiện ích nội khu đa dạng, cư dân Valencia Garden còn hưởng lợi từ hệ thống giao thông, hạ tầng hiện đại xung quanh.', '<p> 	Đại diện MIK Group cho biết, khu Đông đang trở thành tâm điểm, thu hút giới nhà giàu với lượng giao dịch biệt thự, nhà liền kề tăng mạnh. Theo CBRE, riêng trong quý II, hơn 1.200 biệt thự và nhà liền kề ở khu vực này được bán ra thị trường.</p><p> 	Ở khu vực này, một tập đoàn lớn đang đầu tư khu đô thị sinh thái với hàng nghìn căn biệt thự và nhà liền kề, giá bán từ 7 đến 25 tỷ đồng một căn. Một chủ đầu tư khác cũng đang lên kế hoạch đầu tư khu biệt thự hạng sang nằm ven những con kênh nhân tạo với giá bán hàng triệu USD mỗi căn. </p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942061411072770.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phía Đông Hà Nội hút giới thương lưu.</p> 			</td> 		</tr></tbody></table><p> 	Theo đại diện MIK Group, khu Đông thu hút khách hàng bởi môi trường sống trong lành, giao thông thuận tiện, chỉ cách trung tâm thành phố 5-10 phút lái xe. Hàng loạt con đường đang được mở rộng và xây mới ở khu vực này, tạo thành hệ thống giao thông liên hoàn và thông suốt, kết nối khu vực phía Đông với các khu vực trọng điểm của thành phố qua cầu Chương Dương, Vĩnh Tuy và Thanh Trì.</p><p> 	Ngoài ra, với quỹ đất rộng, mật độ xây dựng thấp, phía Đông thích hợp để phát triển những khu đô thị sinh thái, cảnh quan đẹp, môi trường sống trong lành, hệ thống dịch vụ tiện ích phong phú, đáp ứng nhu cầu của giới nhà giàu.</p><p> 	Tọa lạc trên khu đất rộng gần 11.000 m2 tại khu đô thị Việt Hưng, Long Biên, dự án căn hộ Valencia Garden do MIK Group phát triển thừa hưởng những tiện ích nổi bật của khu vực này. Dự án nằm cạnh Vinhomes Riverside - nơi được mệnh danh là Venice giữa lòng Hà Nội với hàng nghìn căn biệt thự có giá hàng chục tỷ, cư dân có thể trải nghiệm hệ thống tiện ích trọn vẹn nơi đây.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942061364565737.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chi phí hợp lý nhưng chất lượng sống tốt là điểm cộng của Valencia Garden.</p> 			</td> 		</tr></tbody></table><p> 	Cư dân Valencia Garden có thể thư giãn trong khu bể bơi vô cực được thiết kế theo phong cách Hawaii trên tầng thượng của trung tâm mua sắm Vincom Center Long Biên hay thưởng thức ẩm thực tại Almaz. Xung quanh dự án có hệ thống ven kênh đào, hồ nước và các vườn cây xanh mát, mang đến không khí trong lành, cảnh quan xanh mát.</p><p> 	Cư dân Valencia Garden còn được mua sắm và giải trí trong những trung tâm thương mại cận kề như Savico - Big C, Aeon Mall, Vincom Center. Cha mẹ có thể lựa chọn cho con các trường công với mức phí vừa phải như Trường tiểu học và Trung học cơ sở Đức Giang hoặc những trường quốc tế chất lượng cao như Wellspring, British International School.</p><p> 	Nội khu dự án Valencia Garden cũng được thiết kế với hàng loạt dịch vụ tiện ích nhưng những khu căn hộ cao cấp… đáp ứng mọi nhu cầu của khách hàng.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942071919294462.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Không chỉ có tiện ích hiện đại, Vanlencia Garden còn có hướng nhìn đẹp<span lang="en-us" xml:lang="en-us"><span>.</span></span> Liên hệ: Hotline: 0961 266 811. Website: <a href="http://Valenciagarden.vn" target="_blank">Valenciagarden.vn</a>.</p> 			</td> 		</tr></tbody></table><p> 	"Sở hữu hạ tầng đồng bộ trong nội khu và các tiện ích hiện đại xung quanh nhưng dự án Valencia Garden lại có giá bán hợp lý, phù hợp với thu nhập của khách hàng trẻ ở Thủ đô", đại diện chủ đầu tư cho biết.</p><p> 	Ngoài giá bán hợp lý, khách hàng còn được vay tới 80% từ ngân hàng. Từ nay tới hết ngày 15/8, khách hàng đặt mua thành công căn hộ Valencia Garden sẽ được nhận ngay quà tặng thiết bị bếp trị giá 30 triệu đồng. Nếu thanh toán sớm, người mua nhà còn được miễn lãi 9 tháng hoặc chiết khấu tới 4%.</p><p> 	Ba tòa căn hộ của Valencia Garden đã cất nóc trong tháng 6 và dự kiến bàn giao vào cuối năm nay.</p><p style="text-align:right;"> 	<strong>Huệ Chi</strong></p>', 1, '1500194206649675071.png', NULL),
(83, 'Chương trình ''Tích tem đổi quà đẳng cấp miễn phí'' chỉ còn 2 tuần', 'Khách hàng chỉ còn thời gian từ nay tới ngày 30/7 để tích tem đổi quà miễn phí từ Saigon Co.op.', '<p> 	Chương trình "Tích tem đổi quà đẳng cấp miễn phí" do Saigon Co.op triển khai trên toàn bộ hệ thống Co.opmart, Co.opXtra và Co.op Food thu hút đông đảo người tiêu dùng tham gia. Tuy nhiên, để có thể sở hữu những sản phẩm pha lê đẳng cấp và giá trị này, khách hàng chỉ còn hai tuần để tích tem (từ nay đến ngày 30/7) và một tháng để đổi quà miễn phí (từ nay đến ngày 13/8). Chương trình được Saigon Co.op kết hợp cùng thương hiệu nổi tiếng Brand Loyalty tổ chức.</p><table class="tplCaption" cellspacing="0" cellpadding="2" border="0" align="center"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942071114796217.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Khách hàng vui mừng khi nhận được quà.</p> 			</td> 		</tr></tbody></table><p> 	Chỉ với mỗi hóa đơn mua hàng từ 200.000 đồng trở lên trong khu tự chọn của hệ thống siêu thị Co.opmart, Co.opXtra hay Co.op Food, cùng thẻ khách hàng Co.opmart ở tất cả cấp độ, khách hàng sẽ được nhận ngay một tem tích lũy.</p><p> 	Khi sưu tầm đủ số lượng tem, khách hàng sẽ được đổi miễn phí các bộ sản phẩm pha lê của thương hiệu Spiegelau như bộ 2 ly vang đỏ, ly vang trắng, ly cao hoặc thấp, bình đựng nước, bát kích cỡ lớn hoặc bát nhỏ. Các sản phẩm này khó vỡ hơn các sản phẩm thông thường, an toàn và không chứa chì, chất liệu tinh khiết, không gây hại môi trường.</p><table class="tplCaption" cellspacing="0" cellpadding="2" border="0" align="center"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942071157700602.jpeg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bộ ly cao cấp của thương hiệu Spiegelau.</p> 			</td> 		</tr></tbody></table><p> 	Chương trình mang lại quyền lợi cộng thêm đáng kể cho người tiêu dùng. Bởi khi mua sắm trong siêu thị, khách hàng vừa được tích điểm vào thẻ Co.opmart để hưởng quyền lợi như thông thường, vừa được nhận tem tích lũy để đổi miễn phí những món quà giá trị. Đặc biệt, sản phẩm này không bán tại thị trường Việt Nam.</p><p style="text-align:right;"> 	(Nguồn: <em>Co.opmart</em>)</p>', 1, '15001942071881503306.jpg', NULL),
(84, 'LDG Group khánh thành hệ thống tiện ích tại The Viva City', 'Chủ đầu tư sẽ khánh thành nhiều hạng mục tiện ích, công trình phục vụ, quản lý vận hành khu đô thị thương mại The Viva City vào ngày 16/7.', '<p> 	Trong đó, khu liêp hợp thể dục thể thao có diện tích hơn 33.191m2 nằm trong lòng khu đô thị thương mại The Viva City được xây dựng để phục vụ cho cư dân dự án và người dân sống xung quanh khu đô thị. Đây sẽ là nơi phục vụ các hoạt động thể dục thể thao, tổ chức chương trình hoạt động cho hàng chục nghìn công nhân, chuyên gia đang làm việc tại khu công nghệ cao Giang Điền liền kề.</p><p> 	Bên cạnh đó, khu nhà trưng bày (showroom) dự án The Viva City sẽ khai trương để phục vụ khách hàng đến tham quan và tìm hiểu cơ hội đầu tư, an cư tại đây. Khu nhà điều hành cũng mở cửa để phục vụ hoạt động quản lý cho khu đô thị lớn hàng đầu khu vực cửa ngõ phía Đông Sài Gòn này.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194208848892126.jpg" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Hạ tầng và các công trình tiện ích tại The Viva City đã tương đối hoàn thiện. </p> 			</td> 		</tr></tbody></table><p> 	Ông Nguyễn Quốc Vy Liêm, Phó tổng giám đốc LDG Group cho biết, trong thời gian tới, khi hệ thống tiện ích của Khu đô thị The Viva City hoàn thiện và cư dân về xây dựng nhà cửa, an cư sinh sống thì cơ sở hạ tầng sẽ được đầu tư đồng bộ để phục vụ cư dân. Việc hoàn thành khu liên hợp thể thao, khu nhà trưng bày, khu nhà điều hành và nhiều công trình khác là nỗ lực đưa The Viva City trở thành khu đô thị kiểu mẫu lớn nhất khu vực Đông Sài Gòn.</p><p> 	Ngoài ra, LDG Group cũng khánh thành khu công viên Hà Lan rộng hơn 2.000m2 và khu sân bóng đá mini để phục vụ cho hoạt động vui chơi giải trí của cư dân và khách tham quan.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942091436900688.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Khu trung tâm thương mại Viva Square sẽ hoạt động vào cuối năm 2017. Hotline: 0938 460 560 - Website: <a href="http://www.ldggroup.vn/">www.ldggroup.vn</a></p> 			</td> 		</tr></tbody></table><p> 	Lãnh đạo LDG Group cũng chia sẻ sẽ tiếp tục đẩy mạnh đầu tư trong giai đoạn tới để hoàn thành khu thể thao trong nhà, hệ thống công viên châu Âu liên hoàn.</p><p> 	Cuối năm nay, chủ đầu tư sẽ hoàn thành và đưa vào khai thác khu trung tâm thương mại Viva Square. Tiện ích này kết hợp hai mô hình truyền thống và hiện đại, phục vụ cư dân The Viva City và khu vực xung quanh.</p><p> 	Nằm trên tuyến đường kết nối thương mại Đông Tây rộng 117m, Viva Square là khu trung tâm thương mại tiềm năng nhằm thúc đẩy kinh tế khu vực và đáp ứng nhu cầu mua sắm, thương mại ngày càng tăng của người dân tại đây và các vùng phụ cận.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942091915986465.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					LDG Group ưu đãi hấp dẫn cho khách hàng sở hữu sản phẩm tại Viva Square và nhà phố xây sẵn tại The Viva City. Hotline: 0938 460 560. Website: <a href="http://www.ldggroup.vn/" target="_blank">www.ldggroup.vn</a>. Facebook: <a href="http://www.facebook.com/LDGgroup.vn/" target="_blank">www.facebook.com/LDGgroup.vn</a>.</p> 			</td> 		</tr></tbody></table><p> 	Nhân dịp này, LDG Group cũng tổ chức chương trình thi đấu giải bóng đá giao hữu giữa các đội thuộc các đoàn thể của địa phương và doanh nghiệp nhằm tạo tiền đề cho các đại hội thể dục thể thao, hoạt động xã hội sẽ diễn ra tại đây trong thời gian tới.</p><p> 	Trong ngày 16/7, LDG Group sẽ tiếp tục triển khai ra thị trường các sản phẩm gian hàng, shop kinh doanh tại trung tâm thương mại Viva Square với giá chỉ từ 99 triệu đồng một gian hàng và nhà phố xây sẵn The Viva City với nhiều ưu đãi.</p><p> 	Theo đó, khách hàng sở hữu sản phẩm tại dự án sẽ được tặng những phần quà như xe Honda Vision, Smart TV Samsung 49 inches, Smart TV Samsung 40 inches. Ngoài ra, người mua còn có cơ hội sở hữu sản phẩm tại trung tâm thương mại Viva Square với giá 0 đồng.</p><p style="text-align:right;"> 	<strong>Huệ Chi</strong></p>', 1, '15001942081619736761.jpg', NULL),
(85, '70 tiện ích trong dự án d’Edge Thảo Điền', 'CapitaLand chú trọng xây dựng không gian sống đầy cảm hứng với các tiện ích gần gũi với thiên nhiên.', '<p> 	Được phát triển bởi chủ đầu tư CapitaLand, d’Edge Thảo Điền được định vị là một dự án hạng sang nổi bật của khu Thảo Điền (quận 2, TP HCM) - nơi an cư của nhiều người nước ngoài, nghệ sĩ và doanh nhân thành đạt. Hiện nay, Thảo Điền quy tụ hàng loạt trường học quốc tế nổi tiếng, những nhà hàng và spa sang trọng, các khu vực nghệ thuật và giải trí.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942091560850302.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hồ bơi không trung đáy kính - một trong các tiện ích nổi bật của d’Edge Thảo Điền.</p> 			</td> 		</tr></tbody></table><p> 	Nếu các dự án hạng sang khác nhấn mạnh yếu tố sang trọng, đẳng cấp thì chủ đầu tư của d’Edge Thảo Điền lại chú trọng xây dựng không gian sống đầy cảm hứng, thể hiện qua phong cách thiết kế kiến trúc vô cực và cảnh quan hài hòa cùng thiên nhiên.</p><p> 	Dự án d’Edge Thảo Điền gồm 273 căn hộ với 2 tòa tháp: Aire và Breze. Trong khi tòa Aire tái hiện cuộc sống xô bồ với đa dạng tiện ích ngay trong chính tòa nhà và khu vực xung quanh thì Breze lại đem đến trải nghiệm hiện đại với đầy đủ tiện nghi, không gian sống hài hòa với thiên nhiên. Mặt đứng 2 tòa tháp được thiết kế đặc biệt với những khối căn hộ đặt xen kẽ, ôm trọn bởi các mảng tường cây xanh và nối với nhau bằng một bể bơi vô cực không trung.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942102101818829.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Vườn chạy bộ và đạp xe tại d’Edge Thảo Điền.</p> 			</td> 		</tr></tbody></table><p> 	Các tiện ích thư giãn là điểm khác biệt nổi bật của d’Edge Thảo Điền. Dự án sở hữu tới 70 tiện ích thiên nhiên, chia thành 4 khu vực như hệ thống vườn xanh đa dạng trong khuôn viên dự án: vườn hồ điệp, vườn thảo mộc và hương vị, vườn hoa đăng (đèn lồng), vườn nhiệt đới, vườn trà. Nếu vườn đèn lồng đem lại không gian riêng tư cho mỗi gia đình, bố trí xen kẽ trong tòa nhà thì vườn hồ điệp là nơi tụ họp của các loài cây tự nhiên, tạo nên cảnh quan đẹp mắt...</p><p> 	Lối chạy bộ và đạp xe được bố trí ở tầng trệt dài khoảng 370m quanh tòa nhà, cứ 5m lại có một cột mốc, giúp cư dân xác định được quãng đường di chuyển của mình giữa con đường với hai hàng cây xanh. Tiếp đó là vườn đậu xe dành cho cư dân ở tầng trệt với những hàng cây cao thẳng tắp, tán lá lớn.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942102015054008.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phòng đọc sách và làm việc tại d’Edge Thảo Điền. Chi tiết dự án xem tại website: <a href="http://d-edge.com.vn/" target="_blank">d-edge.com.vn</a> hoặc liên hệ hotline: 1800 599 986. Dự án được phân phối bởi các sàn chiến lược: Seareal, CBRE, Savills, Huttons.</p> 			</td> 		</tr></tbody></table><p> 	Tầng 12A tập hợp hàng loạt các tiện ích về tập luyện và tái tạo năng lượng như thư viện đọc sách và không gian làm việc chung, phòng mát xa và xông hơi cao cấp, khu tập thể dục hạng sang, phòng rượu và xì gà thượng hạng…</p><p> 	d’Edge Thảo Điền còn có các tiện ích không trung, tập trung trên tầng thượng. Đó là hồ bơi không trung đáy kính đặt trên nóc tòa nhà, trải dài hơn 100m nối liền 2 tòa tháp. Từ đây, cư dân có thể ngắm toàn cảnh sông Sài Gòn. Bên cạnh đó, khu vực bể bơi sục thủy liệu pháp và bậc thềm để ngồi thiền, thư giãn trong không khí đậm hương thơm thảo dược giúp cư dân thư giãn và tái tạo năng lượng.</p><p> 	"Dự án được xây dựng với số lượng căn hộ ít, dành riêng cho những khách hàng tìm kiếm một nơi an cư thư thái, riêng tư để từ đó thêm trân quý sự tĩnh tại trong cuộc sống này", đại diện chủ đầu tư CapitaLand chia sẻ.</p><p style="text-align:right;"> 	<strong>Huệ Chi</strong></p>', 1, '15001942092033853206.png', NULL),
(86, 'LDG Group khai trương khu nhà mẫu căn hộ ven sông Marina Tower', 'Khách hàng có thể tham quan, trải nghiệm không gian sống sang trọng, hiện đại theo phong cách Singapore tại khu nhà mẫu Marina Tower, phía Bắc Sài Gòn.', '<p> 	Đại diện LDG Group cho biết, khu nhà mẫu rộng gần 1.000m2, có thể đón tiếp hơn 1.000 lượt khách mỗi ngày. Tại đây, khách hàng có thể trải nghiệm không gian sống sang trọng, hiện đại của căn hộ ven sông Marina Tower; tham quan khu trưng bày các hình ảnh của khu căn hộ này và tìm hiểu các thông tin đầu tư và an cư tại đây.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194211281861917.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Khu nhà mẫu căn hộ ven sông Marina Tower có thể đón tiếp hơn 1.000 lượt khách mỗi ngày.</p> 			</td> 		</tr></tbody></table><p> 	Được thiết kế theo phong cách căn hộ thông minh, tiết kiệm diện tích và năng lượng theo tiêu chuẩn Singapore, căn hộ Marina Tower có diện tích từ 50 đến 70m2 nhưng có đến 3 phòng ngủ với đầy đủ tiện ích phục vụ cho gia đình 1-3 thế hệ. Toàn bộ đều có phòng khách và phòng ngủ lớn (master bedroom) có hướng ra ngoài. Hơn 80% căn hộ đều nhìn ra sông để tận hưởng không khí thoáng mát.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942111524391787.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Không gian sống bên trong căn hộ Marina Tower được bố trí thông minh.</p> 			</td> 		</tr></tbody></table><p> 	Để mang đến không gian sống tối ưu và hiện đại, chủ đầu tư LDG Group cũng tổ chức triển lãm thiết kế nội thất thông minh và cách trưng bày cho các căn hộ. Hệ thống thiết bị nhà thông minh mới như rèm tự động, hệ điều chỉnh ánh sáng đèn Led cho toàn căn hộ với 16 triệu màu, vách di động cùng nhiều hệ thống nhà hiện đại khác cũng được giới thiệu tại đây.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194211294375082.png" /></div></td> 		</tr><tr><td class="CaptionImg"> 				<p class="Image"> 					Phòng ngủ thoáng mát và luôn ngập tràn ánh sáng tự nhiên. Xem chi tiết dự án tại website: <a href="http://marinatower.vn" target="_blank">marinatower.vn</a>.</p> 			</td> 		</tr></tbody></table><p> 	Ông Nguyễn Quốc Vy Liêm, Phó tổng giám đốc LDG Group cho biết, nhiều căn hộ tại Marina Tower được khách hàng đặt mua nhờ vị trí đắc địa, uy tín chủ đầu tư và thiết kế thông minh. Bên cạnh đó, các tiện ích phục vụ cho cư dân như hồ bơi tràn bờ, khu dịch vụ giải trí bên sông hay khu căn hộ sân vườn trên cao cũng thu hút nhiều người mua nhà.</p><p> 	Với mức giá dưới 1 tỷ đồng một căn, người dân có thể sở hữu căn hộ tại Marina Tower bằng mức lương hàng tháng của mình thông qua sự hỗ trợ của ngân hàng Vietinbank. Người mua căn hộ sẽ được ngân hàng cho vay ưu đãi với thời gian thanh toán kèo dài từ 10 đến 15 năm.</p><p style="text-align:right;"> 	<strong>Huệ Chi</strong></p>', 1, '1500194210592172101.png', NULL),
(87, 'Mã QR - phương thức thanh toán mới trong thời đại số', 'Sự bùng nổ của thanh toán điện tử và điện thoại thông minh là nền tảng để thanh toán bằng mã QR ngày càng phổ biến.', '<p> 	Thanh toán bằng mã QR được nhiều nước trên thế giới áp dụng. Nền tảng bán hàng đa kênh đã được phát triển và trở thành hình thức phổ biến trên thế giới hỗ trợ các doanh nghiệp vừa và nhỏ tận dụng công nghệ, sự phát triển của thương mại điện tử để tối ưu hóa mô hình kinh doanh của mình.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942121803199444.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mô hình bán hàng bằng mã QR trên thế giới.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Tính năng QR trên ứng dụng điện thoại cho phép người dùng sử dụng camera điện thoại quét mã QR để thực hiện nhanh một số giao dịch như chuyển khoản, thanh toán hóa đơn, mua hàng. Chỉ cần một lần quét trong vài giây là có thể thanh toán thành công tại các nhà hàng, siêu thị, cửa hàng, taxi... mà không cần mang theo ví, không lo vấn đề tiền lẻ, không cần mang theo nhiều thẻ, không lo lộ thông tin thẻ tại các điểm thanh toán.</span></p><p class="Normal"> 	<span>Ấn Độ là nước ở khu vực châu Á đang đi tiên phong khi Chính phủ tài trợ dự án có tên gọi IndiaQR, trong nỗ lực thực hiện tham vọng không dùng tiền mặt. Người dân có thể đi chợ mua rau và thanh toán bằng điện thoại.</span></p><p class="Normal"> 	<span>Startup công nghệ có trị giá lớn thứ nhì Ấn Độ, khoảng 8 tỷ USD, với hơn 220 triệu người dùng. </span><span style="color:rgb(34,34,34);">Hay như tại Trung Quốc, nhiều đơn vị bán hàng nhận thanh toán bằng mã QR Code. Theo ước tính, bình quân mỗi ngày một người Trung Quốc tương tác với khoảng 10-15 mã QR.</span></p><p> 	Thông qua thanh toán từ di động, người dân Hàn Quốc có thể đi xe bus mà không cần tiền mặt. Ngân hàng Trung ương Hàn Quốc (BOK) đã lên kế hoạch xây dựng “xã hội phi tiền mặt” vào năm 2020.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942121714473556.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhiều ứng dụng Mobile Banking có chức năng QR Pay.</p> 			</td> 		</tr></tbody></table><p> 	Thanh toán điện tử được khuyến khích bởi người dân sẽ không phải đến ngân hàng để rút tiền, giữ tiền và có thể thanh toán ở bất cứ đâu chỉ với một chiếc điện thoại. Giao dịch trở nên dễ dàng và nhanh chóng hơn, góp phần kích thích bán hàng đa kênh phát triển.</p><p> 	Tại Việt Nam, ứng dụng thanh toán bằng mã QR đã được nhiều ngân hàng áp dụng. Tính năng QR Pay hiện được tích hợp trên ứng dụng Mobile Banking của một số ngân hàng với khoảng 5 triệu người dùng.</p><p class="Normal"> 	<span>Với ưu điểm thanh toán nhanh, tiện lợi trên di động thông qua ứng dụng Mobile Banking, người dùng có thể mua hàng từ bất cứ đâu, bất kỳ thời điểm nào mà không cần phải tới cửa hàng trực tiếp. </span></p><p class="Normal"> 	<span>Nhà bán lẻ không cần phải mở cửa hàng tại tất cả tỉnh thành mà chỉ cần một nền tảng công nghệ chấp nhận thanh toán bằng mã QR là có thể phủ sóng khắp mọi miền đất nước và “có mặt” mọi lúc, mọi nơi để tiếp cận khách hàng.</span></p><p class="Normal"> 	<span style="color:rgb(34,34,34);">Thanh toán bằng QR code đặc biệt hữu ích với doanh nghiệp bán lẻ, từ các cửa hàng tiện lợi, nhà hàng ăn uống, các shop thời trang... đến các đơn vị kinh doanh trực tuyến trên website, Facebook. </span></p><p class="Normal"> 	<span style="color:rgb(34,34,34);">Doanh nghiệp có thể tận dụng lợi thế của hình thức thanh toán này để bán hàng bằng nhiều phương thức khác nhau như: online trên website, mạng xã hội hay bằng tờ rơi, catalogue, quảng cáo trên báo, biển bảng ở khu vực công cộng nhà chờ xe bus, nhà ga, sân bay, bến tàu… Mọi kênh quảng cáo của doanh nghiệp đều có thể biến thành kênh bán hàng hiệu quả.</span></p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194212449838207.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mua sắm tiện lợi bằng cách quét mã QR.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Việc tiếp cận nhiều đối tượng khách hàng ở mọi lúc mọi nơi, giúp doanh nghiệp mở rộng kênh bán hàng. Riêng đối với các đơn vị kinh doanh vừa và nhỏ không đủ chi phí thuê mặt bằng làm cửa hàng, đây là một giải pháp tiếp cận khách hàng và tăng doanh thu hiệu quả với chi phí thấp.</span></p><p class="Normal"> 	<span style="color:rgb(34,34,34);">Theo Nielsen, cuộc đua của các nhà bán lẻ lúc này không chỉ là một cửa hàng vật lý mở ra và chờ đợi khách hàng tiện chân ghé vào, mà là cuộc đua liên tục của quá trình phân tích hành vi mua sắm, dự đoán được món hàng khách hàng quan tâm và tạo mọi điều kiện dễ dàng nhất cho khách hàng mua hàng. </span><span style="color:rgb(34,34,34);">Vì vậy, doanh nghiệp cần cập nhật những giải pháp, phần mềm của điện thoại thông minh cho các hoạt động kinh doanh của mình.</span></p><p> 	Nắm bắt được xu hướng, VNPAY là cổng thanh toán tại Việt Nam đi tiên phong trong lĩnh vực này, cung cấp giải pháp thanh toán bằng mã QR. Hiện đơn vị hợp tác với nhiều ngân hàng và các đơn vị kinh doanh để công cụ thanh toán mới này được sử dụng rộng rãi hơn trong đời sống tiêu dùng.</p><p> 	Đại diện VNPAY cho biết, công nghệ này tận dụng tối đa hạ tầng viễn thông, ngân hàng sẵn có và ưu thế của thiết bị di động cá nhân nhằm mang lại một phương thức thanh toán mới an toàn, tiện lợi cho cả người bán và người mua, tạo điều kiện thúc đẩy thương mại bán lẻ phát triển.</p><p style="text-align:right;"> 	<strong>Mai Thương</strong></p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Mã QR (hay <span style="color:rgb(0,0,0);">mã vạch</span><span style="color:rgb(0,0,0);"> hai chiều) được phát triển bởi công ty Denso Wave (Nhật Bản) vào năm 1994. Chữ "QR" xuất phát từ "Quick Response", trong tiếng Anh có nghĩa là đáp ứng nhanh, vì người tạo ra nó có ý định cho phép mã được giải mã ở tốc độ cao. Các mã QR được sử dụng phổ biến nhất ở </span><span style="color:rgb(0,0,0);">Nhật Bản</span><span style="color:rgb(0,0,0);">, và hiện là loại mã hai chiều thông dụng nhất ở quốc gia này.</span></td> 		</tr></tbody></table>', 1, '1500194211686078059.jpg', NULL),
(88, 'Nhóm thanh niên nổ súng đòi nợ bị cảnh sát khống chế', '7 thanh niên đến nhà người đàn ông ở Khánh Hòa nổ súng, doạ ném "bom xăng" để đòi nợ nhưng bị cảnh sát bao vây.', '<div style="text-align: center;"> 	<div id="video-157025" data-component="true" data-component-type="video" data-component-value="157025" data-component-typevideo="2"></div></div><p> 	Nhóm thanh niên, trong đó có một cô gái, mang theo nhiều "bom xăng", rựa, mác... tới nhà người đàn ông ở xã Vĩnh Trung (TP Nha Trang) đòi nợ, tối 15/7.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942291632939433.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhóm thanh niên bị cảnh sát bắt tại công an. Ảnh:<em> Phước An.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Xảy ra cự cãi, một tên rút súng tự chế bắn chủ nhà, may mắn không trúng. Gia đình nạn nhân hoảng sợ nhưng không dám can ngăn. Hàng xóm nghe tiếng súng vội đóng chặt cửa.</p><p class="Normal"> 	Thấy cảnh sát 113 xuất hiện, nhóm giang hồ chạy tán loạn nhưng bị bao vây, khống chế. "Họ rất manh động, mang theo nhiều hung khí nóng, buộc chúng tôi phải trấn áp", trung tá Đỗ Xuân Kiều - Tổ trưởng tuần tra cảnh sát 113 Công an Khánh Hòa, nói.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194229252081069.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Súng tự chế bị thu giữ. Ảnh: <em>Phước An.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cầm đầu nhóm thanh niên, Phùng Nguyên Tấn cho biết do chủ nhà nợ tiền không trả nên gọi bạn bè đến gây áp lực.</p><p class="Normal" style="text-align:right;"> 	<strong>Xuân Ngọc</strong></p>', 1, '1500194228816118620.jpg', NULL),
(89, 'Ông Phạm Công Danh gây thiệt hại thêm 6.000 tỷ đồng như thế nào', 'Nguyên Chủ tịch HĐQT Ngân hàng VNCB bị cáo buộc làm hồ sơ khống vay nhiều nhà băng, gây thiệt hại 6.000 tỷ đồng.', '<p class="Normal"> 	<span>Phải lĩnh <span style="text-align:right;">30 năm tù trong đại án thất thoát hơn 9.000 tỷ đồng, <span>ông Phạm Công Danh (nguyên Chủ tịch HĐQT Ngân hàng Xây dựng Việt Nam - VNCB, Tập đoàn Thiên Thanh) tiếp tục bị </span></span>đề nghị truy tố cùng 23 đồng phạm về hành vi <em>Cố ý làm trái quy định của Nhà nước trong quản lý kinh tế</em> gây thiệt hại hơn 6.000 tỷ.</span></p><p class="Normal"> 	Theo điều tra, sau khi được chấp thuận chủ trương tái cơ cấu Ngân hàng Đại tín (TrustBank) thành VNCB, ông Danh đã để nhà băng ngày càng thua lỗ. Khoảng tháng 7/2012, ngân hàng bị âm vốn chủ sở hữu hơn 2.800 tỷ đồng, lỗ lũy kế hơn 6.000 tỷ. Đến năm 2013, vốn chủ sở hữu đã âm hơn 8.200 tỷ, lỗ lũy kế tăng lên hơn 11.300 tỷ. Nguyên nhân được cho là ông Danh và đồng phạm đã thực hiện nhiều hành vi phạm tội.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942302086913013.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tổng cộng, ông Danh bị cáo buộc gây thiệt hại 15.000 tỷ đồng trong giai đoạt điều hành VNCB. Ảnh: <em>Hải Duyên. </em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Để có tiền trả các khoản vay trước đó tại Ngân hàng đầu tư và Phát triển Việt Nam (BIDV), tháng 4/2013, ông Danh cùng dàn lãnh đạo cấp dưới đến gặp ông Trầm Bê (Phó chủ tịch HĐQT, Chủ tịch HĐTD Sacombank) đề nghị vay 1.800 tỷ đồng.</p><p class="Normal"> 	Biết ông Danh không thể trực tiếp vay tiền của VNCB, ông Bê đồng ý cho vay với tài sản đảm bảo là tiền gửi của VNCB tại Sacombank và giao cho cấp dưới triển khai.</p><p class="Normal"> 	Ông Danh sau đó phân công Phan Thành Mai (Tổng giám đốc VNCB), Mai Hữu Khương (34 tuổi, TV HĐQT) chuẩn bị tiền đảm bảo và làm 6 bộ hồ sơ pháp nhân của 6 công ty (do ông Danh thành lập) để vay khống theo phương án kinh doanh bất động sản. Giám đốc các công ty này hầu hết là nhân viên bảo vệ, tài xế… của Tập đoàn Thiên Thanh được thuê đứng tên.</p><p class="Normal"> 	Cơ quan điều tra xác định, trong phi vụ này ông Trầm Bê có chủ trương "giải ngân trước bổ sung chứng từ sau" nên chỉ trong một ngày giám đốc các chi nhánh của Sacombank đã <span style="color:#696969;"><strong>chuyển tổng cộng 1.800 tỷ đồng cho 6 công ty của ông Danh</strong></span>.</p><p class="Normal"> 	Số tiền này ông Danh trả nợ cho BIDV 1.700 tỷ đồng - khoản vay để chuyển nhượng 5 lô đất thuộc dự án Khu phức hợp TM&DV cao tầng tại Sân vận động Chi Lăng, Đà Nẵng.</p><p class="Normal"> 	Khoảng tháng 9/2013 cần tiền tăng vốn điều lệ, ông Danh tìm gặp lãnh đạo BIDV tại Hà Nội đặt vấn đề "có 12 doanh nghiệp là khách hàng của VNCB muốn vay vốn kinh doanh vật liệu xây dựng". Do VNCB trong thời gian tái cơ cấu không có khả năng cho vay nên ông giới thiệu sang BIDV. VNCB sẽ dùng tài sản của mình để bảo đảm. Nhưng thực chất các công ty này do ông Danh lập ra, thuê nhân viên của mình đứng tên làm giám đốc.</p><p class="Normal"> 	Sau khi được lãnh đạo BIDV duyệt chủ trương cho vay, ông Danh chỉ đạo cấp dưới mỗi người một khâu, lập khống hồ sơ vay vốn gồm: báo cáo tài chính, phương án kinh doanh, hợp đồng mua bán vật liệu xây dựng, giấy đề nghị vay vốn… để nộp cho BIDV.</p><p class="Normal"> 	Trong đó, hồ sơ vay thể hiện giám đốc các công ty đều là cử nhân kinh tế, cũng chính là chủ sở hữu công ty. Mỗi công ty đều có từ 35 đến 70 lao động chính, đều ký kết các hợp đồng thi công giá trị trên 1.000 tỷ đồng…</p><p class="Normal"> 	Chủ tịch Danh dùng các lô đất tại Sân vận động Chi Lăng Đà Nẵng và hơn 3.000 tỷ đồng tiền gửi của VNCB tại BIDV để bảo đảm và được nhà băng này giải ngân 4.700 tỷ đồng. Sau khi được giải ngân, ông Danh chỉ đạo chuyển tiền lòng vòng trước khi về tài khoản của VNCB để tăng vốn điều lệ dưới danh nghĩa cổ đông mua cổ phần.</p><p class="Normal"> 	Lấy lý do thị trường bất động sản trầm lắng, các dự án phải dừng thi công nên 12 công ty vay vốn không có khả năng trả nợ. Tổng giám đốc Phan Thành Mai đề nghị BIDV tất toán các khoản tiền gửi của VNCB tại ngân hàng này trả nợ cho 12 công ty đối tác. Với cách thức này, ông Danh và đồng phạm gây <span style="color:#696969;"><strong>thiệt hại cho VNCB hơn 2.550 tỷ đồng.</strong></span></p><p class="Normal"> 	Tương tự, để hợp thức hóa việc rút tiền của VNCB, ông Danh chỉ đạo cấp dưới lập khống hồ sơ vay 1.666 tỷ đồng của ngân hàng TPBank nhằm mua trái phiếu Tập đoàn Thiên Thanh và Công ty Trung Dung của mình. Do các công ty này đã đứng tên vay vốn tại Sacombank nên ông Danh nhờ Tổng giám đốc Mai mượn pháp nhân của 11 công ty khác vay.</p><p class="Normal"> 	VNCB đã dùng 1.722 tỷ đồng tiền gửi tại TPBank để cầm cố bảo lãnh, trả nợ cho các khoản vay của những công ty này. Sau khi được TPBank giải ngân, các công ty đã chuyển toàn bộ tiền cho Tập đoàn Thiên Thanh. Ông Danh rút ra trả nợ bà Hứa Thị Phấn (mua TrustBank), trả nợ cũ cho Tập đoàn Thiên Thanh, chi chăm sóc khách hàng... Số tiền này không có khả năng thu hồi, <strong><span style="color:#696969;">gây thiệt hại cho VNCB 1.740 tỷ đồng.</span></strong></p><table class="tbl_insert" style="width:100%;" cellspacing="0" cellpadding="1" border="1" align="center"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal"> 					Cũng trong quá trình điều hàng VNCB, ông Danh cùng 35 đồng phạm đã <em>Cố ý làm trái quy định của Nhà nước trong quản lý kinh tế, Vi phạm quy định về cho vay trong hoạt động của các tổ chức tín dụng</em> gây thiệt hại 9.000 tỷ đồng. </p> 				<p class="Normal"> 					<span style="text-align:right;">Hồi đầu năm, TAND Cấp cao tại TP HCM xử phúc thẩm, tuyên y án 30 năm tù đối với ông Danh và buộc nộp lại hơn 9.000 tỷ đồng thiệt hại cho Ngân hàng Xây dựng. </span></p> 				<p class="Normal"> 					<span style="text-align:right;">Cục Thi hành án TP HCM đã thu hồi được hơn 5.000 tỷ đồng.</span></p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Hải Duyên </strong></p>', 1, '1500194230397912586.jpg', NULL),
(90, 'Giấu vợ mảnh đất cha mẹ cho, phòng khi ly hôn chia tài sản', 'Một năm sau ngày cưới, anh tôi được cha mẹ cho riêng một mảnh đất thì nó có thuộc tài sản chung vợ chồng không?', '<p class="Normal"> 	Anh trai tôi kết hôn năm 2012. Một năm sau, bố mẹ tôi làm hợp đồng tặng cho riêng anh một mảnh đất. Chuyện này anh và gia đình giữ kín với chị dâu tôi.</p><p class="Normal"> 	Nay anh chị ly hôn. Tôi xin hỏi, mảnh đất anh tôi được tặng cho riêng có thuộc tài sản chung vợ chồng để chia khi ly hôn không? Anh tôi muốn góp vốn thành lập doanh nghiệp bằng mảnh đất đó thì cần đáp ứng yêu cầu và những giấy tờ gì?</p><p class="Normal" style="text-align:right;"> 	<strong>Nguyễn Mạnh Thắng</strong></p><p class="Normal"> 	<a title="Luật sư trả lời" href="http://vnexpress.net/tin-tuc/phap-luat/tu-van/giau-vo-manh-dat-cha-me-cho-phong-khi-ly-hon-chia-tai-san-3611791-p2.html"><strong>Luật sư trả lời</strong></a></p>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(91, 'Tài sản đồ sộ của ''đại gia'' chiếm đoạt hàng trăm tỷ', 'Huy động gần 1.100 tỷ của nhà đầu tư, Minh mua biệt thự, xe siêu sang và tiêu xài cá nhân, phần còn lại trả lãi suất...', '<p class="Normal"> 	Năm 2013, khi thành lập Công ty CP Williams Việt Nam và giữ chức tổng giám đốc kiêm chủ tịch HĐQT, Trịnh Anh Minh (quận Thanh Xuân, Hà Nội) mới 29 tuổi. Minh thuê một trụ sở khang trang để làm ăn dưới hình thức huy động vốn của các tổ chức, cá nhân, trái với quy định pháp luật.</p><p class="Normal"> 	Luôn ăn mặc chỉn chu với những bộ quần áo đắt tiền, đi ôtô sang. Trước vẻ hào nhoáng và trụ sở khang trang, nhiều người đã đến công ty Minh tìm hiểu, đầu tư.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194232917128201.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bị cáo Trịnh Anh Minh.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trong các buổi tiếp xúc với tổ chức, cá nhân sau đó, Minh thuyết trình với khách hàng, công ty đầu tư vào một loạt hệ thống cửa hàng kinh doanh thời trang, điện thoại như nhà hàng, quán karaoke KTV tại 212 Nguyễn Trãi... và mua lại các khoản nợ xấu, kinh doanh bất động sản. Ai có nhu cầu đầu tư thì nộp tiền tại công ty để thu lợi nhuận. Minh đưa ra mức lãi suất 3,4 - 5,4%/tháng (từ 34% đến 60%/năm) tương đương với các kỳ hạn 6 tháng, 9 tháng và 12 tháng (trong khi lãi suất ngân hàng thời điểm đó chỉ 6-7%/năm).</p><p class="Normal"> 	Trước sức hút lớn của lãi suất, nhiều người huy động các thành viên trong gia đình góp vốn để đưa cho Minh. Có gia đình còn cầm cố cả nhà cửa, xe ôtô… để có tiền đầu tư vào “các siêu dự án” của Minh. Thực tế, những dự án này của Minh chỉ trên giấy.</p><p class="Normal"> 	Ngoài ra, để hút nhà đầu tư, Minh chỉ đạo nhân viên tổ chức nhiều sự kiện tri ân khách hàng, tặng quà là điện thoại, máy tính, đi du lịch và làm từ thiện với quy mô lớn. Đặc biệt, trong hợp đồng có nhiều nội dung hấp dẫn như tiền lãi sẽ được quy ra thành con tiền mặt tùy thời hạn cho vay và được ghi thành “lợi nhuận thu được trong quá trình kinh doanh”.</p><p class="Normal"> 	Đến ngày thanh toán, nếu trả chậm, Williams sẽ phải trả thêm lợi nhuận trong quá trình kinh doanh của những ngày chậm thanh toán và thời gian trả chậm không quá 30 ngày. Minh còn tổ chức các buổi hội thảo tại các khách sạn 5 sao để thuyết trình phương án kinh doanh của công ty.</p><p class="Normal"> 	Tại công ty, Minh quy định 7 loại hợp đồng ủy thác đầu tư tạo ra 2 kênh huy động tiền.</p><p class="Normal"> 	Khi khách hàng giới thiệu thêm người mang tiền đến gửi sẽ được trả hoa hồng môi giới 2-4%. Còn với nhân viên trong công ty, tiền hoa hồng từ 10-30%/tổng số tiền hợp đồng ủy thác và được Minh cắt trực tiếp từ tiền gửi của khách hàng. </p><p class="Normal"> 	Từ tháng 4/2014 đến tháng 10/2015, tổng số tiền mà Công ty Williams đã huy động là hơn 1.100 tỷ đồng của khoảng 540 khách. Trong đó, người gửi nhiều nhất khoảng 50 tỷ đồng.</p><p class="Normal"> 	Có số tiền kếch sù trong tay, ngoài trả lãi cho những nhà đầu tư “đi trước”, Minh mua biệt thự, siêu xe Bentley, Porsche Cayenne, Aston Martin, điện thoại Vertu… Anh ta còn phóng khoáng tặng xe BMW Z4, Porsche… cho cấp dưới.</p><p class="Normal"> 	Năm 2015, hành vi lừa đảo của Trịnh Anh Minh bị Công an Hà Nội lật tẩy, thu giữ nhiều xe sang, hàng chục tỷ đồng và căn biệt thự... ở quận Thanh Xuân để trả cho các bị hại. Cơ quan chức năng đã xác minh các bị hại, song nhiều người không trình báo. Đến nay, công an chỉ xác định được 263 người nộp tổng số tiền gần 230 tỷ đồng.</p><p class="Normal"> 	Riêng một số nhân viên của Minh, cơ quan công an đã tách hồ sơ để điều tra, xử lý sau.</p><p class="Normal"> 	Với việc chiếm đoạt số tiền rất lớn, vừa qua VKSND Hà Nội đã ra cáo trạng truy tố Trịnh Anh Minh về hành vi lừa đảo chiếm đoạt tài sản, khung hình phạt cao nhất là tù chung thân.</p><p class="Normal" style="text-align:right;"> 	<strong>Mai Chi</strong></p>', 1, '1500194232128656280.jpg', NULL),
(92, 'Vác dao kiếm đi trả thù vì nhà đại ca bị ném ''bom bẩn''', 'Biết chuyện nhà “đại ca” bị khủng bố bằng chất thải, nhóm đàn em đã mang dao kiếm đến cửa hàng đối phương đâm chém.', '<p class="Normal"> 	Ngày 14/7, Công an thành phố Hải Dương cho biết, cảnh sát điều tra vừa ra quyết định khởi tố, bắt giam nhóm thanh niên mới lớn gồm Lưu Văn Trọng, Nguyễn Văn Ngọc, Mạc Văn Sâm và Nghiên Viết Dương để điều tra hành vi hủy hoại tài sản, gây thương tích.</p><p class="Normal"> 	Trước đó, bốn thanh niên trên rủ nhau về thành phố Hải Dương chơi. Trong câu chuyện, cả nhóm biết nhà “đại ca” Nguyễn Tuấn Anh ở phường Ngọc Châu vừa bị kẻ xấu ném "bom bẩn" (dầu thải trộn phân) nên đã lên kế hoạch trả thù hộ. Mai Thành Đạt (trú phường Trần Hưng Đạo, thành phố Hải Dương) bị nghi ngờ gây ra vụ việc vì trước đó có mâu thuẫn làm ăn với Tuấn Anh.</p><p class="Normal"> 	Rạng sáng 10/7, Sâm, Trọng, Ngọc và Dương mang theo dao, kiếm đến quán game của Đạt ở khu 14, phường Ngọc Châu, TP Hải Dương đập phá tài sản, làm hỏng 2 máy bắn cá, chém bị thương thanh niên trông quán thuê cho Đạt.</p><p class="Normal"> 	Qua điều tra, Công an TP Hải Dương đã bắt giữ 10 thanh niên nam, nữ là đàn em của Nguyễn Tuấn Anh. Quá trình sàng lọc, nhà chức trách xác định Lưu Văn Trọng, Nguyễn Văn Ngọc, Mạc Văn Sâm và Nghiên Viết Dương liên quan trực tiếp đến việc đâm chém trên.</p><p class="author" style="text-align:right;"> 	<strong>Giang Chinh</strong></p>', 1, NULL, NULL),
(93, 'Ba sát thủ bịt mặt xông vào quán ăn chém chết người', 'Sau một tuần thực hiện hợp đồng giết người thuê, 2 sát thủ đã ra trình diện cảnh sát.', '<p class="Normal"> 	<span>Rạng sáng 8/7, anh Lê Ngọc Giáp (29 tuổi) đang ăn tối cùng 4 người bạn tại thị trấn Đắk Mil (huyện Đắk Mil, Đăk Nông) đã bất ngờ bị 3 người bịt mặt đi xe máy ập tới tấn công. Anh Giáp bị hai người truy đuổi, chém tử vong. Một tên trong nhóm sát thủ cũng bị thương tích trong cuộc hỗn chiến này, chết trên đường chạy trốn.</span></p><p class="Normal"> 	Đêm 8/7, tại Hà Tĩnh, cảnh sát đã chặn chiếc ôtô được thuê chở xác sát thủ về quê ở Nghệ An để tìm những tên còn lại. Tài xế khai có hai thanh niên thuê chở xác và đi cùng, nhưng đi đến <span>địa phận tỉnh Quảng Bình, cách hầm đèo Ngang khoảng 4km, họ đã xuống xe.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942341750459748.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nghi can Xin và Hà. Ảnh: <em>Cơ quan điều tra Nghệ An.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Sáng 15/7, đại tá Phạm Hoài Nam, Trưởng phòng cảnh sát hình sự (Công an tỉnh Nghệ An) cho biết ngày 14/7 hai nghi can Phan Văn Xin (24 tuổi), Nguyễn Đình Hà (30 tuổi) ra đầu thú, nhận gây ra vụ giết anh Giáp. Hồ sơ sau đó được bàn giao cho C<span>ông an Đăk Nông thụ lý theo thẩm quyền. </span></p><p class="Normal"> 	<span>Trước đó, liên quan vụ án, Công an </span>Đăk Nông đã bắt Phạm Bá Thắng (30 tuổi) và Hà Thanh Cương (32 tuổi), Phạm Trung Anh (22 tuổi) cùng trú tại thị trấn Đăk Mil, huyện Đắk Mil để phục vụ điều tra.</p><p class="Normal"> 	<span>Theo lời khai, </span><span>Thắng có mâu thuẫn với nạn nhân Giáp nên thuê </span><span>nhóm Hà từ Nghệ An vào </span><span>Đăk Nông v</span><span>ới mục đích đánh dằn mặt. Cương giữ vai trò nhận diện, chỉ điểm cho nhóm giang hồ.</span></p><p class="Normal" style="text-align:right;"> 	<strong><span>Thành Vinh</span></strong></p>', 1, '15001942341824425080.jpg', NULL),
(94, 'Người phụ nữ sang Pháp đòi con chưa được thi hành án', 'Được tòa án Pháp và Việt Nam buộc bạn trai trả lại con, song chị Huyền vẫn chưa thể thi hành bản án vì ông này kháng cáo. ', '<p class="Normal"> 	Tòa Gia đình và Người chưa thành niên TAND TP HCM cho biết, ông Azais (42 tuổi, quốc tịch Pháp) đã kháng cáo toàn bộ quyết định công nhận và cho thi hành phán quyết của tòa án Pháp tại Việt Nam - buộc ông phải trả con cho chị Nguyễn Thị Thanh Huyền (32 tuổi, quê Khánh Hòa).</p><p class="Normal"> 	Trong đơn gửi đến toà ông Azais không nêu cụ thể lý do kháng cáo. Trước đó, ông làm đơn kháng cáo bằng tiếng Việt, không hợp lệ nên tòa yêu cầu bổ sung. Động thái này của ông khiến chị Huyền chưa thể nhận lại con đúng theo pháp luật.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019423567916633.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chị Huyền trong lần hiếm hoi được bạn trai cho gặp con tại Pháp. Ảnh: <em>Nhân vật cung cấp. </em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Nhiều năm trước, chị Huyền và ông Azais yêu nhau. Do mâu thuẫn, cả hai chia tay khi chị đang mang thai tháng thứ 6. Sau khi sinh, người bạn trai ngoại quốc đã bí mật làm hộ chiếu cho con mang quốc tịch Pháp và mang đứa bé đi sau lần đến thăm.</p><p class="Normal"> 	<span>Sau thời gian dài một mình lặn lội sang Pháp kiện đòi con, hồi tháng 6 năm ngoái Tòa sơ thẩm, thẩm quyền rộng Albi (trụ sở tại Tòa án Tư pháp ở Albi) buộc ông Azais phải trả con gái cho chị Huyền.</span></p><p class="Normal"> 	"Bằng việc chia cách bất ngờ cô bé khỏi mẹ trong thời kỳ bú sữa, đưa cô bé đi xa hàng nghìn km, giấu người mẹ địa chỉ của cô bé, ông Azais đã vi phạm nghiêm trọng về mối quan hệ giữa mẹ và con, vi phạm các quy định của Bộ luật dân sự và tạo ra một chấn thương tâm lý thực sự cho đứa trẻ khi đang là một em bé", bản án nêu.</p><p class="Normal"> 	Tuy nhiên, ông này không thực thi phán quyết dù đã mang con gái quay lại Việt Nam và sống tại phường Thảo Điền, quận 2. Chị Huyền nhiều lần tìm đến nhà bạn trai cũ để gặp con nhưng không được, nên yêu cầu TAND TP HCM cho thi hành phán quyết của tòa Pháp tại Việt Nam.</p><p class="Normal" style="text-align:right;"> 	​<strong>Hải Duyên </strong></p>', 1, '1500194235257036953.jpg', NULL),
(95, 'Người đàn ông thú nhận ngoại tình để vợ được giảm án tù', 'Trước toà, người đàn ông bất ngờ thú nhận đã quan hệ bất chính, vì thế vợ ghen, gây thương tích cho bà hàng xóm không phải là vô cớ.', '<p class="Normal"> 	<span>Một năm trước, chị Lam nghi chồng quan hệ bất chính do thường rời nhà lúc khuya mỗi khi có điện thoại. Chị ghen, giận dỗi, tra hỏi song chồng đều phủ nhận, thậm chí quát nạt lại.</span></p><p class="Normal"> 	<span>Cuối tháng 7/2016, tình cờ cầm điện thoại của chồng, chị thấy những dòng tin nhắn từ người phụ nữ hàng xóm tên Thuỷ hơn tới 20 tuổi nên gọi lại tra vấn. Bà Thuỷ sau đó tìm đến nhà Lam to tiếng, nói bị vu oan. Hai bên xô xát. </span></p><p class="Normal"> 	Cơ quan điều tra cáo buộc, bà Thuỷ trong lúc <span>vung tay đòi tấn công đã bị Lam cầm dao chém trúng trán, mang thương tật 15%.</span></p><p class="Normal"> 	<span>Tại phiên toà xét xử Lam về tội Cố ý gây thương tích mở tại TAND huyện Anh Sơn (Nghệ An</span><span>), chồng bị cáo và bà Thuỷ đều khẳng định không có quan hệ tình cảm. Bị cáo Lam thừa nhận hành vi phạm tội, bị tuyên phạt 2 năm tù, bồi thường 20 triệu đồng.</span></p><p class="Normal"> 	<span><span>Bị cáo 29 tuổi sau đó kháng cáo xin giảm án cùng mức tiền bồi thường.</span></span></p><p class="Normal"> 	<span>Tại phiên phúc thẩm kéo dài hai ngày 13-14/7, bà Thuỷ giữ nguyên quan điểm đã bị vu oan mang tiếng xấu giờ còn phải chịu thương tật. Bà đề nghị xử nghiêm bị cáo.</span></p><p class="Normal"> 	Tuy nhiên sau đó, chồng bị cáo <span>bất ngờ đứng lên xin lỗi vợ, nói: "Vợ tôi ghen đúng. Sự thật, tôi và bà Thuỷ có quan hệ bất chính. </span><span>Trước đây tôi giữ kín vì muốn giữ thể diện cho bà Thuỷ". Giải thích vì sao không nói điều này tại phiên sơ thẩm, anh đáp: "Tôi không nghĩ bà ấy khăng khăng đòi tăng án, khiến vợ chồng tôi ra nông nỗi này".</span></p><p class="Normal"> 	Anh kể, trong một lần đi soi cá ban đêm tình cờ bắt gặp cảnh riêng tư của bà Thuỷ. Người phụ nữ sợ lộ chuyện, mang tiếng xấu nên năn nỉ anh giữ kín chuyện. Và anh sau đó đã không làm chủ được bản thân trước bà hàng xóm.</p><p> 	HĐXX phúc thẩm nhận định mối quan hệ bất chính của chồng là nguồn cơn khiến bị cáo Lam ghen tuông. Lam gây án trong trạng thái tinh thần bị kích động do bà Thuỷ tới nhà chửi bới. Việc này chưa được cấp sơ thẩm xem xét thấu đáo.</p><p class="Normal"> 	Cân nhắc các tình tiết giảm nhẹ, toà tuyên phạt Lam 15 tháng tù nhưng cho hưởng án treo, buộc bồi thường 19 triệu đồng.</p><p class="Normal"> 	<span>Trong khi bà Thuỷ đứng lên tuyên bố "không đồng ý với mức án toà tuyên", vợ chồng bị cáo dắt tay nhau rời phòng xử...</span></p><p class="Normal"> 	<em>* Tên nhân vật đã thay đổi.</em></p>', 1, NULL, NULL),
(96, 'Đồn phó biên phòng bị bắt trong vụ phá rừng pơ mu ở Quảng Nam', 'Cơ quan điều tra quân sự đã khởi tố, tạm giam bị can Lê Xuân Chính - nguyên phó đồn biên phòng cửa khẩu Nam Giang (Quảng Nam).', '<p class="Normal"> 	Ngày 14/7, đại tá Nguyễn Đăng Chung, Phó Chỉ huy trưởng Bộ đội Biên phòng tỉnh Quảng Nam cho biết, cơ quan điều tra quân sự đã khởi tố bị can, tạm giam Lê Xuân Chính, nguyên đồn phó Đồn Biên phòng cửa khẩu Nam Giang kiêm Trạm trưởng Kiểm soát Biên phòng cửa khẩu Nam Giang (huyện Nam Giang, Quảng Nam) để điều tra nghi vấn liên quan trách nhiệm về việc phá rừng pơ mu.</p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194237451997385.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					<span>Hàng chục cây gỗ pơ mu ở khu vực biên giới huyện Nam Giang bị đốn hạ: Ảnh:<em> Đắc Thành</em></span></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Một năm trước, cơ quan chức năng phát giác bãi tập kết hơn 280 phách gỗ pơ mu nằm gần Trạm kiểm soát Biên phòng Cửa khẩu Nam Giang (thuộc Đồn Biên phòng Cửa khẩu Nam Giang). Vào cuộc tìm hiểu, Hạt kiểm lâm rừng phòng hộ Nam Sông Bung khởi tố vụ án, chuyển hồ sơ cho Công an huyện Nam Giang.</span></p><p class="Normal"> 	Quá trình điều tra, nhà chức trách liên tiếp phát hiện hàng trăm phách pơ mu cất giấu sát Trạm kiểm soát Biên phòng cửa khẩu Nam Giang và trong khuôn viên Chi cục Hải quan cửa khẩu Nam Giang.</p><p class="Normal"> 	<span>Công an tỉnh Quảng Nam xác định </span><span>60 cây gỗ pơ mu bị đốn hạ với khối lượng hơn 115 m3 gỗ nhóm 2A.</span><span> Ủy ban Kiểm tra Tỉnh ủy Quảng Nam thông báo</span><span> 4 sĩ quan Đồn biên phòng cửa khẩu Nam Giang bị kỷ luật do liên quan trách nhiệm quản lý, trong số này có đại uý Lê Xuân Chính...</span></p><table class="tplCaption" align="center" border="0" cellpadding="3" cellspacing="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194237243430521.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					<span>Công an phát hiện hàng chục khối gỗ bị giấu gần Trạm Kiểm soát Biên phòng Cửa khẩu Nam Giang. Ảnh: <em>H.T</em></span></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Tháng 3/2017, Công an tỉnh Quảng Nam khởi tố tổng cộng 20 người, đa phần là trực tiếp tham gia đốn chặt, vận chuyển. Hồ sơ vụ án sau đó được chuyển cho cơ quan quân sự xử lý theo thẩm quyền.</p><p class="Normal" style="text-align:right;"> 	<strong>Đắc Thành</strong></p>', 1, '15001942371458748662.jpg', NULL),
(97, 'Lý lịch bố mẹ ảnh hưởng thế nào tới việc xin học của con?', 'Tôi từng bị phạt 10 tháng tù không giam giữ do gây thương tích thì con có được dự thi vào trường trong lực lượng vũ trang không?', '<p class="Normal" style="text-align:right;"> 	<strong>Nguyễn Hải Đăng</strong></p><p class="Normal"> 	<a title="Luật sư trả lời" href="http://vnexpress.net/tin-tuc/phap-luat/tu-van/ly-lich-bo-me-anh-huong-the-nao-toi-viec-xin-hoc-cua-con-3612852-p2.html"><strong>Luật sư trả lời</strong></a></p>', 1, NULL, NULL),
(98, 'Hai sơn nữ bị lừa bán giá 300 triệu đồng', 'Bán hai cô gái sang Trung Quốc lấy 300 triệu đồng, Hiền đưa một nửa số tiền cho gia đình nạn nhân, lừa rằng ông chủ trả trước tiền công.', '<p class="Normal"> 	<span>Công an huyện Tương Dương (Nghệ An) đang tạm giữ Ngân Văn Hiền (22 tuổi, trú xã Lượng Minh) để điều tra hành vi buôn bán người.</span></p><p class="Normal"> 	Hồ sơ cảnh sát xác định, năm 2014 Hiền dụ dỗ hai sơn nữ tuổi 15 và 17 "qua Trung Quốc lao động lương cao" rồi bán sang Trung Quốc với giá 300 triệu đồng. <span>Hiền sau đó đưa cho gia đình mỗi nạn nhân 80 triệu đồng, nói rằng cho ông chủ trả trước tiền công.</span></p><p class="Normal"> 	Giữa năm 2016, hai cô gái khi thoát được về Việt Nam đã tố cáo hành vi của kẻ buôn người này. Biết lộ tung tích, Hiền bỏ trốn, rạng sáng <span>13/7 bị bắt.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Mận Hiền</strong></p>', 1, NULL, NULL),
(99, '''Người lạ'' nhận là chủ chiếc ví cảnh sát cơ động TP HCM nhặt được', 'Trung uý Hùng đang tìm chủ chiếc ví rơi tại trung tâm Sài Gòn thì nam thanh niên nhận là của mình, song không chứng minh được thông tin.', '<table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942401259191704.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tài sản đánh rơi. Ảnh: <em>Quốc Thắng.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trực bảo vệ trên đường Đinh Tiên Hoàng (quận 1, TP HCM) tối 13/7, trung úy Nguyễn Đoàn Huy Hùng (Trung đoàn Cảnh sát cơ động, Công an TP HCM) phát hiện chiếc ví trên vỉa hè. Ngoài một số giấy tờ, bên trong còn có 300 USD, hơn 9 triệu đồng...</p><p class="Normal"> 	Anh Hùng quan sát xung quanh, đứng chờ chủ nhân chiếc ví quay lại tìm. Ngay lúc đó nam thanh niên từ bên kia đường chạy qua, rối rít nói cảm ơn và "xin nhận lại ví của mình".</p><p class="Normal"> 	Trung uý cảnh sát đề nghị cho biết có những gì bên trong, anh ta tỏ ra lúng túng, không trả lời rồi bỏ đi thật nhanh.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194240564018406.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Người đàn ông nhận lại tài sản. Ảnh: <em>Quốc Thắng.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Tổ công tác Cảnh sát cơ động lập biên bản tạm giữ ví, tìm chủ nhân. Khi ghi nhận đồ vật bên trong, cán bộ trực ban thấy có số điện thoại, gọi tìm hiểu thì anh này đang trình báo mất ví với Công an phường Đakao, quận 1.</p><p class="Normal"> 	Sau khi kiểm tra các thông tin, sáng 14/7 cảnh sát đã bàn giao tài sản lại cho chủ nhân.</p><p class="Normal" style="text-align:right;"> 	<strong>Quốc Thắng</strong></p>', 1, '15001942402131461842.jpg', NULL),
(100, 'Cô gái đi đường vô tình trúng đạn từ cuộc hỗn chiến', 'Trung nã súng tự chế trả thù nhóm đuổi đánh mình song "đạn lạc" sang cả cô gái đi đường.', '<p class="Normal"> 	Ngày 14/7, Công an tỉnh Bắc Giang đang tạm giữ Nguyễn Văn Trung (28 tuổi) để điều tra hành vi giết người.</p><p class="Normal"> 	Tối 7/7, Trung và nhóm bạn xô xát với một số thanh niên tại quán karaoke ở khu công nghiệp Đình Trám (huyện Việt Yên). <span>Bị đuổi đánh, Trung chạy về phòng trọ lấy súng tự chế quay lại hiện trường. Trung nã nhiều phát đạn hoa cải về phía đối thủ khiến hai người bị thương. Một cô gái tình cờ đi xe máy qua khu vực này cũng bị "dính đạn". </span></p><p class="Normal"> 	<span>Cả ba nạn nhân</span><span> được đưa đi cấp cứu kịp thời nên các vết thương không ảnh hưởng đến tính mạng. </span><span>4 ngày sau, Trung bị bắt khi cùng một số người trốn tại Hải Phòng. </span></p><p class="Normal"> 	<span>Nhà chức trách cho biết, liên quan vụ án, 3 người khác cũng bị tạm giữ để phục vụ điều tra.</span><span> </span></p><p class="Normal" style="text-align:right;"> 	<strong style="text-align:right;">Việt Dũng</strong></p>', 1, NULL, NULL),
(101, 'Tên trộm khoắng tiền trong lúc bà lão ngủ trên ghế', 'Lẻn vào ngôi nhà có người đang ngủ, Mạnh lấy được 75 triệu đồng cùng một chiếc điện thoại.', '<p class="Normal"> 	Ngày 14/7, Công an quận Ba Đình (Hà Nội) cho biết đang tạm giữ Trương Thế Mạnh (31 tuổi, ở phường Phúc Xá) để điều tra hành vi trộm cắp tài sản.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942422129915374.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tang vật vụ trộm.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Chiều 21/6, Mạnh vào một gia đình ở phố Gầm Cầu, phường Phúc Xá, lục lọi lấy trộm được gần 75 triệu đồng cùng một chiếc điện thoại. Đang định khoắng thêm vài tài sản khác, thấy bà lão 85 tuổi ngủ trên ghế<span> thức giấc, hắn vội bỏ đi.</span></p><p class="Normal"> 	<span>Mạnh sau đó sử dụng chính chiếc điện thoại này. Gần nửa tháng sau khi ăn trộm, Mạnh bị cảnh sát phát hiện tung tích.</span></p><p class="Normal"> 	<span>Tại cơ quan điều tra, anh ta khai còn gây ra 4 vụ trộm khác trong những ngày qua.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Hoàng Việt</strong></p>', 1, '1500194242174329407.jpg', NULL),
(102, 'Chồng tự tử sau khi đâm chết vợ vì ghen', 'Mâu thuẫn do ghen tuông, người chồng ở Đồng Nai đâm chết vợ rồi uống thuốc sâu tự tử nhưng được người dân đưa đi cấp cứu.', '<p> 	<span style="color:rgb(0,0,0);">Chếnh Vĩnh Dì (39 tuổi) và vợ Nguyễn Thị Yến Nhi (32 tuổi) mâu thuẫn tình cảm dẫn đến to tiếng tối 13/7 tại căn nhà cấp 4 ở phường Phú Bình, thị xã Long Khánh, Đồng Nai.</span></p><p> 	<span style="color:rgb(0,0,0);">Trong lúc cãi nhau, Dì bất ngờ vơ lấy dao đâm vợ nhiều nhát khiến nạn nhân gục xuống nền nhà. Thấy vợ tử vong, gã chồng uống thuốc sâu tự tử nhưng được người thân phát hiện, đưa đi cấp cứu. </span></p><p> 	<span style="color:rgb(0,0,0);">Khám nghiệm hiện trường, cảnh sát thu giữ con dao gây án. </span></p><p style="text-align:right;"> 	<strong><span style="color:rgb(0,0,0);">Phước Tuấn</span></strong></p>', 1, NULL, NULL),
(103, 'Kẻ đâm chết nhân tình lĩnh án chung thân', 'Muốn nối lại tình cảm nhưng bị từ chối, Chí đâm người tình cũ tử vong giữa đường. ', '<p class="Normal"> 	Ngày 14/7, TAND Cấp cao tại TP HCM bác đơn xin giảm nhẹ hình phạt, giữ nguyên án chung thân đối với Đoàn Minh Chí (39 tuổi, quê Tây Ninh) về tội Giết người. Theo tòa, hành vi của bị cáo là côn đồ, mức án này là thỏa đáng.</p><table class="tplCaption" align="center" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942451467563419.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chí bị y án tù chung thân. Ảnh: <em>Bình Nguyên.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Bản án sơ thẩm xác định, năm 2012, Chí sống với chị Thảo (27 tuổi) như vợ chồng. Thời gian sau này hai bên thường xảy ra mâu thuẫn, nhờ chính quyền xã phân chia tài sản rồi đường ai nấy đi.</p><p class="Normal"> 	Vẫn còn tình cảm với chị Thảo, Chí nhiều lần gặp để níu kéo nhưng đều bị từ chối. Chiều 17/11/2016, sau khi uống rượu, anh ta đi tìm Thảo lúc đang đón con từ trường về.</p><p class="Normal"> 	Chí tiếp tục thuyết phục chị quay về sống, rồi xảy ra cãi vã. Tức giận khi Thảo có lời lẽ thách thức, Chí lấy dao trong cốp xe đâm chị nhiều nhát. Nạn nhân được đưa đi cấp cứu nhưng tử vong.</p><p class="Normal"> 	Sau khi bị TAND tỉnh Tây Ninh xử sơ thẩm hồi đầu năm, Chí kháng cáo xin giảm nhẹ hình phạt.</p><p class="Normal" style="text-align:right;"> 	<strong>Bình Nguyên </strong></p>', 1, '15001942441059020037.jpg', NULL),
(104, 'Hơn 21 tấn vỏ sò tai tượng quý hiếm bị tạm giữ', 'Ôtô tải chở hơn 21 tấn vỏ sò tai tượng không rõ nguồn gốc từ Bình Thuận về miền Tây tiêu thụ thì bị phát hiện.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942461431048820.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Số vỏ trai tai tượng đang bị tạm giữ. Ảnh: <em>Chi cục QLTT Tiền Giang.</em></p> 			</td> 		</tr></tbody></table><p> 	Cảnh sát giao thông tỉnh Tiền Giang hai hôm trước phát hiện ôtô tải chạy trên Quốc lộ 1A có dấu hiệu bất thường nên dừng xe kiểm tra.</p><p> 	Họ phát hiện xe chở hơn 21 tấn vỏ sò tai tượng thuộc loài quý hiếm, có nguy cơ tuyệt chủng không có hóa đơn chứng từ. Tài xế khai chở số hàng này từ Bình Thuận về miền Tây tiêu thụ.</p><p> 	Chi cục Quản lý thị trường Tiền Giang đã lấy mẫu số vỏ sò bị tạm giữ gửi Viện Nghiên cứu nuôi trồng thủy sản xác định rõ là loài nào. </p><p> 	Trước đó, rạng sáng 2/7, tại cảng cá Phan Thiết, Bình Thuận, Đặc nhiệm biên phòng phía Nam phát hiện tàu cá đang bốc dỡ 50 tấn vỏ sò tai tượng đi tiêu thụ.</p><p> 	Chủ tàu cho biết số hàng được khai thác ở đảo Trường Sa, sau đó đưa vào bờ bán cho thương lái khoảng 5.000 đồng một kg. Vỏ sò tai tượng được dùng để làm ra các sản phẩm thủ công mỹ nghệ.</p><div style="text-align: center;"> 	<div id="video-154273" data-component="true" data-component-type="video" data-component-value="154273" data-component-typevideo="2"></div></div><p style="text-align:right;"> 	<strong>Hoàng Nam</strong></p>', 1, '15001942461962758868.jpg', NULL),
(105, 'Kẻ hành hạ bé 3 tuổi bị Campuchia phạt 18 năm tù', 'Đang bị giam tại Việt Nam, Nguyễn Thanh Dũng vẫn bị tòa án Campuchia tuyên 18 năm tù về tội Hiếp dâm trẻ em. ', '<p class="Normal"> 	Nguyễn Thanh Dũng (34 tuổi, quốc tịch Việt Nam) ngày 13/7 bị tòa Campuchia xử vắng mặt, kết án 18 năm tù về tội Hiếp dâm trẻ em; buộc bồi thường khoảng 20.000 USD cho nạn nhân, báo <em>Phnom Penh Post</em> đưa tin.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="left"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194247329557499.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dũng bị bắt ở Việt Nam.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Người tình đồng tính của bị cáo là Stefan Struik (53 tuổi, quốc tịch Hà Lan) lĩnh 2 năm tù và phải nộp phạt khoảng 1.000 USD về hành vi che giấu bằng chứng và không tố cáo vụ án.</p><p class="Normal"> 	Dũng bị cáo buộc tra tấn và xâm hại cậu bé 3 tuổi tại đồn điền của Stefan Struik (tỉnh Mondulkiri, Campuchia), sau khi các video ghi lại sự việc bị phát tán trên mạng.</p><p class="Normal"> 	Hình ảnh cậu bé trần truồng bị Dũng đánh đập, lạm dụng bằng điện và súng đã gây phẫn nộ dư luận.</p><p class="Normal"> 	Anh ta bị Bộ Công an Việt Nam bắt vài ngày sau đó. Cảnh sát Campuchia đồng thời cũng mở cuộc điều tra về Dũng sau khi cha mẹ đứa trẻ nộp đơn tố cáo con mình bị xâm hại tình dục.</p><p class="Normal"> 	Tại Việt Nam, VKSND Tối cao đã truy tố Nguyễn Thanh Dũng (35 tuổi) về tội Hành hạ người khác có khung hình phạt cao nhất 3 năm tù.</p><p class="Normal"> 	Cáo buộc thể hiện, từ ngày 10 đến 16/8/2016, sau khi sử dụng ma túy đá, Dũng mang cậu bé ra hành hạ. Hắn dùng kẹp giấy, roi điện liên tục chích vào dương vật, thân thể cậu bé mặc nạn nhân kêu khóc thảm thiết. Sự việc được anh ta dùng điện thoại ghi hình.</p><p class="Normal"> 	Trong lần về Việt Nam chơi, Dũng đưa điện thoại cho người thân đi cầm cố. Những clip anh ta hành hạ bé trai sau đó bị phát hiện.</p><p class="Normal"> 	Trao đổi với <em>VnExpress</em>, luật sư Nguyễn Hữu Thế Trạch (Đoàn Luật sư TP HCM) cho biết, Việt Nam và Campuchia <span class="_5yl5"><span>đã ký Hiệp định tương trợ tư pháp Hình sự ngày 20/12/2016. Theo điểm b Điều 2 của Hiệp định này, việc thi hành án hình sự <span class="_5yl5"><span>của quốc gia yêu cầu tại quốc gia được yêu cầu, trừ trong phạm vi được pháp luật của quốc gia được yêu cầu cho phép.</span></span></span></span></p><p class="Normal"> 	<span class="_5yl5"><span><span class="_5yl5"><span>"Điều này nghĩa là Dũng phải thi hành án tại quốc gia giữ quyền tài phán - tức Việt Nam. Dũng sắp bị đưa ra xét xử thì anh ta chỉ phải thi hành bản án sắp được tuyên về tội Hành hạ người khác của toà án Việt Nam", ông Trạch phân tích. </span></span></span></span></p><p class="Normal" style="text-align:right;"> 	Theo <em>E.VnExpress.net</em></p>', 1, '15001942471432398964.jpg', NULL),
(106, 'Có được đâm chết kẻ cướp khi bị tấn công?', 'Vì bênh một phụ nữ đang bị 3 kẻ cướp tấn công, anh tôi đã cầm dao đâm chết một người.', '<p class="Normal"> 	Anh tôi đi qua đoạn vắng thấy 3 người đàn ông đang trấn lột một phụ nữ nên vào can ngăn. Bị cầm gậy đuổi đánh, anh tôi bỏ chạy vào nhà dân lấy con dao và đâm chết một người. Tuy nhiên trong lúc xô xát, anh tôi cũng bị chúng đánh bị thương với tỷ lệ tổn hại 11% sức khỏe. </p><p class="Normal"> 	Xin hỏi, anh tôi vì bênh người yếu thế mà vô tình gây ra việc đâm chết người như trên thì bị xử lý không?</p><p class="Normal" style="text-align:right;"> 	<strong>Phạm Thế Anh</strong></p><p class="Normal"> 	<a title="Luật sư trả lời" href="http://vnexpress.net/tin-tuc/phap-luat/tu-van/co-duoc-dam-chet-ke-cuop-khi-bi-tan-cong-3612844-p2.html"><strong>Luật sư trả lời</strong></a></p>', 1, NULL, NULL),
(107, 'Không muốn để tên cha trong giấy khai sinh của con', 'Tôi hận cha đứa trẻ nên không muốn để tên người này trong giấy khai sinh của con thì có được không?', '<p class="Normal" style="text-align:right;"> 	<strong>Đoàn Khánh Hương</strong></p><p class="Normal"> 	<a title="Luật sư trả lời" href="http://vnexpress.net/tin-tuc/phap-luat/tu-van/khong-muon-de-ten-cha-trong-giay-khai-sinh-cua-con-3607941-p2.html"><strong>Luật sư trả lời</strong></a></p>', 1, NULL, NULL),
(108, 'Đoạt mạng vợ cũ vì không trả lại con', 'Bực vợ cũ đón con đi chơi mà không đưa về cho mình, Thạch cầm dao đâm, cứa cổ cô này.', '<p class="Normal"> 	Sáng 13/7, TAND tỉnh Thừa Thiên - Huế tuyên án tù chung thân với Ngô Ngọc Thạnh (29 tuổi, trú xã Quảng Thành, Quảng Điền) về tội Giết người.</p><p class="Normal"> 	Theo cáo trạng, sau ly hôn với chị <span>Nguyễn Thị Lan (29 tuổi), </span><span>Ngô Ngọc Thạnh được giao nuôi con gái chung. Hai cha con rời Huế đến tỉnh Hà Nam sinh sống.</span></p><p class="Normal"> 	Đầu tháng 10/2016, chị Lan đón con vào Huế chơi nhưng không đưa trở lại Hà Nam, nói Thạnh muốn nuôi thì vào Huế đón ra.</p><p class="Normal"> 	Bực tức, t<span>ối 26/10/2016, Thạnh đón xe vào đây. Chiều hôm sau, gặp vợ cũ tại một quán chè, anh ta cầm dao đâm nhiều nhát, cứa cổ. Sự việc xảy ra ngay trước mặt con gái nhỏ.</span></p><p class="Normal"> 	Ngày 3/11/2016, chị Lan tử vong.</p><p class="Normal" style="text-align:right;"> 	<strong>Võ Thạnh</strong></p>', 1, NULL, NULL),
(109, 'Bốn ôtô đỗ ở lề đường bị đập vỡ kính, lục tìm tài sản', 'Trong đêm 12/7, 4 ôtô đỗ trước cửa nhà đã đồng loạt bị đập vỡ kính, một chiếc mất cắp tài sản.', '<p class="Normal"> 	<span>Ngày 13/7, Công an thành phố Huế (Thừa Thiên - Huế) đang phối hợp với nhiều lực lượng truy tìm kẻ đập vỡ kính hàng loạt ôtô đỗ ở lề đường.</span></p><table class="tplCaption" border="0" cellpadding="3" cellspacing="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942511737411776.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một xe ôtô bị kẻ gian đập vỡ kính, trộm cắp tài sản. Ảnh: <em>H.0.</em></p> 			</td> 		</tr></tbody></table><p> 	<span>Sáng 13/7, 4 người dân phường Vĩnh Ninh đến trụ sở công an phường trình báo về việc đậu xe ở lề đường trước nhà vào đêm 12/7 song bị đập vỡ cửa kính.</span><span> </span><span style="color:rgb(0,0,0);">Trong 4 xe có 3 chiếc đậu ở đường Phan Chu Trinh, gồm một Mazda6, một Mazda3, một Mazda2.</span></p><p> 	<span style="color:rgb(0,0,0);">Ba xe không mất tài sản nhưng bị lục lọi, một chiếc bị lấy đi hai túi xách cùng giấy tờ xe bên trong.</span></p><p class="Normal"> 	<span>Trước đó, sáng 10/7, kẻ gian cũng đập cửa kính một ôtô đỗ ở đường Ngô Lê Cát (phường Thủy Xuân) lấy đi hơn 2 triệu đồng, 100 USD, giấy tờ xe.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Võ Thạnh</strong></p>', 1, '15001942511950990214.jpg', NULL),
(110, 'Cưỡng hiếp bất thành, gã thanh niên cướp iPhone của thiếu nữ', 'Lừa thiếu nữ mới quen trên Facebook ra biển chơi, Sơn thực hiện hành vi hiếp dâm nhưng bị thiếu nữ chống trả quyết liệt.', '<p class="Normal"> 	Ngày 13/7, Công an thị xã Hương Thuỷ (Thừa Thiên Huế) khởi tố vụ án, khởi tố bị can với Trần Minh Sơn (21 tuổi) về hành vi cướp tài sản.  </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194252112870982.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trần Minh Sơn tại cơ quan điều tra. Ảnh: <em>Trần Hồng.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Theo điều tra, ngày 12/7 Sơn rủ bạn gái quen qua Facebook đi chơi tại bãi tắm Thuận An đến khuya. Trên đường về, Sơn chở cô này đến khu vực vắng người để giở trò đồi bại nhưng bị<span> thiếu nữ chống trả quyết liệt. Không thể thực hiện hành vi hiếp dâm, Sơn quay sang tấn công thiếu nữ rồi cướp điện thoại iPhone 6 rồi bỏ trốn. </span></p><p class="Normal"> 	Công an thị xã Hương Thuỷ đã truy bắt được Sơn sau khi nhận được trình báo. Tại cơ quan công an, Sơn đã khai nhận toàn bộ hành vi phạm tội.  </p><p class="Normal" style="text-align:right;"> 	<strong>Võ Thạnh</strong></p>', 1, '15001942521646425365.jpg', NULL),
(111, 'Đại học đầu tiên công bố danh sách trúng tuyển', 'Ngày 16/7, Đại học FPT công bố danh sách gần 500 thí sinh trúng tuyển đợt đầu tiên.', '<p> 	Đại học FPT cho biết, sau ngày điều chỉnh nguyện vọng xét tuyển đầu tiên (16/7), có hơn 5.000 thí sinh đăng ký nguyện vọng vào trường. Trong đó, gần 500 em trúng tuyển hệ đại học chính quy đợt 1 do đăng ký Đại học FPT ở nguyện vọng 1.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942561294092166.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại học FPT là trường đầu tiên công bố danh sách trúng tuyển năm 2017.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Danh sách trúng tuyển cuối cùng sẽ được công bố trước 17h ngày 1/8. <span>Các thí sinh trúng tuyển nếu thay đổi nguyện vọng 1 sang trường khác có thể sẽ không có tên trong danh sách trúng tuyển cuối cùng.</span></p><p class="Normal"> 	<span>Năm 2017, Đại học FPT tuyển 2.000 chỉ tiêu thuộc các khối ngành </span><span>Khoa học kỹ thuật, Kinh tế - Xã hội và Mỹ thuật ứng dụng. </span><span>Các tổ hợp xét tuyển gồm A00, A01, D01, D90 và D96 tuỳ theo ngành học thí sinh đăng ký.</span></p><p class="Normal"> 	<span>Để trúng tuyển vào Đại học FPT, thí sinh cần có tổng điểm 3 môn thi THPT quốc gia từ 15,5 điểm trở lên, hoặc điểm trung bình học bạ THPT từ 6.0 trở lên với 3 môn tương ứng ngành đăng ký học tại Đại học FPT; đủ điều kiện sơ tuyển của Đại học FPT (trúng tuyển kỳ thi tuyển sinh của trường hoặc đủ điều kiện miễn thi). </span></p><p class="Normal"> 	<span>Các thí sinh đạt 21 điểm trở lên (đã bao gồm điểm ưu tiên) tổng 3 môn thi THPT quốc gia hoặc đạt 7.0 trở lên trung bình học bạ THPT 3 môn tương ứng với ngành đăng ký trong 5 học kỳ liên tiếp đủ điều kiện miễn thi vào Đại học FPT.</span></p>', 1, '1500194256150767715.png', NULL),
(112, '''Nữ hoàng'' Toán học thế giới qua đời ở tuổi 40', 'Giáo sư Toán học Đại học Stanford Maryam Mirzakhani, người phụ nữ duy nhất giành huy chương Fields tính đến nay, qua đời ở tuổi 40.', '<p class="Normal"> 	Đại học Stanford ngày 16/7 đưa tin, bà Maryam Mirzakhani qua đời hôm 15/7 sau thời gian dài chiến đấu với căn bệnh ung thư vú.</p><p class="Normal"> 	<span>Bà Mirzakhani quê gốc Iran, lấy bằng tiến sĩ của Đại học Harvard năm 2004 và giảng dạy tại Đại học Princeton, sau đó về về Stanford làm giáo sư Toán học năm 2008, khi mới 31 tuổi</span><span>.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942571783707148.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					<span>''Nữ hoàng toán học'' thế giới Maryam Mirzakhani qua đời ở tuổi 40.</span></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Năm 2014, bà được trao tặng huy chương Fields, giải thưởng vẫn được coi là "Nobel Toán học" với những nghiên cứu tập trung vào lý thuyết toán liên quan đến hình học, hệ thống động lực học, tính đối xứng của các bề mặt cong như mặt cầu hay hyperbol... Đến nay, bà là phụ nữ đầu tiên và duy nhất trên thế giới nhận danh hiệu này kể từ khi nó được nhà toán học John Charles Fields sáng lập năm 1936.</p><p class="Normal"> 	Đồng nghiệp của Mirzakhani chia sẻ, bà rất tham vọng, kiên quyết và không sợ hãi trước những vấn đề mà người khác không giải quyết được. Trong một bài phỏng vấn, "nữ hoàng" Toán học từng cho biết công việc của bà không khác việc "bị lạc giữa khu rừng và phải cố gắng dùng mọi hiểu biết để tìm ra những thủ thuật mới, thêm một chút may mắn, bạn có thể tìm được đường ra".</p><p class="Normal"> 	Ông Marc Tessier-Lavigne, Hiệu trưởng Đại học Stanford, ca ngợi bà Mirzakhani là nhà lý thuyết gia kiệt xuất với cống hiến không ngừng nghỉ. Bà đã truyền cảm hứng cho hàng nghìn phụ nữ theo đuổi con đường nghiên cứu khoa học và toán học. "Stanford và cả thế giới sẽ không bao giờ quên cô ấy", ông Marc nói.</p><p class="Normal"> 	<span>Đại học Stanford thông tin sẽ tổ chức buổi lễ tưởng niệm bà Maryam Mirzakhani vào mùa thu, khi giảng viên và học sinh quay lại trường để bắt đầu năm học mới</span><span>.</span></p>', 1, '1500194257928989603.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(113, '8 cấu trúc ngữ pháp tiếng Anh người dùng thường mắc lỗi', 'Ngay cả khi thông thuộc ngữ pháp cơ bản, việc vận dụng tiếng Anh trong đặt câu vẫn khiến nhiều người lúng túng.', '<p class="Normal"> 	<strong>1. I wore a jeans</strong></p><p class="Normal"> 	Ngữ pháp đúng: I wore jeans/ I wore a pair of jeans.</p><p class="Normal"> 	Các danh từ như <em>jeans</em>, <em>pants</em>, <em>shorts</em>, <em>scissors</em> và <em>glasses</em> rất đặc biệt. Dù mỗi từ chỉ một vật đơn lẻ, chúng ta không thể dùng "a" mà nên bỏ trống mạo từ hoặc dùng "a pair of". Nếu muốn nói về chúng với số lượng nhiều, bạn nói "two/three/four pairs of ...". Ví dụ:</p><p class="Normal"> 	- She''s wearing glasses. (Cô ấy đeo kính).</p><p class="Normal"> 	- I bought a pair of shorts at the mall. (Tôi mua một chiếc quần đùi ở trung tâm thương mại).</p><p class="Normal"> 	- There''s a pair of scissors on my desk. (Có một chiếc kéo trên bàn làm việc của tôi).</p><p class="Normal"> 	- I have two pairs of white pants. (Tôi có hai chiếc quần trắng). </p><p class="Normal"> 	<strong>2. He loves team sports as soccer</strong></p><p class="Normal"> 	Ngữ pháp đúng: He loves team sports such as soccer/ He loves team sports like soccer.</p><p class="Normal"> 	Khi bạn muốn đưa ra ví dụ, hãy sử dụng "such as" hoặc "<span>like"</span><span>, không dùng "</span><span>as"</span><span>. "Such as" là từ nên dùng trong tình huống trang trọng hơn "like".</span></p><p class="Normal"> 	<strong>3. I didn''t save money enough</strong></p><p class="Normal"> 	Ngữ pháp đúng: I didn''t save enough money.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942591326902189.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>Vecteezy</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Từ "enough" đứng trước danh từ, nhưng đứng sau tính từ. Ví dụ:</p><p class="Normal"> 	- We have enough food. (Chúng ta có đủ thức ăn - "food" là danh từ).</p><p class="Normal"> 	- You''ve not tall enough to go on the ride. (Bạn không đủ cao để lái xe - "tall" là tính từ).</p><p class="Normal"> 	<strong>4. I worked a lot in last month./ We''ll meet on next monday</strong></p><p class="Normal"> 	Ngữ pháp đúng: I worked a lot last month/ We''ll meet next monday.</p><p class="Normal"> 	Chúng ta thường dùng "in" với tháng và "on" với ngày. Tuy nhiên, khi sử dụng hai từ <em>last</em> và <em>next</em>, giới từ được loại bỏ.</p><p class="Normal"> 	<strong>5. It was so long time ago</strong></p><p class="Normal"> 	Ngữ pháp đúng: It was so long ago/ It was such a long time ago.</p><p class="Normal"> 	"So + tính từ/ trạng từ" như:</p><p class="Normal"> 	- She''s so friendly. (Cô ấy rất thân thiện). </p><p class="Normal"> 	- This sandwich is so good. (Chiếc bánh sandwich này rất ngon).</p><p class="Normal"> 	- He works so hard. (Anh ấy làm việc rất chăm chỉ).</p><p class="Normal"> 	Trong khi đó, "such a + tính từ + danh từ":</p><p class="Normal"> 	- She''s such a friendly person. (Cô ấy là một người thân thiện).</p><p class="Normal"> 	- This is such a good sandwich. (Đây là chiếc bánh sandwich thực sự ngon).</p><p class="Normal"> 	- He has such a demanding job. (Anh ấy là người rất ham mê công việc). </p><p class="Normal"> 	Lưu ý: Khi danh từ ở dạng số nhiều, không dùng "a".</p><p class="Normal"> 	- They are such friendly people. (Họ là những người rất thân thiện).</p><p class="Normal"> 	- These are such good cookies. (Những chiếc bánh quy này rất ngon).</p><p class="Normal"> 	<strong>6. I lent to him some money</strong></p><p class="Normal"> 	Ngữ pháp đúng: I lent him some money/ I lent some money to him.</p><p class="Normal"> 	Nguyên tắc này áp dụng cho <em>lend</em>, <em>give</em> hoặc những động từ có cả tân ngữ trực tiếp và tân ngữ gián tiếp. Bạn có thể đặt tân ngữ gián tiếp (him) không kèm "to" ngay sau động từ, hoặc đặt tân ngữ gián tiếp (him) kèm "to" sau tân ngữ trực tiếp (money). Ví dụ:</p><p class="Normal"> 	- He gave Rachel the keys/ <span>He gave the keys to Rachel.</span><span> (Tôi đưa chìa khóa cho Rachel). </span></p><p class="Normal"> 	<strong>7. Roller coasters make me to feel sick</strong></p><p class="Normal"> 	Ngữ pháp đúng: Roller coasters make me feel sick. <span>(Roller coasters: tàu lượn siêu tốc).</span></p><p class="Normal"> 	<em>Let</em>, <em>make</em> và <em>have</em> là những "causative verb" (động từ cầu khiến). Chúng được theo sau bởi tân ngữ gián tiếp và nguyên thể của động từ tiếp theo, không có "to".</p><p class="Normal"> 	- Our boss doesn''t let us leave work early. (Ông chủ không cho chúng tôi về sớm). </p><p class="Normal"> 	- My mom makes me clean my room. (Mẹ bắt tôi dọn phòng).</p><p class="Normal"> 	- I''ll have my assistant call you later today. (Tôi sẽ bảo trợ lý gọi cho anh sau). </p><p class="Normal"> 	Đối với từ <em>help</em>, chúng ta có thể dùng "to" hoặc không, nhưng không dùng "to" vẫn phổ biến hơn. Ví dụ: Please help me carry these boxes./ Please help me to carry these boxes.  (Làm ơn giúp tôi bê những cái hộp này). </p><p class="Normal"> 	<strong>8. You stopped me make a mistake</strong></p><p class="Normal"> 	Ngữ pháp đúng: You stopped me from making a mistake.</p><p class="Normal"> 	Các từ <em>stop</em> và <em>prevent</em> được theo sau bởi "someone/something + from + V-ing":</p><p class="Normal"> 	- The vaccine stops people from catching the disease. (Vắc xin này giúp con người ngừa căn bệnh đó).</p><p class="Normal"> 	- The waterproof covering prevents the equipment from getting wet. (Phần nắp chống nước giúp thiết bị không bị ẩm ướt). </p>', 1, '15001942581546173235.jpg', NULL),
(114, 'Người thân chào đón đoàn học sinh thi Olympic Hóa về nước', 'Đạt thành tích cao nhất từ trước đến nay, đoàn học sinh Olympic Hoá học quốc tế trở về trong sự tự hào của thầy cô và gia đình.', '<p class="Normal"> 	18h ngày 15/7, sảnh A1 cảng hàng không quốc tế Nội Bài đông nghịt người cầm cờ, hoa, quà lưu niệm đứng đợi đoàn học sinh Việt Nam dự thi Olympic Hóa học từ Thái Lan về nước. Dù thấm mệt sau chuyến bay chiều, cả 4 em đều vui mừng trước sự chào đón nồng nhiệt của thầy cô, gia đình và bạn bè.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156947" data-component="true" data-component-type="video" data-component-value="156947" data-component-typevideo="2"></div></div> 	<p class="Normal"> 		<em>Thí sinh và người nhà chia sẻ cảm xúc về thành tích đã đạt được. </em></p> </div><p class="Normal"> 	Bất ngờ với sự chuẩn bị ăn mừng của mọi người, đặc biệt là sự góp mặt của nhiều học sinh lớp dưới, Phạm Đức Anh, trường THPT chuyên Khoa học tự nhiên - Đại học Quốc gia Hà Nội, không giấu nổi niềm sung sướng. "Là học sinh lớp 11 duy nhất của đoàn và giành huy chương vàng, em cảm thấy vui và tự hào vì đã có đóng góp cho Tổ quốc", Đức Anh nói.</p><p class="Normal"> 	Nam sinh chia sẻ cách đây 9 năm, anh trai ruột là Phạm Anh Tuấn, tham gia cuộc thi Olympic Hóa học và giành huy chương đồng. "Ước mơ duy nhất của em trước khi lên đường sang Thái Lan là có thể thay anh đổi màu huy chương. Kết quả em đã thực hiện được, không phụ sự kỳ vọng của anh trai và gia đình", chàng trai vàng chia sẻ.</p><p class="Normal"> 	Nguyễn Bằng Thanh Lâm, học sinh lớp 12 trường THPT chuyên Hà Nội - Amsterdam, kể lại giây phút xướng tên học sinh đạt huy chương. "Em thực sự hồi hộp khi ban tổ chức chuẩn bị đọc tên thí sinh cuối cùng giành được huy chương bạc rồi vỡ òa khi đó không phải tên mình. Lúc này em mới chắc chắn huy chương vàng thuộc về mình", Lâm nói. Em xếp thứ 20/297 thí sinh với số điểm 89,67/100.</p><p class="Normal"> 	<span>Không có chiến lược học tập cụ thể, cũng không xác định học để giành huy chương, Thanh Lâm bất ngờ với kết quả giành được. "Những cố gắng của con trong suốt thời gian qua đã nhận được thành quả xứng đáng", chị Trần Yến, mẹ của Thanh Lâm chia sẻ</span><span>. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942601674829516.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đại diện Bộ Giáo dục và Đào tạo tặng hoa chúc mừng 4 thí sinh đạt thành tích cao trong kỳ thi Olympic Hóa học quốc tế 2017. Ảnh: <em>Thanh Tâm</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Khác với Đức Anh và Thanh Lâm, Đinh Quang Hiếu, học sinh lớp 12 trường THPT chuyên Khoa học tự nhiên - Đại học Quốc gia Hà Nội, không quá bất ngờ với kết quả của chính mình. Em đặt mục tiêu bảo vệ thành công huy chương vàng đã đạt được từ năm ngoái. "Vì giành huy chương vàng một lần nên em cảm thấy rất thoải mái trước và trong khi làm bài thi", Hiếu chia sẻ.</p><p class="Normal"> 	Chủ nhân của chiếc huy chương bạc là Hoàng Nghĩa Tuyến, học sinh lớp 12 trường THPT chuyên Phan Bội Châu (Nghệ An), trở về trong vòng tay của bố. "Dù không đạt thành tích như các bạn nhưng đó là nỗ lực của con trai tôi và tôi ghi nhận điều đó", phụ huynh đến từ Nghệ An chia sẻ.</p><p class="Normal"> 	Đoàn Việt Nam dự thi Olympic Hóa học quốc tế năm nay gồm 4 học sinh, trong đó có 3 em giành huy chương vàng là Đinh Quang Hiếu (92,13/100 điểm, xếp thứ 9), Nguyễn Bằng Thanh Lâm (89,76/100 điểm, xếp thứ 20), Phạm Đức Anh (89,46/100 điểm, xếp thứ 21) và một em đạt huy chương bạc là Hoàng Nghĩa Tuyến (85,91/100 điểm). Đây là kết quả cao nhất của đội tuyển Việt Nam tham dự Olympic Hóa học quốc tế từ trước đến nay.</p><p class="Normal"> 	Olympic Hoá học quốc tế năm 2017 có sự tham gia của 76 quốc gia và vùng lãnh thổ với 297 thí sinh. Theo bảng xếp hạng không chính thức, đội tuyển Mỹ xếp thứ nhất với 4 huy chương vàng; Việt Nam và Trung Quốc xếp thứ hai với 3 huy chương vàng, một huy chương bạc.</p><p class="Normal"> 	<span>Năm 2016, Việt Nam giành hai huy chương vàng và một bạc Olympic Hóa quốc tế. Trong đó em </span><a href="http://vnexpress.net/tin-tuc/giao-duc/chang-trai-vang-hoa-hoc-tung-khoc-khi-khong-lam-xong-bai-3449138.html">Đinh Quang Hiếu</a><span> (THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội) là một trong hai thí sinh giành giải vàng</span><span>.</span></p>', 1, '15001942601723136513.jpg', NULL),
(115, '''Vương quốc dừa sáp'' ở tỉnh nào?', 'Tỉnh này thuộc đồng bằng sông Cửu Long với hàng trăm di tích văn hóa phi vật thể, nhiều lễ hội và công trình kiến trúc độc đáo.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942611400077945.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dừa sáp.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<strong>Câu 1: Dừa sáp là đặc sản độc đáo ở tỉnh nào?</strong></p><p class="Normal"> 	<a title="a. Trà Vinh" href="http://vnexpress.net/tin-tuc/giao-duc/trac-nghiem/vuong-quoc-dua-sap-o-tinh-nao-3613903-p2.html">a. Trà Vinh</a></p><p class="Normal"> 	<a title="b. Sóc Trăng" href="http://vnexpress.net/tin-tuc/giao-duc/trac-nghiem/vuong-quoc-dua-sap-o-tinh-nao-3613903-p3.html">b. Sóc Trăng</a></p><p class="Normal"> 	<a title="c. Bến Tre" href="http://vnexpress.net/tin-tuc/giao-duc/trac-nghiem/vuong-quoc-dua-sap-o-tinh-nao-3613903-p3.html">c. Bến Tre</a></p><p class="Normal" style="text-align:right;"> 	<strong>Mạnh Tùng</strong></p>', 1, '150019426196546743.jpg', NULL),
(116, '8 cụm từ thông dụng để mô tả người trong tiếng Anh', '"Troublemaker" là kẻ chuyên gây rắc rối, trong khi "crybaby" chỉ người hay khóc nhè.', '<p class="Normal"> 	1. <em>Daredevil </em>chỉ kẻ luôn mạo hiểm, có thể theo một cách không cần thiết. (<em>dare</em>: thách thức; <em>devil</em>: ma quỷ, điều khủng khiếp).</p><p class="Normal"> 	Từ này cũng có thể được sử dụng như tính từ. Ví dụ: "racing-car drivers doing daredevil stunts" (những tay đua ôtô đang biểu diễn trò mạo hiểm).</p><p class="Normal"> 	2. <em>Crybaby </em>chỉ người rất hay khóc mà không vì lý do chính đáng, dù người đó không phải là một đứa trẻ. (<em>Cry</em>: khóc, <em>baby</em>: đứa trẻ).</p><p class="Normal"> 	Ví dụ: "He told her she was still the same crybaby as ever". (Anh ấy nói cô vẫn là người mau nước mắt như mọi khi).</p><p class="Normal"> 	3. <em>Cheapskate </em>có nghĩa là kẻ hà tiện, người rất ghét phải bỏ tiền ra.</p><p class="Normal"> 	Ví dụ: "My dad''s such a cheapskate that he cuts his hair himself". (Bố tôi là người tiết kiệm tới nỗi ông tự cắt tóc cho chính mình).</p><p class="Normal"> 	4. <em>Barrel of laughs/fun</em> chỉ một ai đó rất vui vẻ, hay cười hoặc thứ gì đó thú vị. (Nghĩa đen: một thùng tiếng cười).</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194262353583696.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>BBC</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ví dụ: "He''s a bit serious, isn''t he?" - "Yeah, not exactly a barrel of laughs" (Anh ta khá nghiêm túc, đúng chứ? - Vâng, cũng không hẳn là vui tính lắm).</p><p class="Normal"> 	5. <em>Oddball </em>là người lập dị, kỳ quặc. Nó cũng có thể là tính từ.</p><p class="Normal"> 	Ví dụ: "The oddball superstar''s habits include watching TV with his chimpanzee". (Những thói quen của siêu sao lập dị bao gồm xem TV với con tinh tinh của anh ta).</p><p class="Normal"> 	6. <em>Old as the hills</em> chỉ người nào đó rất già hoặc thứ gì đó rất cũ kỹ, cổ xưa. (Nghĩa đen: già như những quả đồi).</p><p class="Normal"> 	Ví dụ: "This is nothing new. The situation is as old as the hills". (Chả có gì mới. Tình trạng vẫn xưa như trái đất).</p><p class="Normal"> 	"While he may be as old as the hills he had not forgotten his manners". (Có thể ông ta rất già nhưng ông ta vẫn không quên những nề nếp của mình).</p><p class="Normal"> 	7. <em>Troublemaker </em>chỉ kẻ luôn gây rắc rối, ngược nghĩa với <em>classclown </em>- người luôn khiến người khác vui vẻ.</p><p class="Normal"> 	Ví dụ: "I was worried that I would be regarded as a troublemaker if I complained about the safety standards". (Tôi lo sẽ bị coi là kẻ phiền hà nếu kêu ca về các tiêu chuẩn an toàn).</p><p class="Normal"> 	8. <em>Nutty as a fruitcake </em>là một người khá điên rồ hoặc cư xử kỳ quặc.</p><p class="Normal"> 	Ví dụ: "This girl is as nutty as a fruicake". (Cô gái này khá quái đản). </p>', 1, '15001942621277805616.jpg', NULL),
(117, 'Từ vựng hay về chủ đề gia đình', '"Obey", "Care for", "Confide in" là những từ vựng hay về chủ đề gia đình, nhưng không phải ai cũng biết.', '<p class="Normal"> 	<strong>Obey, Care for, Confide in</strong></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156674" data-component="true" data-component-type="video" data-component-value="156674" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);line-height:20px;"> </span></div> </div><p class="Normal"> 	<strong>Get my back</strong></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156676" data-component="true" data-component-type="video" data-component-value="156676" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:right;"> 		<strong> Thạch Anh</strong></p> </div>', 1, NULL, NULL),
(118, '5 mẹo hữu ích trong đời sống', 'Muốn xác định góc bao nhiêu độ, đo độ dài một vật, hay nhân trong phạm vi 10, bạn có thể dùng bàn tay.', '<p> 	<strong>1. Tính độ của một góc bằng bàn tay</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194263589070631.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	<span>Bạn hãy xòe các ngón tay căng hết cỡ, sau đó đặt bàn tay lên một mặt phẳng. Ngón út nằm trên cạnh đáy chỉ 0 độ. Góc giữa ngón cái và ngón út sẽ là 90 độ, góc giữa ngón út và từng ngón còn lại tương ứng là 30, 45 và 60 độ</span><span>.</span></p><p class="Normal"> 	<strong>2. Ước lượng độ dài</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194263805581081.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Nếu bạn cần ước lượng độ dài của một vật nhưng không có thước đo, có thể sử dụng các ngón trên một bàn tay. Theo đúng tỷ lệ của một người trung bình, khoảng cách giữa đầu ngón cái và ngón trỏ là khoảng 18 cm, khoảng cách giữa ngón cái và ngón út là khoảng 20 cm.</p><p class="Normal"> 	Tất nhiên phương pháp này không thể giúp bạn tính chính xác bởi mỗi người có kích cỡ bàn tay khác nhau. Tuy vậy, nó có thể hữu ích khi bạn cần đo một vật lớn khi chỉ có thước quá nhỏ. Trường hợp đó, ước lượng trước bằng khoảng cách ngón tay sẽ dễ dàng hơn.</p><p class="Normal"> 	<strong>3. Làm phép nhân bằng ngón tay</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942641711425099.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	<span>Thông thường, trẻ con nhớ phép nhân với các số nhỏ rất nhanh, nhưng lại gặp khó khăn với các số 6, 7, 8, 9. Để giúp giải quyết vấn đề này, bạn có thể dạy trẻ một mẹo đơn giản. Ngửa hai lòng bàn tay, đánh số cho mỗi ngón tay trên từng bàn tay từ 6 đến 10, bắt đầu từ ngón út. </span></p><p class="Normal"> 	Giả sử bạn muốn làm phép nhân 7 với 8, chạm ngón số 7 ở bàn tay trái với ngón số 8 ở bàn tay phải. Tổng số ngón tay ở phần dưới, bao gồm cả hai ngón chạm vào nhau là hàng chục (ở đây là 5). Đối với những ngón tay ở phần trên, bạn nhân hai giá trị trái phải với nhau, đó sẽ là hàng đơn vị (3 x 2 = 6).</p><p class="Normal"> 	Ta có đáp án 7 x 8 = 56.  </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194264573048825.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Để làm phép nhân với 9, bạn xòe bàn tay và đặt úp xuống bàn, đánh số lần lượt từ 1 đến 10 theo chiều từ trái qua phải. Bây giờ, khi muốn nhân số nào với 9, bạn gập ngón tương ứng. Những ngón tay đằng trước nó chỉ hàng chục, những ngón tay đằng sau nó chỉ hàng đơn vị. Ví dụ, để làm phép tính 7 x 9, bạn có 6 là hàng chục và 3 là đơn vị. Đáp án là 63.</p><p class="Normal"> 	<strong>4. Kiểm tra chất lượng pin</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942642064772785.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Để kiểm tra một viên pin còn dùng tốt không, hãy cầm nó dựng đứng cách bàn 1-2 cm, sau đó thả tay. Nếu viên pin nảy lên và đổ xuống, đó là pin cạn hoặc pin chết.</p><p class="Normal"> 	<strong>5. Tính số ngày trong tháng</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194264127959858.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Bạn hãy nắm tay lại và bắt đầu đếm tháng bằng các đốt khớp tay từ trái qua phải. Mỗi đốt khớp và phần nối giữa hai đốt là một tháng riêng biệt. Khi đếm hết tay trái, bạn lại đếm tiếp tay phải.</p><p class="Normal"> 	Tháng nằm trên đốt khớp tay sẽ là tháng có 31 ngày, những tháng còn lại có 30 ngày hoặc ít hơn.</p>', 1, '15001942631467994578.jpg', NULL),
(119, 'Người vợ tài danh của vua Tự Đức', 'Thông minh, hay chữ, bà Tiệp dư Nguyễn Thị Bích được vua Tự Đức giao dạy học cho các hoàng tử.', '<p class="Normal"> 	Theo bài vị tại văn bia, vua Tự Đức có 103 bà vợ được chia theo 9 bậc. Đa số bà vợ có thứ bậc cao là con quan đại thần, trong đó có một bà hàng Tiệp dư tên Nguyễn Thị Bích rất hay chữ.</p><p class="Normal"> 	Theo sách <em>Đại Nam liệt truyện</em>, bà Nguyễn Thị Bích tự Lang Hoàn sinh năm 1830 quê huyện An Phúc (Bình Thuận), là con gái thứ tư của Bố chính sứ hộ lý tổng đốc Thanh Hóa Nguyễn Nhược Sơn. Lúc bé, bà thông minh, có tiếng về văn học. Năm 1848, khi 19 tuổi, bà được tiến vào cung.</p><p class="Normal"> 	Trong một buổi ngâm vịnh, vua Tự Đức bảo bà làm bài thơ tảo mai. Bài thơ được vua khen hay, thưởng 20 đỉnh bạc, tuyển vào cung sung làm việc tại viện Thượng nghi. Năm thứ ba vào cung, bà được phong làm Tài nhân và hầu vua. Là người thông minh, bà được vua Tự Đức sủng ái, dạy cho làm thơ và trở thành nữ môn sinh của vua Tự Đức.</p><p class="Normal"> 	Năm thứ 13 thời vua Tự Đức, bà được phong làm mỹ nhân, sau đó tấn phong làm quý nhân, tiệp dư. Với tài trí thông minh, bà được vua Tự Đức sai dạy kinh điển và dạy tập nội đình cho các hoàng tử. Những hoàng tử con nuôi của vua Tự Đức bà dạy dỗ sau này trở thành vua là Đồng Khánh và Kiến Phúc.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942652047204512.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Lăng vua Tự Đức, nơi có thờ bài vị các bà vợ của vua. Ảnh: <em>Võ Thạnh.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Vào năm 1883, vua Tự Đức mất, vâng ý chỉ của hai cung là bà Đức Từ Dũ mẹ vua Tự Đức và bà Trang Ý vợ chính vua Tự Đức, bà đã thảo sắc dụ.</span></p><p class="Normal"> 	Trong sự kiện Kinh đô Huế thất thủ năm 1885, bà theo vua Hàm Nghi và 2 cung đi Quảng Trị. Nhưng sau khi ra Quảng Trị, thấy sức khỏe hai cung không tốt, khó nhọc bà đã theo trở lại Kinh thành Huế, sau đó lên Khiêm Lăng phụng thờ vua Tự Đức và chịu sự quản chế của người Pháp.</p><p class="Normal"> 	Theo cuốn <em>Chuyện nội cung các vua</em> của Nguyễn Đắc Xuân, bà Nguyễn Thị Bích đã ở bên cạnh vua Tự Đức và chứng kiến những sự kiện xảy ra ở triều đình Huế từ sau ngày vua Tự Đức mất. Với sự hiểu biết của mình, bà đã viết tác phẩm <em>Loan Dư Hạnh Thục Quốc Âm Ca</em> (thường gọi tắt là <em>Hạnh Thục Ca</em>).</p><p class="Normal"> 	Với lời thơ trang nhã, âm điệu nhẹ nhàng, phảng phất nỗi buồn, <em>Hạnh Thục Ca </em>kể lại việc thực dân Pháp xâm lược Việt Nam, ca tụng đức độ của vua Tự Đức, nói về việc nhà vua nuôi nhiều con nuôi, đề <span>cập việc đại thần Nguyễn Văn Tường và Tôn Thất Thuyết phế lập các vua Dục Đức, Hiệp Hòa, Kiến Phúc, Hàm Nghi... </span></p><p class="Normal"> 	<span>Bên cạnh đó, <em>Hạnh Thục Ca </em>còn nói đến việc vua Đồng Khánh băng hà, vua Thành Thái lên ngôi. Đoạn sau cùng của <em>Hạnh Thục Ca</em> nói đến lễ bát tuần của bà Từ Dũ, mẹ vua Tự Đức.</span></p><p class="Normal"> 	Năm Thành Thái thứ 3 (1891), lần đầu tiên triều đình Huế tổ chức lễ tế đàn Nam Giao sau ngày Kinh đô Huế thất thủ (5/1885). Bà Tiệp dư Nguyễn Thị Bích đã làm bài thơ chữ Hán nói về sự kiện này, thể hiện sự mừng rỡ về vua Thành Thái trẻ tuổi nhưng có lòng với tổ tiên, biết nối lại quy củ, phục hồi lễ tế đàn Nam Giao với lễ nhạc đầy đủ mong đất nước thái bình.</p><p class="Normal"> 	Không chỉ làm thơ, bà Tiệp dư Nguyễn Thị Bích đã thảo ra nhiều chỉ dụ của triều đình Nguyễn theo ý của 2 cung.</p><p class="Normal"> 	<span>Trước những đóng góp to lớn đối với triều Nguyễn, vào năm 1892, bà được vua Thành Thái tấn phong lên chức Tam phi Lễ tân. Năm Duy Tân thứ 3 (1909), bà mất, hưởng thọ 80 tuổi</span><span>.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Võ Thạnh</strong></p>', 1, '1500194265747378232.jpg', NULL),
(120, 'Chàng trai Anh leo núi bằng giày cao gót nhằm xin học bổng', 'Conway chọn cách gây chú ý khi nộp đơn xin học bổng cho trường Truyền thông Nghệ thuật ở Brixton. ', '<p class="Normal"> 	Ở độ cao 1.345 m so với mực nước biển, Ben Nevis chắc chắn không phải là ngọn núi khó leo nhất thế giới. Tuy nhiên, mọi thứ trở nên đánh đố khi bạn phải đi một đôi giày cao gót 5 inch (gần 13 cm) thay vì đôi giày leo núi thông thường, đặc biệt khi bạn không phải là con gái. Một nam thanh niên người Anh đã chứng minh không có điều gì là không thể.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156862" data-component="true" data-component-type="video" data-component-value="156862" data-component-typevideo="2"></div></div> </div><p class="Normal"> 	Ben Conway, 19 tuổi ở London vừa thực hiện thử thách leo ngọn núi cao nhất nước Anh bằng giày cao gót, theo <em>Oddity Central</em> ngày 12/7. Conway chọn cách gây chú ý khi nộp đơn xin học bổng cho trường Truyền thông Nghệ thuật ở Brixton. Các ứng viên được yêu cầu làm điều gì đó đầy đam mê.</p><p class="Normal"> 	Conway bắt đầu chuyến leo núi khác thường vào 8h ngày 27/6 cùng một người bạn là Callum MacKenzie Allen, người giúp cậu quay lại video cho dự án ứng tuyển. Trải nghiệm không mấy êm ái kết thúc sau 5 tiếng.</p><p class="Normal"> 	Khi đang leo được nửa đường, dây đeo giày một bên đứt ra, do đó cậu phải dán giày vào chân để đảm bảo không bị nhấc gót. Thời tiết cũng không ủng hộ nam sinh với cơn mưa khá nặng hạt kéo dài suốt chặng đường, khiến cậu phải chịu đựng cơn gió có vận tốc khoảng 40-50 dặm một giờ.</p><p class="Normal"> 	Do điều kiện thời tiết không thuận lợi, Ben và bạn phải quay về khi đạt đến độ cao 900 m. Cậu vẫn đi giày cao gót khi leo xuống núi.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019426667458003.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ben Conway leo núi bằng giày cao gót còn với mục đích làm từ thiện. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Tôi chưa bao giờ leo núi, cũng như chưa từng đi giày cao gót trước đây. Đa số ủng hộ nhưng cũng có vài người nói rằng tôi nên nghĩ đến sức khỏe và an toàn", Conway nói.</p><p class="Normal"> 	Bên cạnh mục đích xin học bổng, việc leo núi bằng giày cao gót cũng giúp nam sinh 19 tuổi quyên tiền cho tổ chức từ thiện Stonwall về quyền LGBT và Sal''s Shoes - nơi chuyên cung cấp giày dép cho trẻ em đi chân đất trên toàn thế giới.</p><p class="Normal"> 	"Nếu tôi phải làm điều gì đó phi lý để mang hạnh phúc đến cho mọi người, điều đó xứng đáng", Conway chia sẻ.</p><br>', 1, '15001942661186979895.jpg', NULL),
(121, 'Bộ Giáo dục tư vấn thí sinh cách điều chỉnh nguyện vọng', 'Nếu đăng ký vào 3 nhóm trường có điểm chuẩn bằng, cao hoặc thấp hơn năm ngoái một chút, thí sinh không cần điều chỉnh nguyện vọng xét tuyển. ', '<p class="Normal"> 	Từ 8h ngày 15/7 đến 17h ngày 21/7, Cổng thông tin thi tuyển sinh của Bộ Giáo dục và Đào tạo sẽ mở để thí sinh đăng nhập vào điều chỉnh nguyện vọng đăng ký xét tuyến trực tuyến tại địa chỉ: http://thisinh.thithptquocgia.edu.vn. T<span>ừ 8h ngày 15/7 đến 17h ngày 23/7, thí sinh sẽ điều chỉnh nguyện vọng đăng ký xét tuyển bằng Phiếu điều chỉnh tại các điểm tiếp nhận.</span></p><p class="Normal"> 	<span>Bộ Giáo dục trả lời một số thắc mắc của thí sinh về việc điều chỉnh này.</span></p><p class="Normal"> 	<span>-<em> Khi đăng ký dự thi, e</em></span><em><span>m chọn trường theo 3 nhóm: nhóm có điểm chuẩn các năm phù hợp với năng lực, nhóm có điểm chuẩn cao hơn một chút và nhóm có điểm thấp hơn. Hiện điểm thi tốt hơn một chút so với dự kiến ban đầu, em có cần điều chỉnh đăng ký xét tuyển?</span></em></p><p class="Normal"> 	- Em đã thực hiện đúng hướng dẫn để có thể trúng tuyển vào trường, ngành phù hợp với năng lực và nguyện vọng của mình. Vấn đề còn lại là em so sánh phổ điểm của năm 2017 với phổ điểm của năm 2016 do Bộ Giáo dục công bố, nếu với mức điểm (theo tổ hợp xét tuyển) của em trở lên có số lượng thí sinh tương đồng (hoặc khác nhau không lớn) so với số học sinh năm 2016 có mức điểm đủ để trúng tuyển vào trường, ngành em đã chọn, em hoàn toàn có thể yên tâm và không phải thay đổi nguyện vọng.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194267470545067.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thí sinh tham gia kỳ thi THPT quốc gia 2017. Ảnh: <em>Xuân Ngọc.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<em>- Năm nay thí sinh được đăng ký không giới hạn số lượng nguyện vọng, vậy nguyên tắc xét tuyển giữa các nguyện vọng như thế nào?</em></p><p class="Normal"> 	- <span>Theo Quy chế tuyển sinh hiện hành, thứ tự ưu tiên của nguyện vọng chỉ có giá trị với chính thí sinh đó. Còn giữa các thí sinh, việc xét tuyển vào cùng ngành là bình đẳng, không phân biệt thứ tự ưu tiên nguyện vọng.</span></p><p class="Normal"> 	Trường hợp cụ thể, nếu bạn B đã trúng tuyển một trong các nguyện vọng từ 1 đến 3 sẽ không được xét tuyển nguyện vọng 4 nữa. Còn nếu cả ba nguyện vọng trên thí sinh B đều không trúng tuyển thì sẽ được xét tuyển vào ngành đăng ký nguyện vọng 4. Khi xét tuyển nguyện vọng này, thí sinh A (đăng ký nguyện vọng 1) và thí sinh B đều được xét tuyển bình đẳng như nhau, ai điểm cao hơn sẽ được lợi thế hơn.</p><p class="Normal"> 	<em>- Em đăng ký xét tuyển vào 3 trường. Trường hợp em đủ điểm trúng tuyển vào cả 3 trường, em có được lựa chọn vào học một trong 3 trường đó?</em></p><p class="Normal"> 	- Phương án xét tuyển năm 2017 cho phép thí sinh đăng ký không hạn chế số nguyện vọng, nhưng phải xếp sắp theo thứ tự ưu tiên từ trên xuống và trường hợp thí sinh đủ điểm để trúng tuyển vào nhiều nguyện vọng, cũng chỉ được quyền vào học ngành có nguyện vọng với thứ tự ưu tiên cao nhất. Trường hợp đã nêu, em chỉ trúng tuyển vào nguyện vọng 1. Chính vì vậy các em phải cân nhắc kỹ khi xác định thứ tự ưu tiên của các nguyện vọng khi đăng ký xét tuyển và khi điều chỉnh nguyện vọng xét tuyển.</p><p class="Normal"> 	<em>- Sau khi trường công bố kết quả xét tuyển, nếu em đã có tên trong danh sách trúng tuyển thì cần làm những thủ tục gì để được vào học?</em></p><p class="Normal"> 	- Theo lịch tuyển sinh, ngày 1/8 các trường sẽ công bố danh sách trúng tuyển. Sau đó, đến trước ngày 7/8 thí sinh phải nộp bản chính Giấy chứng nhận kết quả thi (trực tiếp tại trường hoặc qua đường bưu điện) để khẳng định việc nhập học tại trường đã trúng tuyển. Theo lịch nhập học của trường, thí sinh sẽ đến trường nộp các giấy tờ (học bạ, giấy chứng nhận ưu tiên…) để làm thủ tục nhập học.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<a href="http://vnexpress.net/infographics/tuyen-sinh/5-con-duong-vao-dai-hoc-o-viet-nam-3609195.html"><div style="text-align: center"><img src="/pictures/news/15001942681499856968.jpg" /></div></a></td> 		</tr><tr><td> 				<p class="Image"> 					Các con đường vào đại học ở Việt Nam. Đồ họa: <em>Tạ Lư - Quỳnh Trang.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<em>- Quy định xét tuyển các đợt bổ sung như thế nào?</em></p><p class="Normal"> 	- Các đợt xét tuyển bổ sung sẽ được thực hiện từ sau 13/8. Thời gian cụ thể của đợt bổ sung sẽ do các trường quy định. Trong xét tuyển đợt bổ sung thí sinh được phép đăng ký xét tuyển vào nhiều trường và không hạn chế số nguyện vọng. Tuy nhiên, thí sinh cần lưu ý ở mỗi trường chỉ phải nộp 1 Phiếu ĐKXT riêng và trong mỗi Phiếu ĐKXT các nguyện vọng được xếp theo thứ tự ưu tiên. Thí sinh nộp Phiếu ĐKXT trực tiếp tại trường hoặc qua đường bưu điện. Thí sinh xác nhận nhập học theo lịch do trường quy định. Thí sinh chưa đăng ký xét tuyển đợt 1 vẫn có thể tham gia đăng ký xét tuyển các đợt bổ sung.</p><p class="Normal"> 	<em>- Đăng ký xét tuyển bằng học bạ, em có thể tiếp tục đăng ký xét tuyển bằng kết quả thi THPT quốc gia hay không?</em></p><p class="Normal"> 	- Phương thức xét tuyển bằng học bạ (bằng kết quả học tập ở THPT) và xét tuyển bằng kết quả thi THPT quốc gia được thực hiện độc lập với nhau. Do vậy, em đồng thời có thể đăng ký xét tuyển bằng kết quả thi và xét tuyển bằng học bạ. Tuy nhiên, em cần lưu ý, nếu đã nhập học và nộp cho trường Giấy chứng nhận kết quả thi, sẽ không được tiếp tục tham gia xét tuyển.</p><table border="1" cellpadding="1" cellspacing="0"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Thí sinh truy cập vào <a href="http://diemthi.vnexpress.net/">diemthi.vnexpress.net</a>, nhập tên hoặc số báo danh và lựa chọn cụm thi để biết điểm, phổ điểm của 9 môn thi ở địa phương mình và trên toàn quốc. Phổ điểm của từng môn thi giúp các em biết vị trí của mình so với tổng số thí sinh dự thi, đo lường cơ hội trúng tuyển đại học, cao đẳng, từ đó quyết định có nên điều chỉnh nguyện vọng hay không.</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Xuân Hoa</strong></p>', 1, '15001942671862479732.jpg', NULL),
(122, 'Tuyển sinh thạc sĩ Quản trị kinh doanh tại Đại học kinh tế TP HCM', 'Chương trình xây dựng và phát triển dựa trên nhu cầu trở thành nhà quản lý, lãnh đạo doanh nghiệp của học viên với tính ứng dụng thực tiễn cao.', '<p> 	Chương trình nhận hồ sơ tuyển sinh khóa 8 đến hết ngày 10/8. Hình thức xét tuyển gồm 3 nội dung kiểm tra bằng tiếng Anh. Đó là đánh giá năng lực (theo hình thức GMAT); ngoại ngữ (tiếng Anh); và phỏng vấn.</p><p> 	Trong chương trình học, bên cạnh những môn nền tảng của bậc thạc sĩ, nội dung đào tạo của Thạc sĩ Quản trị kinh doanh (MBUS) còn chú trọng cập nhật kiến thức tiên tiến trong kinh doanh và kỹ năng cần thiết của nhà quản lý tương lai.</p><p> 	Cụ thể, chương trình hướng đến xây dựng kỹ năng nhận biết và xử lý vấn đề qua phương pháp giảng dạy và thảo luận bài tập tình huống thực tiễn. Từ đó, học viên được rèn luyện khả năng phân tích, phát triển kỹ năng giải quyết vấn đề, năng lực lãnh đạo, điều hành công việc tại doanh nghiệp.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194269274051633.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các tân thạc sĩ trong lễ tốt nghiệp năm 2016. Tìm hiểu thông tin chi tiết về tuyển sinh và tư vấn chương trình đào tạo<a href="http://docs.google.com/forms/d/e/1FAIpQLSeq1w_n-uGe8QRlBc3x1W3ve8xpzGB22I5b_NB6XREJW7c1Qg/viewform?c=0&w=1&usp=send_form"> tại đây</a>. </p> 			</td> 		</tr></tbody></table><p> 	Đội ngũ giảng viên trong chương trình có nhiều kinh nghiệm giảng dạy tại các trường đại học, viện nghiên cứu, trường kinh doanh trong và ngoài nước. Họ sẽ định hướng học viên phát triển toàn diện kiến thức chuyên môn lẫn kỹ năng giải quyết vấn đề.</p><p class="Normal" style="margin-top:12pt;margin-right:0in;margin-bottom:12pt;margin-left:0in;line-height:15pt;"> 	Đặc biệt, học viên sẽ có cơ hội mở rộng hơn nữa quan hệ công việc và xã hội thông qua các bạn học cùng lớp. Sau khi tốt nghiệp, học viên có nhiều cơ hội phát triển sự nghiệp, nắm giữ vị trí quan trọng tại các công ty trong và ngoài nước.</p><p class="Normal"> 	Chương trình MBUS thiết kế giảng dạy và học tập hoàn toàn bằng tiếng Anh, giúp học viên có cơ hội tích lũy vốn từ vựng, thuật ngữ chuyên ngành, nâng cao kỹ năng giao tiếp. Đồng thời, học viên còn thường xuyên thuyết trình bằng tiếng Anh thông qua hoạt động nghiên cứu, làm việc nhóm trong suốt thời gian học tập.</p><p> 	Để thuận tiện cho học viên, các lớp học sẽ sắp xếp vào các buổi chiều thứ Bảy, cả ngày Chủ Nhật; hoặc các tối trong tuần. Sau khi hoàn thành chương trình, học viên sẽ nhận bằng Thạc sĩ (chương trình đào tạo bằng tiếng Anh) do Trường Đại học Kinh tế TP HCM cấp.</p><p style="text-align:right;"> 	<strong>Thế Đan</strong></p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal"> 					Viện Đào tạo quốc tế (ISB) trực thuộc Trường Đại học Kinh tế TP HCM được thành lập với mục tiêu xây dựng môi trường giáo dục hiện đại, năng động, đạt tiêu chuẩn quốc tế. Các chương trình đào tạo của ISB liên tục cập nhật nhằm đáp ứng yêu cầu của nhà tuyển dụng và những thách thức của môi trường làm việc thực tiễn.</p> 				<p class="Normal"> 					Bên cạnh các chương trình chính quy đào tạo bằng tiếng Anh do UEH cấp bằng, sinh viên có thể theo học các chương trình liên kết và lấy bằng của các trường đại học đối tác. Đó là chương trình cử nhân kinh doanh và thạc sĩ quản trị kinh doanh của Trường Đại học Western Sydney (Australia), chương trình thạc sĩ quản trị kinh doanh cho các nhà lãnh đạo của Trường Đại học UQAM (Canada).</p> 				<p class="Normal"> 					Trường Đại học Kinh tế TP HCM - Viện đào tạo quốc tế (ISB). Đ<span>ịa chỉ: 17 Phạm Ngọc Thạch, quận 3, TP HCM. </span><span>Điện thoại: (08) 5446 5555 - Hotline: 0906 738 898. </span><span>Email: mbus8@isb.edu.vn,<a href="http://www.isb.edu.vn"> website</a></span></p> 			</td> 		</tr></tbody></table>', 1, '150019426844553583.png', NULL),
(123, 'Sinh viên tổ chức ''show diễn'' ngay sau môn học', 'Vận dụng kiến thức môn quản trị nhân sự, sinh viên Đại học Quốc tế Sài Gòn tự tay tổ chức dự án nghệ thuật trong 4 tuần.', '<p class="Normal"> 	<span>Học theo dự án "project-based learning" là phương pháp đang được áp dụng tại Đại học Quốc tế Sài Gòn (</span><span style="color:rgb(34,34,34);">SIU) </span><span>trong nhiều năm gần đây. </span><span>Theo yêu cầu của giảng viên </span><span>các môn, sinh viên triển khai dự án bằng cách lên kế hoạch thực hiện, quản lý, đánh giá, phân chia công việc phù hợp với từng thành viên. Mô hình này vừa giúp sinh viên tiếp thu được kiến thức học thuật, vừa khám phá năng lực và phát triển kỹ năng cá nhân.</span></p><p class="Normal"> 	Trong quá trình học môn quản trị nhân sự, các sinh viên cùng tổ chức dự án nghệ thuật với 4 tuần chuẩn bị. Điểm số được tính dựa trên mức độ thành công của dự án và nỗ lực cống hiến của mỗi cá nhân. Cụ thể, các thành viên dựa trên thế mạnh của mình đảm nhận vai trò nhất định trong dự án như vẽ kế hoạch, lên ý tưởng, trang trí sân khấu, tổ chức chương trình, MC, trình diễn, chụp ảnh, quay phim, truyền thông… </p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942701690978948.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sinh viên ngành ngôn ngữ Anh trong dự án “SIU prom night - Velvet”</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Dự án được đánh giá là chương trình nghệ thuật đặc sắc mang đậm dấu ấn của sinh viên. Hồng Vi - ngành quản trị kinh doanh cho rằng: "Cách học này giúp mình thoải mái bộc lộ khả năng. Bạn có thể thuộc làu mớ lý thuyết rồi nhanh chóng quên, còn những việc từng làm sẽ là kinh nghiệm đồng hành trong tương lai, dù thất bại hay thành công".</p><p class="Normal"> 	Nhận xét về hình thức học theo dự án, Thạc sĩ Nguyễn Văn Mỹ - Giảng viên SIU cho biết: "Ngoài những giờ lý thuyết cơ bản, tôi tin rằng sinh viên cần cơ hội để thể hiện mình qua những dự án thực tế. Thông qua chương trình, tôi muốn các em khám phá ra tố chất của người khác và nhiều kỹ năng cần thiết như tổ chức sự kiện, lãnh đạo, giao tiếp, thuyết trình trước đám đông… Đó là những yếu tố quan trọng liên quan đến công tác nhân sự và nghề nghiệp trong tương lai".</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194270386922616.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các dự án học thuật do sinh viên lên ý tưởng và thực hiện.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Đại diệ</span><span>n SIU cho biết, nhà trường luôn khuyến khích các em trải nghiệm thực tế thông qua các hoạt động đa dạng ở câu lạc bộ, dự án học thuật, dự án kết nối cộng đồng, nghiên cứu khoa học, cuộc thi tranh tài trong và ngoài nước...</span></p><p class="Normal"> 	Với môi trường học tập năng động và hiện đại, sinh viên SIU là trung tâm lớp học, giảng viên Việt Nam và nước ngoài chỉ giữ vai trò tư vấn và hướng dẫn. Những kỹ năng làm việc nhóm, lập kế hoạch và triển khai dự án góp phần giúp sinh viên tự tin đối mặt, giải quyết vấn đề và thành công sau khi ra trường.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942701240552811.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dự án học thuật “Future” do sinh viên ngành quản trị kinh doanh thực hiện.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>An San</strong></p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="title_news"> 			<p class="Normal"> 				SIU là thành viên của Hiệp hội quốc tế các trường đại học đào tạo ngành kinh doanh (IACBE); Hiệp hội phát triển giảng dạy doanh thương bậc đại học (AACSB); có quan hệ hợp tác với nhiều trường nước ngoài.</p> 			<p class="Normal"> 				SIU đào tạo bậc cử nhân và thạc sĩ, giảng dạy bằng tiếng Anh và Việt theo chuẩn Mỹ. Chương trình học chú trọng vào kỹ năng ứng dụng thực tiễn với các chuyên ngành: quản trị kinh doanh, quản trị nhà hàng - khách sạn, thương mại quốc tế, quản trị du lịch, kinh tế đối ngoại, marketing, khoa học máy tính, kỹ thuật phần mềm và ngôn ngữ Anh.</p> 		</div> 	</div> </div>', 1, '15001942701089785096.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(124, 'Trường công an xét tuyển hồ sơ từ 15,5 điểm', 'Thí sinh phải đạt sơ tuyển, đảm bảo ngưỡng sàn chung 15,5 điểm, trong đó các môn ở tổ hợp xét tuyển không dưới 5 điểm.', '<p class="Normal"> 	Ngày 14/7, thượng úy Nguyễn Mạnh Tuấn, Thư ký Hội đồng tuyển sinh Tổng cục Chính trị Công an nhân dân (Bộ Công an) thông tin về ngưỡng đảm bảo chất lượng đầu vào của các trường khối công an. T<span>hí sinh chỉ được xét tuyển khi đáp ứng được 3 yêu cầu: đạt sơ tuyển; đạt ngưỡng sàn 15,5 điểm của Bộ Giáo dục và Đào tạo; trong đó điểm số các môn thuộc tổ hợp xét tuyển từ 5 trở lên.</span></p><p class="Normal"> 	Riêng ngành Ngôn ngữ Anh của Học viện An ninh nhân dân, điểm tiếng Anh phải đạt từ 7 (chưa nhân hệ số, đối với thang điểm 10).</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style=""><tbody><tr><td style="text-align: center;"> 				<a href="http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/diem-san-xet-tuyen-cua-cac-dai-hoc-3612476.html"><strong>Điểm sàn xét tuyển của các đại học khác</strong></a></td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942721315912219.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sinh viên Học viện Cảnh sát nhân dân tập điều lệnh đầu năm học. Ảnh minh họa: <em>Giang Huy.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Về việc điều chỉnh nguyện vọng đăng ký xét tuyển, Bộ Công an cho phép thí sinh có nhiều lựa chọn</span><span>. </span></p><p class="Normal"> 	<em><span>Lựa chọn 1, điều chỉnh tổ hợp để đăng ký xét tuyển. </span></em><span>Thí sinh </span><span>đạt sơ tuyển, đã </span><span>nộp hồ sơ xét tuyển về trường </span><span>Công an nhân dân (CAND) </span><span>có thể điều chỉnh tổ hợp để </span><span>đăng ký xét tuyển</span><span> trong một trường. Ví dụ, lúc đầu thí sinh đăng ký tổ hợp A1 của Học viện An ninh nhân dân, giờ có thể chuyển sang tổ hợp D01 của trường này.</span></p><p class="Normal"> 	<span><em>Lựa chọn 2, điều chỉnh ngành học trong cùng một trường. </em></span><span>Ví dụ, thí sinh đã đăng ký xét tuyển tổ hợp A1 ngành An toàn thông tin của Học viện An ninh nhân dân, có thể chuyển sang đăng ký xét tuyển bằng tổ hợp A1 hoặc D1, C3 ngành Nghiệp vụ An ninh của Học viện.</span></p><p class="Normal"> 	<em>Lựa chọn 3, điều chỉnh giữa các trường trong khối</em> theo phân vùng tuyển sinh phía Bắc và phía Nam. <span>Ví dụ, lúc đầu thí sinh đăng ký xét tuyển tổ hợp A1 ngành Nghiệp vụ An ninh của Học viện An ninh nhân dân. Sau khi có điểm, trong thời gian điều chỉnh, thí sinh có thể chuyển nguyện vọng đăng ký xét tuyển sang tổ hợp khác, ngành khác tại Học viện Cảnh sát nhân nhân, Đại học Kỹ thuật Hậu cần Công an nhân dân, Học viện Chính trị Công an nhân dân, hoặc Đại học Phòng cháy chữa cháy.</span></p><p class="Normal"> 	<em>Lựa chọn 4, đ</em><em>iều chỉnh thứ tự ưu tiên của các nguyện vọng. </em>Thí sinh đã đạt sơ tuyển vào các trường CAND, mong muốn vào đào tạo theo chỉ tiêu của Bộ Công an nhưng chưa đăng ký xét tuyển vào trường trên Cổng thông tin hoặc thứ tự ưu tiên của nguyện vọng là từ 2 trở đi, nếu muốn xét tuyển vào trường CAND thì phải điều chỉnh nguyện vọng đó là thứ nhất. <span>T</span><span>heo quy định, thí sinh có nguyện vọng vào các trường quân đội, công an sẽ phải đăng ký ở nguyện vọng 1. </span></p><p class="Normal"> 	<em>Lựa chọn 5, điều chỉnh ra trường ngoài.</em> Thí sinh <span>đã nộp hồ sơ xét tuyển về trường CAND nhưng nay không muốn xét tuyển vào trường trong khối nữa, có thể điều chỉnh nguyện vọng ra các trường ngoài ngành. Thí sinh chỉ cần điều chỉnh trường ngoài ngành thành nguyện vọng 1. </span></p><p class="Normal"> 	<span>Bộ Công an có mẫu Phiếu đỉnh chỉnh nguyện vọng xét tuyển riêng (<a title="xem chi tiết" href="http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/truong-cong-an-xet-tuyen-ho-so-tu-15-5-diem-3613385-p2.html">xem chi tiết</a>). Thí sinh muốn thay đổi sẽ khai và nộp phiếu tại đơn vị sơ tuyển từ ngày 7/7 đến 18/7. </span></p><p class="Normal"> 	<span>Đối với chiến sĩ nghĩa vụ, nếu chỉ thi một tổ hợp môn thì chỉ được điều chỉnh ngành xét tuyển hoặc trường xét tuyển.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Quỳnh Trang</strong></p>', 1, '1500194271837587466.jpg', NULL),
(125, 'Cô gái 19 tuổi sắp trở thành bác sĩ trẻ nhất Malaysia', 'Tốt nghiệp vào năm tới, Chan Hao Shan sẽ là bác sĩ trẻ nhất Malaysia. ', '<p class="Normal"> 	Theo<em> Sin Chew Daily</em> ngày 9/7, Chan Hao Shan đang là sinh viên năm cuối ở Đại học Taylor, sẽ lấy bằng Cử nhân Y học và Cử nhân Phẫu thuật khi kết thúc chương trình.</p><p class="Normal"> 	Cô bắt đầu quan tâm đến y học khi mẹ ruột, một kế toán, bị chẩn đoán ung thư lúc cô lên 8. Từ nhỏ, Chan thể hiện sở thích tìm hiểu cơ thể người và cách thức hoạt động của các cơ quan trong cơ thể. "Tôi thực sự muốn biết những gì xảy ra với cổ họng khi nuốt thứ gì đó", cô nói. Ngoài ra, cô cũng đam mê đọc sách. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194274236277053.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chan Hao Shan từng thấy chương trình học ở lớp quá chậm nên muốn nhảy lớp. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Chỉ ba năm sau khi tham dự một trường tiểu học ở Kuala Lumpur, cô được phát hiện có khả năng học tập trội hơn hẳn bạn đồng trang lứa, do đó được bố mẹ chuyển sang học một trường quốc tế. <span>Chan hoàn thành toàn bộ chương trình trung học cơ sở chỉ trong một năm. </span></p><p class="Normal"> 	<span>"Tôi luôn thích thử thách bản thân. Tôi đã nghĩ tại sao không nhảy lên trình độ cao hơn, bởi bình thường tôi chán tốc độ học tập quá chậm ở lớp", Chan giải thích. </span></p><p class="Normal"> 	<span>Chan tham gia chương trình Australian Matriculation Programme, một chương trình dự bị đại học được quốc tế công nhận ở tuổi 14 và hoàn thành trong một năm. Cô là sinh viên trẻ nhất trong khóa tốt nghiệp năm đó. </span></p><p class="Normal"> 	Cô gái 19 tuổi dự định nghiên cứu sâu về lĩnh vực của mình sau khi lấy bằng cử nhân, nhưng vẫn chưa quyết định chuyên môn cụ thể. </p>', 1, '1500194274876776311.jpg', NULL),
(126, 'Việt Nam đoạt 3 huy chương vàng Olympic Hóa học quốc tế', '4 thí sinh Việt Nam tham dự cuộc thi Olympic Hóa học quốc tế 2017 đều đoạt giải, trong đó có 3 huy chương vàng, một bạc. ', '<p class="Normal"> 	Ngày 14/7, Cục Quản lý chất lượng (Bộ Giáo dục và Đào tạo) cho biết đã có kết quả của đoàn học sinh Việt Nam dự thi Olympic Hoá học quốc tế 2017 tại Thái Lan. Cả 4 thí sinh đoạt giải, gồm 3 huy chương vàng, một huy chương bạc. </p><p class="Normal"> 	<span>Đây là kết quả cao nhất của đội tuyển Việt Nam tham dự Olympic Hóa học quốc tế từ trước đến nay.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194275452429177.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					4 học sinh Việt Nam dự thi Olympic Hóa học 2017.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cụ thể, em Đinh Quang Hiếu (lớp 12, THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội) đạt huy chương vàng với 92,13/100 điểm, đứng thứ 9 trên tổng số 297 thí sinh tham dự. Em <span>Nguyễn Bằng Thanh Lâm (lớp 12, THPT chuyên Hà Nội - Amsterdam) được 89,67/100 điểm, xếp thứ 20, đạt huy chương vàng.</span></p><p class="Normal"> 	Em Phạm Đức Anh (lớp 11, THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội) đạt huy chương vàng với 89,46/100 điểm, đứng thứ 21. <span>Huy chương bạc thuộc về em Hoàng Nghĩa Tuyến (lớp 12 THPT chuyên Phan Bội Châu, tỉnh Nghệ An) với 85,91/100 điểm.</span></p><p class="Normal"> 	<span>Olympic Hoá học quốc tế năm 2017 có sự tham gia của 76 quốc gia và vùng lãnh thổ với 297 thí sinh. Theo bảng xếp hạng không chính thức, đội tuyển Mỹ xếp thứ nhất với 4 huy chương vàng; Việt Nam và Trung Quốc xếp thứ hai với 3 huy chương vàng, một huy chương bạc. </span></p><p class="Normal"> 	Năm 2016, Việt Nam giành hai huy chương vàng và một bạc Olympic Hóa quốc tế. Trong đó em <a href="http://vnexpress.net/tin-tuc/giao-duc/chang-trai-vang-hoa-hoc-tung-khoc-khi-khong-lam-xong-bai-3449138.html">Đinh Quang Hiếu</a> (<span>THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội) là một trong hai thí sinh giành giải vàng.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Quỳnh Trang</strong></p>', 1, '1500194275495330760.jpg', NULL),
(127, 'Chàng thủ khoa khối A của tỉnh Nam Định', 'Nhà nghèo, suốt 12 năm phổ thông Hiện không đi học thêm môn nào, chỉ tập trung nghe giảng trên lớp và mượn sách về tự học.', '<p class="Normal"> 	<span>Lưu Văn Hiện (trường THPT A Hải Hậu) là thủ khoa khối A của tỉnh Nam Định với tổng điểm 29,3, trong đó Hóa 10; Toán 9,8, và Lý 9,5. </span></p><p class="Normal"> 	<span>“Nhà khó khăn, thương bố mẹ nên em phải lo học, không dám lơi là”, Hiện cười hiền lành giải thích. Nhà </span><span>Hiện thuộc hộ cận nghèo, bố đau yếu triền miên, mẹ là lao động chính làm ruộng nuôi cả gia đình. Để phụ giúp gia đình, Hiện</span><span> làm đủ thứ việc, từ phụ hồ, bốc vác, cày cấy.</span></p><p class="Normal"> 	<span>Kết thúc kỳ thi THPT quốc gia, Hiện tranh thủ làm thêm </span><span>kiếm tiền vào đại học. “Nhà cậu em có xe tải, thời gian này em cùng cậu đi bốc vác hàng chở thuê. Mỗi ngày em nhận được tiền công từ 150.000 đến 200.000 đồng”, Hiện chia sẻ.</span></p><table align="right" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194276591877503.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Lưu Văn Hiện sắp trở thành sinh viên Đại học Bách khoa Hà Nội. Ảnh: <em>Kim Chi.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Không đi học thêm bất cứ môn nào suốt thời phổ thông, bí quyết học giỏi của Hiện </span><span>là tập trung nghe giảng ở trường, về nhà ôn tập lại ngay. Chỗ nào chưa hiểu em thảo luận với bạn bè hay hỏi thầy cô. </span></p><p class="Normal"> 	<span>Với môn Hóa, em luyện thi bằng cách giải bài tập thật nhiều. Có nhiều sách tham khảo hay, không có tiền mua, cô cho em mượn hoặc mượn bạn đi photo về học. Gặp n</span><span>hững bài hay, lạ, Hiện chép ra quyển vở riêng để lưu ý. </span></p><p class="Normal"> 	<span>Nhắc đến học trò, cô Lê Thị Diệp, giáo viên chủ nhiệm lớp 11 và 12, đánh giá Hiện thông minh, rất có tư chất. Trên lớp, dường như em nuốt từng lời dạy của thầy cô, việc tiếp thu kiến thức vì thế r</span><span>ất hiệu quả. Để tạo điều kiện cho em, nhà trường đã hỗ trợ các suất học bổng từ Quỹ khuyến học.</span></p><p class="Normal"> 	“Khi lựa chọn trường để đăng ký nguyện vọng, Hiện chọn Đại học Bách khoa Hà Nội. Tôi và gia đình khuyên em nên chọn Học viện Quân y hoặc trường quân đội nào đó để đỡ khoản học phí sau này, nhưng em quyết tâm chọn Bách khoa”, cô Diệp kể.</p><p class="Normal"> 	Giải thích quyết tâm theo đuổi Bách khoa, Hiện cho biết từ <span>lớp 11 đã tìm hiểu thông tin, thích tự động hóa và công nghệ thông tin. "Từ nhỏ em hay phải làm công việc chân tay nên ước gì sau này những công việc ấy có máy móc làm thay. Vì thế ngành tự động hóa là ưu tiên số 1 của em", chàng thủ khoa nói.</span></p><p class="Normal"> 	Chia sẻ về dự định học tập, chàng trai quê Hải Hậu cho hay sẽ kiếm học bổng để ăn học trong thời gian đầu. Lâu dài em sẽ đi làm thêm ở gần trường, gần nơi trọ. Công việc mà Hiện nghĩ có thể làm được ở Hà Nội là gia sư.</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				<p class="Normal"> 					Biết hoàn cảnh của Lưu Văn Hiện, Đại học Bách khoa Hà Nội quyết định trao tặng một năm học bổng toàn phần cho em, các năm tiếp theo trường sẽ căn cứ kết quả học tập để xem xét tiếp. Đây cũng là năm đầu tiên trường thực hiện chính sách học bổng và hỗ trợ tài chính mới cho sinh viên.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Cẩm Lệ</strong></p>', 1, '15001942761911106569.jpg', NULL),
(128, 'Sự khác biệt giữa ''triumph'' và ''victory''', 'Cả "triumph" và "victory" đều có nghĩa chung là chiến thắng, tuy nhiên vẫn tồn tại khác biệt.', '<p class="Normal"> 	Như nhiều trường hợp khác trong tiếng Anh, <em>triumph</em> và <em>victory</em> là hai từ gây bối rối cho người sử dụng bởi không biết chắc nên chọn từ nào trong hoàn cảnh cụ thể. Thực tế, cả hai vẫn thường được sử dụng thay thế cho nhau. Tuy nhiên, mỗi từ có điểm riêng biệt.</p><p class="Normal"> 	<em>Victory</em> chỉ một chiến thắng khi có đối thủ cạnh tranh trực tiếp, chẳng hạn trong một trận chiến (battle) hay cuộc thi (contest). Hàm ý ở đây là chiến thắng của ai đó liên quan đến sự thất bại của một người khác. <em>Victory</em> ban đầu chỉ được dùng trong bối cảnh chiến tranh để mô tả các trận chiến, tuy nhiên nó dần được sử dụng phổ biến hơn.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942771041428538.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					"Triumph" thường được sử dụng linh hoạt hơn "victory". </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Trong khi đó, khi bạn sử dụng "triumph", khái niệm thất bại có thể bị lu mờ hoặc không tồn tại. Ví dụ: <em>The new garden is a great triumph.</em> (Khu vườn mới là một thành tựu lớn). Ở đây, người nói muốn chỉ ra rằng khu vườn là thành công của người làm vườn, không hề chỉ ra thất bại của ai hay bất cứ điều gì. Do đó, <em>triumph </em>có cách sử dụng đa dạng hơn <em>victory</em>.</p><p class="Normal"> 	Ngoài ra, <em>victory </em>thường ngầm nhắc đến đối thủ (opponent/rival). <em>Triumph</em>, ngược lại, có thể biểu đạt chiến thắng khi chống lại một người, cũng có thể là một tình huống, hoàn cảnh... Ví dụ: <em>One can triumph over hardship</em>. (Ai cũng có thể chiến thắng khó khăn). Trong câu này, người ta vượt qua hoàn cảnh, không có đối thủ nào đóng vai trò trong chiến thắng mà người nói nhắc đến.</p><p class="Normal"> 	Một số ví dụ khác:</p><p class="Normal"> 	- <em>She triumphed over her classmates.</em> ("Classmates" ở đây là đối thủ, nhưng không nhấn mạnh về sự thua cuộc).</p><p class="Normal"> 	- <em>This wedding cake is a triumph. </em>("Wedding cake" là thành quả, người làm bánh không phải chiến đấu để giành chiến thắng, tương tự "garden" trong ví dụ ở trên).</p>', 1, '15001942771852507122.jpg', NULL),
(129, 'Việt Nam thuộc top 5 nước dẫn đầu về du học sinh tại Australia', 'Việt Nam đứng thứ 4 trong danh sách với gần 20.000 sinh viên du học tại xứ sở kangaroo.', '<p class="Normal"> 	<span>Theo </span><span>một báo cáo </span><span>của Bộ Giáo dục và Đào tạo Australia, t</span><span>ới tháng 3/2017, Việt Nam có 19.700 du học sinh tại nước này, chiếm 4,1% trong tổng số sinh viên quốc tế. Con số này cho thấy sự gia tăng 8,7% so với cùng kỳ năm 2016.</span></p><p class="Normal"> 	<span>Trong quý I/2017, số sinh viên quốc tế tới </span><span>Australia </span><span>tăng 15%, đạt 480.090. Trung Quốc vẫn là quốc gia dẫn đầu trong việc gửi sinh viên tới </span><span>Australia </span><span>để học tập, tiếp theo là Ấn Độ, Malaysia, Việt Nam và Nepal.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942781173018053.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Số lượng du học sinh ở Australia tăng trưởng so với năm 2016. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Một khảo sát của chính phủ <span>Australia </span>vào năm 2016 cho thấy bằng cấp được đánh giá cao và chất lượng của các trường đại học là hai yếu tố hấp dẫn chính đối với sinh viên quốc tế. </p><p class="Normal"> 	<span>“Tôi rất hài lòng khi thấy phụ huynh Việt Nam tiếp tục lựa chọn </span><span>Australia </span><span>là điểm đến để con mình học tập. </span><span>Các du học sinh Việt Nam có thể tận hưởng hệ thống giáo dục hàng đầu thế giới, cũng như phong cách sống vượt trội mà </span><span>Australia </span><span>mang lại</span><span>”, đại biện Lâm thời Australia, ông Layton Pike phát biểu. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942781630499169.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Du học sinh Việt Nam chiếm hơn 4% tổng số sinh viên quốc tế tại Australia. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Australia </span>dẫn đầu trong việc cấp học bổng cho sinh viên Việt Nam. Từ những năm 1970 đến nay, hơn 5.800 công dân Việt Nam đã được nhận học bổng từ Chính phủ <span>Australia</span>. Bên cạnh đó, có hơn 50.000 cựu du học sinh tự túc. </p><p class="Normal" style="text-align:right;"> 	<strong>Xuân Hoa</strong></p>', 1, '1500194278945461199.png', NULL),
(130, 'Điểm nhận hồ sơ của 14 đại học Y Dược', 'Đại học Y Dược TP HCM lấy điểm xét tuyển là 19; Y Hà Nội, Y Dược Thái Nguyên lấy từ 15,5 điểm. ', '<p class="Normal"> 	Chiều 14/7, <strong>Đại học Y Dược TP HCM</strong> thông báo điểm sàn xét tuyển các ngành của trường năm 2017. Các ngành Y đa khoa, Răng - Hàm - Mặt và Dược lấy 23 điểm, các ngành còn lại lấy 19 điểm.</p><p class="Normal"> 	Theo đề án tuyển sinh hệ chính quy của Đại học Y dược TP HCM năm nay, trường tuyển sinh trên cả nước với tổng chỉ tiêu 1.760 - tăng 160 so với năm ngoái. Có 5 ngành giữ nguyên chỉ tiêu gồm: Y đa khoa 400, Răng hàm mặt 100, Y học cổ truyền 150, Điều dưỡng đa khoa 150, Điều dưỡng chuyên ngành hộ sinh 80. Các ngành còn lại tăng 10-20 chỉ tiêu, trong đó Dược học tăng 60.</p><p class="Normal"> 	Mùa tuyển sinh năm ngoái, ngành Y đa khoa có điểm chuẩn cao nhất - 26,75; ngành Răng - Hàm - Mặt 26; ngành Dược học 25,25. Ngành Y tế công cộng có điểm chuẩn thấp nhất là 20,5.</p><p class="Normal"> 	<strong>Đại học Y Dược Cần Thơ</strong>, các ngành Y đa khoa, Y học dự phòng, Y học cổ truyền, Răng - Hàm - Mặt, Dược học xét tuyển từ 20,5 điểm, các ngành còn lại từ 19.</p><p class="Normal"> 	Năm nay, trường có 1.300 chỉ tiêu, trong đó 85% dành cho thí sinh ở các tỉnh đồng bằng sông Cửu Long, còn lại cho thí sinh ở Đông Nam Bộ và Nam Trung Bộ.</p><p class="Normal"> 	<strong>Khoa Y (Đại học Quốc gia TP HCM)</strong> thông báo ngưỡng đảm bảo chất lượng đầu vào cho tất cả ngành là 15,5 điểm. Năm nay, khoa Y tuyển sinh toàn quốc với 100 chỉ tiêu ngành Y đa khoa, 75 chỉ tiêu ngành Dược.</p><p class="Normal"> 	Điều kiện chung là thí sinh tốt nghiệp THPT tính tới thời điểm xét tuyển, điểm trung bình cộng 3 năm THPT từ 6,5 trở lên.</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style=""><tbody><tr><td style="text-align: center;"> 				<strong><a href="http://vnexpress.net/tin-tuc/giao-duc/tuyen-sinh/diem-san-xet-tuyen-cua-cac-dai-hoc-3612476.html">Điểm sàn xét tuyển của các đại học</a></strong></td> 		</tr></tbody></table><p class="Normal"> 	<span><strong>Học viện Y Dược cổ truyền Việt Nam</strong> lấy 15,5 điểm xét tuyển, chỉ tiêu tuyển sinh là 700. Năm 2016, điểm trúng tuyển vào trường là 23 - 24,25.</span></p><p class="Normal"> 	<span>Ngưỡng nhận hồ sơ xét tuyển của <strong>Đại học Điều dưỡng Nam Định</strong> là 15,5 điểm cho tất cả các tổ hợp xét tuyển (B, D7, D8). Điểm chuẩn của trường năm 2016 là 22 - 24,75.</span></p><p class="Normal"> 	Đại diện <strong>Đại học Y Hà Nội</strong> cho biết, mùa tuyển sinh 2017 trường sử dụng ngưỡng đảm bảo chất lượng của Bộ Giáo dục và Đào tạo để xét tuyển. Với 1.100 chỉ tiêu của 10 mã ngành, trong đó Y đa khoa chiếm 500 chỉ tiêu, t<span>rường nhận tất cả hồ sơ có điểm thi THPT quốc gia 3 môn khối B từ 15,5 trở lên. </span></p><p class="Normal"> 	Năm 2016, điểm trúng tuyển vào các ngành của Đại học Y Hà Nội từ 23 trở lên. Ngành Y đa khoa lấy điểm chuẩn cao nhất là 27.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194280736959504.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sinh viên Y Hà Nội thực tập trong bệnh viện. Ảnh minh họa: <em>Giang Huy.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<strong>Đại học Y Dược - Đại học Thái Nguyên</strong><span> cũng lấy điểm nhận hồ sơ bằng ngưỡng sàn của Bộ Giáo dục là 15,5. Năm trước, điểm trúng tuyển vào 5 mã ngành của trường là 22-27,5.</span></p><p class="Normal"> 	<strong>Đại học Y Dược Hải Phòng</strong> sáng 14/7 cũng thông báo ngưỡng điểm nhận hồ sơ. Theo đó, ngành Y đa khoa, Dược, Răng hàm mặt có mức sàn cao nhất 22,5; ngành Y học cổ truyền nhận hồ sơ từ 20 điểm trở lên. Các ngành Y học dự phòng, Xét nghiệm y học, Điều dưỡng điểm xét tuyển là 18. </p><p class="Normal"> 	Năm 2017, Đại học Y dược Hải Phòng tuyển 1.000 chỉ tiêu cho 8 mã ngành. Riêng ngành Dược học có sử dụng tổ hợp các môn thi khối A (Toán - Lý - Hóa) để xét tuyển, các ngành còn lại đều dùng khối B. </p><p class="Normal"> 	Điểm trúng tuyển vào trường năm 2016 là 22,75-25.</p><p class="Normal"> 	<strong>Đại học Y Dược - Đại học Huế</strong><span> có ngưỡng nhận hồ sơ thấp nhất là 18 điểm ở ngành Y tế công cộng. Ngành Y đa khoa, Dược học, Răng hàm mặt có mức sàn cao nhất là 24. Các ngành còn lại gồm Y tế dự phòng; Y học cổ truyền; Điều dưỡng; Xét nghiệm y học; Kỹ thuật hình ảnh Y học, điểm sàn là 21. </span></p><p class="Normal"> 	Trường năm nay tuyển 1.350 chỉ tiêu, trừ ngành Dược tuyển khối A, các ngành còn lại dùng tổ hợp các môn khối B để xét tuyển. </p><p class="Normal"> 	Điểm trúng tuyển vào trường năm 2016 thấp nhất là 21 ngành Y tế công cộng, cao nhất là 26 ngành Y đa khoa. Các ngành còn lại có điểm chuẩn từ 22,5 đến 25,75.</p><p class="Normal"> 	<strong>Khoa Y Dược - Đại học Quốc gia Hà Nội</strong> có ngưỡng nhận hồ sơ n<span>gành Dược học (khối A) và Y đa khoa (khối B) là 21 điểm. </span><span>Ngành Răng hàm mặt chương trình chất lượng cao lấy từ 18 điểm (khối B và tiếng Anh điều kiện). Năm 2017, khoa này tuyển 190 chỉ tiêu.</span></p><p class="Normal"> 	<strong>Đại học Y khoa Phạm Ngọc Thạch</strong> nhận hồ sơ đăng ký xét tuyển từ 21 điểm trở lên với các ngành Y đa khoa, Răng hàm mặt, Dược. Các ngành còn lại từ 17 điểm. Chỉ tiêu tuyển sinh của trường năm nay là 1.250. </p><p class="Normal"> 	Ngưỡng điểm sàn của <strong>Đại học Dược Hà Nội</strong> là 22 với tổ hợp các môn khối A (Toán - Lý - Hóa). Trường có 600 chỉ tiêu tuyển sinh trong năm 2017.</p><p class="Normal"> 	Năm 2016, điểm trúng tuyển vào trường là 26,75. </p><p class="Normal"> 	<span><strong>Đại học kỹ thuật Y tế Hải Dương</strong> lấy ngưỡng xét tuyển đầu vào ngành Y đa khoa là 22 điểm, các ngành còn lại là 18. Năm nay trường tuyển 620 chỉ tiêu ở tổ hợp khối B. Riêng ngành Kỹ thuật hình ảnh y học không tuyển nữ. </span></p><p class="Normal"> 	Điểm trúng tuyển đợt một năm 2016 của trường là 24,5 với Y đa khoa; ngành Xét nghiệm y học là 22,5; Kỹ thuật hình ảnh y học và Điều dưỡng là 22; Phục hồi chức năng là 21.</p><p class="Normal"> 	<strong>Đại học Kỹ thuật Y - Dược Đà Nẵng</strong><span> lấy ngưỡng đảm bảo đầu vào xét tuyển là 15,5 điểm cho tất cả các ngành.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Quỳnh Trang - Mạnh Tùng</strong></p>', 1, '15001942791751042280.jpg', NULL),
(131, 'Thần đồng 15 tuổi tốt nghiệp đại học hạng ưu ở Anh', 'Yasha Asley vừa tốt nghiệp Đại học Leicester (Anh) với bằng hạng ưu ở chuyên ngành Toán học, khi mới 15 tuổi. ', '<p class="Normal"> 	<span>Theo <em>ITV</em> ngày 13/7, Yasha Asley vào đại học năm 12 tuổi. Sắp tới, cậu bé tiếp tục học lên tiến sĩ ở Khoa Toán học. "Ngay từ hồi tiểu học, tôi đã muốn vào đại học sớm. Tôi yêu mọi thứ về toán học và muốn tiếp tục nghiên cứu", cậu nói. Mơ ước của thần đồng 15 tuổi là trở thành nhà nghiên cứu toán học</span>. </p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156587" data-component="true" data-component-type="video" data-component-value="156587" data-component-typevideo="2"></div></div> </div><p class="Normal"> 	<span>Giáo sư Jeremy Levesley rất hạnh phúc khi được dạy cậu sinh viên trẻ tuổi trong ba năm. Ông nhận xét Yasha hòa đồng với các sinh viên và nếu như không biết tuổi từ trước sẽ không nhận ra. </span></p><p class="Normal"> 	"Rõ ràng đây là một trong những sinh viên tốt nhất chúng tôi từng có, do vậy tôi rất vui khi Yasha quyết định ở lại để học lên tiến sĩ. Tôi nghĩ Yasha là chàng trai rất tuyệt và sẽ tiếp tục làm những thứ thực sự thú vị", ông nói. </p><p class="Normal"> 	<span>Bố Yasha, ông Moussa Asley đi cùng con trai đến lễ tốt nghiệp, cho biết rất tự hào về cậu bé và mong chờ ngày này từ rất lâu. Ông tin Yasha sẽ làm tốt nhiệm vụ của mình. </span></p>', 1, NULL, NULL),
(132, '5 phép tính que diêm đòi hỏi sự nhanh trí', 'Với mỗi câu đố, bạn có thể tìm ra càng nhiều đáp án càng tốt để biến phép tính sai thành đúng. ', '<p> 	<strong>Câu 1: </strong>Hãy di chuyển hai que diêm để tạo thành phép tính đúng. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942821668331418.png" /></div></td> 		</tr></tbody></table><p> 	>><a title="Xem đáp án" href="http://vnexpress.net/tin-tuc/giao-duc/5-phep-tinh-que-diem-doi-hoi-su-nhanh-tri-3612945-p2.html">Xem đáp án</a></p><p> 	<strong>Câu 2:</strong> Hãy di chuyển chỉ một que diêm để tạo thành phép tính đúng. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194282864919362.png" /></div></td> 		</tr></tbody></table><p> 	>><a title="Xem đáp án" href="http://vnexpress.net/tin-tuc/giao-duc/5-phep-tinh-que-diem-doi-hoi-su-nhanh-tri-3612945-p3.html">Xem đáp án</a></p><p> 	<strong>Câu 3: </strong>Hãy di chuyển chỉ một que diêm để tạo thành phép tính đúng. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194282803442578.png" /></div></td> 		</tr></tbody></table><p> 	>><a title="Xem đáp án" href="http://vnexpress.net/tin-tuc/giao-duc/5-phep-tinh-que-diem-doi-hoi-su-nhanh-tri-3612945-p4.html">Xem đáp án</a></p><p> 	<strong>Câu 4:</strong> Hãy di chuyển chỉ một que diêm để tạo thành phép tính đúng. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942821568052282.png" /></div></td> 		</tr></tbody></table><p> 	>><a title="Xem đáp án" href="http://vnexpress.net/tin-tuc/giao-duc/5-phep-tinh-que-diem-doi-hoi-su-nhanh-tri-3612945-p5.html">Xem đáp án</a></p><p> 	<strong>Câu 5:</strong> Hãy di chuyển chỉ một que diêm để tạo thành phép tính đúng. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942822051899258.png" /></div></td> 		</tr></tbody></table><p> 	>><a title="Xem đáp án" href="http://vnexpress.net/tin-tuc/giao-duc/5-phep-tinh-que-diem-doi-hoi-su-nhanh-tri-3612945-p6.html">Xem đáp án</a></p>', 1, '150019428256064346.png', NULL),
(133, 'Cặp song sinh chào đời từ mẹ chết não thu hút 13 triệu lượt xem video', 'Video thai phụ chết não nhưng thai nhi vẫn được bác sĩ cưu mang trong bụng mẹ đến khi chào đời, thu hút 13 triệu lượt xem, theo Ladbible. ', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-157006" data-component="true" data-component-type="video" data-component-value="157006" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(134, 'Các bài tập cho cơ bụng khỏe phái mạnh thường bỏ qua', 'Động tác tay không có thể tập tại nhà giúp cơ bụng chắc khỏe và sức bền cơ thể phái mạnh, theo Men''s Health.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-155841" data-component="true" data-component-type="video" data-component-value="155841" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(135, 'Tại sao đàn ông thích ''yêu'' vào buổi sáng?', 'Đầu ngày lượng hormone testosterone cơ thể ở mức cao nhất nên đàn ông mong đợi sex nhiều hơn thời điểm khác, theo Fortafy.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-155685" data-component="true" data-component-type="video" data-component-value="155685" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(136, '''Bắt'' bệnh qua vị trí đau ở bụng', 'Đau ở vị trí trên bên phải bụng, có thể bạn bị viêm gan, giun chui ống mật, viêm bể thận phải...', '<p class="Normal"> 	Đau bụng có thể bình thường không nguy hiểm, nhưng cũng là dấu hiệu cảnh báo bạn cần chú ý đến sức khỏe hơn. Việc xác định vị trí đau có thể giúp bác sĩ phát hiện nguyên nhân.</p><p class="Normal"> 	<span>Bụng gồm có 2 vùng chính là thượng vị (trên rốn) và hạ vị (dưới rốn). Trong ổ bụng gồm có các tạng cơ bản như dạ dày - tá tràng, gan, lách, tuỵ tạng, hệ thống mật (đường dẫn mật và túi mật), ruột (đại tràng, ruột non, mạc treo, trực tràng, hậu môn), hệ tiết niệu (thận, niệu quản, bàng quang). Với phụ nữ còn có tử cung, buồng trứng, vòi trứng, âm đạo.</span></p><p class="Normal"> 	Các cơn đau bụng có thể do ăn quá no hoặc ăn quá nhiều một số loại thực phẩm (như thực phẩm giàu chất béo, thực phẩm sinh hơi, hoặc người không dung nạp lactose ăn nhiều sữa). Các cơn đau này thường khỏi trong vài giờ hoặc kéo dài vài ngày. Đây là hiện tượng bình thường không nguy hiểm, nhưng cũng là dấu hiệu cảnh báo bạn cần chú ý đến sức khỏe hơn.</p><p class="Normal"> 	Trong một số trường hợp, đau có thể là dấu hiệu cảnh báo sớm của rối loạn nặng hơn. Dưới đây là một số vị trí đau ở ổ bụng để có thể nhận biết các bệnh:</p><p class="Normal"> 	<span>- Bụng trên bên phải: Thường do viêm gan, giun chui ống mật, viêm màng phổi bên phải, viêm bể thận của thận bên phải.</span></p><p class="Normal"> 	- Bụng trên bên trái: Phần nhiều do viêm tuyến tụy cấp, lách to, viêm màng phổi dưới bên trái, viêm bể thận của thận bên trái.</p><p class="Normal"> 	- Chính giữa bụng trên: Thường thấy trong bệnh viêm dạ dày cấp tính, viêm loét đường tiêu hóa, viêm màng tim, bị suyễn nặng.</p><p class="Normal"> 	- Bụng dưới bên phải: Có thể do lao ruột, bệnh do ký sinh trùng a-míp, viêm ruột thừa cấp tính.</p><p class="Normal"> 	- Bụng dưới bên trái: Do bệnh lỵ trực khuẩn, bí đại tiện.</p><p class="Normal"> 	- Xung quanh rốn: Thường do giun đũa, viêm ruột, dị ứng thức ăn, viêm ruột non do xuất huyết cấp tính, tắc ruột, lồng ruột, thủng ruột...</p><p class="Normal"> 	- Bụng vùng thắt lưng: Có thể gặp trong trường hợp viêm bể thận, sỏi thận.</p><p class="Normal"> 	Biểu hiện đau bụng có thể gặp trong nhiều bệnh, vì thế khi bị đau bụng chưa rõ nguyên nhân thì tạm thời không cho người bệnh ăn uống. Người bệnh nên đi khám tại các cơ sở y tế, không tự ý dùng thuốc hay dùng thuốc theo sự mách bảo của người không phải là thầy thuốc.</p><p class="Normal" style="text-align:right;"> 	<strong>Bác sĩ Vũ Cao</strong><br><em>Bệnh viện Bạch Mai</em></p>', 1, NULL, NULL),
(137, 'Đàn ông bị bệnh gút nên ăn gì?', 'Người bệnh nên ăn các loại ngũ cốc, trứng, sữa, rau quả... ; hạn chế thịt, cá, hải sản, óc; tuyệt đối không uống rượu, bia, cafe. ', '<p class="Normal"> 	<span>Bệnh gút thường xảy ra với nam giới trung niên (tuổi 40-50) chiếm 95%. Những người có nguy cơ cao là béo phì, nghiện rượu, cà phê, người có tiền sử gia đình bị bệnh gút. </span><span>Gút là một dạng của viêm khớp, thường có những đợt kịch phát, tái phát nhiều lần. B</span><span>ên cạnh dùng thuốc, chế độ ăn cũng có vai trò rất quan trọng trong điều trị cơn gút cấp tính, mạn tính và có tác dụng làm giảm các cơn cấp.</span></p><p class="Normal"> 	<span>Theo bác sĩ Nguyễn Văn Tiến, Trung tâm Giáo dục Truyền thông dinh dưỡng - Viện Dinh dưỡng Quốc gia, người bệnh gút nên sử dụng thức ăn ít chứa nhân purin như ngũ cốc, các loại hạt, bơ, mỡ, đường, trứng, sữa, phomat, rau quả; hạn chế thức ăn chứa nhiều axit uric như thịt, cá, hải sản, gia cầm, óc, gan, bầu dục, đậu đỗ. Tuyệt đối không uống rượu, bia, cà phê, chè vì nó làm giảm khả năng bài xuất axit uric qua thận hậu quả là làm tăng lactat máu.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942861283048366.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thực phẩm tốt cho người bị gút. Ảnh: <em>Lifescript. </em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Bên cạnh đó cần duy trì cân nặng hợp lý, nếu người thừa cân và béo phì không nên giảm cân quá nhanh. Tăng cường đào thải axit uric qua thận bằng cách uống nhiều nước, không ăn thức ăn có vị chua (quả chua, dưa chua, cà muối..).</span></p><p class="Normal"> 	<strong>Các thực phẩm không nên ăn</strong>: Thực phẩm có nhiều purin:</p><p class="Normal"> 	- Không uống rượu, bia, cà phê, chè.</p><p class="Normal"> 	- Không ăn uống thực phẩm (rau quả) có vị chua vì làm tăng axit máu.</p><p class="Normal"> 	- Không ăn phủ tạng động vật, nước luộc thịt, nước sườn, cá hộp, thịt hộp.</p><p class="Normal"> 	- Không ăn chế phẩm có cacao, chocolate.</p><p class="Normal"> 	<strong>Các thực phẩm ăn với số lượng vừa phải</strong> (ăn hạn chế): Thịt, cá các loại, hải sản, gia cầm, đậu đỗ.</p><p class="Normal"> 	<strong>Các thực phẩm nên ăn:</strong></p><p class="Normal"> 	- Uống đủ nước: 2 - 2,5 lít một ngày, nên uống nước khoáng, nước rau.</p><p class="Normal"> 	- Sữa, rau xanh, quả chín, ngũ cốc (gạo, ngô, khoai,…) có thể sử dụng với tỷ lệ nhiều hơn bình thường một chút.</p><p class="Normal"> 	- Giảm lượng đạm trong khẩu phần: Tổng lượng thịt hoặc cá… đạm động vật, đậu đỗ khoảng 150 g một ngày.</p><p class="Normal"> 	- Nên ăn đa dạng các loại thực phẩm để bổ sung các chất dinh dưỡng cần thiết.</p><p class="Normal"> 	Cách tính các thực phẩm tương đương như sau:</p><p class="Normal"> 	Lượng đạm trong 100 g thịt = 180 g đậu phụ = 70 g lạc hạt = 100 g cá = 100 g tôm.</p>', 1, '1500194286870900581.jpg', NULL),
(138, 'Ca mổ thay đổi cuộc đời chàng trai bị rối loạn đông máu', 'Bệnh nhân bệnh rối loạn đông máu Hemophilia, được bác sĩ Bệnh viện Quận Thủ Đức thay khớp gối để có thể đi lại. ', '<p class="Normal"> 	Chàng trai 23 tuổi quê Thái Bình điều trị bệnh <span>Hemophilia A tại Bệnh viện Quận Thủ Đức (TP HCM) từ cuối năm 2016. Bệnh nhân thường phải nhập viện với các biến chứng xuất huyết tiêu hóa, chảy máu răng, xuất huyết khớp gối. </span></p><p class="Normal"> 	<span>Gần đây bệnh nhân bị </span><span>thoái hóa khớp gối nặng, đứt dây chằng bên trong và giãn dây chằng bên ngoài gối phải, </span><span>cần </span><span>phẫu thuật để thay khớp gối, tái tạo dây chằng. Tuy nhiên người</span><span> đang điều trị Hemophilia A suốt 23 năm qua như bệnh nhân thì ca mổ sẽ đối diện nhiều nguy cơ. </span><span>Bệnh nhân phải chịu đau đớn, không đi lại được, điều trị ở nhiều nơi nhưng bác sĩ đều ngại can thiệp bởi nguy cơ chảy máu trong và sau phẫu thuật rất cao.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194287223273901.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các bác sĩ thực hiện phẫu thuật. Ảnh:<em> B.N</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Bác sĩ Lê Hoàng Văn Hải, Trưởng khoa Chấn thương Chỉnh hình, Bệnh viện Quận Thủ Đức cho biết lần này </span><span>bệnh nhân quyết tâm mổ. Các bác sĩ đã kết hợp nhiều chuyên khoa để lên các phương án cho cuộc phẫu thuật. Đối với bệnh nhân </span><span>Hemophilia A thể nặng, việc chuẩn bị càng phải chu đáo để kiểm soát tất cả các tình huống có thể xảy ra, đặc biệt là đảm bảo đầy đủ các yếu tố đông máu.</span></p><p class="Normal"> 	<span>Sau 3 giờ phẫu thuật "</span><span style="color:rgb(34,34,34);">trong sự hồi hộp của toàn bộ nhân viên y tế bệnh viện" ngày 12/7, bệnh nhân đã </span><span>được thay thành công khớp gối nhân tạo kết hợp tái tạo dây chằng gối phải. Hiện bệnh nhân ổn định, được theo dõi hậu phẫu chặt chẽ, </span><span>chưa phát hiện chảy máu vết mổ và đang tập vật lý trị liệu để phục hồi.</span></p><p class="Normal"> 	<span>B</span>ệnh viện đang tiếp nhận và điều trị gần 100 bệnh nhân Hemophilia. Đây là bệnh máu khó đông, t<span>hường gây tụ máu khớp, k</span><span>hối máu tụ trong cơ và dưới da, chảy máu khó cầm ở nhiều vị trí... Bệnh có thể gây b</span><span>iến dạng khớp, teo cơ do chảy máu nhiều lần. Nếu điều trị không đúng, không kịp thời tỷ lệ tàn phế cao. </span><span>Đây là bệnh nhân </span><span>Hemophilia </span><span>đầu tiên được phẫu thuật thay khớp gối và tái tạo dây chằng tại viện. </span></p>', 1, '1500194287374960677.jpg', NULL),
(139, 'Cẩn trọng khi dùng thực phẩm chức năng cho trẻ nhỏ', 'Cần có chỉ định của bác sĩ khi dùng thực phẩm chức năng cho trẻ; bé đầy đủ dinh dưỡng, không bệnh tật thì không cần thiết sử dụng.', '<p class="Normal"> 	Nếu search trên mạng cụm từ thực phẩm chức năng cho trẻ sẽ có hàng triệu kết quả khác nhau với đủ các loại vitamin, canxi, thực phẩm bổ sung… Có hàng sản xuất trong nước, nhập khẩu, xách tay, giá cả 200.000-500.000 đồng, loại đắt có thể lên đến tiền triệu. Trong đó, nhiều loại được quảng cáo giúp trẻ ăn ngon, giảm tái phát viêm đường hô hấp…</p><p class="Normal"> 	Theo phó giáo sư, tiến sĩ Nguyễn Tiến Dũng, nguyên trưởng khoa Nhi, Bệnh viện Bạch Mai (Hà Nội), các bà mẹ đang bị lạc giữa ma trận các loại thực phẩm chức năng. Thực phẩm chức năng được phát minh ra ở Nhật nhưng nơi này cũng chỉ cho phép đăng ký lưu hành khoảng 100 loại, còn tại Việt Nam nhiều vô kể.</p><p class="Normal"> 	<span>Tiến sĩ Dũng cho rằng cần hiểu thực phẩm chức năng là thực phẩm có bổ sung một số chất như các vitamin A, D, dầu cá, tỏi, sâm… với lượng nhất định, để giúp cơ thể phát triển tốt hơn hoặc phòng chống một số bệnh như vitamin D phòng còi xương.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194287649012311.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	<span>Theo ông Dũng, để sử dụng thực phẩm chức năng tốt không phải dễ. </span><span>Thứ nhất về giá cả, chúng thường đắt hơn thuốc. Tại sao phải bỏ ra một số tiền lớn cho thực phẩm chức năng trong khi có thể dùng để mua thức ăn, sữa cho trẻ; khi trẻ có bệnh thì uống thuốc là chính. Hậu quả là mất một khoản tiền lớn, còn thực sự tác dụng và chất lượng như thế nào thì không thể biết được.</span></p><p class="Normal"> 	Vì thế để dùng thực phẩm chức năng cho trẻ cần có chỉ định của bác sĩ. Những trẻ có bệnh mãn tính như ung thư, bệnh chữa dài ngày hoặc thiếu dinh dưỡng trầm trọng, ăn bằng đường tiêu hóa không đủ, hoặc sống trong hoàn cảnh đặc biệt thì nên bổ sung thực phẩm chức năng. Với những bé đầy đủ dinh dưỡng, không bệnh tật thì không cần thiết, ông Dũng khuyên.</p><p class="Normal"> 	Cha mẹ cũng cần biết cho con dùng loại thực phẩm nào và nên tham khảo ý kiến của thầy thuốc để biết con đang thiếu chất gì, có bệnh gì, nên dùng loại thực phẩm chức năng nào đáp ứng với tình trạng bệnh lý của trẻ. <span>Một số thực phẩm chức năng giống như thuốc có thể khiến cha mẹ dễ nhầm lẫn. Điều này sẽ rất tai hại nếu như cha mẹ cứ nghĩ uống thực phẩm chức năng là đủ mà quên thuốc.</span></p><p class="Normal"> 	Theo Phó giáo sư Dũng, ví dụ vitamin C<span> được chỉ định dùng trong trường hợp bệnh nhiễm trùng, ăn uống thiếu kém, suy dinh dưỡng nặng, thiếu máu (giúp tăng cường hấp thu sắt), ung thư, người bị độc mãn như ngộ độc chì. Nếu uống bổ sung C hằng ngày suốt thời gian dài có thể dẫn đến thừa vitamin C, làm tăng hấp thu canxi từ ruột, canxi trong máu cao gây sỏi thận.</span></p><p class="Normal"> 	Canxi cũng rất tốt cho cơ thể nhưng nếu thừa sẽ <span>gây mệt mỏi, chán ăn, sỏi thận, tăng huyết áp..., xương cốt hóa sớm trẻ có thể bị thấp chiều cao.</span></p><p class="Normal"> 	<span>"Thực phẩm chức năng không hoàn toàn thay thế được thực phẩm thông thường, nằm ở ranh giới giữa thực phẩm và thuốc. </span><span>Tác dụng của nó có nhưng không nhiều, vì thế cha mẹ không nên lạm dụng</span><span>", phó giáo sư Dũng nói.</span></p>', 1, '15001942871327601949.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(140, 'Thực đơn tuần dành cho trẻ béo phì', 'Cho bé ăn sáng với phở gà ít bánh phở, uống sữa dành cho trẻ thừa cân, buổi trưa ăn ít cơm với thịt heo bí đỏ và chả cá.', '<p class="Normal"> 	Bác sĩ Trần Thị Minh Nguyệt gợi ý thực đơn tuần ít calo dành cho trẻ béo phì như sau:</p><table border="1" cellpadding="0" cellspacing="0"><tbody><tr><td style="background-color: rgb(255, 204, 255);"> 				<strong>Ngày</strong></td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Sáng</strong><br> 				(6h30 - 7h30)</td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Phụ sáng</strong><br> 				(9h)</td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Trưa</strong><br> 				(11h - 11h30)</td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Phụ xế</strong><br> 				(14h - 14h30)</td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Chiều</strong><br> 				(17h- 17h30)</td> 			<td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Tối </strong><br> 				(20h - 20h30)</td> 		</tr><tr><td style="background-color: rgb(255, 204, 255); text-align: center;"> 				<strong>Thứ hai</strong></td> 			<td> 				<p> 					Phở gà 1 tô: Bánh phở 70 g, thịt gà 30 g, rau giá...</p> 				<p> 					Đu đủ 1 miếng vừa.</p> 			</td> 			<td> 				<p> 					Sữa:</p> 				<p> 					220 ml sữa dành cho trẻ thừa cân</p> 			</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh bí đỏ (thịt heo 5 g, bí đỏ 80 g).</p> 				<p> 					Chả cá chiên: Chả cá 65 g, dầu 9 g.</p> 				<p> 					Dưa hấu 1 miếng 150 g.</p> 			</td> 			<td> 				Bánh flan 1 cái nhỏ</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh cải bó xôi: Thịt heo 10 g, Cải bó xôi 50 g.</p> 				<p> 					Thịt kho đậu hũ: Đậu hũ 50 g, thịt 25 g, dầu 5 g, bún tàu, nấm mèo...</p> 				<p> 					Chuối xiêm 1 trái vừa.</p> 			</td> 			<td> 				<p> 					Sữa:</p> 				<p> 					220 ml sữa cho trẻ thừa cân</p> 			</td> 		</tr><tr><td style="background-color: rgb(255, 204, 255); text-align: center;"> 				<strong>Thứ ba</strong></td> 			<td> 				<p> 					Bánh giò 1 cái vừa:<br> 					Bột gạo 30 g, thịt heo 30 g, trứng cút 1 cái, dầu 4 g.</p> 				<p> 					Nước cam vắt 1 ly 100 ml.</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh chua cá hồi: Cá 20 g, thơm và cà chua 50 g, dầu 5 g.</p> 				<p> 					Thịt kho trứng: Thịt 40 g, trứng 1 trái nhỏ.</p> 				<p> 					Lê nửa trái vừa.</p> 			</td> 			<td> 				Yaourt<br> 				1 hũ 100 ml</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh rau ngót thịt: Thịt heo 5 g, rau ngót 20 g.</p> 				<p> 					Mực xào thập cẩm: mực 50 g, thơm, cà, dưa leo 50 g, dầu 3g.</p> 				<p> 					Cocktail trái cây 1 ly nhỏ.</p> 			</td> 			<td> 				Sữa</td> 		</tr><tr><td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Thứ tư</strong></td> 			<td> 				<p> 					Hủ tiếu 1 tô nhỏ:  Sợi hủ tiếu 60 g, thịt heo đùi 20 g, rau giá...</p> 				<p> 					Xoài nửa trái vừa.</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh bầu nấu thịt:<br> 					Bầu 50 g, thịt nạc 5 g.</p> 				<p> 					Xíu mại: Thịt heo 25 g, thịt nạc dăm 60 g, củ sắn 34 g.</p> 				<p> 					Sa bô chê 1 trái vừa.</p> 			</td> 			<td> 				Đậu hũ nước đường 1 chén nhỏ</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh cải dún thịt: Thịt 5 g, cải dún 50 g.</p> 				<p> 					Thịt gà sốt cà: Thịt gà 30 g, trứng gà 1 trái nhỏ, cà chua 30 g, mè trắng 2 g, bột 5 g, dầu 3 g.</p> 				<p> 					Thanh long 1 miếng 100 g.</p> 			</td> 			<td> 				Sữa</td> 		</tr><tr><td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Thứ năm</strong></td> 			<td> 				<p> 					Bánh há cảo<br> 					1 dĩa 6 cái vừa.</p> 				<p> 					Bưởi 2 múi.</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Bún măng vịt tô nhỏ: Bún 90 g, thịt vịt 35 g, huyết 10 g, măng khô 3 g, rau giá hành ngò...</p> 				<p> 					Chè đậu xanh phổ tai nửa ly: Đậu xanh 20 g, phổ tai 13 g, đường 22 g, dừa 13 g.</p> 			</td> 			<td> 				Bánh su kem<br> 				1 cái vừa</td> 			<td> 				<p> 					Cơm 1 chén vừa<br> 					Canh bắp cải thịt: Thịt 10 g, bắp cải 50 g.</p> 				<p> 					Bò xào đậu cô ve: Thịt bò 25 g, đậu cô ve 50 g, dầu 3 g.</p> 				<p> 					Chôm chôm 4 trái vừa.</p> 			</td> 			<td> 				Sữa</td> 		</tr><tr><td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Thứ sáu</strong></td> 			<td> 				Cháo huyết 1 tô nhỏ: Gạo 90 g, huyết 70 g, bánh quẩy 15 g, hành ngò rau giá... 				<p> 					Vú sữa nửa trái vừa.</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh cua đồng rau dền mồng tơi: Cua đồng 50 g, rau dền mồng tơi 50 g.</p> 				<p> 					Tôm kho tàu: Tôm 50 g, củ hành tây 30 g, dầu 6 g.</p> 				<p> 					Trái hồng 2 trái vừa.</p> 			</td> 			<td> 				Bánh khoai mì nướng 1 miếng 60 g.</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh bí đao thịt: Thịt 5 g, bí đao 50 g.</p> 				<p> 					Cá ngừ đút lò: Cá ngừ 50 g, phô mai 1 miếng, cà rốt 50 g, hành tây 30 g.</p> 				<p> 					Sinh tố nửa ly.</p> 			</td> 			<td> 				Sữa</td> 		</tr><tr><td style="text-align: center; background-color: rgb(255, 204, 255);"> 				<strong>Thứ bảy</strong></td> 			<td> 				<p> 					Bánh mì sandwich kẹp thịt: 1 lát bánh mì lớn, pate, chả lụa, xúc xích, dăm bông 30 g, dưa leo, dưa chua...</p> 				<p> 					Yaourt 1 hũ</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Nui nấu thịt: Nui khô 40 g, thịt heo nạc 20 g, su su, cà rốt, giá rau...</p> 				<p> 					Chè bắp 1 chén nhỏ</p> 			</td> 			<td> 				<br> 				Bánh flan 1 cái nhỏ</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh hẹ thịt đậu hũ:<br> 					Đậu hũ trắng 20 g, thịt heo 20 g, hẹ 30 g.</p> 				<p> 					Bò xào nấm rơm: Thịt bò 20 g, nấm rơm 40 g, dầu 2,5 g.</p> 				<p> 					Nho Mỹ 50 g.</p> 			</td> 			<td> 				Sữa</td> 		</tr><tr><td style="background-color: rgb(255, 204, 255); text-align: center;"> 				<strong>Chủ nhật</strong></td> 			<td> 				<p> 					Bánh canh cua: Bánh canh 80 g, thịt heo 20 g, thịt cua 20 g, nấm rơm 15 g…</p> 				<p> 					Nước ép ổi 1 ly nhỏ.</p> 			</td> 			<td> 				Sữa</td> 			<td> 				<p> 					Bún thịt nướng chả giò 1 tô: Bún 100 g, thịt 25 g, chả giò 17 g, đậu phộng 7 g, mỡ hành, nước mắm, rau giá...</p> 				<p> 					Sinh tố thơm nửa ly.</p> 			</td> 			<td> 				<br> 				Bánh da lợn 1 miếng 60 g</td> 			<td> 				<p> 					Cơm 1 chén vừa.</p> 				<p> 					Canh khoai mỡ: Thịt 5 g, khoai mỡ 40 g.</p> 				<p> 					Cá hú kho thơm: Cá 40 g, thơm 25 g.</p> 				<p> 					Cocktail trái cây nửa ly.</p> 			</td> 			<td> 				Sữa</td> 		</tr></tbody></table>', 1, NULL, NULL),
(141, 'Tại sao 18 tuổi đã bị đau lưng kinh niên?', 'Càng tập cháu càng đau nhiều hơn, lchữa đủ mọi cách vẫn đau nhức.', '<p class="Normal"> 	Năm nay cháu 18 tuổi, sức khỏe khá tốt, chơi nhiều môn thể thao nặng nhẹ đều có như bơi, bóng rổ, bóng đá, cầu lông, trước đây còn tập tạ. Về sau cháu thấy lưng đau nhiều hơn, thoa dầu nóng không bớt. Vậy đây là triệu chứng của bệnh gì? Cháu có tăng cân đột ngột trong thời gian rất ngắn, 15 ngày tăng hơn 10 kg, hiện tại giữ 72 kg. Một bữa cháu ăn trung bình 6 hoặc 7 bát cơm. Cảm ơn bác sĩ. (<em>jayrichard25</em>).</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194288871097249.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa:<em> Medical News Today.</em></p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	Trả lời:</p><p class="Normal"> 	Chào bạn,</p><p class="Normal"> 	Vận động thể dục thể thao rất tốt cho cơ thể nhưng quan trọng là phải khởi động trước khi tập và thư giãn sau tập nữa thì mới đạt hiệu quả cao. Khi vận động xong mà thấy đau nhức, cứng, ê ẩm thì việc sử dụng dầu nóng chỉ giúp cải thiện triệu chứng chứ không giải quyết được vấn đề.</p><p class="Normal"> 	Bạn nên chườm lạnh, bởi vì sau khi vận động hoặc chơi thể thao sẽ hình thành tổn thương nhỏ trên khớp, thường gây viêm. Trong viêm có các triệu chứng như sưng, nóng, đỏ, đau. Chườm lạnh là cách đơn giản và dễ dàng nhất để kiểm soát cơn viêm. Lưu ý không chườm nóng vì sẽ làm tăng viêm.</p><p class="Normal"> 	Tình trạng tăng cân một cách đột ngột trong khoảng thời gian ngắn là hệ quả của việc ăn quá nhiều và không khoa học. Bạn cũng đừng ăn quá nhiều một loại thực phẩm, thức ăn giàu carbohydrate xấu, ví dụ như cơm trắng, khoai tây, các loại bún mì, bánh mì… Nên tiết chế việc ăn uống đồng thời giữ cân bằng lượng chất đạm (protein) và chất béo không no.</p><p class="Normal"> 	Việc tập thể dục thể thao <a href="http://suckhoe.vnexpress.net/tin-tuc/khoe-dep/giam-can/gap-bung-sai-cach-khien-ban-khong-giam-mo-con-bi-dau-lung-3605948.html">không đúng tư thế</a> hoặc vượt quá mức chịu đựng của cơ thể, cộng với tình trạng tăng cân đột ngột cũng là một trong những nguyên nhân gây ra đau cơ xương khớp. Nếu các cơn đau thường xuyên xuất hiện và ngày càng năng hơn, bạn nên đến các bác sĩ chuyên khoa cơ xương khớp và chụp X-quang để biết rõ tình trạng của mình và được tư vấn chuyên sâu hơn.</p><p class="Normal"> 	Chúc bạn luôn vui khỏe.</p><p class="Normal"> 	Thân ái.</p><p class="Normal" style="text-align:right;"> 	Bác sĩ <strong>Randell DuPraw</strong><br><em><span>Trung tâm trị liệu thần kinh cột sống Maple Healthcare</span></em></p>', 1, '15001942881313058997.jpg', NULL),
(142, 'Bác sĩ ồ ạt ''nhảy việc'' sang bệnh viện tư', '48 y bác sĩ ở Đà Nẵng đã chuyển sang làm việc cho các bệnh viện tư trong khi viện công quá tải do bệnh nhân ngày càng tăng.', '<p class="Normal"> 	Báo cáo tại kỳ họp HĐND TP Đà Nẵng vừa qua, Giám đốc Sở Y tế Ngô Thị Kim Yến cho biết nhiều bệnh viện tư ra đời thu hút 48 cán bộ y tế, trong đó có 23 bác sĩ. <span>Theo bà Yến, việc y bác sĩ xin nghỉ việc để chuyển công tác đến các bệnh viện tư có chế độ đãi ngộ tốt hơn là quyền riêng. Tuy nhiên thực tế số người xin nghỉ việc ồ ạt khiến ngành y tế rất đau lòng.</span></p><p class="Normal"> 	Bà Yến nói, y tế tư nhân giúp cho ngành y rất nhiều trong giảm quá tải, đa dạng hóa dịch vụ ở bệnh viện công, có lợi cho người dân. Đây là điều không thể phủ nhận. <span>"Tuy nhiên đau lòng là bác sĩ giỏi sang bệnh viện tư trong tình trạng bệnh viện công đang quá tải. Người nghèo mất cơ hội được điều trị từ các bác sĩ giỏi ở viện công", bà Yến nói thêm.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194289406128160.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một ca điều trị bệnh nhân tại bệnh viện Đa khoa Đà Nẵng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Đà Nẵng hiện có hơn 1.000 bác sĩ, tỷ lệ giường bệnh là 67 trên 10.000 dân. Bên cạnh hệ thống y tế công lập thì hiện có 7 bệnh viện tư nhân, 5 bệnh viện thuộc bộ ngành và 20 phòng khám đa khoa, 600 phòng mạch tư. </span><span>Giám đốc Sở Y tế Đà Nẵng cho biết thành phố đang xảy ra tình trạng quá tải tại các bệnh viện vì không chỉ phục vụ cho người dân Đà Nẵng mà còn điều trị cho người dân miền Trung - Tây Nguyên.</span></p><p class="Normal"> 	<span>Trao đổi với <em>VnExpress.net</em> về tình trạng bác sĩ "nhảy việc" từ công sang tư, bác sĩ Nguyễn Sơn, Phó Giám đốc Bệnh viện Phụ sản - Nhi, nói: "</span><span>Thu nhập ở bệnh viện tư cao gấp nhiều lần, có bác sĩ được trả lương 100 triệu đồng mỗi tháng".</span></p><p class="Normal"> 	<span>Bệnh viện Phụ sản - Nhi có 3 bác sĩ đã nghỉ việc để sang một bệnh viện tư sắp khai trương. Trong đó có một bác sĩ trưởng Khoa Ngoại nhi, một bác sĩ trưởng Khoa chẩn đoán hình ảnh. Hai bác sĩ có trình độ thạc sĩ, một bác sĩ trình độ tiến sĩ. L</span><span>ãnh đạo bệnh viện đã động viên và "giữ chân" được 2 bác sĩ. B</span><span>ác sĩ Sơn nhận định, bệnh viện tư muốn thu hút y bác sĩ có tên tuổi, có kinh nghiệm để đủ hồ sơ trình Bộ Y tế cấp phép hoạt động.</span></p><p class="Normal"> 	<span>Tình trạng cán bộ, nhân viên "nhảy việc" ngoài Phụ sản - Nhi còn có các bệnh viện C, Đa khoa Đà Nẵng, Ung bướu.</span></p><p class="Normal"> 	Bác sĩ Sơn cho biết, việc nhân viên y tế nhận được những lời mời chào "nhảy việc" đã gây ảnh hưởng không nhỏ đến các bệnh viện. Người được mời thì tư tưởng xao động vì mức thu nhập quá hấp dẫn, trong khi đơn vị quản lý bức xúc vì "nuôi người thành tài thì nghỉ việc". <span>Mới đây, lãnh đạo một bệnh viện tư đến tiếp xúc với lãnh đạo bệnh viện công để xin hỗ trợ chuyên môn kỹ thuật khi đi vào hoạt động. Lãnh đạo các bệnh viện công đã lên tiếng phản đối về việc lôi kéo nhân lực.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942891708684715.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một ca bệnh nặng được các bác sĩ Bệnh viện Đà Nẵng cứu chữa kịp thời nhờ bỏ qua thủ tục hành chính. Ảnh: <em>Nguyễn Đông.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>"Các bác sĩ làm việc ở đâu cũng là phục vụ nhân dân. Nhưng bệnh viện công lâu nay nuôi họ ăn học, cầm tay chỉ việc, giờ bắt đầu sử dụng được thì họ lại đi, đó là điều đau lòng", bác sĩ Sơn chia sẻ. </span></p><p class="Normal"> 	<span>Năm 2017, Bệnh viện Phụ sản - Nhi thực hiện cơ chế tự chủ tài chính. Bác sĩ Sơn hy vọng cơ chế này sẽ tăng thêm thu nhập, giúp đội ngũ y bác sĩ ổn định kinh tế và yên tâm công tác. Bên cạnh đó, việc đào tạo nguồn nhân lực cũng được chú trọng để tạo tính kế thừa.</span></p><p class="Normal"> 	Bí thư Thành ủy Đà Nẵng Nguyễn Xuân Anh nhìn nhận việc dịch chuyển nhân lực đến những nơi có điều kiện làm việc tốt hơn, chế độ đãi ngộ cao hơn là một tất yếu. Bác sĩ có năng lực tìm nơi làm việc thu nhập tốt hơn không có gì là xấu.</p><p class="Normal"> 	<span>"Thành phố trong thời gian tới cần có cơ chế hỗ trợ cả về mặt vật chất cũng như cơ hội làm việc theo đúng sở trường, cơ hội thăng tiến để thu hút và giữ chân được đội ngũ y bác sĩ giỏi", Bí thư Thành ủy Đà Nẵng chia sẻ.</span></p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td style="background-color: rgb(204, 204, 204);"> 				Để giảm tải cho các bệnh viện, Đà Nẵng đang xây dựng thêm Trung tâm tim mạch quy mô 250 giường (hoạt động cuối năm 2017). Quý 4 khởi công xây dựng giai đoạn 2 Bệnh viện Phụ sản – Nhi với 750 giường. Chuẩn bị thành lập trung tâm điều trị ung thư chất lượng cao dự kiến 500 giường tại Bệnh viện Ung bướu...</td> 		</tr></tbody></table><p style="text-align:right;"> 	<strong>Nguyễn Đông</strong></p>', 1, '15001942891035934927.jpg', NULL),
(143, 'Lupus, bệnh nguy hiểm tấn công phụ nữ trẻ', 'Bệnh lupus ban đỏ hệ thống gây nhiều biến chứng, chưa có thuốc chữa khỏi hoàn toàn, thường gặp ở nữ 20-30 tuổi.', '<p class="Normal"> 	Tiến sĩ Nguyễn Bách, Trưởng Khoa Thận Lọc máu, Bệnh viện Thống Nhất TP HCM cho biết lupus ban đỏ hệ thống là bệnh lý của mô liên kết, <span>tự miễn, có nghĩa cơ thể tự sản xuất ra kháng thể chống lại chính bản thân mình. B</span><span>ệnh thường gặp ở nữ giới, nhiều gấp 9 lần so với nam. Lứa tuổi mắc bệnh chủ yếu 20 đến 30 tuổi. </span></p><p class="Normal"> 	<span>Theo bác sĩ Bách</span><span>, b</span><span>ệnh có yếu tố di truyền, </span><span>thường xảy ra ở người có HLA-DR 2, 3, 8, các gen mã hóa bổ thể C1q, C2, C4. Yếu tố nguy cơ mắc bệnh là sử dụng </span><span>một số thuốc, tiếp xúc ánh sáng mặt trời, nhiễm một số loại virus... </span><span>Anh chị em ruột của các bệnh nhân có nguy cơ mắc bệnh cao gấp nhiều lần so với người thường.</span></p><p class="Normal"> 	<span>"Biểu hiện lâm sàng thường rất đa dạng, các triệu chứng ở nhiều cơ quan, không điển hình thường gặp là sốt, đau khớp, nổi ban ở da, phù, da xanh xao thiếu máu, rụng tóc, có khi khó thở, đau ngực", bác sĩ Bách chia sẻ. C</span><span>ác triệu chứng thường mơ hồ và giống với nhiều bệnh lý khác</span><span> nên bệnh nhân khi khởi phát bệnh thường đi khám ở nhiều chuyên khoa. N</span><span>hiều người mất vài năm mới được chẩn đoán bệnh.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001942901282904472.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bệnh nhân lupus thường nổi ban, da xanh xao. Ảnh: <em>healthline</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Bệnh gây tổn thương hầu hết hệ cơ quan cơ thể, trường hợp nặng đe dọa tính mạng. </span><span>Những tổn thương ở các nội tạng thường gặp là tràn dịch màng tim, viêm cơ tim, tràn dịch màng phổi, viêm phổi, viêm cầu thận, tác động lên hệ thần kinh gây co giật, rối loạn tâm thần, ở hệ tạo máu gây thiếu máu, xuất huyết...</span></p><p class="Normal"> 	<span>Theo bác sĩ Bách, </span><span>hiện nay việc điều trị có nhiều tiến bộ, có nhiều thuốc ức chế miễn dịch mới, ít độc tính hơn. </span><span>Bệnh chưa</span><span> thể điều trị dứt điểm mà chỉ có thể kiểm soát để hạn chế tái phát. Nếu khống chế bệnh tốt, bệnh nhân vẫn có thể sinh hoạt bình thường. Quá trình</span><span> điều trị phải kéo dài gần như suốt đời, quan trọng nhất là bệnh nhân phải theo dõi thường xuyên, định kỳ. </span></p><p class="Normal"> 	<span>Tình trạng bệnh nặng nhẹ còn phụ thuộc vào cơ địa và mức độ mẫn cảm của cơ thể người bệnh. Có những bệnh nhân chỉ cần duy trì thuốc là sức khỏe ổn định, vẫn sinh con, sống cuộc sống bình thường, tuổi thọ cao. Có người vừa phát hiện bệnh là đã ở giai đoạn nặng, khó cứu chữa, tiên lượng xấu. </span><span>Tuy nhiên đa số trường hợp phát hiện sớm, điều trị kịp thời vẫn mang lại nhiều cơ hội.</span></p><p class="Normal"> 	<span>Thời gian sống còn của bệnh nhân ngày càng được cải thiện đáng kể, có thể đến 20-30 năm tùy số cơ quan tổn thương và hiệu quả điều trị. </span><span>Tại châu Âu và Mỹ, 95% bệnh nhân lupus sống thêm được 5 năm, 90% trong 10 năm và 78% sống 20 năm.</span></p>', 1, '1500194290204363498.jpg', NULL),
(144, 'Chuẩn đẹp thân hình đàn ông thay đổi qua 150 năm', 'Đầu thế kỷ 19 thế giới đói nghèo nên người béo thể hiện sự giàu có; năm 1930-1950 hình thể của các ngôi sao Hollywood thành chuẩn mực, theo Businessinsider.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div style="text-align:center"> 			<div style="text-align: center;"> 				<div id="video-156654" data-component="true" data-component-type="video" data-component-value="156654" data-component-typevideo="2"></div></div> 		</div> 	</div> </div>', 1, NULL, NULL),
(145, 'Cách phát hiện sớm ung thư tinh hoàn để chữa khỏi hoàn toàn', 'Nam giới nên có thói quen tự khám tinh hoàn để phát hiện sớm bệnh ung thư. ', '<p class="Normal"> 	<span>Ung thư tinh hoàn là bệnh ít gặp, chiếm khoảng 1% ung thư ở nam giới. Tuy nhiên đây là loại ung thư có tỷ lệ mắc cao nhất ở nam giới lứa tuổi 25-35. </span></p><p class="Normal"> 	<span>Nguyên nhân chính gây bệnh là những người có tinh hoàn ẩn. 80-85% số người bị ung thư tinh hoàn có tinh hoàn ẩn và 15-20% xảy ra ở tinh hoàn đối bên. Những yếu tố khác gây bệnh là tiền sử quai bị, tràn dịch màng tinh hoàn, thoát vị bẹn</span></p><p class="Normal"> 	Đây là bệnh ung thư có khả năng chữa khỏi rất cao, tỷ lệ sống ở tất cả giai đoạn bệnh là 92%, riêng giai đoạn lan tràn đến 70% có khả năng chữa khỏị.</p><p class="Normal"> 	Để phát hiện sớm bệnh, nam giới có thể tự khám tinh hoàn.</p><p class="Normal"> 	Cụ thể:</p><p class="Normal"> 	- Đứng trước gương xem có sưng ở vùng tinh hoàn không.</p><p class="Normal"> 	- Kiểm tra tinh hoàn bằng hai tay, ngón giữa ở dưới và ngón cái ở trên tinh hoàn.</p><p class="Normal"> 	- Nắn nhẹ nhàng hai bên tinh hoàn, đừng quá lo lắng nếu thấy tinh hoàn hai bên không đều nhau vì đó là bình thường.</p><p class="Normal"> 	- Kiểm tra mào tinh hoàn, đây là một ống mềm nằm phía sau tinh hoàn có nhiệm vụ chứa tinh trùng.</p><p class="Normal"> 	- Vị trí phổ biến nhất của u tinh hoàn là phía hai bên, cũng có thể ở phía trước.</p><p class="Normal"> 	- Có thể kiểm tra bất cứ lúc nào cảm thấy thuận lợi, đơn giản nhất là kiểm tra sau mỗi lần tắm.</p><p class="Normal"> 	Nếu phát hiện điều gì bất thường thì nên đến bệnh viện chuyên khoa khám phát hiện sớm ung thư, tránh để bệnh quá muộn. Đây là bệnh ung thư có thể chữa khỏi ở giai đoạn sớm.</p><p class="Normal"> 	Nếu có khối u hoặc các bất thường khác, bác sĩ chỉ định làm các xét nghiệm để loại trừ ung thư. Ngoài ra siêu âm tinh hoàn, chụp cắt lớp vi tính ổ bụng để phát hiện các tổn thương di căn… </p><p class="Normal"> 	<span>Đối với trường hợp tinh hoàn ẩn, bác sĩ khuyên nên phẫu thuật cắt bỏ để ngừa tiến triển sang ung thư tinh hoàn.</span></p><p class="Normal"> 	<strong>Biểu hiện bệnh:</strong></p><p class="Normal"> 	- Bệnh nhân thường thấy tăng kích thước của bìu, có thể co kéo thừng tinh hoặc có cảm giác nặng bìu.</p><p class="Normal"> 	- Sờ thấy khối u tinh hoàn, không đau.</p><p class="Normal"> 	- Đau bụng đối với những trường hợp có tinh hoàn ẩn.</p><p class="Normal"> 	- Nổi hạch bất thường vùng bẹn, hạch cổ...</p>', 1, NULL, NULL),
(146, 'Bài tập tăng sức mạnh đàn ông', 'Các bài tập plank kết hợp nâng chân, hít đất dang rộng tay, đẩy người theo tạ giúp xây dựng khối cơ trung tâm vững chắc, theo Men''s Health.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-155844" data-component="true" data-component-type="video" data-component-value="155844" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(147, 'Điều gì xảy ra khi bạn cắt nhầm vào tay?', 'Khi bạn cắt vào tay, tình trạng viêm xảy ra, tế bào miễn dịch hoạt động chống lại vi khuẩn, lớp da mới được tái tạo, theo Brightside.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156679" data-component="true" data-component-type="video" data-component-value="156679" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(148, 'Món ăn ít chất béo thích hợp cho người muốn giảm cân', 'Thịt bò xào đậu cô ve là món ăn ít béo mà nhiều dưỡng chất thích hợp cho người đang ăn kiêng giảm cân.', '<p class="Normal"> 	Bác sĩ Trần Thị Minh Nguyệt hướng dẫn chế biến món thị bò xào đậu cô ve ngon miệng mà ít béo như sau:</p><p class="subtitle"> 	<span style="color:rgb(0,0,0);">Nguyên liệu:</span></p><p class="Normal"> 	- Thịt bò 100 g.<br> 	- Đậu cô ve 200 g.<br> 	- Củ hành tây 1 củ vừa.<br> 	- Tỏi băm 1 muỗng súp.<br> 	- Ngò, dầu, đường, tiêu, nước mắm… mỗi thứ một ít.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194292699694258.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thịt bò xào đậu ve.</p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	Cách làm:</p><p class="Normal"> 	- Thịt bò thái lát mỏng, ướp với chút tiêu, muối, dầu ăn.<br> 	- Đậu cô ve lặt 2 đầu tước bỏ xơ, luộc sơ qua cho có màu xanh, vớt ra xả lại nước lạnh để ráo, cắt làm đôi.<br> 	- Củ hành tây lột vỏ xắt múi.<br> 	- Ngò lặt rửa sạch ngắt khúc.<br> 	- Bắc chảo dầu nóng phi tỏi thơm. Cho củ xanh vào xào. Cho thịt bò vào đảo nhanh tay rồi múc ra đĩa.<br> 	- Dùng chảo dầu nóng cho đậu cô ve vào xào với chút nước, thêm chút muối, đường, chỉnh lửa nhỏ.<br> 	- Khi đậu cô ve chín mềm cho thịt bò vào xào nhanh tay, vặn lửa to và đảo đều rồi nhắc xuống cho ra đĩa. Rắc ngò lên trên.</p><p class="Normal"> 	Lưu ý: Trong quá trình chế biến nên dùng dầu thực vật thay cho mỡ động vật. Dùng càng ít dầu càng tốt.</p>', 1, '15001942921944961768.jpg', NULL),
(149, '4 món canh giải nhiệt nên ăn khi trời nóng', 'Canh cua rau đay, rau ngót thịt heo, tôm nấu bầu... giúp giảm nhiệt và phòng một số bệnh do thời tiết nắng nóng.', '<p class="Normal"> 	Theo bác sĩ Trần Thị Minh Nguyệt, cách giải nhiệt cơ thể hiệu quả và an toàn nhất là dùng thực phẩm. Bác sĩ gợi ý một số món ăn canh truyền thống dễ chế biến có tác dụng giảm nhiệt cơ thể như sau:</p><p class="subtitle"> 	Canh khổ qua nhồi thịt </p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942931708584690.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Khổ qua vị đắng, tính mát, giải nhiệt cơ thể rất tốt.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Nguyên liệu: </p><p class="Normal"> 	- Khổ qua 250 g.<br> 	- Thịt nạc 100 g.<br> 	- Gia vị.</p><p class="Normal"> 	Cách chế biến:</p><p class="Normal"> 	- Khổ qua bỏ hột, xắt lát.<br><span>- Thịt nạc thái miếng.</span><br><span>- Cho thịt vào nồi hầm rồi cho khổ qua vào nấu mềm. Nêm nếm gia vị vừa ăn. </span><span>Món ăn này có tác dụng thanh nhiệt, chống say nắng, giải độc, sáng mắt.</span></p><p class="subtitle"> 	Canh cua rau đay với mướp, mồng tơi</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942931291571762.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Canh cua rau đay.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Rau đay vị đắng, tính bình, tác dụng nhuận tràng, thông tiểu tiện, chữa táo bón, mát gan, mát ruột. <span>Cua đồng giàu chất đạm, béo, canxi, phốt pho, ngoài ra còn có sắt và vitamin B1, B2, PP... </span><span>Mướp vị ngọt, tính bình, không độc, tác dụng nhuận tràng, thanh nhiệt, giải độc, hoạt huyết, cầm máu và lợi sữa. </span><span>Mồng tơi vị chua, hàn, không độc, tác dụng lợi tiểu, chữa táo bón, đau đầu do trúng nắng. </span></p><p> 	Nguyên liệu:</p><p> 	- Cua đồng 500 g.<br> 	- Mướp hương 1 quả, gọt vỏ, rửa sạch, cắt thành miếng vừa ăn.<br> 	- Rau mồng tơi, rau đay vừa đủ ăn.</p><p> 	Chế biến:</p><p> 	- Lọc cua kỹ, cho vào nồi nước đun sôi, gạt gạch sang một bên.<br> 	- Cho rau và mướp vào đun chín.<br> 	- Nêm gia vị vừa ăn.</p><p class="subtitle"> 	Rau ngót thịt heo</p><p> 	Rau ngót giàu dĩnh dưỡng, có tác dụng nhuận tràng, thanh nhiệt, giải độc. Thịt heo nạc giàu đạm, béo, khoáng chất và các vitamin, tốt cho sự phát triển thể chất và não bộ.</p><p> 	Cách chế biến:</p><p> 	- Rau ngót tuốt lá, rửa sạch, vò sơ cho mềm.<br> 	- Thịt băm nhỏ, xào chín, cho nước vào đun sôi (tùy lượng người ăn).<br> 	- Cho rau vào, nêm gia vị vừa ăn.</p><p class="subtitle"> 	Canh bầu hoặc bí nấu với tôm nõn</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001942931440357123.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Canh bầu với tôm.</p> 			</td> 		</tr></tbody></table><p> 	Tôm nõn rất giàu đạm, canxi. Bầu và bí đều có tính mát, lợi niệu, nhuận tràng.</p><p> 	Chế biến:</p><p> 	- Tôm khô rửa sạch, ngâm khoảng 10 phút rồi cho vào nồi nước nấu sôi.<br> 	- Cho bầu, bí (băm nhỏ hoặc sắt miếng nhỏ) vào chờ sôi lại rồi nêm gia vị vừa ăn.</p>', 1, '15001942931735333649.jpg', NULL),
(150, 'Thiếu nữ 18 tuổi dũng cảm đón nhận cái chết', 'Biết mình không thể qua khỏi, cô gái trẻ mắc bệnh lupus xin bác sĩ cho xuất viện về chùa để được nghe kinh Phật trước khi đi xa.', '<p class="Normal"> 	Hình ảnh nữ bệnh nhân 18 tuổi trong chiếc áo thun đỏ xinh xắn và quần đen, mái tóc gọn gàng sạch sẽ, nằm trên chiếc giường đẩy được đưa ra khỏi cánh cửa phòng bệnh vừa mở, luôn trong tâm trí chàng sinh viên y khoa năm cuối Nguyễn Thanh Sang, nay là một bác sĩ nội trú. 2 năm đã qua, ánh mắt trìu mến đầy lòng biết ơn của thiếu nữ khi chia tay các y bác sĩ điều trị cho mình để lên xe cứu thương đi về nơi cuối cuộc đời vẫn khiến bác sĩ Sang nhớ mãi. </p><p class="Normal"> 	<span>Năm 2014, nữ bệnh nhân nhập viện Chợ Rẫy (TP HCM) vì ho và sốt cao. Cô gái không biết rằng đó là khởi đầu chuỗi ngày cuối đời nhiều nỗi đau. Bác sĩ chẩn đoán cô mắc bệnh lupus ban đỏ. Ròng rã 6 tháng, bệnh nhân được bác sĩ khoa Nội khớp, Nội thần kinh, Nội thận... chăm sóc. Bệnh lupus đã biến chứng suy đa cơ quan.</span></p><p class="Normal"> 	Bệnh nhân được chuyển sang khoa Nội tim mạch vì tràn dịch màng ngoài tim, đè sụp thất trái. Bác sĩ chỉ định rút dịch, vận mạch vì sợ bệnh nhân suy tim cấp.<span> Chiều 11/1/2015, </span><span>bệnh nhân được </span><span>đặt nội khí quản vì suy hô hấp, độ bão hòa oxy mao mạch giảm dần. Trong khi bố mẹ xót xa cho con gái đau đớn nên không muốn đặt nội khí quản thì bệnh nhân lại đề nghị bác sĩ hãy tiến hành. </span></p><p class="Normal"> 	<span>"Tôi </span><span>chưa từng thấy bệnh nhân nào khi đặt nội khí quản mà lại mở mắt một cách hoàn toàn tự nhiên nhìn bác sĩ. Ánh mắt chứa đựng sự cảm thông trước sự bất lực của bác sĩ với căn bệnh hiểm ác của em cũng như cái giới hạn của y học", bác sĩ Sang chia sẻ. </span><span>Khi đó anh là sinh viên y năm 6 đang kỳ thực tập ở Bệnh viện Chợ Rẫy.</span></p><p class="Normal"> 	<span>Đêm, bệnh nhân bị thiếu nước, thiếu dịch, huyết áp tụt dần nhưng không thể đặt ống thông tĩnh mạch trung tâm vì cơ thể đã quá phù và cổ nhiều vết tiêm do những lần đặt thất bại trước đó. </span><span>Đường truyền tĩnh mạch đùi là đường hiếm hoi cuối cùng, được xem là duy nhất đưa thuốc vào cơ thể để giữ sự sống cho em. Các bác sĩ lo lắng bảo nhau: "Nếu bệnh trở nặng thì việc hồi sức sẽ rất gian nan".</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019429389549313.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>abcnews</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Huyết áp bệnh nhân bắt đầu tụt nhanh. Cậu sinh viên năm cuối cứ chạy ra chạy vào đo huyết áp 5 phút một lần và lần nào anh cũng buộc lòng ghi vào hồ sơ bệnh án của cô gái trẻ 3 chữ "Theo dõi tiếp". Bên ngoài cửa phòng bệnh, n</span><span>gười cha ngoài 50 tuổi của bệnh nhân cứ lặng người trong những cơn nấc từ lồng ngực. Đây không phải lần đầu tiên ông nghe bác sĩ giải thích về tiên lượng xấu của con. </span></p><p class="Normal"> 	<span>Cậu sinh viên Sang khi ấy quyết định ngồi ở khoa Nội tim mạch trông chừng cho bệnh nhân. "</span><span>22h tối, tôi quan sát mọi người trong khoa, cứ mỗi lần nhìn thấy em lòng tôi lại một lần nữa chùng lại", bác sĩ Sang vẫn không quên cảm giác khi ấy. Huyết áp bệnh nhân giảm sâu, Sang báo cho bác sĩ trực. "Đ</span><span>ành chấp nhận thôi. Bây giờ cách duy nhất nâng huyết áp lên là truyền dịch nhưng không có đường truyền, tim thì đang bị chèn ép, còn thuốc vận mạch thì đã dùng hết rồi...", bác sĩ trực nói.</span></p><p class="Normal"> 	<span>Cô gái nhìn Sang, rồi viết gì đấy vào một tờ giấy nhờ người mẹ với đôi mắt đỏ hoe đưa bác sĩ. </span><span>Dòng chữ nguệch ngoạc với tâm nguyện: "Xin bác sĩ cho con về chùa. Con muốn được nghe kinh Phật và mất ở đó". Lặng người trong giây lát, Sang hỏi nguyện vọng của người thân và họ cũng đồng ý cho con gái về chùa. Bình oxy, điều dưỡng chăm sóc tại nhà được chuẩn bị ngay lập tức. Giấy xuất viện được in liền.</span></p><p class="Normal"> 	23h đêm ấy, mọi việc chuẩn bị đã hoàn thành. Cô gái được thay đồ để về chùa. 6 tháng trời nằm trong bệnh viện, cô chưa một lần được về nhà. Có lẽ đây là lần xa nhà lâu nhất và cũng là chuyến đi xa cuối cùng của đời em. <span>Việc Sang có thể làm duy nhất là đo lại huyết áp của bệnh nhân. 5 phút trước đó huyết áp đã tụt rất thấp, giờ lại không đo được.</span></p><p class="Normal"> 	Cánh cửa phòng bệnh mở ra, chiếc giường đẩy xuất hiện với bên trên là cô gái trẻ trong chiếc áo thun đỏ xinh xắn và chiếc quần đen. Bên cạnh cô có bố mẹ, ông bà ngoại, điều dưỡng và Sang. Ánh mắt cô gái nhìn mọi người rất trìu mến. Trước khi được đưa lên xe cấp cứu, cô lại nhìn mọi người với tấm lòng biết ơn. Các bác sĩ không ai giấu được vẻ bất lực và âu lo.</p><p class="Normal"> 	Chiếc xe rời viện đưa cô gái về chùa. Các y bác sĩ lại quần quật trong công việc thường nhật, lao vào cuộc chiến giành sinh mệnh cho những bệnh nhân khác nhưng lòng không tránh khỏi cảm giác nặng trĩu. Một chị điều dưỡng bỗng gợi lại chuyện, rằng đó là con gái duy nhất của hai vợ chồng.<span> "Từ sáng cô bé đã biết là sẽ không qua khỏi nên dặn dò ông bà, bố mẹ nhớ sống tốt và giữ gìn sức khỏe. Bố mẹ hãy đẻ em trai để đừng bị bệnh giống như con gái. Em ấy muốn được nghe kinh ở chùa lần cuối", nữ điều dưỡng tâm sự.</span></p><p class="Normal"> 	Tượng Phật nhỏ màu vàng luôn mang theo trong những ngày điều trị bệnh, cô gái trẻ để lại viện. 5h sáng hôm sau, người nhà báo tin em ấy qua đời, nhẹ nhàng ra đi trong sự thanh thản ấm áp. Phút cuối em không khóc, chỉ xin lỗi vì chưa phụng dưỡng được bố mẹ mà đã ra đi.</p><p class="Normal"> 	<span>Với cậu sinh viên y khoa khi ấy, chuyện về nữ bệnh nhân trẻ này là bài học của sự mạnh mẽ và chấp nhận đương đầu với số phận. </span><span>"Mỗi khi mệt mỏi, tôi nhớ lại câu chuyện ngày cũ</span><span> về cô bé để bước tiếp. Câu chuyện vẫn khiến tôi đau nhói mỗi khi đọc lại và hình ảnh cô bé 18 tuổi vẫn hiển hiện rất rõ", Sang hiện là bác sĩ nội trú chia sẻ.</span></p>', 1, '1500194293602529580.jpg', NULL),
(151, 'Hành trình nâng ngực, gọt hàm của hoa hậu Kiều Ngân tại Hàn Quốc', 'Hoa hậu Việt Nam toàn cầu tại Mỹ 2015 trải qua 2 cuộc đại phẫu nâng ngực và gọt xương hàm, sau đó nâng mũi, hút mỡ cằm, chỉnh mắt...', '<p class="Normal"> 	Trần Thị Kiều Ngân 28 tuổi, giành danh hiệu Hoa hậu Việt Nam toàn cầu tại Mỹ năm 2015. Sở hữu ngoại hình ưa nhìn với chiều cao 1,73 m, số đo 3 vòng 80 - 62 - 90 cm nhưng người đẹp luôn cảm thấy mình còn một số khuyết điểm trên gương mặt cần khắc phục.</p><p class="Normal"> 	"Mỗi lần soi gương em nhận thấy khuyết điểm của mình là chiếc cằm bạnh, mí mắt nhỏ khiến cho gương mặt không cân đối. Những điểm này không thể khắc phục bằng biện pháp tự nhiên được nên em quyết định phẫu thuật thẩm mỹ", cô gái chia sẻ.</p><p class="subtitle" style="text-align:center;"> 	Kiều Ngân chia sẻ lý do phẫu thuật thẩm mỹ</p><div style="text-align: center;"> 	<div style="text-align: center;"> 		<div id="video-156447" data-component="true" data-component-type="video" data-component-value="156447" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:left;"> 		<span>Sau khi tham khảo kinh nghiệm của bạn bè, cô quyết định chọn một bệnh viện nổi tiếng ở thủ đô Seoul, Hàn Quốc là nơi khởi đầu công cuộc trùng tu nhan sắc. </span><span>Ngân quyết định đại phẫu gọt xương hàm và nâng cấp vòng một trước. Người đẹp thú thật đây là lần đầu tiên đụng đến dao kéo nên rất lo lắng, song khát khao làm đẹp đã lấn át nỗi sợ hãi trong lòng cô.</span></p> </div><p class="Normal"> 	<span>Kiều Ngân trải qua 2 cuộc đại phẫu và vài tiểu phẫu kéo dài khoảng 12 tiếng đồng hồ. Đầu tiên, các bác sĩ tiến hành nhấn mí, nâng mũi S-line, xóa thâm mắt. Sau đó bác sĩ hút mỡ cằm, cưa xương hàm 2 bên lấy ra 2 mảng xương nhỏ, mài nhẵn phần xương còn lại rồi đóng vết mổ. Cuối cùng là đặt túi ngực nội soi allergan giúp "núi đôi" căng tròn và tạo dáng hài hòa với cơ thể hơn.</span></p><p class="Normal"> 	Tỉnh lại sau ca phẫu thuật, việc đầu tiên Kiều Ngân làm là nhìn vào gương để xem mặt mình thay đổi thế nào. Cô gái suýt khóc khi nhìn gương mặt băng bó chằng chịt cộng với cảm giác ê ẩm ở 2 bên hàm sau khi hết thuốc tê. Người đẹp không giấu được vẻ hoang mang, lo lắng. Các bác sĩ động viên rằng ca phẫu thuật đã thành công, chỉ vài ngày nữa sẽ được tháo băng và hết đau, Ngân mới yên tâm phần nào.</p><div style="text-align: center;"> 	<div style="text-align: center;"> 		<div id="video-156450" data-component="true" data-component-type="video" data-component-value="156450" data-component-typevideo="2"></div><span style="color:#000000;"> </span></div> </div><p class="Normal"> 	Sau hành trình làm đẹp, Kiều Ngân lưu lại Hàn Quốc vài ngày để chờ cho vết thương lành lặn hẳn mới về. Trong thời gian này một số bức ảnh của hoa hậu với băng dán <span>chi chít trên mặt lan truyền trên mạng xã hội. Kiều Ngân cho biết rất may gia đình luôn tôn trọng quyết định của con nên bố mẹ động viên cô chú ý chăm sóc sức khỏe để sớm bình phục. </span></p><p class="Normal"> 	Đến nay vết thương đã lành và không còn phải băng bó hay kiêng cữ gì, Kiều Ngân bảo <span>rất hài lòng với diện mạo mới của mình và không còn muốn chỉnh sửa gì thêm. Cô không được vận động mạnh trong 6 tháng đầu, nếu thấy đau ở xương hàm thì chườm lạnh, ngoài ra còn kiêng ăn hải sản và thực phẩm nóng để tránh ảnh hưởng đến các bộ phận đã phẫu thuật.</span></p><p class="Normal"> 	<span>Nhan sắc mới giúp Kiều Ngân tự tin hơn khi quay lại với công việc trình diễn thời trang và dẫn chương trình truyền hình. Hoa hậu cho biết luôn sẵn sàng chia sẻ kinh nghiệm làm đẹp cho mọi người mong muốn cải thiện nhan sắc bằng con đường tự nhiên hay dao kéo.</span></p><p class="subtitle" style="text-align:center;"> 	<span>Quy trình nâng ngực như thế nào</span></p><div style="text-align: center;"> 	<div id="video-113402" data-component="true" data-component-type="video" data-component-value="113402" data-component-typevideo="2"></div></div>', 1, NULL, NULL),
(152, '6 ''bác sĩ'' tốt nhất của cơ thể bạn', 'Ánh nắng, nước, không khí trong lành... là những "bác sĩ" luôn thường trực bảo vệ cơ thể bạn khỏe mạnh, theo Cure Joy.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156690" data-component="true" data-component-type="video" data-component-value="156690" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(153, 'Hai giờ mổ rút dao cắm giữa trán cậu bé 12 tuổi', 'Bé trai 12 tuổi ở Đăk Nông cùng em chơi đùa, bất ngờ con dao trên tay em bị tuột cán đâm vào giữa trán anh.', '<p class="Normal"> 	Chiều 12/7 cháu bé được người nhà đưa vào Bệnh viện Đa khoa tỉnh Đăk Nông cấp cứu với con dao lớn vẫn cắm vào trán. Các bác sĩ xác định lưỡi dao rộng khoảng 4 cm găm sâu 3 cm vào trán bệnh nhi gây chấn thương sọ não.</p><p class="Normal"> 	Ca phẫu thuật kéo dài gần 2 tiếng đồng hồ, các bác sĩ đã rút thành công con dao ra khỏi trán em bé, bảo toàn tính mạng cho bệnh nhân.</p><p class="Normal"> 	Hiện sức khỏe của bé trai đã ổn định sau ca mổ và đang được bác sĩ theo dõi phòng nguy cơ biến chứng. </p><p class="Normal" style="text-align:right;"> 	<strong>Quốc Thịnh</strong></p>', 1, NULL, NULL),
(154, 'Hướng dẫn cách ép tim thổi ngạt cứu người ngưng thở', 'Gót bàn tay đặt trên hõm ngực nạn nhân ấn sâu xuống 4-5 cm, cứ 30 lần ép tim dừng lại 5 giây để thổi ngạt 2 lần. ', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div style="text-align:center"> 			<div style="text-align: center;"> 				<div id="video-156192" data-component="true" data-component-type="video" data-component-value="156192" data-component-typevideo="2"></div></div> 		</div> 	</div> </div>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(155, 'Người đàn ông tử vong sau khi uống cồn y tế pha rượu', 'Uống rượu pha từ cồn y tế, người đàn ông Hà Nội hôn mê, xuất huyết não và tử vong sau 4 ngày do ngộ độc methanol.', '<p class="Normal"> 	<span>Ca bệnh này được Bệnh viện Bạch Mai báo cáo Cục Quản lý Dược, Cục Quản lý khám chữa bệnh, Bộ Y tế, ngày 12/7. </span></p><p class="Normal"> 	<span>Bệnh nhân được gia đình đưa vào cấp cứu tại Trung tâm Chống độc, Bệnh viện Bạch Mai ngày 20/6 trong tình trạng nguy kịch, hôn mê, co giật, tổn thương não, sốc, toan chuyển hóa nặng. Kết quả xét nghiệm máu cho thấy nồng độ methanol 321,76mg/dL, cao gấp 16 lần mức cho phép. Bệnh nhân tử vong ngày 24/6 do</span><span> ngộ độc methanol nặng, xuất huyết não</span><span>.</span></p><p class="Normal"> 	Theo người nhà, bệnh nhân nghiện rượu và đã mua cồn y tế về để pha thành rượu uống. Đây là loại cồn 90 độ chai 500 ml, trên nhãn ghi ethanol. Tuy nhiên xét nghiệm dung dịch còn lại trong chai cồn bệnh nhân đã dùng cho thấy nồng độ methanol 88%, không tìm thấy ethanol.</p><p class="Normal"> 	<span>Từ trường hợp này, các bác sĩ Bệnh viện Bạch Mai đề nghị cơ quan chức năng kiểm tra lại việc dùng methanol trong các sản phẩm cồn sát trùng và có biện pháp kiểm soát thành phần này. Nhãn mác các chai lọ cồn sát trùng cần ghi rõ thông tin về hàm lượng ethanol và methanol. Trường hợp cồn có thành phần methanol thì nhãn cần phải ghi rõ “chứa cồn công nghiệp methanol - không được uống”. Ngoài ra có thể thêm chất chỉ thị màu (khác với màu của cồn công nghiệp) vào chai cồn sát trùng để người dân không nhầm lẫn và biết nguy cơ ngộ độc.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019429885545101.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mẫu chai cồn bệnh nhân dùng để pha rượu uống. Ảnh: <em>Bệnh viện cung cấp.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Theo thạc sĩ Nguyễn Trung Nguyên, Phụ trách Trung tâm Chống độc, Bệnh viện Bạch Mai, tình trạng ngộ độc methanol có xu hướng tăng gần đây. </span><span>Từ đầu năm đến nay, Trung tâm tiếp nhận 4 bệnh nhân ngộ độc methanol do uống cồn y tế, trong đó một người tử vong, một bị di chứng não, 2 đã hồi phục). </span><span>Ngoài nguyên nhân uống phải loại rượu không rõ nguồn gốc, còn có những người uống cồn y tế pha thay rượu do nghiện rượu, thiếu rượu để uống hoặc hiểu nhầm cồn y tế an toàn. </span></p>', 1, '1500194297465891528.jpg', NULL),
(156, '5 động tác giúp dân văn phòng thư giãn ngay tại bàn làm việc', 'Bạn thư giãn cơ bắp, đốt cháy mỡ thừa ngay tại bàn làm việc với các bài squat, chống đẩy, đá chân sau..., theo Brightside. ', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156349" data-component="true" data-component-type="video" data-component-value="156349" data-component-typevideo="2"></div></div> </div>', 1, NULL, NULL),
(157, 'Cô gái Sài Gòn quyết ''không vay ai một đồng'' tự sắm nhà, xe trước tuổi 30', '"Khi 29 tuổi, trong túi có 1,5 tỷ, mình chỉ mua căn nhỏ 1,3 tỷ, để dư 200 triệu phòng thân", Quỳnh viết.', '<p class="Normal"> 	<em>Bài viết dưới đây là chia sẻ của Ngô Như Quỳnh, MC 33 tuổi của Đài truyền hình TP HCM về cách sống, tiết kiệm và các nguyên tắc chi tiêu giúp cô không bao giờ nợ ai một đồng và luôn tự chủ về tài chính. </em></p><p class="Normal"> 	<span>Nhiều bạn trẻ hay hỏi mình về cách chi tiêu làm sao để tích luỹ được mà vẫn sống thoải mái, không áp lực, mình muốn chia sẻ quan điểm cá nhân trong bài viết này.</span></p><p class="Normal"> 	Lấy câu chuyện hôm qua làm ví dụ. <span>Hôm qua mình có lên Facebook hỏi kinh nghiệm mua xe hơi nào tầm một tỷ, tiện ích vừa phải, phù hợp với kế hoạch tài chính và nhu cầu sử dụng ở thời điểm hiện tại. Có nhiều bạn góp ý rằng: </span></p><p class="Normal"> 	- Sao ngoài khoản một tỷ sẵn có không vay thêm ngân hàng mua xe xịn đi cho đã. </p><p class="Normal"> 	- Cỡ Quỳnh phải đi cỡ Audi, BMW, Mec mới sang, đi mấy cái xe tầm một tỷ đổ lại... thì rẻ tiền lắm. </p><p class="Normal"> 	- Sao không vay mua xe mà bỏ tiền túi, để tiền đó làm ăn, trả lãi từ từ.</p><p class="Normal"> 	Mình trả lời không thích vay mượn, có nhiêu mua nhiêu, cũng không thích đua đòi. Có bạn chế giễu, ai cũng như Quỳnh chắc ngân hàng ế, kinh tế thị trường trì trệ. Tội lỗi vậy sao?</p><p class="Normal"> 	<span>Mình chợt nhớ lại hồi mua nhà 2013, lúc đó trong túi có 1,5 tỷ, chỉ mua căn nhỏ 1,3 tỷ, để dư 200 triệu đó phòng thân. Cũng có bạn nói sao không vay thêm ngân hàng mua căn 2-3 tỷ cho thoải mái. Mình cũng nói không thích ở nhà to rồi co ro trả nợ.</span></p><p class="Normal"> 	Sau đó vì nhà xa, mình mua ôtô để đi lại thuận tiện che nắng che mưa nên chỉ bỏ một phần tài chính ra mua Kia Morning là được rồi. Cũng có nhiều bạn chế giễu "Quỳnh đi xe đó trông... yếu yếu", sao không vay mua thêm xe sang đi cho oách.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943011202851033.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					MC Ngô Như Quỳnh. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Những suy nghĩ đó không có gì lạ trong thời buổi trọng vật chất ngày nay. <span>Mình biết có rất nhiều bạn trẻ, tiền không có nhưng vẫn vay mượn để quần là áo lượt, hàng hiệu sang chảnh thay vì chọn những thứ vừa tầm mà đẹp. Nhà ở thuê nhưng phải mua xe sang lấy nét. Lương thấp mà vay ngân hàng mua căn hộ cao cấp rồi tới tháng vã mồ hôi trả nợ thay vì tiết kiệm thêm. Tiền ít cũng phải cố đi resort nhiều sao check-in khí thế thay vì đi những nơi vừa tầm...</span></p><p class="Normal"> 	Mình chợt nhận ra là quan điểm chi tiêu của mình có vẻ khác so với nhiều người. Không bàn đúng sai hay dở, chỉ là sự lựa chọn của mỗi người thôi. Có thể do mình là gái miền Trung "chắt bóp" đã quen, hoặc do gia đình không giàu, do hoàn cảnh tự lập từ nhỏ tới lớn không có ai để dựa dẫm nên về tài chính mình đúng kiểu "ăn chắc mặc bền".</p><p class="Normal"> 	Quan điểm chi tiêu của Quỳnh:</p><p class="Normal"> 	<span>Thứ nhất, tiền là phương tiện, không phải mục đích, không làm nô lệ cho nó. Tiêu xài theo nhu cầu và khả năng có thực của bản thân, chứ không phải đua đòi, lấy nét, làm màu, khoe khoang với ai. </span></p><p class="Normal"> 	<span>Thứ 2, không bao giờ xài hết số tiền đang có vào bất kỳ một nhu cầu nào, luôn để dành lại 20-30% phòng trường hợp cấp bách.</span></p><p class="Normal"> 	Thứ 3, không bao giờ vay mượn ngân hàng lẫn người thân, bạn bè. Có nhiều chơi nhiều, có ít chơi ít, không có không chơi. Mình sợ nợ nần, sợ rủi ro. Mình thích ăn ngon ngủ yên không nơm nớp lo đến tháng trả lãi.</p><p class="Normal"> 	Thứ 4, có thể nghiêm khắc với bản thân nhưng rộng lòng với người thân và bạn bè lẫn những người khó khăn ngoài xã hội, giúp nếu có thể.</p><p class="Normal"> 	Thứ 5, nghiêm khắc với bản thân không có nghĩa là tiết kiệm tới mức hà tiện, mà là xài đúng nơi đúng chỗ. Nếu không cần thì một đồng không bỏ, nếu hợp lý thì bao nhiêu cũng chơi.</p><p class="Normal"> 	Cái này đã có gốc rễ từ xưa. <span>Thời sinh viên, đi dạy kèm được 500 ngàn thì mình đã có thói quen tiết kiệm 200 mỗi tháng. Ra trường đi dạy một triệu mình để dành 300 nghìn. Tới lúc đi làm 300 USD mình cũng để lại 100 USD. Tới lúc công việc thuận lợi có khi thu nhập cả 100 triệu/tháng, mình cũng luôn dành lại 30-50%.</span></p><p class="Normal"> 	Mình cũng không có thói quen cứ có được một khoản tiền kha khá là kiếm chuyện mua sắm tiêu xài. Bạn mình nhiều người cứ "vô mánh" là đi thả ga. Mình thì để đó, quăng vô tiết kiệm, vẫn sống bình thường.</p><p class="Normal"> 	Vì vậy, dù công việc của mình từ xưa tới nay là làm công ăn lương, làm thuê, lao động trí óc chứ chẳng phải kinh doanh hay mánh mung phi vụ qua một đêm có tiền tỷ, cũng chẳng ai bao, mình vẫn tích góp được để mua nhà, mua xe, chi xài cho những nhu cầu cần thiết mà chẳng áp lực vay mượn hay xin xỏ ai.</p><p class="Normal"> 	Để duy trì điều đó, mình tuyệt đối không bao giờ có cái thói tự đại với những gì mình có hay vung tay quá trán, đua đòi. Mọi thứ mình chi xài đều là vì nhu cầu thực tế, không chạy theo hàng hiệu, không chạy theo hào nhoáng.</p><p class="Normal"> 	Mình không ngại ở nhà thuê, đi xe ôm, ăn mặc giản dị, ăn uống bình dân, cà phê vỉa hè, lui tới những nơi bình thường, không 5 sao hay sang chảnh. Nhưng nếu cần vì công việc hay vì giao tiếp xã hội, mình cũng không ngại tốn kém, vì mình biết đầu tư đó xứng đáng và tái quay vòng. <span>Nên đừng lầm rằng công việc mình làm có liên quan tới showbiz mà phải thế này thế kia mới được. Đi BMW hay Kia, ăn Hyatt hay vỉa hè, mặc hàng hiệu hay Saigon Square, không khác nhau mấy nếu tự thân thấy vui. Mình cho rằng giá trị con người nằm ở bề ngoài ít thôi, chủ yếu là cái gì chứa trong đầu và nhiều nhất là ở lối sống, cách hành xử.</span></p><p class="Normal"> 	Là một người phụ nữ, ở tuổi 30, mình chẳng là gì so với nhiều người thành công và giàu có khác trong xã hội, nhưng mình hài lòng và tự hào vì những gì đang có, vì nó là kết quả của học hành, làm việc, của nỗ lực bản thân, bao gồm cả sự cân nhắc trong chi tiêu, theo quan điểm của mình. Không nợ nần, dư chút ít, đủ những thứ căn bản, cuộc sống thong dong, tâm hồn thanh thản, đời còn gì sướng hơn.</p><p class="Normal"> 	Mình không ca ngợi việc tiết kiệm cực đoan tới mức hà tiện. Mình vẫn sống, trải nghiệm, hưởng thụ đầy đủ, trong khả năng.</p><p class="Normal" style="text-align:right;"> 	<strong>Ngô Như Quỳnh</strong></p>', 1, '15001943001840591594.jpg', NULL),
(158, 'Tại sao bạn nên sơn nhà màu xanh trước khi bán', 'Màu xanh đem lại cảm giác nhẹ nhàng, bình yên nên được nhiều người yêu thích.', '<p> 	Nhà xã hội học Philip Colen (Đại học Maryland, Mỹ) đã tiến hành khảo sát "Những màu sắc yêu thích của bạn là gì" với 2.000 người. Đa số câu trả lời của cả đàn ông và phụ nữ đều có màu xanh. Trên thực tế, phần lớn mọi người sẵn sàng trả nhiều tiền hơn để mua các món đồ có màu mình thích, trong đó có cả những ngôi nhà sơn xanh. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943021361439364.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>Decopad. </em></p> 			</td> 		</tr></tbody></table><p> 	Theo trang web bất động sản <em>Zillow</em>, trong số 32.000 bức ảnh chụp ảnh nhà đã bán, những nơi ở có tông màu xanh hoặc xám thường có giá bán cao hơn mong đợi. Các nhà tâm lý cũng cho rằng màu xanh đem lại cảm giác bình yên, gần gũi với thiên nhiên và tạo ra sự tin tưởng. </p><p> 	"Tông màu xanh, xám, đặc biệt là xanh da trời nhạt hoặc xám nhẹ là những lựa chọn phù hợp nhất cho những người bán nhà. Chúng sẽ tạo cảm giác tươi sáng, giúp ngôi nhà có vẻ rộng hơn", chuyên gia nội thất Kerrie Kelly cho biết.</p><p> 	Bởi vậy, khi có ý định bán nhà, bạn nên sơn lại phòng có màu hồng của con hoặc phòng đọc sách màu tím sang xanh nhạt hoặc xám. Nhờ đó, các khách mua tiềm năng có thể dễ dàng hình dung cách bố trí đồ trong không gian mới.</p><p class="Normal"> 	Trong khi màu xanh là lựa chọn tuyệt vời nhất thì có một số màu sắc mà các gia chủ nên tránh khi muốn bán nhà.</p><p class="Normal"> 	Những ngôi nhà có tông màu tối hoặc tường trang trí khác lạ, màu đất nung thường khó bán hoặc bị mất giá. Ngoài ra, những ngôi nhà tông trắng hoàn toàn sẽ tạo cảm giác lạnh lẽo, tác động tiêu cực tới tâm lý người mua.</p><p style="text-align:right;"> 	<strong>An Yên</strong></p><p> 	<em style="margin:0px;padding:0px;color:rgb(51,51,51);line-height:18px;">Chia sẻ kinh nghiệm mua nhà và tiết kiệm của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2" style="margin:0px;padding:0px;color:rgb(0,79,139);text-decoration:none;">tại đây</a>.</em></p>', 1, '1500194302141609447.jpg', NULL),
(159, 'Cô bé 2 tuổi đẩy xe nước giữa trời 40 độ C đem tặng công nhân', 'Cô bé Trung Quốc mang nước đến tặng những người công nhân đang sửa đường gần đó, dưới sự gợi ý của mẹ.', '<p class="Normal"> 	Cô bé 2 tuổi, tên Rara, đi bộ trên một con phố ở <span>Thiểm Tây, Trung Quốc, với mẹ hôm thứ 3 vừa qua. Hôm đó nhiệt độ ngoài trời lên tới 40 độ C. Khi con nói khát, </span><span>mẹ cô bé đã vào một cửa hàng tiện lợi gần đó mua vài chai nước lạnh cho con. Cô mua thêm gần chục chai nước cho một số công nhân đang sửa đường gần đó.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194302945052025.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cô bé đẩy nước đến tặng những công nhân sửa đường.<em> Ảnh: Shanghaiist.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Người mẹ quay sang cô con gái nhỏ và nói: "Hãy nhìn họ kìa con. Họ đang làm việc rất chăm chỉ. Chúng ta sẽ đưa cho họ vài chai nước nhé?", Rara gật đầu.</p><p class="Normal"> 	Người mẹ tự hào đứng sang một bên và quan sát con gái đẩy một chiếc xe nhỏ với 8 chai nước, đi về phía các công nhân.</p><p class="Normal"> 	Những bức ảnh được chụp bởi một người bạn của hai mẹ con. Khi được chia sẻ trên mạng xã hội, nó nhận được 60.000 lượt thích trong một ngày. Mọi người đều khen ngợi việc làm của cô gái nhỏ cũng như ca ngợi cách giáo dục tuyệt vời của người mẹ.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194302782007998.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Những công nhân rất xúc động trước hành động của cô bé. <em>Ảnh: Shanghaiist.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Theo Shanghaiist, người mẹ, quốc tịch Singapore, cho biết cô hy vọng con gái mình sẽ trau dồi những thói quen tốt khi cô bé lớn lên.</p><p class="Normal" style="text-align:right;"> 	<strong><span>Mộc Miên</span></strong></p>', 1, '15001943022067770395.jpg', NULL),
(160, 'Cô dâu lái xe buýt đưa chồng đến đám cưới', 'Cô dâu Trung Quốc tự mình lái xe, đưa chồng đến nơi tổ chức hôn lễ.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943031815952606.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trong khi các cô dâu khác thích được hộ tống đến đám cưới bằng những chiếc xe sang, thậm chí là cả máy bay thì cô dâu Trung Quốc lại tự mình lái xe buýt đưa chồng tới nơi tổ chức hôn lễ.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943032065056364.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Theo <em>Shanghaiist</em>, cô dâu tên Wu Zheng là một tài xế lái xe buýt tại thành phố Sơn Đông. Hai người đã nảy sinh tình cảm trên chính những chuyến xe nên họ muốn trong ngày vui cũng có sự góp mặt của "người bạn" này.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194303199663035.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					"Tôi muốn nó có mặt ở đây đế chứng kiến ngày trọng đại của mình", cô dâu Wu Zheng nói.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943032039226508.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đôi vợ chồng trẻ bước xuống từ chiếc xe cưới "đại bự".</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943031245174665.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					2 chiếc xe buýt cũng được điều động thêm để đưa đón khách mời trong ngày vui của nữ tài xế.</p> 			</td> 		</tr></tbody></table><p style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943031972186005.jpg', NULL),
(161, 'Cay đắng của những ông chồng nhắm mắt cho vợ đi ''ăn phở''', 'Không khiến vợ vui trong phòng ngủ, anh Đăng chấp nhận cho chị tìm đối tác bên ngoài, chỉ cần khi về nhà thì chăm con.', '<p class="Normal"> 	<span>Ngay từ khi cưới đã không thể làm vợ thỏa mãn vì "chưa tới chợ đã hết tiền", anh Đăng, 32 tuổi (Tây Mỗ, Hà Nội) luôn day dứt và khổ tâm. Nhưng vì tự ái và sĩ diện, anh không dám trao đổi thẳng thắn với vợ, chỉ đau khổ và âm thầm quan tâm, chăm sóc nhiều hơn để bù đắp.</span></p><p class="Normal"> 	Ngày phát hiện vợ ngoại tình với một đồng nghiệp nam, anh như phát điên, đã dùng mọi lời lẽ nặng nề nhất để chửi rủa chị. Sau đó, anh chìm vào rượu  bia, thuốc lá để quên đi nỗi đau và sự nhục nhã vì bị cắm sừng. Anh bỏ đi một tuần liền và rồi bừng tỉnh khi nghĩ tới cô con gái mới 4 tuổi.</p><p class="Normal"> 	"Một phần lỗi tại tôi nên vợ mới như vậy. Từ lúc yêu tôi đã biết cô ấy là người có nhu cầu rất cao nhưng tôi lại cố gạt đi, nghĩ rằng chỉ cần mình bù đắp bằng sự chăm chỉ làm ăn, vun vén là ổn", anh Đăng kể lại. Muốn giữ tổ ấm cho con, anh đã trở về, nói với vợ rằng cả hai từ nay sẽ sống vì con, anh đồng ý cho chị thỏa mãn sinh lý bên ngoài nhưng phải kín đáo để không ai biết.</p><p class="Normal"> 	"Nói ra như vậy nhưng lòng tôi chỉ mong vợ thấy mình vì gia đình như thế thì cũng chấm dứt đi", anh Đăng nói. Nhưng thực tế khác xa. Chị vợ kết thúc mối quan hệ với đồng nghiệp thì lại lén lút bên một người đàn ông khác.</p><p class="Normal"> 	"Tôi biết hết mọi việc nhưng cứ giả vờ như không, nghĩ rằng, thôi đời này sống vì con. Nhưng càng ngày, tôi càng ghê sợ vợ, thậm chí đi làm chẳng muốn về nhà vì không muốn nhìn thấy mặt cô ta, mệt mỏi khi phải đóng vai hòa thuận, yêu thương trước mặt con", anh nói. Cuối cùng, anh quyết định ly hôn để giải phóng cho vợ và cho chính mình. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943041204801857.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>Motivational tips.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span style="color:#696969;"><strong>Cũng vì không thể</strong></span><span><span style="color:#696969;"><strong> đáp ứng nhu cầu của vợ mà anh Thành (Sóc Sơn, Hà Nội) đã bật đèn xanh cho bà xã đi ra ngoài</strong></span> khi thấy chị hay đá thúng đụng nia, thậm chí có lần đòi ly dị. Ban đầu còn e dè, giấu giếm, về sau, vợ anh quen mui, thường xuyên qua lại với người đàn ông khác mà không hề áy náy với chồng.</span></p><p class="Normal"> 	Ở cơ quan là sếp có nhiều người nể sợ nhưng khi về nhà, anh Thành lại chịu để vợ mắng xơi, có khi trước mặt con cái. Hai con dần lớn, th<span>ấy bố nhu nhược, biết mẹ có nhân tình, nên ngày càng tỏ vẻ bất cần đời, coi thường phụ huynh. </span></p><p class="Normal"> 	<span>Thạc sĩ Lã Linh Nga, Phó giám đốc Trung tâm nghiên cứu và ứng dụng khoa học tâm lý - giáo dục (Hà Nội) cho biết, không ít nam giới vì yếu sinh lý mà trở nên mất hết tự tin, nhu nhược trong gia đình. Họ thấy mình có điểm yếu nên cố nhún, im lặng khi vợ phàn nàn hay thịnh nộ. Sự lảng tránh đó không giúp xoa dịu vấn đề mà càng khiến người vợ cảm thấy ức chế dẫn đến gây sự, làm bầu không khí gia đình thêm căng thẳng.</span></p><p class="Normal"> 	<span>Theo nhà tâm lý, thực tế cho thấy, trong các gia đình mà người phụ nữ thể hiện quyền hành, lấn át và coi thường chồng thì hôn nhân khó bền và con cái họ dễ phát triển lệch lạc, khó xây dựng mối quan hệ thành công.  </span></p><p class="Normal"> 	<span>Chuyện phụ nữ nhắm mắt cho chồng ngoại tình hay đi "ăn bánh trả tiền" khá nhiều nhưng số đàn ông làm ngơ để vợ qua lại với người khác khá hiếm. Lý do một phần do phụ nữ Việt vốn quen được dạy phải chịu đựng, hy sinh vì gia đình. Phần khác, nam giới có cái tôi rất cao và sự ghen tuông của họ nhân lên nhiều lần khi bạn đời ngoại tình thể xác. T</span><span>heo m</span><span>ột nghiên cứu với 64.000 người đăng trên tạp chí <em>Archives of Sexual Behaviour </em>năm 2016, mức độ khó chấp nhận của đàn ông<a href="http://giadinh.vnexpress.net/tin-tuc/to-am/dan-ong-ghen-gap-doi-neu-vo-ngoai-tinh-the-xac-3461607.html"> tăng gấp đôi</a> nếu vợ "ngoài luồng" về thể xác so với ngoại tình về cảm xúc.</span></p><p class="Normal"> 	<span style="color:#333333;"><strong>Chuyên gia nam học Nguyễn Bá Hưng, Bệnh viện Nam khoa và Hiếm muộn Hà Nội</strong></span>, cho biết, <span>anh từng gặp một số trường hợp chồng bất lực hay vô sinh chấp nhận cho vợ quan hệ với đàn ông khác để kiếm con. Có những người vợ chỉ làm việc này một lần vì bất đắc dĩ, khi không đủ kinh tế để thực hiện hỗ trợ sinh sản. Tuy nhiên, cũng có người không cưỡng được cám dỗ về dục vọng, trượt theo mối quan hệ ngoài luồng khiến gia đình tan vỡ.</span></p><p class="Normal"> 	<span>Bị rối loạn nhiễm sắc thể giới tính nên ít ham muốn và khó có con, anh Hữu đang cùng vợ chữa vô sinh. Sau một thời gian tự dưng ngừng điều trị, anh quay lại viện nhưng không phải theo lịch hẹn kiểm tra mà để chữa bệnh hoa liễu. Anh Hữu kể, biết mình không thể mang tới hạnh phúc phòng the cho vợ, anh vờ như không biết khi chị qua lại với người đàn ông khác. Thế nhưng gần đây, anh không thể giả mù giả điếc nữa khi phát hiện bị lây bệnh tình dục từ vợ. </span></p><p class="Normal"> 	"Tôi vẫn cố muốn có một đứa con với vợ để bố mẹ an lòng nhưng cuộc sống thế này thì thực sự quá mức chịu đựng", anh Hữu tâm sự với bác sĩ. </p><p class="Normal"> 	<span>Theo các chuyên gia, việc chấp nhận để vợ ngoại tình không bao giờ là một giải pháp tháo gỡ trục trặc chăn gối, mà thường sẽ đưa đến nỗi đau cho người trong cuộc cũng như kết thúc đau lòng cho hôn nhân. </span></p><p class="Normal"> 	<span>Hiện nay, khoa học tiên tiến có nhiều biện pháp về cả tâm lý lẫn y khoa giúp </span><span>hỗ trợ các trục trặc phòng the. Vì thế vợ chồng khi gặp tình huống này, tốt nhất nên trò chuyện để cùng nhau tìm cách tháo gỡ. Hãy đi khám xác định rõ nguyên nhân, hợp tác với các chuyên gia để trị liệu. Việc không giải quyết tận gốc vấn đề, cố lảng tránh hay tìm người thứ ba chỉ làm vấn đề phức tạp và nhen lên các xung đột, ức chế khác. </span></p><p class="Normal" style="text-align:right;"> 	<strong>Vương Linh</strong></p>', 1, '1500194304848675346.jpg', NULL),
(162, '9 kiểu diện đồ của đàn ông phụ nữ nào cũng thích', 'Phụ nữ thích đàn ông xắn tay áo lên khi diện sơ mi, hoặc áo len cổ chữ V', '<p class="subtitle"> 	<strong>Một đôi giày tốt</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943061884610273.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Một đôi giày tốt là yếu tố thiết yếu nhất của bất kỳ trang phục nào, nó cũng nói lên nhiều điều về con người đó. Các cô gái sẽ có ấn tượng tốt nếu như đôi giày của đàn ông sạch sẽ, phù hợp với màu quần áo đang mặc.</p><p class="subtitle"> 	<strong>Áo Henley</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943061610930018.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Một chiếc áo với phần cổ có cúc, có thể phanh ra, không bao giờ lỗi mốt. Đây là loại áo yêu thích của nhiều ngôi sao Hollywood và các vận động viên nổi tiếng.</p><p class="subtitle"> 	<strong>Áo trắng, quần jeans</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943061677472981.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Cách phối đồ tưởng như đơn giản đã hút hồn bao phụ nữ. Phong cách này khiến đàn ông trở nên nam tính và hấp dẫn hơn.</p><p class="subtitle"> 	<strong>Áo len cổ chữ V</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943062088973771.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Nhiều phụ nữ rất thích đàn ông diện áo len kiểu này. Họ nói cảm giác như người đàn ông ấy rất tốt bụng, biết cách chăm sóc bản thân.</p><p class="subtitle"> 	<strong>Tay áo cuộn lên và cà vạt mỏng</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194306746350842.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Cách ăn mặc này khiến phụ nữ khó rời mắt. Nó như muốn nói lên rằng: "Tôi là một người bận rộn, nhưng công việc không phải là đam mê duy nhất trong cuộc đời tôi".</p><p class="subtitle"> 	<strong>Bộ vest phù hợp</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943061474951101.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Một bộ vest cổ điển, theo phong cách giản đơn, không phá cách, không bao giờ khiến bạn đi ngược lại với thời trang. Bạn có thể diện nó trong nhiều trường hợp khác nhau. </p><p class="subtitle"> 	<strong>Các phụ kiện hấp dẫn</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194306641184381.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Những chiếc thắt lưng da, vòng đeo tay... là điểm nhấn cho bộ trang phục đang mặc. Nó cũng phản ánh cá tính của người đàn ông đó, khiến phụ nữ thấy họ luôn hấp dẫn, thu hút hoặc phủi bụi phá cách.</p><p class="subtitle"> 	<strong>Mũ</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194306334200843.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Khi nói đến phụ kiện, mũ thực sự là đồ sẽ khiến bạn nổi bật trong đám đông. Chọn một chiếc mũ hợp với khuôn mặt, trang phục khiến bạn toát lên vẻ riêng, và là trung tâm chú ý của các cô gái.</p><p class="subtitle"> 	<strong>Ăn mặc gọn gàng</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943061036052143.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Một người đàn ông diện quần áo xịn, phụ kiện thời trang bắt mắt nhưng nếu anh ấy không gọn gàng từ đầu tóc tới quần áo, sẽ không cô gái nào thèm để ý. Xuất hiện với vẻ ngoài gọn gàng, luôn là một bức thư giới thiệu tốt nhất với mọi người.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943062116271914.jpg', NULL),
(163, 'Gia chủ Hà Nội tốn tiền chữa thấm tường vì xây quá sát nhà hàng xóm', 'Nhà chị Bình bị thấm nước, mốc nhiều mảng vì tường phía ngoài không trát bị mưa xối xả nhiều ngày.', '<p> 	Cách đây 10 năm, gia đình chị Bình mua được mảnh đất rộng 60 m2 ở quận Hoàng Mai (Hà Nội). Chủ cũ đã xây một căn nhà cấp bốn rộng 30 m2 ở phía trước, sau nhà là sân, giếng nước.</p><p> 	Ở được hai năm, anh chị thương các con sống trong ngôi nhà lợp mái fibro xi măng nóng hầm hập nên quyết định cải tạo nơi ở. Phải đi vay mượn nên gia đình dự tính chỉ làm nhà 3 tầng có mặt bằng rộng 30 m2 ở phần sân sau. Phần nhà cấp bốn phía trước được giữ nguyên trong thời gian xây dựng nên không cần phải đi thuê chỗ ở.</p><p> 	Để tận dụng tối đa diện tích, chị Bình xây tường phía sau sát vào ngôi nhà một tầng của hàng xóm. Tường 220 mm dày, chắc chắn nhưng không trát, không sơn phía bên ngoài được.</p><p> 	Sau đó một thời gian, gia đình có thêm tiền nên phá bỏ căn cấp bốn phía trước và xây hoàn thiện cả ngôi nhà trên diện tích 60 m2. Ngôi nhà mới là bước đổi đời giúp gia đình thoát khỏi cảnh sống tạm bợ trước đây.</p><p> 	Tuy nhiên, việc xây dựng chắp vá, thiếu tính toán khiến gia đình gặp rắc rối với phần tường sau nhà. Vào mùa mưa lũ, những đợt mưa dầm thấm vào phần tường không trát. Không chỉ thế, những cơn mưa to khiến phần mái nhà hàng xóm thoát nước không kịp, nước cũng tràn vào tường nhà chị Bình.</p><p> 	Phần tường sau bắt đầu bị nước thấm, có những mảng mốc loang lổ. Gia đình phải chuyển tivi, tủ lạnh khỏi phía tường này vì lo ngại điện giật.</p><p> 	Ban đầu, chồng chị Bình tự mua sơn quét lại tường sau mỗi đợt mưa. Nhưng rồi có những bữa mưa xối xả lâu ngày khiến nước ngấm vào tường, chảy thành dòng xuống sàn nhà. Mảng nấm mốc được sơn đè lên trông vẫn nhếch nhác.</p><p> 	Tới lúc này, gia đình mới nhờ một kỹ sư xây dựng quen biết tới xem hộ và tìm giải pháp khắc phục. Tường được đập bỏ toàn bộ lớp vữa cũ, trát lại vữa mới, sơn chống thấm và ốp thêm một lớp gạch men mới kín tường. Chi phí toàn bộ cho việc khắc phục mảng tường rộng 16 m2 hết khoảng 7 triệu đồng.</p><p> 	Tạm yên tâm tường không bị nham nhở, ngấm nước nữa nhưng căn phòng trông cũng không được đẹp mắt. Cũng may đây là khu nấu nướng, kê bàn ăn nên không quá ảnh hưởng thẩm mỹ. Nhiều lúc ngồi trong phòng bếp mà chị thấy giống khu vệ sinh với kiểu ốp gạch này.</p><p> 	Với các ngôi nhà ống tương tự nhà chị Bình, KTS Việt Tùng khuyên gia chủ nên cẩn thận ngay từ khâu xây dựng để tránh tình cảnh ngấm nước. Khi đã xảy ra sự cố, việc chống thấm sẽ rất phức tạp và tốn kém, gây mất thẩm mỹ.</p><p> 	"Chủ nhà nên cố gắng trát và sơn chống thấm mặt bên ngoài tường nhà mình. Trong trường hợp nhà bên cạnh đã làm trước, bạn nên xây cách một khoảng để nước rơi xuống đất, không chảy trực tiếp vào tường. Trước khi xây nhà, bạn nên nhờ kiến trúc sư hoặc kỹ sư xây dựng tới xem trước hiện trạng để có giải pháp thiết kế phù hợp", KTS Việt Tùng gợi ý.</p><p style="text-align:right;"> 	<strong>An Yên</strong></p><p> 	<em style="margin:0px;padding:0px;color:rgb(51,51,51);">Chia sẻ kinh nghiệm xây, sửa nhà</em><em style="margin:0px;padding:0px;color:rgb(51,51,51);"> của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2" style="margin: 0px; padding: 0px; color: rgb(0, 79, 139); text-decoration-line: none; outline: 1px;">tại đây</a></em></p>', 1, NULL, NULL),
(164, 'Con đường ngắn nhất cho thấy bạn thiên tài trong lĩnh vực nào', 'Có nhiều con đường dẫn đến thành công, quan trọng là bạn chọn đúng con đường phù hợp với mình để đến đích nhanh nhất.', '<p> 	Bài trắc nghiệm trên trang <em>Siamok </em>sẽ tìm ra bạn thiên tài trong lĩnh vực nào. Theo đó, có 4 đường ống nước, nếu đổ nước vào cùng một lúc thì đường ống nào nhanh tới đích hơn:</p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td> 				<a title="" href="http://giadinh.vnexpress.net/tin-tuc/trac-nghiem/con-duong-ngan-nhat-cho-thay-ban-thien-tai-trong-linh-vuc-nao-3613250-p2.html"><div style="text-align: center"><img src="/pictures/news/15001943081774557966.jpg" /></div></a></td> 			<td> 				<a title="" href="http://giadinh.vnexpress.net/tin-tuc/trac-nghiem/con-duong-ngan-nhat-cho-thay-ban-thien-tai-trong-linh-vuc-nao-3613250-p3.html"><div style="text-align: center"><img src="/pictures/news/15001943081638581723.jpg" /></div></a></td> 			<td> 				<a title="" href="http://giadinh.vnexpress.net/tin-tuc/trac-nghiem/con-duong-ngan-nhat-cho-thay-ban-thien-tai-trong-linh-vuc-nao-3613250-p4.html"><div style="text-align: center"><img src="/pictures/news/15001943082022305457.jpg" /></div></a></td> 			<td> 				<a title="" href="http://giadinh.vnexpress.net/tin-tuc/trac-nghiem/con-duong-ngan-nhat-cho-thay-ban-thien-tai-trong-linh-vuc-nao-3613250-p5.html"><div style="text-align: center"><img src="/pictures/news/150019430892965847.jpg" /></div></a></td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Bảo Nhiên</strong></p>', 1, '15001943081932756143.jpg', NULL),
(165, 'Cái kết có hậu của đôi trẻ ''trai lớn gả chồng''', 'Chưa từng nghĩ sẽ lấy một cô gái vì sợ làm khổ cả hai, Lương mãn nguyện khi được mẹ đồng ý đi hỏi cưới một chàng trai cho mình.', '<p class="Normal"> 	Tăng Quốc Lương, 31 tuổi, là một chàng trai người Hoa đồng tính đang sống tại TP HCM. Bạn bè biết giới tính của Lương từ lâu, nhưng anh vẫn giấu gia đình. Vì thế, cả nhà rất sốc khi phát hiện Lương yêu một chàng trai mà lúc đầu họ chỉ nghĩ là cậu bạn thân thiết. Mối tình đầu của Lương bị phản đối. Mẹ khóc lóc, ngăn cản còn Lương thì luôn trong tâm trạng dằn vặt vì đã làm mẹ buồn khổ.</p><p class="Normal"> 	Để mẹ không buồn, Lương lao vào làm ăn phát triển sự nghiệp. Nhờ những kinh nghiệm tích lũy được từ ngày nhỏ theo chân cha mẹ buôn bán khắp Sài Gòn khi gia đình còn khó khăn cho đến lúc gia đình khá giả, Lương thành công khi mở tiệm trong nhiều lĩnh vực, từ sửa xe, đồ inox đến nhà hàng. Thấy con trưởng thành, mẹ Lương không còn buồn vì chuyện giới tính của anh nữa. Càng hiểu, bà càng thương cậu con trai thứ hai của mình hơn.</p><p class="Normal"> 	Khi Lương 25 tuổi, anh đã được gia đình cảm thông hoàn toàn. Vì thế, mối quan hệ tình cảm của Lương với chàng trai Vĩnh Long kém mình 4 tuổi là Nguyễn Văn Minh nhận được sự ủng hộ từ phía gia đình.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943091715413403.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh cưới tại Đà Lạt của Lương và Minh - Ảnh: Jacob vs Duy Khanh Nguyen</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Minh cũng từng khiến cha mẹ buồn khi họ phát hiện ra giới tính thật của cậu. May mắn, chị Minh có tư tưởng hiện đại, ra sức thuyết phục giúp cha mẹ hiểu, vì thế đến khi gặp Lương thì cậu không còn phải giấu giếm gia đình nữa. </p><p class="Normal"> 	Quen biết nhau qua Facebook từ năm 2013, chuyện tình của Lương và Minh cũng trải qua những giai đoạn làm quen - tỏ tình - yêu đương, cũng trải qua tất cả những cung bậc vui buồn như bao đôi lứa khác. Ngay từ đầu, Lương đã có thiện cảm với Minh nên sau mỗi status của Minh, Lương lại gây chú ý bằng một mặt cười. Suốt hai tháng, những mặt cười cứ liên tục xuất hiện, nhưng cả hai không hề bình luận hay nói chuyện dù đều tò mò về nhau.</p><p class="Normal"> 	Sau đó, biết Minh đưa mẹ từ Vĩnh Long lên TP HCM khám bệnh, Lương nhắn tin muốn gặp mặt. Trong khi Minh nghĩ đơn giản như hai người bạn gặp nhau thì Lương ngay lập tức đã bị "cảm nắng".</p><p class="Normal"> 	Vẫn còn đau khổ vì mối tình vừa tan vỡ trước đó vài tháng, Minh từ chối. Lương không bỏ cuộc, lấy cớ nhờ Minh làm người quản lý cuộc sống cho mình, hàng ngày gọi điện nhắc anh dậy, nhắc anh ăn, nhắc anh không thức khuya, nghe các tâm sự của anh... Nghĩ “người ta giỡn, thì cũng mình cũng giỡn cho vui”, Minh đồng ý. Mỗi ngày, “ông chủ” ở Sài Gòn đi đâu làm gì đều báo cáo cho “người quản lý” đang sống tại thành phố Vĩnh Long. Một tuần, khoảng ba lần, “ông chủ” đi cả trăm kilomet về thăm “người quản lý”. Có những lần, Lương lái xe về trong đêm rồi ngay sáng hôm sau phải quay lại Sài Gòn để giải quyết công việc.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943091295816880.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					<span>Lương (phải) và Minh trong một lần du lịch Sa Pa - Ảnh: NVCC.</span></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Sinh nhật Minh, Lương gây bất ngờ bằng cách mang bánh kem từ TP HCM, với lời chúc “Chúc mừng sinh nhật Quản lý”. Giây phút cắt bánh kem, thổi nến chỉ có hai người họ và một người thân nhưng giúp Minh nhận rõ sự chân thành của Lương dành cho mình. Vậy là sau lời thuyết phục “không làm người quản lý nữa, làm người yêu luôn cho rồi”, Minh quyết định tự mình vượt qua những băn khoăn về rào cản địa lý, về sự chênh lệch hoàn cảnh gia đình đã đè nặng bấy lâu.</p><p class="Normal"> 	“Ở Sài Gòn, những người thuộc thế giới thứ ba như mình không thiếu, vậy mà Lương vẫn lặn lội mỗi tuần để về thăm suốt hai tháng qua. Đó là gì nếu không phải là tình yêu và sự chân thành?”, cậu thầm nghĩ.</p><p class="Normal"> 	Sau đó, Minh nghỉ việc làm tóc ở Vĩnh Long, lên TP HCM cùng Lương kinh doanh, bắt đầu 4 năm yêu nhau với rất nhiều thăng trầm. Cùng là đàn ông, cùng có cái tôi rất lớn, cùng nóng nảy, những cuộc tranh cãi giữa họ diễn ra thường xuyên, đôi khi chỉ là từ một việc không đâu.</p><p class="Normal"> 	<span>Cách đây hơn một tháng, hai người lại tranh cãi, Minh quyết định ra nước ngoài làm việc để rời xa Lương mãi mãi. Đúng lúc đi khám sức khỏe, cậu đổ bệnh, sốt cao. Biết chuyện, ngay lập tức Lương về Vĩnh Long chăm sóc. Sự việc này khiến cả hai nhận ra sẽ thật sai lầm nếu họ để mất nhau trong đời. "Lấy anh nha", Lương ngỏ lời và Minh không thể từ chối. </span></p><p class="Normal"> 	<span>Nói về tình yêu của mình, Lương cho rằng, trong thế giới thứ ba, các mối quan hệ thường không bền, bị tác động nhiều bởi yếu tố ngoại hình và vật chất. Tình yêu giữa hai chàng gay dễ đồng điệu vì giống như hai thằng bạn thân hiểu nhau, nhưng cũng thường khó giữ vì người trong cuộc cùng là nam giới nên nóng nảy và cái tôi rất lớn. </span><span>Sở dĩ Lương và Minh đã cùng nhau đi được một chặng đường dài bởi giữa cả hai có tình yêu thật sự và sự chân thành. </span><span>Cả Lương và Minh đều thừa nhận họ đã phải tự thay đổi mình, mềm mỏng hơn, nhẹ nhàng hơn kể từ khi gặp nhau.</span></p><p class="Normal"> 	Nếu Lương đánh giá cao sự chân thành, thẳng tính, trong cái nóng nảy vẫn có những nét dễ thương của Minh thì Minh lại bị chinh phục bởi sự chân thành và tâm lý của Lương. Biết "vợ" tương lai không ưa cuộc sống xô bồ nơi phố thị, chỉ thích sự bình yên nơi thôn quê, Lương đã mua một mảnh vườn ở Vĩnh Long, định về đây lập nghiệp. Có điều, không quen công việc nhà nông, anh cứ lóng ngóng và đành bỏ cuộc.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943091865736271.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một hình trong bộ ảnh cưới của cặp đôi - Ảnh: Jacob vs Duy Khanh Nguyen</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Sau cuộc gặp mặt xin cưới đặc biệt, khi hai bà mẹ chẳng biết nói gì mà chỉ nhìn nhau cười, ngày 23 tới đây, đám cưới của hai chàng trai sẽ chính thức diễn ra. Lương không nói nhiều về dự định cuộc sống sau hôn nhân, chỉ tiết lộ họ không định có con hay xin con nuôi, sẽ cố gắng làm việc để kiếm nhiều tiền và đi du lịch cùng nhau thật nhiều. "Chẳng biết tương lai sẽ đi đến đâu, chỉ biết rằng tôi luôn trân trọng phút giây ở bên nhau", chú rể khẳng định.</span></p><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert" style="width:100%;"><tbody><tr><td> 				<p class="Normal"> 					<span>Luật Hôn nhân Gia đình 2014 đã bãi bỏ quy định “cấm kết hôn đồng giới” của Luật Hôn nhân Gia đình trước đó. Kể từ khi luật này có hiệu lực (ngày 1/1/2015) đã có cả chục đám cưới đồng tính được tổ chức tại nhiều tỉnh thành ở Việt Nam, đặc biệt khu vực phía Nam.</span></p> 				<p class="Normal"> 					Dù không ngăn cản việc tổ chức đám cưới hay việc sống chung giữa các đôi đồng tính, nhưng Điều 8 - Điều kiện kết hôn của Luật Hôn nhân Gia đình 2014 cho biết “<em>Nhà nước không thừa nhận hôn nhân giữa những người cùng giới tính".</em> Như vậy, các cặp đôi cùng giới vẫn không thể đi đăng ký kết hôn, không được cấp chứng nhận kết hôn.</p> 			</td> 		</tr></tbody></table><p align="right" class="Normal"> 	<strong>Kim Anh</strong></p>', 1, '15001943091724126824.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(166, 'Chuyện đau lòng sau hình ảnh cậu bé nằm ướp đá trong thùng xốp', 'Em bé Trung Quốc qua đời không phải bị bắt cóc, cũng không phải do mẹ bất cẩn như những lời đồn đại trước đó.', '<p class="Normal"> 	Những ngày gần đây, cư dân mạng Trung Quốc chia sẻ nhiều video ghi lại cảnh một người phụ nữ đang lấy hết đá ra khỏi thùng xốp, bên trong là một bé trai nằm bất động.</p><p class="Normal"> 	Nhiều người không hiểu rõ chuyện đã lan truyền thông tin bé bị bọn buôn người bắt đi và được lực lượng chức năng phát hiện. Có thông tin lại nói mẹ bé bất cẩn không để ý con nằm trong thùng xốp nên đưa thẳng vào kho đông lạnh.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943101216103627.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Nhưng sự thật hoàn toàn không phải vậy.<span style="text-align:center;"> </span><span>Tờ <em>Chinanews</em> cho hay, theo thông tin từ cảnh sát thị trấn Ích Dương, tỉnh Hồ Nam, Trung Quốc, cậu bé sống tại một ngôi làng trên núi ở huyện Đào Giang. </span><span>Nhân dịp nghỉ hè, cậu bé đến chơi nhà họ hàng ở thành phố Quảng Châu không may bị chết đuối. </span></p><p class="Normal"> 	<span>Thời tiết nắng nóng, sợ thi thể nhanh bị phân hủy nên gia đình đã cho bé vào nằm trong thùng xốp đầy đá lạnh, đưa về quê mai táng.</span></p><p class="Normal"> 	<span>Hình ảnh trong video ghi lại cảnh mẹ em vừa bốc đá ra khỏi thùng, vừa khóc không ngừng vì nỗi đau mất con.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '1500194310929369119.jpg', NULL),
(167, 'Nghèo nhưng cố xây nhà to khiến tôi 40 tuổi vẫn tay trắng', 'Nhà hoành tráng nhưng ít người ở, tôi lại không có tiền để làm ăn và thậm chí là chi tiêu, anh Quảng (TP HCM) chia sẻ.', '<p> 	<em>Dướ</em><em>i đây là chia sẻ của anh Xuân Quảng, hiện sống tại TP HCM về những sai lầm khi xây nhà không phù hợp với hoàn cảnh gia đình.</em></p><p class="Normal"> 	Bố tôi trước khi mất có chia cho các con mỗi người một ít tài sản. Tiền bố cho tôi đem mua một mảnh đất rộng khoảng 120 m2 trong một hẻm nhỏ ở quận Tân Phú. Tôi mua năm 2005, hết 50 cây vàng, tính ra tiền mặt khoảng 500 triệu, định chờ lên giá sẽ bán đi vì tôi vẫn sống cùng mẹ trong một ngôi nhà cấp bốn có gác lửng trên diện tích đất 30m2. Các anh chị của tôi đều lập gia đình riêng, chỉ còn mình tôi ở với mẹ.</p><p class="Normal"> 	Năm 2010, tôi chuẩn bị lấy vợ, sẵn miếng đất bán mãi không được, tôi quyết định xây nhà vì cảm thấy ba người <span>sống trong cái nhà 30m2 kia sẽ rất chật</span><span>, chưa kể sau này chúng tôi còn sinh con. Hơn nữa, tôi đặc biệt không thích hàng xóm ở đây, dân trí thấp, nhậu nhẹt tối ngày, mỗi lần nhậu là một lần lấn chiếm hẻm nhỏ.</span></p><p class="Normal"> 	Tôi làm công việc thiết kế đồ họa nhưng không thích ngồi văn phòng nên thu nhập không cao, năm 2010, lương tôi chỉ khoảng 4-5 triệu, vừa đủ tiêu vặt. Mẹ tôi có lương hưu và có dãy nhà trọ nên bà vẫn cho tôi ăn miễn phí. Tôi vẫn ấp ủ ý định đứng ra kinh doanh riêng mà mảnh đất thì mãi không bán được. Thỉnh thoảng có việc lớn, tôi lại thế chấp mảnh đất này để vay vốn ngân hàng nhưng về cơ bản vẫn chưa thành công. Khi quyết định xây nhà trên miếng đất mãi không bán được, tôi chỉ có trong tay khoảng 100 triệu. Thiếu tiền xây nhà, tôi kêu gọi viện trợ từ mẹ.</p><p class="Normal"> 	Mẹ khuyên chỉ nên xây một nửa để ở, một nửa nên xây phòng trọ cho thuê, như thế tôi sẽ kiếm được vài triệu mỗi tháng, ngoài ra còn có tiền dư để kinh doanh. Tôi tất nhiên là không đồng ý vì rất ghét ở chung với người khác. Lúc tôi ngồi với ông thợ xây ước tính chi phí hết khoảng hơn 200 triệu, nhưng đến khi công trình hoàn thành, tính ra tổng chi phí đội lên hơn 500. Thiết bị nội thất nào tôi cũng muốn chọn đồ xịn. Ban đầu tôi định làm 2 tầng nhưng về sau tôi quyết định xây thêm một tum nhỏ trên tầng 3 để làm phòng thờ, làm sân phơi và đặt bộ bàn ghế đá để ngồi uống cà phê mỗi sáng.</p><p class="Normal"> 	Trên toàn bộ miếng đất 120m2, tôi để 30m2 làm hai khoảnh sân vườn phía trước và phía sau nhà. Tầng một gồm một phòng khách, một phòng ngủ dành cho mẹ và nhà bếp. Tầng 2 là hai phòng ngủ và một phòng làm việc, một phòng chơi thể thao nhỏ. </p><p class="Normal"> 	Có nhà mới, tôi cũng sắm thêm vài món đồ, hết khoảng 50 triệu nữa. Mẹ rút hết tiết kiệm cho tôi 200 triệu, số tiền thiếu tôi đi vay bà dì.</p><p class="Normal"> 	Ngôi nhà xây ròng rã 5 tháng mới xong. Sau đó nửa năm thì tôi cưới vợ, mẹ không thích về ở cùng, bảo để hai vợ chồng tự do. Bà cũng quen với hàng xóm ở căn nhà cũ rồi nên không muốn xa họ.</p><p class="Normal"> 	Cuối năm 2013, chúng tôi sinh con. Cho đến bây giờ thì bé vẫn ở chung phòng với bố mẹ. Vậy là dù xây nhà to nhưng thực tế gia đình tôi chỉ sinh hoạt ở phòng ngủ lớn, phòng làm việc. Hai phòng ngủ còn lại bỏ không. Bếp tuần nấu được vài bữa vì cuối tuần chúng tôi hay về nhà mẹ, sáng trưa ăn ở ngoài. Phòng khách cũng ít được sử dụng vì chủ nhà đi suốt thì lấy đâu khách đến. Tết vợ chồng tôi cũng đóng cửa về quê. <span>Phòng tập thể thao cũng bỏ không vì tôi cũng không thích tập ở nhà. Nhà tôi có ba cái toilet nhưng cả nhà thường chỉ dùng một cái.</span></p><p class="Normal"> 	Nếu kinh tế của chúng tôi khá giả thì cái nhà to cũng chẳng có vấn đề gì, nhưng thực sự vợ chồng tôi kiếm tiền chỉ đủ tiêu. Nhà to đồng nghĩa với hệ thống điện nước tốn kém, mỗi tháng chúng tôi tốn hơn triệu tiền điện và nước dù chỉ có một cái điều hòa ở phòng ngủ và cũng chỉ bật vài giờ lúc mới đi ngủ. Vợ chồng tôi bận mải công việc, 7 giờ tối mới về nhà, hôm nào lau dọn toàn bộ cái nhà thì 11 giờ đêm mới được nghỉ. Vì thế, chúng tôi thường chỉ dọn mỗi phòng ngủ và phòng làm việc nên nhà tương đối bẩn. Mảnh vườn nhỏ ngoài sân cũng để cỏ mọc hoang. </p><p class="Normal"> 	Giữa năm 2014, nhà dì có việc, tôi phải vay ngân hàng để lấy tiền trả cho dì. Tôi vay 4 năm, mỗi tháng cả gốc và lãi cũng 6-7 triệu nên vợ chồng càng bí tiền, nhiều lúc hục hoặc với nhau. Thiếu tiền nên dù lớn tuổi mà chúng tôi cũng không dám sinh thêm con. </p><p class="Normal"> 	Đến bây giờ thì tôi đã trả gần hết khoản nợ ngân hàng, nhưng thú thực tiền để làm ăn thì không có. Không yêu công việc mình làm nên tôi mãi không thăng tiến được, cũng chuyển việc vài ba chỗ, 40 tuổi mà vẫn còn là ''lính quèn''. Tôi đang dành thời gian đi học thêm về kinh tế và bất động sản. Vợ chồng tôi đang tính bán nhà, mua căn nhỏ hơn để ở, lấy tiền dư đi làm ăn. 40 tuổi khởi nghiệp kể cũng hơi muộn nhưng tôi không muốn cuộc sống cứ luẩn quẩn vì thiếu tiền nữa.</p><p class="Normal" style="text-align:right;"> 	<strong>Xuân Quảng</strong></p><p class="Normal"> 	<em style="margin:0px;padding:0px;">Chia sẻ kinh nghiệm xây, mua nhà và tiết kiệm của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2" style="margin: 0px; padding: 0px; color: rgb(0, 79, 139); text-decoration-line: none; outline: 1px;">tại đây</a></em></p>', 1, NULL, NULL),
(168, 'Cô chủ Hà Nội đổi đời sau 9 năm tiếc chồng không dám ly hôn', '"10 năm hôn nhân, 9 năm lo giữ chồng và tôi đã thất bại. Nhưng thất bại đó đã khiến đời tôi sang trang", chị Trâm kể.', '<p class="Normal"> 	"Có thể mọi người sẽ không dám kể câu chuyện ly hôn của mình vì nghĩ đó là sự xấu hổ, là sự thất bại, là nỗi đau không muốn nhắc lại. Nhưng tôi muốn chia sẻ với các phụ nữ cùng cảnh ngộ để họ không phí hoài 9 năm qua như mình<span>", chị Ngọc Trâm, 32 tuổi, ở Hà Nội, bắt đầu câu chuyện.</span></p><p class="Normal"> 	Chị và chồng cũ kết hôn sau 4 năm yêu. Ngày ấy, anh có một cửa hàng nhỏ chuyên về thẩm mỹ, nên sau cưới chị toàn tâm toàn ý làm cùng chồng. Chị quản lý học viên, lo quảng cáo, mời khách, bận rộn từ sáng sớm đến tối khuya.</p><p class="Normal"> 	Sau khi sinh bé gái đầu tiên năm 2005, chị lại càng bận, một tay chăm con, một tay lo cửa hàng. Đó cũng là thời điểm bạn bè nói thấy chồng chị hay đi với người khác. Ban đầu chị không tin, đến khi mọi người nói nhiều quá, chị phải nhờ đến thám tử mới tìm sự thật. </p><p class="Normal"> 	"Tôi chìm trong tuyệt vọng nhưng không đánh ghen vì nghĩ không giải quyết được gì. Tôi cho rằng lỗi không phải của cô ấy. Suốt một tháng sau đó, tôi không thể ngủ nổi. Những hình ảnh đó dằn vặt tôi ngày đêm", chị Trâm kể.</p><p class="Normal"> 	Không muốn gia đình đổ vỡ khi con còn quá nhỏ, lại vẫn còn yêu chồng nên khi anh xin tha thứ, chị quyết định bỏ qua. Gia đình hạnh phúc trở lại trong một thời gian ngắn. <span>Nhưng rồi chuyện cũ lặp lại, kể cả khi chị sinh con thứ hai. Chị kể đã k</span><span>hông biết bao nhiêu lần mua đơn ly hôn nhưng lại không đủ can đảm để viết vì sợ xì xào của mọi người, sợ người ta nói chị không biết cách giữ chồng, sợ con phải sống không có cha… </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943111711377117.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chị Trâm kể mình từng bị trầm cảm vì nghĩ quá nhiều về chuyện của chồng. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Chị từng bế con về nhà ngoại, nhưng thấy bố mẹ buồn rầu, lo lắng cho mình, thấy tương lai bấp bênh phía trước, chị quyết định quay về, quyết xây dựng sự nghiệp riêng, để nếu có phải ra đi một lần nữa, chị có thể tự mình nuôi con. Chị bán xe ôtô của hai vợ chồng, chia đôi tiền rồi mở cửa hàng riêng. </span><span>Chị cũng theo các khóa học làm đẹp như vẽ lông mày, làm mí, trang điểm... để nâng cao tay nghề.</span></p><p class="Normal"> 	"Hai vợ chồng vẫn sống chung nhưng cuộc sống rất ngột ngạt. Anh ít ở nhà, mà nếu có thì tâm hồn cũng để ở nơi khác. Anh n<span>ói không muốn ly hôn, nhưng bản thân lại không thể hiện ra quyết tâm thay đổi", chị kể.</span></p><p class="Normal"> 	<span>Mỗi lần cảm thấy nỗi đau quá lớn, chị lại đi phẫu thuật thẩm mỹ. Chị đã </span><span>trải qua 6 lần dao kéo mà không hề dùng thuốc giảm đau, vì</span><span> "tôi muốn dùng nỗi đau thể xác khỏa lấp đi một phần đau đớn trong tâm hồn". </span></p><p class="Normal"> 	<span>"</span><span>Có thời điểm nó bị khủng hoảng đến mức mất trí nhớ, không nhớ một ngày phải làm gì, chỉ biết ôm con ngồi khóc. Tôi sợ con gái làm liều, nên lúc nào cũng phải nhờ người nhà ở bên để chăm sóc", mẹ đẻ chị kể lại.</span></p><p class="Normal"> 	<span style="color:#666666;"><span><strong>Gần 10 năm lập gia đình, 3 lần bỏ về nhà ngoại và vô số lần dọa mang con bỏ đi, nhưng rồi chị Trâm lại trở về.</strong></span></span><span> Chị vẫn cố, vẫn nuôi hy vọng bạn đời thay đổi. </span><span>Khi bản thân bế tắc cùng cực, năm 2014, chị tìm đến các bác sĩ tâm lý. Sau một thời gian nói chuyện, kể hết về những nỗi đau đang phải gánh chịu, chị cảm thấy nhẹ nhõm hơn rất nhiều. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943111040805985.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sau 9 năm sống trong hôn nhân ngột ngạt, chị Trâm mới dám tách ra khỏi chồng, tự mình mở cửa hàng kiếm tiền để nuôi con. Ảnh: <em>Tuệ Minh.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Và rồi chị hiểu ra rằng: "<span>Người đã muốn đi thì nên để họ đi, người cần thay đổi, không phải là chồng, mà là mình. Khi bản thân mình còn ấu trĩ như vậy, mình còn không yêu thương mình thì đừng mong ai yêu thương mình cả".</span></p><p class="Normal"> 	<span>Khi suy nghĩ đã thông suốt, chị quyết định đệ đơn ly hôn. Sau ly hôn, chị thực sự chú tâm vào việc đào tạo tay nghề, chăm lo cho cửa hàng. Chị chăm chút cho bản thân nhiều hơn, đi chơi cùng bạn bè, làm đẹp... Tinh thần thoải mái khiến mọi sự đều thuận lợi, trơn tru. </span><span>Sau hai năm, chị đã mua được hai căn nhà riêng, và thường xuyên cho hai con, gia đình nhà ngoại đi du lịch.</span></p><p class="Normal"> 	<span>Bà mẹ hai con chia sẻ, giờ nghĩ lại quãng thời gian đã qua, chị chỉ thấy có lỗi với các con. "Tôi thấy mình thật độc ác. Thời gian trước đây, tôi suốt ngày nghĩ ngợi chuyện của chồng, rồi mặt nặng mày nhẹ, giận cá chém thớt với con, khiến các con cũng buồn bã lây", chị Trâm nói.</span></p><p class="Normal"> 	Con gái chị, bé Mai, 13 tuổi, cho biết bé rất vui khi thấy mẹ cười nhiều hơn và vui vẻ hơn trước. Cô bé cảm nhận được tình yêu thương, sự quan tâm của mẹ mỗi ngày cho hai chị em. "Con không buồn vì bố mẹ ly hôn. Con chỉ muốn mẹ thật vui, không còn buồn bã và khóc nhiều như trước nữa", Mai nói.</p><p class="Normal"> 	Chị Trâm cho biết, ly hôn đã khiến chị thay đổi rất nhiều, đặc biệt là cách suy nghĩ. Chị không nặng lòng nghĩ về quá khứ, không trăn trở hướng về tương lai, mà tận hưởng cuộc sống hiện tại. Có nhiều người đàn ông tìm đến mong muốn ở bên cạnh chị, nhưng hiện giờ chị chỉ muốn sống cho mình và các con.</p><p class="Normal"> 	"Có lần đón con trai đi học về, hai mẹ con đứng giữa ngã ba. Con nói rẽ trái là về nhà mẹ, rẽ phải là về nhà bố.</p><p class="Normal"> 	Tôi hỏi: ''Con muốn về nhà ai?''.</p><p class="Normal"> 	Bé nói: ''Con muốn về nhà đợt trước cả nhà mình cùng ở''.</p><p class="Normal"> 	Tôi giật mình trước câu trả lời của thằng bé. Hóa ra chúng hiểu hết tất cả mọi chuyện, hiểu bằng trái tim ngây thơ và luôn thèm khát được sự yêu thương bao bọc của mẹ cha.</p><p class="Normal"> 	Tôi nói với con rằng: ''Mẹ sẽ dắt con đến nhà mới, nơi con sẽ chỉ thấy niềm vui, ở đó dù không có bố, con vẫn luôn được yêu thương đong đầy".</p><p class="Normal"> 	Mẹ chồng cũ của Trâm, hiện sống ở quận Thanh Xuân (Hà Nội), chia sẻ dù hai con đã ly hôn, bà vẫn rất quý con dâu cũ, có chuyện gì quan trọng trong nhà cũng gọi chị về tham khảo ý kiến. <span>"Tất cả mọi chuyện đều do lỗi của con trai tôi. Nó đã làm vợ con khổ rất nhiều. Tôi thương Trâm vì con dâu là người sống rất biết điều, con chịu đựng thời gian rất dài mới ly hôn. Đó cũng bởi cháu yêu thương và tôn trọng chúng tôi. Tôi tiếc khi mất đi cô con dâu như vậy. Tôi cũng mong hai đứa quay lại với nhau".</span></p><p class="Normal"> 	<em><span>* Tên nhân vật đã được thay đổi</span></em></p><p class="Normal" style="text-align:right;"> 	<strong>Tuệ Minh</strong></p><p class="Normal"> 	<em>* Chia sẻ hành trình vượt nỗi đau ly hôn của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003819/type/2">tại đây.</a></em></p>', 1, '1500194311663304648.jpg', NULL),
(169, 'Cô dâu tự tử vì chồng bác bỏ các xét nghiệm trinh tiết', 'Cô dâu mới 18 tuổi phải tìm tới cái chết vì chồng từ chối tin cô là trinh nữ, dù có tới 3 tờ xét nghiệm chứng minh.', '<p class="Normal"> 	<span>40 ngày sau cuộc hôn nhân sắp đặp, cô dâu Rajabbi Khurshed, 18 tuổi (ở Tajikistan) đã kết liễu đời mình bằng cách uống giấm quá liều.</span></p><p class="Normal"> 	Theo <em>Metro</em>, trước khi từ giã cõi đời, thiếu nữ 18 tuổi này nói với bố mẹ rằng chồng cô - Zafar Pirov (24 tuổi) - đã đưa cô tới hai cuộc kiểm tra trinh tiết khác, dù đã vượt qua vòng đầu. Mục đích là anh ta muốn được cưới người vợ thứ hai.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943121628949833.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					 Rajabbi và chồng trong ngày cưới.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Rajabbi </span>cảm thấy áp lực trước đòi hỏi quá đáng của chồng và "không thể chịu đựng thêm được nữa" nên mới tìm tới cái chết để giải phóng. <span>Mẹ của Rajabbi nói thêm con gái bà chưa từng có bạn trai hay quan hệ tình dục.</span></p><p class="Normal"> 	Thế nhưng người chồng tuyên bố Rajabbi đã đưa cho anh ta một bản xác nhận không còn trong trắng trong ngày cưới và đồng ý cho anh ta cưới vợ. Hiện người đàn ông này đang phải đối mặt với án tù 8 năm.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943121240469020.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cô dâu 18 tuổi chết 40 ngày sau đám cưới.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Chuyện kiểm tra trinh tiết khá phổ biến ở Tajikistan khi kết hôn vì tình dục trước hôn nhân là điều cấm kỵ tại đây.</span></p><p class="Normal"> 	Đã có gần 600 trường hợp xét xử liên quan đến các tranh chấp về trinh tiết ở Tajikistan trong năm 2014.</p><p class="Normal" style="text-align:right;"> 	<strong>Bảo Nhiên</strong></p>', 1, '1500194312331773607.jpg', NULL),
(170, 'Mua đồ thanh lý trên mạng dễ mang rác về nhà', 'Lần thứ 2 gọi thợ tới sửa chiếc tủ lạnh mua giá rẻ tháng trước, chị Liễu ngao ngán tự trách mình "chết vì tham".', '<p class="Normal"> 	Tháng 3/2017, thuê được một căn hộ ưng ý, c<span>hị Trần Thị Liễu (Cầu Diễn, Hà Nội) quyết định mua đồ gia dụng thanh lý để tiết kiệm. Lướt qua các diễn đàn mạng, chị mừng rơn khi thấy một gia đình đăng bán lại toàn bộ đồ đang sử dụng vì cần chuyển nhà gấp. Theo lời người bán, 4 thứ gồm máy giặt - tủ lạnh - tủ quần áo - kệ TV họ dùng 3 năm trong căn hộ chung cư nên hầu như còn rất tốt. Hiện tại, do mua căn hộ cao cấp đã đủ nội thất, họ muốn bán trọn bộ với giá rẻ. </span></p><p class="Normal"> 	"Mình nghĩ, người ở chung cư cao cấp chắc dùng đồ tốt, ảnh chụp đồ rất chi tiết, lại thấy họ nói năng tử tế qua điện thoại, sẵn sàng hỗ trợ thêm khoản gọi xe và tiền vận chuyển xa nên quyết định chuyển khoản luôn", chị Liễu kể. </p><p class="Normal"> 	Chị vội vàng như vậy vì tổng số tiền phải trả cho 4 món chỉ là 5 triệu đồng, chỉ bằng 1/3 giá tiền mua mới, đồng thời sợ chậm chân sẽ mất món hời vì thấy có nhiều người vào hỏi mua.</p><p class="Normal"> 	Mong đợi lúc mua bao nhiêu, chị thất vọng bấy nhiêu khi nhận hàng vì chiếc tủ quần áo trông bên ngoài rất mới nhưng bằng gỗ ép mỏng dính, các ngăn bên trong đều lỏng lẻo. Tủ lạnh và máy giặt đều rất cũ, không hề như trên ảnh. Phàn nàn với người bán, chị Liễu nhận được câu trả lời rằng họ đã nói rõ là đồ qua sử dụng, có thể trông hơi cũ nhưng vẫn dùng tốt. Nói qua nói lại không giải quyết được gì, chị đành tắt máy, hy vọng các món sẽ dùng ổn.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194313726640850.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ảnh minh họa: <em>Appliance Repair.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Thế nhưng, chiếc tủ lạnh dùng được nửa tháng thì không tỏa chút hơi lạnh nào. Chị gọi thợ tới sửa, tốn 500.000 đồng nhưng cũng chỉ hơn tuần sau nó phát bệnh cũ. "Sửa hai lần tốn 900.000 đồng, nhưng nếu không sửa mà mua mới thì lại mất vài triệu nữa", chị Liễu phân trần. </p><p class="Normal"> 	Chiếc máy giặt sau 2 tháng hoạt động cũng trở chứng: cứ xả đầy nước là đứng im, không chịu quay, vắt. Chị Liễu lại tốn thêm 400.000 đồng để thay van kéo xả nước. </p><p class="Normal"> 	"Đúng là bỏ thì thương, vương thì tội. Đồ đã mua rồi không thể trả lại nên chỉ cầu mong sau khi bỏ thêm đống tiền ra sửa thì nó dùng tạm được. Đó là chưa kể rước thêm bao phiền như cảm giác bực bội, mất thời gian chờ thợ sửa, đồ ăn bao lần hỏng hết...", chị Liễu kể. </p><p class="Normal"> 	Sau lần đó, chị cũng "cai" luôn tính ham mua đồ thanh lý trên mạng. </p><p class="Normal"> 	Ngân sách eo hẹp, muốn mua được những món đồ chất lượng nhưng có giá rẻ vì đã qua sử dụng, tiện lợi khi dễ dàng tiếp cận trên mạng... là các lý do khiến nhiều người mua đồ thanh lý qua mạng cho gia đình. Tuy nhiên, như trường hợp của chị Liễu, vì không tìm hiểu và xem xét kỹ, không ít người mua phải đồ kém chất lượng hoặc không phù hợp. </p><p class="Normal"> 	<span style="color:#666666;"><strong>Chị Linh Nga (Đống Đa, Hà Nội) cho biết, chị vừa phải vứt bỏ chiếc máy xay cầm tay mua thanh lý trên mạng</strong></span> sau khi đi sửa hai lần vẫn không chạy được. Con mới tập ăn dặm, muốn mua máy xay để tiện chế biến, chị đồng ý mua lại máy của một thành viên trên diễn đàn cho các bà mẹ. Người bán hẹn chị tới cơ quan họ lấy nhưng không tiện vào văn phòng nên đưa chị tới một hàng nước gần đó để cắm điện chạy thử máy. Thấy ổn, chị Nga mua luôn với giá 500.000 đồng nhưng về dùng tới lần thứ hai thì máy đơ, không chạy nữa. </p><p class="Normal"> 	<span style="color:#666666;"><span><strong>Chị Thanh (Phương Mai, Hà Nội) cũng rước phiền khi mua một chiếc giá sách thanh lý. </strong></span></span><span>Người bán đăng trên một diễn đàn xe cộ giới thiệu chiếc giá sách bằng gỗ tốt, muốn bán rẻ lại vì nhà sửa theo thiết kế mới. Thấy giá tốt, hình ảnh ổn, đúng thứ nhà mình đang cần, chị Thanh mua, chấp nhận tốn 200.000 đồng tiền vận chuyển. Khi nhận đồ, chị tá hỏa phát hiện chiếc giá sách trông cũ như đồ từ vài chục năm trước và quá to, không thể để vừa nhà, thậm chí chẳng thể khênh nổi lên cầu thang hẹp lên căn hộ tập thể ở tầng 5 của mình. </span></p><p class="Normal"> 	<span>"Lỗi tại mình không hỏi han kỹ và tới tận nơi xem. Gọi điện trả lại thì họ không đồng ý. Để chiếc giá dưới chân cầu thang không được, cho người lái xe họ không thèm nhận, còn đòi thêm tiền công chở đi vứt", chị Thanh nói.</span></p><p class="Normal"> 	<span style="color:#666666;"><span><strong>Anh Thành Trung, quản trị viên một diễn đàn mạng có mục thanh lý</strong></span></span><span> cho biết, chuyên mục này khá nhộn nhịp nhưng cũng hay xảy ra các tranh chấp giữa người bán và người mua. Ban quản trị cũng cố định đăng cảnh báo những người mua cần chú ý các chi tiết để tránh bị lừa hay mua phải món đồ không phù hợp, nhưng tình trạng này vẫn xảy ra thường xuyên. </span></p><p class="Normal"> 	<span>Theo anh Trung, để không rơi vào các tình huống mất tiền mua bực như trên, khi mua đồ gia dụng thanh lý nên chú ý: </span></p><p class="Normal"> 	- Không mua chỉ vì ham rẻ. Chỉ nên mua vì đó là mặt hàng mình thực sự cần và hiểu rõ.</p><p class="Normal"> 	- Nên đến tận nơi xem hàng kỹ lưỡng, nhất là các món lớn tiền, đồ gia dụng. Chỉ nhìn tận mắt, sờ tận tay và đôi khi cả dùng thử bạn mới phát hiện được các vấn đề. Cũng cần xem xét món đồ đó có vừa không gian nhà mình.</p><p class="Normal"> 	- Hạn chế mua đồ điện tử cũ vì độ rủi ro hỏng hóc mà không được bảo hành, trả lại rất lớn. </p><p class="Normal"> 	- Xem xét cẩn thận về người bán hàng, mức độ uy tín của họ trong các diễn đàn hay qua các thông tin trên Facebook. </p><p class="Normal"> 	- Thỏa thuận kỹ với người bán về khả năng trả lại khi sản phẩm không như giới thiệu, có vấn đề khi sử dụng. </p><p class="Normal"> 	- Cân nhắc tổng chi phí: tiền mua sản phẩm, phí vận chuyển, phí thuê người khuân vác...</p><p class="Normal" style="text-align:right;"> 	<strong style="text-align:right;">Hoàng Minh</strong></p>', 1, '1500194313223516467.jpg', NULL),
(171, 'Vườn rau thẳng đứng giữa sân bay Mỹ', 'Cây được trồng trong những ống nhựa thẳng đứng, không cần đất mà vẫn lên xanh tốt.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156668" data-component="true" data-component-type="video" data-component-value="156668" data-component-typevideo="2"></div></div> </div><p class="Normal"> 	Cũng như những người hay phải đi máy bay, chị Lamanda (sống ở Chicago, Mỹ) luôn cảm thấy bực bội khi bị hoãn chuyến. Tuy nhiên, có một chuyến bay bị hủy đã đem tới cho chị một trải nghiệm tuyệt vời. Chị có cơ hội khám phá khu vườn trồng trong ống nước ở sân bay O''Hara (bang Illinois, Mỹ).</p><p> 	Một góc hẻo lánh từng bị bỏ không của sân bay đã được khoanh vùng thành nơi trồng trọt. Một loạt những tháp cây làm từ ống nhựa trồng đủ các loại rau gia vị, cà chua, xà lách, đậu...</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943141428318324.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Dưới mỗi tháp cây đều ghi tên loại cây trồng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Mùa hè năm 2011, sân bay đã kết hợp với một công ty chuyên thi công vườn rau thiết kế vườn aeroponic (khí canh). Ở đây có 26 tháp với 1.100 lỗ trồng cây.</p><p class="Normal"> 	Hình thức trồng rau này không cần đất. Thay vào đó, rễ cây sẽ hấp thụ nước dinh dưỡng được bơm tuần hoàn liên tục. Nhờ đó, vườn không có sự lãng phí và không cần người tưới trực tiếp.</p><p class="Normal"> 	Theo <em>Climate Connection</em>, công nghệ này giúp cho cây có thể phát triển quanh năm, bất chấp điều kiện thời tiết bên ngoài. Nguồn thực phẩm được dùng cho các nhà hàng trong sân bay như rau húng quế trong pizza hay ớt trong món sốt.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194314695429116.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cây trồng trong nhà nhưng luôn xanh tốt.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Hành khách ở sân bay có thể đi tham quan hoặc ngồi thư giãn, thưởng thức bữa ăn ở sảnh liền kề.</p><p class="Normal"> 	"Khu vườn giống như một ốc đảo đem lại sự bình yên, thực phẩm sạch và đề cao sự phát triển bền vững. Luôn có những người đứng ở đây chụp ảnh và hỏi han mọi chuyện", ông Brad Maher, Giám đốc điều hành Thức ăn và Đồ uống của sân bay, cho biết.</p><p class="Normal" style="text-align:right;"> 	<strong>An Yên</strong><br> 	Ảnh: <em>Future Growing</em><br> 	Video: <em>ABC</em></p><p class="Normal"> 	<em><em><em>Chia sẻ hình ảnh căn nhà của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2">tại đây</a>.</em></em></em></p>', 1, '15001943142089144366.jpg', NULL),
(172, 'Các lối thoát hiểm nhất thiết phải có cho nhà ống', 'Các gia đình nên bố trí giếng trời, sân thượng thông thoáng và hạn chế kiểu khóa cần dùng chìa để mở.', '<p class="Normal"> 	Trong hầu hết các vụ cháy có thiệt hại về người ở Hà Nội, TP HCM, nạn nhân thường không thể thoát khỏi nhà do thiếu lối thoát hiểm. Trong khi đó, lực lượng cứu hỏa, cứu hộ lại rất khó tiếp cận với những công trình này, đặc biệt là nhà trong ngõ hẻm.</p><p class="Normal"> 	Phần lớn những ngôi nhà phố đều có chung đặc điểm bất lợi: Hẹp và sâu, diện tích nhỏ, ít mặt thoáng, sát với nhiều nhà cao tầng khác. Chủ nhà thường quan tâm tới diện tích sử dụng, sự phân bố các phòng... mà ít ai nghĩ đến phương án thoát hiểm trong trường hợp khẩn cấp.</p><p class="Normal"> 	Ngoài ra, do lo ngại về vấn đề trộm cắp, an toàn (tránh ngã trên cao), nhà phố thường được xây kín đáo với cửa, khóa nhiều lớp, che chắn mặt tiền bằng gạch hoa, lam, lưới, lồng sắt, cửa sổ có hoa sắt…</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194315891764694.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hoa sắt cửa sổ có tác dụng chống trộm nhưng cũng là con dao hai lưỡi - cản trở việc thoát hiểm. Ảnh: <em>Hà Thành</em>.</p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	Nhà phố thường khó thiết kế, lắp đặt thiết bị phòng báo, chữa cháy như các công trình lớn<strong>. Chủ nhà có thể áp dụng các giải pháp sau: </strong></p><p class="Normal"> 	- Nhà nên có ban công: Đây là lối thoát hiểm hữu hiệu và có tính thẩm mỹ, giúp che nắng, mưa. Nếu nhà cháy, các thành viên có thể mở cửa để ra ngoài kêu cứu. Nếu ban công không dùng lan can mà quây lam, lưới thì nên có ô cửa mở bằng bản lề, có khóa để mở khi cần.</p><p class="Normal"> 	- Nhà nên có sân thượng và giếng trời: Sân thượng là một khoảng trống lớn, thoáng giúp thoát hiểm hữu hiệu tương tự như ban công. Tuỳ vào địa hình cụ thể mà người bị nạn có thể sang nhà hàng xóm kế bên, hoặc chờ lực lượng cứu hộ tới. Giếng trời trong nhà phố giúp thông thoáng và thoát khói thẳng lên trên.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194316225418449.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhà phố nên có ban công để thoát hiểm khi có sự cố. Ảnh: <em>Hà Thành.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	- Với những nhà có nhiều hơn một mặt thoáng, nên bố trí cửa thoát hiểm ở phía sau hoặc bên hông nhà, phòng khi không thoát ra được ở cửa chính thì thoát ở cửa phụ. Các hệ thống chốt khóa đơn giản, dễ vận hành.</p><p class="Normal"> 	- Bố trí mỗi tầng có ít nhất hai lối thoát hiểm: Một lối ra cầu thang (lên hoặc xuống) và một lối thoát ra cửa sổ, ban công.</p><p class="Normal"> 	- Nên làm cửa có cánh đóng mở: Các loại cửa sắt kéo, cửa nhôm cuốn không thuận tiện cho việc thoát hiểm. Các loại cửa trong nhà như cửa phòng, mở ra ban công, sân thượng nên sử dụng chốt khóa đơn giản, dễ vận hành; nên dùng khóa bằng các loại chốt, giảm số cửa dùng chìa.</p><p class="Normal"> 	- Trang bị bình chữa cháy (bình CO2, bình phun bọt); thường xuyên kiểm tra các vị trí cửa, lối thoát hiểm; lắp đặt hệ thống báo cháy (nếu có điều kiện).</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943161767761114.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sân thượng cần lối lên thuận tiện, không bịt kín mọi bề. Ảnh: <em>Trọng Nhân. </em></p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>KTS Nguyễn Trần Đức Anh</strong><br> 	 </p>', 1, '15001943151826270991.jpg', NULL),
(173, 'Bé 2 tuổi bị tàu điện cán chết trong khu vui chơi mà không ai biết', 'Người dì của em bé Trung Quốc đứng ngay gần đó nhưng không phát hiện ra do mải mê xem mấy trò đằng sau.', '<p class="Normal"> 	Theo <em>Chinanews,</em> sự việc xảy ra tối 11/7 tại một khu vui chơi giải trí tỉnh Tứ Xuyên, Trung Quốc. </p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156624" data-component="true" data-component-type="video" data-component-value="156624" data-component-typevideo="2"></div></div> </div><p class="Normal"> 	Cậu bé 2 tuổi rất thích thú khi được một người thân trong gia đình cho ngồi lên chiếc tàu điện để đi vài vòng. Trong khi người phụ nữ trung niên đang mải mê xem mấy thứ xung quanh, em bé vẫn tha thẩn chơi quanh chiếc tàu.</p><p class="Normal"> 	Trong lúc không ai để ý, em bé đã trèo lên phần đường ray và bị tàu chèn qua người. Khi đó, có khá nhiều người lớn ở xung quanh nhưng không ai phát hiện ra sự việc. Cậu bé không kêu gào nên càng không ai để ý.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943171700369550.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Em bé trèo lên đường ray và bị tàu cán qua nhưng không ai để ý. <em>Ảnh: Chinanews.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Người thân sau đó vội vàng đi tìm cháu nhưng cô không nghĩ em bé bị kẹt dưới chiếc tàu. Mãi sau đó cậu bé mới được phát hiện ra. Bé được đưa đi cấp cứu nhưng đã tử vong.</p><p class="Normal"> 	Sự việc dấy lên hồi chuông cảnh tỉnh dành cho các bậc phụ huynh khi cho con đi chơi. Bố mẹ, người thân phải luôn để mắt trông chừng bé vì nguy hiểm có thể đến bất cứ lúc nào.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943161638115536.jpg', NULL),
(174, 'Chủ nhà dễ gặp họa với nhà ống không lối thoát', 'Để tránh bị dòm ngó, nhiều gia chủ làm lồng sắt, gạch lỗ bao bọc mặt tiền nên đường thoát hiểm duy nhất là cửa chính.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943181972316379.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngôi nhà bị cháy sáng 13/7 tại Hà Nội chỉ có một đường thoát là cửa chính. Ảnh: <em>Phạm Dự. </em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ngày 13/7, ngôi nhà của ông Văn Trọng Bộ ở quận Bắc Từ Liêm (Hà Nội) bị lửa cháy bao trùm khiến cả <a href="http://vnexpress.net/tin-tuc/thoi-su/tieng-keu-that-thanh-trong-can-nha-chay-khien-4-nguoi-chet-3612850.html">bốn người trong gia đình tử vong</a>. Khi vụ cháy xảy ra, những người sống xung quanh phải phá cửa khóa rất chặt mới vào được.</p><p class="Normal"> 	Theo lời hàng xóm, nhà có mặt bằng nhỏ, xây 4 tầng, mặt tiền có cửa sổ song sắt. Phần tum cũng được bao bọc kín. Bởi vậy, đường thoát hiểm duy nhất của các thành viên trong nhà là cửa ra vào tầng một.</p><p> 	<span style="color:#696969;"><strong>Thiết kế nhà ống với phần mặt tiền đóng kín như ngôi nhà trên đang rất phổ biến ở thành phố lớn.</strong> </span>Trước đây, nhiều gia đình có xu hướng làm ban công ở các tầng trên, nằm ở mặt trước nhà. Đó cũng là chỗ ngồi chơi ngắm cảnh, đặt cây trang trí.</p><p class="Normal"> 	Tuy nhiên, khi mật độ nhà cao tầng tăng, khoảng diện tích này bị bỏ phí, cửa luôn phải đóng kín hoặc kéo rèm để tránh bị dòm ngó. Nhà cũng trở nên tối và bí bức.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194318588938046.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Để đảm bảo an ninh và riêng tư, chủ nhà có thể dùng hệ lam bao kín nhưng nhất định phải mở được cửa. Ảnh: <em>Quốc Anh. </em></p> 			</td> 		</tr></tbody></table><p> 	Vì lẽ đó, các gia đình lựa chọn kiểu nhà lam gỗ, cửa sổ song sắt, gạch lỗ bao kín mặt trước. Cách thiết kế này giúp ánh sáng vào nhà, giảm bớt khói bụi, đảm bảo an ninh và riêng tư cho gia chủ.</p><p> 	Tuy nhiên, giải pháp này đồng thời biến ngôi nhà ống trở thành pháo đài không lối thoát khi cả bốn mặt của nhà bị bịt kín.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019431846734343.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngôi nhà có mặt tiền được xây gạch bao nhưng có các khoảng trống bên hông để thoát hiểm. Ảnh: <em>Giang Đoàn. </em></p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	Theo KTS Đoàn Bằng Giang, để tránh tình trạng biến nhà thành lồng kín khi có cháy, các gia đình nên áp dụng triệt để những điều sau:</p><p class="Normal"> 	- Các tầng đều phải có lối mở ra bên ngoài. Nếu chủ nhà sử dụng lam gỗ, song sắt, gạch lỗ ở mặt tiền... thì vẫn cần một khoảng đóng mở được, đủ chỗ cho ít nhất một người chui ra.</p><p class="Normal"> 	- Các nhà cần có giếng trời tạo ra sự thông thoáng và để hơi độc tản bớt. Bố trí được giếng trời phía sau là tốt nhất vì sẽ tạo thêm một mặt thoáng nữa cho nhà ống. Nhờ đó, chủ nhà cũng có thêm một lối thoát hiểm khi gặp sự cố.</p><p style="text-align:right;"> 	<strong>An Yên</strong></p>', 1, '1500194318261461847.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(175, 'May rủi như mua hàng nội địa Nhật', 'Mỗi lần nguồn nước bẩn hay những khi trời nồm, máy giặt vốn là hàng nội địa Nhật của nhà anh Đức lại "đình công".', '<p class="Normal"> 	Có bạn thân buôn bán đồ điện tử điện lạnh cũ, vợ chồng anh Đức (Hà Nội) thường mua sắm các đồ gia dụng đắt tiền trong nhà là hàng nội địa Nhật Bản. Từ thứ lớn như máy tính, điều hòa, tủ lạnh, máy giặt đến thứ nhỏ như quạt bàn, ấm siêu tốc...</p><p class="Normal"> 	Đây đều là hàng đã qua sử dụng, nhưng vẫn còn mới trên 50%, nhiều món mới tới 80-90%. Người bạn kiểm tra thấy ổn, vợ chồng anh mới lấy về. Hai vợ chồng đều không biết tiếng Nhật, nên nhờ bạn hướng dẫn vài chức năng cơ bản của sản phẩm rồi về dùng theo. "Nhiều chức năng mà người bán không giải thích thì vợ chồng mình cũng chịu, không dám động tới".</p><p class="Normal"> 	Sau một thời gian dùng, anh Đức cảm thấy mua hàng nội địa Nhật giống như đánh bạc, ăn may từng cái. Có món đồ nhà anh dùng rất thích như chiếc tủ lạnh 400 lít mua 14 triệu, chiếc quạt bàn đơn giản giá 400 nghìn đồng... chưa hề hỏng suốt 2 năm qua. Tủ lạnh bảo quản thức ăn tốt, còn quạt chạy êm hơn hàng mới của Việt Nam, và cơ bản cả hai đều tiết kiệm điện. Tuy nhiên, chiếc máy tính anh đi mua cùng với một người bạn, trong khi của người kia dùng vẫn tốt thì của nhà anh thường xuyên hỏng, liên tục phải đem đi sửa.</p><p class="Normal"> 	Ngoài ra, các sản phẩm nội địa Nhật vốn dùng cho người Nhật nên cũng đòi hỏi môi trường hoàn hảo. Chiếc máy giặt hiệu National cửa ngang anh mua 10 triệu thường xuyên đình công mỗi khi nguồn nước có vấn đề. Đặc biệt, mùa <span>nồm, trời mưa phùn, quần áo càng bẩn, càng cần giặt nhiều, càng cần máy giặt để vắt khô thì máy lại báo lỗi và dừng làm việc nhiều nhất. Có lần, anh tự lấy máy sấy để sấy khô bộ bo mạch điều khiển nhưng đa số là phải gọi thợ đến sửa. Mới dùng 30 tháng, anh đã phải gọi thợ 6 lần.</span></p><p class="Normal"> 	<span>"K</span><span>hi mua máy giặt, vợ chồng tôi được đưa một quyển hướng dẫn sử dụng, khi máy có vấn đề, không chạy sẽ có số hiện ra, tra là biết phần nào trục trặc nhưng </span><span>sửa thì phải gọi thợ", anh Đức kể và cho biết thêm m</span><span>áy giặt thời gian đầu chạy rất êm, nhưng giờ kêu rất to, bị nghẹt ở bộ phận xả do nước của nhà sử dụng có nhiều cặn và tạp chất đọng lại.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019431980036121.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chiếc máy giặt quá thông minh nhà anh Đức thường tự nghỉ khi nguồn nước bẩn hay không khí ẩm. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Anh Tùng (TP HCM) cũng đồng tình các sản phẩm nội địa Nhật rất kén môi trường và mua hàng bãi của Nhật cũng "hên xui". Gia đình anh Tùng lắp chiếc điều hòa Nhật 9.000 BTU vào năm 2015. "</span><span>Trong lô hàng khoảng 50 bộ, tôi lựa được một bộ Sharp sản xuất 2008, inverter, buổi tối thì được nhưng trưa nắng thì nhiệt độ điều hòa chỉ xuống đến 27 - 28 độ C là dừng, dù tôi đặt chế độ nào".</span></p><p class="Normal"> 	<span>Do các sản phẩm nội địa của Nhật tiết kiệm điện, thường dùng nguồn điện 100V - 110V nên anh phải mua một bộ chuyển đổi điện, tốn 500 nghìn. Đáng nói là việc chuyển đổi nguồn điện này khiến việc lắp đặt không được thẩm mỹ lắm. Vì thế anh chỉ "lắp điều hòa nội địa ở phòng ngủ, phòng khách vẫn dùng điều hòa liên doanh sản xuất tại Việt Nam cho đẹp". Chưa kể vì dùng nguyên vật liệu tốt nên cục nóng máy điều hòa nội địa nhà anh Tùng cũng nặng hơn hẳn chiếc sản xuất liên doanh trong nước.</span></p><p class="Normal"> 	<span style="color:#333333;"><span><strong>Mua hàng nội địa Nhật mới tinh được xách tay về nhưng gia đình chị Bích (Hà Nội) cũng gặp những bất tiện nhất định</strong></span></span><span>. Năm ngoái, chị mua một robot hút bụi giá 5,6 triệu, mới dùng một hôm thì robot gặp vấn đề, người bán đến tận nhà sửa không được nên mang về, nói đổi lại hàng bên Nhật. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943192069039800.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bị hỏng 2 lần chỉ sau hơn chục lần dùng, con robot hút bụi Nhật trị giá 5,6 triệu đã bị cất vào gầm giường nhà chị Bích. Ảnh: <em>T. An.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Đổi máy mới, nhưng chỉ được chục hôm dùng tiếp, robot lại ngừng chạy, vợ chồng chị loay hoay các kiểu, không biết sửa thế nào vì nhìn vào tờ hướng dẫn chỉ có tiếng Nhật, chứ không có tiếng Việt hay tiếng Anh. Người bán cũng không đổi hàng cho nữa, chỉ nói phải chờ đi sửa. Sau đó gọi mãi người này cũng không đến. Quá nản, sợ mất công mang ra tiệm không có ai biết tiếng, chị Bích đành bỏ xó con robot. Về sau, chị mua một cái máy hút bụi cơ sản xuất trong nước giá chỉ 2 triệu và "cảm thấy khỏe hẳn</span><span>".</span></p><p class="Normal"> 	<span style="color:#666666;"><span><strong>Anh Xuân Tiến, giám đốc một công ty phân phối và bảo hành điện lạnh tại TP HCM</strong></span></span><span> nhận xét: Trong mắt người tiêu dùng Việt Nam, hàng nội địa của Nhật Bản luôn được ưa chuộng. Nhiều người sẵn sàng bỏ ra nhiều tiền hơn để mua hàng Nhật đã qua sử dụng thay vì mua một hàng mới được sản xuất, lắp ráp trong nước. </span></p><p class="Normal"> 	Tuy nhiên, do hàng nội địa Nhật là hàng sản xuất cho người Nhật sử dụng, không dành cho xuất khẩu nên không thực sự phù hợp với điều kiện thời tiết khí hậu Việt Nam. <span>Nhật Bản thuộc xứ ôn đới, không khí khô thoáng trong khi Việt Nam lại là xứ nhiệt đới gió mùa, độ ẩm cao nên dễ khiến sản phẩm hỏng hóc. Ngoài ra, h</span><span>àng Nhật đòi hỏi nguồn điện, nguồn nước sạch và ổn định, phù hợp với môi trường không khí sạch.</span></p><p class="Normal"> 	Dù là hàng cũ hay hàng mới 100% thì những sản phẩm này đều không được bảo hành chính hãng, việc sửa chữa tương đối khó khăn, đôi khi không sửa được. Anh Tiến thừa nhận không phải thợ nào cũng có thể sửa được những sản phẩm nội địa Nhật Bản, những người tay nghề không cao thường rất lúng túng. Khi một bộ phận nào đó bị hỏng hóc, ở Việt Nam thường không có sẵn phụ tùng thay thế, đa số thợ sẽ lắp ghép các phụ tùng không đồng bộ hoặc là những bộ phận mà họ đã tận dụng được khi mua đồ đồng nát.</p><p class="Normal" style="text-align:right;"> 	<strong>Hoàng Anh</strong></p>', 1, '15001943191599390698.jpg', NULL),
(176, 'Nhà 38m2 thay đổi bất ngờ nhờ nội thất thông minh', 'Ngôi nhà chật chội, đi lại vướng víu biến thành không gian tiện nghi, đủ chỗ cho 4 người ngủ.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943211804162946.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ngôi nhà một tầng của một cặp vợ chồng ở Thành Đô (Trung Quốc) chỉ rộng 38 m2 với nội thất không đồng bộ, thiếu chỗ để đồ.</p> 			</td> 		</tr></tbody></table><table align="center" border="1" cellpadding="1" cellspacing="0" class="tbl_insert tplCaption" style="height:182px;"><tbody><tr><td> 				<div style="text-align: center;"> 					<div style="text-align:center"> 						<div style="text-align: center;"> 							<div id="video-156458" data-component="true" data-component-type="video" data-component-value="156458" data-component-typevideo="2"></div></div> 					</div> 				</div> 			</td> 		</tr><tr><td> 				<p class="Image"> 					Gia chủ quyết định cải tạo toàn bộ nhà với các kiểu nội thất thông minh. Nhờ đó, nhà đáp ứng được nhiều nhu cầu hơn mà vẫn gọn gàng. Sofa phòng khách có thể biến thành giường ngủ vào buổi tối, bàn trà thay đổi được độ cao. Tủ đồ trong phòng ngủ có thể di chuyển còn giường ngủ thêm được một chỗ nằm khi con còn nhỏ.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001943211636969555.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhà có tông màu trắng-nâu nhạt. Đồ nội thất được làm chủ yếu bằng mây tre, gỗ mộc mạc, không đánh bóng, đem tới cảm giác gần gũi.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194321840689777.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mọi chi tiết trong nhà đều được tính toán kỹ như đôn ngồi ở bàn ăn có thể cất dưới gầm bàn, ghế băng thiết kế gọn gàng. Các hốc tường biến thành tủ để bát đĩa, bày đồ trang trí.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943211522415569.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trong phòng bếp trước đây, hai người đứng cũng chật. Không gian mới có cửa sổ, hệ tủ bếp rộng, để được tủ lạnh, cất máy giặt.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194321418855026.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Một số mảng tường màu hoặc gối, thảm họa tiết giúp phòng ngủ thêm ấm cúng.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943212056793404.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Khu WC vẫn giữ kiểu bệ xí truyền thống nhưng được bố trí sạch sẽ, tiện nghi hơn.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019432138236570.jpg" /></div></td> 		</tr></tbody></table><p style="text-align:right;"> 	<strong>An Yên</strong><br> 	Ảnh:<em> 16 Hour</em></p><p> 	<em><em><em>Chia sẻ hình ảnh căn nhà của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2">tại đây</a>.</em></em></em></p>', 1, '15001943201692356545.jpg', NULL),
(177, 'Ông bố Sài Gòn dùng Binh pháp Tôn tử để dỗ con ăn', 'Dùng kế Man thiên quá hải, anh Long vừa đút con ăn vừa kể chuyện hào hứng: ''há miệng to nha'', thoáng cái đĩa thức ăn đã hết.', '<p> 	Chủ trương nuôi con trong môi trường tích cực nên anh<a href="http://giadinh.vnexpress.net/tin-tuc/to-am/anh-ky-su-sai-gon-dung-mot-cau-noi-hoa-giai-moi-bat-dong-voi-vo-3611412.html"> Đinh Ngọc Vỹ Long</a> (kỹ sư xây dựng ở Sài Gòn) không bao giờ dùng bạo lực hay đe dọa để ép con. <span style="color:rgb(0,0,0);">Dưới đây là 4 mẹo hay anh đã áp dụng để bé Soda ăn ngon trở lại khi có biểu hiện biếng ăn:</span></p><p class="Normal"> 	Bài chia sẻ này của anh Long lên Facebook cá nhân nhận được sự tán thưởng của nhiều cha mẹ đang nuôi con nhỏ.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194323950115741.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Công việc thường xuyên phải đi công tác nhưng anh Long vẫn cố dành thời gian cho con. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	<span style="color:#808080;"><strong>- Mẹo số 1: Bất chiến tự nhiên thành</strong></span></p><p class="Normal"> 	Có hôm Soda lười ăn, "mẹ Hòa mê li" mới để một dĩa trái cây trên cái bàn nhỏ vừa tầm với của Soda. Chỉ để đấy và không nói gì. Không "rắc thính câu like", không chào mời dụ dỗ ép buộc. Soda tha thẩn chơi trong nhà, thỉnh thoảng chạy lại nhón một miếng nho, rồi một lát sau quay lại măm thêm miếng xoài. Chẳng mấy chốc, đĩa trái cây sạch veo.</p><p class="Normal"> 	Mẹo này ít tốn công sức nhất và cũng khá hiệu quả.</p><p class="subtitle"> 	- Mẹo số 2: Man thiên quá hải</p><p class="Normal"> 	Đây là một trong "Tam thập lục kế", trong đó một vị quân sư mời nhà vua vào ngôi nhà của mình để đàm đạo. Ngôi nhà này thực chất là một cái thuyền khổng lồ, giúp nhà vua vượt biển mà không bị say sóng.</p><p class="Normal"> 	OK, giờ đến phần áp dụng cho Soda.</p><p class="Normal"> 	Có mấy hôm Soda biếng ăn do bị bệnh, "ba Long biết tuốt" vừa đút cho Soda ăn vừa kể một câu chuyện với giọng điệu vô cùng hào hứng: "Hôm nay ba đi làm, gặp một bạn hươu cao cổ. Ba hỏi bạn ấy đang làm gì thế. Hươu cao cổ nói là đang ăn lá cây. Rồi ba thấy bạn hươu cao cổ há miệng to, ăn rất giỏi. Bạn ấy cũng rất thích những em bé há miệng to và ăn giỏi. Soda cũng há miệng to nha". Có thể biến tấu một chút tùy theo từng hôm, thay thế "hươu cao cổ" bằng sư tử, bọ cạp (nói chung là nói những con vật mà Soda đã biết), nhưng tập trung mạnh vào từ khóa "há miệng to".</p><p class="Normal"> 	Lại tỉ tê một chút về cái từ "há miệng to" này. Số là ba Long đã kiên trì xây dựng khái niệm "há miệng to" cho Soda từ hồi tập ăn. Nếu gia đình có bố mẹ ở các vùng miền khác nhau, thì hãy cùng thống nhất từ khóa để dạy con nhé. "há miệng to", "há mồm ra"... Thống nhất được cụm từ nào thì dùng xuyên suốt cho bé luôn.</p><p class="Normal"> 	Lại nữa, khi Soda đang tập trung nghe câu chuyện và đột nhiên nghe đến cụm từ quen thuộc "há miệng to", cộng với việc thấy ba Long cũng đang há to như thể sắp ăn, thì vùng não điều khiển cái việc "há miệng to" nó sáng lên, gửi mấy bức tâm thư đến cơ miệng, thế là Soda bất giác "há miệng to" mà không hề chủ ý.</p><p class="Normal"> 	Cách này rất tốn noron (để bịa ra câu chuyện) và chỉ áp dụng khi bé biếng ăn bệnh lý thôi nha bạn, đừng lạm dụng nó.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194323370010177.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bé Soda đã tích cực ăn uống hơn nhờ những mẹo của bố mẹ. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	- Mẹo số 3: Dương đông kích tây</p><p class="Normal"> 	Bữa nọ mẹ Hòa làm món sinh tố chuối xoài, Soda cứ lắc đầu quầy quậy. Đột nhiên, một đám noron thần kinh lạ mặt sáng lên, khiến cho mẹ Hòa thông minh đột xuất. Mẹ Hòa nghĩ ra một diệu kế, rồi quay sang nói với Soda.</p><p class="Normal"> 	"Soda ơi, con không uống sinh tố, vậy 2 mẹ con mình mang sinh tố cho bạn sóc nhé".</p><p class="Normal"> 	Rồi 2 mẹ con xuống chỗ cái cây hay xuất hiện bạn sóc, mẹ Hòa cẩn thận để bình sinh tố bên bãi cỏ, rồi í ới gọi: "Bạn sóc ơi, bạn sóc đâu rồi. Xuống đây mà uống sinh tố nè. Bạn ấy đi đâu rồi nhỉ. Chắc là bạn sóc đang ở trong lùm cây kiếm mồi rồi". Nói chuyện vu vơ một hồi thì anh Soda chạy lại cầm bình sinh tố tu ừng ực như thể thèm khát lắm, khác hẳn với cái cậu chảnh chọe cách đây ít phút. Soda uống sinh tố xong rồi, chừa lại chút dưới đáy bình rồi nói: "Con chừa lại một chút cho bạn sóc".</p><p class="Normal"> 	Soda vừa uống sinh tố, lại được thêm một bài học về sự chia sẻ. Đây gọi là "nhất tiễn hạ song điêu".</p><p class="subtitle"> 	- Mẹo số 4: Bình mới, rượu cũ</p><p class="Normal"> 	Nếu bạn đã từng đọc qua tác phẩm "Tôi đã thấy hoa vàng trên cỏ xanh", thì sẽ biết nhân vật chính trong chuyện đã khăng khăng rằng: "Ăn cơm trong thau ngon hơn trong tô".</p><p class="Normal"> 	Ba Long cũng còn nhớ như in cái lần đầu tiên tự xới cơm ra đĩa, có miếng thịt ram và quả dưa leo mà cảm giác như ăn sơn hào hải vị. Ăn bằng thìa, nĩa trong đĩa thay cho ăn trong bát.</p><p class="Normal"> 	Hôm nọ mẹ Hòa thử cho Soda uống sinh tố trong… bình sữa, uống sữa trong… ly rượu, thấy Soda có vẻ hào hứng và uống được nhiều hơn thường ngày".</p><p class="Normal" style="text-align:right;"> 	<b>Đinh Ngọc Vỹ Long</b></p>', 1, '15001943222130232143.jpg', NULL),
(178, 'Nhà giàu Trung Quốc cho con du học từ thuở lên ba', 'Những đứa trẻ được cho đi du học với mục tiêu cải thiện tiếng Anh và mở rộng tầm nhìn.', '<p class="Normal"> 	Zhang Feiyu thậm chí chưa tròn 5 tuổi, nhưng cậu bé đang tìm hiểu về thế giới thông qua một hoạt động ngày càng phổ biến ở Trung Quốc: du học mầm non.</p><p class="Normal"> 	Cách đây vài ngày, cậu bé đã bay hơn 15 tiếng đồng hồ, cùng mẹ và em gái từ Thượng Hải đến nhà của người dì ở Texas (Mỹ). <span>Tại đây, Feiyu sẽ theo học một trường mẫu giáo trong 5 tháng để mở rộng tầm nhìn, trong khi mẹ và em gái sơ sinh sẽ tận hưởng kỳ nghỉ.</span></p><p class="Normal"> 	<span>Mẹ của Feiyu nói: "Tôi muốn con mình ra ngoài và khám phá thế giới. Tôi tò mò cu cậu sẽ chơi trong môi trường toàn các bạn nói tiếng Anh như thế nào".</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019432343101113.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Du học mầm non Trung Quốc được đánh giá là hoạt động tiềm năng.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Du học mầm non ngày càng phổ biến tại Trung Quốc vì một công đôi việc: trẻ được đi học còn phụ huynh kết hợp du lịch. S<span>ố trẻ em ở tuổi Feiyu đi học ngắn hạn ở nước ngoài không còn là chuyện hiếm.</span></p><p class="Normal"> 	"Ngày càng có nhiều học sinh mẫu giáo, hoặc trẻ em từ ba đến sáu tuổi, tham gia các chuyến du học. <span>Trong khi đó, các trường mầm non quốc tế nội địa cũng khai giảng nhiều khóa học song ngữ cho lứa tuổi mẫu giáo", ông </span><span>Zhang Jie, Giám đốc kinh doanh của một công ty du lịch ở Trung Quốc cho biết.</span></p><p class="Normal"> 	Chia sẻ trên <em>Scmp</em>, Zhang Jie, những đứa trẻ trong độ tuổi mầm non chiếm khoảng 10% số khách hàng đặt tour du lịch nước ngoài vào mùa hè này và con số đã tăng đáng kể so với năm ngoái.</p><p class="Normal"> 	Mỹ là điểm đến phổ biến nhất, theo sau là Anh. Mỗi chuyến du học như vậy cần đầu tư <span>20.000 - 30.000 nhân dân tệ (70 đến 100 triệu đồng). </span><span>Trên thực tế, giá cả có thể khác nhau rất nhiều, tùy thuộc vào điểm đến.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194323593526644.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Những đứa trẻ mẫu giáo đã được gia đình cho đi du học để mở mang tiếng Anh và hiểu biết thế giới.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Chị Maggie Xiao, một bà nội trợ Thượng Hải cũng quyết định đưa con gái Xie Lele, 6 tuổi, vào một trại hè của Mỹ trong mùa hè năm ngoái, khi biết một người bạn cũng làm điều đó. </span><span>Dù phải bỏ ra 400 USD mỗi tuần cho bé nhưng chị cảm thấy đáng đồng tiền bát gạo.</span></p><p class="Normal"> 	"Tôi đã có kế hoạch đi du lịch ở Mỹ và tôi nghĩ rằng đưa Lele đến một trại hè ở đây sẽ là một công đôi việc", Xiao nói. Vợ chồng chị đã quyết định sẽ cho con đi du học nên việc này là một trải nghiệm nên có.</p><p class="Normal"> 	Trong trại hè 3 tuần ở Mỹ, cô bé Lele<span> đi cắm trại từ 8 giờ sáng. Buổi chiều bé tham gia các hoạt động nghệ thuật, khoa học và ngoài trời.</span></p><p class="Normal"> 	<span>Chị Xiao cho biết con gái rất thích hoạt động này vì nó "rất Mỹ". Chỉ có 3 đứa bé châu Á trong 100 học sinh ở đó. </span></p><p class="Normal"> 	Theo ông <span>Zhang Jie,</span><span> có hai lựa chọn du học. Đ</span><span>ầu tiên là các trại hè kết hợp với các chuyến tham quan hoặc tham dự bài giảng của các chuyên gia giáo dục. </span><span>Thứ hai là trường mầm non ở nước ngoài mà trẻ em có thể tham gia trong thời gian ngắn, tương tự như trao đổi sinh viên. </span></p><p class="Normal"> 	Ông Andrew Chen, giám đốc một công ty tư vấn cho sinh viên Trung Quốc tại Mỹ, cho biết ngành công nghiệp này vẫn đang trong giai đoạn sơ khởi với chi phí cao, nhưng triển vọng tương lai rất khả quan.</p><p class="Normal" style="text-align:right;"> 	<strong><span>Bảo Nhiên</span></strong></p>', 1, '15001943231611698328.jpg', NULL),
(179, 'Ông chồng thành đạt khuyên nam giới ''sợ vợ, đừng dạy vợ''', '"Sợ vợ không phải là sợ vợ hành hung, trùm váy lên đầu mình mà là sợ vợ buồn. Vợ buồn thì nhà cửa chẳng còn vui", anh Tú viết.', '<p class="Normal"> 	<em>Bài viết dưới đây là chia sẻ của ông bố ba con Hoàng Anh Tú, một nhà văn có nhiều tác phẩm về tình yêu - gia đình, đồng thời là ông chủ vài nhà hàng lớn tại Hà Nội về những lý do đàn ông không nên "dạy" vợ.</em></p><p class="Normal"> 	<span>Cái thời đàn ông ảo tưởng sức mạnh với câu: "Dạy con từ thuở còn thơ/ Dạy vợ từ thuở bơ vơ mới về" đã xa lâu lắc rồi. Ông nào thời nay còn nghĩ đến chuyện dạy vợ thì chỉ có nước... cưới đàn ông về thôi.</span></p><p class="Normal"> 	Phụ nữ trải qua chừng đó thời gian "tiến hoá" như đàn ông cơ mà cánh mày râu thì bụng vẫn cứ một múi, ngồi vẫn cứ một đống quanh các hàng bia, vỉa hè.</p><p class="Normal"> 	Quan điểm về đàn ông thành đạt cũng khác đi nhiều. Thay vì kiếm tiền giỏi, chí khí muôn phương hay có danh có phận thì nay những anh được coi là thành đạt luôn phải gắn theo "rơ moóc" vợ con. <span>Chẳng người đàn ông nào được coi là thành đạt nếu suốt ngày bị vợ kêu ca trên Facebook.</span></p><p class="Normal"> 	Phụ nữ ngày nay chỉ cần khoe một tí, xinh một tí, thành đạt một tí là trên Facebook có cả nghìn người theo dõi, hàng nghìn like và nói ra câu gì cũng thành chân lý.</p><p class="Normal"> 	Kể cả cô ấy có không xinh, không kiếm tiền giỏi, không viết văn hay thì chỉ cần cô ấy kêu lên một tiếng về chồng chắc chắn cũng sẽ có hàng trăm like ủng hộ. Mạng xã hội luôn sẵn có một đạo quân ủng hộ nữ giới đông đảo.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943252132245479.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Anh Hoàng Anh Tú và vợ. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Thế nên, nói thật, các ông đừng mong dạy vợ. Hãy sợ vợ thay vì dạy vợ. Nếu các ông giỏi, nâng tầm sợ vợ lên thành nghệ thuật thì các ông còn được nhiều hơn cả thế nữa.</p><p class="Normal"> 	Tôi bảo là nên sợ vợ vì xét cho cùng, vợ mình mình sợ chứ có sợ vợ ông hàng xóm đâu nào? Có những bà vợ cả xóm đều phải sợ chứ riêng gì chồng. Thế nên sợ vợ chẳng có gì đáng sợ cả.</p><p class="Normal"> 	Đùa vậy thôi chứ sợ vợ không phải là sợ vợ hành hung mình, sợ vợ trùm váy lên đầu mình. Sợ vợ không phải là sợ sệt, sợ hãi mà là sợ vợ mình buồn. Vợ buồn vì mình thì nhà cửa chẳng còn vui. <span>Tan sở về nhà chẳng núp được vào đâu, chẳng tìm thấy nụ cười của vợ để gột rửa giùm khói bụi, mệt nhọc ngoài kia. Sợ vợ buồn là sợ không khí gia đình vì thế mà buồn theo.</span></p><p class="Normal"> 	Người đàn ông hô mưa gọi gió bao nhiêu ngoài kia rốt cho cùng về nhà vẫn cần dựa vào lòng vợ. Đó là người ta thương, là người ta tin. Sợ làm vợ buồn là vì thế. Đàn ông nên cân nhắc, nên dịu dàng, nên để tâm, gìn giữ và trân trọng. <span>Bởi vợ vui thì bao nhiêu cũng dốc lòng. Bữa cơm vì thế cũng ngon hơn. Cái áo cái quần cũng trở nên tinh tươm hơn. Thân thể và tinh thần đều tột cùng sảng khoái. Sợ vợ buồn là vì thế.</span></p><p class="Normal"> 	Sợ nữa là sợ cả những gã đàn ông "lang sói" ngoài kia. Cùng là đàn ông sao mà ta không hiểu? Đàn ông toàn lũ à ơi rắc thính vợ người.</p><p class="Normal"> 	Hôn nhân nào mà chả có đôi phen chớp tắt. Giữa đôi lần chớp tắt ấy, ai mà biết có kẻ thọc cái gậy vào bánh xe của mình? Có khi nào thèm quan tâm đến việc bánh xe tốt xấu?</p><p class="Normal"> 	Cái thói đùa nhau chuyện bậy, tay chân loằng ngoằng ấy đáng ghét nhưng chưa đáng sợ bằng cái thói an ủi, lắng nghe, tỉ tê tâm sự của những gã đồng nghiệp lãng mạn. <span>Vừa cãi nhau với mình xong, vợ đến cơ quan, gã đồng nghiệp đã ra chừng hiểu biết, rủ đi cà phê. Gió mưa lãng mạn, quán xá yêu kiều, lòng đang đầy tâm sự tha hồ trút, hỏi sao mà lòng không rung rinh, tim không xúng xính?</span></p><p class="Normal"> 	Trái tim phụ nữ vốn dĩ yếu mềm. Phụ nữ vốn dĩ hảo ngọt mà đàn ông thì sẵn cả tạ đường, yến mật. Mất vợ đôi phen.</p><p class="Normal"> 	Đừng trách người phụ nữ ấy là lẳng lơ. Không, họ chỉ là nhẹ dạ. Họ vẫn yêu chồng cơ mà cứ bị cô đơn ngay trong chính nhà mình thì bảo sao không ngã?</p><p class="Normal"> 	<span>Sợ. Sợ lắm! Sợ khiến vợ buồn và bị dụ dỗ. Chẳng ai nói trước được điều gì. Vợ mình vốn mạnh mẽ, nghiêm túc, chung thuỷ nhưng mình vô tình mà xô vợ ngã vào vòng tay kẻ khác đôi khi.</span></p><p class="Normal"> 	Tôi nghĩ, đàn ông nên học cách sợ vợ để gìn giữ hạnh phúc gia đình và người vợ nào cũng muốn chồng mình sợ mất mình như vậy.</p><p class="Normal"> 	Bởi không phải sao, một món đồ giá trị người ta mới sợ mất. Chứ đồ hỏng, đồ thừa, đồ vô giá trị thì ai sợ làm gì? Thế nên, sợ vợ chính là cho nàng nhận ra rằng nàng có giá trị với đời ta vậy.</p><p class="Normal" style="text-align:right;"> 	<strong>Hoàng Anh Tú</strong></p>', 1, '1500194325190855530.jpg', NULL),
(180, 'Bố mẹ sốc khi con một tuổi bị người giúp việc tát nhiều lần', 'Cậu bé một tuổi ở Trung Quốc bị người giúp việc tát, lấy khăn lau nhà lau vào mặt.', '<p class="Normal"> 	Theo <em>Chinanews</em>, sự việc xảy ra ở Hồ Nam, Trung Quốc. Hôm 7/7, t<span>heo dõi camera từ xa, chị Ding phát hiện người giúp việc tên Liu đang bạo hành cậu con trai một tuổi của mình.</span></p><p class="Normal"> 	Chị vô cùng tức giận khi thấy cô này đang lấy cây lau nhà lau vào mặt cậu bé ngồi trong xe tập đi.</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156472" data-component="true" data-component-type="video" data-component-value="156472" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> </div><p class="Normal"> 	Tiếp tục xem lại các video từ những ngày trước đó, chị Ding bàng hoàng khi thấy cô Liu còn lấy áo sơ mi buộc tay con, thậm chí tát vào mặt bé tới tấp. <span>Hai vợ chồng chị lập tức trở về nhà và thay người giúp việc, dù mới thuê từ cuối tháng trước.</span></p><p class="Normal"> 	Liu cho biết do tâm trạng không tốt nên mới tát bé. Còn cô buộc tay chân bé là do bé luôn kéo quần áo khiến mình khó chịu.</p><p class="Normal"> 	Video đang được chia sẻ khắp mạng xã hội Trung Quốc. Mọi người đều rất tức giận trước hành động vô nhân đạo của người giúp việc. Sự việc đồng thời cảnh báo các gia đình cẩn trọng trong việc thuê người chăm sóc con mình.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, NULL, NULL),
(181, 'Thùy Lâm: ''Tự tay nấu ăn cho các con là niềm vui''', 'Ở nhà, Thùy Lâm được 3 bố con gọi là ''nội tướng'' vì nấu ăn ngon, chiều khẩu vị gia đình.', '<p class="Normal"> 	Kể từ khi kết hôn, Thùy Lâm lui về hậu trường, chuyên tâm chăm sóc gia đình, dù đang ở đỉnh cao sự nghiệp. Mỗi lần xuất hiện trước công chúng, hoa hậu luôn bày tỏ niềm tự hào về gia đình nhỏ nhưng hạnh phúc của mình. Từ một người chỉ quen với ánh hào quang sân khấu, Thùy Lâm giờ đây đảm đang trong vai trò "nội tướng" của gia đình 4 thành viên.</p><p class="Normal"> 	Làm mẹ 2 con lại quản lý kinh doanh, hoa hậu vẫn tự tay giặt quần áo, đích thân chuẩn bị bữa ăn hàng ngày. Mỗi ngày cô thức dậy sớm, chuẩn bị bữa sáng cho cả nhà, đưa con đi học rồi ghé spa do mình làm chủ. Trưa, mỗi người ăn một nơi, nên buổi chiều Thùy Lâm thường tranh thủ về nhà sớm, nấu món khoái khẩu cho các thành viên. Hai nhóc tì nhà Thùy Lâm, Su 7 tuổi và Mon lên 4, đều rất quấn mẹ.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194326886284646.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bận rộn đến mấy, Thùy Lâm cũng đích thân chuẩn bị bữa cơm cho cả nhà.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Song cũng như nhiều phụ nữ hiện đại khác, Thuỳ Lâm phải nỗ lực cân bằng giữa công việc và gia đình. Để có thêm thời gian cho bản thân, Thùy Lâm chủ động sắp xếp lịch làm việc, sắm đồ gia dụng hiện đại làm trợ thủ đắc lực quán xuyến chuyện nội trợ. Ví dụ như tủ lạnh, phải đảm bảo tiêu chí lưu trữ thực phẩm tươi và đúng vị suốt cả tuần, đảm bảo bữa ăn ngon miệng cho cả nhà.</p><p class="Normal"> 	Mỗi tuần, hoa hậu dành buổi sáng chủ nhật thảnh thơi đi siêu thị mua thực phẩm cho cả tuần. Đồ ăn thức uống cho 7 ngày, rau và trái cây nhiều không xuể, được Thùy Lâm cất hết trong chiếc tủ lạnh Twin Cooling Plus dung tích lớn. Đôi khi mua sắm quá tay, cô phải dùng tính năng chuyển đổi nhiệt độ giữa ngăn mát và ngăn đông để mở rộng không gian lưu trữ thực phẩm.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943261811032822.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Thùy Lâm xem việc chuẩn bị bữa ăn hàng ngày cho gia đình là niềm vui.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Su và Mon thích ăn trái cây, nên Thùy Lâm muốn lúc nào cũng sẵn hoa quả trong tủ lạnh cho các con. Nhờ tủ lạnh sở hữu 2 dàn lạnh độc lập, duy trì nhiệt tối ưu 0-4 độ C và độ ẩm 70% nên thực phẩm, trái cây luôn tươi ngon đến 10 ngày. Bà mẹ 2 con cũng không lo thức ăn lây mùi của nhau, bởi tủ tích hợp công nghệ ngăn trao đổi khí lạnh từ ngăn mát sang ngăn đá.</p><p class="Normal"> 	Buổi chiều về sớm, bà mẹ 2 con thường tranh thủ ép nước trái cây trữ trong ngăn mát để cậu cả Su đi học về giải khát ngay. Su có thể dễ dàng lấy nước mát và đá viên ngay trước tủ lạnh, mà không cần mở cửa tủ hay nhón chân, với tay lên cao. Vì thích tính năng này, nên 2 nhóc tì nhà Thùy Lâm tự giác uống đủ nước mỗi ngày, không đợi mẹ nhắc.</p><p class="Normal"> 	"Tôi luôn cố gắng hàng ngày, để trở thành một người phụ nữ hiện đại giỏi giang trong công việc, nhưng vẫn khéo léo chăm sóc cho gia đình và người thân yêu. May mắn là ngày nay, phụ nữ hiện đại có đủ mọi thiết bị gia dụng hỗ trợ đắc lực trong việc nhà", Thùy Lâm chia sẻ.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194326766126015.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tủ lạnh nhà Thùy Lâm lúc nào cũng đầy ắp hoa quả sạch, chiều sở thích các con.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>An San</strong></p>', 1, '15001943262021844968.jpg', NULL),
(182, 'Thành phần ít ngờ của 7 loại đồ ăn đóng hộp quen thuộc', 'Trong một hộp sữa chua trái cây, phần hoa quả có khi chỉ chiếm 1%, còn lại là đủ thứ hương liệu và chất phụ gia.', '<p class="Normal"> 	Các đồ ăn đóng gói bạn mua ở cửa hàng thường chứa nhiều chất phụ gia. <span><em>Bright Side</em> đã chỉ ra thành phần thực tế bên trong những món đồ nhiều người thường mua. </span><span>Thông tin này không có nghĩa là bạn không bao giờ nên mua các sản phẩm này mà cho thấy chúng ta cần phải kiểm soát lượng tiêu thụ. Điều này đặc biệt quan trọng nếu bạn mua cho con ăn:</span></p><p class="subtitle"> 	Sốt cà chua</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943271431310288.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Bột cà chua cô đặc ở đây là dạng sốt đặc gồm cà chua và bột ngô. Lượng cà chua trong đó tùy thuộc vào chất lượng của loại sốt và dao động từ 6% tới 10%. Khi làm sốt cà chua, người ta thường cho thêm siro chế biến từ bột ngô để giúp bảo quản và tạo độ sệt.</p><p class="Normal"> 	<em>Lưu ý:</em> Khi chọn sốt cà chua, nên tìm loại có chất lượng cao. </p><p class="subtitle"> 	<span>Sữa đặc</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194327256757903.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	<span>Sữa đặc được làm bằng cách làm bốc hơi bớt chất lỏng từ sữa (1,5 lít sữa được khoảng 1 lít sữa đặc). Nó chỉ nên gồm sữa và chất béo động vật. Các nhà sản xuất thường giảm lượng sữa và sử dụng thêm các chất béo từ thực vật và chất làm dày để đạt được lượng chất béo và độ đặc như yêu cầu. </span></p><p class="Normal"> 	<span><em>Lưu ý</em>: Nếu bao bì lon sữa có ghi "sữa đặc có đường" thì khả năng nó được làm theo cách trên rất lớn.</span></p><p class="subtitle"> 	<span style="color:rgb(95,95,95);">Thanh cua</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194327664588997.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Loại thịt xay được sử dụng trong các thanh cua thường dùng các loại thịt cá rẻ tiền. Phần phi lê được làm sạch và dùng các cách khác nhau để loại bỏ mùi cá, sau đó xay mịn. Các thành phần khác như muối, đường, protein đậu nành, dầu thực vật, màu thực phẩm và chất phụ gia được trộn đều vào sau đó. </p><p class="Normal"> 	<em>Lưu ý</em>: Những thanh cua cấp đông có chất lượng cao thường không bị gãy khi bẻ cong và sẽ gắn chặt vào lớp giấy bọc dày. Nếu chúng dễ dàng bị gãy khi bẻ thì có lẽ trong thanh cua đó có lượng lớn bột ngô.</p><p class="subtitle"> 	Khoai tây chiên</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943271692772136.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Khoai tây chiên thường làm từ bột khoai tây hơn là củ khoai tây tươi. Bột gạo hay bột ngô thường được thêm vào. Chất a<span>crylamide - một chất gây ung thư, tích tụ trong quá trình xử lý nhiệt của khoai tây chiên. Một số loại khoai tây chiên còn chứa lượng hóa chất này cao trên mức cho phép. </span></p><p class="Normal"> 	<span><em>Lưu ý:</em> Khoai tây chiên làm từ củ khoai tây chứa nhiều dầu hơn bởi vì chúng được chiên, trong khi những loại làm từ bột khoai tây thì thường được nướng.</span></p><p class="subtitle"> 	Bỏng ngô</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/150019432781590635.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Ngoài các thành phần được đề cập trên, một số nhãn bỏng ngô còn chứa cả dầu cọ. Ngoài ra, si rô từ bột ngô cũng có trong nhiều loại sản phẩm này. Bỏng ngô còn chứa lượng đường fructose cao nhưng nhà sản xuất không ghi trên bao bì. </p><p class="Normal"> 	<em>Lưu ý</em>: Vì chứa nhiều đường và bột ngô, các chuyên gia khuyên không nên thường xuyên cho trẻ ăn bỏng ngô.</p><p class="subtitle"> 	Sữa chua trái cây</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943271253527043.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	Ngoài trái cây thực sự với lượng chỉ khoảng 1%-5%, thành phần mứt trái cây trong sữa chua còn chứa bột ngô, đường, hương liệu, màu thực phẩm và chất điều chỉnh độ axit. Bột ngô, gelatine, phụ gia làm dầy và nhựa thơm đều được sử dụng như chất ổn định. </p><p class="Normal"> 	<em>Lưu ý</em>: Sữa chua tốt nhất nên chứa toàn bộ là sữa và các vi khuẩn có lợi. Kem tươi và sữa tươi lên men cũng là các thành phần có thể chấp nhận được.</p><p class="subtitle"> 	Mứt dạng kem</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943271739506479.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	<span>Dầu thực vật, thường là dầu cọ, được sử dụng trong các sản phẩm này. Các thành phần khác bao gồm vani, chất nhũ hóa, chất làm dày và hương liệu. </span></p><p class="Normal"> 	Lưu ý: Đừng mua loại mứt có một lớp màng màu trắng vì nó thường chứa nhiều dầu cọ và ít bột cacao<span>. Các nhà sản xuất khuyên không nên ăn quá 2 thìa một ngày.</span></p><p class="subtitle"> 	Lời khuyên chung khi lựa chọn thực phẩm: </p><p class="Normal"> 	- Nguyên tắc chung: <span>Sản phẩm càng có ít thành phần thì càng tốt. Hãy nhớ rằng những chất thêm vào thường không hề có lợi cho sức khỏe. </span></p><p class="Normal"> 	- Tên gọi có thể tương tự nhưng các thương hiệu nổi tiếng thường cố gắng tuân thủ các tiêu chuẩn về chất lượng sản phẩm. Ở một số nơi, các công ty có thể tự đặt ra tiêu chuẩn cho mình, khác xa với yêu cầu từ các cơ quan chức năng.</p><p class="Normal"> 	- Hãy kiểm tra tỉ lệ đường trong sản phẩm. Đường có thể được viết với nhiều tên khác nhau trên bao bì như si rô (syrup), mật mía (<span>molasses), đường dextrose (dextrose), đường sorbose (sorbose) và các tên khác có kết thúc bằng ose. Nó có thể xuất hiện trên bao bì bằng các ký hiệu như E967, E954... nói chung đều bắt đầu với E9.</span></p><p class="Normal" style="text-align:right;"> 	<strong><span>Vương Linh</span></strong></p>', 1, '15001943271111703095.jpg', NULL),
(183, 'Lưu ý mặc tã quần chống hăm cho bé', 'Chọn tã mỏng, thay thường xuyên, rửa nước sạch... giúp bé thoải mái vận động và phòng hăm da.', '<p class="Normal"> 	Dùng tã quần thế nào, cần thay sau bao lâu... là thắc mắc của nhiều ông bố bà mẹ vào mùa hè. Bé có thể quấy khóc, biếng ăn, trằn trọc khó ngủ vì tã ướt gây bí bách, mông hăm đỏ.</p><p class="Normal"> 	Thời tiết nóng nực khiến nhiệt độ và độ ẩm thay đổi liên tục. Sử dụng tã quần sai cách sẽ gây ra tình trạng hăm da, mẩn đỏ, thậm chí lở loét, viêm đường tiết niệu... Cuộc sống bận rộn, song cha mẹ đừng quên lưu ý khi mặc tã cho bé.</p><table border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943291333563165.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tã là vật dụng bé sử dụng hàng ngày. Ảnh: <em><span>Pyxabay</span></em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>C</span><span>ác loại tã giả, nhái, hàng gia công giá rẻ có thể khiến trẻ nhiễm khuẩn, dị ứng. Trước hết, cần chọn tã quần có nguồn gốc xuất xứ rõ ràng, thương hiệu được tin cậy.</span></p><p class="Normal"> 	Với thời tiết nóng nực mùa hè, nên ưu tiên các loại tã quần mỏng, khô thoáng, có đặc điểm phù hợp với từng giai đoạn phát triển của trẻ. Có thể kiểm tra khả năng thấm hút nhanh của sản phẩm bằng cách đổ nước trực tiếp lên tã. Tã tốt giúp bé thoải mái vui chơi, vận động cả ngày và ngủ ngon giấc. Tránh mua tã dày, cứng, bí và không đúng kích thước của con.</p><p class="Normal"> 	Tã nên thay mỗi 4 tiếng đồng hồ, để vi khuẩn trong nước tiểu không kịp sinh sôi và phát triển. Nếu không ước lượng đúng thời gian, cha mẹ có thể cho bé dùng loại tã quần Goodry Ultrathin có vạch chỉ màu, cho biết chính xác thời điểm bé cần được thay.</p><p class="Normal"> 	<span>Ngoài ra, nên rửa vùng mông cho bé bằng nước sạch sau mỗi lần thay tã, tránh dùng giấy ướt lau qua loa. Sau 5-10 phút chờ mông bé khô ráo, mới bôi kem hăm và mặc tã khác</span><span>. </span></p><p class="Normal" style="text-align:right;"> 	<strong>An San</strong></p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="thumb"> 			<div style="text-align: center"><img src="/pictures/news/15001943291661062631.png" /></div></div> 		<div class="title_news"> 			<a href="http://goodry.vn/goodry/ta-quan-sieu-mong.html" style=''font-family: "Times New Roman"; font-size: 11.8pt;'' target="_blank">Goodry Ultrathin</a><span style="color:rgb(0,0,0);"> có thảm thấm hút sử dụng các chất liệu mỏng, thấm, khô thoáng giúp bé có mùa hè vui vẻ, ngủ ngon, phát triển cân đối. Tã quần trang bị vạch chỉ màu, cho bố mẹ biết chính xác thời điểm bé cần được thay tã. Thông tin </span><a href="http://goodry.vn/goodry/ta-quan-sieu-mong.html" style=''font-family: "Times New Roman"; font-size: 11.8pt;'' target="_blank">tại đây</a><span>.</span></div> 	</div> </div>', 1, '15001943281680981333.png', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(184, 'Hai câu hỏi tiết lộ trí tuệ của bạn', 'Hãy xem bạn có phải là người tư duy nhạy bén và thích ứng nhanh với môi trường.', '<p class="Normal"> 	Năm 2016, các nhà tâm lý học đã tiến hành các nghiên cứu để xem xét mật độ dân số ảnh hưởng tới một cá nhân thế nào. <span>Người ta phát hiện rằng những người có chỉ số IQ cao có những đặc điểm khá giống nhau.</span></p><p class="Normal"> 	Hãy trả lời 2 câu hỏi trên <em>Brightside </em>để biết bạn có là một trong số họ.</p><p class="subtitle"> 	<strong>Câu 1: Bạn có thể sống ở khu dân cư đông đúc trong thành phố lớn không?</strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194330990242463.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	1. Không, quá nhiều người khiến tôi mệt mỏi.</p><p class="Normal"> 	2. Tôi không để ý điều này, tôi cảm thấy như ở nhà.</p><p class="subtitle"> 	<strong>Câu 2: Bạn có thấy <span>buồn hoặc cô đơn, hay rơi vào trạng thái thu mình nếu lâu không liên lạc với bạn bè?</span></strong></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943301150548538.jpg" /></div></td> 		</tr></tbody></table><p class="Normal"> 	1. Nhiều hoặc ít.</p><p class="Normal"> 	2. Không hẳn.</p><p class="Normal"> 	<strong>Đáp án:</strong></p><p class="Normal"> 	- Nếu hai câu bạn đều chọn đáp án đầu tiên, bạn có lẽ nằm trong ngưỡng "có chỉ số hạnh phúc cao".</p><p class="Normal"> 	- Nếu hai câu bạn đều chọn đáp án 2, bạn là người điển hình có IQ cao.</p><p class="Normal"> 	Như các nghiên cứu đã chỉ ra, những người có trí tuệ thông minh, nhanh nhạy là những người có khả năng giải quyết các công việc nhanh nhất. Họ thích ứng nhanh với môi trường mới (trong trường hợp này là khu vực đông dân cư tại các thành phố lớn).</p><p class="Normal"> 	Họ rất giỏi trong việc sắp xếp cuộc sống, biết cách giải tỏa căng thẳng, và không cần sự hỗ trợ từ bạn bè. Họ độc lập và có thể sống tốt một mình.</p><p class="Normal"> 	- Nếu bạn chọn hai đáp án khác nhau ở hai câu, bạn có lẽ đã tìm thấy sự cân bằng giữa hạnh phúc và trí tuệ.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943301225854230.jpg', NULL),
(185, '1.000 bồn nước sạch miễn phí giúp vùng hạn mặn', 'Để san sẻ khó khăn thiếu nước sạch, 10 công trình và 1.000 bồn chứa sẽ được trao tặng cho bà con vùng hạn mặn trên cả nước.', '<table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194331365174384.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nắng nóng kéo dài tại nhiều tỉnh thành miền Trung khiến đất đai khô cằn, nước sinh hoạt khan hiếm. Hàng ngày, mẹ con chị K’sor HLec (buôn Kít, xã Sông Hinh, Phú Yên) phải đi bộ hàng chục cây số, xin từng can nước từ các gia đình có giếng sâu mang về sử dụng.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001943311569403564.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mùa khô, việc trồng trọt chăn nuôi của bà con Phú Yên cũng gặp không ít khó khăn. Người miền Trung nhọc nhằn, nay lại càng nghèo do thu nhập nông nghiệp giảm sút trong mùa nắng hạn.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001943311975853763.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Trong suốt 2 tháng qua, chương trình “Xả một lần cùng Comfort, tiết kiệm cho bạn, giúp vùng hạn, mặn” đã đồng hành cùng miền Trung và các vùng thiếu nước sạch khác trên toàn quốc. Ngay tại Buôn Kít, giếng bơm sâu 100m được xây dựng, phục vụ nhu cầu cho 108 hộ dân nơi đây.  </p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194331403410954.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mùa khô năm nay, bà con Buôn Kít có thể thoải mái dùng nước sạch từ chương trình, mà không phải đi bộ nhiều cây số như trước.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/15001943321552152059.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Gần đây nhất, 300 bồn chứa nước 500 lít được chuyển đến các hộ gia đình khó khăn tại Phú Yên, Ninh Thuận, Bình Thuận. Những chiếc bồn dung tích lớn sẽ giúp người dân vùng sâu vùng xa có công cụ trữ nước cho mùa khô.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194332778485857.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Tại nhiều nơi, người dân tích cực tham gia các hoạt động tiết kiệm nước như thay đổi thói quen giặt giũ truyền thống, tham gia thử thách xả - xách online... để san sẻ 85.940 lít nước cho bà con vùng hạn mặn.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:center;"> 	<strong>Cuộc sống người dân vùng hạn mặn </strong></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156267" data-component="true" data-component-type="video" data-component-value="156267" data-component-typevideo="2"></div></div> </div><p class="Normal" style="text-align:right;"> 	<strong>An San</strong></p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="title_news"> 			<span style="color:rgb(0,0,0);">Chương trình "Xả một lần cùng Comfort, tiết kiệm cho bạn, giúp vùng hạn mặn" tiếp nối hành trình "Tiết kiệm một tỷ m3 nước sạch cho Việt Nam" do Comfort khởi động từ năm 2013. T</span><span style="color:rgb(0,0,0);">ham gia chương trình thông qua hoạt động xách - xả vui nhộn tại website <a href="http://1tym3nuoc.vn">1tym3nuoc.vn</a>.</span></div> 	</div> </div>', 1, '15001943312066544007.png', NULL),
(186, 'Chuyện bất ngờ khi người đàn ông nghèo làm xước xe sang', 'Vô tình làm xước sơn chiếc xe BMW, ông bố nghèo Trung Quốc đã để lại bức thư xin lỗi và hứa sẽ chịu trách nhiệm.', '<p class="Normal"> 	Câu chuyện xúc động này đang được chia sẻ khắp mạng xã hội Trung Quốc.</p><p class="Normal"> 	Theo <em>Chinanews</em>, trên đường chở con đi học trên chiếc xe đạp điện hôm 4/7, anh Chin không may va phải chiếc xe ôtô BMW đỗ bên đường, gây ra một vết xước ở thân xe. Không thấy chủ nhân đâu, lại sợ con đi học muộn nên anh đã viết một tờ giấy để lại lời nhắn. Anh gửi lời xin lỗi và hứa sẽ chịu trách nhiệm.</p><p class="Normal"> 	"<span>Hiện tại, tình hình kinh tế của tôi rất khó khăn, trong người chỉ còn 100 nhân dân tệ (khoảng hơn 300 nghìn đồng) và hơn 200 tệ (khoảng 670 nghìn đồng) trong tài khoản. </span><span>Anh hãy đem xe đi sửa rồi nhắn lại cho tôi biết. Vì lỗi của tôi nên tôi sẽ chịu trách nhiệm. Trước hết, tôi sẽ gửi anh 100 tệ, số tiền còn lại tôi xin khất khoảng 2-3 tháng. Tôi nhất định sẽ gửi lại cho anh. Tôi không bao giờ bỏ trốn nên anh hãy an tâm.</span></p><p class="Normal"> 	Đây là số điện thoại của tôi. Anh hãy gọi cho tôi, tôi sẽ lập tức đưa cho anh 100 tệ. Một lần nữa tôi xin lỗi vì đã gây phiền phức cho anh. Tôi thực lòng xin lỗi", anh Chin viết.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943331016366739.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Anh Chin để lại bức thư trên chiếc xe sang anh làm xước. <em>Ảnh: Chinanews.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<br> 	Khi đọc được lời nhắn, chủ chiếc xe BMW, anh Wong đã liên hệ với anh Chin để hẹn gặp mặt. <span>Anh Wong cho biết gói bảo hiểm mình mua là loại một chiều, tức là bên gây tai nạn sẽ phải chịu trách nhiệm sửa chữa, bảo hiểm không thanh toán. Thợ sửa xe nói chi phí hết khoảng 1.900 nhân dân tệ (khoảng 6,4 triệu đồng). </span></p><p class="Normal"> 	<span>Khi đến gặp mặt, anh Chin thật thà trình bày hoàn cảnh và xin chủ xe cho khất 3 tháng để trả dần dần. Anh nói đang đi tìm công việc mới sau vài lần kinh doanh thất bại.</span></p><p class="Normal"> 	Anh Chin không thể ngờ sau khi nói chuyện, anh Wong không những không bắt đền mà còn tạo điều kiện cho anh vào làm trong công ty mình. Wong nói rằng anh rất quý tính thật thà và có trách nhiệm của người đàn ông này. Trong khi người khác có thể bỏ đi ngay, giả vờ không làm gì hết, nhưng anh Chin lại hành động khác hẳn. Việc làm của anh sẽ tác động rất tốt tới cậu con trai nhỏ ngồi đằng sau.</p><p class="Normal"> 	Anh Chin rất cảm kích tấm lòng của người đàn ông lạ tốt bụng, và cho biết anh sẽ dùng tiền lương để thanh toán dần tiền sửa xe.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '1500194333773421131.jpg', NULL),
(187, '''Nuôi 5 con tôi vẫn mua được nhà, xe nhờ tiết kiệm''', '"5 con tôi hôm nào đi học cũng mang theo đồ ăn mẹ làm và không tốn đồng nào cho căng tin trường", chị Đào kể.', '<p class="Normal"> 	<em>Bài chia sẻ dưới đây là của chị Lâm Anh Đào, 47 tuổi người Sài Gòn đang định cư tại Australia cùng chồng và 5 con, từ 5 đến 17 tuổi. </em></p><p class="Normal"> 	Hai vợ chồng tôi lấy nhau khi cả hai tay trắng, chưa có bất cứ tài sản gì và chỉ đi làm công ăn lương. Chồng tôi có mức lương ổn, còn thu nhập từ nghề chính của tôi (làm văn phòng tại một trường đại học) không mấy cao nên tôi có thêm nghề tay trái là dạy nhạc.</p><p class="Normal"> 	<span>Ngày chưa có con, tôi cũng thuộc dạng "phóng khoáng", cứ thích gì là mua, chẳng bao giờ tính toán hay xem lại hóa đơn chi tiêu. Vì sự quá tay đó mà tôi hầu như không để dành được bao nhiêu dù đi làm chăm chỉ.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943331366947775.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chị Lâm Anh Đào kể, chiếc áo khoác mình mặc mua giảm giá chỉ có giá 120 đôla Australia trong khi giá thường là 370 đôla. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Từ lúc sinh con, tự thấy nếu tiếp tục kiểu chi tiêu này thì không ổn nên tôi đã cố gắng điều chỉnh. Từ một người vụng về, chẳng biết nấu nướng gì, tôi học hỏi để<span> tự tay nấu cho con ăn chứ không mua đồ hộp như nhiều mẹ khác ở bên này. </span><span>Ngay cả khi gửi con đi nhà trẻ từ lúc 4 tháng tuổi để đi làm, tôi cũng luôn gửi kèm chiếc hũ đựng thức ăn do mình làm theo. Nhiều người quen thấy vậy gọi tôi là "bà mẹ hà tiện", thậm chí, có chị phụ huynh gửi con cùng lớp </span><span style="color:rgb(34,34,34);">còn mua tặng 20 hũ đồ ăn chế biến sẵn, có lẽ vì tội nghiệp con tôi có mẹ "ky".</span></p><p class="Normal"> 	Đến khi các con đi học, tôi lại tiếp tục "hà tiện" kiểu này với mục đích chính là bảo vệ sức khỏe cho con. </p><p class="Normal"> 	Để các con có bữa ăn trưa đầy đủ chất, tôi giảm một tiếng ngủ nướng, sáng nào cũng dậy tầm 5 giờ. Sau khi chạy bộ hay tập yoga, tôi làm đồ ăn sáng và chuẩn bị bữa trưa để cả nhà mang đi học, đi làm. Một suất ăn trưa ở ngoài giá 15-20 đôla Australia. Nếu cả nhà cùng ăn tiệm buổi trưa, mỗi ngày chúng tôi sẽ tốn hàng trăm đôla. 10 năm con đi học, tôi chưa bao giờ để con phải mua đồ chiên nướng ở căng tin và nhờ vậy các bé không trở thành tâm điểm của bệnh béo phì như nhiều bạn cùng lứa, lại có thể ăn được cả đồ Tây lẫn đồ Việt. </p><p class="Normal"> 	<span>Thông thường tôi chi một tuần đi chợ cho 7 người ăn là 200 đô nhưng hầu như chưa bao giờ dùng hết số tiền này vì biết linh hoạt chọn đồ dựa theo mặt hàng được bày bán và món được giảm giá từng hôm. Chẳng hạn, t</span><span>hông thường cửa hàng sẽ bán 3 bó rau là 2 đôla, còn một bó là một đôla. Tôi sẽ mua 3 bó, một để nấu canh, một xào và một luộc. </span><span>Tôi thường mua những đồ được giảm giá nhưng đảm bảo tươi ngon, chỉ có điều hạn không còn lâu. Điều quan trọng là mình mua về làm ngay và tuyệt đối không mua nhiều bỏ ngăn đá tủ lạnh. </span><span> </span><span>Nếu khéo léo tính toán, mỗi tuần bạn cũng tiết kiệm 30-40 đôla, tuy không nhiều nhưng góp gió sẽ thành bão. </span></p><p class="Normal"> 	Với các đồ thiết yếu cho gia đình như xà phòng rửa tay, xà phòng giặt, giấy lau tay... t<span>ôi cũng thường canh đợt giảm giá sâu</span><span> là mua nhiều dùng hơn 3-4 tháng.</span></p><p class="Normal"> 	Cuối tuần người Việt có thói quen ra tiệm ăn phở hay bún. Tôi tự mua thịt hay xương về nấu được nồi bún bò Huế to chỉ tốn khoảng 30-45 đôla trong khi ăn ngoài tốn số tiền gấp đôi trở lên. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943331207222269.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chị Lâm Anh Đào và 4 cô con gái. Ảnh:<em> NVCC. </em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Về trang phục, t</span><span>hích mặc đẹp cho con và bản thân, tiêu chí của tôi là đồ phải đẹp, bền và rẻ. Thường đồ đẹp, chất liệu tốt sẽ không rẻ, vì vậy tôi phải canh... thời tiết, tức là luôn đợi dịp giảm giá khi giao mùa. </span><span>Ví dụ, một chiếc áo khoác chất lượng có giá bình thường là 370 đôla, nhưng tới cuối mùa thì giá chỉ còn 120 đô la. </span></p><p class="Normal"> 	<span>Tôi sẽ chọn các món đồ cơ bản, không bao giờ bị lỗi mốt để mặc được lâu. Cứ sắp sang hè, tôi sẽ đi mua đồ đông và ngược lại khi sắp tới đông thì mua đồ mát cho cả gia đình. </span></p><p class="Normal"> 	Số tiền tiết kiệm từ mua sắm đó, tôi để dành mua sách và quần áo mới cho các con vào đầu năm học vì những món này thường không bao giờ hạ giá và tốn khá nhiều</p><p class="Normal"> 	Với một gia đình đông con, chi phí cho trẻ là cả một vấn đề lớn. Tôi quan niệm, khi con còn nhỏ mình phải cố gắng làm việc, để tới khi con lớn hơn, mình có thời gian dạy con nhiều. Cách này không chỉ giúp tôi biết ưu tiên từng việc ở từng giai đoạn mà còn thực sự tiết kiệm được khá nhiều chi phí giáo dục trẻ. </p><p class="Normal"> 	Tất cả các con của tôi đều học trường công nên học phí rẻ hơn trường tư. Tôi rèn cho con ý thức tự học từ nhỏ, đồng thời luôn dành thời gian kiểm tra, kèm con vào mỗi tối nên không bé nào phải đi học thêm. Nhiều gia đình khác, khi con vào lớp 3-4 thì thường cho con đi học thêm, mỗi tiếng tốn 30 đôla. Hiện nay, con gái thứ hai đang học lớp 9 của tôi còn dạy thêm toán và Anh văn cho trẻ nhỏ hơn và dành 2 tiếng dạy nhạc tại trường. Anh lớn nhất cũng dạy đánh trống cho 3 học trò nhí. Các con học tập tính tiết kiệm của mẹ, không bao giờ đòi mua sắm đồ gì. Số tiền các con có được từ việc dạy thêm được mẹ mở tài khoản để gửi tiền vào đó.</p><p class="Normal"> 	Ông xã cũng góp phần "hà tiện" cùng vợ. Anh không uống bia rượu, không hút thuốc, tan làm là về nhà phụ vợ, chăm con, chỉ thỉnh thoảng tốn tiền mời vợ đi ăn trưa để "hâm nóng". </p><p class="Normal"> 	<span>Nhờ "hà tiện" như vậy cộng với chăm chỉ làm ăn và tính toán chi ly, sau 23 năm lấy nhau, vợ chồng tôi đã lo cho 5 con ăn học đầy đủ và dần tích cóp mua được xe, nhà lớn ở trung tâm, thuận tiện cho con cái học hành.</span></p><p class="Normal"> 	<span>Bây giờ tôi bắt đầu chọn việc chứ không để việc chọn tôi. Tôi xin làm 4 ngày một tuần và giờ giấc phù hợp với việc đưa đón con. Việc làm cuối tuần không còn là việc kiếm tiền mà là tình yêu âm nhạc nên tôi vẫn giữ. </span><span>Dù cuộc sống khá sung túc, tôi vẫn đi làm với bộ đồ giản dị và khi trở về nhà là vào bếp nấu đủ các món đồ ăn, bánh trái.</span></p><p class="Normal" style="text-align:right;"> 	<strong style="text-align:right;">Lâm Anh Đào</strong></p>', 1, '1500194333821586970.jpg', NULL),
(188, 'Những điều lạ lùng trong mắt rể Tây về tập quán quê vợ', 'Jimmy thấy lạ khi thấy vợ sáng nào cũng ăn mỳ với hành, thịt, mà anh cứ ngỡ là cho bữa tối.', '<p class="Normal"> 	<span>Jimmy và Ngọc Thy hiện định cư tại Canada. Những ngày đầu về chung nhà, cả hai có một số điểm khác biệt trong cách sinh hoạt, ăn uống. Ví như Jimmy ăn sáng chủ yếu là ngũ cốc và sữa. Trong khi Thy quen kiểu ăn mỳ hoặc phở. </span></p><p class="Normal"> 	<span>Khi nhìn thấy vợ pha một gói mỳ, bỏ thịt, hành... vào một tô to, Jimmy hỏi ngay "trời đã tối đâu mà em ăn tối", khiến cô gái 25 tuổi phải giải thích rằng đó là thói quen ăn sáng của nhiều người Việt.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943341241309096.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Jimmy rất sợ mỗi khi phải qua đường ở Việt Nam.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Jimmy còn cảm thấy rất sợ khi nhìn thấy vợ "chén" trứng vịt lộn ngon lành, hay ăn bún đậu mắm tôm tì tì. </span><span>Anh nói bị ám ảnh bởi cái mùi cực kỳ khó chịu này, anh mô tả nó như mùi tất bị bỏ lâu ở gầm giường. </span></p><p class="Normal"> 	Mỗi lần về Việt Nam, chàng kỹ sư Canada sợ hãi mỗi khi phải qua đường vì nhiều xe cộ quá. <span>Khi được Thy dạy là giơ tay ra để xin đường, anh chàng quơ quơ tới tấp rồi ba chân bốn cẳng chạy vèo sang. Lần nào vẻ căng thẳng cũng lộ rõ trên khuôn mặt. Mỗi lần đi qua, </span><span>chàng trai Canada đều nói đùa với vợ ''Ready to be die?'' (Sẵn sàng chết chưa?).</span></p><p class="Normal"> 	<span>Cũng giống như Jimmy, chồng của Hoàng Mai, anh Jeffrey, người Australia, cực kỳ sợ các loại mắm. Có lần từ sân bay về, Mai dẫn anh một mạch tới quán bún mắm quen thuộc, nhưng ngay khi bước vào quán, anh chỉ đứng ở ngoài chờ cô ăn xong, mặc dù rất đói.</span></p><p class="Normal"> 	<span>Cô gái Đắk Lắk cho hay cô vốn là người thích gặm xương gà, xương lợn nhưng trong mắt chồng cô đó là chuyện lạ lùng, vì ở bên này người ta thường bỏ hết phần xương đi. "Mỗi lần ăn món gì có xương là anh lại gạt sang đĩa của tôi, rồi trêu ''cho bà xã vì hôm nay ngoan''", bà mẹ một con kể lại.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943342093506915.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Anh Noel rất sợ ăn bánh chưng và tục chúc rượu của người Việt.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Sinh sống và làm việc 4 năm ở Việt Nam, anh Noel sợ nhất việc chúc tụng ngày Tết, nhất là đi đâu cũng bị mời ăn bánh chưng. "Cả một cái Tết, đi khắp họ hàng nhà vợ, ai cũng mời bánh chưng, tôi không thể chối từ vì mất lịch sự, nên phải cố ăn. Từ đó đến nay tôi không dám đụng vào món này nữa", anh Noel nói.</p><p class="Normal"> 	Anh cũng đặc biệt sợ rượu gạo, và màn làm quen từ những bàn bên cạnh trong quán nhậu. Có lúc anh phải giả vờ là người ăn chay, không biết uống rượu để trốn.</p><p class="Normal"> 	Ở Việt Nam một thời gian nên anh Noel biết đến việc Tây hay bị nói thách giá. Anh đã nghĩ ra một cách rất hay để mua đồ không bị hớ, thậm chí còn mua rẻ hơn cả vợ. "Cuối tuần nào chồng tôi cũng ra chợ <span>Hôm tự mặc cả mua vải may quần áo, cùng cả hội bạn. Cũng biết giá sơ sơ rồi nên khi người ta nói thách quá anh giả vờ ''Ối ối đắt quá'' rồi ngất ra đấy. Cậu bạn đi cùng vội vàng quạt quạt cho bạn đỡ tăng xông. Mấy người bán hàng ở chợ Hôm ai cũng biết mặt", chị Trà My, vợ anh Noel hài hước kể lại.</span></p><p class="Normal" style="text-align:center;"> 	<strong><span>Bài hát anh Noel cùng các đồng nghiệp nước ngoài sáng tác về Hà Nội</span></strong></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div style="text-align:center"> 			<div style="text-align: center;"> 				<div style="text-align:center"> 					<div style="text-align: center;"> 						<div id="video-156174" data-component="true" data-component-type="video" data-component-value="156174" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> 				</div> 			</div> 		</div> 	</div> </div><p class="Normal"> 	Chị Thu Trang và anh chồng Michael đã tổ chức đám cưới ở Đức từ cuối năm ngoái. Hai người mới về Hà Nội làm hôn lễ hồi tháng 2 vừa qua. Phải sát đến ngày về, anh chị mới đặt vé và tất bật thu xếp công việc, xin nghỉ phép. Không phải vì không có vé, mà vì gia đình chị Trang liên tục thay đổi ngày cưới do đi xem ở 3 thầy khác nhau.</p><p class="Normal"> 	"Chồng tôi tỏ ra khá ngạc nhiên vì sao có ngày cưới thôi lại phải xem kỹ thế, trong khi ở bên này đám cưới người ta thường chỉ chọn vào cuối tuần để mọi người tiện tới chúc mừng, chứ không câu nệ ngày giờ gì cả", chị Trang kể.</p><p class="Normal"> 	Chị cũng cảm thấy rất ngại khi trong ngày cưới, nhiều người chúc vợ chồng chị sinh con trai, rồi "cố sinh năm sau nhé, năm chó vàng đấy, hợp tuổi, hợp mệnh". Khi nghe chị giải thích là ở Việt Nam mình nhiều người vẫn còn coi trọng việc sinh con trai, rồi lấy vợ phải hợp mọi thứ, chồng chị lắc đầu. Ở nước ngoài, người ta không hề để ý chuyện này, chỉ cần yêu nhau, muốn đến với nhau là đủ, con cái giới tính gì cũng không quan trọng.</p><p class="Normal"> 	Sống ở hai nền văn hóa phương Đông - phương Tây, nên có những điểm khác biệt trong cuộc sống, trong cách sinh hoạt, suy nghĩ của chồng Tây - vợ Việt là điều dễ hiểu. Tuy nhiên, nhiều chàng rể Tây thừa nhận họ thấy những điểm rất đáng quý của con gái Việt Nam, những tính cách họ không thể tìm thấy ở những cô gái nước ngoài khác như sự giản dị, không ngại khó, ngại khổ, lúc nào cũng mạnh mẽ, hy sinh hết mình vì chồng, vì con...</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '1500194334806348802.jpg', NULL),
(189, 'Anh kỹ sư Sài Gòn dùng một câu nói hóa giải mọi bất đồng với vợ', 'Mỗi khi vợ càm ràm gì đó, anh Vỹ Long (32 tuổi) nói: ''Hãy nghĩ cách giúp anh đi'', khiến vợ anh dù có bực cũng phải phì cười. ', '<p class="Normal"> 	Như bao đôi vợ chồng, dù có yêu nhau đến đâu vẫn có những lúc bất đồng quan điểm, để rồi tranh cãi. Vợ chồng anh Long, chị Hòa, đang sống ở TP HCM, cũng từng trải qua thời điểm như vậy. Nhưng nhờ có một câu "thần chú" mà anh chị đã hóa giải được nhiều tình huống hiểm nghèo.</p><p class="Normal"> 	Anh Long (quê Bình Thuận) và chị Hòa (quê Thanh Hóa) có mối tình thời đại học tươi đẹp. Họ kết hôn sau ba năm ra trường. Song khi về sống chung, khác biệt trong tính cách, văn hoá vùng miền làm nảy sinh những bất đồng. Ba năm đầu có lúc họ tưởng như không thể chấp nhận nổi nhau.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943362007435111.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhờ có câu thần chú mà gia đình anh Long chỉ có tiếng cười, không còn chiến tranh lạnh nữa. Ảnh: <em>NVCC.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Một lần tình cờ anh Long đọc được bài báo về bà </span><span style="color:rgb(34,34,34);">Debbie Phelps, mẹ</span><span> của </span><span>cậu bé Michael Phelps đã khắc phục được chứng tăng động cho con và <a href="http://giadinh.vnexpress.net/tin-tuc/to-am/me-michael-phelps-bien-con-tu-tre-tang-dong-thanh-kinh-ngu-huyen-thoai-3454099.html?utm_source=search_vne">giúp con </a></span><span><a href="http://giadinh.vnexpress.net/tin-tuc/to-am/me-michael-phelps-bien-con-tu-tre-tang-dong-thanh-kinh-ngu-huyen-thoai-3454099.html?utm_source=search_vne">trở thành huyền thoại</a> môn bơi lội với 23 huy chương vàng ở các kỳ Olympic.</span></p><p class="Normal"> 	<span>Khi các thầy cô phàn nàn về con mình, bà chỉ hỏi một câu đơn giản: "Thầy/cô đã làm gì để giúp cháu?". Và bà cũng luôn luôn tự chất vấn mình: "Tôi phải làm gì để giúp con".</span></p><p class="Normal"> 	<span>"Tôi thật sự ấn tượng trước sức mạnh của câu hỏi đơn giản này và bắt đầu áp dụng vào cuộc sống của mình từ hơn một năm trước", anh Long chia sẻ.</span></p><p class="Normal"> 	Mỗi khi vợ càm ràm về tật xấu của mình, anh Long nói: "Ừ, anh biết. <span>Hãy cố gắng nghĩ cách giúp anh đi". Chị Hòa vẫn càu nhàu: "</span><span>Em đã giúp anh bao lâu nay rồi, mà có thay đổi được gì đâu". Anh Long vẫn với khuôn mặt vô tội, ngước nhìn vợ: "</span><span>Chắc chắn là có một cách nào đó có thể giúp anh. Em hãy cố gắng suy nghĩ. Chỉ có em mới giúp được anh thôi"...</span></p><p class="Normal"> 	<span>"Tôi không quên tăng thêm sức mạnh cho lời yêu cầu vợ giúp đỡ bằng cách tăng vị thế của cô ấy: ''Chỉ có em mới giúp được anh''. Sau đó tôi sẽ chuyển toàn bộ tâm trí vào việc tìm ra giải pháp: ''Chúng ta đến với nhau là để hoàn thiện lẫn nhau, chứ không phải để trách móc''. Kỳ diệu là cô ấy nguôi rất nhanh và cũng giở bài của mình ngay được: ''Mấy hôm nay anh quên chưa đấm lưng cho em rồi đó''", anh Long cười kể.</span></p><p class="Normal"> 	Chị Hòa cũng cho biết, nghe chồng nói câu này tự nhiên lửa giận trong lòng giảm hẳn, trong đầu đã thay đổi đối tượng chất vấn. <span>"Tôi tự chất vấn chính bản thân, sau đó nghĩ xem mình cần làm gì, nói gì để giải quyết vấn đề đó, hoặc nếu thấy mình quá đáng quá thì xin lỗi ảnh liền", chị Hòa chia sẻ.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943361057726362.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Anh Long là con người hòa đồng, vui vẻ, luôn tìm tòi, học hỏi để áp dụng vào cuộc sống vợ chồng cũng như nuôi dạy con.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span style="color:#808080;"><span><strong>Bên cạnh câu thần chú thì hai năm trở lại đây, tổ ấm của đôi vợ chồng kỹ sư đón thêm một thành viên mới. </strong></span></span><span>Sự ăn ý trong cách dạy con đã giúp cuộc sống của anh chị "chỉ có những điều tích cực mà thôi".</span></p><p class="Normal"> 	<span>Đôi vợ chồng trẻ tin rằng, </span><span>1.000 ngày đầu tiên có ảnh hưởng lớn đến sự phát triển thể chất, trí tuệ lẫn tâm hồn của trẻ. </span><span>Để làm tốt được điều này, cách duy nhất là phải chăm con một cách khoa học, đầy nhẫn nại và yêu thương. Họ không tin là có người nào khác phù hợp với công việc này hơn là chính bố mẹ các bé.</span></p><p class="Normal"> 	<span>"Vì ý nghĩ đó, chúng tôi xác định một trong hai vợ chồng sẽ nghỉ việc chăm con và may mắn vợ tôi quyết định từ bỏ sự nghiệp để nhận trọng trách này", anh Long nói, đầy cảm kích.</span></p><p class="Normal"> 	<span>Trước khi nghỉ sinh hai năm trước, chị Hòa đang làm trong ban quản lý dự án một công ty xây dựng lớn, có bằng thạc sỹ, tương lai rộng mở. Quyết định nghỉ việc của chị liên tục vấp phải ý kiến phản đối của người thân quen.</span></p><p class="Normal"> 	"Bố mẹ tôi lo kinh tế hai vợ chồng khó khăn, cần phải đi làm để đảm bảo cuộc sống. Một số người khác nói tôi không nên lệ thuộc kinh tế vào chồng. Nhưng tôi nghĩ, rủi ro là ở tương lai, còn hành trình làm mẹ cần dấn thân và tận hiến từng giây từng phút. Nhất là ba năm đầu đời của con phải tận dụng thời gian triệt để", chị nói. </p><p class="Normal"> 	<span>Khi ở</span><span> nhà cùng con, chị cho bé tham gia vào mọi hoạt động của mình. V</span><span>ừa học vừa chơi như vậy mà đến nay - khi 21 tháng tuổi - bé đã phân biệt được khá nhiều loại rau củ thông dụng, các loại hải sản, thịt cá và các hoạt động gia đình như</span><span> giặt đồ, phơi đồ, ủi quần áo...</span></p><p class="Normal"> 	<span>"Chúng tôi sử dụng chính ''thành tích'' của bé Soda để chứng minh với ông bà nội ngoại lựa chọn của chúng tôi là hoàn toàn chính xác. ''Soda 37 ngày tuổi đã cứng cổ rồi nè, bà nội thấy chưa'', ''Mới 8 tháng tuổi đẩy xe vèo vèo rồi bà ngoại ơi, ''14 tháng tuổi, con đã biết phân biệt được các cây ở công viên trước nhà rồi''", người mẹ trẻ kể thêm.</span></p><p class="Normal"> 	Càng lớn, bé Soda càng tự tin, vui vẻ, tất cả các mốc phát triển đều sớm hơn bình thường. Từ khoảng 15 tháng tuổi, gia đình hai bên đã bắt đầu ủng hộ lựa chọn của anh chị.</p><p class="Normal"> 	<span>Anh chị dự định, khi Soda được 3 tuổi, chị Hoà quay lại công việc của mình và tiếp tục tìm hiểu những cách dạy con hay, chia sẻ đến các bậc phụ huynh.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Phan Dương</strong></p>', 1, '1500194335469897976.jpg', NULL),
(190, 'Treo ảnh cưới hai năm mới phát hiện không phải ảnh mình', 'Đang ngả lưng nghỉ ngơi, anh Wang ở Trung Quốc thấy ảnh cưới bị quăn mép, sau đó phát hiện bí mật không thể chấp nhận.', '<p class="Normal"> 	Vào ngày 3/7, anh Wang ở Thành Đô (Tứ Xuyên) trong lúc đang nằm nghỉ ngơi đột nhiên thấy bức ảnh cưới của vợ chồng mình treo trên đầu giường bị quăn mép. Anh bật dậy định mang xuống dán thì bàng hoàng phát hiện phía dưới góc ảnh có một hàng cây kỳ lạ.</p><p class="Normal"> 	Vừa mở, vừa run, anh bóc tấm ảnh của mình ra thì thấy đằng sau là ảnh cưới của một đôi hoàn toàn xa lạ. Trong giây phút đó, vợ chồng anh bị sốc nặng.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194337116709366.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Từ một mép ảnh bị quăn, anh Wang phát hiện ra bí mật bị lừa dối suốt 2 năm. </p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Nếu gần đây thời tiết không trở nên ẩm ướt, có lẽ tôi không thể nào phát hiện ra được chuyện này", anh Wang nói trên tờ <em>Finance</em>.</p><p class="Normal"> 	Vợ chồng anh chụp ảnh cưới vào tháng 9/2015 tại một tiệm ảnh của thị trấn với giá 5.000 tệ (gần 17 triệu đồng). Một tháng sau, họ nhận được ảnh cưới và treo trong phòng tân hôn suốt từ đó cho tới khi phát hiện ra sự gian dối sau bức ảnh.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943371722315359.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bức ảnh của vợ chồng anh Wang dán chồng lên một bức ảnh hỏng khác.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Ngày 4/7, anh Wang tìm đến tiệm ảnh làm rõ sự việc, nhưng đáp lại chỉ là vài câu xin lỗi. Chủ cửa hàng đề nghị chụp cho vợ chồng anh Wang bộ ảnh khác và tặng thêm khoản tiền mặt 200 tệ (khoảng 700 nghìn đồng).</p><p class="Normal"> 	Câu chuyện đang thu hút sự quan tâm của dư luận Trung Quốc. Một số người cho rằng<span> bức ảnh gốc bị lỗi nên khách hàng đã đem trả lại. Xưởng chế tác vì không muốn lãng phí nên đã tận dụng sản xuất ảnh cho người đến sau.</span></p><p class="Normal" style="text-align:right;"> 	<strong><span>Bảo Nhiên</span></strong></p>', 1, '15001943371901208265.jpg', NULL),
(191, 'Cậu bé 8 tuổi bán trứng gà để mong thành triệu phú', 'Muốn đủ tiền xây biệt thự để cả nhà ở với nhau mãi mãi, cậu bé người Anh đã tự lập công ty và đi giao bán trứng gà. ', '<p> 	<span style="color:rgb(0,0,0);">Junior Wyatt ở </span>Staffordshire, Anh<span style="color:rgb(0,0,0);"> đã tự lập công ty chỉ với số tiền tiêu vặt 10 bảng (khoảng 290.000 đồng) vào tháng trước. Cậu bé đã nảy ra ý tưởng này sau khi xem một chương trình về các triệu phú trên TV và quyết tâm trở thành một người như thế.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194338198300001.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Junior Wyatt rất hào hứng khi kiếm được tiền từ việc bán trứng gà. Ảnh:<em> NWNS.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Cậu bé đi giao trứng quanh thị trấn sau khi được mẹ giúp tạo một trang Facebook để rao hàng. </span>Sau khi thu mua trứng từ một trang trại địa phương, cậu nhóc bán với giá 2 bảng 6 quả, 3,5 bảng 12 quả và 6 bảng 30 quả. </p><p class="Normal"> 	Tuần trước, em bán được 750 quả trứng và kiếm được khoảng 250 bảng và với số khách ngày càng tăng, ước tính một năm ít nhất em sẽ thu được 13.000 bảng (khoảng gần 379 triệu đồng. </p><p class="Normal"> 	<span>"Cháu rất hào hứng khi tự kiếm được tiền và gặp gỡ nhiều người. Cháu đã dùng những đồng đầu tiên mua bộ chơi bóng đá của đội tuyển mình yêu thích nhưng từ giờ cháu sẽ tiết kiệm tất cả lợi nhuận để sau này trở nên giàu có. Ước mơ của cháu là trở thành một người thành công, một vận động viên đấm bốc chuyên nghiệp và có căn biệt thự để mọi người trong gia đình ở cùng cháu mãi mãi", Junior nói với </span><em>The Sun</em><span>. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194338828358755.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cậu bé 8 tuổi Junior Wyatt muốn trở thành triệu phú và bắt đầu thực hiện giấc mơ từ việc bán trứng. Ảnh: <em>NWNS.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Cậu bé nói lời cảm ơn tới tất cả những người đã ủng hộ việc buôn bán của mình, đặc biệt là mẹ, bố và cô em gái nhỏ. </p><p class="Normal"> 	Bố mẹ của cậu nhóc, đều 26 tuổi, nói rằng họ rất hạnh phúc vì sự kiên định và đầu óc kinh doanh của con trai.</p><p class="Normal" style="text-align:right;"> 	<strong>Vương Linh</strong></p>', 1, '15001943381446496753.jpg', NULL),
(192, 'Những ngôi nhà, văn phòng mới lạ bằng container', 'Các thùng container cũ được tái chế, sửa chữa thành văn phòng kinh doanh hoặc nhà ở.', '<p class="Normal"> 	<span>Những nă</span>m gần đây, công trình bằng container xuất hiện ngày càng nhiều. Năm 2006, làng sinh viên Keetwonen (Amstesdam, Hà Lan) lắp đặt hơn 1.000 căn hộ tiện nghi từ các thùng container. Trên thế giới và Việt Nam cũng xuất hiện không ít các công trình hoán cải từ container.</p><p class="Normal"> 	<span>Container được sử dụng làm văn phòng, quán cà phê, khách sạn, khu nghỉ dư</span><span>ỡng… So với nhà truyền thống xây dựng bằng các vật liệu quen thuộc, công trình container mang đến cảm giác mới mẻ, tạo cảm hứng làm việc nhất là với giới trẻ. </span></p><p class="Normal"> 	<span>Loại hình này có ưu điểm xây dựng nhanh chóng, lắp đặt trên mọi địa hình, chi phí thấp, thân thiện với môi trường, hạn chế tạo ra rác thải xây dựng, đáp ứng nhu cầu thực tiễn đời sống. Dưới bàn tay thiết kế và óc sáng tạo của kiến trúc sư, các công trình bằng container ngày càng độc đáo, cách âm và giảm nhiệt tốt.</span></p><table border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943391879281334.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Văn phòng container tại quận 2, TP HCM.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>"Tậ</span><span>n dụng container cũ, tự sửa chữa, tái chế thành văn phòng để kinh doanh hay sinh hoạt ngày càng trở nên phổ biến. Tuy nhiên nhiều người mới lo giải bài toán kinh tế, mà quên mất khâu an toàn", bà Kiều Ngọc Phương - Phó tổng giám đốc Công ty CP Thương mại Cơ khí Tân Thanh cho biết.</span></p><p class="Normal"> 	Theo bà Phương, hiện trên thị trường có nhiều nguồn container đã qua sử dụng. Người mua container cũ về cắt, sửa chữa thành công trình container phục vụ nhu cầu sinh hoạt, kinh doanh vì cho rằng tự gia công sẽ tiết kiệm được chi phí. Thực tế, điều này tiềm ẩn rủi ro nếu thiếu kinh nghiệm, kiến thức về kết cấu container, thiết bị phù hợp để thi công an toàn.</p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/150019434014438272.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mẫu thiết kế công trình từ container hoán cải.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Bà Phương chia sẻ thêm, khi người thi công không nắm vững đặc tính kỹ thuật của container, tay nghề non yếu, tự cắt sẽ làm ảnh hưởng đến độ vững của khung container và hư hỏng kết cấu. Ngoài ra, khung xương không đi đúng cách còn gây khó khăn trong việc thi công dây âm tường cũng như lắp đặt các trang thiết bị bên trong.</p><p class="Normal"> 	Việc lắp đặt sai cách kéo theo nguy cơ rò điện dẫn đến cháy nổ, hoặc rỉ nước gây hư vách. Đọng nước trên nóc cũng khiến <span>container </span><span>rỉ sét và hư hại nhanh chóng, giảm tuổi thọ công trình và tiềm ẩn nguy hiểm khi sử dụng. Chính vì vậy, yếu tố kinh nghiệm gia công cơ khí, kiến thức an toàn trong việc hoán cải các container cần đặt lên hàng đầu.</span></p><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194340552098567.jpeg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Phòng khách trong công trình container tại triễn lãm Vietbuild Cần Thơ.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Bà Phương dẫn một số ví dụ cho thấy cá nhân, doanh nghiệp không chuyên tự hoán cải container, khiến thời gian thi công kéo dài, công trình chưa đạt yêu cầu về thẩm mỹ và an toàn, khó lắp đặt nội thất. Bà tư vấn, cần tính toán nâng - hạ container sao cho an toàn, không bị lệch trước khi đưa vào sử dụng. Có thể tìm đơn vị chuyên thi công container uy tín và kinh nghiệm lâu năm, để được tư vấn các biện pháp giảm thời gian và chi phí.</p><p class="Normal" style="text-align:right;"> 	<strong>An San</strong></p>', 1, '15001943391038519584.png', NULL),
(193, 'Bé gái được dựng biển quảng cáo sau khi phá vỡ ''lời nguyền'' gia tộc', 'Cô bé người Mỹ là bé gái đầu tiên của gia tộc sau 137 năm chỉ sinh toàn con trai.', '<p class="Normal"> 	<span>Ngày 25/6 vừa qua, vợ anh Will Settle, 38 tuổi, ở bang South Carolina, Mỹ, vừa hạ sinh bé gái </span><span>Carter Louise Settle. Cô bé ra đời không chỉ mang lại niềm vui cho vợ chồng anh, mà còn có ý nghĩa to lớn với đại gia đình nhà Settle. Bởi lẽ suốt 137 năm qua, gia tộc này chỉ toàn sinh con trai.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943421004680735.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cô bé được dựng biển quảng cáo ngay đường quốc lộ. <em>Ảnh: ABC News.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	"Đó là một cú sốc cho tôi, vì tôi vẫn luôn nghĩ rằng tôi sẽ lại có một cậu bé khác. Tôi không thể tưởng tượng nổi. Cả gia đình tôi đã ở đó khi em bé sinh ra. Mẹ tôi đã vui sướng phát điên khi biết có một cô cháu gái", anh Will chia sẻ trên tờ <em>ABC News.</em></p><p class="Normal"> 	Để chúc mừng tin vui này, đồng nghiệp của anh Will đã cùng dựng một tấm biển quảng cáo, cao 3,6m và rộng 7,3m tại một vị trí bắt mắt ngay đường quốc lộ South Carolina. Tấm biển màu hồng với dòng chữ: "Chào mừng đến với thế giới này, <span>Carter Louise Settle".</span></p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '1500194342957651916.jpg', NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(194, 'Cặp vợ chồng Hà Nội khổ sở vì xây nhà hoành tráng cho đại gia đình', 'Dự định ở chung với gia đình 4 người con nên ông Hoàng xây nhà 6 tầng, nhưng được hai năm thì quyết định bán.', '<p class="Normal"> 	Rao bán nhà cả năm nay nhưng ông Hoàng (quận Hai Bà Trưng, Hà Nội) vẫn chưa tìm được người mua. Ngôi nhà đồ sộ của ông nằm trên mảnh đất rộng 120 m2 (mặt tiền 6m).</p><p class="Normal"> 	4 con ông đều đã lập gia đình nhưng phải đi thuê trọ hoặc ở nhà tập thể cũ. Bởi vậy, cách đây 3 năm, ông Hoàng quyết định quy tụ cả nhà ở chung một chỗ.</p><p class="Normal"> 	Xác định nhà là để ở cả đời mình, đời con nên ông Hoàng quyết tâm đầu tư đồ tốt, xịn để "ăn chắc, mặc bền". Tầng một là không gian sinh hoạt chung, chỗ để xe. Từ tầng 2 tới tầng 6, mỗi tầng giống như một căn hộ khép kín.</p><p class="Normal"> 	Bà Lan, vợ ông Hoàng, dự tính, với cách bố trí như vậy, cả nhà quây quần bên nhau mà vẫn có thể sống độc lập. Các con có thể tự nấu nướng, ăn uống, sinh hoạt theo giờ của mình.</p><p class="Normal"> 	Tuy nhiên, khi cả nhà chuyển về sống chung mới nảy sinh nhiều vấn đề. Ngoài 2 chiếc xe ôtô gửi ngoài, trong nhà còn có tới 8 xe máy, xe đạp của các cháu để bày bừa ở tầng một. Khách vừa vào nhà là có cảm giác ngột ngạt, khó chịu.</p><p class="Normal"> 	Tầng một, sảnh hành lang, cầu thang là không gian chung nên chẳng ai chịu trách nhiệm, phó mặc hết cho bố mẹ. Ông Hoàng phải tuyển giúp việc mới liên tục vì họ mệt mỏi với việc dọn dẹp đống bày bừa của đại gia đình hơn 20 người.</p><p class="Normal"> 	Mang tiếng nhà riêng, nhưng nơi ở của gia đình ông Hoàng giờ giống như một khu tập thể mini. Dù ăn riêng nhưng 5 gia đình cũng nhiều lần va chạm, phải cố tươi cười nhưng bên trong không hài lòng.</p><p class="Normal"> 	Chán nản vì suốt ngày nghe hết con trai cả tới con gái út nói xấu nhau, ông Hoàng - bà Lan quyết định bán nhà để chia tiền cho các con mua nhà riêng.</p><p class="Normal"> 	Khi ấy, ông bà mới thấy nuối tiếc việc xây nhà hoành tráng nhất khu của mình. Đất rộng, nhà xây toàn bằng vật liệu đắt tiền, thiết bị nội thất cũng tốn kém. Bởi vậy, ông định giá tới 20 tỷ mới đủ bù lỗ.</p><p class="Normal"> 	Công trình thiết kế theo phân khúc nhà riêng, có cầu thang ở giữa nên không phù hợp làm văn phòng. Nếu muốn sửa thì chủ mới lại tốn nhiều tiền đập bỏ các khu bếp tiện nghi. Người có khoản tiền lớn để mua căn nhà như vậy thì cũng không muốn ở trong ngõ. Họ có thể mua biệt thự ở các khu chung cư rộng rãi, xe ôtô vào tới tận nhà.</p><p class="Normal"> 	Chính vì "trở đi mắc núi trở lại mắc sông" như vậy mà đến giờ hai người con lớn đã đòi ra ở riêng cho thoải mái. Chỉ còn ông bà Hoàng sống trong cảnh bằng mặt mà không bằng lòng với hai người con út, cũng không biết bao giờ mới bán được nhà để chia.</p><p class="Normal"> 	<span style="color:#333333;"><strong>Không chỉ riêng nhà ông Hoàng mà rất nhiều gia đình rơi vào cảnh lãng phí do không tính hết nhu cầu thực tế như vậy.</strong></span> Bố mẹ luôn muốn con cái sống chung nên dành hết tiền bạc để xây những ngôi nhà lớn, nhiều tầng. Nhưng các tầng nhanh chóng bị bỏ trống sau một vài năm khi con cái lên thành phố hoặc ra ở riêng.</p><p class="Normal"> 	Từng nhận nhiều đề nghị thiết kế của các đại gia đình, kiến trúc sư Ngọc Anh cho biết, anh luôn đưa ra lời khuyên các chủ nhà nên tính trước mục đích sử dụng trong thời gian tối thiểu 10 năm. Bố mẹ cũng nên hỏi kỹ các con về kế hoạch riêng để xây cho phù hợp.</p><p class="Normal"> 	Ngoài ra, các gia chủ có thể tính tới giải pháp thiết kế nhà linh hoạt có thể chuyển đổi công năng. Ví dụ, cầu thang nên xây sát gọn vào góc để khi nhà còn ít người ở có thể chuyển thành văn phòng, lớp học cho thuê. Trên các mảnh đất rộng, gia chủ nên tính tới việc xây thành 2-3 ngôi nhà với lối đi riêng, bố mẹ và con có thể sống tách biệt. Khi không còn nhu cầu sử dụng, gia chủ sẽ dễ dàng bán, cho thuê căn nhỏ với giá tiền vừa phải, nhiều khách mua hơn.</p><p class="Normal" style="text-align:right;"> 	<strong>An Yên</strong></p>', 1, NULL, NULL),
(195, 'Vì sao người Nhật không thích làm ''chuyện ấy''', 'Nhiều người cho biết họ bị ám ảnh bởi phụ nữ, và cho rằng quan hệ yêu đương với một người là ''phiền phức''.', '<p class="Normal"> 	Một báo cáo mới của <em>BBC </em>cho biết, 43% người Nhật từ 18 đến 34 tuổi nói rằng họ chưa bao giờ quan hệ tình dục, 64% số người trong độ tuổi được hỏi cho hay họ chưa từng yêu ai.</p><p class="Normal"> 	Bị "ám ảnh" bởi phụ nữ, quá nhiều bộ phim khiêu dâm và quan niệm giới tính truyền thống là một số lý do khiến người Nhật không quan tâm tới sex.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194345876712275.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Nhiều đàn ông Nhật Bản thích thỏa mãn nhu cầu cá nhân với búp bê tình dục hơn là đi tìm một người phụ nữ. Họ cho rằng phụ nữ "quá phức tạp".<em> Ảnh: Asiaone.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Kể từ khi bị từ chối bởi một cô gái 26 tuổi, anh Ano Matsui đã không theo đuổi một mối quan hệ nào nữa. Anh nói những người đàn ông như anh thấy phụ nữ "đáng sợ" và muốn dành thời gian của mình cho những sở thích khác.</p><p class="Normal"> 	"Tôi ghét bản thân mình nhưng không thể làm bất cứ điều gì để thay đổi", anh Ano nói.</p><p class="Normal"> 	Trong khi đó, Anna, 24 tuổi, cho hay cô nghĩ rằng bạn trai sẽ hạn chế sự tự do của mình. <span>"Khi tôi lên đại học, bố mẹ cho tôi tự do hơn trước, cuối cùng tôi đã có thể uống rượu với bạn bè trễ hơn một chút. Nhưng nếu có một người bạn trai, anh ấy sẽ hạn chế sự tự do của tôi. Tôi không muốn điều đó", Anna nói. </span><span>Cô gái trẻ cũng cho biết cô chọn ăn và ngủ hơn là quan hệ tình dục.</span></p><p class="Normal" style="text-align:center;"> 	<span style="color:#666666;"><strong><span>Nhiều đàn ông Nhật chọn búp bê tình dục thay vì lấy vợ</span></strong></span></p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156012" data-component="true" data-component-type="video" data-component-value="156012" data-component-typevideo="2"></div></div> </div><p class="Normal"> 	Mặc dù nền công nghiệp sex ở Nhật Bản rất phát triển nhưng những người Nhật truyền thống vẫn coi sex là một điều cấm kỵ. </p><p class="Normal"> 	Một người phụ nữ ẩn danh cho hay: "Nhiều người không dám thể hiện ý kiến cá nhân hay suy nghĩ thật sự, đó là văn hóa của chúng tôi, che giấu những cảm xúc chân thật nhất. Nhiều người sợ khi nói tới vấn đề quan hệ tình dục vì lo ngại sẽ bị mọi người đánh giá bằng cái nhìn khác".<span style="text-align:center;"> </span></p><p class="Normal"> 	Một báo cáo của<em> Insider</em> cho biết những cuộc hôn nhân ở Nhật căng thẳng do thời gian làm việc dài: Người Nhật làm việc từ 10-12 tiếng một ngày so với chỉ 8 tiếng ở các nước phát triển khác. Họ cũng là một trong những nước có tỷ lệ sinh thấp nhất thế giới.</p><p class="Normal" style="text-align:center;"> 	<span style="color:#666666;"><strong>Có người Nhật còn coi búp bê tình dục như vợ hai của mình</strong></span></p><p class="Normal" style="text-align:center;"> 	<div id="video-156028" data-component="true" data-component-type="video" data-component-value="156028" data-component-typevideo="2"></div></p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943441702647105.jpg', NULL),
(196, 'Cô gái lột xác sau khi ''vứt bỏ'' 200 kg mỡ khỏi cơ thể', 'Cô gái trẻ người Mỹ đã có màn giảm cân ngoạn mục trong vòng một năm.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943461124567021.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Amber Rachdi đến từ Troutdale, Oregon, Mỹ bước qua tuổi 26 đã nặng gần 300 kg. Cân nặng của cô gái trẻ tăng chóng mặt vì bị mắc chứng thèm ăn từ khi lên 5 tuổi. Mỗi khi tâm trạng không tốt, Amber lại tìm đến việc ăn uống như một cách giải tỏa tinh thần. </p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="2" cellspacing="0" class="tplCaption"><tbody><tr><td style="text-align: center;"> 				<div style="text-align: center"><img src="/pictures/news/1500194346705082391.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Vì thân hình quá khổ nên Amber phải nhờ đến xe lăn để di chuyển. Trong một lần tới sân bay, xe lăn bị gãy, bố Amber đã phải dùng tới xe chở hàng để đưa cô đi.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943461773266074.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Các bác sĩ cảnh báo nếu không giảm cân, mạng sống của Amber sẽ bị đe dọa khi bước sang độ tuổi 30. </p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194346529235432.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Amber quyết tâm giảm cân sau đó khi loại bỏ 200 kg mỡ khỏi cơ thể trong một năm.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943461483568248.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Đầu tiên, cô gái trẻ đã phẫu thuật cắt bỏ mỡ thừa 127 kg. Tiếp đó là tuân thủ chế độ luyện tập nghiêm ngặt khi tham gia vào một chương trình giảm cân, để giảm tiếp được 54 kg nữa.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194346399203557.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Sau một năm, số cân nặng của cô đã giảm xuống chỉ còn 90 kg. Mặc dù vẫn còn những lớp da thừa nhưng Amber vẫn quyết tâm tập luyện để cơ thể săn chắc, thon gọn hơn.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943461545602171.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Cô gái trẻ giờ đã tự tin với vóc dáng hiện tại của mình, không còn sống thu mình khép kín như trước.</p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194346157671570.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Câu chuyện giảm cân của Amber trở thành tấm gương sáng cho những người béo phì.</p> 			</td> 		</tr></tbody></table><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943461369855119.jpg', NULL),
(197, 'Những lỗi lịch sự nơi công cộng rất nhiều người mắc phải', 'Ngồi dạng chân, cắt ngang hàng, đến cuộc hẹn muộn, bắt tay bằng tay trái...', '<p> 	Đó là lỗi lịch sự mà rất nhiều người mắc phải.</p><p> 	Video của <em>5-Minute Crafts</em> dưới đây sẽ liệt kê giúp bạn những hành động nào chấp nhận được, những hành động nào không nên làm để không rơi vào tình trạng xấu hổ:</p><div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-155980" data-component="true" data-component-type="video" data-component-value="155980" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:right;"> 		<strong> Hoàng Anh</strong></p> </div>', 1, NULL, NULL),
(198, 'Bố qua đời, con phát hiện số tiền lớn bị chuyển cho người giúp việc', 'Toàn bộ tiền tiết kiệm hơn 700.000 nhân dân tệ của cụ ông Trung Quốc nhiều lần được chuyển cho người giúp việc của gia đình.', '<p class="Normal"> 	Cuối năm 2016, cụ Huang Po, ở Quảng Đông, Trung Quốc qua đời vì bệnh nặng. Sau khi lo hậu sự xong, các con của cụ Huang phát hiện toàn bộ số tiền tiết kiệm của bố hơn 700.000 tệ (hơn 2 tỉ đồng) hai lần được chuyển sang tài khoản của bà Chen, người giúp việc kiêm trợ lý cho cụ Huang tại phòng khám tư nhiều năm qua. </p><p class="Normal"> 	Tháng 3 năm nay, 2 con trai cụ Huang đã kiện bà Chen và các con của bà ra tòa, yêu cầu trả lại toàn bộ số tiền.</p><p class="Normal"> 	Vụ việc đã được đưa ra tòa án để xét xử. Tòa xác định khoản tiền 700.000 tệ được chuyển sang tài khoản của bà Chen nhiều lần, lần đầu có cụ Huang đi cùng, còn lần sau chỉ có bà Chen đến giao dịch.</p><p class="Normal"> 	Bà Chen khai mọi việc bà làm đều có sự ủy thác của cụ Huang. Những lần không đi cùng là cụ Huang nói cho bà mật mã thẻ. Bà Chen nói đó là số tiền cụ Huang muốn cảm ơn vì đã chăm sóc và giúp đỡ ông. </p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194348605340792.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bà Chen tại tòa. <em>Ảnh: Sina.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Năm 1985, cụ Huang mở một phòng chăm sóc sức khỏe. Bà Chen làm tại đây cùng ông lo thuốc men, hậu cần. Bà vẫn làm ở đây cho đến khi cụ ông qua đời. <span>Mỗi tháng, bà được trả lương 5.000 tệ.</span></p><p class="Normal"> 	<span>"Ông Huang và tôi đã làm việc cùng nhau mấy chục năm. Chúng tôi đã cùng nhau kinh doanh phòng khám và kiếm tiền. Ông ấy nói sau này sẽ bù đắp cho tôi một khoản", bà Chen nói.</span></p><p class="Normal"> 	<span>Sau khi nghiên cứu các tài liệu liên quan, tòa án xác định cụ Huang qua đời không để lại căn cứ trên giấy tờ, không có người làm chứng nên không xác minh được di nguyện của cụ. Việc bà Chen biết mật khẩu của cụ Huang cũng không chứng minh được việc đã được ông ủy quyền. Những gì bà Chen khai là được cụ Huang tặng là"thỏa thuận miệng", không có giá trị pháp lý trước tòa.</span></p><p class="Normal"> 	Theo phán quyết của tòa, bà Chen cuối cùng phải trả lại toàn bộ số tiền cho các con cụ Huang.</p><p class="Normal" style="text-align:right;"> 	<strong>Mộc Miên</strong></p>', 1, '15001943481766151333.jpg', NULL),
(199, '8 mẹo đơn giản giúp bạn bán nhà dễ dàng', 'Bạn chỉ cần lau dọn lại các phòng, thêm một vài chi tiết trang trí cũng đủ để gây ấn tượng với khách hàng.', '<p class="subtitle"> 	1. Cất bớt đồ đạc</p><p class="Normal"> 	Đây là bước đầu tiên để bạn có được hình ảnh giới thiệu hoàn hảo về ngôi nhà sắp bán. Nếu đã có chỗ ở mới, bạn đóng gói, chuyển bớt một số đồ hoặc để chúng ở nhà kho, garage. Những thứ không còn sử dụng có thể quyên góp, cho tặng. Nhờ vậy, nhà sẽ trở nên rộng rãi, thoáng đãng hơn. Bạn nên để lại một số nội thất lớn (bàn ghế, tủ đồ...) để khi khách mua nhà dễ hình dung việc bố trí không gian và có cảm giác nhà ấm cúng.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194349859976781.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Bạn chỉ cần bỏ một ngày để tân trang nhà cũ. Ảnh minh họa: <em>Koth. </em></p> 			</td> 		</tr></tbody></table><p class="subtitle"> 	2. Lau sạch sẽ mọi thứ</p><p class="Normal"> 	Trước khi chụp ảnh hoặc mời khách tới tham quan, bạn hãy làm sạch sàn nhà, cửa sổ, khu vệ sinh cũng như tất cả các bề mặt bàn ghế. Trong tâm trí của phần lớn người mua, một ngôi nhà sạch là một tổ ấm được chăm nom kỹ càng. Họ cũng dễ suy luận nhà được xây dựng, cải tạo với chất lượng tốt.</p><p class="subtitle"> 	3. Cất bớt đồ cá nhân</p><p class="Normal"> 	Mục tiêu của bạn là khiến những khách hàng tiềm năng cảm thấy ngôi nhà của bạn là dành cho họ. Bởi vậy, những bức ảnh cá nhân, đồ lưu niệm yêu thích của bạn có thể khiến họ thấy xa lạ.</p><p class="subtitle"> 	4. Sơn lại nhà</p><p class="Normal"> 	Theo <em>Wisebread</em>, sơn tường là cách đơn giản và ít tốn kém nhất để làm mới một ngôi nhà. Chọn màu sơn trung tính sẽ giúp bạn thu hút được nhiều khách hơn. Màu trắng, kem chủ đạo với điểm nhấn là sắc xanh có thể khiến không gian sinh động nhưng vẫn gần gũi, dịu mát. Bạn nên lựa chọn loại sơn dễ lau chùi cho các khu vực hay dính bẩn như phòng tắm, bếp.</p><p class="subtitle"> 	5. Thêm các chi tiết trang trí</p><p class="Normal"> 	Hãy bổ sung những chiếc gối màu sắc, rèm cho nhà tắm, trải tấm ga mới và rèm cửa sổ nhẹ nhàng. Nếu mua đồ mới, bạn nên chọn những kiểu dáng đơn giản, màu sắc hài hòa với nội thất có sẵn. Cuối cùng, bạn bổ sung một vài chậu cảnh trồng trong nhà, hãy bỏ qua hoa tươi vì bạn phải thay mới thường xuyên. Ngoài ra, có một số người bị dị ứng với phấn hoa.</p><p class="subtitle"> 	6. Tẩy trừ mùi khó chịu</p><p> 	Khi ở lâu trong một ngôi nhà, bạn sẽ quen với mùi của nơi ở đó nên đôi khi không thấy phiền toái với sự ẩm mốc, mùi của chó mèo. Cách đơn giản nhất là bạn hãy mở tung tất cả cửa sổ và bật một vài chiếc quạt, thay thảm. Phòng tắm, tầng hầm sẽ có mùi dễ chịu hơn với quạt thông gió, máy hút ẩm. Bạn không nên lạm dụng quá nhiều nến thơm, tinh dầu nồng nàn, hãy chọn các mùi hương nhẹ nhàng.</p><p class="subtitle"> 	7. Quan tâm tới vẻ ngoài của nhà</p><p class="Normal"> 	Hãy để người mua ấn tượng ngay khi họ nhìn thấy bên ngoài nhà, đặt bước chân đầu tiên lên bậc cửa. Bạn hãy sơn mới mặt tiền, quét dọn lối vào, thay thế những bóng đèn vỡ, đặt một vài chậu cây xanh tốt.</p><p class="subtitle"> 	8. Đơn giản mọi chuyện</p><p> 	Việc bán nhà thường dễ gây căng thẳng. Bạn có thể vừa phải thu dọn, chuyển đồ và tìm nơi ở mới, bạn vừa phải đảm bảo cho nhà cũ luôn tinh tươm. Bởi vậy, bạn đừng cố để tiến hành sửa chữa những hạng mục lớn, tốn kém. Hãy từ bỏ ý định lát lại sàn nhà tắm, biến gác lửng thành phòng sinh hoạt chung, mở rộng hiên nhà... Mục tiêu của bạn là làm nhà trông gần gũi, ấm cúng để thu hút khách chứ không phải cải tạo mọi thứ. Việc sửa chữa tốn kém sẽ khiến bạn mệt mỏi và thêm nhiều chi phí.</p><p style="text-align:right;"> 	<strong>An Yên</strong></p>', 1, '1500194349964020372.jpg', NULL),
(200, '700 triệu nên mua đất ở đâu tại ngoại thành TP HCM?', 'Vợ chồng tôi có 700 triệu và đang tìm mua đất ở TP HCM, sao cho trong vòng 2-3 năm nữa bán sẽ được giá.', '<p class="Normal"> 	Hiện tại, chúng tôi chưa có nhà riêng, đang ở nhờ nhà bố mẹ và rất chật chội, hai vợ chồng và đứa nhỏ chỉ có một không gian khoảng 20m2. Bố mẹ tôi ở quận Tân Bình, vợ chồng tôi làm việc tại quận Phú Nhuận.</p><p class="Normal"> 	Chúng tôi dự định dùng số tiền hiện có để mua một miếng đất vùng ven, như quận 9, Hóc Môn hay Bình Chánh, chờ sau này được giá sẽ bán để lấy tiền mua chung cư khu vực Tân Bình hay Gò Vấp, Phú Nhuận để được ở gần bố mẹ và gần chỗ làm. Theo các bạn, tính toán của chúng tôi có hợp lý không? Chúng tôi nên mua đất vùng ven ở đâu để nhanh sinh lời nhất.</p><p class="Normal" style="text-align:right;"> 	<strong>Mỹ</strong></p><p class="Normal"> 	<em style="margin:0px;padding:0px;line-height:18px;"><span style="margin:0px;padding:0px;color:#222222;">Chia sẻ các thắc mắc, kinh nghiệm mua nhà đất, tiêu dùng </span></em><em style="margin:0px;padding:0px;line-height:18px;"><span style="margin:0px;padding:0px;color:#222222;">của bạn </span><a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2" style="margin: 0px; padding: 0px; color: #004f8b; text-decoration-line: none; outline: 1px;"><span style="margin:0px;padding:0px;color:#008080;"><span style="margin:0px;padding:0px;">tại đây</span></span><span style="margin:0px;padding:0px;color:#222222;">.</span></a></em></p>', 1, NULL, NULL),
(201, '8 đồ chơi nguy hiểm nhất cho trẻ', 'Bộ đồ chơi tập làm bác sĩ trông có vẻ vô hại và được nhiều trẻ thích nhưng có thể tiềm ẩn nhiều nguy cơ khó lường. ', '<p class="Normal"> 	<span>Trẻ phát triển và khám phá thế giới bằng cách chơi nhưng có một số loại đồ chơi mang đến mối nguy hơn là điểm tốt. </span><em>Bright Side</em><span> giới thiệu 8 loại sản phẩm bạn nên thận trọng khi mua cho trẻ cũng như lời khuyên để chọn được món đồ an toàn hơn cho bé:</span></p><p class="subtitle"> 	<span>Các đồ chơi có cánh quạt</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943531433918349.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Máy bay trực thăng và các loại đồ chơi khác có cánh chỉ nên dành cho trẻ tuổi teen. Trẻ nhỏ chưa thể sử dụng được các thứ này và dễ bị thương khi va vào cánh quạt quay.</p><p class="Normal"> 	<em>Lời khuyên:</em> Chọn đồ chơi có cánh làm từ nhựa mềm và cạnh ngoài của cánh không sắc nhọn.</p><p class="subtitle"> 	Bộ đồ chơi bác sĩ</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194353719928244.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Trẻ thường thích sử dụng các dụng cụ trong bộ đồ chơi này và một vài thứ trong đó có các bộ phận nhỏ có thể bị mắc trong mũi hay đường thở của trẻ. Ngoài ra, những đồ chơi kiểu này cũng kích thích trẻ quan tâm hơn đến ống tiêm, kéo và các viên thuốc thật.</p><p class="Normal"> 	<em>Lời khuyên:</em> Tốt nhất nên sử dụng các bộ đồ dùng bác sĩ không có bất cứ các phần nhỏ, ống tiêm hay các dụng cụ trẻ dễ cho vào miệng, mũi. </p><p class="subtitle"> 	Phao tròn và phao tay</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943531609090421.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	<span>Phao tròn, phao tay không hoàn toàn đảm bảo an toàn khi ở dưới nước vì trẻ dễ dàng bị trượt hay lật úp. Ngoài ra, một số sản phẩm loại này chứa các chất độc hại như </span><span>isophorone, phenol và hexanone có thể gây ngứa hay các vấn đề về lâu dài khác.</span></p><p class="Normal"> 	<span><em>Lời khuyên: </em>Kích cỡ phao tròn và phao tay nên vừa vặn với trẻ, sử dụng dưới sự giám sát của người lớn và không nên đeo quá nửa tiếng. </span></p><p class="subtitle"> 	Các đồ chơi dùng pin nhỏ</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943531187642966.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Những viên pin tròn dẹt trông giống như những viên kẹo và con bạn có thể muốn nếm thử. Kích cỡ và hình dáng của chúng cũng dễ dàng nuốt vào và chất lithium bên trong chúng có thể gây ngộ độc và bỏng nghiêm trọng. </p><p class="Normal"> 	<span><em>Lời khuyên:</em> Những viên pin này nên được cố định chặt trong đồ chơi để trẻ không lấy ra nghịch.</span></p><p class="subtitle"> 	<span>Súng đồ chơi</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194353836637610.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	<span>Trẻ thường không để ý tới khoảng cách an toàn hay dùng nước sạch khi sử dụng súng đồ chơi. Hệ quả, đồ chơi này được bắn bằng bất cứ loại "đạn" nào, kể cả nước bẩn, khí phun nên gây ra các chấn thương ở mặt, đặc biệt là mắt. </span></p><p class="Normal"> 	<span><em>Lời khuyên:</em> Mua các đồ chơi này kèm kính bảo vệ hay mua kính riêng cho trẻ dùng khi chơi.</span></p><p class="subtitle"> 	Bộ đồ chơi nam châm</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943541183922132.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Đồ chơi nam châm phổ biến thường được dán nhãn dành cho trẻ từ 3 tuổi trở lên nhưng thực tế những món đồ này nên dành cho trẻ trên 14 tuổi. Khi cố gắng thử tách các viên nam châm bằng răng, trẻ thường nuốt không chỉ một mà vài viên. Khi ở bên trong cơ thể, những viên nam châm này có thể hút nhau và gây hại cho thành ruột.</p><p class="Normal"> 	<em>Lời khuyên: </em>Không nên cho trẻ nhỏ chơi bộ nam châm này, với trẻ lớn hơn, các viên nam châm cần được bảo vệ bằng lớp ngoài bằng nhựa cứng sẽ an toàn hơn. </p><p class="subtitle"> 	Bóng bay</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194354486656071.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Bóng bay có thể nổ khi thổi căng phồng hay lúc chơi khiến trẻ sợ, thậm chí gây nguy cơ điếc. Khi sợ hãi, trẻ nhỏ có thể hít các mảnh vỡ của quả bóng và bị ngạt. Đó là lý do vì sao tất cả các sự kiện có nhiều bóng bay được khuyên chỉ nên dành cho trẻ trên 8 tuổi. </p><p class="Normal"> 	<em>Lời khuyên:</em> Đừng mua bóng bay có bề mặt nhăn nhúm hay có mùi khó chịu. Hạn chế cho trẻ nhỏ chơi bóng bay.</p><p class="subtitle"> 	Đồ chơi xoay bằng tay (spinner)</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943541034937612.jpg" /></div></td> 		</tr><tr><td> 				 </td> 		</tr></tbody></table><p class="Normal"> 	Một phần đồ chơi này có thể tách ra trong quá trình quay và gây chấn thương. Cũng có các trường hợp trẻ nuốt phải các mảnh nhỏ trong đó. Ngoài ra, những đồ chơi kiểu này cản trở trẻ tập trung, dẫn tới các vấn đề học tập.</p><p class="Normal"> 	<em>Lời khuyên: </em>Thay thế bộ phận xoay bằng chính các ngón tay của trẻ giúp bé phát triển kỹ năng vận động. </p><p class="subtitle"> 	Làm thế nào để chọn được đồ chơi an toàn: </p><p class="Normal"> 	Hằng năm, Tổ chức ngăn ngừa các đồ chơi gây hại trên thế giới đều công bố 10 loại đồ chơi nguy hiểm nhất. Bạn có thể theo dõi thông tin này. Tương tự, bạn có thể tham khảo lời khuyên hữu ích từ Hướng dẫn lựa chọn sản phẩm an toàn cho trẻ của Hiệp hội an toàn trẻ em châu Âu:</p><p class="Normal"> 	- Kích cỡ: Bất cứ đồ chơi nào đều nên có kích cỡ không dưới 6cm chiều dài và 3cm chiều rộng. Nếu đồ chơi hình tròn thì đường kính nên lớn hơn 5cm. </p><p class="Normal"> 	- Màu sắc: Tránh những đồ chơi có màu rực rỡ. Màu vàng rực đặc biệt không nên chọn bởi nguy cơ cao có chì bên trong. </p><p class="Normal"> 	- Hình dạng và chất liệu: Đồ chơi không nên có cạnh sắc hay các lỗ rộng khiến trẻ dễ bị đứt hoặc mắc kẹt ngón tay. Cố gắng chọn những đồ chơi bằng vải hay gỗ không sơn, nhuộm. </p><p class="Normal" style="text-align:right;"> 	<strong>Vương Linh</strong></p>', 1, '1500194353551364059.jpg', NULL),
(202, 'Chúng tôi tốn cả trăm triệu và bị hành xác khi chụp ảnh cưới ở xa', '"Tổng tiền chi cho quay phim chụp ảnh lên đến cả trăm triệu, nhưng tôi chỉ dám kể với bố mẹ là vài chục", anh Hùng kể.', '<p class="Normal"> 	<em>Dưới đây là chia sẻ của anh Đức Hùng, sống tại Hà Nội, về chuyến chụp hình cưới tại Nha Trang - Phú Yên của anh vào dịp hè năm ngoái.</em></p><p class="Normal"> 	Tôi biết, bây giờ các cặp đôi đang có mốt dắt nhau đi thật xa để chụp ảnh cưới, kết hợp với du lịch. Tôi cũng từng làm vậy, thực hiện xong mới thấy mình sai lầm, vì chỉ có thể chụp ảnh chứ không thể du lịch. Nếu bạn có nhiều thời gian, bạn được chụp hình miễn phí hoặc người chụp cùng chia tiền đi lại, ăn ở với bạn thì bạn đi xa chụp cũng không có vấn đề gì. Còn không, bạn sẽ rơi vào tình cảnh như vợ chồng tôi, <span>vừa tốn tiền vừa bị hành xác để đổi lấy một bộ ảnh cưới cũng không có gì quá đặc biệt.</span></p><p class="Normal"> 	Tôi quê ở Ninh Bình còn vợ ở ngoại thành Hà Nội. Chúng tôi cùng làm việc ở thủ đô. Hai đứa không tổ chức cưới ở khách sạn mà tổ chức ở quê, theo ý nguyện của bố mẹ. Vợ chồng có làm một tiệc báo hỷ nhỏ cho bạn bè thân trên Hà Nội.</p><p class="Normal"> 	Chúng tôi đã tiết kiệm được 200 triệu để lo cho đám cưới. Khi bố mẹ tuyên bố hỗ trợ tiền lo tiệc cưới ở quê nhà, không phải lo vấn đề kinh tế nữa, hai đứa càng quyết tâm đi Nha Trang và Phú Yên chụp hình, vì chúng tôi cũng chưa bao giờ tới đó.</p><p class="Normal"> 	Chúng tôi thuê một ê kíp thợ chụp hình tại Hà Nội. Theo báo giá, nếu chụp trong nhà, album tốn khoảng 20 triệu, chụp ngoài trời, album tốn 30 triệu. Trong đó, chúng tôi được trang điểm cô dâu miễn phí, được mượn ba bộ váy cưới để thay đổi. Ngoài một cuốn album, chúng tôi sẽ được in khoảng 10 ảnh khổ 1m x 1,5m để treo bên ngoài sảnh. Chúng tôi cũng được chụp hình trong đám cưới nhưng không in ảnh. Ngoài ra, chúng tôi được làm hai video.</p><p class="Normal"> 	Tuy nhiên, vì đi chụp hình ngoài Hà Nội nên chúng tôi phải lo hết chi phí ăn ở cho ê kíp chụp. Tổng tiền vé máy bay cho hai chúng tôi và ê kíp 5 người gồm thợ ảnh, trang điểm, quay video là 21 triệu. Chúng tôi ở khách sạn 4 sao và đương nhiên cũng phải thuê khách sạn cho ê kíp nọ, tổng 3 đêm hết 15 triệu. Tiền ăn uống cho 7 bữa hết khoảng 10 triệu, thực ra toàn ăn cơm bụi cho nhanh, chỉ có hai bữa ăn hải sản. Chúng tôi mất gần 5 triệu để mua vé vào một trung tâm giải trí lớn ở Nha Trang, tiền taxi di chuyển giữa các địa điểm chụp hình cũng lên đến hàng triệu. Tức là, nếu so với chụp hình tại Hà Nội, vợ chồng tôi tự dưng mất khoảng 60 triệu. </p><p class="Normal"> 	<span>Ngoài việc tốn thêm tiền, thực sự đi chụp ảnh xa cũng khiến chúng tôi mệt thêm, mất nhiều thời gian hơn trong khi ngày cưới đang cận kề và quỹ thời gian eo hẹp.</span></p><p class="Normal"> 	Hai lần đi và về, từ Hà Nội đến Nha Trang và từ Phú Yên về Hà Nội, chúng tôi đều bị hoãn chuyến bay, ngồi vạ vật chờ đợi ở sân bay mỗi lần thêm hơn tiếng đồng hồ. Rồi thời gian đi từ sân bay về nhà khoảng hai tiếng, vậy là mỗi lần đi về cũng mất nửa ngày.</p><p class="Normal"> 	Ba ngày chụp hàng trăm kiểu ảnh, nhiều địa điểm khác nhau, vợ tôi phải dậy sớm từ 5 giờ để trang điểm, tôi tranh thủ chạy ra biển tắm được vài phút, nói chung là chưa kịp đã. Đi đâu cũng vội vã như bị đuổi, chưa xong điểm này đã lo di chuyển sang địa điểm khác. Chúng tôi vào trung tâm giải trí nọ, không kịp nhìn ngắm gì chứ đừng nói đến chơi trò gì ở đây, tất bật chọn địa điểm và tất bật thu dọn đồ nghề đi nơi khác. </p><p class="Normal"> 	Chuyến chụp ảnh cưới lấy đi của vợ tôi 3kg và của tôi 2kg. Vợ tôi mặt lên đầy mụn, cô ấy còn phải truyền nước để đảm bảo sức khỏe cho đám cưới. Hành trình vội vã ấy cũng khiến chúng tôi có cảm giác Nha Trang và Phú Yên chẳng có gì thú vị. Địa điểm nào chúng tôi cũng ghé qua theo gợi ý của thợ, và địa điểm nào cũng chỉ ở vài phút, chưa kịp nhìn thấy điều gì hay mà chỉ thấy mệt.</p><p class="Normal"> 	Đáng nói là ngoài album ảnh mà chúng tôi còn giữ đến nay và một bức hình khổ lớn treo ở phòng ngủ, còn lại tất cả những bức hình trưng bày ở đám báo hỷ, chúng tôi đều không biết giờ ở đâu. Đôi lúc vợ chồng tôi nói đùa, biết đâu trung tâm tiệc cưới đã ném nó vào thùng rác và biết đâu ngày nào nó sẽ bị troll trên mạng. Chúng tôi không có nhu cầu lấy số ảnh này về do chả biết để ở đâu trong nhà.</p><p class="Normal"> 	Tôi cũng phải kể thêm vì gói chụp hình có quay video và chụp trong tiệc cưới nên những anh thợ cũng đi theo xe cưới về quê. Họ đi xa, gia đình lại phải dành một mâm. Đến lúc họ về, vì không kịp đi cùng xe của gia đình, chúng tôi lại phải thuê taxi riêng cho họ. Thợ ảnh, thợ quay phim nhiều đến mức mọi người trêu đám cưới không khác gì của người nổi tiếng vì có nhiều paparazzi chạy theo.</p><p class="Normal"> 	Đám cưới qua đi, cuộc sống trở lại bình thường. Album ảnh cưới được cất gọn trên giá sách. Mấy ngày sau đám cưới, chúng tôi tung vài bức hình và video lên facebook để câu like của bạn bè, rồi cũng chán. Theo thời gian, chúng tôi đã tạm quên đi vụ chụp hình.</p><p class="Normal"> 	Hè này, vợ chồng tôi vừa có dịp trở lại Nha Trang. Có thời gian để khám phá những món ăn, cảnh đẹp nơi đây, chúng tôi càng thấm thía mình đã quá lãng phí tiền bạc và thời gian khi đi chụp ảnh cưới ở xa, tự nhiên mất tiền, không được hưởng thụ gì mà còn bị hành xác.</p><p class="Normal"> 	<em>* Tên nhân vật đã thay đổi</em></p><p class="Normal" style="text-align:right;"> 	<strong>Đức Hùng</strong></p><p class="Normal"> 	<em style="margin:0px;padding:0px;line-height:18px;color:rgb(51,51,51);">Gửi thắc mắc và chia sẻ kinh nghiệm chi tiêu của bạn <a href="http://giadinh.vnexpress.net/postarticle/index/cateid/1003806/type/2" style="margin: 0px; padding: 0px; color: rgb(0, 79, 139); text-decoration-line: none;">tại đây</a>.</em></p>', 1, NULL, NULL),
(203, 'Nhện đực Tarantula liều chết để giao phối', 'Nhện đực Tarantula dù biết có thể sẽ mất mạng nhưng vẫn liều lĩnh áp sát con cái để bắt đầu cuộc giao phối.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156910" data-component="true" data-component-type="video" data-component-value="156910" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 Cuộc giao phối cảm tử của nhện đực Tarantula. Video: <em>National Geographic</em>.</p> </div><p class="Normal"> 	Một sinh viên ngành sinh học động vật hoang dã quay được cảnh cặp nhện Tarantula giao phối trong tự nhiên ở tỉnh Limpopo, Nam Phi, theo <em>National Geographic</em>.</p><p class="Normal"> 	Trong video, dấu hiệu rung chân của con đực cho thấy hai con vật chuẩn bị giao phối. <span>Nhện đực tỏ ra cẩn trọng trong toàn bộ quá trình vì nhiều nhện cái lạnh lùng ăn thịt bạn tình sau cuộc giao phối.</span></p><p class="Normal"> 	Nhện đực trong tư thế các chân trước có lớp lông bảo vệ giơ cao, tiếp cận con cái để ngăn đôi nanh độc của bạn tình cắn vào mặt. Sau cuộc ghép đôi chóng vánh và căng thẳng, nhện đực nhanh chóng tháo chạy để bảo toàn tính mạng.</p><p class="Normal"> 	<span>Cảnh nhện Tarantula giao phối ít được bắt gặp trong tự nhiên. Hành vi này thường được quan sát thấy ở những con trong môi trường nuôi nhốt. </span></p><p class="Normal"> 	<span>Theo nhiều nghiên cứu, hành vi ăn thịt con đực của nhện cái sau giao phối liên quan đến các lý do tiến hóa phức tạp như lợi ích cho loài hoặc sự cạnh tranh của tinh trùng.</span></p><p class="Normal"> 	<span>Tuy nhiên, hai nhà nghiên cứu Shawn Wilder và Ann Rypstra tại Đại học Miami, Ohio, Mỹ trong nghiên cứu đăng trên tạp chí American Naturalist cho biết lý do nằm ở sự tương quan kích cỡ giữa hai con vật. </span></p><p class="Normal"> 	<span>Những con cái to lớn ăn bạn tình chỉ vì chúng đói hoặc có thể ăn được bạn tình. Ở loài nhện sói, Wilder và Rypstra nhận thấy những con đực lớn không bao giờ bị bạn tình ăn thịt trong khi tỷ lệ con đực nhỏ bị ăn thịt là 80%. Quy tắc về kích thước này cũng đúng ở nhiều loài nhện khác.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, NULL, NULL),
(204, 'Sức mạnh của súng bắn nước lớn nhất thế giới', 'Một cựu kỹ sư NASA chế tạo khẩu súng phun nước lớn nhất trên thế giới, có thể bắn ra tia nước với vận tốc 437 km/h.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156914" data-component="true" data-component-type="video" data-component-value="156914" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Súng phun nước siêu lớn của Rober. Video: <em>YouTube. </em></p> </div><p class="Normal"> 	Mark Rober, cựu kỹ sư từng làm việc tại Phòng Thí nghiệm Sức đẩy Phản lực (JPL) của Cơ quan Hàng không Vũ trụ Mỹ (NASA), chế tạo thành công súng phun nước đồ chơi dài 2,1 m có thể bắn ra tia nước với vận tốc 437 km/h, đủ sức bắn nát một quả dưa hấu và làm vỡ cửa kính, <em>Red Orbit</em> hôm 12/7 đưa tin.</p><p class="Normal"> 	Tổ chức kỷ lục thế giới Guinness chứng nhận đây là khẩu súng bắn nước lớn nhất thế giới. Tuy khẩu súng có kích thước khổng lồ, cơ chế hoạt động của nó tương tự như các phiên bản súng nước nhỏ hơn bán trong các cửa hàng đồ chơi. Rober nén không khí, trong trường hợp này là khí nitơ, vào trong một bồn chứa nước, sau đó bóp cò súng để bắn.</p><p class="Normal"> 	Rober cung cấp danh sách các bộ phận và hướng dẫn lắp đặt cho những người muốn chế tạo khẩu súng. Áp lực khí sử dụng cho khẩu súng rất mạnh và nguy hiểm. Do đó, người chế tạo cần tiến hành cẩn thận, đặc biệt là khi làm ở nhà. </p><p class="Normal"> 	Đây không phải là lần đầu tiên Rober chế ra những khẩu súng khổng lồ. Mùa hè năm ngoái, anh từng chế tạo khẩu súng bắn đạn xốp (Nerf gun) lớn nhất thế giới.</p><p class="Normal" align="right"> 	<strong>Lê Hùng</strong></p>', 1, NULL, NULL),
(205, 'Kỹ thuật giảm chấn trên mũi tên thời Đồ đá', 'Đường rãnh trên mũi tên đá thời cổ đại giúp chúng ít bị vỡ khi va chạm, được coi là bước đột phá công nghệ thời Đồ đá.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156856" data-component="true" data-component-type="video" data-component-value="156856" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Thử nghiệm vũ khí cổ đại. Video: <em>AP</em>. </p> </div><p class="Normal"> 	Nghiên cứu mới đây của <span>Metin Eren, giáo sư </span><span>34 tuổi </span><span>chuyên ngành khảo cổ học thực nghiệm</span><span> tại Đại học bang Kent, Mỹ, cho thấy n</span><span>hững đường rãnh trên phần đầu mũi tên của cư dân Bắc Mỹ thời Đồ đá </span><span>là kỹ thuật chế tác vũ khí tiên tiến</span><span>, </span><em>AP</em><span> ngày 13/7 đưa tin.</span></p><p class="Normal"> 	<span>Đầu những năm 1900, các nhà khảo cổ tìm thấy nhiều mũi tên đá </span><span>ở Bắc Mỹ </span><span>với các đường rãnh kỳ lạ được khắc từ nửa thân kéo dài tới mũi. Nhiều giả thuyết được đặt ra để giải thích cho các đường rãnh này, trong đó một số nhà nghiên cứu cho rằng chúng chỉ là họa tiết trang trí hoặc phục vụ nghi thức tôn giáo.</span></p><p class="Normal"> 	<span>Eren tạo ra bản sao của mũi tên đá cổ đại để nghiên cứu trong phòng thí nghiệm. </span><span>Dùng máy nghiền kiểm tra áp lực làm vỡ đầu mũi tên kết hợp </span><span>các mô hình máy tính, Eren </span><span>phát hiện các đường rãnh có tác dụng như bộ phận giảm chấn, giúp mũi tên ít bị gãy khi bắn vào mục tiêu.</span></p><p class="Normal"> 	<span>Kỹ thuật này cho phép đầu mũi tên khó gãy hơn do đường rãnh có thể hấp thu năng lượng từ va chạm. "Đây là phát minh đầu tiên của các cư dân Bắc Mỹ", Eren nói.</span><span> </span><span>"Dù sống ở thời Đồ đá, cách tư duy của họ vẫn thật hiện đại".</span></p><p class="Normal"> 	<span>Kỹ thuật chế tác vũ khí này </span><span>xuất hiện lần đầu tiên hơn 13.000 năm trước và nhanh chóng lan khắp lục địa Bắc Mỹ.</span></p><p class="Normal"> 	"Điều khiến tôi ấn tượng là phải dùng đến công nghệ của thế kỷ 21 chúng ta mới khám phá được điều những người sống cách đây 11-12 nghìn năm đã biết", Eren nói thêm.</p><p class="Normal"> 	Bằng cách cải tiến vũ khí, những cư dân cổ đại này học cách thích ứng với môi trường sống mới. "Họ biết họ đang đi vào một lãnh thổ mới và do đó được trang bị cực tốt về mặt công nghệ", Eren nói.</p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, NULL, NULL),
(206, 'Máy bay không người lái nhanh nhất thế giới', 'RacerX, máy bay không người lái nhanh nhất thế giới do một công ty Mỹ chế tạo, có thể đạt vận tốc 263 km/h.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156993" data-component="true" data-component-type="video" data-component-value="156993" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Máy bay không người lái RacerX. Video: <em>YouTube. </em></p> </div><p class="Normal"> 	RacerX, chiếc máy bay không người lái 4 cánh quạt (quadcopter) do Ryan Gury và nhóm kỹ sư tại công ty Drone Racing League (DRL) ở Mỹ chế tạo, được Tổ chức kỷ lục Guinness công nhận là máy bay điều khiển từ xa có tốc độ nhanh nhất thế giới với vận tốc 263 km/h, <em>Newatlas</em> hôm 14/7 đưa tin.</p><p class="Normal"> 	Nguyên mẫu đầu tiên của RacerX ban đầu mắc lỗi và bốc cháy khi tiến hành thử nghiệm. Sau nhiều lần cải tiến, DRL cho ra sản phẩm cuối cùng chỉ nặng 800 g, chạy bằng hai cục pin lithium-polymer dung lượng 1.300 mA, bốn động cơ điện quay với tốc độ 46.000 vòng/phút.</p><p class="Normal"> 	Vận tốc tối đa của RacerX có thể đạt 289 km/h. Nhưng con số ghi trong sách kỷ lục Guinness là 263 km/h, do đây là vận tốc trung bình của hai lần bay trên một quãng đường dài 100 m. Theo <em>Gizmodo</em>, với tốc độ này RacerX có thể di chuyển nhanh hơn các dòng xe hơi thể thao như Ford Mustangs và Chevrolet Camaros.</p><p class="Normal"> 	"Chúng tôi rất vui khi sử dụng công nghệ độc quyền của DRL để tạo ra các mẫu thiết kế máy bay không người lái đạt đến mức giới hạn về tốc độ", Nicholas Horbaczewski, giám đốc điều hành của DRL, cho biết.</p><p class="Normal" align="right"> 	<strong>Lê Hùng</strong></p>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(207, 'Sâu bướm gai độc có bộ lông vàng mượt ở Brazil', 'Sinh vật lạ được bao phủ trong bộ lông màu ngà khiến cộng đồng mạng Brazil xôn xao.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-153526" data-component="true" data-component-type="video" data-component-value="153526" data-component-typevideo="2"></div></div> 	<p class="Normal" style="text-align:left;"> 		<span>Video sinh vật kỳ lạ với bộ lông vàng mượt bò trên tường nhà ở Brazil xuất hiện trên mạng xã hội nước này hồi cuối tháng 6, gây tò mò với nhiều người, </span><em>Sun </em><span>đưa tin. Trong video, m</span><span>ột người dùng ngón tay chạm vào bộ lông khiến con vật ngoe ngẩy đuôi. </span></p> </div><p class="Normal"> 	<span>Video thu hút nhiều lượt like và chia sẻ trên Facebook. Tài khoản Moon del Valle cho rằng sinh vật này giống một con Pokemon, trong khi tài khoản Dafydd Goose Griffiths bông đùa: "Ai đó làm ơn đưa cho tôi một cuốn bách khoa toàn thư về Pokemon đi nào, tôi phải xem con vật thần bí này là gì". </span></p><p class="Normal"> 	<span>Bộ lông bóng mượt màu vàng của con vật thậm chí còn khiến tài khoản Shaii Wallace liên tưởng đến mái tóc đặc trưng của Tổng thống Mỹ Donald Trump.</span></p><p class="Normal"> 	<span>Theo các chuyên gia, sinh vật này chính là </span><span>loài sâu bướm có tên Megalopyge Opercularis. Bên dưới vẻ ngoài đáng yêu của bộ lông óng mượt là những cái gai có độc. Gai độc của loài sâu bướm này có thể gây ngứa ngáy và cảm giác đau đớn cho nạn nhân nếu chạm phải.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, NULL, NULL),
(208, '3,5 tấn lươn nhớt phủ khắp đường cao tốc Mỹ', 'Hơn ba tấn lươn nhớt đổ từ một xe tải xuống đường cao tốc ở bang Oregon, Mỹ, nhớt do chúng tiết ra phủ đầy những chiếc xe đi qua.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156875" data-component="true" data-component-type="video" data-component-value="156875" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 Công nhân vệ sinh dọn nhớt lươn trên mặt đường. Video: <em>National Geographic.</em></p> </div><p class="Normal"> 	Những con lươn nhớt rơi xuống từ một chiếc xe tải trên đường cao tốc 101 ở Oregon hôm 13/7, gây ra tai nạn liên hoàn cho 5 chiếc xe và phủ đầy nhớt lên những xe khác, theo <em>Live Science</em>. Trên thực tế, sinh vật không phải thuộc loài lươn mà thuộc loài cá mù (hagfish). Loài cá thời tiền sử sống ở đáy biển này sử dụng chất nhờn để xua đuổi động vật săn mồi.</p><p class="Normal"> 	Lươn nhớt được thu thập từ thành phố Newport Bayfront ở vùng ven biển Oregon và chất trong các container xếp trên chiếc xe tải bị lật nhào, theo Cari Boyd, trung úy ở sở cảnh sát Oregon. Điểm đến cuối cùng của số lươn nhớt trên là Hàn Quốc, nơi chúng được xem như một món đặc sản.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943651525175655.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Chất nhầy của lươn nhớt phủ kín một chiếc xe hơi. Ảnh: <em>RT.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Tuy nhiên, khi lái xe tới gần công trường thi công trên đường, tài xế không kịp phanh khiến xe bị lật và toàn bộ 3.400 kg lươn nhớt văng khỏi xe. Sau tai nạn, các công nhân buộc phải phong tỏa đoạn đường để làm sạch nhớt dính.</p><p class="Normal"> 	Lươn nhớt là loài cá không răng tồn tại từ thời khủng long và trải qua rất ít thay đổi trong 300 triệu năm qua, theo William O''Connor, nhà sinh vật học làm việc cho công ty tư vấn môi trường Ecofact Environmental Consultants Ltd. ở Ireland, một chuyên gia về loài lươn. Sinh vật hình dáng kỳ lạ thuộc họ Agnatha, có nhiều điểm giống cá mút đá.</p><p class="Normal"> 	Lươn nhớt đóng vai trò quan trọng trong hệ sinh thái, giúp tái tạo chất dinh dưỡng và cung cấp thức ăn cho nhiều động vật. Chúng không phải loài nguy cấp nhưng số lượng đang giảm dần do tình trạng khai thác quá mức làm thức ăn và lấy da, Douglas Fudge, nhà sinh vật học và chuyên gia lươn nhớt ở Đại học Chapman, California, Mỹ, cho biết.</p><p class="Normal"> 	Khi gặp nguy hiểm, lươn nhớt phun nhớt nghẽn mang của cá ăn thịt. "Chúng được tìm thấy ở vùng biển sâu có phần đất nền mềm và thường vùi mình dưới đáy biển để trốn các loài săn mồi, nhờ đó chúng không cần phải sản sinh nhớt. Trường hợp xảy ra trên đường cao tốc là vấn đề khác", O''Connor nói.</p><p class="Normal"> 	Việc bị văng ra đột ngột khỏi xe tải chắc chắn đã kích hoạt cơ chế phun nhớt. Chúng thường tiết ra nhớt để tự vệ trước loài săn mồi, nhưng chúng cũng làm vậy khi căng thẳng. Và đổ chồng chất trên đường cao tốc có thể xem như một mối căng thẳng với lươn nhớt, Fudge cho biết.</p><p class="Normal"> 	Chất nhớt sinh ra từ các tuyến chạy dọc chiều dài cơ thể lươn nhớt. Chất nhớt này là hỗn hợp của dịch nhầy và sợi protein đóng vai trò như những sợi chỉ nhỏ. Các sợi chỉ duỗi ra, trong khi dịch nhầy hấp thụ nước, tạo ra một mạng lưới 3D lớn hơn gấp 10.000 lần thể tích ban đầu, theo Ryan Kincer, kỹ sư vật liệu thuộc Hải quân Mỹ.</p><p class="Normal"> 	Tại Oregon, các nhà chức trách dọn chất nhớt trên đường bằng nước và vòi phun áp suất cao. Chất nhớt không tan rã trong nước nhưng nhờ cấu tạo dạng sợi, áp suất từ vòi phun có thể đẩy nhớt chảy xuống một rãnh cống.</p><p class="Normal" align="right"> 	<strong>Phương Hoa </strong></p>', 1, '15001943652012280887.jpg', NULL),
(209, 'Điều hòa ôtô khác với điều hòa trong phòng như thế nào?', 'Xin hỏi điều hòa sử dụng cho ôtô có cục nóng như điều hóa ở trong phòng không? (Đức Cảnh)', '<table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943662026719159.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hình minh họa: <em>Wordpress.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Mời độc giả đặt câu hỏi <a href="http://vnexpress.net/contactus/?p=khoahoc">tại đây</a>.</p>', 1, '1500194366766735298.jpg', NULL),
(210, 'NASA thừa nhận chưa có tiền đưa người lên sao Hỏa', 'NASA cho biết không giấc mơ thám hiểm hành tinh Đỏ đang tạm gián đoạn do không đủ ngân sách.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943681724387215.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Bill Gerstenmaier phát biểu tại hội nghị thượng đỉnh Con người lên sao Hỏa năm 2015. Ảnh: <em>NASA</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	<span>Cơ quan Hàng không và Vũ trụ Mỹ (NASA) </span><span>5 năm qua </span><span>không ngừng quảng bá mục tiêu đưa con người lên sao Hỏa trong thập niên 2030 nhờ tên lửa SLS và phi thuyền Orion.</span></p><p class="Normal"> 	<span>Tuy nhiên, </span><span>William H. Gerstenmaier, </span><span>trưởng bộ phận đưa con người thám hiểm vũ trụ của NASA </span><span>ngày 12/7 </span><span>tại diễn đàn Propulsion & Energy ở bang Georgia, Mỹ cho biết cơ quan này không có nguồn tiền cần thiết để thực hiện giấc mơ này, theo <em>A</em></span><span style="color:rgb(34,34,34);"><em>rstechnica</em>.</span></p><p class="Normal"> 	"Tôi không thể ấn định ngày con người lên được sao Hỏa với mức ngân sách chỉ tăng khoảng 2%, chúng tôi chưa có các hệ thống để sử dụng trên bề mặt sao Hỏa", Gerstenmaier cho biết. "Việc hạ cánh xuống sao Hỏa cũng là một thách thức lớn".</p><p class="Normal"> 	<span>Các quan chức NASA hiếm khi đề cập vấn đề kinh phí trong chiến dịch quảng bá thám hiểm sao Hỏa. Cơ quan này không</span><span> thể bắt tay thiết kế các phương tiện phục vụ việc hạ hay cất cánh trên sao Hỏa do v</span><span>iệc chế tạo t</span><span>ên lửa SLS và phi thuyền Orion quá tốn kém.</span></p><p class="Normal"> 	<span>Trong khi đó, ý tưởng dựa vào các công ty thương mại để thực hiện các chuyến thám hiểm sâu trong vũ trụ không được quốc hội Mỹ đồng tình dù hy vọng vẫn le lói. "Sự thật là các công ty Mỹ đang nắm công nghệ vũ trụ tiên tiến", Phó tổng thống Mike Pence phát biểu tại Trung tâm Vũ trụ Kennedy hôm 6/7.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, '15001943682077274222.jpg', NULL),
(211, 'Gấu nước - Loài vật vẫn tồn tại dù Trái Đất diệt vong', 'Với khả năng sinh tồn vô địch của mình, gấu nước vẫn có thể sống sót ngay cả khi Trái Đất bị phá hủy hay Mặt Trời chết đi.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943691632437679.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Gấu nước là sinh vật sống dai nhất hành tinh. Ảnh: <em>YouTube.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Theo nghiên cứu của Đại học Harvard và Oxford về những nguy cơ đe dọa sự sống trên Trái Đất qua hàng tỷ năm, sự sống trên hành tinh của chúng ta sẽ không bị xóa sổ hoàn toàn ngay cả khi Trái Đất bị tấn công bởi tiểu hành tinh, vụ nổ siêu tân tinh hay tia gamma. Loài gấu nước vẫn có thể tồn tại đến tận lúc Mặt Trời "chết", tức là rất lâu sau khi loài người tuyệt chủng.</p><p class="Normal"> 	Gấu nước, sinh vật <span>8 chân</span><span> có chiều dài chưa đến một mm, thường sống dưới nước và gần như không thể chết. Chúng có thể tồn tại đến 30 năm mà không cần ăn, dù bị đóng băng, đun sôi, nghiền nát dưới sức ép lớn, thậm chí tiếp xúc với môi trường chân không và tia bức xạ của vũ trụ mà không hề hấn gì.</span></p><p class="Normal"> 	"Nhiều nghiên cứu trước đây tập trung vào những viễn cảnh ''ngày tận thế'' của Trái Đất, những sự kiện vũ trụ như vụ nổ siêu tân tinh có thể hủy diệt nhân loại. Trong khi đó, chúng tôi quan tâm đến sinh vật sống dai nhất – gấu nước", n<span>hà nghiên cứu David Sloan cho biết</span><span>.</span></p><p class="Normal"> 	"Điều ngạc nhiên là, chúng tôi phát hiện dù vụ nổ siêu tân tinh hay tiểu hành tinh lớn đâm xuống sẽ là thảm họa diệt vong với loài người, gấu nước vẫn không hề bị ảnh hưởng", ông nói thêm. "Dường như một khi sự sống đã bắt đầu và phát triển thì sẽ rất khó bị loại bỏ hoàn toàn".</p><p class="Normal"> 	Nhiều nghiên cứu chỉ ra sao Hỏa từng có điều kiện sống rất lý tưởng với bầu khí quyển dày, các hồ và mạch nước. Nếu <span>sự sống từng phát triển </span><span>trên các hành tinh như sao Hỏa với các sinh vật giống loài gấu nước thì chúng sẽ vẫn tồn tại bất chấp những điều kiện khắc nghiệt hiện tại.</span></p><p class="Normal"> 	"Gấu nước gần như là loài vật ''bất diệt'' trên Trái Đất, nhưng có thể còn những sinh vật sống dai khác ở nơi nào đó trong vũ trụ. Nếu gấu nước là loài vật sống dai nhất Trái Đất, ai biết được ngoài vũ trụ sẽ còn những thứ gì khác?", n<span>hà nghiên cứu Rafael Alves Batista tại Đại học Oxford đặt câu hỏi.</span></p><p align="right" class="Normal"> 	<strong>Thu Thảo</strong></p>', 1, '1500194369883916246.jpg', NULL),
(212, 'Bề mặt lởm chởm của sao Diêm vương', 'NASA công bố video mô tả chi tiết địa hình sao Diêm vương và vệ tinh Charon dựa trên dữ liệu do phi thuyền thu thập.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156837" data-component="true" data-component-type="video" data-component-value="156837" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 Địa hình sao Diêm vương và vệ tinh Charon. Video: <em>NASA</em>.</p> </div><p class="Normal"> 	<span>Các nhà khoa học của Cơ quan Hàng không và Vũ trụ Mỹ (NASA) công bố video mô tả bề mặt của sao Diêm vương và vệ tinh Charon được dựng từ</span> dữ liệu của phi thuyền New Horizons và các mô hình 3D, <em>Mashable </em>ngày 14/7 đưa tin.</p><p class="Normal"> 	Video mô phỏng chuyến bay trên sao Diêm vương cho thấy các cao nguyên, đồng bằng băng Sputnik Planitia, núi Tartarus Dorsa. K<span>he núi Serenity Chasma cùng hố Dorothy Gale trên Charon, mặt trăng lớn nhất của sao Diêm vương, cũng xuất hiện.</span></p><p class="Normal"> 	Hai video <span>được công bố nhằm kỷ niệm sự kiện p</span><span>hi thuyền New Horizons thực hiện chuyến bay vòng quanh sao Diêm vương ngày 14/7/2015, </span><span>mang lại cái nhìn mới và sắc nét hơn về địa hình của hành tinh này và vệ tinh Charon</span><span>.</span></p><p class="Normal"> 	<span style="color:rgb(34,34,34);">Năm 2006, Hiệp hội Thiên văn Quốc tế xếp sao Diêm vương vào loại hành tinh lùn. </span>Kirby Runyon, nhà khoa học hành tinh tại Đại học Johns Hopkins, Mỹ hồi tháng 3 <span style="color:rgb(34,34,34);">đề xuất định nghĩa mới, theo đó đưa sao Diêm Vương trở lại danh sách các hành tinh trong hệ Mặt Trời.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, NULL, NULL),
(213, 'Giải cứu sư tử rơi xuống giếng sâu 25 mét', 'Con sư tử cái được giải cứu kịp thời sau nhiều tiếng vùng vẫy dưới miệng giếng sâu 25 mét ở Ấn Độ.', '<div style="text-align:center"> 	 	<div style="text-align: center;"> 		<div> 			<div id="video-156760" data-component="true" data-component-type="video" data-component-value="156760" data-component-typevideo="2"></div></div> 	</div> 	<p class="Image"> 		 Dân làng Ấn Độ giải cứu sư tử rơi xuống giếng sâu 25 mét. Video: <em>National Geographic.</em></p> </div><p class="Normal"> 	Con sư tử cái hai tuổi được cứu sống sau khi rơi xuống một giếng nước ở làng Somnath, bang Gujarat, Ấn Độ, hôm 8/7, theo <em>National Geographic</em>.</p><p class="Normal"> 	Trước đó, dân làng dùng còi báo động để xua đuổi con sư tử đang lang thang tìm mồi gần làng. Anil Kumar, người ghi hình cuộc giải cứu, cho biết những con sư tử thường xuyên lẻn vào làng ít nhất 2-3 lần một tháng và người dân phải xua đuổi để chúng không tấn công gia súc.</p><p class="Normal"> 	Con sư tử con trong lúc hốt hoảng tháo chạy đã bị rơi xuống giếng nước ở rìa làng. Một nông dân sống gần đó phát hiện sư tử con rơi xuống giếng và báo tin cho những người khác trong làng. "Đó là một khoảnh khắc rất đáng buồn đối với tôi khi chứng kiến sư tử con <span>chật vật tìm cách sống sót</span> như vậy", Kumar chia sẻ.</p><p class="Normal"> 	Con sư tử vùng vẫy dưới <span>giếng sâu gần 25 mét </span>suốt nhiều giờ. Do giếng quá sâu, người dân địa phương không thể cứu con vật và phải gọi cơ quan bảo vệ động vật hoang dã tới trợ giúp.</p><p class="Normal"> 	Để cứu thoát sư tử, một nhân viên cứu hộ trèo xuống đáy giếng trong một chiếc lồng kim loại. Người này buộc dây quanh vai con sư tử cái, sau đó những người bên trên hợp sức kéo nó lên. Sau cuộc giải cứu gần hai tiếng, con vật được nhốt vào chuồng riêng.</p><p class="Normal"> 	Con sư tử được đưa tới trung tâm chăm sóc động vật Sasan Gir để xử lý những vết thương nhẹ trước khi thả về rừng.</p><p class="Normal"> 	<span>Khoảng 500 con sư tử châu Á nằm trong nhóm động vật nguy cấp đang sinh sống ở Ấn Độ, tất cả đều tập trung ở bang Gujarat phía tây. Những trường hợp sư tử chết do rơi xuống giếng nước khá phổ biến, khiến nhà chức trách địa phương phải tìm cách làm rào chắn bao quanh các giếng.</span></p><p align="right" class="Normal"> 	<strong>Phương Hoa </strong></p>', 1, NULL, NULL),
(214, 'Lưu phim đen trắng vào ADN của vi khuẩn', 'Các nhà khoa học Mỹ lưu thành công một bộ phim đen trắng vào ADN của vi khuẩn.', '<div style="text-align: center;"> 	<div id="video-156732" data-component="true" data-component-type="video" data-component-value="156732" data-component-typevideo="2"></div></div><p class="Normal"> 	<span>Các nhà khoa học tại Đại học Y Harvard, Mỹ trong nghiên cứu đăng trên tạp chí Nature ngày 12/7 giới thiệu công nghệ lưu dữ liệu trên ADN sau khi lưu thành công bộ phim đen trắng vào ADN của vi khuẩn, theo <em>New York Times</em>.</span></p><p class="Normal"> 	<span>Bộ phim đen trắng năm 1878 về một con ngựa phi nước đại trở thành bộ phim đầu tiên được mã hóa vào ADN của một tế bào sống. Thông tin được nhân lên gấp nhiều lần khi tế bào ban đầu phân chia, lớn lên và c</span><span>ó thể được khôi phục vào bất kỳ thời điểm nào.</span></p><p class="Normal"> 	Trong nghiên cứu, hai nhà di truyền học tiến sĩ <span>George Church và</span><span> Seth Shipman cùng đồng nghiệp ấn định mỗi điểm trong bộ phim đen trắng là một mã ADN. Họ sau đó thu được một chuỗi AND tương ứng với toàn bộ bộ phim, được lắp ghép từ bốn loại nucleotide có ký hiệu X, G, A và T cấu thành ADN.</span></p><p class="Normal"> 	<span>Chuỗi ADN sau đó được đưa vào hệ gene của vi khuẩn E. coli, loại vi khuẩn thông thường trong đường ruột, </span><span>nhờ công nghệ can thiệp gene crispr. </span><span>Vi khuẩn sinh trưởng bình thường và lớn lên. Bộ phim được lưu trữ hoàn hảo trong hệ gene của các thế hệ vi khuẩn mới.</span></p><p class="Normal"> 	<span>Các nhà khoa học hiện đã mã hóa toàn bộ thơ tình của thi hào người Anh William Shakespeare vào ADN. Tiến sĩ Church gần đây cũng mã hóa cuốn sách của ông vào ADN của vi khuẩn và tạo ra 90 tỷ bản sao. "Đó là một kỷ lục xuất bản", Church nói trong một cuộc phỏng vấn.</span></p><p class="Normal"> 	<span>Các nhà khoa học đang có ý tưởng biến vi khuẩn thành thiết bị ghi dữ liệu để đưa vào máu lên não trước khi được lấy ra ngoài. ADN của vi khuẩn sau đó được nghiên cứu để các nhà khoa học thu thập thông tin chúng ghi nhận được từ tế bào thần kinh người.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194372106287718.png" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hình gốc từ bộ phim đen trắng và hình khôi phục từ ADN của vi khuẩn. Ảnh: <em>Seth Shipman</em>.</p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Nửa thế kỷ trước, nhà vật lý học người Mỹ Richard Feynman từng nêu ý tưởng dùng ADN để lưu thông tin. Ý tưởng của ông ra đời trước cuộc cách mạng sinh học phân tử và nhiều thập kỷ trước khi các nhà khoa học có thể lắp ghép một chuỗi ADN.</p><p class="Normal"> 	<span>Tiến sĩ Leonard Adleman, nhà toán học tại Đại học Southern California, Mỹ năm 1994 cho biết từng lưu dữ liệu trong ADN và sử dụng công cụ này như máy tính để giải một vấn đề toán học. Theo ông, lượng thông tin ADN có thể chứa gấp một triệu triệu lần so với một đĩa ghi.</span></p><p class="Normal"> 	Lưu dữ liệu ngày càng trở thành vấn đề lớn khi thông <span>tin được tạo ra với lượng khổng lồ nhưng </span><span>công nghệ lưu trữ đang trở nên lỗi thời, trái ngược với ADN. "Các tổ chức sống lưu thông tin trong ADN trong hàng tỷ năm và những thông tin này không bị hỏng", Adleman nói, cho biết vi khuẩn hiện đại có thể đọc được gene khôi phục từ hóa thạch côn trùng có niên đại hàng triệu năm.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, '1500194372453615842.png', NULL),
(215, 'Những cỗ máy siêu tốc hái hàng tấn cà chua trong vài giờ', 'Hàng tấn cà chua được thu hái, phân loại, sơ chế và đóng hộp nhanh chóng nhờ những cỗ máy nông nghiệp khổng lồ.  ', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156656" data-component="true" data-component-type="video" data-component-value="156656" data-component-typevideo="2"></div><span style="color:rgb(0,0,0);"> </span></div> </div><p class="Normal" style="text-align:right;"> 	(Nguồn: <em>Postfeed</em>)</p><div class="box_quangcao list_news mg_bottom_20"> 	<div class="block_image_news width_common"> 		<div class="title_news"> 			<p class="Normal"> 				<span>Ý kiến phản h​ồi gửi về email: </span><a href="mailto:thongtin@nongnghiepsach.tv" style=''font-family: "Times New Roman"; font-size: 15.7333px;''>thongtin@nongnghiepsach.tv</a><span>.</span><br><span>Hotline: 043.629​.0688.</span></p> 		</div> 	</div> </div>', 1, NULL, NULL),
(216, '10 nhiệm vụ trọng yếu TP HCM muốn phối hợp Bộ Khoa học', 'Xây dựng bản đồ công nghệ, hỗ trợ công nghiệp phụ trợ, chống ngập... được ông Nguyễn Thiện Nhân đề xuất hợp tác với Bộ Khoa học.', '<p> 	Trong buổi làm việc với Bộ Khoa học và Công nghệ sáng 15/7 tại Hà Nội, Bí thư thành ủy TP HCM Nguyễn Thiện Nhân đã đề xuất 10 nhiệm vụ quan trọng mà bộ và TP HCM cần phối hợp, hỗ trợ thực hiện.</p><p> 	Theo đó, nhiệm vụ đầu tiên là TP HCM có trách nhiệm góp ý để Bộ hoàn thiện các thể chế chính sách.</p><p> 	Thứ hai, để đảm bảo những vấn đề mới, văn bản đi vào thực tiễn, TP HCM cam kết làm thí điểm có chọn lọc. Bộ sẽ giúp thành phố thiết kế thí điểm ban đầu đủ độ an toàn và hiệu quả, hỗ trợ giải quyết một số bài toán lớn của thành phố cũng như của cả nước.</p><p> 	Ông Nhân dẫn ra việc thành phố phải hình thành trong thực tiễn hệ sinh thái khởi nghiệp sáng tạo và đặt ra một đề bài cụ thể về vốn cho khởi nghiệp để đề nghị Bộ Khoa học và Công nghệ cùng thành phố sớm bàn với nhau xây dựng một quỹ hỗ trợ cho việc này. Cùng với đó, TP HCM sẽ hành động để trung tâm giao dịch khoa học công nghệ phía Nam sớm đi vào hoạt động.</p><p> 	Bộ Khoa học và Công nghệ cần giúp đỡ TP HCM có một chương trình đào tạo nhân lực cho khởi nghiệp sáng tạo, đặc biệt đẩy mạnh ở các trường đại học, THPT.</p><p> 	Theo người đứng đầu thành ủy TP HCM, hai bên cần kết hợp vận động, kêu gọi một số nước lớn như Nhật Bản, Hàn Quốc, Mỹ, Đức thành lập trung tâm hỗ trợ khởi nghiệp ở thành phố.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943741158698233.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Ông Nguyễn Thiện Nhân chỉ ra 10 nhiệm vụ Bộ Khoa học và Công nghệ và TP HCM cần phối hợp thực hiện. Ảnh: <em>Dương Tâm</em></p> 			</td> 		</tr></tbody></table><p> 	Ông Nhân cho rằng Bộ Khoa học và Công nghệ phải hỗ trợ lĩnh vực vi mạch cho TP HCM và cho vấn đề quốc phòng. Bên cạnh đó, lĩnh vực công nghiệp phụ trợ cũng cần được quan tâm, đặc biệt là phụ trợ cho Samsung TP HCM. "Chỉ cần nội địa hóa được một nửa số thiết bị mà Samsung yêu cầu, số tiền thu lại được sẽ rất lớn, việc làm của các doanh nghiệp Việt Nam được đảm bảo và đầu ra cũng rất ổn định", ông Nhân phân tích. </p><p> 	Một nhiệm vụ quan trọng khác là bộ giúp hoàn thiện, chuyển giao cho thành phố các bản đồ công nghệ ở 4 lĩnh vực ưu tiên gồm cơ khí chế tạo máy công cụ công nghệ cao; vật liệu nano; sản xuất nhựa kỹ thuật cho công nghiệp điện tử, chế tạo thiết bị, y tế; điện tử gia dụng và bản đồ công nghệ về tế bào gốc để hỗ trợ các doanh nghiệp mới thành lập. "Bộ phải cập nhật bản đồ công nghệ này để thành phố chỉ việc sử dụng", ông Nhân nhấn mạnh.</p><p> 	TP HCM mong Bộ Khoa học và Công nghệ có buổi làm việc chuyên đề với thành phố để nâng cấp trung tâm công nghệ sinh học thành phố thành trung tâm công nghệ sinh học vùng; giúp thành phố đánh giá các chỉ số đổi mới công nghệ một cách chính xác; triển khai khu công nghệ cao giai đoạn 2.</p><p> 	Cuối cùng, về vấn đề chống ngập, ông Nhân đề nghị nếu có thể, Bộ Khoa học và Công nghệ cho phía TP HCM xem văn bản phối hợp với Đức và coi thành phố là nơi triển khai hợp tác với Đức về chống ngập. "Chúng tôi sẽ bàn thêm với Hà Lan, ba bên cùng phối hợp rà soát toàn bộ chiến lược của thành phố trong nhiều năm qua và có những định hướng mới", ông Nhân gợi ý và thông tin thêm đầu tháng 9, phía Ý sẽ tổ chức hội nghị với các nhà khoa học Việt Nam ở TP HCM về quản lý nước trong thành phố.</p><p> 	Chỉ ra những nhiệm vụ quan trọng mà Bộ Khoa học và Công nghệ và TP HCM cần phối hợp thực hiện, ông Nguyễn Thiện Nhân cho rằng từ nay đến năm 2020, bộ và thành phố không nên cam kết phối hợp quá 10 việc mà chú trọng "ít nhưng chất lượng".</p><p> 	"6 tháng một lần, tôi sẽ ngồi cùng Bộ trưởng đánh giá những việc đã thực hiện", ông Nhân đề nghị.</p><p> 	Bộ trưởng Khoa học và Công nghệ Chu Ngọc Anh hoàn toàn đồng ý với ý kiến của ông Nguyễn Thiện Nhân. Ông Ngọc Anh khẳng định sẽ hỗ trợ TP HCM trong từng hoạt động, thực hiện một cách chắc chắn nhằm đến đích thành công.</p>', 1, '1500194374609521052.jpg', NULL),
(217, 'Tại sao kẹo cao su dễ gỡ nếu áp đá lạnh lên trên?', 'Nếu để đá lạnh lên kẹo cao su, kẹo sẽ cứng lại và có thể bóc ra khỏi quần áo. Tại sao chúng ta làm được như vậy? (Thu Trang)', '<table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943761138756484.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Hình minh họa: <em>Juno.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Mời độc giả đặt câu hỏi <a href="http://vnexpress.net/contactus/?p=khoahoc">tại đây</a>.</p>', 1, '15001943761879553792.jpg', NULL),
(218, 'Đường hầm sang thế giới bên kia ẩn dưới kim tự tháp Mexico', 'Các nhà khảo cổ học phát hiện một đường hầm bí mật ẩn bên dưới kim tự tháp Mặt Trăng ở Mexico.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194377131273701.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Mô phỏng đường hầm bên dưới kim tự tháp Mặt Trăng. Ảnh: <em>Express.co.uk.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Các nhà khảo cổ học tại Viện Nhân chủng học Quốc gia Mexico (NIAH) sử dụng công nghệ hình ảnh chụp cắt lớp điện trở suất (ERT) đã phát hiện một đường hầm ở độ sâu khoảng 10 m bên dưới kim tự tháp Mặt Trăng tại thành phố cổ Teotihuacan, Mexico, <em>Science Alert</em> hôm 8/7 đưa tin.</p><p class="Normal"> 	Nhóm nghiên cứu cho rằng, đường hầm bên dưới kim tự tháp Mặt Trăng đại diện cho con đường dẫn tới thế giới bên kia trong văn hoá và nghi lễ của người Teotihuacan cổ đại. Họ vẽ bản đồ cấu trúc ngầm và khoảng trống bên dưới kim tự tháp dựa vào mức độ ngăn cản dòng điện của các lớp đất đá.</p><p class="Normal"> 	"Khám phá này chỉ ra rằng, người dân sống ở Teotihuaca luôn làm theo khuôn mẫu giống nhau trong các ngôi đền quy mô lớn. Chức năng của chúng là mô phỏng và bắt chước thế giới bên kia", Veronica Ortega, thành viên của nhóm khảo cổ, cho biết.</p><p class="Normal"> 	Các khoa học tìm thấy những dấu vết mai táng và lễ vật thờ cúng xung quanh kim tự tháp Mặt Trăng nhiều hơn so với các kim tự tháp khác trong cùng khu vực. Họ chắc chắn sẽ có nhiều điều để khám phá trong đường hầm mới phát hiện này, mang đến hiểu biết tốt hơn về ý nghĩa của thành phố Teotihuacan.</p><p class="Normal"> 	Nền văn minh Teotihuacan trước khi bị người Tây Ban Nha chinh phục tồn tại giữa thế kỷ 1 và thế kỷ 8, cách phía bắc Mexico City khoảng 40 km. Lý do người dân Teotihuacan biến mất một cách đột ngột cho đến nay giới nghiên cứu vẫn chưa thể giải đáp.</p><p class="Normal"> 	Kim tự tháp Mặt Trăng với chiều cao 43 m không phải là công trình nổi tiếng duy nhất ở Teotihuacan. Bên cạnh nó là kim tự tháp Mặt Trời và đền thờ thần rắn. Các nhà khảo cổ cũng tìm thấy đường hầm ẩn bên dưới những công trình này.</p><p align="right" class="Normal"> 	<strong>Lê Hùng</strong></p>', 1, '1500194377494782833.jpg', NULL),
(219, 'Kangaroo con giơ tay cầu cứu từ trong xác mẹ', 'Kangaroo con vài ngày tuổi được cứu từ xác con mẹ, nhưng không thể sống sót vì còn quá non yếu.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156744" data-component="true" data-component-type="video" data-component-value="156744" data-component-typevideo="2"></div></div> 	<p class="Image"> 		Kangaroo con mắc kẹt trong túi mẹ. Video: <em>Caters News. </em></p> </div><p class="Normal"> 	Các nhân viên cứu hộ của tổ chức Macedon Ranges Wildlife Network tìm thấy xác kangaroo mẹ nằm bất động cùng con non còn sống trong túi ở bang Victoria, Australia, <em>Long Room</em> hôm qua đưa tin. Con kangaroo sơ sinh nhô cánh tay nhỏ xíu ra khỏi miệng túi giống như thể đang cầu cứu sau khi chuột mẹ chết do bị xe đâm.</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194378652454404.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Kangaroo con không thể sống sót do còn quá nhỏ. Ảnh: <em>Caters News.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Lúc đầu, chuột túi con tên Dawson hồi phục tốt nhờ sự chăm sóc kỹ lưỡng của Christine Gilmore, chủ khu bảo tồn Coopers Animal Rescue ở Dales Creek, Victoria. Tuy nhiên, nó qua đời không lâu sau đó trong sự bất lực của những người chăm sóc.</p><p class="Normal"> 	"Dawson còn quá nhỏ. Nó không khác gì một đứa trẻ sinh non ở tuần 24. Mắt của nó vẫn khép chặt và chưa mọc lông. Dawson đang phục hồi tốt và có sẵn bình sữa để bú ban đêm nhưng khi chúng tôi dậy vào buổi sáng, nó đã ra đi", Christine chia sẻ.</p><p align="right" class="Normal"> 	<strong>Phương Hoa </strong></p>', 1, '1500194378536875007.jpg', NULL),
(220, 'Hổ Bengal trắng sinh 4 ở Trung Quốc', '4 con non của loài hổ Bengal trắng chào đời tại một sở thú ở Trung Quốc.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156766" data-component="true" data-component-type="video" data-component-value="156766" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 4 hổ con ra đời ở sở thú tại Trung Quốc. Video: <em>CCTV</em>.</p> </div><p class="Normal"> 	Một con hổ Bengal trắng gần đây sinh 4 con non tại sở thú Thế giới Động vật Hoang dã ở Tế Nam, tỉnh Sơn Đông, Trung Quốc, <em>CCTV</em> ngày 14/7 đưa tin. <span>Hổ mẹ Niu Niu vượt cạn ngày 23/6, đang nuôi ba con non khỏe mạnh. Hổ con thứ tư tên Qun Qun yếu hơn được nhân viên sở thú chăm sóc.</span></p><p class="Normal"> 	<span>"Chúng tôi chú trọng đến thức ăn và sự bài tiết hàng ngày của con vật ngay từ giai đoạn sớm </span><span>vì hổ được con người nuôi sẽ chậm lớn hơn những con được mẹ nuôi"</span><span>, Zhang Qingmei, nhân viên sở thú nói. </span><span>Qun Qun đang phát triển khỏe mạnh.</span></p><p class="Normal"> 	Hổ trắng là phân loài của hổ Bengal, loài nằm trong diện bị đe dọa. Hổ trắng trong tự nhiên đã bị tuyệt diệt,  <span>khoảng 200 con </span><span>còn tồn tại đều được con người chăm sóc.</span></p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong></p>', 1, NULL, NULL),
(221, 'Phát hiện ngôi sao nhỏ nhất trong vũ trụ', 'Các nhà khoa học Anh phát hiện ngôi sao nhỏ nhất trong vũ trụ, có độ sáng yếu hơn Mặt Trời khoảng 2.000 - 3.000 lần.', '<table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/15001943801142215799.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					So sánh kích thước của EBLM J0555–57Ab so với sao Thổ và ngôi sao Trappist-1. Ảnh: <em>Alexander Boetticher.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Các nhà khoa học tại Đại học Cambridge, Anh, phát hiện ngôi sao EBLM J0555–57Ab có kích thước nhỏ nhất từng được biết đến trong vũ trụ. Nó chỉ lớn hơn một chút so với sao Thổ, cách Trái Đất 600 năm ánh sáng, theo <em>Science Alert</em>. Kết quả nghiên cứu được công bố trên tạp chí Astronomy & Astrophysics vào tháng 6/2017.</p><p class="Normal"> 	EBLM J0555–57Ab là một phần của hệ thống sao nhị phân, khi nó quay quanh một ngôi sao lớn hơn. Dù khá nhỏ so với các ngôi sao khác, EBLM J0555–57Ab vẫn có đủ khối lượng để cho phép phản ứng tổng hợp hạt nhân xảy ra bên trong lõi, biến đổi hydro thành heli.</p><p class="Normal"> 	"Phát hiện của chúng tôi cho thấy những ngôi sao có thể nhỏ đến mức nào. Nếu ngôi sao EBLM J0555–57Ab hình thành với khối lượng chỉ nhỏ hơn một chút thì phản ứng nhiệt hạch của hydro bên trong lõi không thể duy trì, và ngôi sao này sẽ biến thành một sao lùn nâu", Alexander Boetticher, thành viên của nhóm nghiên cứu, cho biết.</p><p class="Normal"> 	EBLM J0555–57Ab có độ sáng mờ hơn Mặt Trời khoảng 2.000 - 3.000 lần. Các nhà khoa học phát hiện ngôi sao này khi nó di chuyển qua phía trước ngôi sao lớn hơn mà nó quay quanh gọi là EBLM J0555-57A. Theo ước tính, khối lượng của EBLM J0555-57Ab tương đương với ngôi sao Trappist-1 được phát hiện vào đầu năm nay nhưng với bán kính nhỏ hơn 30%.</p><p class="Normal"> 	Những ngôi sao nhỏ, mờ và mát mẻ như EBLM J0555–57Ab được coi là ứng cử viên tiềm năng chứa hành tinh có sự sống, do làm tăng khả năng lưu trữ nước lỏng trên bề mặt. Các ngôi sao nhỏ hơn 20% so với Mặt Trời là những ngôi sao phổ biến nhất trong vũ trụ, nhưng còn nhiều điều chúng ta chưa biết về chúng.</p><p align="right" class="Normal"> 	<strong>Lê Hùng</strong></p>', 1, '15001943801897425034.jpg', NULL),
(222, 'Xác ướp nguyên vẹn sau 1.000 năm của đại sư Phật giáo', 'Xác ướp của vị đại sư Phật giáo cách đây 1.000 năm vẫn giữ được bộ xương và sọ não hoàn chỉnh theo kết quả chụp cắt lớp vi tính.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156688" data-component="true" data-component-type="video" data-component-value="156688" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 Các bác sĩ chụp cắt lớp xác ướp nhà sư. Video: <em>Pear Video.</em></p> </div><p class="Normal"> 	Hài cốt mạ vàng của đại sư Từ Hiền được kiểm tra y khoa tại chùa Định Huệ ở thành phố Vũ An thuộc tỉnh Hà Bắc phía bắc Trung Quốc, theo <em>Pear Video</em>. Buổi chụp cắt lớp vi tính diễn ra hôm 8/7 trước sự chứng kiến của các nhà sư, phật tử và phương tiện truyền thông.</p><p class="Normal"> 	Mọi người có mặt đều bất ngờ khi các bác sĩ thông báo thi hài đại sư Từ Hiền vẫn còn đầy đủ xương cũng như sọ não. "Chúng tôi có thể trông thấy xương cốt đại sư vẫn còn chắc khỏe như xương người thường. Xương hàm trên, hàm dưới, xương sườn, cột sống và tất cả khớp đều hoàn chỉnh. Điều này thật khó tin", bác sĩ Wu Yongqing chia sẻ.</p><table class="tplCaption" cellspacing="0" cellpadding="3" border="0" align="center"><tbody><tr><td> 				<div style="text-align: center"><img src="/pictures/news/1500194381609918167.jpg" /></div></td> 		</tr><tr><td> 				<p class="Image"> 					Xác ướp đại sư Từ Hiền. Ảnh: <em>Chùa Định Huệ.</em></p> 			</td> 		</tr></tbody></table><p class="Normal"> 	Đại sư Từ Hiền là một cao tăng được kính trọng. Theo ghi chép lịch sử, ông sinh ra và lớn lên tại Ấn Độ. Đại sư Từ Hiền du hành từ Ấn Độ cổ đại đến nước Khiết Đan (tồn tại từ năm 916 đến 1125) ở vùng đông bắc Trung Quốc gần bán đảo Triều Tiên ngày nay. Ông có công dịch 10 bộ Kinh Phạn quan trọng sang tiếng Trung. Sau đó, ông được vua Khiết Đan phong là quốc sư. Một số bản dịch của ông được khắc vào các phiến đá đến nay vẫn còn xem được.</p><p class="Normal"> 	Sau khi đại sư Từ Hiền viên tịch, các đệ tử bảo quản thi hài của ông nhưng trải qua nhiều năm, xác ướp bị thất lạc. Hài cốt của đại sư được phát hiện trong một hang động vào thập niên 1970.</p><p class="Normal"> 	Theo nhà sư trụ trì Du ở chùa Định Huệ, các nhà sư Trung Quốc cổ đại bảo tồn thi thể những bậc cao tăng bằng phương pháp tự nhiên. Thông thường, một đại sư có thể dự đoán thời điểm viên tịch. Đại sư sẽ thông báo cho các đệ tử nếu muốn hỏa táng hoặc bảo quản thi thể.</p><p class="Normal"> 	Để bảo quản thi thể đại sư, các đệ tử đặt hài cốt vào bên trong một vại gốm lớn chứa đầy nguyên liệu chống ăn mòn có nguồn gốc tự nhiên. Sau ba năm, các đệ tử sẽ chuyển hài cốt ra khỏi vại. Khi đó, nếu thi thể không phân hủy, họ sẽ phủ một loại bột nhão đặc biệt làm từ gạo lên hài cốt để tạo ra "nhục thân Phật".</p><p class="Normal"> 	Xác ướp đại sư Tự Hiền được đưa về thờ ở chùa Định Huệ từ năm 2011 và được mạ vàng vào năm ngoái.</p><p class="Normal" align="right"> 	<strong>Phương Hoa </strong></p>', 1, '15001943811616474776.jpg', NULL),
(223, 'Sư tử cái cho báo con bú khiến chuyên gia kinh ngạc', 'Các nhà bảo tồn lần đầu chụp được ảnh sư tử cái châu Phi cho báo con bú dù hai loài là kẻ thù với nhau.', '<div style="text-align:center"> 	<div style="text-align: center;"> 		<div id="video-156650" data-component="true" data-component-type="video" data-component-value="156650" data-component-typevideo="2"></div></div> 	<p class="Image"> 		 Sư tử cái cho báo con bú. Ảnh: <em>Joop van der linde</em>.</p> </div><p class="Normal"> 	Hình ảnh sư tử cái <span>Nosikitok </span><span>5 tuổi </span><span>cho báo con bú </span><span>ở khu bảo tồn Ngorongoro, Tanzania</span><span> </span><span>khiến những người làm công tác bảo tồn động vật hoang dã vô cùng ngạc nhiên, </span><em>National Geographic</em><span> ngày 13/7 đưa tin. T</span><span>rong tự nhiên, sư tử và báo là "kẻ thù không đội trời chung" và sư tử thường ra tay giết báo khi bắt gặp.</span></p><p class="Normal"> 	"Đây là chuyện chưa từng xảy ra", tiến sĩ Luke Hunter, chủ tịch <span>Panthera, </span><span>tổ chức </span><span>bảo tồn động vật hoang dã thuộc họ mèo</span><span>,</span><span> nói. "Chúng tôi chưa từng thấy cảnh này trong tự nhiên trước đây".</span></p><p class="Normal"> 	Tuy hoàn cảnh sư tử cái gặp báo con lẫn tung tích của báo mẹ chưa được làm rõ, Hunter cho rằng bản năng làm mẹ đã chiến thắng sự hung dữ <span>của sư tử cái</span><span>. Sư tử </span><span>Nosikitok đang nuôi con nhỏ x</span><span>ấp xỉ độ tuổi của báo con.</span></p><p class="Normal"> 	Hunter phân tích rằng, các đặc điểm sinh lý học không cản trở việc sư tử cái cho báo con bú vì cả hai loài cùng tạo ra loại sữa gần giống nhau và trải qua thời gian nuôi con như nhau.</p><p class="Normal"> 	Tuy nhiên, tiến sĩ Hunter cho rằng báo con dù được sư tử cho bú vẫn có thể đối mặt với tương lai không mấy sáng sủa. Nó có thể bị sư tử con cạnh tranh nếu được sư tử cái cho gia nhập đàn. Nếu không bị cạnh tranh, con vật cũng chỉ có tỷ lệ sống sót là 50%, bằng tỷ lệ sống sót của sư tử con trong năm đầu đời trước các mối đe dọa từ linh cẩu và cháy rừng. <span>"Tôi hy vọng điều tốt đẹp nhất, nhưng thách thức báo con sẽ đối mặt là rất lớn", ông chia sẻ.</span></p><p class="Normal"> 	Sư tử cái thường rời đàn để sinh con và nhập đàn khi sư tử con được 6-8 tuần tuổi. Những con sư tử khác trong đàn không có hormone làm mẹ như sư tử cái lẫn mối liên hệ với báo con qua quá trình nuôi dưỡng nên chúng có thể giết chết con vật ngay khi nhìn thấy.</p><p class="Normal"> 	Báo con thường phụ thuộc vào báo mẹ đến lúc được 12-14 tháng tuổi. Một số báo con có thể tự sinh tồn khi mới 7-8 tháng tuổi. Nếu sống sót đủ lâu để được mang về đàn sư tử, báo con cũng chỉ mới hai tháng tuổi, khó có khả năng tự sinh tồn.</p><p class="Normal"> 	"Sư tử cái là những bà mẹ vô cùng cần cù và tận tụy", tiến sĩ Hunter nói. Nhưng theo ông, ngay cả một bà mẹ nuôi tài giỏi nhất thế giới cũng cần phép màu để mang lại cái kết đẹp cho câu chuyện này.</p><p class="Normal" style="text-align:right;"> 	<strong>Vũ Phong</strong><strong> </strong></p>', 1, NULL, NULL);
INSERT INTO `news` (`news_id`, `news_title`, `news_quote`, `news_content`, `news_status`, `news_picture`, `news_category_id`) VALUES
(224, 'Video chồng ném điện thoại và vợ khỏi ban công gây chú ý tuần qua', 'Người chồng có hành động thiếu suy nghĩ trong lúc cãi nhau, nhưng cô vợ khẳng định tha thứ cho anh này khi cảnh sát xuất hiện.', '<p class="Normal"> 	Tuần từ 9/7 đến 15/7 còn một số video nhiều người xem trên Internet như lý do nên lắp camera trên xe hơi, đem cả tủ lạnh lên tàu để tiết kiệm tiền thuê xe chở đồ...</p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<a title="" href="http://sohoa.vnexpress.net/tin-tuc/doi-song-so/video-chong-nem-dien-thoai-va-vo-khoi-ban-cong-gay-chu-y-tuan-qua-3613938-p2.html"><div style="text-align: center"><img src="/pictures/news/1500194416794992666.png" /></div></a></td> 		</tr><tr><td> 				<p class="Normal" style="text-align:center;"> 					<strong><a title="Chồng đẩy vợ khỏi ban công trong lúc cãi vã" href="http://sohoa.vnexpress.net/tin-tuc/doi-song-so/video-chong-nem-dien-thoai-va-vo-khoi-ban-cong-gay-chu-y-tuan-qua-3613938-p2.html">Chồng đẩy vợ khỏi ban công trong lúc cãi vã</a></strong></p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<a title="" href="http://sohoa.vnexpress.net/tin-tuc/doi-song-so/video-chong-nem-dien-thoai-va-vo-khoi-ban-cong-gay-chu-y-tuan-qua-3613938-p3.html"><div style="text-align: center"><img src="/pictures/news/15001944161297770423.png" /></div></a></td> 		</tr><tr><td> 				<p class="Normal" style="text-align:center;"> 					<strong><a title="Cố tình lùi xe máy vào đầu ôtô để ăn vạ" href="http://sohoa.vnexpress.net/tin-tuc/doi-song-so/video-chong-nem-dien-thoai-va-vo-khoi-ban-cong-gay-chu-y-tuan-qua-3613938-p3.html">Cố tình lùi xe máy vào đầu ôtô để ăn vạ</a></strong></p> 			</td> 		</tr></tbody></table><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td> 				<a title="" href="http://sohoa.vnexpress.net/tin-tuc/doi-song-so/video-chong-ne